INSERT INTO texts VALUES(39180960,'再造的死神',replace(replace('①：这张卡反转的场合发动。双方玩家各自选自己1张手卡丢弃。\r\n②：这张卡被和怪兽的战斗破坏的场合发动。那只怪兽的攻击力·守备力下降500。','\r',char(13)),'\n',char(10)),'丢弃手牌','攻守下降500','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39188539,'风暴射手',replace(replace('1回合只有1次，可以从下面的效果选择1个发动。\r\n●移动到没有使用的相邻的怪兽卡区域。\r\n●这张卡的正对面存在的1张对方的怪兽·魔法·陷阱卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'移动到没有使用的相邻的怪兽卡区域','正对面存在的1张对方的怪兽·魔法·陷阱卡回到持有者手卡。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39191307,'假面龙','这张卡被战斗破坏送去墓地时，可以从卡组选择1只攻击力1500以下的龙族怪兽特殊召唤到自己场上。之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39229392,'叠光狙击者','这张卡不能特殊召唤。这张卡召唤成功时，变成守备表示。此外，自己场上有持有超量素材的超量怪兽存在的场合，把墓地的这张卡从游戏中除外，选择对方场上1只怪兽才能发动。选择的怪兽的攻击力下降自己场上的超量素材数量×500的数值。','变成守备表示','攻击下降','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39238953,'天声的服从',replace(replace('①：支付2000基本分，宣言1个怪兽卡名才能发动。对方把自身卡组确认，有宣言的怪兽的场合，把那之内的1只给双方确认从以下效果选择1个适用。\r\n●确认的卡加入把这张卡发动的玩家手卡。\r\n●确认的卡在把这张卡发动的玩家场上无视召唤条件攻击表示特殊召唤。','\r',char(13)),'\n',char(10)),'加入手卡','特殊召唤','','','','','','','','','','','','','','');
