INSERT INTO texts VALUES(39246582,'毛绒动物·狗',replace(replace('「毛绒动物·狗」的效果1回合只能使用1次。\r\n①：这张卡从手卡的召唤·特殊召唤成功时才能发动。从卡组把1只「锋利小鬼·剪刀」或者1只「毛绒动物·狗」以外的「毛绒动物」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
