INSERT INTO texts VALUES(39261576,'颗粒融合','从自己场上把融合怪兽卡决定的融合素材怪兽送去墓地，把名字带有「宝石骑士」的那1只融合怪兽当作融合召唤从额外卡组特殊召唤。这个效果融合召唤成功时，把墓地存在的这张卡从游戏中除外，选择那次融合召唤使用的1只名字带有「宝石骑士」的融合素材怪兽发动。那只融合怪兽的攻击力直到结束阶段时上升选择的怪兽的攻击力数值。','攻击上升','请选择一个使用的融合素材','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39271553,'方程式运动员 智能导航助手','这张卡在手卡·墓地存在的场合：可以选择自己场上持有比原本等级高的等级的1只「方程式运动员」怪兽；这张卡特殊召唤，选择的怪兽的等级下降和那个原本等级的相差数值，这张卡的等级变成和那个下降的数值相同。这张卡召唤·特殊召唤成功的场合：可以从卡组把1张「方程式运动员」场地魔法卡加入手卡。「方程式运动员 智能导航助手」的每个效果1回合各能使用1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39272762,'超银河眼光子龙',replace(replace('8星怪兽×3\r\n「银河眼光子龙」作为素材让这张卡超量召唤成功时，这张卡以外的场上表侧表示存在的卡的效果无效。1回合1次，把这张卡1个超量素材取除才能发动。对方场上的超量素材全部取除，这个回合这张卡的攻击力上升取除数量×500的数值。并且，这个回合这张卡在同1次的战斗阶段中可以作出最多有取除数量的攻击。','\r',char(13)),'\n',char(10)),'效果无效','取除素材','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39276790,'真剑胜负','①：伤害步骤有怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39284521,'机甲大炮','这张卡不能通常召唤。从手卡把机械族怪兽任意数量送去墓地的场合可以特殊召唤。这张卡的攻击力上升这张卡的特殊召唤时送去墓地的怪兽数量×800的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39303359,'古代的机械骑士',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39343610,'暗黑烈焰龙',replace(replace('①：这张卡从墓地的特殊召唤成功的场合发动。这张卡的攻击力·守备力变成原本数值的2倍。\r\n②：这张卡战斗破坏怪兽送去墓地的场合发动。给与对方那只怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'攻守变化','LP伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39357122,'真红眼凶雷皇-邪性恶魔',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●1回合1次，自己主要阶段才能发动。持有比这张卡的攻击力低的守备力的对方场上的表侧表示怪兽全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39373426,'秘旋谍任务-夺还',replace(replace('这张卡发动后，第3次的自己结束阶段破坏。\r\n①：1回合1次，自己场上有「秘旋谍」怪兽特殊召唤的场合，以对方场上1只怪兽为对象才能发动。那只怪兽的控制权直到结束阶段得到。这个效果得到控制权的怪兽在这个回合不能直接攻击。\r\n②：自己场上的「秘旋谍」怪兽被战斗·效果破坏的场合，可以作为那1只破坏的怪兽的代替而把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39387565,'真红眼的铠旋',replace(replace('「真红眼的铠旋」的①②的效果1回合各能使用1次。\r\n①：自己场上有「真红眼」怪兽存在的场合，以自己墓地1只通常怪兽为对象才能把这个效果发动。那只怪兽特殊召唤。\r\n②：这张卡被对方的效果破坏送去墓地的场合，以自己墓地1只「真红眼」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'是否使用效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39389320,'野蛮人国王','①：1回合1次，把这张卡以外的自己场上的战士族怪兽任意数量解放才能发动。这个回合，这张卡在同1次的战斗阶段中在通常攻击外加上可以作出最多有为这个效果发动而解放的怪兽数量的攻击。','多次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39399168,'查克拉的复活','「查克拉」的降临必要。必须从场上或者手札，牺牲奉献等级合计为7个以上的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39402797,'三头龙王',replace(replace('龙族调整＋调整以外的龙族怪兽1只以上\r\n这张卡不用同调召唤不能特殊召唤。这张卡同调召唤成功时，可以把自己场上存在的最多2张卡破坏。这个回合这张卡可以在通常攻击外加上这张卡的效果破坏的数量在同1次的战斗阶段中攻击。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39432962,'怒怒怒魔女','这张卡召唤·特殊召唤成功时，可以从手卡把「怒怒怒魔女」以外的1只名字带有「怒怒怒」的怪兽表侧攻击表示或者里侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39439590,'电子恐龙','对方从手卡特殊召唤怪兽时，可以从手卡特殊召唤这张卡。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39440937,'妨害电波','双方场上存在的同调怪兽全部变成守备表示，结束阶段时场上表侧表示存在的同调怪兽全部回到额外卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39454112,'胡出乱目',replace(replace('①：只要这张卡在魔法与陷阱区域存在，自己或者对方掷骰子的场合，可以把那之内1个数目作为以下数目适用。\r\n●1·3·5出现的场合：当作6使用。\r\n●2·4·6出现的场合：当作1使用。','\r',char(13)),'\n',char(10)),'是否要使用「胡出乱目」的效果？','要改变第几次骰子的结果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39477584,'魔轰神 利威坦',replace(replace('名字带有「魔轰神」的调整＋调整以外的怪兽1只以上\r\n场上存在的这张卡被破坏送去墓地时，选择自己墓地存在的最多3只名字带有「魔轰神」的怪兽才能发动。把选择的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39507162,'刀锋骑士',replace(replace('①：自己手卡是1张以下的场合，这张卡的攻击力上升400。\r\n②：自己场上没有这张卡以外的怪兽存在的场合，这张卡战斗破坏的反转怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39512984,'宝石骑士王·辉钻',replace(replace('「宝石骑士」怪兽×3\r\n这张卡用融合召唤才能从额外卡组特殊召唤。\r\n①：这张卡的攻击力上升自己墓地的「宝石」怪兽数量×100。\r\n②：1回合1次，把自己墓地1只7星以下的「宝石骑士」融合怪兽除外才能发动。直到结束阶段，这张卡当作和除外的怪兽同名卡使用，得到相同效果。','\r',char(13)),'\n',char(10)),'获得效果','复制效果结束','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39526584,'礼品卡','对方回复3000基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39531794,'脑灾害','①：以除外的1只自己的念动力族怪兽为对象才能把这张卡发动。那只怪兽特殊召唤。这张卡从场上离开时那只怪兽破坏。那只怪兽破坏时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39537362,'旅人的试练','对方的怪兽攻击宣言时才能发动。对方随机选择自己的1张手卡，对方猜选择的那张卡的种类（怪兽·魔法·陷阱）。猜不中的场合，那只攻击怪兽回到主人的手卡。','宣言卡种','是否现在使用「旅人的试练」的效果？','发动同时使用效果','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39552864,'神圣球体','被神圣光辉包住的天使灵魂。看见其美丽姿态者，据说能够实现愿望。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39564736,'重炼装融合','①：从自己的手卡·场上把「炼装」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39581190,'地中族射手',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己场上有这张卡以外的「地中族」怪兽存在，这张卡向对方的里侧守备表示怪兽攻击的伤害步骤开始时才能发动。那只对方怪兽回到持有者卡组。\r\n②：场上的这张卡被战斗·效果破坏送去墓地的场合才能发动。从卡组把1只「地中族」怪兽表侧守备表示或者里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39618799,'电子化天使-荼吉尼-',replace(replace('「机械天使的仪式」降临。\r\n①：这张卡仪式召唤成功的场合才能发动。对方必须把自身场上1只怪兽送去墓地。\r\n②：只要这张卡在怪兽区域存在，自己的仪式怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n③：自己结束阶段以自己墓地1只仪式怪兽或者1张「机械天使的仪式」为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39648965,'机皇兵 神智一型','这张卡的攻击力上升这张卡以外的场上表侧表示存在的名字带有「机皇」的怪兽数量×100的数值。此外，1回合1次，对方场上守备表示存在的怪兽为攻击对象的这张卡以外的自己的名字带有「机皇」的怪兽的攻击宣言时才能发动。那只怪兽向守备表示怪兽攻击的场合，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','贯穿伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39672388,'进化龙·达尔文翼龙','这张卡用名字带有「进化虫」的怪兽的效果特殊召唤成功时，可以选择场上表侧表示存在的1只怪兽等级上升最多2星。','等级上升','等级上升１星','等级上升２星','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39674352,'巨歧蜥·魔蜥义豪','已经精神崩溃，肉体追求更强的力量而暴走。在它身上再也找不到昔日的面影……','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39695323,'隆隆隆巨人','这张卡召唤成功时，可以选择自己墓地1只名字带有「隆隆隆」的怪兽表侧守备表示特殊召唤。那之后，这张卡变成守备表示。此外，这张卡攻击的场合，战斗阶段结束时变成守备表示。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39699564,'速炎星-戴豹','这张卡召唤·特殊召唤成功的回合的主要阶段时，把自己场上1只名字带有「炎星」的怪兽解放才能发动。从卡组选1张名字带有「炎舞」的魔法·陷阱卡在自己场上盖放。「速炎星-戴豹」的效果1回合只能使用1次。','盖放','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39701395,'调和的宝札','从手卡丢弃1只攻击力1000以下的龙族调整发动。从自己卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39703254,'番茄小子','场上表侧表示存在的这张卡以外的1只植物族怪兽成为对方的魔法·陷阱卡的效果的对象时才能发动。把自己场上存在的这张卡解放，从自己卡组抽2张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39711336,'不死王 巫妖','这张卡不能通常召唤。满足条件的「大神官 迪·扎德」做祭品特殊召唤。这张卡1个回合可以有1次变成里侧守备表示。场上表侧表示存在的这张卡为对象的魔法·陷阱卡的发动和效果无效并破坏。这张卡反转时，选择自己的墓地的1只不死族怪兽特殊召唤上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39712330,'决战之火盖','可以把自己墓地1张怪兽卡从游戏中除外，从手卡把1只通常怪兽通常召唤。这个效果在自己回合的主要阶段时才能发动。','通常召唤','是否现在使用「决战之火盖」的效果？','表侧表示召唤','里侧表示放置','','','','','','','','','','','','');
INSERT INTO texts VALUES(39719977,'三角攻势','这张卡仅当自己场上存在3只同名的通常怪兽（衍生物除外）时才能发动。这张卡发动的回合，这3只同名的通常怪兽可以对对方进行直接攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39751093,'龙宫之姬','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到主人的手卡。这张卡召唤·反转时，可以选择对方场上的1只表侧表示的怪兽改变表示形式。','返回手牌','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39751094,'龙宫之姬','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到主人的手卡。这张卡召唤·反转时，可以选择对方场上的1只表侧表示的怪兽改变表示形式。','返回手牌','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39761138,'新炎狱萨满','自己墓地有名字带有「炎狱」的怪兽3只以上存在，这张卡战斗破坏对方怪兽的场合，选择对方墓地存在的1张卡从游戏中除外。这个效果的发动时对方墓地没有魔法卡存在的场合，再给与对方基本分500分伤害。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39765115,'飞溅捕获','对方超量召唤成功时，把自己墓地2只鱼族怪兽从游戏中除外才能发动。得到那1只超量怪兽的控制权。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39765958,'琰魔龙 红莲魔',replace(replace('调整＋调整以外的怪兽1只以上\r\n1回合1次，自己的主要阶段1才能发动。这张卡以外的场上表侧攻击表示存在的怪兽全部破坏。这个效果发动的回合，这张卡以外的怪兽不能攻击。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39774685,'魔菌','植物族才能装备。1只装备怪兽的攻击力·守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39778366,'同盟紧急出动',replace(replace('「同盟紧急出动」在1回合只能发动1张。\r\n①：以除外的自己的机械族·光属性的最多3只通常怪兽或者同盟怪兽为对象才能发动。那些怪兽特殊召唤。\r\n②：把墓地的这张卡除外，以除外的自己的机械族·光属性的1只通常怪兽或者同盟怪兽为对象才能发动。那只怪兽回到手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39806198,'机关傀儡-磁力人偶','对方场上有怪兽存在，自己场上存在的怪兽只有名字带有「机关傀儡」的怪兽的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39817919,'灵魂鸟-忍鸦',replace(replace('这张卡不能特殊召唤。\r\n①：1回合1次，这张卡和对方怪兽进行战斗的从伤害步骤开始时到伤害计算前，从手卡丢弃1只灵魂怪兽才能发动。这张卡的攻击力·守备力直到战斗阶段结束时上升丢弃的怪兽的攻击力·守备力的各自数值。\r\n②：这张卡召唤·反转的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39823987,'太阳龙 因蒂',replace(replace('「赤蚁」＋调整以外的怪兽1只以上\r\n这张卡被战斗破坏送去墓地时，把让这张卡破坏的怪兽破坏，给与对方基本分那只怪兽的攻击力一半数值的伤害。场上存在的这张卡被破坏的场合，下个回合的准备阶段时，可以把自己墓地存在的1只「月影龙 基利亚」特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','是否要发动「太阳龙 因蒂」的效果？','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39829561,'命运英雄 死逝人','自己的准备阶段时，这张卡在墓地存在的场合，在对方场上表侧攻击表示特殊召唤。这张卡被战斗破坏的场合，不去墓地从游戏中除外。这张卡从手卡·卡组被卡的效果送去墓地的场合，不去墓地从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39838559,'方程式运动员赛道大奖赛',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：只要这张卡在场地区域存在，场上的「方程式运动员」怪兽的等级只在战斗阶段内上升2星。\r\n②：1回合1次，自己的「方程式运动员」怪兽战斗破坏对方怪兽时才能发动。自己从卡组抽1张。\r\n③：场上的表侧表示的这张卡被效果破坏的场合才能发动。从卡组把「方程式运动员赛道大奖赛」以外的1张「方程式运动员」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39853199,'妖仙兽 阎魔巳裂',replace(replace('①：这张卡和风属性以外的表侧表示怪兽进行战斗的伤害步骤开始时才能发动。那只怪兽破坏。\r\n②：这张卡灵摆召唤成功时，以对方场上1张卡为对象才能发动。那张卡破坏。\r\n③：这张卡特殊召唤的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'破坏怪兽','破坏1张卡','返回手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39890958,'强化支援机械·重装铠甲',replace(replace('①：这张卡召唤成功的场合，以自己墓地1只同盟怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只机械族怪兽为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n③：装备怪兽不会成为对方的效果的对象。','\r',char(13)),'\n',char(10)),'变成装备','装备的这张卡特殊召唤','墓地同盟怪兽特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39892082,'气球蜥蜴','每到自己的准备阶段时，在这张卡上放置1个指示物。破坏这张卡的卡的控制者受到这张卡上放置的指示物数量×400点的伤害。','放置指示物','伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39897277,'精灵之光','光属性的怪兽才能装备。装备的怪兽攻击力上升400，守备力下降200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39900763,'异次元的邂逅','①：双方有除外的自己怪兽1只以上存在的场合才能发动。双方玩家各自选除外的1只自己怪兽里侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39905966,'遗式纹身者','这张卡召唤·反转召唤·特殊召唤成功时，可以选择名字带有「遗式」的自己墓地存在的1张仪式怪兽或者仪式魔法卡加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39910367,'魔法都市 恩底弥翁',replace(replace('①：只要这张卡在场地区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。\r\n②：有魔力指示物放置的卡被破坏的场合把那些卡放置的魔力指示物数量的魔力指示物给这张卡放置。\r\n③：1回合1次，自己为让卡的效果发动而把自己场上的魔力指示物取除的场合，可以作为代替从这张卡取除。\r\n④：这张卡被破坏的场合，可以作为代替把这张卡1个魔力指示物取除。','\r',char(13)),'\n',char(10)),'发动','是否要使用「魔法都市 恩底弥翁」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39913299,'真实之名',replace(replace('「真实之名」在1回合只能发动1张。\r\n①：宣言1个卡名才能发动。自己卡组最上面的卡翻开，宣言的卡的场合，那张卡加入手卡。并且，可以再从卡组把1只神属性怪兽加入手卡或特殊召唤。不是的场合，翻开的卡送去墓地。','\r',char(13)),'\n',char(10)),'是否把神属性怪兽加入手卡或特殊召唤？','请选择神属性怪兽','加入手卡','特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(39956951,'封魔一闪','对方场上的怪兽卡区域全部有怪兽存在的场合才能发动。对方场上存在的全部怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39964797,'水晶机巧-量子刚玉',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：对方的主要阶段以及战斗阶段才能发动。用包含这张卡的自己场上的怪兽为同调素材作同调召唤。\r\n②：同调召唤的这张卡被战斗·效果破坏的场合，以同调怪兽以外的自己墓地1只「水晶机巧」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39967326,'过早的复活','选择自己墓地存在的1只名字带有「地缚神」的怪兽发动。选择的怪兽在自己场上特殊召唤。这个效果特殊召唤的怪兽在那个回合不能攻击宣言。此外，这个效果特殊召唤的怪兽进行战斗的场合，对方玩家受到的战斗伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39972129,'No.64 古狸 三太夫',replace(replace('兽族2星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。在自己场上把1只「影武者狸衍生物」（兽族·地·1星·攻?/守0）特殊召唤。这衍生物的攻击力变成和这衍生物的特殊召唤时场上存在的攻击力最高的怪兽相同攻击力。只要自己场上有这张卡以外的兽族怪兽存在，这张卡不会被战斗以及卡的效果破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39972130,'影武者狸衍生物','由「No.64 古狸 三太夫」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39978267,'电子袭击者',replace(replace('这张卡召唤·反转召唤·特殊召唤时，从下列效果中选择1项发动：\r\n●选择场上1张装备在怪兽身上的装备卡并将其破坏。\r\n●选择场上1张装备在怪兽身上的装备卡并将其装备在这张卡身上。','\r',char(13)),'\n',char(10)),'选择效果发动','选择场上1张装备在怪兽身上的装备卡并将其破坏','选择场上1张装备在怪兽身上的装备卡并将其装备在这张卡身上','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39980304,'连锁素材','这张卡的发动回合进行融合召唤的场合，可以把融合怪兽卡决定的怪兽从自己的手卡·卡组·场上·墓地选择并从游戏中除外，把这些作为融合素材。这张卡发动的回合不能攻击，这个效果融合召唤的怪兽在结束阶段时破坏。','是否使用「连锁素材」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39984786,'产卵短吻鳄','把爬虫族怪兽解放对这张卡的上级召唤成功的场合，把1只为这张卡的上级召唤而解放的怪兽在那个回合的结束阶段时从墓地往自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39987164,'双西格马大日',replace(replace('4星怪兽×3\r\n1回合1次，可以把这张卡1个超量素材取除，选择对方场上表侧攻击表示存在的1只效果怪兽当作装备卡使用给这张卡装备。这张卡和与这个效果装备的怪兽卡相同属性的怪兽进行战斗的场合，不进行伤害计算把那只怪兽破坏。','\r',char(13)),'\n',char(10)),'装备','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39996157,'机械天使的仪式',replace(replace('「电子化天使」仪式怪兽的降临必需。\r\n①：等级合计直到变成仪式召唤的怪兽的等级以上为止，把自己的手卡·场上的怪兽解放，从手卡把1只「电子化天使」仪式怪兽仪式召唤。\r\n②：自己场上的光属性怪兽被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'是否使用「机械天使的仪式」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(39998992,'超机怪虫·对观突触虫',replace(replace('地属性怪兽2只\r\n①：这张卡所连接区的「机怪虫」怪兽不会被战斗破坏，攻击力·守备力上升300，同1次的战斗阶段中最多2次可以向怪兽攻击。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合或者被战斗破坏的场合，以自己墓地2只「机怪虫」怪兽为对象才能发动（同名卡最多1张）。那些怪兽里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40005099,'不知火流 转生之阵',replace(replace('「不知火流 转生之阵」在1回合只能发动1张。\r\n①：1回合1次，自己场上没有怪兽存在的场合，可以把1张手卡送去墓地，从以下效果选择1个发动。\r\n●以自己墓地1只守备力0的不死族怪兽为对象才能发动。那只怪兽特殊召唤。\r\n●以除外的1只自己的守备力0的不死族怪兽为对象才能发动。那只怪兽回到墓地。','\r',char(13)),'\n',char(10)),'特殊召唤','回到墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40012727,'爆裂斩','自己场上有名字带有「/爆裂体」的怪兽表侧表示存在的场合才能发动。场上表侧表示存在的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40028305,'超重武者装留 光爪',replace(replace('「超重武者装留 光爪」的②的效果1回合只能使用1次。\r\n①：自己主要阶段以自己场上1只「超重武者」怪兽为对象才能发动。从自己的手卡·场上把这只怪兽当作攻击力·守备力上升500的装备卡使用给那只自己怪兽装备。装备怪兽不会被战斗破坏。\r\n②：这张卡的效果让这张卡装备中的场合才能发动。装备的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40044918,'元素英雄 天空侠',replace(replace('①：这张卡召唤·特殊召唤成功时，可以从以下效果选择1个发动。\r\n●选最多有这张卡以外的自己场上的「英雄」怪兽数量的场上的魔法·陷阱卡破坏。\r\n●从卡组把1只「英雄」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'选择一个效果发动','选最多有这张卡以外的自己场上的「英雄」怪兽数量的场上的魔法·陷阱卡破坏。','从卡组把1只「英雄」怪兽加入手卡。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40048324,'奥火法师','这张卡被同调怪兽的同调召唤使用送去墓地的场合，可以从自己卡组把1张「爆裂模式」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40061558,'隐藏的机壳内核 无神论',replace(replace('这张卡不能特殊召唤，把自己场上3只「机壳」怪兽解放的场合才能通常召唤。\r\n①：通常召唤的这张卡不受魔法·陷阱卡的效果影响，也不受原本的等级或者阶级比这张卡的等级低的怪兽发动的效果影响。\r\n②：1回合1次，以对方场上1只怪兽为对象才能发动。那只怪兽的控制权直到结束阶段得到。','\r',char(13)),'\n',char(10)),'把3只「机壳」怪兽解放','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40089744,'混沌场',replace(replace('「混沌场」在1回合只能发动1张。\r\n①：作为这张卡的发动时的效果处理，从卡组把1只「混沌战士」仪式怪兽或者「暗黑骑士 盖亚」怪兽加入手卡。\r\n②：只要这张卡在场地区域存在，每次从双方的手卡·场上有怪兽被送去墓地，每有1只给这张卡放置1个魔力指示物（最多6个）。\r\n③：1回合1次，把这张卡3个魔力指示物取除才能发动。自己从卡组把1张仪式魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40101111,'究极念动力体',replace(replace('念动力族同调怪兽＋念动力族怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。这张卡不会被卡的效果破坏。这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。此外，这张卡战斗破坏怪兽送去墓地时，自己基本分回复破坏的怪兽的攻击力的数值。','\r',char(13)),'\n',char(10)),'回复破坏的怪兽的攻击力的数值','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40133511,'食魂者 巴祖','可以从自己的墓地选最多3张怪兽从游戏中除外。每除外1张卡，在对方回合结束前，这张卡的攻击力上升300。这个效果在自己的回合只能用1次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40143123,'星圣·角宿一','这张卡召唤成功时，可以从手卡把1只名字带有「星圣」的5星怪兽表侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40155554,'盟军头脑','为了强化正义盟军的思考回路而开发的高性能装置。嵌有从来自异虫星云的陨石中取得的物质，拥有引以为傲的高调节能力。其未知力量的谜团至今仍未解开。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40159926,'镜子共鸣者',replace(replace('「镜子共鸣者」的①的效果1回合只能使用1次。\r\n①：这张卡在手卡·墓地存在，从额外卡组特殊召唤的怪兽只有对方场上才存在的场合才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。\r\n②：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。这张卡在这个回合作为同调素材的场合，当作和作为对象的怪兽的原本等级相同等级使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40160226,'机海龙 蛇颈龙','自己场上有海龙族怪兽存在的场合，这张卡可以不用解放作召唤。1回合1次，可以通过把自己场上1只水属性怪兽解放，选择对方场上表侧表示存在的1张卡破坏。','不进行解放作召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40164421,'光道少女 密涅瓦',replace(replace('①：这张卡召唤成功时才能发动。把持有自己墓地的「光道」怪兽种类数量以下的等级的1只龙族·光属性怪兽从卡组加入手卡。\r\n②：这张卡从手卡·卡组送去墓地的场合发动。从自己卡组上面把1张卡送去墓地。\r\n③：自己结束阶段发动。从自己卡组上面把2张卡送去墓地。','\r',char(13)),'\n',char(10)),'检索','送墓','送墓','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40172183,'狭窄的通路','双方场上的怪兽都在2只以下时才能发动。双方都至多只能往自己场上召唤2只怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40173854,'水陆两用战斗艇','「陆战型战斗艇」＋「守卫海洋的战士」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40177746,'地外生命',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡被送去墓地的场合，把这张卡以外的自己的场上·墓地最多2只天使族·光属性怪兽除外才能发动。把除外数量的「地外生命」以外的2星以下的天使族·光属性怪兽从卡组加入手卡（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40189917,'炎狱死亡枪手','这张卡不能被特殊召唤。只能通过解放自己场上1只名字带有「炎狱」的怪兽来召唤。1回合1次，可以把自己墓地中存在的1只守备力200的怪兽从游戏中除外，给与对方玩家那只怪兽攻击力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40196604,'暗影','从水晶中发射强烈的光线进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40200834,'睡狮子','平时沉睡着的猛兽。一旦苏醒就使人招架不住。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40213117,'魔导弓士 拉穆尔','1回合1次，把手卡1张名字带有「魔导书」的魔法卡给对方观看才能发动。从手卡把1只4星以下的魔法师族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40217358,'蓝尘妖','这张卡不能解放，也不能作为融合·同调·超量召唤的素材。场上的这张卡被破坏时，这张卡的控制者的手卡随机选1张直到下个回合的准备阶段时里侧表示从游戏中除外。「蓝尘妖」在自己场上只能有1只表侧表示存在。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40225398,'双剑破坏者','这张卡向守备表示怪兽攻击的场合，只有1次可以继续攻击。这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','是否使用「双剑破坏者」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40230018,'魔导书库 科瑞森','自己墓地没有名字带有「魔导书」的魔法卡存在的场合才能发动。从卡组选名字带有「魔导书」的魔法卡3种类给对方观看，对方从那之中随机选1张。对方选的1张卡加入自己手卡，剩下的卡回到卡组。「魔导书库 科瑞森」在1回合只能发动1张，这张卡发动的回合，自己不能把名字带有「魔导书」的卡以外的魔法卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40240595,'进化之茧','这张卡可以在手上当作装备卡使用装备在场上表侧表示的「幼虫宝宝」上。装备的场合，「幼虫宝宝」的攻防数值使用「进化之茧」的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40252269,'魔术师的再演',replace(replace('①：只在这张卡在场上表侧表示存在才有1次，以自己墓地1只3星以下的魔法师族怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：这张卡被送去墓地的场合才能发动。从卡组把「魔术师的再演」以外的1张「魔术师」永续魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40253382,'TG－SX1','自己场上存在的名字带有「科技属」的怪兽战斗破坏对方怪兽送去墓地时才能发动。选择自己墓地存在的1只名字带有「科技属」的同调怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40267580,'捕脑魔','反转：这张卡当作装备卡使用，装备到对方场上的怪兽上。获得这张卡装备的怪兽的控制权。每次对方准备阶段对方基本分回复500。','装备获得控制权','LP回复','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40279770,'黑羽的引导','卡的效果让自己受到伤害时才能发动。从手卡把1只4星以下的名字带有「黑羽」的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40318957,'娱乐伙伴 骷髅杂技小丑',replace(replace('←8【灵摆】8→\r\n①：自己不是「娱乐伙伴」怪兽、「魔术师」灵摆怪兽、「异色眼」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n①：这张卡召唤成功时才能发动。从卡组把「娱乐伙伴 骷髅杂技小丑」以外的「娱乐伙伴」怪兽、「魔术师」灵摆怪兽、「异色眼」怪兽之内任意1只加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40320754,'毒根王','这张卡被战斗破坏送去墓地时，从自己墓地里特殊召唤1只「毒根王」以外的植物族怪兽上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40343749,'屋尘妖','①：在场上的表侧表示的这张卡被对方的效果破坏送去墓地时或者在伤害步骤开始时是表侧表示的这张卡被和对方怪兽的战斗破坏送去墓地时才能发动。从手卡·卡组选「尘妖」怪兽任意数量在双方场上各相同数量特殊召唤。','特殊召唤','请选择在自己场上特殊召唤的怪兽','请选择在对方场上特殊召唤的怪兽','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40348946,'高超同调士','这张卡被龙族怪兽的同调召唤使用送去墓地的场合，这张卡为同调素材的同调怪兽的攻击力上升800，结束阶段时从游戏中除外。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40350910,'癔病风','直到下次的对方回合的结束阶段，场上的盖伏的魔法·陷阱不能破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40371092,'白夜之骑士 盖亚',replace(replace('「白夜之骑士 盖亚」的以下效果1回合各能使用1次。\r\n●把这张卡以外的自己场上1只光属性怪兽解放才能发动。从卡组把1只战士族·暗属性·4星怪兽加入手卡，那之后1张手卡送去墓地。\r\n●把自己墓地1只暗属性怪兽除外，选择场上1只怪兽才能发动。选择的怪兽的攻击力直到对方的结束阶段时下降500。','\r',char(13)),'\n',char(10)),'从卡组把1只战士族·暗属性·4星怪兽加入手卡，那之后1张手卡送去墓地','选择场上1只怪兽攻击力直到对方的结束阶段时下降500','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40374923,'猛犸的墓场','守护着伙伴墓地的猛犸，对于任何胆敢践踏墓地的盗墓者进行毫不客气的攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40383551,'大寒气','这个回合，自己不能作魔法·陷阱卡的效果使用以及发动·盖放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40384720,'音速射手','对方的魔法·陷阱卡区域没有卡存在的场合，这张卡可以直接攻击对方玩家。这个时候，给与对方玩家的战斗伤害变成这张卡的原本攻击力的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40387124,'开在深渊的花','默默生长在黑暗的深渊里的花，平常很难见到。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40390147,'辉光帝 盖勒克西恩',replace(replace('名字带有「光子」的4星怪兽×2\r\n1回合1次，把这张卡最多2个超量素材取除才能发动。为这个效果发动而取除的超量素材数量的以下效果适用。\r\n●1个：从手卡把1只「银河眼光子龙」特殊召唤。\r\n●2个：从卡组把1只「银河眼光子龙」特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','从手卡把1只「银河眼光子龙」特殊召唤','从卡组把1只「银河眼光子龙」特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40391316,'扰乱骑士',replace(replace('「扰乱」怪兽×2\r\n①：这张卡在怪兽区域表侧表示存在期间，指定没有使用的对方的怪兽区域最多2处，那些区域不能使用。','\r',char(13)),'\n',char(10)),'是否还要选择一个区域？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40392714,'方界帝 疾风之盖伊罗',replace(replace('这张卡不能通常召唤。把自己场上1只「方界」怪兽送去墓地的场合可以特殊召唤。\r\n①：这个方法特殊召唤的这张卡的攻击力上升800。\r\n②：这张卡从手卡的特殊召唤成功的场合发动。给与对方800伤害。\r\n③：这张卡战斗的伤害步骤结束时，以自己墓地最多2只「方界胤 毗贾姆」为对象才能发动。这张卡送去墓地，作为对象的怪兽特殊召唤。那之后，可以从卡组把1只「方界帝 神火之德拉耆尼」加入手卡。','\r',char(13)),'\n',char(10)),'是否把1只「方界帝 神火之德拉耆尼」加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40410110,'炼金生物 人工生命体','这只怪兽的属性可以改变。这个效果1回合可以使用1次。','改变属性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40418351,'铠黑龙-电子暗黑龙',replace(replace('「电子暗黑魔角」＋「电子暗黑翼刃」＋「电子暗黑龙骨」\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：这张卡特殊召唤成功的场合，以自己墓地1只龙族怪兽为对象发动。那只龙族怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡的攻击力上升这张卡的效果装备的怪兽的原本攻击力数值以及自己墓地的怪兽数量×100。\r\n③：这张卡被战斗破坏的场合，作为代替把装备的那只怪兽破坏。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40424929,'烈华炮舰 抚子',replace(replace('3星怪兽×3\r\n把这张卡1个超量素材取除才能发动。给与对方基本分对方手卡数量×200的数值的伤害。「烈华炮舰 抚子」的效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40450317,'同胞的牵绊',replace(replace('这张卡发动的回合，自己不能进行战斗阶段。\r\n①：支付2000基本分，以自己场上1只4星以下的怪兽为对象才能发动。和那只怪兽相同种族·属性·等级而卡名不同的2只怪兽从卡组特殊召唤（同名卡最多1张）。这张卡的发动后，直到回合结束时自己不能把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40453765,'野蛮人2号','①：自己场上的「野蛮人1号」每有1只，这张卡的攻击力上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40465719,'凡人的施舍','从卡组抽2张卡，之后从手卡把1张通常怪兽卡从游戏中除外。手卡没有通常怪兽卡的场合，手卡全部送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40473581,'雷帝神','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到主人的手卡。这张卡给与对方玩家的战斗伤害减半。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40502912,'幻奏的音女 阿莉娅','①：只要特殊召唤的这张卡在怪兽区域存在，自己场上的「幻奏」怪兽不会成为效果的对象，不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40509732,'武者破坏王',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡和对方怪兽进行战斗的场合，对方直到伤害步骤结束时魔法·陷阱·怪兽的效果不能发动，只在战斗阶段内那只对方怪兽的效果无效化。表侧表示的这张卡因对方的卡的效果从场上离开的场合：可以选择自己墓地1只机械族怪兽；那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40516623,'相扑魂 横纲',replace(replace('←1 【灵摆】 1→\r\n场上有怪兽灵摆召唤的场合：灵摆区域的这张卡回到持有者手卡。\r\n【怪兽效果】\r\n这张卡召唤成功时：可以把和自己的灵摆区域的卡相同纵列的对方场上的怪兽全部送去墓地。1回合1次，这张卡召唤·反转的回合的结束阶段时：这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40522482,'摩天一击','①：以自己场上1只「元素英雄」融合怪兽为对象才能发动。比那只怪兽攻击力高的对方场上的表侧表示怪兽全部破坏。那之后，给与对方这个效果破坏送去墓地的怪兽之内原本攻击力最高的怪兽的那个数值的伤害。自己的场地区域有「摩天楼」场地魔法卡存在的场合，给与对方的伤害变成这个效果破坏送去墓地的怪兽全部的原本攻击力的合计数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40529384,'爆翼龙',replace(replace('调整＋调整以外的龙族怪兽1只以上\r\n和持有这张卡的攻击力以下的攻击力的场上表侧表示存在的怪兽进行战斗的场合，可以不进行伤害计算把那只怪兽破坏，给与对方基本分破坏怪兽的攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'破坏并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40542825,'凭依装着-莱娜',replace(replace('①：这张卡可以把1只「光灵使 莱娜」和1只光属性怪兽从自己场上送去墓地，从手卡·卡组特殊召唤。\r\n②：这张卡的①的方法特殊召唤成功时才能发动。从卡组把「凭依装着-莱娜」以外的1只守备力1500的魔法师族怪兽加入手卡。\r\n③：这张卡的①的方法特殊召唤的这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40555959,'空手假象','自己场上有名字带有「永火」的怪兽表侧表示存在的场合，1回合只有1次，可以直到下次的自己的准备阶段时把自己手卡全部里侧表示从游戏中除外。','手卡除外','是否要除外手卡？','发动同时使用效果','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40575313,'地狱亡灵','出现在荒野的野兽亡灵。数量多了也很棘手。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40583194,'强要共鸣者','把自己场上表侧表示存在的这张卡送去墓地，选择自己场上表侧表示存在的1只怪兽发动。这个回合，选择的怪兽攻击的场合，对方直到伤害步骤结束时不能把以怪兽为对象的魔法·陷阱·效果怪兽的效果发动。','效果抑制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40591390,'命运英雄 恐惧人','通过「幽狱之时计塔」的效果特殊召唤的场合，自己场上的名字带有「命运英雄」的怪兽以外的自己怪兽全部破坏。之后，可以从自己墓地特殊召唤最多2只名字带有「命运英雄」的怪兽。这张卡特殊召唤的回合，自己场上的名字带有「命运英雄」的怪兽不会被破坏，对控制者造成的战斗伤害变成0。这张卡的攻击力·守备力变成自己场上除这张卡以外的名字带有「命运英雄」的怪兽的原本攻击力合计数值。','特殊召唤','是否要特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40605147,'神之通告',replace(replace('①：可以支付1500基本分把以下效果发动。\r\n●怪兽的效果发动时才能发动。那个发动无效并破坏。\r\n●自己或者对方把怪兽特殊召唤之际才能发动。那次特殊召唤无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40607210,'吸血鬼·格蕾丝','这张卡在墓地存在，不死族怪兽的效果让自己场上有5星以上的不死族怪兽特殊召唤时，支付2000基本分才能发动。这张卡从墓地特殊召唤。「吸血鬼·格蕾丝」的这个效果1回合只能使用1次。此外，1回合1次，宣言卡的种类（怪兽·魔法·陷阱）才能发动。对方把宣言的种类的1张卡从卡组送去墓地。','特殊召唤','卡组送墓','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40619741,'大日士兵','这张卡的攻击宣言时，可以选择最多有这张卡装备的装备卡数量的对方场上存在的怪兽，把表示形式变更。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40619825,'恶魔之斧',replace(replace('①：装备怪兽的攻击力上升1000。\r\n②：这张卡从场上送去墓地时，把自己场上1只怪兽解放才能发动。这张卡回到卡组最上面。','\r',char(13)),'\n',char(10)),'回到卡组的最上面','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40633297,'药物的副作用','对方的基本分的回复的效果变成给与对方基本分的伤害的效果。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40634253,'BM-4炸弹蜘蛛',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：1回合1次，以自己场上1只机械族·暗属性怪兽和对方场上1张表侧表示的卡为对象才能发动。那些卡破坏。\r\n②：自己场上的原本的种族·属性是机械族·暗属性的怪兽用战斗或者自身的效果破坏对方场上的怪兽送去墓地的场合才能发动。给与对方那1只破坏送去墓地的怪兽的原本攻击力一半数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40636712,'魔玩具·钩乌贼',replace(replace('「锋利小鬼」怪兽＋「毛绒动物」怪兽\r\n①：1回合1次，以对方场上1只怪兽为对象才能发动。那只怪兽送去墓地。这个效果发动的回合，这张卡不能直接攻击。\r\n②：这张卡在同1次的战斗阶段中可以作2次攻击。\r\n③：这张卡进行战斗的战斗阶段结束时才能发动。这张卡变成守备表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40640057,'栗子球','①：对方怪兽攻击的场合，那次伤害计算时把这张卡从手卡丢弃才能发动。那次战斗发生的对自己的战斗伤害变成0。','战斗伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40640058,'栗子球','①：对方怪兽攻击的场合，那次伤害计算时把这张卡从手卡丢弃才能发动。那次战斗发生的对自己的战斗伤害变成0。','战斗伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40640059,'栗子球','①：对方怪兽攻击的场合，那次伤害计算时把这张卡从手卡丢弃才能发动。那次战斗发生的对自己的战斗伤害变成0。','战斗伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40659562,'守护者斯芬克斯','这张卡1个回合可以有1次变回里侧守备表示。这张卡反转召唤成功时，对方场上的全部怪兽回到持有者手卡。','变成里侧守备表示','对方场上的全部怪兽返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40666140,'极星灵 光精灵','这张卡召唤成功时，选择这张卡以外的自己场上表侧表示存在的1只怪兽才能发动。选择的怪兽的等级以下的1只名字带有「极星」的怪兽从手卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40669071,'余码语者',replace(replace('电子界族怪兽2只以上\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功时，指定额外怪兽区域的怪兽数量的没有使用的主要怪兽区域才能发动。指定的区域在这只怪兽表侧表示存在期间不能使用。\r\n②：这张卡所连接区的怪兽攻击力上升500，不会被效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40672993,'避雷神','①：只要这张卡在怪兽区域存在，双方在主要阶段1内不能把魔法卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40695128,'磨破罗魏','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到主人的手卡。这张卡召唤·反转的场合，下次的自己的抽卡阶段时，抽卡前看卡组最上面的1张卡并把这张卡放到卡组的最上面或最下面。','返回手牌','确认卡','放回卡组最上面','放回卡组最下面','','','','','','','','','','','','');
INSERT INTO texts VALUES(40703222,'增殖','把自己场上表侧表示存在的1只「栗子球」解放才能发动。在自己场上把「栗子球衍生物」（恶魔族·暗·1星·攻300/守200）尽可能守备表示特殊召唤。这衍生物不能为上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40703223,'栗子球衍生物','由「增殖」的效果特殊召唤。这衍生物不能为上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40703393,'冥界流傀儡术','选择自己墓地的1只恶魔族怪兽。在自己场上选择合计等级和选择的那只怪兽的等级相同的怪兽从游戏中除外。之后，选择的那只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40732515,'神圣魔导王 恩底弥翁','这张卡可以把自己场上存在的「魔法都市 恩底弥翁」放置的6个魔力指示物取除，从自己的手卡或者墓地特殊召唤。这个方法特殊召唤成功时，把自己墓地存在的1张魔法卡加入手卡。1回合1次，可以从手卡丢弃1张魔法卡，场上存在的1张卡破坏。','墓地存在的1张魔法卡加入手牌','场上存在的1张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40736921,'心镇壶的复制品','选择场上盖放的1张魔法·陷阱卡发动。只要这张卡在场上存在，选择的卡不能发动。不能对应这张卡的发动把魔法·陷阱·效果怪兽的效果发动。','请选择一张盖放的魔法·陷阱卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40737112,'混沌之黑魔术师',replace(replace('「混沌之黑魔术师」的①的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的回合的结束阶段，以自己墓地1张魔法卡为对象才能发动。那张卡加入手卡。\r\n②：这张卡战斗破坏对方怪兽的伤害计算后发动。那只对方怪兽除外。\r\n③：表侧表示的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'魔法回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40817915,'电击栗子','自己的主要阶段时，选择持有超量素材的1只超量怪兽才能发动。把选择的怪兽1个超量素材取除，从自己卡组把1只「电击栗子」特殊召唤。这张卡不能作为同调素材。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40826495,'岩浆人','岩浆里出生的怪兽，灼热的身体使接近的物体融化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40830387,'古代的机械掌','名字带有「古代的机械」的怪兽才能装备。和装备怪兽进行战斗的怪兽在那个伤害步骤结束时破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40838625,'沙尘防护罩 -尘埃之力-','①：对方怪兽的攻击宣言时才能发动。对方场上的攻击表示怪兽全部变成里侧守备表示。这个效果变成里侧守备表示的怪兽不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40844552,'极星天 女武神','这张卡召唤成功时，对方场上有怪兽存在，自己场上没有这张卡以外的卡存在的场合，把手卡2只名字带有「极星」的怪兽从游戏中除外才能发动。在自己场上把2只「英灵衍生物」（战士族·地·4星·攻/守1000）守备表示特殊召唤。','特殊召唤Token','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40844553,'英灵衍生物','由「极星天 女武神」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40854197,'元素英雄 绝对零度侠',replace(replace('名字带有「英雄」的怪兽＋水属性怪兽\r\n这张卡不能作融合召唤以外的特殊召唤。这张卡的攻击力上升场上表侧表示存在的「元素英雄 绝对零度侠」以外的水属性怪兽数量×500的数值。这张卡从场上离开时，对方场上存在的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40854824,'飞越虹桥','①：自己场上的原本卡名是「究极宝玉神 虹龙」或者「究极宝玉神 虹暗龙」的怪兽把效果发动的回合才能发动。从卡组把「宝玉兽」怪兽任意数量特殊召唤（同名卡最多1张）。','是否还要继续特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40867519,'静寂虫','这张卡召唤·反转召唤成功的场合变成守备表示。只要这张卡在场上表侧表示存在，永续魔法·永续陷阱卡的效果无效。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40884383,'凶恶犯-开膛手','这张卡反转召唤成功时，可以从自己墓地里选择1张装备魔法卡装备在这张卡身上。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40894584,'电豪灵蜥',replace(replace('①：「电豪灵蜥」在自己场上只能有1只表侧表示存在。\r\n②：自己场上有魔法师族怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n③：这张卡被战斗·效果破坏送去墓地时才能发动。从卡组把「电豪灵蜥」以外的1只攻击力1500/守备力200的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40907115,'灵兽使 文',replace(replace('自己对「灵兽使 文」1回合只能有1次特殊召唤。\r\n①：这张卡召唤成功的场合，以除外的1只自己的「灵兽」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40908371,'苍眼银龙',replace(replace('调整＋调整以外的通常怪兽1只以上\r\n①：这张卡特殊召唤成功的场合发动。自己场上的龙族怪兽直到下个回合的结束时不会成为效果的对象，不会被效果破坏。\r\n②：每次自己准备阶段以自己墓地1只通常怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'效果耐性','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40916023,'水之精灵 阿库艾丽娅','这张卡不能通常召唤。自己墓地1只水属性怪兽从游戏中除外特殊召唤上场。每次对方的准备阶段可以选对方的1只表侧表示的怪兽改变它的表示形式。被改变表示形式的怪兽这个回合不能改变表示形式。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40921545,'入魔鲸人','把这张卡解放发动。选择对方场上存在的1张魔法·陷阱卡破坏。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40921744,'堕天使 杰拉特',replace(replace('自己墓地有暗属性怪兽4种类以上存在的场合，这张卡可以把1只暗属性怪兽解放作上级召唤。\r\n①：从手卡把1只暗属性怪兽送去墓地才能发动。对方场上的怪兽全部破坏。\r\n②：这张卡的①的效果发动的回合的结束阶段发动。这张卡破坏。','\r',char(13)),'\n',char(10)),'把1只暗属性怪兽解放上级召唤','对方场上存在的怪兽全部破坏','这张卡破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40933924,'黑蝎盗掘团','这张卡给与对方玩家战斗伤害时，对方从卡组选择1张魔法卡送去墓地，之后卡组洗切。','选择1张魔法卡送去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40937767,'墓穴食人魔','只要自己场上存在里侧守备表示的怪兽，这张卡不能被选择为攻击对象。每次自己场上的怪兽反转召唤，给与对方300分伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40941889,'异热同心武器-阿修罗副腕','自己的主要阶段时，手卡或者自己场上的这只怪兽可以当作攻击力上升1000的装备卡使用给自己场上的名字带有「希望皇 霍普」的怪兽装备。装备怪兽可以向对方场上的全部怪兽各作1次攻击。「异热同心武器-阿修罗副腕」在自己场上只能有1张表侧表示存在。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40945356,'黄昏之中忍-日轮',replace(replace('这张卡在规则上也当作「忍者」卡使用。\r\n①：1回合1次，可以从手卡丢弃1只「忍者」怪兽，从以下效果选择1个发动。这个效果在对方回合也能发动。\r\n●这个回合，自己场上的「忍者」怪兽以及「忍法」卡不会被战斗·效果破坏。\r\n●选自己场上1只「忍者」怪兽，那个攻击力直到回合结束时上升1000。','\r',char(13)),'\n',char(10)),'破坏耐性','攻击上升1000','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40975574,'红莲共鸣者',replace(replace('「红莲共鸣者」的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从手卡把1只4星以下的怪兽特殊召唤。\r\n②：这张卡特殊召唤成功时，以场上1只表侧表示怪兽为对象才能发动。自己回复那只怪兽的攻击力数值的基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(40991587,'白骨夫人','这张卡的卡名，在墓地作为「白骨」使用。此外，只要这张卡在自己场上表侧表示存在，场上表侧表示存在的「白骨夫人」以外的3星以下的不死族怪兽不会被战斗破坏，也不受魔法·陷阱的效果影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41006930,'速攻的黑衣忍者','把自己墓地里的2只暗属性怪兽从游戏中除外，这张卡即可从游戏中除外直到此回合的结束阶段。此效果在对方回合也可以使用。此效果每回合只能使用1次。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41039846,'乌鸦巨群','这张卡不能从卡组特殊召唤。这张卡1回合只有1次可以变成里侧守备表示。这张卡反转召唤成功时，对方手卡随机丢弃1张。','变成里侧表示','手牌丢弃','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41061625,'椰树','有意志的椰子树。以落下的果实进行攻击。果实中的椰汁味道鲜美。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41077745,'元素英雄 冰棱侠','1回合1次，自己的主要阶段一丢弃1张手卡发动。这个回合这张卡可以直接攻击对方玩家。此外，这张卡直接攻击给与对方基本分战斗伤害时，可以把对方的魔法与陷阱卡区域盖放的1张卡破坏。','直接攻击','魔陷破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41089128,'火焰支配者','祭品召唤炎属性怪兽的场合，这只怪兽1只作为2只份的祭品使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41090784,'冰结界的大僧正','这张卡召唤·反转召唤成功时，变成守备表示。只要这张卡在场上表侧表示存在，自己场上表侧表示存在的名字带有「冰结界」的怪兽不会被魔法·陷阱卡的效果破坏。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41091257,'秘旋谍-花公子',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡在手卡存在的场合，宣言卡的种类（怪兽·魔法·陷阱）才能发动。对方卡组最上面的卡给双方确认，宣言的种类的卡的场合，这张卡从手卡特殊召唤。\r\n②：这张卡用「秘旋谍」卡的效果特殊召唤成功的场合，以对方场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41097056,'同调破解','①：以自己场上1只同调怪兽为对象才能发动。那只怪兽回到持有者的额外卡组，持有那只同调怪兽的原本攻击力以下的攻击力的对方场上的表侧表示怪兽全部破坏。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41098335,'战士猿','这张卡战斗破坏对方怪兽的场合，这张卡的攻击力上升300。这张卡在自己回合没有进行攻击的场合，这个效果上升的数值在那个回合的结束阶段时回到0。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41113025,'圆盘骑手','把自己墓地存在的1张通常陷阱卡从游戏中除外，这张卡的攻击力直到对方回合的结束阶段时上升500。这个效果1回合只能使用1次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41114306,'方界兽 暗黑之甘尼克斯',replace(replace('这张卡不能通常召唤。把自己场上1只「方界」怪兽送去墓地的场合可以特殊召唤。\r\n①：这个方法特殊召唤的这张卡的攻击力上升1000。\r\n②：这张卡战斗破坏怪兽时，以自己墓地最多2只「方界胤 毗贾姆」为对象才能发动。这张卡送去墓地，作为对象的怪兽特殊召唤。那之后，可以从卡组把1只「方界兽 利刃之迦楼迪亚」加入手卡。','\r',char(13)),'\n',char(10)),'是否把1只「方界兽 利刃之迦楼迪亚」加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41128647,'雾动阵·输力',replace(replace('①：场上的「雾动机龙」怪兽的攻击力·守备力上升300。\r\n②：自己的「雾动机龙」怪兽进行战斗的场合，对方直到伤害步骤结束时魔法·陷阱·怪兽的效果不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41141943,'超重武者 飞脚-Q',replace(replace('「超重武者 飞脚-Q」的②的效果1回合只能使用1次。\r\n①：自己墓地没有魔法·陷阱卡存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤成功的回合，自己不是「超重武者」怪兽不能特殊召唤。\r\n②：自己墓地没有魔法·陷阱卡存在的场合，把这张卡解放才能发动。从手卡把最多2只怪兽在对方场上守备表示特殊召唤。那之后，自己从卡组抽出这个效果特殊召唤的怪兽的数量。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41142615,'开朗的殡葬师','从自己的手卡中丢弃最多3张怪兽卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41147577,'混沌超量 风纪大宫司 祭文',replace(replace('7星怪兽×3\r\n这张卡不受这张卡以外的怪兽的效果影响。此外，这张卡有「风纪宫司 祝词」在作为超量素材的场合，得到以下效果。\r\n●1回合1次，把这张卡1个超量素材取除，选择对方场上1只怪兽才能发动。选择的怪兽的表示形式变更，那只怪兽的效果直到回合结束时无效。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41158734,'阿普卡提基尔','自己场上有调整表侧表示存在的场合，场上表侧表示存在的这张卡的等级当作5星使用。场上存在的这张卡被破坏送去墓地时，可以选择自己墓地存在的1只「太阳之神官」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41160533,'蔷薇藤蔓','这张卡不能特殊召唤。自己的战斗阶段开始时对方场上有表侧表示植物族怪兽存在的场合，这个回合这张卡可以在通常攻击外加上那些植物族怪兽数量的攻击。这张卡战斗破坏植物族怪兽的场合，给与对方基本分300分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41160595,'神召魔法','对方控制的魔法·陷阱卡的效果把盖伏的这张卡破坏送去墓地时，从卡组选择1张速攻魔法卡给双方确认并在自己场上盖伏。','检索盖放','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41172955,'绿色零件','①：这张卡召唤·特殊召唤成功时才能发动。从卡组把1只「红色零件」加入手卡。','选1只「红色零件」加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41175645,'沉默魔术师',replace(replace('这张卡不能通常召唤。把自己场上1只魔法师族怪兽解放的场合才能特殊召唤。\r\n①：这张卡的攻击力上升自己手卡数量×500。\r\n②：1回合1次，魔法卡发动时才能发动。那个发动无效。\r\n③：场上的这张卡被战斗或者对方的效果破坏的场合才能发动。从手卡·卡组把「沉默魔术师」以外的1只「沉默魔术师」怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41181774,'地缚神 维拉科查·拉斯卡','名字带有「地缚神」的怪兽在场上只能有1只表侧表示存在。场上没有表侧表示场地魔法卡存在的场合这张卡破坏。对方不能选择这张卡作为攻击对象。这张卡可以直接攻击对方玩家。这张卡召唤成功时，这张卡以外的自己场上最多3张卡回到卡组，回去数量的对方手卡随机丢弃，这张卡的攻击力上升丢弃数量×1000的数值。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41182875,'标枪甲虫的契约','「标枪甲虫」的降临必要。必须从场上或者手札，牺牲奉献等级合计为8个以上的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41197012,'真红莲地毯','①：场上有龙族同调怪兽存在的场合，以自己墓地最多2只「共鸣者」怪兽为对象才能发动。那些怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41201386,'升阶魔法-急袭之力',replace(replace('①：以自己场上1只超量怪兽为对象才能发动。比那只怪兽阶级高1阶的1只「急袭猛禽」怪兽在作为对象的自己怪兽上面重叠当作超量召唤从额外卡组特殊召唤。\r\n②：把墓地的这张卡和手卡1张「急袭猛禽」卡除外，以「升阶魔法-急袭之力」以外的自己墓地1张「升阶魔法」魔法卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41201555,'核成孕妇蔷薇','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只植物族怪兽给对方观看。或者都不进行让这张卡破坏。自己的准备阶段时只有1次，可以从自己卡组把1只3星以下的怪兽送去墓地。','选择一张「核成兽的钢核」送去墓地','选择一张植物族怪兽给对方观看','破坏「核成孕妇蔷薇」','送墓','','','','','','','','','','','','');
INSERT INTO texts VALUES(41209827,'凶饿毒融合龙',replace(replace('衍生物以外的场上的暗属性怪兽×2\r\n①：这张卡融合召唤成功的场合才能发动。选对方场上1只特殊召唤的怪兽，这张卡的攻击力直到回合结束时上升那个攻击力数值。\r\n②：1回合1次，以对方场上1只5星以上的怪兽为对象才能发动。直到回合结束时，这张卡当作和那只怪兽同名卡使用，得到相同效果。\r\n③：融合召唤的这张卡被破坏的场合才能发动。对方场上的特殊召唤的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'攻击力上升','复制效果','结束复制效果','全部破坏','','','','','','','','','','','','');
INSERT INTO texts VALUES(41209828,'凶饿毒融合龙',replace(replace('衍生物以外的场上的暗属性怪兽×2\r\n①：这张卡融合召唤成功的场合才能发动。选对方场上1只特殊召唤的怪兽，这张卡的攻击力直到回合结束时上升那个攻击力数值。\r\n②：1回合1次，以对方场上1只5星以上的怪兽为对象才能发动。直到回合结束时，这张卡当作和那只怪兽同名卡使用，得到相同效果。\r\n③：融合召唤的这张卡被破坏的场合才能发动。对方场上的特殊召唤的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'攻击力上升','复制效果','结束复制效果','全部破坏','','','','','','','','','','','','');
