INSERT INTO texts VALUES(41224658,'野望之高弗','1回合1次，选择对方场上存在的最多2只怪兽才能发动。对方可以把手卡1只怪兽给人观看让这张卡的效果无效。不给观看的场合，选择的怪兽破坏。','破坏','是否要把一只怪兽给对方观看？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41230939,'电子暗黑魔角',replace(replace('①：这张卡召唤成功的场合，以自己墓地1只3星以下的龙族怪兽为对象发动。那只龙族怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡的攻击力上升这张卡的效果装备的怪兽的原本攻击力数值。\r\n③：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n④：这张卡被战斗破坏的场合，作为代替把这张卡的效果装备的怪兽破坏。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41234315,'伪爆炸五星','对方怪兽的攻击宣言时才能发动。怪兽不会被那次战斗破坏，伤害计算后从自己的手卡或者墓地把1只「召唤反应机·大式」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
