INSERT INTO texts VALUES(41356845,'蓄积硫酸的落穴','①：以场上1只里侧守备表示怪兽为对象才能发动。那只怪兽变成表侧守备表示，守备力是2000以下的场合破坏。守备力比2000高的场合回到里侧守备表示。','','','','','','','','','','','','','','','','');
