INSERT INTO texts VALUES(41420027,'神之宣告',replace(replace('①：可以把基本分支付一半把以下效果发动。\r\n●魔法·陷阱卡发动时才能发动。那个发动无效并破坏。\r\n●自己或者对方把怪兽召唤·反转召唤·特殊召唤之际才能发动。那个无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
