INSERT INTO texts VALUES(41493640,'行列怪兽 骑士之桂马',replace(replace('这张卡召唤成功时，选择对方的魔法与陷阱卡区域盖放的1张卡才能发动。把盖放的那张卡确认，陷阱卡的场合，那张卡破坏。不是的场合，回到原状。这个效果把陷阱卡破坏时，可以把以下效果发动。\r\n●从手卡把1只地属性·3星怪兽表侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
