INSERT INTO texts VALUES(41554273,'雾动机龙突进',replace(replace('「雾动机龙突进」在1回合只能发动1张。\r\n①：从卡组把1只「雾动机龙」怪兽特殊召唤。这个效果特殊召唤的怪兽不受其他卡的效果影响，结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41578483,'千年眼纳祭神',replace(replace('「纳祭之魔」＋效果怪兽\r\n①：1回合1次，对方怪兽的效果发动时，以对方的场上·墓地1只效果怪兽为对象才能发动。那只对方的效果怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡的攻击力·守备力上升这张卡的效果装备的怪兽的各自数值。\r\n③：和这张卡的效果装备的怪兽同名的怪兽不能攻击，那个效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41587307,'折断的竹光','装备怪兽的攻击力上升0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41589166,'天下人 紫炎','这张卡不受陷阱卡的效果影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41613948,'命运英雄 恶魔人','这张卡在自己场上表侧攻击表示存在的场合，1回合只有1次，可以把1只对方怪兽从游戏中除外。使用这个效果的玩家在这个回合不能进行战斗。这个效果除外的怪兽在第2次自己的准备阶段时以相同的表示形式回到对方场上。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41620959,'龙之灵庙',replace(replace('「龙之灵庙」在1回合只能发动1张。\r\n①：从卡组把1只龙族怪兽送去墓地。并且这个效果送去墓地的怪兽是龙族的通常怪兽的场合，可以再从卡组把1只龙族怪兽送去墓地。','\r',char(13)),'\n',char(10)),'是否再从卡组把1只龙族怪兽送去墓地？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41628550,'超重武者 鬼若-O2',replace(replace('①：这张卡召唤·特殊召唤成功时才能发动。这张卡的表示形式变更。\r\n②：这张卡不会被战斗破坏。','\r',char(13)),'\n',char(10)),'表示变更','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41639001,'圣刻龙-半龙努特','场上表侧表示存在的这张卡成为魔法·陷阱·效果怪兽的效果的对象时发动。从自己的手卡·卡组·墓地选1只龙族的通常怪兽，攻击力·守备力变成0特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41705642,'打捞战士','这张卡上级召唤成功时，可以从手卡或者自己墓地把1只调整特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41721210,'龙骑士 黑魔术师',replace(replace('「黑魔术师」＋龙族怪兽\r\n①：这张卡的卡名只要在场上·墓地存在当作「黑魔术师」使用。\r\n②：只要这张卡在怪兽区域存在，自己场上的魔法·陷阱卡不会成为对方的效果的对象，不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41722932,'暗黑灭绝切割','自己场上有暗属性怪兽3只以上存在的场合才能发动。选择场上表侧表示存在的1张卡从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41735184,'黑魔术的继承',replace(replace('「黑魔术的继承」在1回合只能发动1张。\r\n①：从自己墓地把2张魔法卡除外才能发动。把「黑魔术的继承」以外的有「黑魔术师」的卡名或者「黑魔术少女」的卡名记述的1张魔法·陷阱卡从卡组加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41741922,'溶岩龙','把自己场上表侧守备表示存在的这张卡解放发动。从自己的手卡以及墓地各把1只3星以下的龙族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41753322,'龙脚兽 腕龙',replace(replace('这张卡不能从卡组特殊召唤。这张卡可以把1只恐龙族怪兽解放表侧表示上级召唤。\r\n①：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n②：这张卡反转召唤成功的场合发动。这张卡以外的场上的怪兽全部变成里侧守备表示。\r\n③：这张卡被攻击的场合，那次战斗发生的对对方的战斗伤害变成2倍。','\r',char(13)),'\n',char(10)),'把1只恐龙族怪兽解放上级召唤','变成里侧守备','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41762634,'吸血跳蚤','攻击力高强的巨型吸血跳蚤，轻视它会非常危险。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41767843,'幻奏的音女 丝可尔','①：自己的「幻奏」怪兽和对方怪兽进行战斗的伤害计算时，把这张卡从手卡送去墓地才能发动。那只对方怪兽的攻击力·守备力直到回合结束时变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41782653,'超顶科技血神翼龙',replace(replace('这张卡不能通常召唤。让除外的5只自己的恐龙族怪兽回到卡组的场合才能特殊召唤。这个卡名的②的效果1回合只能使用1次。\r\n①：1回合1次，对方把魔法·陷阱卡发动时才能发动。选自己的手卡·场上1只恐龙族怪兽破坏，那个发动无效并破坏。\r\n②：这张卡被效果送去墓地的场合才能发动。从卡组把1张「进化药」魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41788781,'极星兽 古尔法克西','对方场上有同调怪兽表侧表示存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41855169,'升灵术师 奘玄','手卡随机丢弃1张卡去墓地。这个场合，场上的特殊召唤的怪兽全部破坏。只要这张卡在场上存在，不能特殊召唤怪兽。','特殊召唤的怪兽全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41858121,'大日物质',replace(replace('选择自己墓地存在的3张装备魔法卡发动。选择的卡加入卡组洗切，从以下效果选择1个适用。\r\n●从自己卡组抽1张卡。\r\n●对方场上存在的1张卡破坏。','\r',char(13)),'\n',char(10)),'从自己卡组抽1张卡。','对方场上存在的1张卡破坏。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41859700,'燃藻','当这张卡被送去墓地时，对方回复1000基本分。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41872150,'蝗虫的军势','这张卡1个回合可以有1次变回里侧守备表示。这张卡反转召唤成功时，破坏对方场上1张魔法·陷阱卡。','变成里侧守备表示','破坏对方1张魔法·陷阱卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41902352,'黑羽-东云之东风','特殊召唤的这张卡不能作为同调素材。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41925941,'冥王的咆哮','自己的场上的恶魔族进行战斗的场合，在那个伤害计算阶段发动。支付自己的基本分（以100为单位），在结束阶段前对方1只怪兽攻击力守备力下降那个支付基本分的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41930553,'暗黑瘴气','1回合1次，选择对方墓地1只怪兽才能发动。从手卡丢弃1只恶魔族怪兽，选择的怪兽从游戏中除外。','是否现在使用「暗黑烟雾」的效果？','丢弃手牌并除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41940225,'破坏剑士融合',replace(replace('「破坏剑士融合」的①②的效果1回合各能使用1次。\r\n①：从自己手卡以及自己·对方场上把融合怪兽卡决定的融合素材怪兽送去墓地，把以「巴斯达·布雷达」为融合素材的那1只融合怪兽从额外卡组融合召唤。\r\n②：这张卡在墓地存在的场合，把1张手卡送去墓地才能发动。墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'融合召唤','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41949033,'暗之暗杀者','持有精神之剑、君临魔界的暗杀者。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41952656,'寻你海豚','场上的这张卡被对方破坏送去墓地时，自己卡组最上面的卡送去墓地。送去墓地的卡是水属性怪兽的场合，从自己卡组抽1张卡。','卡组送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41978142,'霞之谷的执行者','这张卡召唤成功时，场上表侧表示存在的魔法·陷阱卡全部回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41999284,'连接栗子球',replace(replace('1星怪兽1只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：对方怪兽的攻击宣言时，把这张卡解放才能发动。那只对方怪兽的攻击力直到回合结束时变成0。\r\n②：这张卡在墓地存在的场合，把自己场上1只1星怪兽解放才能发动。这张卡从墓地特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42002073,'娱乐伙伴 扑克少女',replace(replace('←4 【灵摆】 4→\r\n【怪兽效果】\r\n①：1回合1次，自己主要阶段才能发动。融合怪兽卡决定的包含这张卡的融合素材怪兽从自己场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：这张卡在灵摆区域被破坏的场合，以自己墓地1只龙族的融合怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽在结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42009836,'化石恐龙 肿头龙',replace(replace('①：这张卡反转的场合发动。场上的特殊召唤的怪兽全部破坏。\r\n②：只要这张卡在怪兽区域存在，双方不能把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤的怪兽全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42015635,'新宇宙','「元素英雄 新宇侠」以及用「元素英雄 新宇侠」作为融合素材的融合怪兽的攻击力上升500。用「元素英雄 新宇侠」作为融合素材的融合怪兽在结束阶段时可以不发动回到卡组效果。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42023223,'电磁石战士α',replace(replace('「电磁石战士α」的①的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1只8星的「磁石战士」怪兽加入手卡。\r\n②：对方回合把这张卡解放才能发动。从卡组把1只4星的「磁石战士」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'加入手卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42024143,'XX-剑士 嘉德斯特莱克','若自己墓地存在2只以上名字带有「X-剑士」的怪兽，且自己场上不存在怪兽的场合，可以从自己的手卡特殊召唤这张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42029847,'幻奏的音女 塞瑞娜',replace(replace('①：天使族怪兽上级召唤的场合，这张卡可以作为2只的数量解放。\r\n②：这张卡特殊召唤成功的回合，自己在通常召唤外加上只有1次，自己主要阶段可以把1只「幻奏」怪兽召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42035044,'漆黑之豹战士','①：只要这张卡在怪兽区域存在，这张卡的攻击宣言之际，自己必须把这张卡以外的自己场上1只怪兽解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42035045,'漆黑之豹战士','①：只要这张卡在怪兽区域存在，这张卡的攻击宣言之际，自己必须把这张卡以外的自己场上1只怪兽解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42071342,'暗黑之海龙兵','守护暗黑之海的战士。在水中的战斗力就不用多说了，即使是在陆地上也同时拥有很高的战斗能力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42079445,'失星降落','①：以自己墓地1只同调怪兽为对象才能发动。那只怪兽的等级下降1星，守备力变成0守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化，不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42082363,'薰风狮鹫','这张卡从手卡送去墓地的场合，可以从自己卡组把1只名字带有「薰风」的怪兽特殊召唤。「薰风狮鹫」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42110434,'自然蝴蝶','1回合1次，对方怪兽的攻击宣言时才能发动。把自己卡组最上面1张卡送去墓地，那次攻击无效。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42110604,'高速疾行机人 比剑骑手',replace(replace('调整＋调整以外的怪兽1只以上\r\n自己对「高速疾行机人 比剑骑手」1回合只能有1次特殊召唤。\r\n①：这张卡在同1次的战斗阶段中可以作2次攻击。\r\n②：这张卡进行战斗的伤害步骤开始时发动。这张卡的攻击力上升200。\r\n③：这张卡被送去墓地的场合，以除外的1张自己的「疾行机人」卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'攻击上升','加入手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42129512,'巨大树熊','非常巨大的死亡树熊的一种。虽然性格温顺。但是因为拥有非常强大的力量而令人感到恐惧。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42143067,'怒气土器',replace(replace('「怒气土器」的效果1回合只能使用1次。\r\n①：把手卡1只岩石族怪兽丢弃才能发动。原本的属性·等级和那只怪兽相同的1只岩石族怪兽从卡组表侧攻击表示或者里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42155488,'基因组斗士','这张卡可以不用解放作召唤。这个方法召唤的这张卡的等级变成3星，原本攻击力变成一半。此外，1回合1次，宣言1个种族才能发动。这个回合把这张卡作为同调素材的场合，包含这张卡的那一组同调素材怪兽当作宣言的种族使用。这个效果发动过的回合，自己不能把宣言的种族以外的怪兽召唤·特殊召唤。','不用解放作召唤','素材变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42160203,'霸王眷龙 暗叛逆',replace(replace('暗属性4星灵摆怪兽×2\r\n①：1回合1次，这张卡和对方怪兽进行战斗的伤害计算前，把这张卡1个超量素材取除才能发动。直到回合结束时，那只对方怪兽的攻击力变成0，这张卡的攻击力上升那个原本攻击力数值。\r\n②：自己·对方的战斗阶段让这张卡回到额外卡组才能发动。从自己的额外卡组的表侧表示的灵摆怪兽之中选「霸王眷龙」怪兽或者「霸王门」怪兽合计最多2只守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'攻击力上升','回到卡组并特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42167046,'万能地雷未爆弹','选择场上表侧攻击表示存在的2只怪兽发动。选择的怪兽从场上离开时，这张卡破坏。这张卡破坏时，选择的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42175079,'大喷火','自己场上有「侏罗纪世界」存在的场合，自己的结束阶段时才能发动。场上的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42199039,'妖刀竹光',replace(replace('「妖刀竹光」的②的效果1回合只能使用1次。\r\n①：装备怪兽的攻击力上升0。\r\n②：以这张卡以外的自己场上1张「竹光」卡为对象才能发动。那张卡回到持有者手卡，装备怪兽在这个回合可以向对方直接攻击。\r\n③：这张卡被送去墓地的场合才能发动。从卡组把「妖刀竹光」以外的1张「竹光」卡加入手卡。','\r',char(13)),'\n',char(10)),'回到手卡','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42216237,'杰拉的天使',replace(replace('调整＋调整以外的怪兽1只以上\r\n「杰拉的天使」的②的效果1回合只能使用1次。\r\n①：这张卡的攻击力上升除外的对方的卡数量×100。\r\n②：这张卡被除外的场合，下个回合的准备阶段发动。除外的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42233477,'野蛮人之怒','①：以自己场上1只战士族怪兽为对象才能把这张卡发动。那只怪兽的攻击力上升1000，那只怪兽战斗破坏的怪兽不送去墓地回到持有者手卡。作为对象的怪兽从场上离开时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42237854,'机动要塞 铁堡垒',replace(replace('①：以自己场上的机械族·4星怪兽任意数量为对象才能发动。这张卡发动后变成效果怪兽（机械族·地·4星·攻/守0）在怪兽区域特殊召唤。那之后，作为对象的表侧表示怪兽当作装备卡使用给这张卡装备。这张卡也当作陷阱卡使用。\r\n②：这张卡的效果特殊召唤的这张卡的攻击力上升这张卡的效果装备的怪兽的攻击力的合计数值，对方不能把其他的自己场上的怪兽作为攻击对象，也不能作为效果的对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42239546,'茧状体·小鼹鼠','场上有「新宇宙」存在时，可以把这张卡作为祭品从手卡·卡组特殊召唤1只「新空间侠·大地鼹鼠」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42256406,'卡片防卫士','这张卡召唤·反转召唤·特殊召唤成功时，变成守备表示。自己场上表侧表示存在的怪兽被选择作为攻击对象时，可以让攻击对象改变为这张卡。这张卡成为攻击对象时，可以把自己卡组的卡从上面最多3张送去墓地。每把1张卡送去墓地，这张卡的守备力直到结束阶段时上升500。','变成守备表示','改变攻击对象','守备上升','请选择要送去墓地的卡的数量','','','','','','','','','','','','');
INSERT INTO texts VALUES(42280216,'太阳之神官','对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。场上存在的这张卡被破坏送去墓地时，可以从自己卡组把1只「赤蚁」或者「苏帕伊」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42291297,'花札卫-雨四光-',replace(replace('调整＋调整以外的怪兽3只\r\n①：只要这张卡在怪兽区域存在，自己场上的「花札卫」怪兽不会被效果破坏，不会成为对方的效果的对象。\r\n②：对方抽卡阶段对方通常抽卡的场合发动。给与对方1500伤害。\r\n③：对方结束阶段从以下效果选择1个发动。\r\n●下次的自己回合的抽卡阶段跳过。\r\n●这张卡的效果直到下次的对方准备阶段无效。','\r',char(13)),'\n',char(10)),'给与对方1500伤害','下次的自己回合的抽卡阶段跳过','这张卡的效果直到下次的对方准备阶段无效','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42303365,'爬虫妖蛇','这张卡召唤成功时，可以选择对方场上表侧表示存在的1只攻击力是0的怪兽得到控制权。','获得控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42309337,'反击反击','反击陷阱的发动无效，并把那个破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42314669,'完全防音壁','自己场上没有同调怪兽表侧表示存在的场合，主要阶段1的开始时才能发动。直到下次的对方的结束阶段时，双方不能把同调怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42328171,'发条蝙蝠','自己的主要阶段时才能发动。把自己场上表侧攻击表示存在的这张卡变更为表侧守备表示，选择自己墓地存在的1只名字带有「发条」的怪兽加入手卡。这个效果只在这张卡在场上表侧表示存在能使用1次。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42338879,'喔喔公鸡',replace(replace('①：双方场上没有怪兽存在的场合，这张卡可以作为3星怪兽从手卡特殊召唤。\r\n②：对方场上有怪兽存在，自己场上没有卡存在的场合，这张卡可以作为4星怪兽从手卡特殊召唤。\r\n③：表侧表示的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'作为3星怪兽特殊召唤','作为4星怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42348802,'虎纹龙','虎型恐龙。跑过旷野的速度相当快。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42364257,'避雷针','对方使用了「雷击」的时候，破坏对方全部怪兽代替自己的怪兽。发动后这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42364374,'甲蝇','自己的场上没有这张卡以外的昆虫族存在，这张卡的攻击力守备力变成1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42378577,'灵摆切换',replace(replace('这个卡名的①②的效果1回合只能有1次使用其中任意1个。\r\n①：以自己的灵摆区域1张卡为对象才能发动。那张卡特殊召唤。\r\n②：以自己的怪兽区域1只灵摆怪兽为对象才能发动。那只灵摆怪兽在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'灵摆区域怪兽特殊召唤','灵摆怪兽在灵摆区域放置','请选择要放置到灵摆区域的卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42386471,'卡通双生精灵',replace(replace('①：这张卡在召唤·反转召唤·特殊召唤的回合不能攻击。\r\n②：自己场上有「卡通世界」存在，对方场上没有卡通怪兽存在的场合，这张卡可以直接攻击。\r\n③：这张卡给与对方战斗伤害时才能发动。对方手卡随机选1张丢弃。\r\n④：场上的「卡通世界」被破坏时这张卡破坏。','\r',char(13)),'\n',char(10)),'丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42391240,'星圣·心宿二','这张卡召唤·特殊召唤成功时，可以选择自己墓地1只名字带有「星圣」的怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42418084,'诞生的天使','据说它能知道女性腹部是否存在着生命。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42421606,'No.85 狂箱',replace(replace('4星怪兽×2\r\n这张卡不能攻击。1回合1次，把这张卡1个超量素材取除才能发动。掷1次骰子，根据出现的数目适用以下效果。\r\n1·自己基本分变成一半\r\n2·自己从卡组抽1张卡\r\n3·对方选1张手卡丢弃\r\n4·选场上1张卡，那个效果直到回合结束时无效\r\n5·选场上1张卡破坏\r\n6·这张卡破坏','\r',char(13)),'\n',char(10)),'投掷骰子','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42425831,'雷遁封印式','1回合1次，可以让自己场上表侧表示存在的1只风属性怪兽回到卡组最下面，选择对方墓地最多2张卡从游戏中除外。','除外','是否现在使用「雷遁封印式」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42431843,'魔天老','被天界放逐的堕天使，在暗域中的战斗很出色。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42444868,'轮回之珀耳修斯','①：怪兽的效果·魔法·陷阱卡发动时，把手卡1张反击陷阱卡给对方观看，丢弃1张手卡，支付1000基本分才能发动。那个发动无效，那张卡回到持有者卡组。那之后，可以从卡组·额外卡组选1只「珀耳修斯」怪兽特殊召唤。','是否特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42461852,'电脑网风暴',replace(replace('①：场上的连接状态的怪兽的攻击力·守备力上升500。\r\n②：只要这张卡在场地区域存在，连接怪兽的连接召唤不会被无效化。\r\n③：自己受到2000以上的战斗·效果伤害的场合才能发动。只把自己的额外卡组的里侧表示的卡洗切，那张最上面的卡翻开。翻开的卡是电子界族连接怪兽的场合，那只怪兽特殊召唤。不是的场合回到原状。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42463414,'刺鱼人','只要这张卡在场上表侧表示存在，自己场上表侧表示存在的鱼族·海龙族·水族怪兽的攻击力上升400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42502956,'艾尔斯岩的日出',replace(replace('「艾尔斯岩的日出」在1回合只能发动1张。\r\n①：以自己墓地1只兽族怪兽为对象才能发动。那只兽族怪兽特殊召唤，对方场上的怪兽的攻击力直到回合结束时下降自己墓地的兽族·鸟兽族·植物族怪兽数量×200。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42534368,'沉默的死者','①：以自己墓地1只通常怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42541548,'鬼哥布林','只要这张卡在自己场上以表侧表示存在，在自己回合的结束阶段时，可以将1张通常怪兽卡从手卡放回卡组最下方，再从卡组抽1张卡。此效果每回合只能使用1次。','交换','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42548470,'摇晃的发条秤','选择自己场上表侧表示存在的2只等级不同的名字带有「发条」的怪兽发动。对方选那之内1只，另1只怪兽的等级直到结束阶段时变成和对方选的怪兽相同。对方选等级低的怪兽的场合，那之后自己可以从卡组抽1张卡。','是否抽卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42551040,'武神器-生','自己的主要阶段时，自己场上有名字带有「武神」的怪兽存在的场合，把墓地的这张卡从游戏中除外，选择对方场上表侧攻击表示存在的1只怪兽才能发动。选择的怪兽变成表侧守备表示，那个守备力直到结束阶段时变成0。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42562690,'娱乐伙伴 小艇水黾','①：攻击表示的这张卡被选择作为攻击对象时才能发动。这张卡变成表侧守备表示，那次攻击无效。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42566602,'瑚之龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：1回合1次，丢弃1张手卡，以对方场上1张卡为对象才能发动。那张卡破坏。\r\n②：同调召唤的这张卡被送去墓地的场合才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'卡片破坏','抽卡','','','','','','','','','','','','','本数据库来自23333服，如果您获得的版本未标明出处','则那个版本是可耻的盗版');
INSERT INTO texts VALUES(42578427,'捕鼠夹','对方召唤·反转召唤的怪兽的攻击力在500以下的场合，可以把那1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42589641,'星辉士 冬三角',replace(replace('4星「星守」怪兽×3\r\n把这张卡超量召唤的回合，自己不是「星守」怪兽不能特殊召唤。\r\n①：这张卡超量召唤成功的场合发动。这张卡以外的场上的卡全部回到持有者手卡。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。对方手卡随机选1张送去墓地。\r\n③：持有超量素材的这张卡被送去墓地的场合，以自己墓地1只「星守」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'回到手卡','手卡破坏','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42591472,'笑花','总是在笑着的花。如果持续听它的笑声的话，头脑就会变得混乱。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42592719,'剑斗兽 亚历山大','「剑斗兽 双斗」以外的效果不能把这张卡特殊召唤。特殊召唤的这张卡只要在自己场上表侧表示存在，不受魔法的效果的影响。这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组，从卡组把「剑斗兽 亚历山大」以外的1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42598242,'特殊飓风','丢弃1张手卡。破坏场上存在的所有特殊召唤的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42599677,'火焰杀手','使用火焰盾的剑士，之盾能无视任何攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42600274,'电子化芭蕾练习裙',replace(replace('①：这张卡可以从自己的手卡·场上把1只战士族或者天使族怪兽解放从手卡特殊召唤。\r\n②：这张卡为仪式召唤而被解放的场合，以自己墓地1张仪式魔法卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'出场方式为特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42625254,'小霸王龙','恐龙的孩子。性格极其凶暴。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42647539,'石像怪道化师','这张卡片召唤·反转召唤·特殊召唤时可以使对方1只表侧表示的怪兽的表示形式改变。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42664989,'天降的宝札','①：把自己的手卡·场上的卡全部除外才能发动。自己直到手卡变成2张为止从卡组抽卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42671151,'芙莉嘉的苹果','①：自己场上没有怪兽存在，自己受到战斗伤害时才能发动。自己基本分回复受到的伤害的数值，在自己场上把1只「邪精衍生物」（恶魔族·暗·1星·攻/守?）特殊召唤。这衍生物的攻击力·守备力变成和这个效果让自己回复的数值相同。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42671152,'邪精衍生物','由「芙莉嘉的苹果」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42679662,'入魔浑沌 阿撒托斯','反转：选择场上1只特殊召唤的怪兽回到持有者卡组。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42682609,'茧状体·小海豚','场上有「新宇宙」存在时，可以把这张卡作为祭品，从手卡·卡组特殊召唤1只「新空间侠·水波海豚」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42685062,'来自地狱的使者','这张卡不能特殊召唤。这张卡可以把1只怪兽解放作召唤。这个方法召唤的这张卡的等级变成5星，原本的攻击力·守备力变成一半数值。','解放１只怪兽召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42703248,'飓风','场上的魔法卡和陷阱卡全部回到控制者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42709949,'守护神之矛','装备怪兽的攻击力上升双方墓地存在的和装备怪兽同名的卡数量×900的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42713844,'地中族邪界兽·利古钻土蜥',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合，以对方场上1只怪兽为对象才能发动。那只怪兽除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42737833,'XX-剑士 埃默斯布莱德','①：这张卡被战斗破坏送去墓地时才能发动。从卡组把1只4星以下的「X-剑士」怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42752141,'进化帝·翔龙',replace(replace('恐龙族4星怪兽×2\r\n把这张卡1个超量素材取除才能发动。效果怪兽的效果的发动无效并破坏。','\r',char(13)),'\n',char(10)),'无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42776855,'追赶之翼',replace(replace('以自己场上1只同调怪兽为对象才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，作为对象的怪兽不会被战斗以及对方的效果破坏。\r\n②：作为对象的怪兽和5星以上的对方怪兽进行战斗的伤害步骤开始时才能发动。那只对方怪兽破坏。作为对象的怪兽的攻击力直到回合结束时上升这个效果破坏的怪兽的原本攻击力数值。\r\n③：作为对象的怪兽从场上离开的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42776960,'魂之接力','①：从手卡把1只怪兽特殊召唤。只要这个效果特殊召唤的怪兽在自己场上表侧表示存在，自己受到的全部伤害变成0。那只怪兽从场上离开时对方决斗胜利。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42790071,'幻变骚灵·多功能诈骗者',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己把陷阱卡发动的场合才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡特殊召唤成功的场合才能发动。从卡组把「幻变骚灵·多功能诈骗者」以外的1只「幻变骚灵」怪兽守备表示特殊召唤。这个效果发动的回合，自己不是「幻变骚灵」怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'这张卡从手卡特殊召唤','从卡组把「幻变骚灵」怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42793609,'极星宝 布里希嘉·曼','选择自己以及对方场上表侧表示存在的怪兽各1只发动。选择的自己怪兽的攻击力直到结束阶段时变成和选择的对方怪兽的原本攻击力相同攻击力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42810973,'废品弓手',replace(replace('「废品同调士」＋调整以外的怪兽1只以上\r\n1回合1次，选择对方场上存在的1只怪兽才能发动。选择的怪兽从游戏中除外。这个效果除外的怪兽在这个回合的结束阶段时以相同表示形式回到对方场上。','\r',char(13)),'\n',char(10)),'除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42829885,'强引的番兵','查看对方手卡。选择对方手卡中的1张卡回到卡组，对方卡组洗切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42851643,'穿孔重机 凿岩台车','这张卡召唤成功时，可以把自己场上的全部机械族怪兽的等级上升1星。这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。此外，这张卡攻击的场合，伤害步骤结束时变成守备表示。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42868711,'幻影筮龟','以场上表侧表示的这张卡为对象的，由对方所控制的魔法·陷阱卡的效果无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42874792,'发条兔','选择自己场上1只名字带有「发条」的怪兽才能发动。选择的怪兽直到下次的自己的准备阶段时从游戏中除外。这个效果在对方回合也能发动。此外，这个效果只在这张卡在场上表侧表示存在能使用1次。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42878636,'古代的机械猎犬',replace(replace('①：这张卡召唤成功的场合发动。给与对方600伤害。\r\n②：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。\r\n③：1回合1次，自己主要阶段才能发动。从自己的手卡·场上把「古代的机械」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42880485,'超重辉将 翡翠-E',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「超重武者」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n②：1回合1次，以自己场上1只「超重武者」怪兽为对象才能发动。那只怪兽的等级上升1星。\r\n【怪兽效果】\r\n这张卡在规则上也当作「超重武者」卡使用。这张卡可以把1只「超重武者」怪兽解放作上级召唤。\r\n①：这张卡召唤·特殊召唤成功时才能发动。这张卡的表示形式变更。\r\n②：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。','\r',char(13)),'\n',char(10)),'把1只「超重武者」怪兽解放作上级召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42883273,'森之住人 乌丹','场上每存在1只表侧表示的植物族怪兽，这张卡的攻击力上升100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42901635,'电磁石战士 电磁狂神',replace(replace('这张卡不能通常召唤。从自己的手卡·场上·墓地把「电磁石战士α」「电磁石战士β」「电磁石战士γ」各1只除外的场合可以特殊召唤。\r\n①：从自己墓地把1只4星以下的「磁石战士」怪兽除外，以对方场上1张卡为对象才能发动。那张卡破坏。\r\n②：这张卡被战斗或者对方的效果破坏的场合，以除外的自己的「电磁石战士α」「电磁石战士β」「电磁石战士γ」各1只为对象才能发动。那些怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42908201,'死魔袋獾','①：这张卡战斗破坏对方怪兽时才能发动。从卡组把1只4星以下的兽族怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42921475,'妖精传姬-塔利娅',replace(replace('「妖精传姬-塔利娅」的②的效果1回合只能使用1次。\r\n①：这张卡反转的场合才能发动。从手卡把1只怪兽特殊召唤。\r\n②：对方把通常魔法·通常陷阱卡发动时，把这张卡以外的自己场上1只怪兽解放才能发动。那个效果变成「选对方场上1只表侧表示怪兽变成里侧守备表示」。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42940404,'机甲机械骨架','这张卡召唤成功时，可以从自己卡组把「机甲机械骨架」以外的1只名字带有「机甲」的怪兽加入手卡。1回合1次，自己的主要阶段时可以当作装备卡使用给自己场上的机械族怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。（1只怪兽可以装备的同盟最多1张。装备怪兽被破坏的场合，作为代替把这张卡破坏。）','变成装备卡','特殊召唤','检索','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42941100,'扰乱·黄','使用一切手段捣乱的扰乱三人组中的一员。据说集齐了三人就会发生什么。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42945701,'火灵术-「红」','自己场上存在的1只炎属性怪兽做祭品。给与对方基本分祭品怪兽原本的攻击力的数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42956963,'梦魇恶魔群','①：把自己场上1只怪兽解放才能发动。在对方场上把3只「梦魇恶魔衍生物」（恶魔族·暗·6星·攻/守2000）攻击表示特殊召唤。「梦魇恶魔衍生物」被破坏时那控制者受到每1只800伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42956964,'梦魇恶魔衍生物','由「梦魇恶魔群」的效果特殊召唤。「梦魇恶魔衍生物」被破坏时，这衍生物的控制者受到每1只800分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42969214,'钢铁指猴','1回合1次，自己的主要阶段时可以让这张卡的攻击力上升400。这个效果发动的回合，这张卡不能攻击。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(42994702,'彷徨的木乃伊','这张卡1个回合可以有1次变回里侧守备表示。这个效果使用后，把自己场上的全部里侧守备表示的怪兽洗切，再次重新里侧守备表示按自己的顺序安排到场上的位置。','变成里侧守备','攻守上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43002864,'爬虫妖女守卫者','自己场上存在的这张卡被破坏送去墓地时，从自己卡组把1只名字带有「爬虫妖」的怪兽加入手卡。','卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43014054,'恶狂战士','这张卡作为同调召唤的素材送去墓地的场合，给与那个玩家2000分伤害。此外，这张卡为同调素材的同调怪兽的攻击力直到这个回合的结束阶段时上升2000。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43017476,'氘素龙',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己主要阶段把这张卡从手卡丢弃才能发动。从卡组把1张「化学结合」魔法·陷阱卡加入手卡。\r\n②：这张卡召唤·特殊召唤成功的场合，以自己墓地的「氢素龙」「氧素龙」「氘素龙」的其中1只为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43034264,'机壳的要塞',replace(replace('①：只要这张卡在场地区域存在，自己在通常召唤外加上只有1次，自己主要阶段可以把1只「机壳」怪兽召唤。\r\n②：只要这张卡在场地区域存在，「机壳」怪兽的召唤不会被无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43040603,'怪兽之门','①：把自己场上1只怪兽解放才能发动。直到可以通常召唤的怪兽出现为止从自己卡组上面翻卡，那只怪兽特殊召唤。剩下的翻开的卡全部送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43047672,'急袭猛禽-最终要塞猎鹰',replace(replace('12星怪兽×3\r\n①：有「急袭猛禽」超量怪兽在作为超量素材中的这张卡不受其他卡的效果影响。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。除外的自己的「急袭猛禽」怪兽全部回到墓地。\r\n③：这张卡的攻击破坏怪兽时，把自己墓地1只「急袭猛禽」超量怪兽除外才能发动。这张卡可以继续攻击。这个效果1回合可以使用最多2次。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43061293,'火焰飞镖','自己手卡0张的时候才能发动。投掷3次骰子。给与对方基本分那个投掷结果的合计数目×100数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43096270,'紫翠玉龙','拥有紫翠玉鳞的稀世珍龙。那美丽鳞片冠以古代帝王之名，被视为是神秘的象征。——它的获得者并未意识到已经抓住莫大的幸运。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43114901,'宝石骑士·红缟',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡战斗破坏对方怪兽送去墓地时，可以从自己卡组把1张名字带有「宝石骑士」的卡加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43138260,'超量印鲨','这张卡可以把自己场上2个超量素材取除，从手卡特殊召唤。这个方法特殊召唤成功时，可以选择自己墓地2只鱼族·4星怪兽表侧守备表示特殊召唤。这个效果特殊召唤的怪兽不能攻击，效果无效化，也不能作表示形式的变更。此外，把这个效果特殊召唤的怪兽作为超量素材的场合，不是水属性怪兽的超量召唤不能使用。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43140791,'虫饵',replace(replace('这张卡发动的回合，自己不能把3·4星的怪兽召唤·特殊召唤。\r\n①：自己场上有昆虫族怪兽存在的场合才能发动。在自己场上把2只「虫衍生物」（昆虫族·地·1星·攻/守0）特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43140792,'虫衍生物','由「虫饵」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43175858,'卡通王国',replace(replace('①：作为这张卡的发动时的效果处理，从自己卡组上面把3张卡里侧表示除外。\r\n②：这张卡的卡名只要在场地区域存在当作「卡通世界」使用。\r\n③：只要这张卡在场地区域存在，自己场上的卡通怪兽不会成为对方的效果的对象。\r\n④：自己场上的卡通怪兽被战斗·效果破坏的场合，可以作为代替把破坏的怪兽每1只1张卡从自己卡组上面里侧表示除外。','\r',char(13)),'\n',char(10)),'是否代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43191636,'龙影鱼 雷龙鳐',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当通常召唤使用的再度召唤，这张卡当作效果怪兽使用并得到以下效果。\r\n●这张卡的原本攻击力变成2300。这张卡攻击的场合，战斗阶段结束时变成守备表示。直到下次的自己回合结束时这张卡不能把表示形式改变。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43202238,'邪龙星-睚眦',replace(replace('调整＋调整以外的怪兽1只以上\r\n「邪龙星-睚眦」的②③的效果1回合各能使用1次。\r\n①：这张卡不会成为对方的效果的对象。\r\n②：以自己场上1只「龙星」怪兽和对方场上1张卡为对象才能发动。那些卡破坏。\r\n③：自己场上的这张卡被战斗·效果破坏送去墓地时才能发动。从卡组把1只幻龙族怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'卡片破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43225434,'决斗融合',replace(replace('「决斗融合」在1回合只能发动1张。\r\n①：自己场上的融合怪兽和对方怪兽进行战斗的攻击宣言时才能发动。那只自己怪兽的攻击力直到伤害步骤结束时上升进行战斗的对方怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43230671,'古代的蜥蜴战士','远古的保持着昔日姿态的蜥蜴战士，意外的强悍。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43237273,'新空间侠·黑暗豹','可以选择对方场上表侧表示存在的1只怪兽。只要这张卡在自己场上表侧表示存在，这张卡直到结束阶段时当作选择怪兽的同名卡使用，并且得到和选择怪兽相同的怪兽效果。这个效果1回合只能使用1次。','效果复制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43241495,'娱乐伙伴 蹦床猞猁',replace(replace('←4 【灵摆】 4→\r\n「娱乐伙伴 蹦床猞猁」的灵摆效果1回合只能使用1次。\r\n①：自己灵摆召唤成功时，以自己或者对方的灵摆区域1张卡为对象才能发动。那张卡回到持有者手卡。\r\n【怪兽效果】\r\n①：这张卡召唤成功时，以自己或者对方的灵摆区域1张卡为对象才能发动。那张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'回到手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43250041,'吸收盾','①：对方怪兽的攻击宣言时，以1只攻击怪兽为对象才能发动。那只攻击怪兽的攻击无效，自己回复那只怪兽的攻击力数值的基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43262273,'红莲之指名者','支付2000基本分，把手卡全部给对方观看发动。把对方手卡确认并选择1张卡。直到下次的对方的结束阶段时，选择的卡从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43266605,'PSY骨架多线人',replace(replace('「PSY骨架多线人」的③的效果1回合只能使用1次。\r\n①：这张卡只要在手卡·墓地存在，当作「PSY骨架驱动者」使用。\r\n②：自己场上的「PSY骨架」卡被战斗·效果破坏的场合，可以作为代替把手卡的这张卡丢弃。\r\n③：这张卡在墓地存在，自己场上有「PSY骨架」调整特殊召唤的场合才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'是否使用「PSY骨架多线人」的效果代替破坏？','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43268675,'幻奏的音女 欧佩拉',replace(replace('①：这张卡在召唤·反转的回合不能攻击。\r\n②：这张卡成为融合召唤的素材送去墓地的场合才能发动。这个回合，自己场上的「幻奏」怪兽不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43318266,'云魔物-卷层云','这张卡不会被战斗破坏。这张卡表侧守备表示在场上存在的场合，这张卡破坏。这张卡的召唤成功时，给这张卡放置场上存在的名字带有「云魔物」的怪兽数量的雾指示物。可以把这张卡放置的雾指示物取除2个，场上1只怪兽破坏。','放置指示物','怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43332022,'朱罗纪似鸡龙','这张卡被战斗破坏送去墓地时发动。对方可以丢弃1张手卡让这张卡的效果无效。没丢弃的场合，把让这张卡破坏的怪兽破坏。','破坏','是否要丢弃手牌？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43340443,'反冲','怪兽的召唤·反转召唤无效，那只怪兽回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43352213,'猫女郎','用敏捷的动作躲避攻击、用锋利的钩爪袭击对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43359262,'电镀军曹',replace(replace('①：1回合1次，以对方的魔法与陷阱区域盖放的1张卡为对象才能发动。这张卡得到以下效果。\r\n●只要这张卡在怪兽区域存在，作为对象的盖放的卡不能发动。','\r',char(13)),'\n',char(10)),'发动限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43366227,'七剑战士',replace(replace('调整＋调整以外的怪兽1只以上\r\n1回合1次，这张卡被装备卡装备时，给与对方基本分800分伤害。此外，1回合1次，可以把这张卡装备的1张装备卡送去墓地。这张卡装备的装备卡送去墓地时，可以选择对方场上表侧表示存在的1只怪兽破坏。','\r',char(13)),'\n',char(10)),'伤害','装备送墓','破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43378048,'混沌幻魔 阿米泰尔',replace(replace('「神炎皇 乌利亚」＋「降雷皇 哈蒙」＋「幻魔皇 拉比艾尔」\r\n把自己场上的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。\r\n①：这张卡的攻击力在自己回合内上升10000。\r\n②：这张卡不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43378076,'罗刹','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。这张卡召唤·反转时，把「罗刹」以外的手卡1只灵魂怪兽给对方观看才能发动。选择对方场上表侧攻击表示存在的1只怪兽回到持有者手卡。这个效果发动的回合，自己不能把怪兽特殊召唤。','返回手卡','返回手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43383478,'升阶魔法-猛禽之力','①：自己场上的「急袭猛禽」超量怪兽被破坏送去墓地的回合，以自己墓地1只「急袭猛禽」超量怪兽为对象才能发动。那只怪兽特殊召唤，比那只怪兽阶级高1阶的1只「急袭猛禽」怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43385557,'魔力人造机人',replace(replace('调整＋调整以外的怪兽1只以上\r\n自己的结束阶段时，回复自己场上表侧表示存在的念动力族怪兽数量×600的数值的基本分。','\r',char(13)),'\n',char(10)),'回复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43387895,'霸王眷龙 凶饿毒',replace(replace('暗属性灵摆怪兽×2\r\n这张卡用融合召唤以及以下方法才能特殊召唤。\r\n●把自己场上的上记卡解放的场合可以从额外卡组特殊召唤（不需要「融合」）。\r\n①：1回合1次，以这张卡以外的自己或者对方的场上·墓地1只怪兽为对象才能发动。直到结束阶段，这张卡得到和那只怪兽相同的原本的卡名·效果。这个回合，自己怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'复制效果','结束复制效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43405287,'D-锁链',replace(replace('①：以自己场上1只「命运英雄」怪兽为对象才能把这张卡发动。这张卡当作攻击力上升500的装备卡使用给那只自己怪兽装备。\r\n②：装备怪兽战斗破坏对方怪兽送去墓地的场合发动。给与对方500伤害。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43413875,'花札卫-芒上月-',replace(replace('这张卡不能通常召唤。把「花札卫-芒上月-」以外的自己场上1只8星的「花札卫」怪兽解放的场合可以特殊召唤。\r\n①：这张卡特殊召唤成功的场合发动。自己从卡组抽1张，给双方确认。那是「花札卫」怪兽的场合，可以把那只怪兽特殊召唤。不是的场合，那张卡送去墓地。\r\n②：1回合1次，这张卡战斗破坏对方怪兽时才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽1张卡并给双方确认','是否特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43417563,'诱发之舞','「舞蹈战士」的降临必需。若不从场地和手卡把等级直到6以上的卡作为祭品，则「舞蹈战士」不能降临。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43422537,'二重召唤','①：这个回合自己可以进行通常召唤最多2次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43426903,'爬虫妖女·戈耳工','这张卡进行攻击的伤害计算后，和这张卡进行战斗的怪兽攻击力变成0，变成不能把表示形式改变。','攻击变成0，不能改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43434803,'过浅的墓穴','自己和对方在各自的墓地选择1只怪兽，以守备表示的形式在场上盖伏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43436049,'废品破坏者','①：这张卡召唤成功的回合的自己主要阶段，把这张卡解放才能发动。场上的全部表侧表示怪兽的效果直到回合结束时无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43452193,'异次元隧道-镜之门-','自己场上表侧表示存在的名字带有「元素英雄」的怪兽为攻击对象的对方怪兽攻击宣言时才能发动。对方的攻击怪兽和成为攻击对象的自己怪兽交换控制权进行伤害计算。直到这个回合结束阶段时得到控制权交换怪兽的控制权。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43455065,'魔力之泉',replace(replace('「魔力之泉」在1回合只能发动1张。\r\n①：自己从卡组抽出对方场上的表侧表示的魔法·陷阱卡的数量。那之后，从自己手卡选自己场上的表侧表示的魔法·陷阱卡数量的卡丢弃。这张卡的发动后，直到下次的对方回合的结束时，对方场上的魔法·陷阱卡不会被破坏，发动和效果不会被无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43464884,'妖海的巨魔歹徒',replace(replace('这个卡名的①②的效果1回合各能使用1次，这些效果发动的回合，自己不是兽战士族怪兽不能特殊召唤。\r\n①：这张卡召唤·特殊召唤成功的场合，以自己墓地1只兽战士族怪兽为对象才能发动。这张卡的属性·等级直到回合结束时变成和那只怪兽相同。\r\n②：自己主要阶段才能发动。把持有和这张卡相同属性·等级的1只兽战士族怪兽从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43476205,'升阶魔法-革命之力',replace(replace('①：可以把发动回合的以下效果发动。\r\n●自己回合：以自己场上1只「急袭猛禽」超量怪兽为对象才能发动。阶级高1阶的1只「急袭猛禽」怪兽在作为对象的自己怪兽上面重叠当作超量召唤从额外卡组特殊召唤。\r\n●对方回合：以对方场上1只没有超量素材的超量怪兽为对象才能发动。得到那只超量怪兽的控制权。那之后，阶级高1阶的1只「急袭猛禽」怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43487744,'白洞','对方使用了「黑洞」的时候，自己的怪兽不被破坏。发动后这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43490025,'未来No.0 未来皇 霍普-未来斩',replace(replace('「No.」怪兽以外的相同阶级的超量怪兽×2\r\n规则上，这张卡的阶级当作1阶使用。这张卡也能在自己场上的「希望皇 霍普」怪兽或者「未来No.0 未来皇 霍普」上面重叠来超量召唤。\r\n①：这张卡的攻击力上升双方墓地的「No.」超量怪兽数量×500。\r\n②：这张卡不会被战斗破坏。\r\n③：1回合1次，把这张卡1个超量素材取除才能发动。这个回合，这张卡在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'2次攻击','是否在霍普怪兽上重叠来超量召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43500484,'魔界之棘','栖息在魔界的有刺灌木，将硬闯的人牢牢缠住。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43509019,'卡通防御','①：对方怪兽向自己的4星以下的卡通怪兽攻击宣言时才能把这个效果发动。那只对方怪兽的攻击变成对自己的直接攻击。','改变攻击对象','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43513897,'星圣·柳宿增三','这张卡召唤·特殊召唤成功时，自己场上的全部名字带有「星圣」的怪兽的攻击力上升500。','攻击力上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43530283,'勇气之沙漏','召唤·反转召唤成功时，直到下次自己的回合的结束阶段这张卡的原来的攻击力·守备力减半，之后这张卡的原来的攻击力·守备力变成2倍。','攻守变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43543777,'伊奘波','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。这张卡召唤·反转时，可以丢弃1张手卡把自己墓地存在的1只灵魂怪兽加入手卡。','回到手牌','把自己墓地存在的1只灵魂怪兽加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43573231,'幻蝶刺客 闪蝶','对方场上的怪兽的表示形式变更时，选择那1只怪兽才能发动。选择的怪兽的攻击力·守备力下降1000。这个效果1回合只能使用1次。','攻守下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43577607,'龙星的气脉',replace(replace('①：得到自己墓地的「龙星」怪兽的属性种类数量的以下效果。\r\n●2种类以上：自己场上的「龙星」怪兽的攻击力上升500。\r\n●3种类以上：自己场上的「龙星」怪兽被战斗·效果破坏的场合，可以作为代替把这张卡送去墓地。\r\n●4种类以上：对方不能把怪兽盖放，对方场上的表侧表示怪兽全部变成攻击表示。\r\n●5种类以上：把这张卡送去墓地才能发动。场上的卡全部破坏。','\r',char(13)),'\n',char(10)),'全部破坏','是否要使用「龙星的气脉」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43580269,'无限真理','①：这张卡战斗破坏对方怪兽送去墓地的场合发动。这张卡的攻击力上升700。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43583400,'抗锯齿星人',replace(replace('①：1回合1次，这张卡和怪兽进行过战斗的自己·对方的战斗阶段才能发动。从手卡把1只电子界族怪兽召唤。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'怪兽召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43586926,'多尔·多拉',replace(replace('这个卡名的效果在决斗中只能使用1次。\r\n①：场上的这张卡被破坏送去墓地的回合的结束阶段才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡的攻击力·守备力变成1000。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43641473,'善变的裁缝师','把怪兽装备的1张装备卡，转换给1只别的能变成正确对象的怪兽。','请选择一张装备卡','请选择要转移装备的对象','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43642620,'猛犸僵尸','自己墓地没有不死族怪兽存在的场合，这张卡破坏。场上表侧表示存在的这张卡被破坏的场合，给与那个时候的控制者这张卡的原本攻击力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43644025,'茧状体再生','可以把自己场上表侧表示存在的1只名字带有「茧状体」的怪兽作为祭品，那张卡记述的1只名字带有「新空间侠」的怪兽从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43658697,'超级运动员王牌转队',replace(replace('「超级运动员王牌转队」在1回合只能发动1张。\r\n①：从卡组把1只「超级运动员」怪兽特殊召唤。这个效果特殊召唤的怪兽不能作为同调·超量召唤的素材，效果无效化。那之后，自己失去那只怪兽的等级×300基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43661068,'望星许愿','选择自己场上表侧表示存在的1只怪兽发动。持有和选择的怪兽相同攻击力或者相同守备力的自己场上的怪兽的等级直到结束阶段时变成和选择的怪兽相同。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43694075,'法理的祈祷','「法理守护者」的降临必要。必须从场上或者手札，牺牲奉献等级合计为7个以上的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43694481,'雪暴猎鹰','这张卡的攻击力比原本攻击力高的场合才能发动。给与对方基本分1500分伤害。这个效果只在这张卡在场上表侧表示存在能使用1次，「雪暴猎鹰」的效果1回合只能使用1次。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43697559,'喷气机人','这张卡被对方怪兽选作为攻击对象时，这张卡的控制者可以从手卡发动陷阱卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43698897,'魔玩具工厂',replace(replace('「魔玩具工厂」的①②的效果1回合各能使用1次。\r\n①：从自己墓地把1张「融合」魔法卡除外才能把这个效果发动。从自己的手卡·场上把「魔玩具」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：这张卡被送去墓地的场合，以除外的1张自己的「魔玩具融合」为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43708041,'阳炎光轮','只要这张卡在场上存在，自己可以把名字带有「阳炎兽」的怪兽召唤的场合需要的解放减少1只。此外，可以通过把场上表侧表示存在的这张卡送去墓地，从自己墓地选择「阳炎光轮」以外的1张名字带有「阳炎」的卡加入手卡。','返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43708640,'牛马林神','这张卡不能特殊召唤。可以把这张卡解放，从自己卡组把2只兽战士族·4星的通常怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43711255,'禁止令',replace(replace('宣言1个卡名才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，宣言的卡名为原本卡名的双方的卡受以下所适用（对从这个效果的适用前开始在场上存在的卡不适用）。\r\n●不能在场上出现。\r\n●不能作卡的发动以及效果的发动和适用。\r\n●不能通常召唤·反转召唤·特殊召唤。\r\n●不能作攻击以及表示形式的变更。\r\n●不能作为要为需要素材的特殊召唤而用的素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43714890,'投人的巨魔','每祭掉自己场上1只通常怪兽（衍生物除外），给与对方基本分800分的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43716289,'剧毒木乃伊','反转：给与对方500基本分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43722862,'风魔女-冰铃',replace(replace('「风魔女-冰铃」的①②的效果1回合各能使用1次。\r\n①：自己场上没有怪兽存在的场合才能发动。这张卡从手卡特殊召唤。那之后，可以从卡组把1只「风魔女」怪兽特殊召唤。这个效果从卡组特殊召唤的怪兽不能解放，这个效果发动的回合，自己不是5星以上的风属性怪兽不能从额外卡组特殊召唤。\r\n②：这张卡召唤·特殊召唤成功的场合才能发动。给与对方500伤害。','\r',char(13)),'\n',char(10)),'特殊召唤','是否从卡组把1只「风魔女」怪兽特殊召唤？','伤害','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43730887,'拘束臂',replace(replace('①：这张卡召唤·特殊召唤成功的场合，以对方场上1只表侧表示怪兽为对象发动。这只怪兽表侧表示存在期间，作为对象的表侧表示怪兽不能攻击，效果无效化。\r\n②：只要这张卡的①的效果作为对象的怪兽在场上存在，这张卡不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'选择怪兽不能攻击，效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43748308,'微炎星-龙史进','1回合1次，自己把名字带有「炎舞」的魔法·陷阱卡发动的场合，可以从卡组选1张名字带有「炎舞」的陷阱卡在自己场上盖放。此外，1回合1次，把自己场上表侧表示存在的2张名字带有「炎舞」的魔法·陷阱卡送去墓地才能发动。从自己墓地选择「微炎星-龙史进」以外的1只名字带有「炎星」的怪兽特殊召唤。','盖放','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43751755,'茧状体·小黑豹','场上有「新宇宙」存在时，可以把这张卡作为祭品从手卡·卡组特殊召唤1只「新空间侠·黑暗豹」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43785278,'傅科魔炮石',replace(replace('←2 【灵摆】 2→\r\n①：这张卡发动的回合的结束阶段，以场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张卡破坏。\r\n【怪兽描述】\r\n是彷徨于梦幻空间的机关生命体，本应是如此。\r\n最大的谜团是，过去的记录却几乎··留下来。\r\n那理由···呢，·····干涉···它在···拒··？\r\n···消去···','\r',char(13)),'\n',char(10)),'魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43791861,'凤王兽 铠楼罗','这张卡向对方怪兽攻击的场合，伤害步骤内攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43793530,'歧蜥·魔蜥义豪','为了面对强大的邪恶，进行了各式肉体改造从而获得了可怕的能力，而作为代价失去了正义的心。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43797906,'亚特兰蒂斯的战士','把这张卡从手卡丢弃去墓地。从卡组把1张「传说之都 亚特兰蒂斯」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43803845,'假威鸭',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡不会成为效果的对象，不会被效果破坏。\r\n●只要这张卡在怪兽区域存在，对方怪兽的攻击全部变成直接攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43813459,'反射光子流','自己场上的龙族·光属性怪兽被选择作为攻击对象时才能发动。那只攻击对象怪兽的攻击力直到伤害步骤结束时上升攻击怪兽的攻击力数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43839002,'电脑网后门',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己场上1只电子界族怪兽为对象才能发动。那只怪兽除外，把持有比那只怪兽的原本攻击力低的攻击力的1只电子界族怪兽从卡组加入手卡。这个效果除外的怪兽在下次的自己准备阶段回到场上，那个回合可以直接攻击。','\r',char(13)),'\n',char(10)),'「电脑网后门」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43841694,'魔导书的奇迹','选择自己墓地1只魔法师族超量怪兽和从游戏中除外的最多2张自己的名字带有「魔导书」的魔法卡才能发动。选择的怪兽特殊召唤，把选择的名字带有「魔导书」的魔法卡在那只怪兽下面重叠作为超量素材。「魔导书的奇迹」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43845801,'究极爆风弹','①：以自己场上1只融合召唤的「青眼究极龙」为对象才能发动。这个回合，那只怪兽在同1次的战斗阶段中可以作3次攻击，那只怪兽攻击的场合，直到伤害步骤结束时对方不能把魔法·陷阱·怪兽的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43889633,'忘却的海底神殿','只要这张卡在场上存在，这张卡的卡名当作「海」使用。1回合1次，可以选择自己场上表侧表示存在的1只4星以下的鱼族·海龙族·水族怪兽从游戏中除外。这个效果除外的怪兽在自己的结束阶段时在场上特殊召唤。','是否现在使用「忘却的海底神殿」效果？','除外','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43892408,'龙骑士 黑魔术少女',replace(replace('「黑魔术少女」＋龙族怪兽\r\n这张卡用以上记卡为融合素材的融合召唤或者用「蒂迈欧之眼」的效果才能特殊召唤。\r\n①：1回合1次，把1张手卡送去墓地，以场上1张表侧表示的卡为对象才能发动。那张表侧表示的卡破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43898403,'双龙卷','①：丢弃1张手卡，以场上最多2张魔法·陷阱卡为对象才能发动。那些卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43905751,'魔人铳','从口中能够发射子弹攻击的生物兵器。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43906884,'黑羽-地雷','盖放的这张卡被对方的效果破坏时，自己场上有名字带有「黑羽」的怪兽表侧表示存在的场合，给与对方基本分1000分伤害，自己从卡组抽1张卡。','伤害和抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43912676,'异层空间',replace(replace('①：场上的幻龙族怪兽的攻击力·守备力上升300。\r\n②：1回合1次，把自己墓地3只幻龙族怪兽除外，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'卡片破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43925870,'次世代风能人',replace(replace('「次世代控制员」＋调整以外的风属性怪兽1只以上\r\n这张卡的攻击力上升场上盖放的魔法·陷阱卡数量×300的数值。可以把1张手卡送去墓地，场上盖放的1张魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43930492,'吸收魔术师',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。\r\n②：这张卡的等级上升这张卡的魔力指示物数量的数值。\r\n③：把这张卡3个魔力指示物取除，以自己墓地1张速攻魔法卡为对象才能发动。那张卡在自己的魔法与陷阱区域盖放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43932460,'自然地龟',replace(replace('地属性调整＋调整以外的地属性怪兽1只以上\r\n只要这张卡在场上表侧表示存在，可以把手卡1张魔法卡送去墓地，效果怪兽的效果的发动无效并破坏。','\r',char(13)),'\n',char(10)),'效果怪兽的效果发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43959432,'变形黏土要塞',replace(replace('①：以自己场上1只4星以上的怪兽为对象才能发动。这张卡发动后变成效果怪兽（岩石族·地·4星·攻/守1000）在怪兽区域特殊召唤。那之后，作为对象的表侧表示怪兽当作装备卡使用给这张卡装备。这张卡也当作陷阱卡使用。\r\n②：这张卡的效果特殊召唤的这张卡的攻击力·守备力上升这张卡的效果装备的怪兽的攻击力数值，这张卡在攻击的伤害步骤结束时变成守备表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(43973174,'呼龙笛','①：从手卡把最多2只龙族怪兽特殊召唤。这个效果在场上有「龙之支配者」存在的场合才能发动和处理。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44009443,'影星轨道兵器 着陆九头蛇',replace(replace('这张卡不能通常召唤。自己墓地有怪兽5只以上存在，那些怪兽的卡名全部不同的场合才能特殊召唤。\r\n①：1回合1次，从自己卡组上面把3张卡送去墓地才能发动。自己墓地的怪兽的卡名全部不同的场合，选场上1张卡破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44026393,'幻兽机 雷电貂',replace(replace('①：丢弃1张手卡才能发动。在自己场上把1只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。这个效果的发动后，直到回合结束时自己不是「幻兽机」怪兽不能作为融合·同调·超量·连接召唤的素材。\r\n②：这张卡的等级上升自己场上的「幻兽机衍生物」的等级的合计数值。\r\n③：只要自己场上有衍生物存在，这张卡不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44026394,'幻兽机衍生物','由「幻兽机 雷电貂」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44028461,'黑翼回归','自己回合才能发动。选择自己墓地存在的1只攻击力2000以下的名字带有「黑羽」的怪兽特殊召唤。这张卡发动的回合，自己不能把怪兽通常召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44035031,'重力战士',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡同调召唤成功时，这张卡的攻击力上升对方场上表侧表示存在的怪兽数量×300的数值。1回合1次，对方的战斗阶段时选择对方场上守备表示存在的1只怪兽才能发动。选择的怪兽变成表侧攻击表示。这个回合那只怪兽可以攻击的场合必须作出攻击。','\r',char(13)),'\n',char(10)),'攻击上升','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44046281,'次元之门','这张卡的发动时，选择自己场上1只怪兽表侧表示从游戏中除外。此外，对方怪兽的直接攻击宣言时，可以把场上表侧表示存在的这张卡送去墓地。这张卡被送去墓地的场合，可以把这张卡的效果除外的怪兽特殊召唤。','送去墓地','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44052074,'古代的机械射出机',replace(replace('「古代的机械射出机」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上没有怪兽存在的场合，以自己场上1张表侧表示的卡为对象才能发动。那张卡破坏，从卡组把1只「古代的机械」怪兽无视召唤条件特殊召唤。\r\n②：把墓地的这张卡除外，以自己场上1张表侧表示的卡为对象才能发动。那张卡破坏，在自己场上把1只「古代的齿车衍生物」（机械族·地·1星·攻/守0）特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44052075,'古代的齿车衍生物','由「古代的机械射出机」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44072894,'物资调达员','反转：通过「融合」而送去自己的墓地的2只融合素材怪兽加入手卡。','加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44073668,'塔库利米诺斯','身上长有鳍，在水中自由活动的海龙的伙伴。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44088292,'进化合兽 二氧鬼神',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●只要这张卡在怪兽区域存在，二重怪兽的召唤不会被无效化。\r\n●1回合1次，把自己墓地1只二重怪兽除外，以对方场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44095762,'神圣防护罩 -反射镜力-','①：对方怪兽的攻击宣言时才能发动。对方场上的攻击表示怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44125452,'蔷薇妖精','这张卡被魔法·陷阱·效果怪兽的效果从自己卡组加入手卡的场合，这张卡可以在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44139064,'巨机人都市',replace(replace('这个卡名的①②的效果1回合各能使用1次，这些效果发动的回合，自己不是融合怪兽不能从额外卡组特殊召唤。\r\n①：以这张卡以外的自己场上1张卡为对象才能发动。那张卡破坏，从卡组把1张「机人」卡加入手卡。\r\n②：自己的「机人」怪兽进行战斗的伤害计算时，从卡组把1只「机人」怪兽送去墓地才能发动。那只进行战斗的自己怪兽只在那次伤害计算时原本攻击力和原本守备力交换。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44155002,'魔轰神兽 尤尼科',replace(replace('名字带有「魔轰神」的调整＋调整以外的怪兽1只以上\r\n自己手卡的数量和对方手卡的数量相同的场合，对方发动的魔法·陷阱·效果怪兽的效果无效并破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44178886,'光道武僧 艾琳',replace(replace('①：这张卡向守备表示怪兽攻击的伤害计算前发动。那只怪兽回到持有者卡组。\r\n②：自己结束阶段发动。从自己卡组上面把3张卡送去墓地。','\r',char(13)),'\n',char(10)),'返回卡组','从卡组送3张卡去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44179224,'魔界剧团 启幕人',replace(replace('←7 【灵摆】 7→\r\n这个卡名的灵摆效果在决斗中只能使用1次。\r\n①：自己场上没有怪兽存在的场合才能发动。灵摆区域的这张卡特殊召唤。\r\n【怪兽效果】\r\n①：自己场上没有这张卡以外的怪兽存在的场合，这张卡的攻击力上升1100。\r\n②：1回合1次，从卡组把1张「魔界台本」魔法卡送去墓地才能发动。从自己的额外卡组把1只表侧表示的「魔界剧团」灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44182827,'轮回转生','作为仪式召唤的祭品的怪兽卡不去墓地，回到持有者的卡组。之后卡组洗切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44186624,'DDD 制霸王 皇帝',replace(replace('①：这张卡灵摆召唤成功的场合发动。对方场上的表侧表示的卡的效果直到回合结束时无效。\r\n②：这张卡灵摆召唤成功的回合的主要阶段1次，以自己的魔法与陷阱区域最多2张卡为对象才能发动。那些卡破坏。这个回合，这张卡在同1次的战斗阶段中在通常攻击外加上可以作出最多有这个效果破坏的卡数量的攻击。','\r',char(13)),'\n',char(10)),'效果无效','多次攻击','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44203504,'机械军曹','侍从机械王的机械族的司令官。统率机械部队的攻击性用兵而很有名。','','','','','','','','','','','','','','','','');
