INSERT INTO texts VALUES(44236692,'死灵连接者','把这张卡解放，选择自己墓地存在的1只名字带有「同调士」的调整发动。选择的怪兽特殊召唤。这个效果特殊召唤的怪兽这个回合不能作为同调素材。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44250812,'我我我书记','自己场上有「我我我书记」以外的名字带有「我我我」的怪兽存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44256816,'独奏的第1乐章',replace(replace('「独奏的第1乐章」在1回合只能发动1张，这张卡发动的回合，自己不是「幻奏」怪兽不能特殊召唤。\r\n①：自己场上没有怪兽存在的场合才能发动。从手卡·卡组把1只4星以下的「幻奏」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44273680,'光天使 天书','1回合1次，把手卡1张魔法卡送去墓地才能发动。从手卡把1只名字带有「光天使」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44287299,'传说的剑豪 正树','据说是完成了百人斩的传说中的剑豪。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44311445,'魔偶甜点·布丁公主 巧克力布丁拼盘',replace(replace('地属性5星怪兽×2\r\n这张卡也能在自己场上的4阶以下的「魔偶甜点」超量怪兽上面把这张卡重叠来超量召唤。\r\n①：1回合1次，以自己墓地1张「魔偶甜点」卡为对象才能发动。那张卡回到卡组。\r\n②：这张卡有「魔偶甜点·布丁公主」在作为超量素材的场合自己墓地的「魔偶甜点」卡回到卡组时，把这张卡1个超量素材取除才能发动。从卡组把1只「魔偶甜点」怪兽表侧攻击表示或者里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'是否在「魔偶甜点」超量怪兽上面重叠来超量召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44330098,'冥府之使者 格斯',replace(replace('自己场上没有卡存在的场合，因对方控制的卡受到伤害时，这张卡可以从手卡特殊召唤。这个方法特殊召唤成功时，把受到的伤害种类的以下效果发动。\r\n●战斗伤害的场合，在自己场上把1只「冥府之使者 凯恩衍生物」（天使族·光·7星·攻/守?）特殊召唤。这衍生物的攻击力·守备力变成和这个时候受到的战斗伤害相同的数值。\r\n●卡的效果伤害的场合，给与对方基本分和受到的伤害相同的伤害。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤衍生物','给与伤害','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44330099,'冥府之使者 凯恩衍生物','由「冥府之使者 格斯」的效果特殊召唤。这衍生物的攻击力·守备力变成和「冥府之使者 格斯」特殊召唤时受到的战斗伤害相同的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44335251,'食魂窃蛋龙',replace(replace('「食魂窃蛋龙」的①②的效果1回合各能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组选1只恐龙族怪兽加入手卡或送去墓地。\r\n②：以这张卡以外的场上1只4星以下的恐龙族怪兽为对象才能发动。那只怪兽破坏。那之后，从自己墓地选1只恐龙族怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'恐龙族怪兽加入手卡或送去墓地','破坏并特殊召唤','请选择要加入手卡或送去墓地的卡','是否加入手卡？','','','','','','','','','','','','');
INSERT INTO texts VALUES(44341034,'暗虫','这张卡召唤成功时，选择自己墓地存在的1只3星的调整在自己场上特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44352516,'电子暗黑业火',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：有装备卡装备的自己场上的「电子暗黑」效果怪兽不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：以自己场上1只「电子暗黑」怪兽为对象才能发动。那只怪兽回到持有者手卡，那之后可以把1只「电子暗黑」怪兽召唤。\r\n③：场上的这张卡被对方的效果破坏的场合才能发动。从卡组把1张「融合」魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'是否把「电子暗黑」怪兽召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44364077,'娱乐伙伴 士兵虎',replace(replace('「娱乐伙伴 士兵虎」的效果1回合只能使用1次。\r\n①：这张卡战斗破坏对方怪兽送去墓地时才能发动。从卡组把1只「娱乐伙伴」灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44364207,'翡翠骑士','只要这张卡在自己场上表侧攻击表示存在，自己场上表侧表示存在的攻击力1200以下的机械族怪兽不会被陷阱卡的效果破坏。场上表侧表示存在的这张卡被战斗破坏送去墓地时，可以从自己卡组把1只光属性·机械族的4星怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44373896,'破坏轮龙','这张卡用「克里底亚之牙」的效果把自己的手卡·场上的「破坏轮」送去墓地才能特殊召唤。1回合1次：可以选择对方场上1张卡；破坏那张卡。那之后，破坏的卡是怪兽卡的场合，给与对方那只怪兽的原本攻击力数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44394295,'影依融合',replace(replace('「影依融合」在1回合只能发动1张。\r\n①：从自己的手卡·场上把「影依」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。从额外卡组特殊召唤的怪兽在对方场上存在的场合，自己卡组的怪兽也能作为融合素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44397496,'真红眼之魂','①：以自己墓地1只「真红眼」怪兽为对象才能发动。那只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44405066,'真红眼钢炎龙',replace(replace('7星怪兽×2\r\n①：持有超量素材的这张卡不会被效果破坏。\r\n②：只要持有超量素材的这张卡在怪兽区域存在，每次对方把魔法·陷阱·怪兽的效果发动给与对方500伤害。\r\n③：1回合1次，把这张卡1个超量素材取除，以自己墓地1只「真红眼」通常怪兽为对象才能发动。那只怪兽特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44424095,'变形斗士·加速装置','从自己手卡让1只名字带有「变形斗士」的怪兽回到卡组。场上1张卡破坏，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44430454,'六武众的侍从','暗地里支持着六武众的充满谜团的人物。虽然现在已经没有再参加战斗，刻在身上的无数伤痕仍受到种种猜测。他的过去无人知晓。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44436472,'双价体','①：暗属性怪兽上级召唤的场合，这张卡可以作为2只的数量解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44472639,'太阳射线','对对方造成数值与自己场上存在的光属性怪兽数量×600点等同的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44481227,'娱乐伙伴 降落驼',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，以自己场上1只表侧表示怪兽为对象才能发动。对方场上的全部怪兽的守备力直到回合结束时下降800，这个回合作为对象的怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n【怪兽效果】\r\n①：这张卡被战斗破坏的场合才能发动。让把这张卡破坏的怪兽的攻击力下降800。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44487250,'超量阻挡','把自己场上1个超量素材取除才能发动。对方发动的效果怪兽的效果的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44505297,'甲虫装机 艾可萨甲虫',replace(replace('6星怪兽×2\r\n这张卡超量召唤成功时，可以选择自己或者对方的墓地1只怪兽当作装备卡使用给这张卡装备。这张卡的攻击力·守备力上升这个效果装备的怪兽的各自一半数值。此外，1回合1次，可以把这张卡1个超量素材取除，选择自己以及对方场上表侧表示存在的卡各1张送去墓地。','\r',char(13)),'\n',char(10)),'装备','送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44508094,'星尘龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：要让场上的卡破坏的魔法·陷阱·怪兽的效果发动时，把这张卡解放才能发动。那个发动无效并破坏。\r\n②：这张卡的①的效果适用的回合的结束阶段才能发动。为那个效果发动而解放的这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'破坏场上卡的效果发动无效并破坏','回合结束时特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44509898,'精确防御','①：对方怪兽的攻击宣言时，以自己墓地1只4星以下的怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽在这个回合不会被战斗·效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44519536,'被封印者的左足','被封印的左足。假若解开封印，能得无限之力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44584775,'伤害=爬虫','因爬虫族怪兽的战斗受到战斗伤害时，可以把持有那个数值以下的攻击力的1只爬虫族怪兽从卡组特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44586426,'太阳电池人',replace(replace('这个卡名的①②③的效果1回合各能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1只雷族怪兽送去墓地。\r\n②：这张卡已在怪兽区域存在的状态，雷族怪兽召唤·特殊召唤的场合发动。在自己场上把1只「电池人衍生物」（雷族·光·1星·攻/守0）特殊召唤。\r\n③：以自己的场上·墓地1只「电池人」效果怪兽为对象才能发动。直到结束阶段，这张卡当作那同名卡使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44586427,'电池人衍生物','由「太阳电池人」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44595286,'裁决之光',replace(replace('①：场上有「天空的圣域」存在的场合从手卡把1只光属性怪兽丢弃去墓地才能发动。从以下效果选1个适用。\r\n●把对方手卡确认，从那之中选1张卡送去墓地。\r\n●选对方场上1张卡送去墓地。','\r',char(13)),'\n',char(10)),'从对方场上选择1张卡送去墓地','从对方手卡选择1张卡送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44612603,'生存境界',replace(replace('①：场上的通常怪兽全部破坏，把最多有破坏数量的4星以下的恐龙族怪兽从卡组往自己场上特殊召唤。这个效果特殊召唤的怪兽在结束阶段破坏。\r\n②：把墓地的这张卡除外，以自己场上1只恐龙族怪兽和对方场上1张卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44635489,'星圣·羽林军二十六','对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。此外，1回合1次，选择自己的场上·墓地1只名字带有「星圣」的怪兽才能发动。这张卡变成和选择的怪兽相同等级。把场上的这张卡作为超量素材的场合，不是名字带有「星圣」的怪兽的超量召唤不能使用。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44656491,'和平使者','双方表侧表示的攻击力1500以上的怪兽不能进行攻击宣言。每次自己的准备阶段支付100基本分。若不支付，这张卡破坏。','是否要支付100LP？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44663232,'异怪之妖精 艾尔佛彼娅','1回合1次，把手卡1只风属性怪兽给对方观看才能发动。直到下次的对方的主要阶段1结束时，双方玩家不能把比给人观看的怪兽等级高的怪兽的效果发动。','效果抑制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44665365,'神光之宣告者',replace(replace('「宣告者的预言」降临。\r\n①：对方把怪兽的效果·魔法·陷阱卡发动时，从手卡把1只天使族怪兽送去墓地才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'效果怪兽的效果·魔法·陷阱卡的发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44676200,'英雄障壁','自己场上名字中带有「元素英雄」的怪兽表侧表示存在的场合，对方怪兽的攻击只有1次无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44682448,'垃圾王','这张卡可以支付2000基本分，从手卡特殊召唤。把这张卡作为超量素材的场合，不是暗属性怪兽的超量召唤不能使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44686185,'影六武众-初芽',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：从自己墓地以及自己场上的表侧表示怪兽之中把2只「六武众」怪兽除外，以「影六武众-初芽」以外的自己墓地1只「六武众」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：只让自己场上的「六武众」怪兽1只被效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44689688,'朱罗纪棘龙','这张卡战斗破坏对方怪兽送去墓地时，在对方场上把1只「棘龙衍生物」（恐龙族·炎·1星·攻300/守0）攻击表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44689689,'棘龙衍生物','由「侏罗纪棘龙」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44702857,'咬噬龙','这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44717069,'海星','1回合1次，自己的主要阶段时才能发动。自己场上的全部「海星」的等级上升1星。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44729197,'蒸汽机人','这张卡向对方怪兽攻击的场合，伤害步骤内攻击力上升500。这张卡被对方怪兽攻击的场合，伤害步骤内攻击力下降500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44762290,'光学迷彩铠甲','只能给1星的怪兽装备。装备这张卡的怪兽可以对对方进行直接攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44763025,'爱恶作剧的双子恶魔','支付1000分。随机丢弃对方的1张手卡，对方再选择自己1张手卡丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44771289,'旅人的结彼岸',replace(replace('①：从自己的手卡·场上把「彼岸」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：自己主要阶段把墓地的这张卡除外，以场上1只「彼岸」怪兽为对象才能发动。那只怪兽的攻击力·守备力直到下次的对方回合结束时上升800。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44789585,'自然豆一族','这张卡1回合只有1次不会被战斗破坏。场上表侧表示存在的这张卡被选择作为攻击对象时，给与对方基本分500分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44790889,'重锤突击保镖','对方场上有卡存在，自己场上没有卡存在的场合，这张卡可以不用解放作召唤。自己场上没有魔法·陷阱卡存在，这张卡向对方怪兽攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。','不用解放召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44792253,'D.D.破坏王','场上存在的这张卡从游戏中除外时，可以选择对方场上表侧表示存在的1张卡破坏。','对方场上表侧表示存在的1张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44800181,'连击大师','这张卡在场上表侧表示存在时自己回合的主要阶段一有连锁发生的场合，只在这个回合这张卡可以在同1次战斗阶段作2次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44811425,'林克斯异虫','反转：这张卡在结束阶段时表侧表示存在的场合，自己从卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44852429,'DDD 咒血王 赛弗里德',replace(replace('调整＋调整以外的「DD」怪兽1只以上\r\n「DDD 咒血王 赛弗里德」的①的效果1回合只能使用1次。\r\n①：以场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张卡的效果直到下个准备阶段无效化。这个效果在对方回合也能发动。\r\n②：这张卡被战斗·效果破坏送去墓地的场合发动。自己回复自己场上的「契约书」卡数量×1000基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44860890,'捷炎星-清鸦','这张卡从场上送去墓地的场合，可以从卡组选1张名字带有「炎舞」的魔法卡在自己场上盖放。此外，只要这张卡在场上表侧表示存在，自己场上的名字带有「炎舞」的魔法·陷阱卡不会被对方的卡的效果破坏。','盖放','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44865098,'机械士兵','护卫机械王的兵队。圆圆的身体到处转动着进行巡逻。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44874522,'古代的机械热核龙',replace(replace('①：把「古代的机械」怪兽解放作上级召唤的这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n②：把「零件」怪兽解放作上级召唤的这张卡在同1次的战斗阶段中可以作2次攻击。\r\n③：这张卡攻击的场合，对方直到伤害步骤结束时怪兽的效果·魔法·陷阱卡不能发动。\r\n④：这张卡攻击的伤害步骤结束时才能发动。选场上1张魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44877690,'冰结界的神精灵','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。自己场上有这张卡以外的名字带有「冰结界」的怪兽表侧表示存在的场合，结束阶段时发动的这张卡的效果变成选择对方场上存在的1只怪兽回到手卡的效果。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44883600,'二重防御者','自己场上有表侧守备表示怪兽2只以上存在的场合，对方怪兽的攻击宣言时才能发动。把那1只对方怪兽的攻击无效。这个效果1回合只能使用1次。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44883830,'死之合唱','自己场上有3只「死亡青蛙」表侧表示存在时才能发动。对方场上存在的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44887817,'增草剂','①：1回合1次，以自己墓地1只植物族怪兽为对象才能发动。那只植物族怪兽特殊召唤。这个效果把怪兽特殊召唤的回合，自己不能通常召唤。这个效果特殊召唤的怪兽从场上离开时这张卡破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44891812,'超重武者 御助-K',replace(replace('①：自己墓地没有魔法·陷阱卡存在的场合，自己的守备表示怪兽和对方怪兽进行战斗的伤害计算时把这张卡从手卡丢弃，以进行战斗的怪兽以外的自己场上1只「超重武者」怪兽为对象才能发动。那只进行战斗的自己怪兽的守备力只在那次伤害计算时上升作为对象的怪兽的守备力数值。\r\n②：对方怪兽的直接攻击宣言时，把墓地的这张卡除外，以自己墓地1只「超重武者」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44901281,'剑之落穴','①：自己场上有「X-剑士」怪兽存在，自己或者对方把怪兽召唤·反转召唤·特殊召唤之际才能发动。那个无效，那些怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44910027,'胜利龙','这张卡不能特殊召唤。只能用自己场上3只龙族怪兽作为祭品进行祭品召唤出场。这张卡直接攻击对方造成对方基本分为0的时候，这张卡的主人得到比赛的胜利。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44913552,'时间吞噬者','这张卡战斗破坏对方怪兽的场合，下次的对方回合的主要阶段1跳过。','跳过阶段','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44920699,'炎舞-「天璇」','这张卡的发动时，选择自己场上1只兽战士族怪兽。选择的怪兽的攻击力直到结束阶段时上升700。此外，只要这张卡在场上存在，自己场上的兽战士族怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44928016,'胡萝卜人',replace(replace('「胡萝卜人」的效果1回合只能使用1次\r\n①：这张卡在墓地存在，把自己手卡·场上的「胡萝卜人」以外的1只植物族怪兽送去墓地才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44935634,'左起子',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。这张卡的等级直到回合结束时变成3星。\r\n②：把墓地的这张卡除外才能发动。从卡组把1只「右起子」加入手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44944304,'娱乐伙伴 逗乐家',replace(replace('←5 【灵摆】 5→\r\n①：1回合1次，对方场上有持有比原本攻击力高的攻击力的怪兽存在的场合才能发动。自己回复1000基本分。\r\n【怪兽效果】\r\n「娱乐伙伴 逗乐家」的①②的怪兽效果1回合只能有1次使用其中任意1个。\r\n①：这张卡的攻击宣言时才能发动。这张卡的攻击力直到战斗阶段结束时上升这张卡以及对方场上的怪兽之内持有比原本攻击力高的攻击力的怪兽数量×1000。\r\n②：持有比原本攻击力高的攻击力的这张卡被战斗·效果破坏的场合，以自己墓地1只怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44947065,'强者的苦痛','①：对方场上的怪兽的攻击力下降那怪兽的等级×100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44954628,'巨大战舰 四腕','这张卡召唤时放置3个指示物。这张卡不会被战斗破坏。进行战斗的场合，在伤害步骤结束时取除1个指示物。没有指示物放置的状态进行战斗的场合，伤害步骤结束时这张卡破坏。此外，可以把这张卡的1个指示物取除，破坏场上1张魔法·陷阱卡。这个效果1个回合只能使用1次。','放置指示物','移除指示物','破坏','魔陷破坏','','','','','','','','','','','','');
INSERT INTO texts VALUES(44956694,'ROM云雌羊',replace(replace('①：这张卡召唤成功时，以「ROM云雌羊」以外的自己墓地1只电子界族怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：这张卡被战斗·效果破坏的场合才能发动。从卡组把「ROM云雌羊」以外的1只4星以下的电子界族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44968459,'沉默魔爆破',replace(replace('①：自己场上有「沉默魔术师」怪兽存在，自己手卡比对方多的场合，自己·对方的战斗阶段才能发动。双方直到手卡变成6张为止从卡组抽卡。这张卡的发动和效果不会被无效化。\r\n②：把墓地的这张卡除外才能发动。从卡组把1只「沉默魔术师」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'抽卡','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(44968687,'传说的渔人三世',replace(replace('这张卡不能通常召唤。把自己场上1只「传说的渔人」解放的场合才能特殊召唤。\r\n①：这张卡特殊召唤成功时才能发动。对方场上的怪兽全部除外。这个回合这张卡不能攻击。\r\n②：这张卡不会被战斗·效果破坏，不受魔法·陷阱卡的效果影响。\r\n③：1回合1次，自己主要阶段才能发动。除外的对方的卡全部回到墓地，这个回合，对方受到的战斗·效果伤害只有1次变成2倍。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45002991,'大君主之圣像骑士',replace(replace('包含连接怪兽的效果怪兽2只以上\r\n①：这张卡的攻击力上升这张卡所连接区的怪兽的原本攻击力数值。\r\n②：这张卡所连接区的怪兽不能攻击。\r\n③：1回合1次，把这张卡所连接区的自己1只「圣像骑士」怪兽或者「星遗物」怪兽解放，以对方场上1张表侧表示的卡为对象才能发动。那张卡的效果直到回合结束时无效。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45010690,'鲜花机器人','①：这张卡被送去墓地的场合发动。自己从卡组抽1张。那之后，1张手卡回到持有者卡组最上面。','把自己1张手卡放在卡组最上面','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45014450,'霸王紫龙 异色眼猛毒龙',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，以自己场上1只融合怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升对方场上的怪兽数量×1000。\r\n【怪兽效果】\r\n「凶饿毒」怪兽＋「异色眼」怪兽\r\n这张卡用融合召唤以及灵摆召唤才能特殊召唤。\r\n①：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。直到结束阶段，这张卡的攻击力上升那只怪兽的攻击力数值，这张卡得到和那只怪兽相同的原本的卡名·效果。\r\n②：怪兽区域的这张卡被破坏的场合才能发动。选自己的灵摆区域1张卡特殊召唤，这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'融合怪兽攻击力上升','复制效果','结束复制效果','灵摆区域卡特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(45023678,'闪电处刑人','连锁积累有3个的场合，把对方场上1张卡破坏。同1组连锁上有复数次同名卡的效果发动的场合，这个效果不能发动。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45025640,'拒绝棉羊',replace(replace('①：这张卡的战斗发生的对对方的战斗伤害由自己代受。\r\n②：这张卡的战斗不让这张卡被破坏，让自己受到战斗伤害的场合发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45033006,'正义盟军 擒敌乌贼','这张卡召唤成功时，直到这个回合的结束阶段时可以得到对方场上里侧守备表示存在的1只怪兽的控制权。','获得控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45037489,'鲜花骑士',replace(replace('「鲜花同调士」＋调整以外的怪兽1只以上\r\n对方把魔法·陷阱卡发动时才能发动。那次发动无效并破坏。这个效果在自己回合才能发动1次。','\r',char(13)),'\n',char(10)),'魔法·陷阱卡的发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45041488,'核成守护者','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只岩石族怪兽给对方观看。或者都不进行让这张卡破坏。效果怪兽的效果发动时，可以把这张卡解放让那个发动无效并破坏。','选择一张「核成兽的钢核」送去墓地','选择一只岩石族怪物给对方观看','破坏「核成守护者」','效果怪物发动无效并破坏','','','','','','','','','','','','');
INSERT INTO texts VALUES(45042329,'地雷兽','强大的电磁波不停的向周围发射。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45045866,'海洋看守者','这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。这张卡被战斗破坏的场合，从自己卡组把1只「千眼水母」或「母脑鱼」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45078193,'电子暗黑火炮',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：把这张卡从手卡丢弃才能发动。从卡组把1只机械族「电子暗黑」怪兽加入手卡。\r\n②：把这张卡当作装备卡使用来装备的怪兽进行战斗的伤害计算时才能发动。从卡组把1只怪兽送去墓地。\r\n③：给怪兽装备的这张卡被送去墓地的场合才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45082499,'异热同心武器-雷神猛虎剑','自己的主要阶段时，手卡或者自己场上的这只怪兽可以当作攻击力上升1200的装备卡使用给自己场上的名字带有「希望皇 霍普」的怪兽装备。此外，这张卡当作装备卡使用而装备中的场合，自己场上的名字带有「异热同心武器」的卡不会被对方的卡的效果破坏。装备怪兽被卡的效果破坏的场合，作为代替把这张卡破坏。「异热同心武器-雷神猛虎剑」在自己场上只能有1张表侧表示存在。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45103815,'召唤师 莱斯贝尔特',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的等级上升1星。\r\n【怪兽描述】\r\n非常关心爱护妹妹塞姆贝尔，温和亲切的哥哥莱斯贝尔特。刚过中午的午后他跟妹妹一起读魔术书的时间是每日惯例，见到那俩人和睦的情景让周围人们也自然而然被治愈心灵。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45118716,'魔法回收士','对方怪兽的攻击宣言时把墓地的这张卡从游戏中除外，选择自己墓地1张魔法卡才能发动。自己卡组最上面的卡送去墓地，选择的卡回到卡组最下面。','魔法回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45121025,'黑影之鬼王','被黑影附体的恶鬼。用惊人的速度突击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45133463,'死神的呼声','从自己墓地有怪兽特殊召唤时才能发动。选择自己墓地存在的1只「苏帕伊」或者「赤蚁」特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45141013,'大热波','主要阶段1的开始时才能发动。直到下次的自己的抽卡阶段时，双方不能把效果怪兽召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45141844,'执着的老魔术师','反转：破坏对方场上1只怪兽。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45148985,'玄化执行神',replace(replace('这张卡不能通常召唤。从自己墓地以及自己场上的表侧表示的卡之中把「玄化」卡5种类各1张除外的场合才能特殊召唤。\r\n①：场上的这张卡不会被效果破坏，不能用效果除外。\r\n②：对方场上的卡数量比自己场上的卡多的场合，1回合1次，以除外的1只自己的「玄化」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽在下个回合的结束阶段除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45159319,'石像迎击炮','这张卡1个回合1次可以变成里侧守备表示。','变成里侧守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45170821,'幻影英雄 崇拜人',replace(replace('「英雄」怪兽×2\r\n①：1回合1次，以对方场上1只表侧表示怪兽和这张卡以外的自己场上1只「英雄」怪兽为对象才能发动。那只对方怪兽的攻击力·守备力直到回合结束时下降那只自己怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'攻守下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45178472,'满堂红','选择这张卡以外的场上表侧表示存在的2张魔法·陷阱卡和盖放的3张魔法·陷阱卡才能发动。选择的卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45184165,'急袭猛禽-骷髅雕',replace(replace('「急袭猛禽-骷髅雕」的①的效果1回合只能使用1次。\r\n①：超量素材的这张卡为让超量怪兽的效果发动而被取除送去墓地的场合，把墓地的这张卡除外，以自己墓地1张「急袭猛禽」卡为对象才能发动。那张卡加入手卡。\r\n②：场上的这张卡为素材作超量召唤的怪兽得到以下效果。\r\n●这次超量召唤成功的场合发动。这张卡的攻击力上升300。','\r',char(13)),'\n',char(10)),'这张卡的攻击力上升300','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45195443,'元素英雄 刚甲侠',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从手卡把1只4星以下的「英雄」怪兽特殊召唤。\r\n②：这张卡被魔法卡的效果从怪兽区域送去墓地的场合，以「元素英雄 刚甲侠」以外的自己墓地1只「英雄」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45206713,'DD 螺涡史莱姆',replace(replace('「DD 螺涡史莱姆」的①②的效果1回合各能使用1次。\r\n①：这张卡在手卡存在的场合，自己主要阶段才能发动。「DDD」融合怪兽卡决定的包含这张卡的融合素材怪兽从手卡送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：把墓地的这张卡除外才能发动。从手卡把1只「DD」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45215225,'毛绒动物天使',replace(replace('←8 【灵摆】 8→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：以自己墓地1只「毛绒动物」怪兽或者「锋利小鬼」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果的发动后，直到回合结束时自己不是融合怪兽不能从额外卡组特殊召唤。\r\n【怪兽效果】\r\n这个卡名的怪兽效果1回合只能使用1次。\r\n①：把这张卡解放，以自己墓地1只「魔玩具」融合怪兽为对象才能发动。选1张手卡丢弃，作为对象的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45215453,'德尔塔大日',replace(replace('调整＋调整以外的光属性怪兽1只以上\r\n这张卡表侧守备表示存在的场合，自己的结束阶段时可以从自己卡组选择1张装备魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45221020,'寻宝熊猫','①：从自己墓地把最多3张魔法·陷阱卡里侧表示除外才能发动。和除外的卡数量相同等级的1只通常怪兽从卡组特殊召唤。','特殊召唤','请选择要特殊召唤的怪兽的等级','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45222299,'邪遗式幽风乌贼怪','名字带有「遗式」的仪式魔法卡降临。这张卡仪式召唤成功时，把对方手卡随机最多2张确认，从那之中选择1张回到持有者卡组。','确认手卡','','','','','','','','','','','','','','','');
