INSERT INTO texts VALUES(45247637,'蔷薇刻印','把自己墓地存在的1只植物族怪兽从游戏中除外发动。得到这张卡装备的1只对方怪兽的控制权。自己的结束阶段时装备怪兽的控制权转移给对方。自己的准备阶段时得到装备怪兽的控制权。','控制权转移','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45282603,'召唤师 塞姆贝尔','自己的主要阶段时，可以把和这张卡相同等级的1只怪兽从手卡特殊召唤。这个效果只在这张卡在场上表侧表示存在能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45286019,'齿轮齿轮人 Mk-3','这张卡用名字带有「齿轮齿轮」的卡的效果特殊召唤成功时，可以从自己的手卡·墓地选「齿轮齿轮人 Mk-3」以外的1只名字带有「齿轮齿轮」的怪兽表侧守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。「齿轮齿轮人 Mk-3」的效果1回合只能使用1次，这个效果发动的回合，自己不能把名字带有「齿轮齿轮」的怪兽以外的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45298492,'伤痕战士',replace(replace('调整＋调整以外的怪兽1只以上\r\n只要这张卡在场上表侧表示存在，对方不能选择表侧表示存在的其他的战士族怪兽作为攻击对象。此外，这张卡1回合只有1次不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45305419,'继承之印','自己墓地有3张同名怪兽卡存在时发动。选择那些怪兽的其中1只在自己场上特殊召唤，并装备这张卡。这张卡破坏时，装备怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45311864,'哥布林盗贼','给与对方基本分500分的伤害。自己回复500基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45313993,'红莲狼','①：自己对「共鸣者」怪兽的召唤成功时才能发动。这张卡从手卡特殊召唤。这个效果特殊召唤的这张卡的攻击力变成一半。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45349196,'恶魔龙 暗黑魔龙',replace(replace('6星「恶魔」通常怪兽＋「真红眼」通常怪兽\r\n自己对「恶魔龙 暗黑魔龙」1回合只能有1次特殊召唤。\r\n①：这张卡进行战斗的场合，对方直到伤害步骤结束时魔法·陷阱·怪兽的效果不能发动。\r\n②：融合召唤的这张卡进行战斗的战斗阶段结束时，以自己墓地1只「真红眼」通常怪兽为对象才能发动。给与对方为墓地的那只怪兽的原本攻击力数值的伤害。那之后，那只怪兽回到卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45358284,'镜瓢虫','自己场上有表侧表示怪兽1只以上存在，自己墓地没有怪兽存在的场合，这张卡可以从手卡特殊召唤。这个效果特殊召唤成功时，这张卡的等级变成这张卡以外的自己场上存在的怪兽的等级合计的等级。此外，场上表侧表示存在的这张卡的等级超过12的场合，这张卡破坏。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45379225,'念力通灵保健师',replace(replace('调整＋调整以外的怪兽1只以上\r\n把自己墓地存在的1只念动力族怪兽从游戏中除外发动。自己回复1200基本分。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'回复1200基本分','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45383307,'三形金字塔·巡航机',replace(replace('「三形金字塔·巡航机」的③的效果1回合只能使用1次。\r\n①：只要这张卡在场地区域存在，每次岩石族怪兽召唤，自己回复500基本分。\r\n②：场上有「三形金字塔」怪兽召唤的场合才能发动。自己从卡组抽1张，那之后选1张手卡丢弃。\r\n③：场地区域的表侧表示的这张卡被送去墓地的场合才能发动。从卡组把1只「三形金字塔」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'回复基本分','','','','','','','','','','','','','','','本数据库来自23333服，转载请注明出处');
INSERT INTO texts VALUES(45410988,'真红眼转生',replace(replace('「真红王」的降临必需。\r\n①：从自己的手卡·场上把等级合计直到8以上的怪兽解放或者作为解放的代替而把自己墓地的「真红眼」怪兽除外，从手卡把「真红王」仪式召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45425051,'妖精王 奥布朗','①：只要这张卡在怪兽区域守备表示存在，自己场上的植物族怪兽的攻击力·守备力上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45439263,'熔岩风神翼龙','自己墓地有名字带有「熔岩」的怪兽3种类以上存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45450218,'正义盟军 致命武器','这张卡战斗破坏光属性怪兽送去墓地时，从自己卡组抽1张卡。这个效果抽到的卡是4星以下的暗属性怪兽的场合，可以把那张卡给对方观看在自己场上特殊召唤。','抽卡','是否要特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45452224,'金华猫',replace(replace('这张卡不能特殊召唤。\r\n①：这张卡召唤·反转时，以自己墓地1只1星怪兽为对象才能发动。那只怪兽特殊召唤。这张卡从场上离开时那只怪兽除外。\r\n②：这张卡召唤·反转的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'返回手牌','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45458027,'上弦式神','这张卡不能通常召唤。自己墓地的怪兽只有机械族的场合可以特殊召唤。1回合1次，选择对方场上表侧表示存在的1只怪兽才能发动。选择的怪兽的攻击力直到结束阶段时变成0。','攻击力变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45462639,'暗红之魔导师','这张卡召唤成功时，这张卡放置2个魔力指示物。只要这张卡在场上表侧表示存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。这张卡放置的魔力指示物每有1个，这张卡的攻击力上升300。1回合1次，可以把这张卡放置的2个魔力指示物取除，对方手卡随机丢弃1张。','放置指示物','丢弃手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45467446,'白色灵龙',replace(replace('这张卡在规则上也当作「青眼」卡使用。\r\n①：这张卡只要在手卡·墓地存在，当作通常怪兽使用。\r\n②：这张卡召唤·特殊召唤成功时，以对方场上1张魔法·陷阱卡为对象才能发动。那张卡除外。\r\n③：对方场上有怪兽存在的场合，把这张卡解放才能发动。从手卡把1只「青眼白龙」特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45496268,'超级防卫机器人·象斗士','这张卡召唤成功时，可以从手卡把1只名字带有「超级防卫机器人」的怪兽或者「轨道 7」特殊召唤。1回合1次，选择自己场上1只名字带有「超级防卫机器人」的怪兽或者「轨道 7」才能发动。选择的怪兽的等级直到结束阶段时变成8星。此外，把这张卡作为超量召唤的素材的场合，不是机械族怪兽的超量召唤不能使用。','特殊召唤','等级变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45500495,'地狱荆棘',replace(replace('调整＋调整以外的植物族怪兽1只以上\r\n只要这张卡在场上表侧表示存在，双方必须为从手卡把植物族怪兽以外的怪兽召唤·特殊召唤支付每1只1000基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45531624,'精灵圣剑士',replace(replace('这张卡在规则上也当作「精灵剑士」卡使用。\r\n①：自己手卡是1张以上的场合，这张卡不能攻击。\r\n②：1回合1次，自己主要阶段才能发动。从手卡把1只「精灵剑士」怪兽特殊召唤。\r\n③：这张卡的攻击给与对方战斗伤害时才能发动。自己从卡组抽出自己场上的「精灵剑士」怪兽的数量。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45533023,'急袭猛禽-烈焰猎鹰',replace(replace('鸟兽族5星怪兽×3\r\n①：持有超量素材的这张卡可以直接攻击。\r\n②：这张卡给与对方战斗伤害时，以对方场上1只怪兽为对象才能发动。那只怪兽破坏。\r\n③：1回合1次，把这张卡1个超量素材取除才能发动。对方场上的特殊召唤的怪兽全部破坏，给与对方破坏的怪兽数量×500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45547649,'鸟面人','这张卡被战斗破坏送去墓地时，从卡组选1张「鹰身女郎」加入手卡。之后洗切卡组。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45586855,'正义盟军 造风鸟','丢弃1张手卡发动。场上表侧表示存在的调整数量的场上存在的魔法·陷阱卡回到手卡。这个效果1回合只能使用1次。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45591967,'娱乐伙伴 额外射手',replace(replace('←6 【灵摆】 6→\r\n「娱乐伙伴 额外射手」的灵摆效果1回合只能使用1次，这个效果发动的回合，自己不能灵摆召唤。\r\n①：自己主要阶段才能发动。给与对方为自己的额外卡组的表侧表示的灵摆怪兽数量×300伤害。\r\n【怪兽效果】\r\n①：1回合1次，从自己的额外卡组把1只怪兽除外，以自己或者对方的灵摆区域1张卡为对象才能发动。那张卡破坏，给与对方300伤害。','\r',char(13)),'\n',char(10)),'效果伤害','卡片破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45593005,'变形斗士·磁铁I',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：自己场上有这张卡以外的怪兽表侧攻击表示2只存在的场合，1回合1次，可以让这张卡的攻击力直到这个回合的结束阶段时上升那些怪兽的攻击力的合计数值。这个效果发动的回合，其他怪兽不能攻击。\r\n●守备表示：只要这张卡在场上表侧表示存在，自己场上存在的怪兽不能攻击。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45593826,'彼岸的恶鬼 德拉基尼亚佐',replace(replace('「彼岸的恶鬼 德拉基尼亚佐」的①③的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上没有魔法·陷阱卡存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：自己场上有「彼岸」怪兽以外的怪兽存在的场合这张卡破坏。\r\n③：这张卡被送去墓地的场合才能发动。从卡组选1张「彼岸」卡在卡组最上面放置。','\r',char(13)),'\n',char(10)),'特殊召唤','选1张「彼岸」卡在卡组最上面放置','请选择要在卡组最上面放置的卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45620686,'骚骚虫','把这张卡从手卡送去墓地发动。场上存在的1只怪兽的等级直到结束阶段时上升1星。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45627618,'霸王黑龙 异色眼叛逆龙',replace(replace('←4 【灵摆】 4→\r\n①：1回合1次，另一边的自己的灵摆区域没有卡存在的场合才能发动。从卡组选1只灵摆怪兽在自己的灵摆区域放置。\r\n【怪兽效果】\r\n龙族7星怪兽×2\r\n7星可以灵摆召唤的场合在额外卡组的表侧表示的这张卡可以灵摆召唤。\r\n①：这张卡用超量怪兽为素材作超量召唤成功的场合发动。对方场上的7星以下的怪兽全部破坏，给与对方破坏数量×1000伤害。这个回合这张卡在同1次的战斗阶段中可以作3次攻击。\r\n②：怪兽区域的这张卡被战斗·效果破坏的场合才能发动。自己的灵摆区域的卡全部破坏，这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45644898,'青色眼睛的祭司',replace(replace('「青色眼睛的祭司」的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时，以自己墓地1只光属性·1星调整为对象才能发动。那只怪兽加入手卡。\r\n②：让墓地的这张卡回到卡组，以自己场上1只效果怪兽为对象才能发动。那只怪兽送去墓地，从自己墓地选那只怪兽以外的1只「青眼」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45651298,'似耳怪','对方场上有怪兽存在，自己场上有3星的怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法的「似耳怪」的特殊召唤1回合只能有1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45653036,'暴风雨',replace(replace('选择自己场上表侧表示存在的1只名字带有「云魔物」的怪兽发动。把选择怪兽的攻击力下降，以下效果适用。\r\n●下降1000：对方场上1张魔法或者陷阱卡破坏。\r\n●下降2000：对方场上2张卡破坏。','\r',char(13)),'\n',char(10)),'攻击力下降1000','攻击力下降2000','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45662855,'宝石骑士·堇青',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●1回合1次，可以把自己墓地存在的1只名字带有「宝石」的怪兽从游戏中除外，选择自己墓地存在的1张名字带有「宝石骑士」的卡加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45667991,'降龙之魔术师',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的种族直到对方回合结束时变成龙族。\r\n【怪兽效果】\r\n①：1回合1次，自己主要阶段才能发动。这张卡的种族直到回合结束时变成龙族。\r\n②：场上的这张卡为素材作融合·同调·超量召唤的怪兽得到以下效果。\r\n●这张卡和龙族怪兽进行战斗的伤害步骤内，这张卡的攻击力变成原本攻击力的2倍。','\r',char(13)),'\n',char(10)),'种族变更','这张卡的种族变更','「降龙之魔术师」效果适用中','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45688586,'机巧蜘蛛','这张卡攻击的怪兽是暗属性的场合，那只怪兽破坏，伤害的计算适用。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45702014,'元素灵剑士·幽暮',replace(replace('①：1回合1次，从手卡把1只「元素灵剑士」怪兽送去墓地，以场上1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示。这个效果在对方回合也能发动。\r\n②：这张卡在墓地存在的场合，1回合1次，宣言1个属性才能发动。墓地的这张卡直到回合结束时变成宣言的属性。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45705025,'纹章兽 独角兽','把墓地的这张卡从游戏中除外，选择自己墓地1只念动力族超量怪兽才能发动。选择的怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。「纹章兽 独角兽」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45711266,'朱罗纪翼龙','这张卡被攻击的场合，伤害计算后攻击怪兽回到手卡。这张卡的守备力上升这个效果回到手卡的怪兽等级×100的数值。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45725480,'七星宝刀',replace(replace('「七星宝刀」在1回合只能发动1张。\r\n①：从手卡或者自己场上的表侧表示怪兽之中把1只7星怪兽除外才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45742626,'巡死神 收割者',replace(replace('6星怪兽×2\r\n「巡死神 收割者」的②的效果1回合只能使用1次。\r\n①：这张卡的攻击力·守备力上升双方墓地的暗属性怪兽数量×200。\r\n②：把这张卡1个超量素材取除才能发动。从双方卡组上面把5张卡送去墓地。','\r',char(13)),'\n',char(10)),'卡组送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45778242,'克莱因客户端蚁',replace(replace('①：只要通常召唤的这张卡在怪兽区域存在，自己场上的电子界族怪兽的攻击力·守备力在自己回合内上升500。\r\n②：场上的这张卡被战斗·效果破坏的场合，可以作为代替把自己的手卡·场上1只电子界族怪兽破坏。','\r',char(13)),'\n',char(10)),'是否使用「克莱因客户端蚁」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45778932,'沙漠风暴','全部的风属性的怪兽攻击力上升500，守备力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45801022,'电气狐猴','这张卡被对方破坏的场合，下次的对方回合，对方不能进行战斗阶段。','对方不能进行战斗阶段','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45803070,'蒂奥之虫惑魔',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时，以自己墓地1只「虫惑魔」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。\r\n②：这张卡特殊召唤成功时，以自己墓地1张「洞」通常陷阱卡或者「落穴」通常陷阱卡为对象才能发动。那张卡在自己场上盖放。盖放的卡在下次的自己回合的结束阶段除外。\r\n③：这张卡不受「洞」通常陷阱卡以及「落穴」通常陷阱卡的效果影响。','\r',char(13)),'\n',char(10)),'特殊召唤','盖放','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45809008,'命运抽卡','①：从手卡丢弃1张「命运英雄」卡才能发动。自己从卡组抽2张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45812361,'卡片汽车·D',replace(replace('这张卡不能特殊召唤。这张卡的效果发动的回合，自己不能把怪兽特殊召唤。\r\n①：这张卡召唤成功的自己主要阶段1把这张卡解放才能发动。自己从卡组抽2张。那之后，变成这个回合的结束阶段。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45815891,'废铁死亡恶魔','调整＋调整以外的怪兽1只或以上','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45869829,'速攻魔力增幅器','从自己墓地选择「速攻魔力增幅器」以外的1张速攻魔法卡回到卡组。此外，场上的这张卡被对方破坏送去墓地的场合，可以从卡组把「速攻魔力增幅器」以外的1张速攻魔法卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45871897,'失落守护者','这张卡原本的守备力，是自己除外的岩石族怪兽数×700。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45894482,'骏足之迅猛龙',replace(replace('①：这张卡可以从手卡特殊召唤。\r\n②：这张卡的①的方法特殊召唤成功的场合发动。对方可以选自身墓地1只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'对方特殊召唤','是否特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45895206,'消除黑暗的光','对方场上里侧表示的怪兽全部表侧表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45898858,'化学结合-H2O','把自己场上存在的2只「氢素龙」和1只「氧素龙」作为祭品。从自己的手卡·卡组·墓地特殊召唤1只「水龙」上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45906428,'奇迹融合','①：从自己的场上·墓地把「元素英雄」融合怪兽卡决定的融合素材怪兽除外，把那1只融合怪兽从额外卡组融合召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45909477,'月之使者','侍奉月之女神的战士。使用像新月一样的戈进行攻击！','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45939611,'小妖精','自己的主要阶段时把1张手卡送去墓地才能发动。这张卡的等级上升1星。这个效果1回合可以使用最多2次。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45939841,'龙卷','支付500基本分。场上存在的1张表侧表示的魔法或者陷阱卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45945685,'单车机人','众多交通工具机人中，和人最亲近而深受喜爱的交通工具机人。还可以装备辅助轮喔！','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45948430,'超战士的萌芽',replace(replace('「混沌战士」仪式怪兽的降临必需。「超战士的萌芽」在1回合只能发动1张。\r\n①：等级合计直到8的以下其中1个组合的怪兽送去墓地，从自己的手卡·墓地把1只「混沌战士」仪式怪兽仪式召唤。\r\n●手卡1只光属性怪兽和卡组1只暗属性怪兽\r\n●手卡1只暗属性怪兽和卡组1只光属性怪兽','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45950291,'升阶魔法-星光之力',replace(replace('①：以自己场上1只阶级最高的超量怪兽为对象才能发动。和那只自己怪兽相同种族·属性而阶级高2阶的1只怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。\r\n②：这张卡在墓地存在的场合，自己抽卡阶段的抽卡前才能发动。墓地的这张卡加入手卡。这个效果发动的回合，自己不能进行通常抽卡，不能用「升阶魔法-星光之力」的效果以外把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45960523,'玄化海龙 泰达路斯',replace(replace('①：这张卡用「玄化」怪兽的效果特殊召唤成功的场合才能发动。这张卡以外的场上的特殊召唤的表侧表示怪兽全部除外。\r\n②：这张卡被除外的场合，下个回合的准备阶段让除外的这张卡回到卡组才能发动。从卡组把「玄化海龙 泰达路斯」以外的1张「玄化」卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45974017,'暗魔界的契约书',replace(replace('「暗魔界的契约书」的①的效果1回合只能使用1次。\r\n①：从以下效果选择1个才能把这个效果发动。\r\n●以自己墓地1只「DD」灵摆怪兽为对象才能发动。那只怪兽在自己的灵摆区域放置。\r\n●从自己的额外卡组把1只表侧表示的「DD」灵摆怪兽在自己的灵摆区域放置。\r\n②：自己准备阶段发动。自己受到1000伤害。','\r',char(13)),'\n',char(10)),'选择1个效果发动','自己墓地1只「DD」灵摆怪兽在灵摆区放置','额外卡组1只「DD」灵摆怪兽在灵摆区放置','受到1000伤害','','','','','','','','','','','','');
INSERT INTO texts VALUES(45985838,'日珥龙','若自己场上有除这张卡以外的炎族怪兽存在，则这张卡不能被攻击。在自己的每回合的结束阶段，给与对方基本分500分的伤害。','给予对方500伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(45986603,'强夺','得到装备这张卡的对方怪兽的控制权。对方每次准备阶段，对方基本分回复1000分。','对方回复1000LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46008667,'圣剑 断钢湖中剑',replace(replace('「圣骑士」怪兽才能装备。「圣剑 断钢湖中剑」的②的效果1回合只能使用1次。\r\n①：装备怪兽不会成为对方的效果的对象。\r\n②：自己主要阶段把墓地的这张卡除外，以自己场上1只「圣骑士」超量怪兽为对象才能发动。和那只自己怪兽卡名不同的1只「圣骑士」超量怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46009906,'猛兽之齿','兽族才能装备。1只装备怪兽的攻击力和守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46031686,'伤害偏光盾','给与伤害的效果发动时才能发动。那个发动和效果无效，双方玩家抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46035545,'DD 魔导贤者 尼古拉',replace(replace('←8 【灵摆】 8→\r\n①：自己不是「DD」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n②：1回合1次，从手卡丢弃1只「DDD」怪兽，以自己场上1只6星以下的「DD」怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时上升2000。\r\n【怪兽效果】\r\n「DD 魔导贤者 尼古拉」的怪兽效果1回合只能使用1次。\r\n①：这张卡在灵摆区域被破坏的场合，以自己场上1只「DDD」怪兽为对象才能发动。那只怪兽回到持有者手卡，从自己的额外卡组选最多2只表侧表示的「DD」灵摆怪兽在自己的灵摆区域放置。那些卡的灵摆效果在这个回合不能发动。','\r',char(13)),'\n',char(10)),'攻守变化','回到手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46037213,'守护者·特莱斯','当自己场上存在「闪光之双剑-雷震」时才能召唤·反转召唤·特殊召唤。这张卡被破坏送去墓地时，将墓地里存在的这张卡祭牲召唤时作为祭品的怪兽特殊召唤到自己场上。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46044841,'薰风隼','场上表侧表示存在的这张卡被战斗以外送去墓地时，可以从自己卡组把1只名字带有「薰风」的怪兽里侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46052429,'高等仪式术',replace(replace('仪式怪兽的降临必需。\r\n①：等级合计直到变成和仪式召唤的怪兽相同为止，从卡组把通常怪兽送去墓地，从手卡把1只仪式怪兽仪式召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46060017,'十二兽的会局',replace(replace('「十二兽的会局」的①的效果1回合只能使用1次。\r\n①：以自己场上1张表侧表示的卡为对象才能把这个效果发动。那张卡破坏，从卡组把1只「十二兽」怪兽特殊召唤。\r\n②：这张卡被效果破坏送去墓地的场合，以自己场上1只「十二兽」超量怪兽为对象才能发动。把墓地的这张卡在那只超量怪兽下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46066477,'娱乐乐队飓风',replace(replace('「娱乐乐队飓风」在1回合只能发动1张。\r\n①：以最多有自己场上的「娱乐伙伴」怪兽数量的对方场上的卡为对象才能发动。那些卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46083111,'机怪虫·树突虫',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡反转的场合才能发动。从卡组把1只怪兽送去墓地。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合才能发动。「机怪虫·树突虫」以外的2只「机怪虫」怪兽从卡组里侧守备表示特殊召唤（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'送去墓地','2只怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46083380,'攻通规制','对方场上有怪兽3只以上存在的场合，对方不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46089249,'核杀轮','把手卡1张「核成兽的钢核」给对方观看发动。把场上表侧表示存在的1只怪兽破坏，双方受到1000分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46104361,'电子界白帽客',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：自己场上有相同种族的怪兽2只以上存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡作为连接素材送去墓地的场合才能发动。对方场上的全部怪兽的攻击力直到回合结束时下降1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46128076,'黑魔导师 库兰','自己的准备阶段时，给与对方基本分对方场上存在的怪兽数×300分数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46130346,'火球','给与对方基本分500分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46132282,'甲化铠骨骼',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡同调召唤成功的场合发动。这个回合，这张卡不会被战斗·效果破坏，自己受到的全部伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46136942,'娱乐伙伴 异色眼融解者',replace(replace('←4 【灵摆】 4→\r\n①：1回合1次，自己主要阶段才能发动。从自己的手卡·场上把龙族融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n【怪兽效果】\r\n这个卡名的①的怪兽效果1回合只能使用1次。\r\n①：自己的灵摆怪兽和对方怪兽进行战斗的伤害步骤开始时才能发动。这张卡从手卡特殊召唤，那只自己怪兽不会被那次战斗破坏。\r\n②：自己主要阶段才能发动。融合怪兽卡决定的包含这张卡的融合素材怪兽从自己场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。那个时候，自己的灵摆区域存在的融合素材怪兽也能作为融合素材使用。','\r',char(13)),'\n',char(10)),'融合召唤','这张卡从手卡特殊召唤','使用灵摆区怪兽融合召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46145256,'闪光之结界像','只要这张卡在场上表侧表示存在，不能作光属性怪兽以外的特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46159582,'遗式的仪水镜','名字带有「遗式」的仪式怪兽的降临必需。必须从手卡·自己场上把直到变成和仪式召唤的怪兽相同等级为止的怪兽解放。此外，可以让墓地存在的这张卡回到卡组，选择自己墓地存在的1只名字带有「遗式」的仪式怪兽回到手卡。','墓地的仪式怪兽回到手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46173679,'终焉之焰',replace(replace('这张卡发动的回合，自己不能用这张卡的效果以外把怪兽召唤·反转召唤·特殊召唤。\r\n①：在自己场上把2只「黑焰衍生物」（恶魔族·暗·1星·攻/守0）守备表示特殊召唤。这衍生物不能为暗属性以外的怪兽的上级召唤而解放。','\r',char(13)),'\n',char(10)),'特殊召唤衍生物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46173680,'黑焰衍生物','由「终焉之焰」的效果特殊召唤。这衍生物不能为暗属性以外的怪兽的上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46173681,'黑焰衍生物','由「终焉之焰」的效果特殊召唤。这个衍生物不能作为暗属性怪兽以外的祭品召唤的祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46181000,'前线基地','在自己的主要阶段可以从手卡特殊召唤1只4星以下的同盟怪兽。此效果1回合只能启动1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46195773,'涡轮战士',replace(replace('「涡轮同调士」＋调整以外的怪兽1只以上\r\n这张卡向6星以上的同调怪兽攻击的场合，把攻击对象怪兽的攻击力变成一半数值。这张卡不会成为6星以下的效果怪兽的效果的对象。','\r',char(13)),'\n',char(10)),'攻击对象攻击变成一半','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46232525,'赫谟之爪',replace(replace('这张卡的卡名在规则上也当作「传说之龙 赫谟」使用。「赫谟之爪」在1回合只能发动1张。\r\n①：「赫谟之爪」的效果才能特殊召唤的融合怪兽卡记述的种族的1只怪兽从自己的手卡·场上送去墓地（那张卡在场上盖放的场合，翻开确认）。那之后，把那1只融合怪兽从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46237548,'红莲魔斗士','自己墓地存在的通常怪兽只有3只的场合，这张卡可以把自己墓地存在的2只通常怪兽从游戏中除外，从手卡特殊召唤。1回合1次，可以选择自己墓地存在的1只4星以下的通常怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46239604,'魔知青蛙','这张卡的卡名只要在场上表侧表示存在当作「死亡青蛙」使用。只要这张卡在场上表侧表示存在，对方不能选择其他怪兽作为攻击对象。这张卡从场上送去墓地时，可以从自己的卡组·墓地把「魔知青蛙」以外的1只名字带有「青蛙」的怪兽加入手卡。','加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46247282,'铁骑龙 创世母神机',replace(replace('这张卡不能通常召唤，用这张卡的①的效果才能特殊召唤。这个卡名的①的效果1回合只能使用1次。\r\n①：3张以上的卡在相同纵列存在的场合才能发动。这张卡从手卡特殊召唤。这个效果在对方回合也能发动。\r\n②：这张卡特殊召唤成功的场合发动。和这张卡相同纵列的其他卡全部破坏。\r\n③：只要这张卡在怪兽区域存在，和这张卡相同纵列的没有使用的区域不能使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46247516,'阴阳师 道','侵蚀阴与阳的力量、产生出邪恶力量的魔导师。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46253216,'友方误射','①：对方的魔法·陷阱·怪兽的效果发动时，以那张卡以外的场上1张卡为对象才能发动。作为对象的卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46259438,'契约洗净','①：自己的魔法与陷阱区域的「契约书」卡全部破坏。自己从卡组抽出破坏的数量。那之后，自己回复抽出数量×1000基本分。','卡片破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46263076,'地缚神 卡帕克·阿普','名字带有「地缚神」的怪兽在场上只能有1只表侧表示存在。场上没有表侧表示场地魔法卡存在的场合这张卡破坏。对方不能选择这张卡作为攻击对象。这张卡可以直接攻击对方玩家。这张卡战斗破坏对方怪兽的场合，给与对方基本分破坏怪兽的攻击力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46272804,'长枪鳞虫','①：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46290741,'城塞鲸',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡在手卡·墓地存在的场合，把自己场上2只水属性怪兽解放才能发动。这张卡特殊召唤。\r\n②：这张卡特殊召唤成功的场合才能发动。从卡组选1张「潜海奇袭」在自己场上盖放。\r\n③：1回合1次，只以自己场上的水属性怪兽1只为对象的对方的魔法·陷阱·怪兽的效果发动时才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46291010,'念动力兽','这张卡召唤成功时，可以把自己卡组1只念动力族怪兽从游戏中除外。这张卡变成和这个效果除外的怪兽相同等级。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46303688,'轮盘炸弹','在自己的每回合的主要阶段可以掷2次骰子，在掷出的点数中选择1个，破坏场上1只表侧表示的与此点数相同等级的怪兽。','掷骰子','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46337945,'仪水镜的瞑想术','把手卡1张仪式魔法卡给对方观看，选择自己墓地存在的2只名字带有「遗式」的怪兽发动。选择的墓地的怪兽回到手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46354113,'火箭赫谟炮',replace(replace('这张卡在用「赫谟之爪」的效果把自己的手卡·场上的战士族怪兽送去墓地的场合才能特殊召唤。\r\n①：这张卡特殊召唤成功的场合，以这张卡以外的场上1只怪兽为对象发动。这张卡当作装备卡使用给那只怪兽装备。\r\n②：用这张卡的效果把这张卡装备的怪兽在同1次的战斗阶段中可以作2次攻击，向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46363422,'熟练的白魔导师','自己与对方每发动1次魔法，就在这张卡上放置1个魔力指示物（最多只能放置3个）。当这张卡处于放置了3个魔力指示物的状态时，可以将这张卡祭掉，从自己的手卡、卡组或墓地中特殊召唤1只「巴斯达·布雷达」上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46372010,'地狱门的契约书',replace(replace('「地狱门的契约书」的①的效果1回合只能使用1次。\r\n①：自己主要阶段才能发动。从卡组把1只「DD」怪兽加入手卡。\r\n②：自己准备阶段发动。自己受到1000伤害。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46384403,'迅捷蝠鲼','场上存在的这张卡被卡的效果送去墓地时，可以从自己卡组把「迅捷蝠鲼」任意数量特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46384672,'武装龙 LV5',replace(replace('①：从手卡把1只怪兽送去墓地，以持有那只怪兽的攻击力以下的攻击力的对方场上1只怪兽为对象才能发动。持有为这个效果发动而送去墓地的怪兽的攻击力以下的攻击力的那只对方怪兽破坏。\r\n②：这张卡战斗破坏怪兽的回合的结束阶段，把场上的这张卡送去墓地才能发动。从手卡·卡组把1只「武装龙 LV7」特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46404281,'熔岩人岩浆炮兵','从手卡把1只炎属性怪兽送去墓地发动。给与对方基本分500分伤害。这个效果1回合可以使用最多2次。','给与对方500伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46411259,'突然变异','祭掉自己场上1只怪兽。从融合卡组选取和祭掉的怪兽同样等级的融合怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46425662,'元素灵剑士·波涛',replace(replace('①：1回合1次，从手卡把1只「元素灵剑士」怪兽送去墓地，以自己墓地1只「元素灵剑士·波涛」以外的「元素灵剑士」怪兽或者「灵神」怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：这张卡在墓地存在的场合，1回合1次，宣言1个属性才能发动。墓地的这张卡直到回合结束时变成宣言的属性。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46427957,'破灭之女神 露茵',replace(replace('「世界末日」降临。\r\n①：这张卡的攻击破坏对方怪兽时才能发动。这张卡只再1次可以继续攻击。','\r',char(13)),'\n',char(10)),'连续攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46448938,'魔导书的神判','这张卡发动的回合的结束阶段时，从自己卡组把最多有这张卡的发动后自己或者对方发动的魔法卡数量的「魔导书的神判」以外的名字带有「魔导书」的魔法卡加入手卡。那之后，可以把持有这个效果加入手卡的卡数量以下的等级的1只魔法师族怪兽从卡组特殊召唤。「魔导书的神判」在1回合只能发动1张。','检索效果','是否要把1只魔法师族怪兽从卡组特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46457856,'小恐龙','体形虽然小，但性格凶暴。经常和同伴相争。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46461247,'陷阱大师','反转：场上1张陷阱卡破坏。里侧表示翻开确认后破坏。','陷阱破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46474915,'魔法幽灵','向对方施展法术，使敌人陷入混乱和恐惧，从而胡乱攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46480475,'不协和音','双方玩家不能同调召唤。发动后第3次的自己的结束阶段时这张卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46500985,'炼成阵·构成',replace(replace('①：自己场上的「炼装」怪兽的攻击力·守备力上升300。\r\n②：只要自己的灵摆区域有「炼装」卡存在，效果怪兽以外的自己场上的「炼装」怪兽不受对方的效果影响。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46502013,'妖精轮','自己场上有怪兽表侧攻击表示2只以上存在的场合，对方不能选择攻击力最低的怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46502744,'应战的G',replace(replace('①：对方把包含把怪兽特殊召唤效果的魔法卡发动时才能发动。这张卡从手卡特殊召唤。\r\n②：只要这张卡的①的效果特殊召唤的这张卡在怪兽区域存在，被送去墓地的卡不去墓地而除外。\r\n③：这张卡从场上送去墓地的场合才能发动。从卡组把「应战的G」以外的1只攻击力1500以下的昆虫族·地属性怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46508640,'龙脉栖息者','①：这张卡的攻击力上升自己的魔法与陷阱区域的永续魔法卡数量×300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46534755,'火焰乌贼','在水里仍然被火焰熊熊包围着的章鱼。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46565218,'撒旦老人',replace(replace('①：这张卡可以把对方场上1只怪兽解放，从手卡往对方场上守备表示特殊召唤。\r\n②：这张卡用这张卡的①的方法特殊召唤成功的回合的结束阶段才能发动。把这个效果发动的玩家从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46571052,'血兰','这张卡召唤成功时，可以从手卡特殊召唤1只「死亡石斛」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46572756,'真次世代终极人','场上表侧表示存在的这张卡被破坏送去墓地时，可以让自己墓地存在的2只名字带有「次世代」的怪兽回到卡组。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46589034,'急袭猛禽-痛苦伯劳',replace(replace('「急袭猛禽-痛苦伯劳」的效果1回合只能使用1次，把这张卡作为超量召唤的素材的场合，不是鸟兽族怪兽的超量召唤不能使用。\r\n①：这张卡在手卡存在的场合，以自己场上1只「急袭猛禽」怪兽为对象才能发动。自己受到那只怪兽的攻击力或守备力之内较低方数值的伤害，这张卡从手卡特殊召唤。这个效果特殊召唤的这张卡的等级变成和作为对象的怪兽的等级相同。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46609443,'混沌终结之王','这张卡战斗破坏对方怪兽送去墓地，可以从自己卡组把1只5星以上的攻击力1600以下的怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46613515,'洁净栗子球',replace(replace('「洁净栗子球」的②的效果1回合只能使用1次。\r\n①：给与伤害的怪兽的效果由对方发动时，把这张卡从手卡丢弃才能发动。那个发动无效。\r\n②：对方怪兽的直接攻击宣言时，把墓地的这张卡除外才能发动。自己从卡组抽1张。那张抽到的卡是怪兽的场合，可以把那只怪兽特殊召唤。那之后，攻击对象转移为那只怪兽。','\r',char(13)),'\n',char(10)),'伤害效果的发动无效','把墓地的这张卡除外','是否把那只怪兽特殊召唤？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46647144,'星遗物-『星枪』',replace(replace('这个卡名的①③的效果1回合各能使用1次。\r\n①：包含连接怪兽的怪兽之间进行战斗的伤害计算时，把这张卡从手卡丢弃才能发动。那只进行战斗的对方怪兽的攻击力下降3000。\r\n②：只要这张卡在怪兽区域存在，对方不能向其他的「星遗物」怪兽攻击。\r\n③：从额外卡组有怪兽特殊召唤的场合发动。在双方场上把「星遗物衍生物」（机械族·暗·1星·攻/守0）各1只守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46647145,'星遗物衍生物','由「星遗物-『星枪』」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46652477,'转生的预言','选择墓地存在的2张卡，加入持有者卡组洗切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46656406,'咒言之镜','对方从卡组把怪兽特殊召唤时才能发动。那些怪兽破坏，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46657337,'姆卡姆卡','只要这张卡表侧表示在场上存在，控制者手上每有1张卡，这张卡的攻击力·守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46659709,'银河战士',replace(replace('「银河战士」的②的效果1回合只能使用1次。\r\n①：把这张卡以外的手卡1只光属性怪兽送去墓地才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：这张卡特殊召唤成功时才能发动。从卡组把1只「银河」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46668237,'森之番人 绿狒狒','①：这张卡在手卡·墓地存在，自己场上的表侧表示的兽族怪兽被效果破坏送去墓地时，支付1000基本分才能发动。这张卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
