INSERT INTO texts VALUES(46710683,'黑羽-银盾之密史脱拉','场上存在的这张卡被破坏送去墓地的场合，这个回合自己受到的战斗伤害只有1次变成0。','这个回合自己受到的战斗伤害只有1次变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46718686,'海星葵','被污染的水源侵蚀而狂暴化的海星。从口中吐出溶解液攻击敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46724542,'电子龙·凯旋',replace(replace('包含「电子龙」的机械族怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡的卡名只要在场上·墓地存在当作「电子龙」使用。\r\n②：这张卡没有攻击宣言的自己·对方的战斗阶段，以自己场上1只攻击力2100以上的机械族怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时上升2100。这个效果的发动后，直到回合结束时这张卡的战斗的双方的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46759931,'幻影英雄 三一人',replace(replace('「英雄」怪兽×3\r\n①：这张卡融合召唤成功的回合，这张卡的攻击力变成原本攻击力的2倍。\r\n②：融合召唤的这张卡在同1次的战斗阶段中可以作3次攻击。\r\n③：这张卡不能直接攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46772449,'励辉士 入魔蝇王',replace(replace('4星怪兽×2\r\n①：自己主要阶段或者对方战斗阶段，对方的手卡·场上的卡合计数量比自己的手卡·场上的卡合计数量多的场合，把这张卡1个超量素材取除才能发动。这张卡以外的场上的卡全部破坏。这个效果的发动后，直到回合结束时对方受到的全部伤害变成0。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46796664,'DD 魔导贤者 哥白尼',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「DD」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n②：只在这张卡在灵摆区域存在才有1次，给与自己伤害的魔法卡的效果发动的场合，可以把那个效果无效。那之后，这张卡破坏。\r\n【怪兽效果】\r\n「DD 魔导贤者 哥白尼」的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把「DD 魔导贤者 哥白尼」以外的1张「DD」卡或者「契约书」卡送去墓地。','\r',char(13)),'\n',char(10)),'送去墓地','是否使用「DD 魔导贤者 哥白尼」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46820049,'地狱将军·墨非斯特','这张卡攻击守备表示的怪兽时，攻击力超出守备力部分的数值，对方受到那个数值的战斗伤害。这张卡对对方造成战斗伤害时，对方随机丢弃1张手卡。','丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46821314,'人形史莱姆','身体中组合了人类的遗传因子。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46833854,'魔轰神 托比','自己手卡比对方手卡少2张以上的场合，手卡1只名字带有「魔轰神」的怪兽给对方观看，把自己场上表侧表示存在的这张卡解放发动。对方场上存在的2张魔法·陷阱卡破坏。','对方场上存在的2张魔法·陷阱卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46848859,'航空母舰机人','对方以自己场上的魔法·陷阱卡为对象把持有使场上的魔法·陷阱卡破坏效果的卡发动时，可以丢弃1张手卡，那张卡的发动和效果无效并破坏。','无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46864967,'岩之战士','岩石战士。挥舞着非常重的石头剑。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46871387,'No.55 隆隆隆巨无霸',replace(replace('4星怪兽×2\r\n把这张卡1个超量素材取除才能发动。从自己墓地选择1只岩石族·地属性·4星怪兽加入手卡。「No.55 隆隆隆巨无霸」的效果1回合只能使用1次。此外，只要这张卡在场上表侧表示存在，自己场上的全部怪兽的守备力上升800。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46874015,'六武众推参！','自己墓地存在的1只名字带有「六武众」的怪兽在自己场上特殊召唤。这个效果特殊召唤的怪兽在这个回合的结束阶段时破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46895036,'鬼计无头骑士',replace(replace('1星怪兽×2\r\n①：这张卡的攻击力上升自己场上的「鬼计」卡数量×200。\r\n②：1回合1次，把这张卡1个超量素材取除，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时变成一半。这个效果在对方回合也能发动。\r\n③：这张卡被送去墓地的场合，以这张卡以外的自己墓地1张「鬼计」卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'攻击降低','返回手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(46897277,'电气狐','这张卡被对方破坏的场合，那个回合对方不能作怪兽的特殊召唤以及把魔法·陷阱·效果怪兽的效果发动。','对方发动限制','','','','','','','','','','','','','','','');
