INSERT INTO texts VALUES(47013502,'躲避龙','这张卡的召唤不会被无效化。这张卡召唤成功的回合，对方不能把反击陷阱卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47017574,'混沌No.92 伪骸虚龙 心地心混沌龙',replace(replace('10星怪兽×4\r\n这张卡不会被战斗破坏。自己场上的怪兽给与对方基本分战斗伤害时，自己基本分回复那个数值。此外，这张卡有「No.92 伪骸神龙 心地心龙」在作为超量素材的场合，得到以下效果。\r\n●1回合1次，把这张卡1个超量素材取除才能发动。对方场上表侧表示存在的全部卡的效果直到回合结束时无效。这个效果的发动和效果不会被无效化。','\r',char(13)),'\n',char(10)),'LP回复','效果无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47025270,'白兵战型援助机器人','这张卡每次战斗破坏对方怪兽，自己抽1张卡，之后从手卡选择1张卡放回卡组最下面。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47030842,'齿轮齿轮加速人','自己场上有名字带有「齿轮齿轮」的怪兽存在的场合，这张卡可以从手卡表侧守备表示特殊召唤。此外，这张卡从场上送去墓地时，可以从自己墓地选择「齿轮齿轮加速人」以外的1只名字带有「齿轮齿轮」的怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47060154,'狂战士','用狂暴的力量攻击，一旦暴走无人能挡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47060347,'女神的加护','自己回复3000基本分。自己场上表侧表示存在的这张卡从场上离开时，自己受到3000分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47075569,'娱乐伙伴 灵摆魔术家',replace(replace('←2 【灵摆】 2→\r\n①：自己场上有「娱乐伙伴」怪兽灵摆召唤的场合发动。自己场上的「娱乐伙伴」怪兽的攻击力直到回合结束时上升1000。\r\n【怪兽效果】\r\n「娱乐伙伴 灵摆魔术家」的怪兽效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合，以自己场上最多2张卡为对象才能发动。那些卡破坏，把破坏数量的「娱乐伙伴 灵摆魔术家」以外的「娱乐伙伴」怪兽从卡组加入手卡（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47077318,'森罗的蜜柑子 柑橘',replace(replace('①：场上的这张卡被对方破坏送去墓地时才能发动。自己卡组最上面的卡翻开，那张卡是植物族怪兽的场合，那只怪兽送去墓地。不是的场合，那张卡回到卡组最下面。\r\n②：卡组的这张卡被效果翻开送去墓地的场合发动。自己场上的全部植物族怪兽的攻击力·守备力上升300。','\r',char(13)),'\n',char(10)),'翻开卡组','攻守上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47084486,'虚无魔人',replace(replace('这张卡不能特殊召唤。\r\n①：只要这张卡在怪兽区域存在，双方不能把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47106439,'幻影遗式术师','水属性的仪式怪兽特殊召唤的场合，可以用这1张卡作为仪式召唤的解放使用。此外，从手卡把这张卡丢弃才能发动。从卡组把1只名字带有「遗式」的仪式怪兽加入手卡。','仪式怪兽加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47111934,'亚冈异虫','自己场上存在的怪兽只有「泽克斯异虫」1只的场合，自己墓地存在的这张卡可以在自己场上里侧守备表示盖放。这个效果特殊召唤的这张卡从场上离开的场合从游戏中除外。这张卡反转时，对方场上表侧表示存在的1只怪兽回到持有者手卡。','特殊召唤','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47120245,'圣骑士 鲍斯',replace(replace('①：这张卡只要在怪兽区域存在，当作通常怪兽使用。\r\n②：只要这张卡有「圣剑」装备魔法卡装备，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡等级上升1星并变成暗属性。\r\n●自己主要阶段才能发动。从卡组把3张「圣剑」卡给对方观看，对方从那之中随机选1张。那1张卡加入自己手卡，剩余送去墓地。这个卡名的这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47121070,'宝玉之双璧','自己场上名字带有「宝玉兽」的怪兽被战斗破坏送去墓地时才能发动。从卡组把1只名字带有「宝玉兽」的怪兽当作永续魔法卡使用在自己的魔法与陷阱卡区域表侧表示安置。这个回合自己受到的战斗伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47126872,'时空警察','这张卡特殊召唤成功时，选择对方场上表侧表示存在的1张卡从游戏中除外。这张卡从场上离开时，这张卡的效果从游戏中除外的卡在对方场上盖放。','除外','盖放','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47150851,'守护者·格拉尔','当自己场上存在「重力之斧-咆哮」时才能召唤·反转召唤·特殊召唤。当手卡仅有这张卡1张时，这张卡可以从手卡直接特殊召唤上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47185546,'光虫信号',replace(replace('「光虫信号」在1回合只能发动1张。\r\n①：以自己场上1只昆虫族超量怪兽为对象才能发动。比那只自己怪兽阶级高2阶或者阶级低2阶的1只昆虫族超量怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47198668,'DDD 死伟王 地狱终末神',replace(replace('←4 【灵摆】 4→\r\n①：1回合1次，以自己场上1只「DD」怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升800。\r\n【怪兽效果】\r\n①：1回合1次，自己场上的怪兽被战斗·效果破坏的场合，以那1只怪兽为对象才能发动。这张卡的攻击力直到回合结束时上升作为对象的怪兽的原本攻击力数值。这个效果发动的回合，这张卡不能直接攻击。\r\n②：这张卡不会被不以这张卡为对象的魔法·陷阱卡的效果破坏。','\r',char(13)),'\n',char(10)),'攻击力上升800','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47217354,'魔轰神 雷文','可以丢弃自己手卡任意数量，这张卡的等级直到结束阶段时上升那个数。这张卡的攻击力直到结束阶段时上升这个效果丢弃的手卡数量×400的数值。这个效果1回合只能使用1次。','等级、攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47222536,'黑魔导阵',replace(replace('「黑魔导阵」的①②的效果1回合各能使用1次。\r\n①：作为这张卡的发动时的效果处理，从自己卡组上面把3张卡确认。那之中有着有「黑魔术师」的卡名记述的魔法·陷阱卡或者「黑魔术师」的场合，可以把那1张给对方观看并加入手卡。剩下的卡用喜欢的顺序回到卡组上面。\r\n②：自己场上有「黑魔术师」召唤·特殊召唤的场合，以对方场上1张卡为对象才能发动。那张卡除外。','\r',char(13)),'\n',char(10)),'是否把1张卡加入手卡？','对方场上1张卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47226949,'骑狮机兽','具备高适应能力的电子兽。领地意识很强。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47228077,'大日五胞体','1回合1次，自己的主要阶段时可以当作装备卡使用给自己场上的名字带有「大日」的怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。只在这个效果当作装备卡使用的场合，装备怪兽战斗破坏对方怪兽的场合，可以选择对方场上存在的1张卡破坏。（1只怪兽可以装备的同盟最多1张。装备怪兽被破坏的场合，作为代替把这张卡破坏。）','变成装备卡','特殊召唤','对方场上的1张卡破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47233801,'黑蛇病','每到自己的准备阶段，这张卡对双方玩家造成200点伤害。2个回合以后，每到自己的准备阶段，这个伤害都会加倍。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47247413,'退回','对方把墓地的卡加入手卡时才能发动。对方让加入手卡的那1张卡回到卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47264717,'流星','①：场上有「星尘」怪兽存在的场合，以场上1张卡为对象才能发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47274077,'新宇之力','「元素英雄 新宇侠」才能装备。装备怪兽的攻击力上升800。装备怪兽战斗破坏怪兽送去墓地时，给与对方基本分破坏怪兽的攻击力数值的伤害。结束阶段时把这张卡加入卡组洗切。','伤害','返回卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47295267,'电子科技区','选择自己场上表侧表示存在的1只机械族的融合怪兽，直到发动回合的结束阶段时从游戏中除外。从游戏中除外的怪兽回到场上时，那只怪兽的攻击力变成2倍。下次的自己回合的准备阶段时，成为这张卡的对象的1只机械族的融合怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47297616,'光与暗之龙','这张卡不能特殊召唤。这张卡的属性也当作「暗」使用。只要这张卡在场上表侧表示存在，效果怪兽的效果·魔法·陷阱卡的发动无效。每次这个效果把卡的发动无效，这张卡的攻击力·守备力下降500。这张卡被破坏送去墓地时，选择自己墓地存在的1只怪兽发动。自己场上的卡全部破坏。选择的1只怪兽在自己场上特殊召唤。','不能特殊召唤','属性视作"暗"','效果发动无效','攻防下降500','特殊召唤','','','','','','','','','','','');
INSERT INTO texts VALUES(47319141,'飞鹰','从高空寻找猎物，一旦看上绝不让其逃走。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47325505,'化石调查','①：从卡组把1只6星以下的恐龙族怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47346782,'寝青蛙',replace(replace('这张卡不能作为融合·同调·超量·连接召唤的素材。\r\n①：这张卡召唤·反转召唤成功的场合发动。这张卡变成守备表示。\r\n②：1回合1次，以对方的主要怪兽区域1只怪兽为对象才能发动。守备表示的这张卡向作为对象的对方怪兽的相邻的怪兽区域转移控制权。那之后，对方的主要怪兽区域的「寝青蛙」只有2只的场合，得到那2只的中间存在的全部怪兽的控制权。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47346845,'电池人-单四型','这张卡召唤·反转时，可以把自己的手卡·墓地存在的1只「电池人-单四型」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47349116,'星刻之魔术师',replace(replace('4星「魔术师」灵摆怪兽×2\r\n这张卡用以上记的卡为超量素材的超量召唤才能从额外卡组特殊召唤。\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。从自己的卡组·墓地的怪兽以及自己的额外卡组的表侧表示的灵摆怪兽之中选1只魔法师族·暗属性怪兽加入手卡。\r\n②：1回合1次，自己的怪兽区域·灵摆区域的灵摆怪兽卡被战斗·效果破坏的场合，可以作为代替从自己卡组把1只魔法师族怪兽送去墓地。','\r',char(13)),'\n',char(10)),'加入手卡','是否使用「星刻之魔术师」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47349310,'天鬼食人蝠鲼','这张卡可以直接攻击对方玩家。这张卡进行直接攻击的战斗阶段结束时，这张卡直到下次的自己的准备阶段时从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47355498,'王家长眠之谷',replace(replace('①：场上的「守墓」怪兽的攻击力·守备力上升500。\r\n②：只要这张卡在场地区域存在，双方不能把墓地的卡除外，对墓地的卡有涉及的效果无效化并且不适用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47360060,'反射之圣刻印','把自己场上1只名字带有「圣刻」的怪兽解放才能发动。效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47372349,'杂技猴','用超尖端技术所开发的猴子型自律机器人。行动像做杂技一般。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47387961,'No.8 纹章王 基因组继承者',replace(replace('名字带有「纹章兽」的4星怪兽×2\r\n1回合1次，选择对方场上1只超量怪兽才能发动。这张卡当作和选择的怪兽同名卡使用，得到相同的攻击力和效果。那之后，选择的怪兽的攻击力变成0，效果无效化。这张卡的效果直到结束阶段时适用。','\r',char(13)),'\n',char(10)),'获得效果','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47395382,'魔轰神 雷吉昂',replace(replace('名字带有「魔轰神」的调整＋调整以外的怪兽1只以上\r\n自己手卡是1张以下的场合，这张卡同调召唤成功时，可以直到自己手卡变成2张从卡组抽卡。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47408488,'宝玉之树',replace(replace('①：只要这张卡在魔法与陷阱区域存在，每次「宝玉兽」怪兽在魔法与陷阱区域被放置，给这张卡放置1个宝石指示物。\r\n②：把有宝石指示物放置的这张卡送去墓地才能发动。把这张卡放置的宝石指示物数量的「宝玉兽」怪兽从卡组当作永续魔法卡使用在自己的魔法与陷阱区域表侧表示放置。','\r',char(13)),'\n',char(10)),'放置宝玉指示物','放置宝玉兽到魔法陷阱区','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47415292,'漆黑的斗龙',replace(replace('1回合1次，在自己的主要阶段，这张卡可以视为1张装备卡装备在自己的「暗魔界的战士 暗黑之剑」上，或者解除装备状态表侧攻击表示特殊召唤。当这张卡以自身效果变成装备卡时具有以下效果：\r\n●装备怪兽的攻击力·守备力上升400点。装备这张卡的怪兽攻击守备表示的怪兽时，攻击力超出部分对对方造成战斗伤害。（1只怪兽至多只能装备1张同盟。当装备了同盟的怪兽被战斗破坏时，同盟会代替怪兽被破坏。）','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47421985,'次世代水能人',replace(replace('「次世代控制员」＋调整以外的水属性怪兽1只以上\r\n这张卡战斗破坏对方怪兽送去墓地时，自己基本分回复破坏怪兽的攻击力的数值。','\r',char(13)),'\n',char(10)),'回复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47432275,'缎带栗子','这张卡成为对方怪兽的攻击对象的战斗伤害计算时，可以把那次战斗发生的对自己的战斗伤害变成0并让对方基本分回复攻击怪兽的攻击力的数值，这张卡回到手卡。','伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47435107,'原初的叫唤',replace(replace('「辉神鸟 贝努鸟」的降临必需。\r\n①：从自己的手卡·场上把等级合计直到8以上的怪兽解放，从手卡把「辉神鸟 贝努鸟」仪式召唤。\r\n②：自己结束阶段把墓地的这张卡除外，以这个回合从场上送去墓地的自己墓地1只仪式怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47436247,'紫炎的道场','每次名字带有「六武众」的怪兽召唤·特殊召唤，给这张卡放置1个武士道指示物。可以把这张卡送去墓地，把持有这张卡放置的武士道指示物数量以下的等级的1只名字带有「六武众」或者「紫炎」的效果怪兽从自己卡组特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47439573,'无情的打落','①：场上的表侧表示的卡或者墓地的卡因效果加入对方手卡时才能发动。把对方手卡确认，从那之中把加入手卡的卡以及那些同名卡全部除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47453433,'振出','丢弃1张手卡。场上1只怪兽回到持有者卡组最上面。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47457347,'魔法名-「大兽」','①：以除外的自己的「召唤兽」怪兽任意数量为对象才能发动（同名卡最多1张）。那些怪兽守备表示特殊召唤。','是否继续选择怪兽特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47459126,'调整魔术师',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●只要这张卡在场上表侧表示存在，这张卡当作调整使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47475363,'波纹防护罩 -波浪之力-','①：对方怪兽的直接攻击宣言时才能发动。对方场上的攻击表示怪兽全部回到持有者卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47480070,'亚马逊圣战士','自己场上每存在1张名称中含有「亚马逊」的怪兽卡，这张卡的攻击力上升100点。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47482043,'古代的机械苏生',replace(replace('①：「古代的机械苏生」在自己场上只能有1张表侧表示存在。\r\n②：1回合1次，自己场上没有怪兽存在的场合，以自己墓地1只「古代的机械」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽的攻击力上升200。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47506081,'红莲恐龙',replace(replace('3星怪兽×2\r\n这张卡战斗破坏对方怪兽送去墓地时，可以把这张卡1个超量素材取除，给与对方基本分1000分伤害。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47507260,'谜之剑士 LV2','攻击里侧守备表示怪兽的场合，不进行伤害计算，那只怪兽以里侧守备表示的状态直接破坏。这张卡战斗破坏怪兽的回合的结束阶段，可以把这张卡送去墓地，从手卡·卡组特殊召唤1只「谜之剑士 LV4」','破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47529357,'雾状躯体','装备怪兽不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47556396,'地中族邪界兽·埃琴磁魔神',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合，以场上1只怪兽为对象才能发动。那只怪兽是守备表示的场合，变成表侧攻击表示。那只怪兽的攻击力变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47558785,'温火鸡',replace(replace('←7 【灵摆】 7→\r\n①：1回合1次，自己主要阶段才能发动。掷1次骰子。直到回合结束时，这张卡的灵摆刻度下降出现的数目数值（最少到1）。\r\n【怪兽描述】\r\n对保龄球的热情烤焦全身的狂放火鸡。为了拿到全中而锻炼出来的身体，经常散发出极品的香味。以还没见到的火鸡球作为目标，每天不间断地练习着。','\r',char(13)),'\n',char(10)),'灵摆刻度下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47579719,'星圣·毕星团',replace(replace('光属性3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。对方场上存在的怪兽全部变成表侧守备表示。','\r',char(13)),'\n',char(10)),'变成表侧守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47594192,'三条','自己场上有衍生物以外的同名怪兽3只以上存在的场合才能发动。选择对方场上3张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47594939,'召唤限制-不协和扇区','只要这张卡在场上存在，双方玩家不能把持有和自身场上的怪兽相同等级的怪兽特殊召唤。此外，双方玩家不能把持有和自身场上的怪兽相同阶级的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47596607,'摩天楼2-英雄都市','可以从自己墓地特殊召唤1只被战斗破坏的名字带有「元素英雄」的怪兽。这个效果1回合只有1次在自己的主要阶段才能发动。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47598941,'无形噬体溶解',replace(replace('「无形噬体溶解」的②的效果1回合只能使用1次。\r\n①：「无形噬体」怪兽以外的场上的怪兽的攻击力·守备力下降场上的「无形噬体」卡数量×100。\r\n②：自己的灵摆区域的卡被破坏的场合才能发动。从卡组选1只「无形噬体」灵摆怪兽在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'发动但不使用效果','发动并使用②效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47606319,'基冈提斯',replace(replace('这张卡不能通常召唤。从自己墓地把1只地属性怪兽除外的场合可以特殊召唤。\r\n①：这张卡被战斗破坏送去墓地的场合发动。场上的魔法·陷阱卡全部破坏。','\r',char(13)),'\n',char(10)),'魔法·陷阱卡全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47611119,'宝石骑士女郎·琉璃',replace(replace('「宝石骑士·小琉」＋「宝石骑士」怪兽\r\n这张卡用以上记的卡为融合素材的融合召唤才能从额外卡组特殊召唤。自己对「宝石骑士女郎·琉璃」1回合只能有1次特殊召唤。\r\n①：1回合1次，自己主要阶段才能发动。从卡组·额外卡组把1只「宝石骑士」怪兽送去墓地，给与对方为场上的特殊召唤的怪兽数量×500伤害。','\r',char(13)),'\n',char(10)),'基本分伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47658964,'红莲之炎壁','把自己墓地存在的名字带有「熔岩」的怪兽任意数量从游戏中除外发动。为这张卡发动而除外的怪兽数量的「熔岩衍生物」（炎族·炎·1星·攻/守0）在自己场上守备表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47658965,'熔岩衍生物','由「红莲之炎壁」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47660516,'升阶魔法-异晶人的魔力','选择自己场上1只超量怪兽才能发动。把和选择的怪兽相同种族而阶级高1阶的1只名字带有「混沌No.」或者「混沌超量」的怪兽在选择的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。那之后，对方场上有超量素材存在的场合，把对方场上1个超量素材在这个效果特殊召唤的超量怪兽下面重叠作为超量素材。','请选择要转移的素材','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47664723,'堕天使 埃地·阿拉耶',replace(replace('①：从墓地特殊召唤的这张卡得到以下效果。\r\n●这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47679935,'暴走魔法阵',replace(replace('「暴走魔法阵」在1回合只能发动1张。\r\n①：作为这张卡的发动时的效果处理，可以从卡组把1只「召唤师 阿莱斯特」加入手卡。\r\n②：只要这张卡在场地区域存在，包含把融合怪兽融合召唤效果的效果由自己发动的场合，那个发动不会被无效化，在那次融合召唤成功时对方不能把魔法·陷阱·怪兽的效果发动。','\r',char(13)),'\n',char(10)),'是否从卡组把「召唤师 阿莱斯特」加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47687766,'齿轮齿轮钻地人','①：这张卡召唤成功时才能发动。从卡组把「齿轮齿轮钻地人」以外的1只机械族·地属性·4星怪兽加入手卡。这个回合，自己不能攻击宣言，不是机械族怪兽不能特殊召唤。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47693640,'僵尸虎',replace(replace('1回合1次，在自己的主要阶段，这张卡可以视为1张装备卡装备在自己的「腐朽的武将」上，或者解除装备状态表侧攻击表示特殊召唤。当这张卡以自身效果变成装备卡时具有以下效果：\r\n●装备怪兽的攻击力·守备力上升500点。装备怪兽每次战斗破坏对方怪兽时，对方随机丢弃1张手卡。（1只怪兽至多只能装备1张同盟。当装备了同盟的怪兽被战斗破坏时，同盟会代替怪兽被破坏。）','\r',char(13)),'\n',char(10)),'装备','特殊召唤','手牌丢弃','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47695416,'命运之蜡烛','指尖的火焰消失时，将决定对方的命运。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47705572,'月光狼',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「月光」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n②：1回合1次，自己主要阶段才能发动。从自己的场上·墓地把「月光」融合怪兽卡决定的融合素材怪兽除外，把那1只融合怪兽从额外卡组融合召唤。\r\n【怪兽效果】\r\n①：只要这张卡在怪兽区域存在，自己的「月光」怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47728740,'彼岸的恶鬼 阿利基诺',replace(replace('「彼岸的恶鬼 阿利基诺」的①③的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上没有魔法·陷阱卡存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：自己场上有「彼岸」怪兽以外的怪兽存在的场合这张卡破坏。\r\n③：这张卡被送去墓地的场合，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的效果直到回合结束时无效。','\r',char(13)),'\n',char(10)),'特殊召唤','效果无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47731128,'结界术师 明孔','把这张卡解放发动。场上表侧表示存在的1张永续魔法或者永续陷阱卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47737087,'元素英雄 铁壁炮手',replace(replace('「元素英雄 爆热女郎」＋「元素英雄 黏土侠」\r\n这只怪兽不能作融合召唤以外的特殊召唤。这张卡表侧守备表示的场合，可以用守备表示的状态直接攻击对方玩家。那个场合，这张卡的攻击力在伤害计算时变成一半。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47741109,'森罗的隐蜜 金鱼草','手卡·场上的这张卡被送去墓地的场合，可以把自己卡组最上面的卡翻开。翻开的卡是植物族怪兽的场合，那只怪兽送去墓地。不是的场合，那张卡回到卡组最下面。此外，卡组的这张卡被卡的效果翻开送去墓地的场合，可以把自己卡组最上面的卡翻开。翻开的卡是植物族怪兽的场合，那只怪兽送去墓地。不是的场合，那张卡回到卡组最下面。','翻开卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47754278,'地狱龙',replace(replace('①：这张卡攻击的回合的结束阶段发动。这张卡破坏。\r\n②：场上的这张卡被破坏送去墓地时，把自己场上1只怪兽解放才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47766694,'荒野的大龙卷',replace(replace('①：以魔法与陷阱区域1张表侧表示的卡为对象才能发动。那张表侧表示的卡破坏。那之后，破坏的卡的控制者可以从手卡把1张魔法·陷阱卡盖放。\r\n②：盖放的这张卡被破坏送去墓地的场合，以场上1张表侧表示的卡为对象发动。那张表侧表示的卡破坏。','\r',char(13)),'\n',char(10)),'是否要放置魔法或陷阱卡？','表侧表示的1张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47778083,'骨木鱼','丢弃1张手卡。双方从对方墓地选择1只4星以下的怪兽在自己场上特殊召唤。这个效果特殊召唤的怪兽在下个回合的结束阶段时破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47795344,'自然绣球花','自己场上的名字带有「自然」的怪兽的效果发动的回合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47805931,'No.20 蚁岩土 辉蚁',replace(replace('3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。自己场上表侧表示存在的全部怪兽的攻击力上升300。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47819246,'超级量子机神剑-炎磁光层剑',replace(replace('①：以自己场上1只「超级量子」超量怪兽为对象才能把这张卡发动。这张卡当作装备卡使用给那只怪兽装备。\r\n②：这张卡装备的怪兽攻击力上升那只怪兽的阶级数值×100，向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n③：自己战斗阶段，把装备的这张卡送去墓地才能发动。这张卡装备过的怪兽在这个回合在同1次的战斗阶段中可以作3次攻击。','\r',char(13)),'\n',char(10)),'3次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47826112,'海皇龙 波塞德拉','把自己场上3只3星以下的水属性怪兽解放才能发动。这张卡从手卡或者墓地特殊召唤。这个效果特殊召唤成功时，场上的魔法·陷阱卡全部回到持有者手卡。这个效果让卡3张以上回到手卡的场合，对方场上的全部怪兽的攻击力下降回到手卡的卡数量×300的数值。','特殊召唤','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(47829960,'混沌骑手 古斯塔夫','从自己墓地里除外至多2张魔法卡。以此效果每除外1张卡，这张卡的攻击力就上升300点直到对方回合结束。此效果1回合只能使用1次。','攻击上升','','','','','','','','','','','','','','','');
