INSERT INTO texts VALUES(48086335,'古遗物-百中弓','这张卡可以当作魔法卡使用从手卡到魔法与陷阱卡区域盖放。魔法与陷阱卡区域盖放的这张卡在对方回合被破坏送去墓地时，这张卡特殊召唤。对方回合中这张卡特殊召唤成功的场合，可以从自己墓地选择1只名字带有「古遗物」的怪兽在自己的魔法与陷阱卡区域盖放。','特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48092532,'异次元的生还者','自己场上表侧表示存在的这张卡从游戏中除外的场合，这张卡在结束阶段时特殊召唤到场上。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48094997,'战斗橄榄球员','以高守备能力自豪的电子人。据说原本是作为橄榄球机器开发的。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48109103,'复仇的河童','被同伴杀害，为了复仇而将心灵卖给邪恶的河童。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48115277,'积木人','①：把这张卡解放才能发动。和这张卡在自己场上表侧表示存在的自己回合数相同数量的「积木衍生物」（岩石族·地·4星·攻1000/守1500）在自己场上守备表示特殊召唤。这衍生物不能攻击宣言。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48115278,'积木衍生物','由「积木人」的效果特殊召唤。这衍生物不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48130397,'超融合',replace(replace('不能对应这张卡的发动让魔法·陷阱·怪兽的效果发动。\r\n①：丢弃1张手卡才能发动。从自己·对方场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48135190,'砂石飓风','选择自己以及对方场上存在的魔法·陷阱卡各1张发动。选择的卡回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48144509,'异色眼融合',replace(replace('「异色眼融合」在1回合只能发动1张。\r\n①：从自己的手卡·场上把龙族的融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。对方场上有怪兽2只以上存在，自己场上没有怪兽存在的场合，自己的额外卡组的「异色眼」怪兽也能有最多2只作为融合素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48148828,'异次元的狂兽','被这张卡战斗破坏的怪兽从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48152161,'神属的堕天使',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：从手卡以及自己场上的表侧表示怪兽之中把1只「堕天使」怪兽送去墓地才能发动。选场上1只效果怪兽，那个效果直到回合结束时无效，自己基本分回复那只怪兽的攻击力的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48156348,'剑斗兽 凯撒',replace(replace('「剑斗兽 枪斗」＋「剑斗兽」怪兽\r\n让自己场上的上记卡回到卡组的场合才能从额外卡组特殊召唤（不需要「融合」）。\r\n①：这张卡特殊召唤成功时，以场上最多2张卡为对象才能发动。那些卡破坏。\r\n②：这张卡进行战斗的战斗阶段结束时让这张卡回到持有者的额外卡组才能发动。从卡组把「剑斗兽 枪斗」以外的2只「剑斗兽」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','请选择一张「剑斗兽 枪斗」','请选择一张「剑斗兽」怪兽','','','','','','','','','','','','');
INSERT INTO texts VALUES(48179391,'山铜结界',replace(replace('这个卡名的卡在决斗中只能发动1张。\r\n①：作为这张卡的发动时的效果处理，自己场上有特殊召唤的怪兽存在的场合，那些怪兽全部破坏。\r\n②：自己不能从额外卡组把怪兽特殊召唤。\r\n③：自己场上的怪兽的攻击力上升500。\r\n④：自己场上有表侧攻击表示怪兽2只以上存在的场合，对方不能选择自己场上的攻击力最低的怪兽作为攻击对象。\r\n⑤：这张卡1回合只有1次不会被效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48202661,'彼者','虽然样子不可靠，其实隐藏着惊人的潜在能力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48206762,'契约履行','支付800基本分。从自己墓地选择1只仪式怪兽在自己场上特殊召唤，并装备上这张卡。这张卡破坏时，装备怪兽从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48210156,'DD 夜吼怪','①：这张卡召唤成功时，以自己墓地1只「DD」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽的攻击力·守备力变成0，那只怪兽被破坏的场合自己受到1000伤害。这个效果的发动后，直到回合结束时自己不是恶魔族怪兽不能特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48216773,'强制退出装置','双方各自选自己场上1只怪兽，那些怪兽回到持有者卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48229808,'荷鲁斯之黑炎龙 LV8','这张卡不能通常召唤。只能通过「荷鲁斯之黑炎龙 LV6」的效果才能特殊召唤这张卡。只要这张卡在自己场上表侧表示存在，可以把魔法的发动和效果无效并且破坏。','魔法发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48252330,'魔偶甜点·面包干管家','这张卡被对方破坏送去墓地时，这张卡回到卡组。这张卡召唤成功时，场上有这张卡以外的名字带有「魔偶甜点」的怪兽存在的场合，可以从卡组把1张场地魔法卡加入手卡。','返回卡组','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48276469,'连锁炸药','给与发动陷阱卡的玩家基本分1000分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48305365,'巨斧袭击者','持斧的战士。单手挥舞斧头的攻击相当强劲。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48308134,'幻煌龙的螺旋突',replace(replace('通常怪兽才能装备。「幻煌龙的螺旋突」的②的效果1回合只能使用1次。\r\n①：装备怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n②：装备怪兽给与对方战斗伤害时才能发动。从自己的手卡·卡组·墓地选1只「幻煌龙 螺旋」特殊召唤，这张卡给那只怪兽装备。那之后，可以选对方场上1只攻击表示怪兽变成守备表示。','\r',char(13)),'\n',char(10)),'「幻煌龙 螺旋」特殊召唤','是否选对方怪兽变成守备表示？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48310593,'无念的手札','①：只要这张卡在魔法与陷阱区域存在，双方的手卡限制数量变成3张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48333324,'升阶魔法-源数之力','选择自己场上1只超量怪兽才能发动。和选择的怪兽相同种族而阶级高1阶的1只名字带有「混沌No.」的怪兽在选择的自己怪兽上面重叠当作超量召唤从额外卡组特殊召唤。那之后，这个效果特殊召唤的怪兽以外的场上表侧表示存在的卡的效果全部无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48343627,'墓场蠕动者','①：这张卡被战斗破坏送去墓地的场合，以场上1张卡为对象发动。那张卡破坏。','场上1张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48356796,'闪光之宝札','只要这张卡在场上存在，自己的魔法·陷阱卡区域1处变成不能使用。只要这张卡以外的「闪光之宝札」在场上表侧表示存在，自己的抽卡阶段时的通常抽卡可以抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48357738,'暴君的暴饮暴食','把自己场上存在的1只怪兽解放发动。只要这张卡在场上存在，双方不能把6星以上的怪兽特殊召唤。自己手卡是3张以上的场合，这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48365709,'暗杀者','能在黑暗中悄然无声地靠近敌人，精通暗杀的战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48370501,'西格马大日',replace(replace('光属性调整＋调整以外的光属性怪兽1只以上\r\n自己场上没有这张卡以外的怪兽存在的场合，这张卡的攻击宣言时才能发动。从自己卡组选择1张装备魔法卡给这张卡装备。','\r',char(13)),'\n',char(10)),'装备','请选择一张装备魔法卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48381268,'变形斗士·滑板',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：只要这张卡在场上表侧表示存在，自己场上表侧表示存在的名字带有「变形斗士」的怪兽可以直接攻击对方玩家。\r\n●守备表示：只要这张卡在场上表侧表示存在，自己场上表侧表示存在的这张卡以外的名字带有「变形斗士」的怪兽不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48411996,'朱罗纪南十字龙','这张卡被战斗破坏的场合，在自己场上把2只「朱罗纪衍生物」（恐龙族·炎·1星·攻0/守0）守备表示特殊召唤。这衍生物不能为名字带有「朱罗纪」的怪兽以外的上级召唤而解放。','特殊召唤Token','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48411997,'朱罗纪衍生物','由「朱罗纪南十字龙」的效果特殊召唤。这个衍生物不能为名字带有「朱罗纪」的怪兽以外的上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48422921,'猪突猛进','①：宣言1个属性，以自己场上1只表侧表示怪兽为对象才能发动。这个回合，那只自己的表侧表示怪兽和持有宣言的属性的对方怪兽进行战斗的场合，那次伤害步骤开始时那只对方怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48424886,'神影依·神子晶',replace(replace('「影依」怪兽＋炎属性怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。这个卡名的①的效果1回合只能使用1次。\r\n①：对方把怪兽特殊召唤之际才能发动。那次特殊召唤无效，那些怪兽破坏。那之后，自己把手卡1张「影依」卡送去墓地。\r\n②：这张卡被送去墓地的场合，以自己墓地1张「影依」魔法·陷阱卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'无效并破坏','卡片回收','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48427163,'月光紫蝶',replace(replace('「月光紫蝶」的②的效果1回合只能使用1次。\r\n①：把自己的手卡·场上的这张卡送去墓地，以自己场上1只「月光」怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升1000。\r\n②：把墓地的这张卡除外才能发动。从手卡把1只「月光」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48439321,'魔偶甜点圆舞','自己场上的名字带有「魔偶甜点」的怪兽进行战斗的伤害计算后，给与对方基本分300分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48444114,'月光香',replace(replace('①：以自己墓地1只「月光」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：把墓地的这张卡除外，丢弃1张手卡才能发动。从卡组把1只「月光」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48445393,'废铁阵雨','选择自己场上表侧表示存在的1只名字带有「废铁」的怪兽发动。从自己卡组把1只名字带有「废铁」的怪兽送去墓地，抽1张卡。那之后，选择的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48447192,'剑之辉煌','名字带有「X-剑士」的怪兽才能装备。装备怪兽战斗破坏对方怪兽的场合，可以把对方场上存在的1张卡破坏。此外，可以把自己场上存在的1只怪兽解放，自己墓地存在的这张卡回到卡组最上面。','对方场上存在的1张卡破坏','回到卡组最上面','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48453776,'天魔神 诺雷拉斯','这张卡不能通常召唤。把自己墓地的1只光属性·天使族怪兽和3只暗属性·恶魔族怪兽从游戏中除外的场合才能特殊召唤。可以支付1000基本分，把双方的手卡和场上的卡全部送去墓地，从自己卡组抽1张卡。','送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48461764,'紫毒之魔术师',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，自己的魔法师族·暗属性怪兽进行战斗的伤害计算前才能发动。那只怪兽的攻击力直到伤害步骤结束时上升1200。那之后，这张卡破坏。\r\n【怪兽效果】\r\n这张卡在规则上也当作「融合龙」卡使用。\r\n①：这张卡被战斗·效果破坏的场合，以场上1张表侧表示的卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48468330,'魔神童',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡从手卡·卡组送去墓地的场合才能发动。这张卡里侧守备表示特殊召唤。\r\n②：这张卡反转的场合才能发动。从卡组把1只恶魔族怪兽送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48497555,'废铁像',replace(replace('「废铁像」的①②的效果1回合各能使用1次。\r\n①：对方场上的已是表侧表示存在的魔法·陷阱卡把那个效果发动时才能发动。那张卡破坏。发动后这张卡不送去墓地，直接盖放。\r\n②：这张卡被送去墓地的场合，以自己墓地1只「废品」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48505422,'真六武众-竹刀','自己场上有「真六武众-瑞穂」表侧表示存在的场合，这张卡可以从手卡特殊召唤。场上存在的这张卡被解放的场合，选择自己墓地存在的「真六武众-竹刀」以外的1只名字带有「六武众」的怪兽加入手卡。','墓地回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48531733,'电击企鹅','用两腕的电击使对方麻痹，然后绞住颈项，进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48539234,'便乘','对方的抽卡阶段以外抽卡时才能发动。之后对方每次抽卡阶段以外抽卡1次，自己抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48546368,'崇光之宣告者',replace(replace('「宣告者的神托」降临。这张卡不用仪式召唤不能特殊召唤。\r\n①：可以从手卡把1只天使族怪兽送去墓地把以下效果发动。\r\n●对方把怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。\r\n●对方把怪兽特殊召唤之际才能发动。那次特殊召唤无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'无效并破坏','无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48568432,'三角火龙','1回合1次，自己的主要阶段时可以当作装备卡使用给自己场上的机械族怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。只在这个效果当作装备卡使用的场合，装备怪兽战斗破坏对方怪兽的场合，从自己墓地选择1只机械族·光属性·4星以下的怪兽特殊召唤。（1只怪兽可以装备的同盟最多1张。装备怪兽被破坏的场合，作为代替把这张卡破坏。）','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48576971,'与夺之首饰',replace(replace('自己场上这张卡的装备怪兽被战斗破坏，这张卡送去墓地时，选择下面1个效果发动。\r\n●从卡组抽1张卡。\r\n●对方随机丢弃1张手卡去墓地。','\r',char(13)),'\n',char(10)),'选择效果发动','从卡组抽1张卡','对方随机丢弃1张手卡去墓地','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48579379,'究极完全态大飞蛾','这张卡不能通常召唤。把有「进化之茧」装备的状态用自己回合计算经过6回合以上的自己场上1只「幼虫宝宝」解放的场合可以特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48582558,'保镖防御','选择自己场上1只名字带有「保镖」的怪兽才能发动。这个回合，选择的怪兽不会成为卡的效果的对象，不会被战斗破坏。这个回合，对方怪兽攻击的场合，必须把选择的怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48588176,'壁虱宝宝','这张卡被战斗破坏送去墓地时，可以从自己卡组把1只守备力1000以下的昆虫族怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48605591,'终端世界二代',replace(replace('自己场上没有其他卡存在，对方场上的怪兽是3只以下，对方场上的魔法·陷阱卡是3张以下的场合才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，双方场上能出的怪兽变成各自最多到3只，双方场上能出的魔法·陷阱卡变成各自最多到3张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48608796,'抒情歌鸲-聚集夜莺',replace(replace('1星怪兽×2只以上\r\n①：这张卡的攻击力上升这张卡的超量素材数量×200。\r\n②：这张卡可以直接攻击，持有超量素材的这张卡在同1次的战斗阶段可以作出最多有那个数量的攻击。\r\n③：1回合1次，把这张卡1个超量素材取除才能发动。直到回合结束时，自己场上的「抒情歌鸲」怪兽不会被战斗·效果破坏，自己受到的战斗伤害变成0。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'自己的「抒情歌鸲」怪兽不会被破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48642904,'催眠术','①：下次的对方回合，对方不能把怪兽的表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48649353,'牛鬼','通过黑魔术苏醒的牛之恶魔。从壶中现身。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48653261,'防守罚则','选择场上1只怪兽。这个回合选择的怪兽变成守备表示的场合，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48659020,'呼魂者','反转：从自己墓地里特殊召唤1只3星以下的通常怪兽上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48675364,'将军恶魔','把这张卡从手卡丢弃去墓地。从卡组把1张「万魔殿-恶魔的巢窟-」加入手卡。场上没有「万魔殿-恶魔的巢窟-」存在的场合，场上的这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48680970,'永远之魂',replace(replace('「永远之魂」的①的效果1回合只能使用1次。\r\n①：从以下选择1个才能把这个效果发动。\r\n●从自己的手卡·墓地选1只「黑魔术师」特殊召唤。\r\n●从卡组把1张「黑·魔·导」或者「千把刀」加入手卡。\r\n②：只要这张卡在魔法与陷阱区域存在，自己的怪兽区域的「黑魔术师」不受对方的效果影响。\r\n③：表侧表示的这张卡从场上离开的场合发动。自己场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'是否使用「永远之魂」的效果？','从自己的手卡·墓地选1只「黑魔术师」特殊召唤。','从卡组把1张「黑·魔·导」或者「千把刀」加入手卡。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48686504,'孤火花','①：1回合1次，把自己场上1只表侧表示的植物族怪兽解放才能发动。从卡组把1只植物族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48700891,'记忆破坏者','这张卡对对方玩家直接攻击造成伤害的场合，给与对方基本分对方额外卡组的卡数×100分数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48712195,'卡片商人','自己的准备阶段时可以让1张手卡回到卡组，从自己卡组抽1张卡。这个效果1回合只能使用1次。','手牌交换','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48716139,'草薙剑','灵魂怪兽才能装备。装备怪兽攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。装备怪兽从自己场上回到手卡让这张卡被送去墓地时，这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48716527,'帝王的熔击',replace(replace('自己的额外卡组没有卡存在，自己场上有上级召唤的怪兽存在的场合才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，上级召唤的怪兽以外的场上的表侧表示怪兽的效果无效化。\r\n②：自己结束阶段，上级召唤的怪兽不在自己场上存在的场合这张卡送去墓地。','\r',char(13)),'\n',char(10)),'送去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48737767,'妥善料理的队长','①：这张卡召唤成功时才能发动。让1张手卡回到卡组洗切。那之后，自己从卡组抽1张。那张抽到的卡是怪兽的场合，可以把那只怪兽特殊召唤。','手卡回到卡组','是否特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48739166,'No.101 寂静荣誉方舟骑士',replace(replace('4星怪兽×2\r\n「No.101 寂静荣誉方舟骑士」的①的效果1回合只能使用1次。\r\n①：把这张卡2个超量素材取除，以对方场上1只特殊召唤的表侧攻击表示怪兽为对象才能发动。那只怪兽在这张卡下面重叠作为超量素材。\r\n②：场上的这张卡被战斗·效果破坏的场合，可以作为代替把这张卡1个超量素材取除。','\r',char(13)),'\n',char(10)),'吸收素材','是否使用「No.101 寂静荣誉方舟骑士」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48742406,'妖精弓手','自己的主要阶段时，可以给与对方基本分自己场上表侧表示存在的光属性怪兽每1只400分伤害。这个效果发动的回合这张卡不能攻击。「妖精弓手」的效果1回合只能使用1次。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48766543,'电子科技翼龙','机械技术强化的翼龙。被龙打败而濒死之际让饲主给半机械化了。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48768179,'黑蝎-强力的高戈',replace(replace('这张卡对对方造成战斗伤害时，可以从下列效果中选择1项发动：\r\n●将对方场上1张怪兽卡弹回对方卡组最上面。\r\n●将对方卡组最上面1张卡送去墓地。','\r',char(13)),'\n',char(10)),'选择一个效果发动','将对方场上1张怪兽卡弹回对方牌组最上面。','将对方牌组最上面1张卡送去墓地。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48770333,'雷击坏兽 雷鸣龙王',replace(replace('①：这张卡可以把对方场上1只怪兽解放，从手卡往对方场上攻击表示特殊召唤。\r\n②：对方场上有「坏兽」怪兽存在的场合，这张卡可以从手卡攻击表示特殊召唤。\r\n③：「坏兽」怪兽在自己场上只能有1只表侧表示存在。\r\n④：1回合1次，把自己·对方场上3个坏兽指示物取除才能发动。这个回合，对方不能把魔法·陷阱·怪兽的效果发动，这张卡在同1次的战斗阶段中最多3次可以向怪兽攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48783998,'神召新星','①：这张卡被战斗破坏送去墓地时才能发动。从卡组把1只攻击力1500以下的天使族·光属性怪兽特殊召唤。场上有「天空的圣域」存在的场合，可以作为代替把1只「天空骑士 珀耳修斯」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48791583,'召唤兽 墨瓦腊泥加','「召唤师 阿莱斯特」＋地属性怪兽','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48800175,'龙觉醒旋律','①：丢弃1张手卡才能发动。把最多2只攻击力3000以上而守备力2500以下的龙族怪兽从卡组加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48868994,'变形斗士·吸尘器',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：1回合1次，可以把这张卡装备的1张装备卡送去墓地，给与对方基本分500分伤害。\r\n●守备表示：1回合1次，可以把对方场上表侧攻击表示存在的1只怪兽当作装备卡使用只有1只给这张卡装备。','\r',char(13)),'\n',char(10)),'给与对方500伤害','装备','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48891960,'龙骑兵团骑士-屠枪龙骑士',replace(replace('「龙骑兵团」调整＋调整以外的怪兽1只以上\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：把自己墓地1只「龙骑兵团」怪兽除外，以对方场上1只怪兽为对象才能发动。那只怪兽除外。\r\n②：同调召唤的这张卡被对方破坏的场合才能发动。从额外卡组把1只攻击力3000以下的「龙骑兵团」同调怪兽当作同调召唤作特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48905153,'十二兽 龙枪',replace(replace('4星怪兽×4\r\n「十二兽 龙枪」1回合1次也能在同名卡以外的自己场上的「十二兽」怪兽上面重叠来超量召唤。\r\n①：这张卡的攻击力·守备力上升这张卡作为超量素材中的「十二兽」怪兽的各自数值。\r\n②：1回合1次，把这张卡1个超量素材取除，以场上1张表侧表示的卡为对象才能发动。那张卡破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'是否在「十二兽」怪兽上面重叠来超量召唤？','卡片破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48928529,'No.83 银河女王',replace(replace('1星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除才能发动。直到下次的对方的结束阶段时，自己场上存在的怪兽不会被战斗破坏，向守备表示怪兽攻击的场合，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','\r',char(13)),'\n',char(10)),'附加能力','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48934760,'终焉之地','对方对怪兽的特殊召唤成功时才能发动。从自己卡组选择1张场地魔法卡发动。','请选择要发动的场地魔法卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48940337,'枪头喙嘴龙',replace(replace('←7 【灵摆】 7→\r\n①：自己的通常怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n【怪兽描述】\r\n太古灭绝之下幸存的梦幻翼龙。它的模样进化得更有攻击性，尖喙化成了贯穿一切的长枪。尽管如此主食好像还是吃鱼。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48948935,'假面魔兽 死亡护法师',replace(replace('这张卡不能通常召唤。把包含「假面咒术师 诅咒之喉」「梅尔基多四面兽」之内任意种的自己场上2只怪兽解放的场合可以特殊召唤。\r\n①：这张卡从场上送去墓地的场合，以对方场上1只表侧表示怪兽为对象发动。从卡组把1张「遗言之假面」当作装备卡使用给作为对象的怪兽装备。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48964966,'雅典娜',replace(replace('①：1回合1次，把「雅典娜」以外的自己场上1只表侧表示的天使族怪兽送去墓地，以「雅典娜」以外的自己墓地1只天使族怪兽为对象才能发动。那只天使族怪兽特殊召唤。\r\n②：这张卡已在怪兽区域存在的状态，这张卡以外的天使族怪兽召唤·反转召唤·特殊召唤的场合发动。给与对方600伤害。','\r',char(13)),'\n',char(10)),'特殊召唤','伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48976825,'来自异次元的埋葬','①：从除外的自己以及对方的怪兽之中以合计最多3只为对象才能发动。那些怪兽回到墓地。','请选择要回到墓地的卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48995978,'No.88 机关傀儡-命运狮子',replace(replace('8星怪兽×3\r\n1回合1次，自己的魔法与陷阱卡区域没有卡存在的场合才能发动。把这张卡1个超量素材取除，给这张卡放置1个命运指示物。这个效果发动的回合，自己不能进行战斗阶段。这张卡有3个命运指示物放置时，这张卡的控制者决斗胜利。','\r',char(13)),'\n',char(10)),'放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(48996569,'元素英雄 大地新宇侠',replace(replace('「元素英雄 新宇侠」＋「新空间侠·大地鼹鼠」\r\n把自己场上存在的上记的卡回到卡组的场合才能从额外卡组特殊召唤（不需要「融合」魔法卡）。可以使对方场上存在的1只怪兽回到持有者手卡。这个效果1回合只能使用1次。结束阶段时这张卡回到额外卡组。','\r',char(13)),'\n',char(10)),'返回卡组','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49003308,'魔蜥义豪','虽然曾经拥有邪恶的心灵，但是由于遇见某个人物而觉醒了正义之心的年轻恶魔。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49003716,'黑羽-黑枪之布拉斯特',replace(replace('①：自己场上有「黑羽-黑枪之布拉斯特」以外的「黑羽」怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
