INSERT INTO texts VALUES(49032236,'No.81 超重型炮塔列车 优越多拉炮',replace(replace('10星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以场上1只表侧表示怪兽为对象才能发动。那只怪兽直到回合结束时不受那只怪兽以外的卡的效果影响。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49033797,'禁断的偏方面体',replace(replace('「禁断的偏方面体」在1回合只能发动1张。\r\n①：自己场上的融合·同调·超量怪兽只有那之内2种类的场合，那个组合的以下效果适用。\r\n●融合·同调怪兽：从额外卡组把1只「外神」超量怪兽特殊召唤，把这张卡在下面重叠作为超量素材。\r\n●同调·超量怪兽：从额外卡组把1只「旧神」融合怪兽特殊召唤。\r\n●超量·融合怪兽：从额外卡组把1只「古神」同调怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
