INSERT INTO texts VALUES(49121795,'重装甲列车 铁狼',replace(replace('机械族4星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以自己场上1只机械族怪兽为对象才能发动。这个回合，自己不用那只怪兽不能攻击，那只怪兽可以直接攻击。\r\n②：这张卡被对方破坏送去墓地的场合才能发动。从卡组把1只机械族·4星怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
