INSERT INTO texts VALUES(49905576,'天空圣者 莫提乌斯','①：只要这张卡在怪兽区域存在，每次自己或者对方把反击陷阱卡发动，自己回复1000基本分，场上有「天空的圣域」存在的场合，再选对方场上1张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49919798,'音响战士 合成器',replace(replace('①：这张卡反转的场合才能发动。从卡组把「音响战士 合成器」以外的1只「音响战士」怪兽加入手卡。\r\n②：1回合1次，以「音响战士 合成器」以外的自己的场上·墓地1只「音响战士」怪兽为对象才能发动。这张卡直到结束阶段当作和那只怪兽同名卡使用，得到相同效果。\r\n③：把墓地的这张卡除外，以「音响战士 合成器」以外的除外的1只自己的「音响战士」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'复制效果结束','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49930315,'白棘魟',replace(replace('这个卡名的作为①的方法的特殊召唤1回合只能有1次，作为②的效果1回合只能使用1次。\r\n①：这张卡可以把手卡1只水属性怪兽丢弃，从手卡特殊召唤。\r\n②：这张卡从墓地的特殊召唤成功的场合才能发动。这个回合，这张卡当作调整使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49959355,'齐唱僵尸',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：以场上1只表侧表示怪兽为对象才能发动。丢弃1张手卡，作为对象的怪兽的等级上升1星。\r\n②：以场上1只表侧表示怪兽为对象才能发动。从卡组把1只不死族怪兽送去墓地，作为对象的怪兽的等级上升1星。这个效果的发动后，直到回合结束时不死族以外的自己怪兽不能攻击。','\r',char(13)),'\n',char(10)),'丢弃手卡','从卡组把怪兽送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49966595,'灰篮寄生体',replace(replace('「灰篮寄生体」的①②的效果1回合各能使用1次。\r\n①：对方怪兽的直接攻击宣言时才能把这个效果发动。自己场上没有怪兽存在的场合，从卡组把1只「灰篮」怪兽攻击表示特殊召唤。\r\n②：自己的「灰篮」怪兽的直接攻击宣言时，以对方墓地1只怪兽为对象才能发动。对方场上没有怪兽存在的场合，那只怪兽在对方场上特殊召唤。','\r',char(13)),'\n',char(10)),'从卡组把1只「灰篮」怪兽攻击表示特殊召唤','对方墓地1只怪兽在对方场上特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49980185,'超能力治疗','这张卡在结束阶段时才能发动。自己回复这个回合送去墓地的念动力族怪兽数量×1000的数值的基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(49998907,'平庸鬼的研究成果','自己卡组最上面3张卡确认，并且把那些卡按照自己的意愿交换顺序放回卡组最上面。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50005218,'闪刀空域-零区',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：以这张卡以外的自己场上1张卡为对象才能发动。从自己卡组上面把3张卡翻开。可以从那之中选1张「闪刀」卡加入手卡。剩下的卡回到卡组。「闪刀」卡被翻开的场合，再把作为对象的卡送去墓地。\r\n②：这张卡被效果从场地区域送去墓地的场合才能发动。从卡组把1只「闪刀姬」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','是否选卡加入手卡？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50005633,'复仇的潜行剑士','寄宿着被杀害的同伴的怨念的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50032342,'冰结界的军师','从手卡把1只名字带有「冰结界」的怪兽送去墓地发动。从自己卡组抽1张卡。这个效果1回合只能使用1次。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50045299,'龙族封印之壶','只要这张卡在场上存在，场上表侧表示存在的龙族怪兽全部变成守备表示，不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50065971,'超重忍者 忍足-A·C',replace(replace('机械族调整＋调整以外的机械族怪兽1只以上\r\n这张卡在规则上也当作「超重武者」卡使用。\r\n①：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。\r\n②：自己墓地没有魔法·陷阱卡存在的场合才能发动。这张卡的原本守备力直到回合结束时变成一半，这个回合这张卡可以直接攻击。\r\n③：这张卡被效果破坏送去墓地的场合，下次的准备阶段才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50074392,'灵水鸟 鲸鸟海妖','自己场上有鱼族以及鸟兽族怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤成功时，宣言从3到5的任意等级才能发动。自己场上的全部怪兽的等级变成宣言的等级。这个效果发动过的回合，水属性以外的自己怪兽不能把效果发动。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50074522,'电磁蚊','反转：场上表侧表示存在的机械族怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50078320,'刻印之调停者',replace(replace('①：对方把宣言1个卡名发动的效果发动时，把这张卡从手卡送去墓地才能发动。宣言1个卡名。对方宣言的卡名变成这个效果宣言的卡名。\r\n②：1回合1次，以场上1张表侧表示的卡为对象才能发动。下个回合的结束阶段把那张卡破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50078509,'魔族之链',replace(replace('以场上1只效果怪兽为对象才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，那只表侧表示怪兽不能攻击，效果无效化。那只怪兽破坏时这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50088247,'冰结界的传道师','自己场上有名字带有「冰结界」的怪兽表侧表示存在的场合，这张卡可以从手卡特殊召唤。这个效果特殊召唤的回合，自己不能把5星以上的怪兽特殊召唤。此外，可以把这张卡解放，选择「冰结界的传道师」以外的自己墓地存在的1只名字带有「冰结界」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50091196,'方程式同调士',replace(replace('调整＋调整以外的怪兽1只\r\n①：这张卡同调召唤成功时才能发动。自己从卡组抽1张。\r\n②：对方主要阶段才能发动。用包含这张卡的自己场上的怪兽为同调素材作同调召唤。','\r',char(13)),'\n',char(10)),'抽卡','同调召唤','','','','','','','','','','','','','','');
