INSERT INTO texts VALUES(50260683,'No.36 先史遗产-超机关 叉丘',replace(replace('名字带有「先史遗产」的4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上表侧表示存在的1只怪兽才能发动。选择的怪兽的攻击力直到回合结束时变成0。这个效果在对方回合也能发动。此外，把自己场上1只名字带有「先史遗产」的怪兽解放才能发动。选择对方场上1只持有和原本攻击力不同攻击力的怪兽破坏。','\r',char(13)),'\n',char(10)),'攻击变成0','怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50263751,'贪婪类星体',replace(replace('①：这张卡的原本的攻击力·守备力变成这张卡的等级×300。\r\n②：只要这张卡在怪兽区域存在，这张卡的等级上升这张卡战斗破坏的怪兽的原本等级数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50277973,'镜像沼泽人','①：宣言种族和属性各1个才能发动。这张卡发动后变成持有宣言的种族·属性的通常怪兽（4星·攻1800/守1000）在怪兽区域特殊召唤。这张卡也当作陷阱卡使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50278554,'废铁双生龙',replace(replace('名字带有「废铁」的调整＋调整以外的怪兽1只以上\r\n1回合1次，选择自己场上存在的1张卡和对方场上存在的2张卡才能发动。选择的自己的卡破坏，选择的对方的卡回到手卡。这张卡被对方破坏送去墓地时，选择同调怪兽以外的自己墓地存在的1只名字带有「废铁」的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50282757,'邪心英雄 地狱狙魔',replace(replace('「元素英雄 黏土侠」＋「元素英雄 爆热女郎」\r\n这只怪兽不能作「暗黑融合」的融合召唤以外的特殊召唤。这张卡在场上表侧守备表示存在的场合，每次自己的准备阶段给与对方基本分1000分伤害。这张卡不会被魔法的效果破坏。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50287060,'暗黑魔族 基尔法恶魔','①：这张卡被送去墓地时，以场上1只表侧表示怪兽为对象才能发动。墓地的这张卡当作攻击力下降500的装备卡使用给那只怪兽装备。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50292967,'过去的影像','自己场上有念动力族怪兽表侧表示存在的场合，选择对方场上存在的1只怪兽才能发动。选择的怪兽从游戏中除外。这个效果除外的怪兽在下次的准备阶段时以相同表示形式回到对方场上。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50304345,'邪心英雄 地狱小魔','己方场上没有怪兽存在的场合，这张卡可以从手卡表侧攻击表示特殊召唤。用这张卡作为祭品把名字带有「英雄」的卡祭品召唤的场合，那个回合的结束阶段时从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50319138,'漏洞人Z','这张卡召唤成功时，自己场上有「漏洞人X」表侧表示存在的场合，可以从自己卡组把1只「漏洞人Y」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50321796,'冰结界之龙 光枪龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n「冰结界之龙 光枪龙」的效果1回合只能使用1次。\r\n①：把手卡任意数量丢弃去墓地，以丢弃数量的对方场上的卡为对象才能发动。那些卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50323155,'升天之黑角笛','1只对方怪兽的特殊召唤无效，那只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50366775,'格式弹涂鱼',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。把额外卡组1只连接怪兽给对方观看。这个回合连接召唤的场合，这张卡也能当作和给人观看的怪兽相同的卡名·种族·属性的素材使用。\r\n②：这张卡作为连接素材送去墓地的场合才能发动。从卡组把1只5星以上的电子界族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50371210,'光之引导',replace(replace('①：自己场上没有其他的「光之引导」存在，自己墓地有「青眼」怪兽3只以上存在的场合，以那之内的1只为对象才能把这张卡发动。那只怪兽效果无效特殊召唤，把这张卡装备。这张卡从场上离开时那只怪兽除外。\r\n②：装备怪兽以外的自己怪兽不能攻击，自己墓地有「青眼」怪兽存在的场合，装备怪兽在同1次的战斗阶段中可以作出最多有那个数量的攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50383626,'黯黑之魔王 迪亚波罗斯',replace(replace('这个卡名的①③的效果1回合各能使用1次。\r\n①：这张卡在手卡·墓地存在，自己场上的暗属性怪兽被解放的场合才能发动。这张卡特殊召唤。\r\n②：只要这张卡在怪兽区域存在，对方不能把这张卡解放，也不能作为效果的对象。\r\n③：把自己场上1只暗属性怪兽解放才能发动。对方选1张手卡回到卡组最上面或者最下面。','\r',char(13)),'\n',char(10)),'','','卡组最上面','卡组最下面','','','','','','','','','','','','');
INSERT INTO texts VALUES(50400231,'卫星加农炮','这张卡不会被7星以下的怪兽战斗破坏。每次的自己的结束阶段，这张卡的攻击力上升1000。这张卡进行攻击的场合，用这个效果上升的攻击力在伤害计算后回到0。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50407691,'点火骑士·镇暴',replace(replace('←7 【灵摆】 7→\r\n①：另一边的自己的灵摆区域有「点火骑士」卡存在的场合才能发动。自己的灵摆区域的卡全部破坏，从自己的卡组·墓地选1只战士族·炎属性怪兽加入手卡。\r\n【怪兽描述】\r\n点火骑士的上级战士。二刀流剑枪擅长不分场合大范围横扫，不只是敌方害怕就连己方也一样害怕。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50412166,'沙布提俑护符','从手卡丢弃这张卡。到本回合结束阶段为止，自己场上名称中带有「守墓」的怪兽卡所受战斗伤害为0。','名称中带有「守墓」的怪兽卡所受战斗伤害为0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50418970,'灵魂火阵','1回合1次，可以把装备怪兽变成守备表示。装备怪兽从场上回到手卡让这张卡被送去墓地时，给与对方基本分600分伤害。这张卡在墓地存在的场合，可以作为自己的抽卡阶段时进行通常抽卡的代替，把这张卡加入手卡。','变成守备表示','伤害','加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50426119,'退行手雷破坏者',replace(replace('这张卡不能通常召唤。把自己墓地2只电子界族怪兽除外的场合可以特殊召唤。这个卡名的效果1回合只能使用1次。\r\n①：以持有比这张卡的攻击力高的攻击力的对方场上1只怪兽为对象才能发动。那只怪兽直到结束阶段除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50427388,'暴走的魔力','自己墓地的魔法卡全部从游戏中除外才能发动。持有除外的魔法卡数量×300的数值以下的守备力的对方场上表侧表示存在的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50433147,'极星的光辉','场上名字带有「极星」的怪兽不会被战斗破坏。场上的这张卡被破坏时，场上表侧表示存在的名字带有「极星」的怪兽全部破坏。','表侧表示的名字带有「极星」的怪兽全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50449881,'鲨鱼要塞',replace(replace('5星怪兽×2\r\n①：只要这张卡在怪兽区域存在，对方不能选择其他怪兽作为攻击对象。\r\n②：1回合1次，把这张卡1个超量素材取除，以自己场上1只表侧表示怪兽为对象才能发动。这个回合，那只怪兽在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'多次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50457953,'幻木龙','1回合1次，选择自己场上1只龙族·水属性怪兽才能发动。这张卡的等级变成和选择的怪兽的等级相同。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50470982,'命运的分岔道','双方玩家各自进行1次投掷硬币。出现表侧的场合回复2000基本分，出现里侧的场合基本分受到2000分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50474354,'武神器-八尺琼','自己的主要阶段2，把这张卡从手卡送去墓地才能发动。从卡组把1只名字带有「武神」的怪兽加入手卡。这个效果发动的回合，自己不能把名字带有「武神」的卡以外的魔法·陷阱·效果怪兽的效果发动。「武神器-八尺琼」的效果1回合只能使用1次。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50485594,'救援鼠',replace(replace('←5 【灵摆】 5→\r\n「救援鼠」的灵摆效果在决斗中只能使用1次。\r\n①：把自己的灵摆区域的这张卡除外才能发动。从自己的额外卡组把2只表侧表示的同名灵摆怪兽加入手卡。\r\n【怪兽效果】\r\n①：这张卡召唤成功的回合，自己的额外卡组有表侧表示的5星以下的灵摆怪兽存在的场合，把这张卡解放才能发动。选自己的额外卡组1只表侧表示的5星以下的灵摆怪兽，从卡组把那2只同名怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，结束阶段破坏。','\r',char(13)),'\n',char(10)),'卡组检索','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50491121,'英豪挑战者 斯巴达兵','1回合1次，对方怪兽的攻击宣言时选择这张卡以外的自己场上1只名字带有「英豪」的怪兽才能发动。这张卡的攻击力直到战斗阶段结束时上升选择的怪兽的原本攻击力数值。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50501121,'背德的堕天使',replace(replace('「背德的堕天使」在1回合只能发动1张。\r\n①：从手卡以及自己场上的表侧表示怪兽之中把1只「堕天使」怪兽送去墓地才能发动。选场上1张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50527144,'鬼计匿影','把手卡1只名字带有「鬼计」的怪兽给对方观看才能发动。这个回合，自己场上的名字带有「鬼计」的卡以及里侧守备表示存在的怪兽不会成为卡的效果的对象，不会被卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50532786,'鲨口人','这张卡不能特殊召唤。这张卡上级召唤的场合，解放的怪兽必须是水属性怪兽。这张卡的攻击力上升这张卡以外的自己场上表侧表示存在的水属性怪兽每1只300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50548657,'提示符叉角羚',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：把自己场上1只4星以下的电子界族怪兽解放才能发动。等级合计直到变成和解放的怪兽的等级相同为止，从自己的卡组·墓地选电子界族通常怪兽任意数量特殊召唤。这个效果特殊召唤的怪兽在结束阶段除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50554729,'无形噬体感染',replace(replace('「无形噬体感染」的②的效果1回合只能使用1次。\r\n①：场上的「无形噬体」怪兽的攻击力·守备力上升场上的「无形噬体」卡数量×100。\r\n②：自己的手卡·场上的怪兽被解放的场合或者被战斗·效果破坏的场合才能发动。从卡组把1张「无形噬体」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50584941,'红莲霸权','①：把自己墓地1只「红莲魔」同调怪兽除外，以自己场上1只「红莲魔」同调怪兽为对象才能发动。那只怪兽当作和为这张卡发动而除外的「红莲魔」同调怪兽同名卡使用，变成相同效果。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50588353,'水晶机巧-继承玻纤',replace(replace('包含调整1只以上的怪兽2只\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡连接召唤成功的场合才能发动。从手卡·卡组把1只3星以下的调整守备表示特殊召唤。这个效果特殊召唤的怪兽在这个回合不能把效果发动。\r\n②：对方的主要阶段以及战斗阶段把场上的这张卡除外才能发动。从额外卡组把1只同调怪兽调整当作同调召唤作特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50593156,'沙之赌徒','进行3次投掷硬币。3次都是表的场合，对方场上怪兽全部破坏。3次都是里的场合，自己场上怪兽全部破坏。这个效果1回合只有1次在自己的主要阶段才能使用。','投掷硬币','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50604950,'X-剑士 加拉哈德','这张卡向对方怪兽攻击的场合，伤害步骤内攻击力上升300。这张卡被对方怪兽攻击的场合，伤害步骤内攻击力下降500。这张卡被选择作为攻击对象时，可以把自己场上存在的这张卡以外的1只名字带有「剑士」的怪兽解放，那次攻击无效。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50608164,'假面英雄 光牙',replace(replace('这张卡用「假面变化」的效果才能特殊召唤。\r\n①：这张卡的攻击力上升对方场上的怪兽数量×500。\r\n②：1回合1次，把自己墓地1只「英雄」怪兽除外，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时下降因为这个效果发动而除外的怪兽的攻击力数值。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50613779,'伤害法师','卡的效果让自己受到伤害时才能发动。这张卡从手卡特殊召唤，自己基本分回复受到的伤害的数值。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50615578,'机巧忍者 七七四九','这张卡可以攻击的场合必须作出攻击。场上表侧表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变更。这张卡召唤成功时，可以从自己卡组抽出自己场上表侧守备表示存在的名字带有「机巧」的怪兽数量的卡。','表示形式变更','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50619462,'乌合无象',replace(replace('「乌合无象」在1回合只能发动1张。\r\n①：从自己场上把1只原本种族是兽族·兽战士族·鸟兽族的表侧表示怪兽送去墓地才能发动。原本种族和送去墓地的那只怪兽相同的1只怪兽从额外卡组特殊召唤。这个效果特殊召唤的怪兽不能攻击，效果无效化，结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50621530,'强化调整龙','这张卡的攻击力上升场上表侧表示存在的调整数量×500的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50684552,'神奇车库','盖伏的这张卡被破坏送去墓地时，可以从手卡特殊召唤1只4星以下的名字带有「机人」的机械族怪兽。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50692511,'急袭猛禽-呼唤',replace(replace('「急袭猛禽-呼唤」在1回合只能发动1张，这张卡发动的回合，自己不是「急袭猛禽」怪兽不能特殊召唤。\r\n①：以自己场上1只「急袭猛禽」怪兽为对象才能发动。那1只同名怪兽从手卡·卡组守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50696588,'粗人舞导',replace(replace('（注：暂时无法正常使用）\r\n\r\n这个卡名的效果在决斗中只能适用1次。\r\n①：指定没有使用的对方的主要怪兽区域1处才能发动。只要指定的区域是可以使用，对方要在主要怪兽区域把怪兽通常召唤·特殊召唤的场合，不是那个区域不能使用。这个效果直到指定的区域有怪兽被放置为止适用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50702124,'同调磁铁头','这张卡不能通常召唤。自己对同调怪兽的同调召唤成功时，这张卡可以从手卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50705071,'金属恶魔 佐亚','这张卡不能通常召唤。把自己场上1只「金属化·魔法反射装甲」所装备给的「恶魔 佐亚」解放的场合可以从卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50712728,'守墓的咒术师','这张卡召唤·反转召唤·特殊召唤时，给与对方玩家基本分500分的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50720316,'元素英雄 影雾女郎',replace(replace('「元素英雄 影雾女郎」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡特殊召唤成功的场合才能发动。从卡组把1张「变化」速攻魔法卡加入手卡。\r\n②：这张卡被送去墓地的场合才能发动。从卡组把「元素英雄 影雾女郎」以外的1只「英雄」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'从卡组把1张「变化」速攻魔法卡加入手卡','从卡组把1只「英雄」怪兽加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50725996,'黑魔术骑士',replace(replace('这张卡不能通常召唤。「骑士的称号」的效果才能特殊召唤。\r\n①：这张卡特殊召唤成功的场合，以场上1张卡为对象发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50732780,'地狱治安警察','①：这张卡被战斗破坏送去墓地的场合发动。从卡组把1只恶魔族·1星怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50756327,'魔导原典 克劳利',replace(replace('魔法师族怪兽2只\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功的场合才能发动。从卡组把「魔导书」卡3种类给对方观看，对方从那之中随机选1张。那1张卡加入自己手卡，剩下的卡回到卡组。\r\n②：只要这张卡在怪兽区域存在，自己在5星以上的魔法师族怪兽召唤的场合需要的解放可以不用。这个效果1回合只能适用1次。','\r',char(13)),'\n',char(10)),'卡组检索','不用解放召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50766506,'忍法 分身之术','①：把自己场上1只「忍者」怪兽解放才能把这张卡发动。等级合计最多到解放的怪兽的等级以下为止，从卡组把「忍者」怪兽任意数量表侧攻击表示或者里侧守备表示特殊召唤。这张卡从场上离开时那些怪兽全部破坏。','是否要继续特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50781944,'古代深红猿','自己场上存在的怪兽被破坏送去墓地时，自己回复1000基本分。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50785356,'啸叫战士','这张卡召唤·特殊召唤成功时，选择自己场上表侧表示存在的1只怪兽才能发动。选择的怪兽的等级变成3星。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50789693,'铠甲河童',replace(replace('2星怪兽×2\r\n「铠甲河童」的②的效果在决斗中只能使用1次。\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。这张卡的攻击力或者守备力上升1000。\r\n②：自己怪兽进行战斗的战斗步骤，丢弃1张手卡才能发动。这个回合，自己场上的怪兽不会被战斗破坏，自己受到的战斗伤害全部变成0。','\r',char(13)),'\n',char(10)),'攻守上升','破坏耐性','攻击力上升1000','守备力上升1000','','','','','','','','','','','','');
INSERT INTO texts VALUES(50823978,'军队食人鱼','这张卡对对方进行直接攻击时战斗伤害加倍。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50866755,'凤凰','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。这张卡召唤·反转时，对方场上盖放的魔法·陷阱卡全部破坏。','回到手牌','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50893987,'剑斗兽 老虎','这张卡不能作为融合素材怪兽使用。这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功时，可以从手卡把1张名字带有「剑斗兽」的卡丢弃，从自己卡组把1只名字带有「剑斗兽」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50896944,'暗黑腕龙','①：这张卡召唤成功时，以场上1只怪兽为对象才能发动。那只怪兽变成表侧守备表示。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50903514,'苍炎之剑士',replace(replace('①：1回合1次，自己或者对方的战斗阶段以这张卡以外的自己场上1只战士族怪兽为对象才能发动。这张卡的攻击力下降600，作为对象的怪兽的攻击力上升600。\r\n②：场上的这张卡被对方破坏送去墓地时，把墓地的这张卡除外，以自己墓地1只战士族·炎属性怪兽为对象才能发动。那只战士族·炎属性怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'攻守变化','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50913601,'山','全部龙族·鸟兽族·雷族的怪兽攻击力·守备力上升200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50916353,'地狱战士','这张卡因对方怪兽的攻击破坏送去墓地时，也给与对方基本分这次战斗让自己受到的战斗伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50920465,'雪暴雷鸟','丢弃1张手卡才能发动。「雪暴雷鸟」以外的鸟兽族·水属性怪兽从自己的手卡·墓地各选1只特殊召唤。那之后，场上的这张卡回到持有者手卡。「雪暴雷鸟」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50930991,'魔法剑士 尼奥','武术和剑术都出类拔萃的魔法师，在异世界旅行。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50933533,'古代的机械巨龙',replace(replace('①：得到为这张卡的召唤而解放的怪兽的以下效果。\r\n●绿色零件：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n●红色零件：这张卡给与对方战斗伤害的场合发动。给与对方400伤害。\r\n●黄色零件：这张卡战斗破坏对方怪兽的场合发动。给与对方600伤害。\r\n②：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。','\r',char(13)),'\n',char(10)),'给予对方400伤害','给予对方600伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50939127,'异次元龙','这张卡不会被不指定对象的魔法、陷阱卡的效果破坏。这张卡不会被攻击力1900以下的怪兽战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50951359,'调整抓取','对方对同调怪兽的同调召唤成功时才能发动。那1只作为同调素材的调整怪从对方墓地在自己场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50954680,'水晶翼同调龙',replace(replace('调整＋调整以外的同调怪兽1只以上\r\n①：1回合1次，这张卡以外的怪兽的效果发动时才能发动。那个发动无效并破坏。这个效果破坏怪兽的场合，这张卡的攻击力直到回合结束时上升这个效果破坏的怪兽的原本攻击力数值。\r\n②：这张卡和5星以上的对方怪兽进行战斗的伤害计算时发动。这张卡的攻击力只在那次伤害计算时上升进行战斗的对方怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50957346,'狂怒大地','这张卡被战斗破坏送去墓地时，场上表侧表示存在的地属性怪兽以外的怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51011872,'淘气仙星·布拉蒂玛丽',replace(replace('「淘气仙星」怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，每次这张卡所连接区有「淘气仙星」怪兽召唤·特殊召唤，自己回复200基本分。\r\n②：从手卡丢弃1张「淘气仙星」卡才能发动。双方玩家各自从卡组抽1张。这个效果的发动时自己基本分比对方多2000以上的场合，这个效果让自己抽出的数量变成2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51020079,'电子人博士','把自己场上存在的1只调整解放发动。和解放怪兽相同属性·等级的1只怪兽从自己墓地特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51028231,'魔界剧团-莽撞新人',replace(replace('←2 【灵摆】 2→\r\n①：自己场上的「魔界剧团」怪兽被战斗或者对方的效果破坏的场合，可以作为代替把这张卡破坏。\r\n【怪兽效果】\r\n①：这张卡1回合只有1次不会被战斗·效果破坏。\r\n②：怪兽区域的这张卡被战斗或者对方的效果破坏的场合才能发动。从卡组把「魔界剧团-莽撞新人」以外的1只4星以下的「魔界剧团」怪兽特殊召唤。\r\n③：这张卡在灵摆区域被破坏的场合，以对方场上1只4星以下的怪兽为对象才能发动。那只怪兽破坏。','\r',char(13)),'\n',char(10)),'是否使用「魔界剧团-莽撞新人」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51043053,'防霸龙 日球层龙','对方手卡是4张以下而自己场上的怪兽只有这张卡的场合，对方不能攻击宣言。此外，1回合1次，自己场上有龙族·8星怪兽存在的场合才能发动。这张卡的等级直到结束阶段时变成8星。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51043243,'卡尔塔罗斯异虫','反转：从自己卡组把1只4星以下的名字带有「异虫」的爬虫族怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51047350,'卡片破坏者','这张卡不能通常召唤。把自己的魔法与陷阱卡区域存在的1张卡送去墓地的场合可以表侧攻击表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51053997,'PSY骨架加速器',replace(replace('①：1回合1次，支付500基本分，以自己场上1只「PSY骨架」怪兽为对象才能发动。那只怪兽直到下次的自己准备阶段除外。\r\n②：1回合1次，这张卡以外的自己场上的表侧表示的「PSY骨架」卡因战斗以外从场上离开的场合才能发动。从手卡把1只「PSY骨架」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'发动但不使用②效果','发动并使用②效果','选择1只怪兽除外','手卡怪兽特殊召唤','除外的怪兽回到场上','','','','','','','','','','','');
INSERT INTO texts VALUES(51085303,'元素英雄 鲜花女郎','这张卡不能通常召唤。这张卡用「花瓣」的效果才能特殊召唤。对方只能把「元素英雄 鲜花女郎」选择作为攻击对象。每次给与对方玩家战斗伤害，这张卡的攻击力上升200，守备力下降200。','攻守变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51091138,'导爆线','①：以和这张卡相同纵列的1张卡为对象才能把盖放的这张卡发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51099515,'恐击','①：把自己墓地2只怪兽除外，以场上1只表侧攻击表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51119924,'傀儡植物','把这张卡从手卡丢弃去墓地。直到这个回合的结束阶段时，得到对方场上表侧表示存在的1只战士族或者魔法师族怪兽的控制权。','获取控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51124303,'影灵衣的万华镜',replace(replace('「影灵衣」仪式怪兽的降临必需。「影灵衣的万华镜」的①的效果1回合只能使用1次。\r\n①：直到和仪式召唤的怪兽相同等级为止，把自己的手卡·场上1只怪兽解放或者把额外卡组1只怪兽送去墓地，从手卡把「影灵衣」仪式怪兽任意数量仪式召唤。\r\n②：自己场上没有怪兽存在的场合，从自己墓地把这张卡和1只「影灵衣」怪兽除外才能发动。从卡组把1张「影灵衣」魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'是否作为仪式召唤需要的等级数值的解放使用？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51126152,'深夜急行骑士',replace(replace('这张卡不能从卡组特殊召唤。\r\n①：这张卡可以不用解放作召唤。\r\n②：这张卡的①的方法召唤的这张卡的原本攻击力变成0。','\r',char(13)),'\n',char(10)),'不解放怪兽召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51192573,'宇宙兽 冈吉尔','把自己场上存在的原本持有者是对方的怪兽作为祭品的场合，这张卡可以用1只祭品作召唤。1回合1次，可以在对方场上1只怪兽放置1个A指示物。放置有A指示物的怪兽和名字带有「外星」的怪兽战斗的场合，每有1个A指示物攻击力·守备力下降300。','用1只祭品作召唤','放置「A指示物」','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51194046,'机壳汇编器 不安定',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「机壳」怪兽不能特殊召唤。这个效果不会被无效化。\r\n②：自己上级召唤成功的回合的结束阶段才能发动。自己从卡组抽出这个回合自己为上级召唤而解放的「机壳」怪兽的数量。\r\n【怪兽描述】\r\nqliphoth.exe 中的 0x1i-666 确认到未处理的异常。\r\n写入位置 0x00-000 时发生访问冲突。\r\n您想忽略此错误并尝试继续吗? <Y/N>...[ ]\r\n===CARNAGE===\r\n恶gn善iod道ru知能oy似no相yr们gn我a与s经i已do人G那\r\n着doo活lfe远永rif就g吃n子i果r的b树o命t生t摘又n手a伸w他怕d恐n在a现','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51196174,'计算器人','①：这张卡的攻击力变成自己场上的怪兽的等级合计×300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51196805,'鬼计骷髅','自己场上有名字带有「鬼计」的怪兽存在的场合才能让这张卡表侧表示召唤。这张卡1回合只有1次可以变成里侧守备表示。此外，这张卡反转时，把最多有自己场上的名字带有「鬼计」的怪兽数量的卡从对方卡组上面里侧表示除外。「鬼计骷髅」的这个效果1回合只能使用1次。','变成里侧守备','里侧除外','请选择要除外的卡的数量','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51205763,'机怪虫·神经胶质虫',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡反转的场合才能发动。从自己的手卡·墓地选「机怪虫·神经胶质虫」以外的1只「机怪虫」怪兽表侧攻击表示或者里侧守备表示特殊召唤。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合才能发动。「机怪虫·神经胶质虫」以外的2只「机怪虫」怪兽从卡组里侧守备表示特殊召唤（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'特殊召唤','2只怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51227866,'闪刀机-虎鲨加农炮','①：自己的主要怪兽区域没有怪兽存在的场合，以对方墓地1只怪兽为对象才能发动。那只怪兽除外。自己墓地有魔法卡3张以上存在的场合，可以不除外而把那只怪兽在自己场上特殊召唤。这个效果特殊召唤的怪兽不能攻击。','是否特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51228280,'迷宫的蠕虫','潜在迷路者的地下，捕食经过此处迷路的生物。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51232472,'暗黑界的策士 格林','这张卡被其他卡的效果从手卡丢弃去墓地的场合，把场上1张魔法或者陷阱卡破坏。','把场上1张魔法或者陷阱卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51254277,'大口鲨','这张卡攻击的场合，战斗阶段结束时从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51254980,'自然螳螂','对方对怪兽的召唤成功时，可以从手卡把1只名字带有「自然」的怪兽送去墓地，那只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51267887,'体温上升','恐龙族怪兽可以装备。装备怪兽的攻击力·守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51275027,'不幸的美少女','这张卡被战斗破坏送去墓地，当时那个回合的战斗阶段结束。','结束战斗阶段','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51282878,'电子鱼人-栽培者','只在这张卡在墓地存在才能发动1次。自己卡组最上面的卡送去墓地。送去墓地的卡是水属性怪兽的场合，再把这张卡从墓地特殊召唤。「电子鱼人-栽培者」的效果1回合只能使用1次。','卡组送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51316684,'狱火机·巴力',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把2只「狱火机」怪兽除外的场合才能从手卡·墓地特殊召唤。\r\n①：这张卡向对方怪兽攻击的战斗阶段结束时才能发动。选场上1张卡除外。\r\n②：1回合1次，把自己场上1只怪兽解放，以对方墓地1张卡为对象才能发动。那张卡除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'场上的卡除外','对方墓地的卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51324455,'魔制产卵床','自己场上表侧表示存在的鱼族·海龙族·水族怪兽从游戏中除外时才能发动。从自己卡组把1只4星以下的鱼族·海龙族·水族怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51335426,'出入连接',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己的场上·墓地1只连接怪兽为对象才能发动。那只怪兽回到持有者的额外卡组，自己从卡组抽出那个连接标记的数量。那之后，选抽出数量的手卡用喜欢的顺序回到卡组下面。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51345461,'狩剑猎人','被这张卡战斗破坏的怪兽，在战斗阶段结束的时候作为攻击力＋200的装备卡来使用装备在这张卡上。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51351302,'有法眼的男人','这张卡通常召唤时和每到自己准备阶段时，选择对方场上1张盖伏着的卡，确认后回复原状。','确认','请选择一张要确认的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51355346,'爆炎集合体 地球之魂','1回合1次，可以把自己场上最多2只炎族怪兽做祭品。因这个效果使用祭品的场合，这张卡的攻击力上升祭品怪兽数量×1000的数值。这张卡攻击守备表示的怪兽时，这张卡的攻击力超过守备表示怪兽守备力的数值，给与对方基本分那个数值的战斗伤害。结束阶段时后这张卡破坏。','攻击上升','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51365514,'超力之圣刻印','从手卡把1只名字带有「圣刻」的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51371017,'剑之女王','反转：对方场上每存在1张魔法·陷阱，对方受到500分的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51391183,'魔界剧团-狂放新秀',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，以另一边的自己的灵摆区域1张「魔界剧团」卡为对象才能发动。那张卡的灵摆刻度直到回合结束时变成9。这个效果的发动后，直到回合结束时自己不是「魔界剧团」怪兽不能特殊召唤。\r\n【怪兽效果】\r\n「魔界剧团-狂放新秀」的②的怪兽效果1回合只能使用1次。\r\n①：1回合1次，自己主要阶段才能发动。这张卡的攻击力直到回合结束时上升自己场上的「魔界剧团」怪兽种类×100。\r\n②：这张卡被战斗·效果破坏的场合才能发动。从卡组把「魔界剧团-狂放新秀」以外的1张「魔界剧团」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51394546,'公墓炸弹','对方受到对方墓地卡数×100的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51402177,'斯芬克斯·迪蕾雅','场上有「光之金字塔」存在的场合，可以支付500基本分把这张卡从手卡特殊召唤。这张卡在召唤·特殊召唤的回合不能攻击。这张卡不能作从墓地的特殊召唤。这张卡战斗破坏守备表示怪兽的场合，给与对方基本分破坏的怪兽的守备力一半数值的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51402908,'至尊太阳','这张卡不用这张卡的效果不能特殊召唤。场上表侧表示存在的这张卡被破坏送去墓地的场合，下个回合的准备阶段时，可以丢弃1张手卡，这张卡从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51405049,'汪分之一机会！？','①：自己场上有1星怪兽存在的场合才能发动。从卡组把1只1星怪兽加入手卡。发动后，这个回合中自己对这个效果加入手卡的怪兽或者那些同名卡的召唤没有成功的场合，结束阶段让自己受到2000伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51435705,'先史遗产 水晶头骨','自己场上有名字带有「先史遗产」的怪兽存在的场合，自己的主要阶段时把这张卡从手卡丢弃才能发动。从自己的卡组·墓地选「先史遗产 水晶头骨」以外的1只名字带有「先史遗产」的怪兽加入手卡。「先史遗产 水晶头骨」的效果1回合只能使用1次。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51447164,'科技属 刃枪手',replace(replace('同调怪兽调整1只＋调整以外的同调怪兽1只以上\r\n这张卡为对象的对方的魔法·陷阱卡发动时，可以把1张手卡送去墓地，那个效果无效。此外，对方回合1次，可以把自己墓地存在的1只名字带有「科技属」的怪兽从游戏中除外，场上表侧表示存在的这张卡从游戏中除外。下次的准备阶段时，这个效果除外的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'魔法·陷阱卡的效果无效','这张卡除外','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51449743,'连锁爆破','给与对方基本分500分伤害。这张卡在连锁2或者连锁3发动的场合，这张卡加入卡组洗切。这张卡在连锁4以后发动的场合，这张卡回到手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51452091,'王宫的通告','①：只要这张卡在魔法与陷阱区域存在，这张卡以外的场上的全部陷阱卡的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51481927,'魔法吸收','每次魔法卡发动，这张卡的控制者基本分回复500分。','恢复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51482758,'陷阱拆除','破坏表侧表示的场上的存在的1张陷阱卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51497409,'DDD 磐石王 大流士',replace(replace('恶魔族3星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以自己场上1张「契约书」卡为对象才能发动。那张卡破坏，自己从卡组抽1张。这个效果在对方回合也能发动。\r\n②：这张卡和对方怪兽进行战斗的伤害计算时，把这张卡1个超量素材取除才能发动。这张卡不会被那次战斗破坏，伤害计算后那只对方怪兽破坏，给与对方500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51531505,'龙穴之魔术师',replace(replace('←8 【灵摆】 8→\r\n①：1回合1次，另一边的自己的灵摆区域有「魔术师」卡存在的场合，把手卡1只灵摆怪兽丢弃，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。\r\n【怪兽描述】\r\n年纪轻轻就领会唤醒龙魂的神通力的天才魔术师。由于他沉默寡言加上清心寡欲这种对魔术的态度而不擅长与人交际，但总被徒弟「龙脉之魔术师」折腾到抓狂。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51534754,'通向黄泉的渡船','①：这张卡被战斗破坏送去墓地的场合发动。把让这张卡破坏的怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51543904,'No.99 希望皇龙 霍普德拉古恩',replace(replace('10星怪兽×3\r\n这张卡也能把手卡1张「升阶魔法」魔法卡丢弃，在自己场上的「希望皇 霍普」怪兽上面重叠来超量召唤。\r\n①：1回合1次，以自己墓地1只「No.」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。\r\n②：这张卡为对象的怪兽的效果发动时，把这张卡1个超量素材取除才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'是否在「希望皇 霍普」怪兽上面重叠超量召唤？','特殊召唤','发动无效并破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51549976,'最终甲虫注射','把自己场上表侧表示存在的5张名字带有「甲虫装机」的卡送去墓地才能发动。对方场上的卡全部破坏。对方在这个回合的战斗阶段中不能把手卡·墓地发动的效果怪兽的效果发动。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51554871,'灼热工的巨匠 火炎','把自己墓地存在的这张卡从游戏中除外发动。自己场上表侧表示存在的名字带有「熔岩」的怪兽的攻击力上升400。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51555725,'废铁破坏者','对方场上存在怪兽的场合，这张卡可以从手卡特殊召唤。用这个方法把这张卡特殊召唤成功时，选择自己场上表侧表示存在的1只名字带有「废铁」的怪兽破坏。','名字带有「废铁」的怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51562916,'大波小波','自己场上表侧表示存在的水属性怪兽全破坏。之后，可以从手卡特殊召唤最多和破坏数同等数量的水属性怪兽上场。','是否要特殊召唤水属性怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51566770,'永火守护者','自己手卡是0张的场合，场上表侧表示存在的这张卡不会被战斗以及卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51570882,'强欲毒融合龙',replace(replace('「捕食植物」怪兽＋原本等级是8星以上的暗属性怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。\r\n①：1回合1次，以场上1只表侧表示怪兽为对象才能发动。直到回合结束时，那只怪兽的攻击力变成0，效果无效化。\r\n②：这张卡被破坏送去墓地的场合发动。场上的怪兽全部破坏。那之后，可以把自己墓地1只8星以上的暗属性怪兽除外把这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'效果无效','全部破坏','是否把「强欲毒融合龙」特殊召唤？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51589188,'光子三叉戟','选择自己场上1只名字带有「光子」的怪兽才能发动。直到结束阶段时，选择的怪兽的攻击力上升700，向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。此外，选择的怪兽给与对方基本分战斗伤害时，可以选择场上1张魔法·陷阱卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51606429,'幻影骑士团 墓盾',replace(replace('①：这张卡发动后变成通常怪兽（战士族·暗·3星·攻/守0）在怪兽区域攻击表示特殊召唤（不当作陷阱卡使用）。\r\n②：自己回合把墓地的这张卡除外，以对方场上1张表侧表示的陷阱卡为对象才能发动。那张卡的效果直到回合结束时无效。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'表侧表示的陷阱卡效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51616747,'努比亚的守卫','这张卡对对方造成战斗伤害时，可以将自己墓地里的1张永续魔法卡弹回卡组最上面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51617185,'机甲部队·超大变形',replace(replace('「机甲部队·超大变形」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：把这张卡解放才能发动。从手卡·卡组把「机甲部队·超大变形」以外的1只「机甲」怪兽特殊召唤。\r\n②：这张卡在墓地存在，自己场上的「机甲要塞」被送去自己墓地的场合，把那1只「机甲要塞」从墓地除外才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51630558,'上级抽卡','把自己场上表侧表示存在的1只8星以上的怪兽解放发动。从自己卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51632798,'可变机兽 炮手龙',replace(replace('①：这张卡可以不用解放作通常召唤。\r\n②：这张卡的①的方法通常召唤的这张卡的原本的攻击力·守备力变成一半。','\r',char(13)),'\n',char(10)),'不使用祭品通常召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51638941,'V-喷气虎','擅长空中战，具备合体能力的怪兽。驱使合体和分离施展立体攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51644030,'迪亚邦德之核',replace(replace('「迪亚邦德之核」的②的效果1回合只能使用1次。\r\n①：这张卡的攻击宣言时发动。这张卡的攻击力上升600。\r\n②：以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时下降这张卡的攻击力数值。那之后，这张卡直到下个回合的准备阶段除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'这张卡的攻击力上升600','对方怪兽攻击力下降','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51670553,'吸魂的竹光','选择名字带有「竹光」的装备魔法卡装备的1只怪兽发动。选择怪兽给与对方玩家战斗伤害的场合，下次的对方回合的抽卡阶段跳过。第2次的自己的准备阶段时这张卡破坏。','跳过抽卡阶段','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51701885,'瞬移宝宝D.D.','自己场上表侧表示存在的这张卡从游戏中除外的场合，可以从自己卡组把1只攻击力1500以下的念动力族怪兽从游戏中除外。下次的自己的准备阶段时，这个效果除外的怪兽特殊召唤。','除外','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51717541,'永火雷破','自己手卡是0张的场合才能发动。选择自己墓地存在的1张名字带有「永火」的卡从游戏中除外，选择对方场上存在的1张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51728779,'堕天使 安度西亚斯',replace(replace('自己对「堕天使 安度西亚斯」1回合只能有1次特殊召唤，那些作为①②的效果1回合各能使用1次。\r\n①：从手卡把这张卡和1张「堕天使」卡丢弃，以自己墓地1张「堕天使」卡为对象才能发动。那张卡加入手卡。\r\n②：支付1000基本分，以自己墓地1张「堕天使」魔法·陷阱卡为对象才能发动。那张魔法·陷阱卡的效果适用。那之后，墓地的那张卡回到卡组。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51735257,'No.50 黑玉米号',replace(replace('4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择持有这张卡的攻击力以下的攻击力的对方场上1只怪兽才能发动。选择的怪兽送去墓地，给与对方基本分1000分伤害。这个效果发动的回合，这张卡不能攻击。','\r',char(13)),'\n',char(10)),'送墓并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51773900,'宁神魔法','主要阶段一的开始时才能发动。那个回合的主要阶段时以及战斗阶段时，双方玩家不能把怪兽通常召唤·反转召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51777272,'月光舞猫姬',replace(replace('「月光」怪兽×2\r\n①：这张卡不会被战斗破坏。\r\n②：1回合1次，自己主要阶段1把这张卡以外的自己场上1只「月光」怪兽解放才能发动。这个回合，对方怪兽各有1次不会被战斗破坏，这张卡可以向全部对方怪兽各作2次攻击。\r\n③：这张卡的攻击宣言时发动。给与对方100伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51788412,'古代的机械混沌巨人',replace(replace('「古代的机械」怪兽×4\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：只要这张卡在怪兽区域存在，这张卡不受魔法·陷阱卡的效果影响，对方在战斗阶段中不能把怪兽的效果发动。\r\n②：这张卡可以向对方怪兽全部各作1次攻击，向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51790181,'无欲之壶','选择自己或者对方墓地存在的卡合计2张，回到持有者卡组。这张卡发动后，不送去墓地从游戏中除外。「无欲之壶」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51808422,'XX-剑士 福特罗尔',replace(replace('这张卡不能通常召唤。自己场上有「X-剑士」怪兽2只以上存在的场合才能特殊召唤。\r\n①：1回合1次，以自己墓地1只4星以下的「X-剑士」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51814159,'急袭猛禽-死灵秃鹫','①：1回合1次，把自己场上1只「急袭猛禽」怪兽解放，以自己墓地1张「升阶魔法」魔法卡为对象才能发动。那张卡加入手卡。这个效果的发动后，直到回合结束时自己不用「升阶魔法」魔法卡的效果不能把怪兽超量召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51827737,'次世代再生品','选择自己墓地存在的1只名字带有「次世代」的怪兽发动。直到结束阶段时，这张卡当作和选择怪兽同名卡使用。这个效果1回合只能使用1次。','卡名变化','请选择一张名字带有「次世代」的怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51828629,'魔导骑士 基尔提亚','「冥界的番人」＋「王座守护者」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51838385,'底比斯的梦魇','①：自己手卡和自己的魔法与陷阱区域没有卡存在的场合，这张卡的攻击力上升1500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51852507,'蔷薇之圣弓手','自己场上有植物族怪兽存在，对方把陷阱卡发动时，把这张卡从手卡送去墓地才能发动。那个发动无效并破坏。这个效果在对方回合也能发动。','陷阱无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51855378,'废品剑士','可以把自己墓地存在的1只名字带有「废品」的怪兽从游戏中除外，这张卡的攻击力直到结束阶段时上升400。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51858200,'捕食惑星',replace(replace('「捕食惑星」的①的效果1回合只能使用1次。\r\n①：这张卡在魔法与陷阱区域存在，有捕食指示物放置的怪兽从场上离开的场合发动。从卡组把1张「捕食」卡加入手卡。\r\n②：把墓地的这张卡除外才能发动。从自己的手卡·场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。这个效果融合召唤的场合，不是「捕食植物」怪兽不能作为融合素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51858306,'日食翼龙',replace(replace('①：这张卡被送去墓地的场合发动。从卡组把1只光属性或者暗属性的龙族·7星以上的怪兽除外。\r\n②：墓地的这张卡被除外的场合才能发动。这张卡的①的效果除外的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'除外','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51865604,'异热同心从者-幻影贤者','自己场上有名字带有「希望皇 霍普」的怪兽存在的场合，自己的主要阶段时才能发动。从卡组抽1张卡。此外，战斗阶段中自己场上的怪兽被除外的场合，选择那1只除外的怪兽，把场上的这张卡除外才能发动。选择的怪兽在自己场上特殊召唤，选对方场上1只攻击力3000以下的怪兽除外。「异热同心从者-幻影贤者」的效果1回合只能使用1次。','抽卡','特殊召唤并除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51912531,'幻妖种 密多罗','把这张卡作为同调素材的场合，不是地属性怪兽的同调召唤不能使用。自己的主要阶段时，选择场上1只地属性怪兽才能发动。选择的怪兽的等级下降1星。「幻妖种 密多罗」的效果1回合可以使用最多2次。','等级下降1','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51916032,'人造人-念力插孔者',replace(replace('「人造人-念力插孔者」的②的效果1回合只能使用1次。\r\n①：这张卡的卡名只要在场上·墓地存在当作「人造人-念力震慑者」使用。\r\n②：把这张卡解放才能发动。从卡组把「人造人-念力插孔者」以外的1只「人造人」怪兽加入手卡。那之后，对方的魔法与陷阱区域盖放的卡全部确认。那之中有陷阱卡的场合，可以把最多有那个数量的「人造人」怪兽从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'加入手卡','是否特殊召唤「人造人」怪兽？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51925772,'惧龙','这张卡被战斗破坏送去墓地时，可以从自己卡组把1只3星以下的龙族怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51934376,'打大喷嚏的河马龙','拥有巨大身体的河童怪兽。从其巨大的身体打出的喷嚏，可以匹敌台风。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51945556,'雷帝 扎博尔格','①：这张卡上级召唤成功的场合，以场上1只怪兽为对象发动。那只怪兽破坏。','破坏场上1只怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51960178,'妖精啦啦队少女',replace(replace('天使族4星怪兽×2\r\n把这张卡1个超量素材取除才能发动。从卡组抽1张卡。「妖精啦啦队少女」的效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51962254,'猎手猫头鹰','自己场上表侧表示存在的风属性怪兽每有1只，这张卡的攻击力上升500。此外，只要自己场上有其他的风属性怪兽表侧表示存在，对方不能选择这张卡作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51976476,'卑鄙的落穴','①：对方把怪兽守备表示特殊召唤时才能发动。那些守备表示怪兽除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51987571,'岩石战士','这张卡的战斗发生的对自己的战斗伤害变成0。这张卡的攻击让这张卡被战斗破坏送去墓地时，在自己场上把2只「岩石衍生物」（岩石族·地·1星·攻/守0）特殊召唤。这衍生物不能为上级召唤而解放。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(51987572,'岩石衍生物','由「岩石战士」的效果特殊召唤。这个衍生物不能为上级召唤而解放。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52022648,'妖精传姬-辛德瑞拉',replace(replace('①：只要这张卡在怪兽区域存在，双方不能把这张卡以外的场上的怪兽作为魔法卡的效果的对象。\r\n②：1回合1次，从手卡丢弃1张魔法卡才能发动。从自己的手卡·卡组·墓地选1张装备魔法卡给这张卡装备。这个效果装备的装备魔法卡在结束阶段回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52031567,'元素英雄 泥球侠',replace(replace('「元素英雄 水泡侠」＋「元素英雄 黏土侠」\r\n这张卡只能融合召唤不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52035300,'不死武士','这张卡不能作为战士族怪兽的祭品召唤以外的祭品。自己的准备阶段时这张卡在墓地存在，自己场上没有怪兽卡存在的场合，可以把这张卡在自己场上特殊召唤。这个效果在自己墓地有战士族以外的怪兽存在的场合不能发动。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52038272,'狱火机·路西弗格',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把1只「狱火机」怪兽除外的场合才能从手卡特殊召唤。\r\n①：1回合1次，以场上1只怪兽为对象才能发动。那只怪兽破坏。这个效果发动的回合，这张卡不能攻击。\r\n②：对方回合1次，把自己场上1只怪兽解放，以对方墓地1张卡为对象才能发动。那张卡除外。','\r',char(13)),'\n',char(10)),'怪兽破坏','对方墓地的卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52040216,'鹰身女妖的宠物龙','①：这张卡的攻击力·守备力上升场上的「鹰身女郎」数量×300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52068432,'三叉龙之影灵衣',replace(replace('「影灵衣」仪式魔法卡降临。不是用只使用9星以外的怪兽的仪式召唤不能特殊召唤。「三叉龙之影灵衣」的①②的效果1回合各能使用1次。\r\n①：自己场上的「影灵衣」怪兽成为效果的对象时，把这张卡从手卡丢弃才能发动。那个发动无效。这个效果在对方回合也能发动。\r\n②：这张卡仪式召唤成功时才能发动。对方的手卡·场上·墓地的卡各选1张，那3张除外。','\r',char(13)),'\n',char(10)),'效果无效','卡片除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52077741,'翻弄敌人的精灵剑士','①：这张卡不会被和攻击力1900以上的怪兽的战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52085072,'绝望神 安提霍普',replace(replace('这张卡不能通常召唤。把自己场上4只表侧表示的1星怪兽送去墓地的场合才能从手卡·墓地特殊召唤。\r\n①：只要这张卡在怪兽区域存在，其他的自己怪兽不能攻击。\r\n②：这张卡进行战斗的战斗步骤中1次，把自己墓地1只1星怪兽除外才能发动。这张卡直到那次伤害步骤结束时不受其他卡的效果影响，不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52090844,'弩弓手','在自己的每1个准备阶段，给与对方基本分600分的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52097679,'右手持盾左手持剑','这个回合结束前，这张卡发动时全部场上存在的表侧表示的怪兽原来的攻击力和原来的守备力交替。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52098461,'新宇之怒','选择自己场上表侧表示存在的1只「元素英雄 新宇侠」发动。选择的「元素英雄 新宇侠」回到卡组，场上的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52101615,'融合咒印生物-暗',replace(replace('①：这张卡可以作为融合怪兽卡有卡名记述的1只融合素材怪兽的代替。那个时候，其他的融合素材怪兽必须是正规品。\r\n②：把暗属性融合怪兽卡决定的一组融合素材怪兽（这张卡作为那之内的1只）从自己场上解放才能发动。把以那些解放的怪兽为融合素材的1只暗属性融合怪兽从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52105192,'武器洞',replace(replace('这张卡发动的回合，自己不能通常召唤。\r\n①：把卡组最上面的卡送去墓地才能发动。从自己的卡组·墓地选1张装备魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52112003,'卡片上移','①：从自己卡组上面把最多5张卡确认，用喜欢的顺序回到卡组上面。这个回合自己在通常召唤外加上只有1次可以把1只怪兽上级召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52121290,'蛇女郎','以为是美女而随便靠近的话，便会被吸干全身的血液。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52128900,'同调礼物','选择自己场上表侧表示存在的1只同调怪兽和1只同调怪兽以外的怪兽发动。直到这个回合的结束阶段时，选择的同调怪兽的攻击力变成0，另1只怪兽的攻击力上升那个原本攻击力数值。','请选择一只同调怪兽','请选择1只同调怪兽以外的怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52140003,'爆弹海胆','对方把陷阱卡发动时才能发动。自己的准备阶段时，对方场上有陷阱卡表侧表示存在的场合，给与对方基本分1000分伤害。发动后第3次的自己的结束阶段时这张卡送去墓地。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52145422,'灰篮龙',replace(replace('水族调整＋调整以外的怪兽1只以上\r\n「灰篮龙」的①②的效果1回合各能使用1次。\r\n①：这张卡同调召唤成功时，以最多有那些作为同调素材的水属性怪兽数量的对方场上的卡为对象才能发动。那些卡破坏。\r\n②：这张卡被战斗·效果破坏送去墓地时，以这张卡以外的自己墓地1只水属性怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52158283,'先史遗产 巨石人头像','把墓地的这张卡从游戏中除外，选择场上攻击表示存在的1只3星以上的怪兽才能发动。选择的怪兽变成表侧守备表示或者里侧守备表示。「先史遗产 巨石人头像」的效果1回合只能使用1次。','改变表示形式','变成表侧守备表示','变成里侧守备表示','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52176579,'水晶机巧入舱',replace(replace('「水晶机巧入舱」的②的效果1回合只能使用1次。\r\n①：从自己的手卡·墓地各选1只「水晶机巧」调整特殊召唤。\r\n②：把墓地的这张卡除外，以自己场上1只「水晶机巧」怪兽为对象才能发动。把持有和那只怪兽的等级不同等级的1只「水晶机巧」怪兽从卡组送去墓地。作为对象的怪兽的等级变成和送去墓地的怪兽的等级相同。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52182715,'绿尘妖','这张卡不能解放，也不能作为融合·同调·超量召唤的素材。场上的这张卡被破坏时，这张卡的控制者选择自己场上1张魔法·陷阱卡回到持有者手卡。「绿尘妖」在自己场上只能有1只表侧表示存在。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52198054,'烈焰加农炮弹匣',replace(replace('「烈焰加农炮弹匣」的②的效果1回合只能使用1次。\r\n①：这张卡的卡名只要在魔法与陷阱区域存在当作「烈焰加农炮-三叉戟式」使用。\r\n②：自己以及对方主要阶段才能把这个效果发动。手卡1张「火山」卡送去墓地，自己从卡组抽1张。\r\n③：自己以及对方主要阶段把墓地的这张卡除外才能发动。从卡组把1张「火山」卡送去墓地。','\r',char(13)),'\n',char(10)),'是否使用「烈焰加农炮弹匣」的效果？','送去墓地','发动同时使用效果','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52222372,'次世代涡轮机','只要这张卡在自己场上表侧表示存在，自己场上表侧表示存在的名字带有「次世代」的怪兽的攻击力上升400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52228131,'格挡','从手卡把1张名字带有「剑斗兽」的卡回到卡组。陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52240819,'魔界剧团-恶魔反派',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，把自己场上1只「魔界剧团」怪兽解放，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时下降解放的怪兽的原本攻击力数值。\r\n【怪兽效果】\r\n①：这张卡召唤·特殊召唤成功的场合，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时下降自己场上的「魔界剧团」怪兽数量×1000。\r\n②：这张卡战斗破坏对方怪兽时，以自己墓地1张「魔界台本」魔法卡为对象才能发动。那张卡在自己场上盖放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52248570,'幽狱王后恶魔','这张卡的控制者在每次自己的准备阶段支付1000基本分。场上有「万魔殿-恶魔的巢窟-」存在，这张卡在墓地存在的场合，每次自己的准备阶段把场上存在的1只4星以下的恶魔族怪兽的攻击力直到结束阶段时上升1000。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52263685,'哥布林偷窥者','对方从卡组上面把3张卡翻开。自己从那之中选择1张回到对方卡组最下面，剩下的卡用喜欢的顺序回到对方卡组上面。','请选择放回卡组最下方的卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52265835,'正义盟军 风哮怪','这张卡和光属性怪兽进行战斗的场合，伤害步骤内这张卡的攻击力上升700。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52286175,'陷阱反应机·空式','对方把陷阱卡发动时才能发动。把那张陷阱卡破坏，给与对方基本分800分伤害。这个效果1回合只能使用1次。','破坏并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52319752,'暗黑迅猛龙','这张卡向对方怪兽攻击的场合，伤害步骤内攻击力上升400。这张卡被对方怪兽攻击的场合，伤害步骤内攻击力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52323207,'番兵石人','这张卡1回合1次，可以变成里侧守备表示。这张卡反转召唤成功时，选择对方1只怪兽回到持有者手卡。','变成里侧守备','返回手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52323874,'急袭猛禽-恶魔雕',replace(replace('3星「急袭猛禽」怪兽×2\r\n「急袭猛禽-恶魔雕」的效果1回合只能使用1次。\r\n①：把这张卡1个超量素材取除，以对方场上1只特殊召唤的表侧表示怪兽为对象才能发动。给与对方那只怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52339733,'龙胆花鳞虫','①：这张卡在怪兽区域存在，自己怪兽和对方怪兽进行战斗的伤害计算时发动。直到伤害步骤结束时，进行战斗的各自怪兽的攻击力变成和各自守备力相同数值。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52340274,'银河女王之光','①：以自己场上1只7星以上的怪兽为对象才能发动。自己场上的全部怪兽的等级直到回合结束时变成和作为对象的怪兽相同等级。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52340444,'闪刀机-大黄蜂浮游单元','①：自己的主要怪兽区域没有怪兽存在的场合才能发动。在自己场上把1只「闪刀姬衍生物」（战士族·暗·1星·攻/守0）守备表示特殊召唤。这衍生物不能解放。自己墓地有魔法卡3张以上存在的场合，那衍生物的攻击力·守备力变成1500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52340445,'闪刀姬衍生物','由「闪刀机-大黄蜂浮游单元」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52346240,'锁猫','这张卡召唤成功时，可以选择自己墓地存在的1只1星的兽族怪兽表侧守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52352005,'XX-剑士 加特姆士',replace(replace('调整＋地属性怪兽1只以上\r\n①：把自己场上1只「X-剑士」怪兽解放才能发动。对方手卡随机选1张丢弃。','\r',char(13)),'\n',char(10)),'对方随机丢弃1张手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52367652,'食命者','嗜食所有生物的灵魂，将将其作为自己的能量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52370835,'英豪挑战者 剑盾兵','自己场上有名字带有「英豪」的怪兽存在的场合，把这张卡从手卡送去墓地才能发动。这个回合，战斗发生的对自己的战斗伤害变成0，自己场上的名字带有「英豪」的怪兽不会被战斗破坏。这个效果在对方回合也能发动。','破坏耐性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52404456,'魔偶甜点·胶凝冰糕邮递员',replace(replace('①：这张卡特殊召唤成功时才能发动。从卡组把1张「魔偶甜点」魔法·陷阱卡加入手卡。这个效果在自己场上有兽族「魔偶甜点」怪兽存在的场合才能发动和处理。\r\n②：这张卡被对方破坏送去墓地的场合发动。这张卡回到卡组。','\r',char(13)),'\n',char(10)),'返回卡组','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52417194,'大暴落','对方的手卡在8张以上时发动。对方把手卡加入卡组洗切，之后抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52430902,'念力跳跃者','支付1000基本分并选择对方场上表侧表示存在的1只怪兽，那只怪兽和「念力跳跃者」以外的自己场上表侧表示存在的1只念动力族怪兽的控制权交换。选择的怪兽在这个回合不能作表示形式的改变。这个效果1回合只能使用1次。','交换控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52467217,'牛头鬼',replace(replace('「牛头鬼」的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。从卡组把1只不死族怪兽送去墓地。\r\n②：这张卡被送去墓地的场合，从自己墓地把「牛头鬼」以外的1只不死族怪兽除外才能发动。从手卡把1只不死族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'卡组不死族怪兽送去墓地','手卡不死族怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52496105,'剑斗兽的斗器 护臂','名字带有「剑斗兽」的怪兽才能装备。只要有这张卡装备，装备怪兽不会被战斗破坏（伤害计算适用）。装备怪兽从自己场上回到卡组让这张卡被送去墓地时，这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52497105,'蛮勇鳞粉','选择自己场上表侧表示存在的1只怪兽才能发动。选择的怪兽的攻击力上升1000，这个回合不能向对方玩家直接攻击。这个回合的结束阶段时，选择的怪兽的攻击力下降2000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52503575,'最终突击命令','①：只要这张卡在魔法与陷阱区域存在，场上的表侧表示怪兽变成攻击表示，不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52512994,'火车','这张卡不能通常召唤。自己场上不死族怪兽有表侧表示2只以上存在的场合才能特殊召唤。这张卡特殊召唤成功时，场上存在的这张卡以外的怪兽全部回到卡组。这张卡的攻击力变成这个效果回到卡组的不死族怪兽数量×1000的数值。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52518793,'剑斗兽之槛-圆形斗技场','每次怪兽从卡组在场上特殊召唤，给这张卡放置1个指示物。场上表侧表示存在的名字带有「剑斗兽」的怪兽，这张卡每放置有1个指示物，攻击力·守备力上升100。这张卡被卡的效果破坏时，可以从手卡丢弃1张「剑斗兽之槛-圆形斗技场」让这张卡不破坏。','放置指示物','是否要丢弃一张「剑斗兽之槛-圆形斗技场」代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52550973,'法老的仆人','据域是曾经侍奉过法老的人们的亡灵。拥有丝毫不会动摇的忠诚之心。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52551211,'影依猎犬',replace(replace('「影依猎犬」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡反转的场合，以自己墓地1张「影依」卡为对象才能发动。那张卡加入手卡。\r\n②：这张卡被效果送去墓地的场合，以场上1只怪兽为对象才能发动。那只怪兽的表示形式变更。这个时候，「影依」怪兽以外的反转怪兽的效果不发动。','\r',char(13)),'\n',char(10)),'加入手卡','表示形式变更','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52558805,'太鼓魔人 咚咚拍子',replace(replace('3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上1只超量怪兽才能发动。把选择的怪兽1个超量素材取除，自己场上的全部名字带有「魔人」的超量怪兽的攻击力上升500。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52571838,'偷偷靠近的恶魔蝠鲼','这张卡召唤成功时，陷阱卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52575195,'机灵兔骑士','自己场上的兽战士族·光属性怪兽1只成为对方的卡的效果的对象时或者成为对方怪兽的攻击对象时才能发动。成为对象的自己怪兽直到下次的自己的准备阶段时从游戏中除外，这张卡从手卡特殊召唤。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52584282,'大力独角仙','巨大的甲虫，锐利的角与坚硬的身体非常强力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52589809,'风来王 野风',replace(replace('①：自己场上有攻击力1500以下的恶魔族调整存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤过的回合，自己不是同调怪兽不能从额外卡组特殊召唤。\r\n②：把墓地的这张卡除外才能发动。从卡组把1只攻击力1500以下的恶魔族调整加入手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52601736,'甲虫装机 蝗虫','1回合1次，可以从自己的手卡·墓地把1只名字带有「甲虫装机」的怪兽当作装备卡使用给这张卡装备。这张卡当作装备卡使用而装备中的场合，装备怪兽的等级上升4星。此外，可以通过把当作装备卡使用而装备中的这张卡送去墓地，这个回合装备怪兽可以直接攻击对方玩家。这个效果发动的回合，装备怪兽以外的怪兽不能攻击。','装备','直接攻击','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52624755,'暗道化师 彼得','①：这张卡被送去墓地时，把墓地的这张卡除外才能发动。从手卡·卡组把1只「暗道化师 彼得」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52628687,'死灵之魔导书','把自己墓地1只魔法师族怪兽从游戏中除外，把这张卡以外的手卡1张名字带有「魔导书」的魔法卡给对方观看才能发动。选择自己墓地1只魔法师族怪兽表侧攻击表示特殊召唤，把这张卡装备。此外，装备怪兽的等级上升因为这张卡发动而除外的魔法师族怪兽的等级数值。「死灵之魔导书」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52639377,'魔界斗士 屠龙之剑',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡被卡的效果破坏送去墓地时，可以从自己墓地选择这张卡以外的1只4星以下的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52648457,'戈耳工之眼','结束阶段结束前，场上存在的守备表示的怪兽的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52653092,'闪光No.0 希望之异热同心',replace(replace('相同阶级的「No.」超量怪兽×3\r\n规则上，这张卡的阶级当作1阶使用。这张卡也能把手卡1张「升阶魔法」通常魔法卡丢弃，在自己场上的「希望皇 霍普」怪兽上面重叠来超量召唤。\r\n①：这张卡的超量召唤不会被无效化。\r\n②：在这张卡的超量召唤成功时，对方不能把效果发动。\r\n③：这张卡的攻击力·守备力上升这张卡的超量素材数量×1000。\r\n④：对方回合1次，把这张卡1个超量素材取除才能发动。这个回合对方不能把效果发动。','\r',char(13)),'\n',char(10)),'是否在自己场上的「希望皇 霍普」怪兽上面重叠来超量召唤?','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52665542,'光道的神域',replace(replace('①：1回合1次，把手卡1只「光道」怪兽送去墓地，以那只怪兽以外的自己墓地1只「光道」怪兽为对象才能发动。作为对象的怪兽加入手卡。\r\n②：只要这张卡在魔法与陷阱区域存在，每次从自己卡组有卡被送去墓地，给这张卡放置1个光指示物。\r\n③：自己场上的「光道」卡被效果破坏的场合，可以作为代替把破坏的「光道」卡每1张2个自己场上的光指示物取除。','\r',char(13)),'\n',char(10)),'送去墓地','是否要使用「光道的神域」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52675689,'诱发暗黑长眠的路西法','召唤·反转召唤的时候，选对方场上的1只怪兽。只要这张卡在场上表侧表示存在，被选择的怪兽不能攻击。','攻击限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52684508,'黑炎弹',replace(replace('这张卡发动的回合，「真红眼黑龙」不能攻击。\r\n①：以自己的怪兽区域1只「真红眼黑龙」为对象才能发动。给与对方那只「真红眼黑龙」的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52687916,'冰结界之龙 三叉龙',replace(replace('调整＋调整以外的怪兽2只以上\r\n①：这张卡同调召唤成功时才能发动。对方的手卡·场上·墓地的卡可以各选最多1张除外。','\r',char(13)),'\n',char(10)),'除外','是否要除外场上的卡？','是否要除外墓地的卡？','是否要除外手卡？','','','','','','','','','','','','');
INSERT INTO texts VALUES(52702748,'异次元的案内人','这张卡召唤成功时，这张卡的控制权转移给对方。每次结束阶段，对方选择这张卡的控制者的墓地里的1张卡从游戏中除外。','控制权转移','除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52709508,'盟军·次世代三力人',replace(replace('名字带有「次世代」的调整＋调整以外的怪兽1只以上\r\n作为这张卡的同调素材的调整以外的怪兽属性让这张卡得到以下效果。\r\n●地属性：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。\r\n●炎属性：这张卡战斗破坏怪兽的场合，给与对方基本分那只怪兽的攻击力数值的伤害。\r\n●光属性：1回合1次，可以选择自己墓地1只光属性怪兽在自己场上盖放。','\r',char(13)),'\n',char(10)),'伤害','选择自己墓地1只光属性怪兽在自己场上盖放','地属性怪兽为同调素材','炎属性怪兽为同调素材','光属性怪兽为同调素材','','','','','','','','','','','');
INSERT INTO texts VALUES(52714670,'玄化次元',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：对方对怪兽的特殊召唤成功的场合，以除外的1只自己的「玄化」怪兽为对象才能把这个效果发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽在下个回合的结束阶段除外。\r\n②：这张卡已在魔法与陷阱区域存在的状态，这张卡以外的自己的「玄化」卡被除外的场合，以对方场上1张卡为对象才能发动。那张卡除外。','\r',char(13)),'\n',char(10)),'发动但不使用效果','发动并使用①效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52738610,'影灵衣舞姬',replace(replace('「影灵衣舞姬」的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，对方不能对应「影灵衣」仪式魔法卡的发动把魔法·陷阱·怪兽的效果发动，自己场上的「影灵衣」仪式怪兽不会成为对方的效果的对象。\r\n②：这张卡被效果解放的场合，以「影灵衣舞姬」以外的除外的1只自己的「影灵衣」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52768103,'KA-2 死亡钳','这张卡战斗破坏怪兽并将其送去墓地时，对对方基本分造成数值等同于被破坏怪兽等级×500点的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52786469,'熔岩战士','这张卡战斗破坏对方怪兽的场合自己墓地有名字带有「熔岩」的怪兽4种类以上存在的场合，给与对方基本分那次战斗破坏的怪兽的攻击力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52792430,'捕食植物 捕蝇草棘龙',replace(replace('①：这张卡召唤·特殊召唤成功的场合，以对方场上1只表侧表示怪兽为对象才能发动。给那只怪兽放置1个捕食指示物。有捕食指示物放置的2星以上的怪兽的等级变成1星。\r\n②：这张卡和持有这张卡的等级以下的等级的怪兽进行战斗的伤害计算后才能发动。从卡组把「捕食植物 捕蝇草棘龙」以外的1只「捕食植物」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52800428,'死者之腕','从混沌沼泽中伸出手腕，将生者拖入其中。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52817046,'记忆抹消','对方手卡3张以下的场合才能发动。对方把手卡加入到卡组洗切。之后对方抽出和加入卡组的卡数量相同的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52823314,'由魔界到现世的巴士','这张卡被送去墓地时，选择「由魔界到现世的巴士」以外的自己或者对方的墓地1只怪兽回到持有者卡组。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52824910,'帝王翔风龙',replace(replace('①：这张卡不会被和相同攻击力的怪兽的战斗破坏。\r\n②：这张卡被破坏送去墓地的场合，以场上1只怪兽为对象发动。那只怪兽回到持有者手卡。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52833089,'黑翼雷电','自己场上存在的名字带有「黑羽」的怪兽被战斗破坏送去墓地时才能发动。对方场上存在的卡每有1张，给与对方基本分400分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52834429,'极光之天气模样',replace(replace('①：「极光之天气模样」在自己场上只能有1张表侧表示存在。\r\n②：和这张卡相同纵列的自己的主要怪兽区域以及那些两邻的自己的主要怪兽区域存在的「天气」效果怪兽得到以下效果。\r\n●只让卡1张加入自己或者对方的手卡时，把这张卡除外才能发动。加入手卡的那张卡除外，那个玩家从卡组抽1张。','\r',char(13)),'\n',char(10)),'加入手卡的那张卡除外并抽卡（极光之天气模样）','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52840267,'堕天使 伊希塔布',replace(replace('自己对「堕天使 伊希塔布」1回合只能有1次特殊召唤，那些作为①②的效果1回合各能使用1次。\r\n①：从手卡把这张卡和1张「堕天使」卡丢弃才能发动。自己从卡组抽2张。\r\n②：支付1000基本分，以自己墓地1张「堕天使」魔法·陷阱卡为对象才能发动。那张魔法·陷阱卡的效果适用。那之后，墓地的那张卡回到卡组。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52840598,'无赖同调士','这张卡作为同调召唤的素材送去墓地的场合，直到这个回合的结束阶段时，这张卡为同调素材的同调怪兽攻击力上升600，效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52846880,'灾亡虫之影灵衣',replace(replace('「影灵衣」仪式魔法卡降临。不是只使用除「灾亡虫之影灵衣」以外的怪兽来仪式召唤不能特殊召唤。「灾亡虫之影灵衣」的①的效果1回合只能使用1次。\r\n①：把这张卡从手卡丢弃，以自己墓地1只「影灵衣」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：自己的「影灵衣」怪兽和从额外卡组特殊召唤的怪兽进行战斗的伤害步骤开始时发动。那只怪兽破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52860176,'凭依的血魂','这张卡做祭品。得到对方场上的全部3星以下的怪兽的控制权。','得到控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52869807,'黑羽-逆风之加斯特','自己场上没有卡存在的场合，这张卡可以从手卡特殊召唤。只要这张卡在场上表侧表示存在，对方怪兽向自己场上存在的名字带有「黑羽」的怪兽攻击的场合，那只攻击怪兽在伤害步骤内攻击力下降300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52875873,'进化光焰','自己场上有「电子龙」表侧表示存在的场合才能发动。对方场上1张卡破坏。这张卡发动的回合「电子龙」不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52900000,'灵魂鸟神-彦孔雀',replace(replace('「灵魂的降神」降临。这张卡不用仪式召唤不能特殊召唤。\r\n①：这张卡仪式召唤成功的场合才能发动。选对方场上最多3只怪兽回到持有者手卡。那之后，可以从手卡把1只4星以下的灵魂怪兽无视召唤条件特殊召唤。\r\n②：这张卡特殊召唤的回合的结束阶段发动。这张卡回到持有者手卡，在自己场上把2只「灵魂鸟衍生物」（鸟兽族·风·4星·攻/守1500）特殊召唤。','\r',char(13)),'\n',char(10)),'对方怪兽回到手卡','是否从手卡把灵魂怪兽特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52900001,'灵魂鸟衍生物','由「灵魂鸟神-彦孔雀」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52900379,'黑羽-追风之艾莉婕','自己场上表侧表示存在的名字带有「黑羽」的怪兽有2只以上被破坏的回合，这张卡可以从手卡特殊召唤。这张卡作为同调召唤的素材送去墓地的场合，自己回复600基本分。','回复600LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52913738,'破灭的仪式','「破灭之魔王 加兰道夫」的降临必需。必须从手卡·自己场上把等级合计直到7以上的怪兽解放。可以把自己墓地存在的这张卡从游戏中除外，这个回合自己场上表侧表示存在的仪式怪兽战斗破坏的怪兽不送去墓地回到卡组最上面。','仪式怪兽战斗破坏的怪兽返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52927340,'幻变骚灵·查询昆提兰那克',replace(replace('①：自己场上有「幻变骚灵」卡存在的场合，对方怪兽的攻击宣言时才能发动。这张卡从手卡特殊召唤，那次攻击无效。\r\n②：这张卡特殊召唤成功的场合，以对方场上1张表侧表示的卡为对象才能发动。这只怪兽表侧表示存在期间，那张卡的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52963531,'娱乐伙伴 洒水猛犸',replace(replace('←4 【灵摆】 4→\r\n①：1回合1次，自己场上有融合怪兽特殊召唤时才能发动。从自己的额外卡组把1只表侧表示的「异色眼」灵摆怪兽特殊召唤。\r\n【怪兽效果】\r\n「娱乐伙伴 洒水猛犸」的怪兽效果在决斗中只能使用1次。\r\n①：自己主要阶段才能发动。从自己场上把龙族的融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52971673,'衍生物复活祭','自己场上存在的衍生物全部破坏。把最多有这个效果破坏的衍生物数量的场上存在的卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52971944,'火遁封印式','1回合1次，可以把自己墓地1只炎属性怪兽从游戏中除外，选择对方墓地1张卡从游戏中除外。','是否现在使用「火遁封印式」的效果？','除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(52977572,'龙骑兵团-重标枪龙','这张卡召唤成功时，可以从手卡把1只名字带有「龙骑兵团」的鸟兽族怪兽特殊召唤，把这张卡当作装备卡使用来装备。这张卡被卡的效果当作装备卡使用装备中的场合，装备怪兽可以直接攻击对方玩家。这个时候，装备怪兽给与对方基本分的战斗伤害变成一半数值。','特殊召唤并装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53025096,'异色眼龙','①：这张卡战斗破坏对方怪兽送去墓地的场合发动。给与对方那只怪兽的原本攻击力一半数值的伤害。','基本分伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53027855,'风灵神 温德罗斯','这张卡不能通常召唤。自己墓地的风属性怪兽是5只的场合才能特殊召唤。这张卡特殊召唤成功时，对方场上的魔法·陷阱卡全部破坏。「风灵神 温德罗斯」的这个效果1回合只能使用1次。这张卡从场上离开的场合，下次的自己回合的战斗阶段跳过。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53039326,'钢核合成兽研究所','这张卡的控制者在每次自己的结束阶段从手卡给1张「核成兽的钢核」对方观看。或者不给观看让这张卡破坏。每次场上存在的名字带有「核成」的怪兽在结束阶段时被破坏，那些怪兽的原本持有者可以从卡组把1只名字带有「核成」的怪兽加入手卡。','把1张「核成兽的钢核」对方观看','破坏「钢核合成兽研究所」','检索','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53046408,'非常食','①：把这张卡以外的自己场上的魔法·陷阱卡任意数量送去墓地才能发动。自己回复因为这张卡发动而送去墓地的卡数量×1000基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53054833,'疾行机人 双球悠悠','①：这张卡召唤成功时，以自己墓地1只3星以下的「疾行机人」怪兽为对象才能发动。那只怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53063039,'罪爪流','自己场上有名字带有「罪」的怪兽表侧表示存在的场合才能发动。选择对方场上存在的1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53077251,'单一化','①：以对方场上1只表侧表示怪兽为对象才能发动。作为对象的怪兽以外的场上的全部怪兽的攻击力直到回合结束时变成和作为对象的怪兽相同。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53090623,'黄血鬼','自己超量召唤成功时，这张卡可以从手卡特殊召唤。此外，1回合1次，把自己场上1个超量素材取除，选择场上1只超量怪兽才能发动。选择的怪兽的阶级下降1阶，攻击力下降300。','特殊召唤','阶级下降','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53100061,'X-剑士 阿克塞尔','只要这张卡在自己场上表侧表示存在，名字带有「剑士」的怪兽被战斗破坏送去墓地时，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53112492,'对抗魔术','把自己场上存在的2个魔力指示物取除发动。魔法卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53116300,'侵入魔鬼的呼唤者','把这张卡解放对名字带有「侵入魔鬼」的怪兽的上级召唤成功时，可以从自己卡组把1只4星以下的名字带有「侵入魔鬼」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53119267,'魔力之棘','对方的手卡丢弃去墓地时，每丢弃1张卡就给与对方基本分500分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53129443,'黑洞','①：场上的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53136004,'魔导皇士 安普尔','把这张卡以外的自己场上表侧表示存在的1只魔法师族怪兽和自己墓地1张名字带有「魔导书」的卡从游戏中除外才能发动。选择对方场上表侧表示存在的1只怪兽直到结束阶段时得到控制权。「魔导皇士 安普尔」的效果1回合只能使用1次，这个效果发动的回合，这张卡不能攻击。','获得控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53143898,'幻变骚灵·网络傀儡师',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从卡组选1张「幻变骚灵」陷阱卡在自己的魔法与陷阱区域盖放。\r\n②：以自己场上1张「幻变骚灵」卡和自己墓地1只「幻变骚灵」怪兽为对象才能发动。作为对象的场上的卡送去墓地，作为对象的墓地的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53152590,'链·线圈人','选择自己场上存在的1只名字带有「链」的怪兽发动。那只怪兽的攻击力·守备力上升300。这个效果1回合只能使用1次。','攻守上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53153481,'铠甲剑尾战士','有着剑状尾的怪异战士。用双手和尾巴进行三连攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53162898,'亚马逊贤者','这张卡攻击的场合，那次伤害步骤结束时选择对方场上存在的1张魔法·陷阱卡破坏。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53180020,'影灵衣战士 艾可萨',replace(replace('「影灵衣战士 艾可萨」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡被效果解放的场合才能发动。从卡组把1只龙族的「影灵衣」仪式怪兽加入手卡。\r\n②：这张卡被除外的场合，以这张卡以外的除外的1只自己的「影灵衣」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'加入手卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53183600,'青眼卡通龙',replace(replace('这张卡不能通常召唤。自己场上有「卡通世界」存在，把自己场上2只怪兽解放的场合可以特殊召唤。\r\n①：这张卡在特殊召唤的回合不能攻击。\r\n②：这张卡的攻击宣言之际，自己必须支付500基本分。\r\n③：对方场上没有卡通怪兽存在的场合，这张卡可以直接攻击。存在的场合，必须把卡通怪兽作为攻击对象。\r\n④：场上的「卡通世界」被破坏时这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53193261,'电气钥匙','自己场上表侧表示存在的名字带有「电气」的怪兽在这个回合可以直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53199020,'魔轰神 狄阿尼拉','这张卡可以把1只名字带有「魔轰神」的怪兽解放表侧攻击表示上级召唤。只要这张卡在自己场上表侧表示存在，对方发动的通常魔法卡的效果变成「对方选择1张手卡丢弃」。这个效果1回合只能适用1次。','把1只名字带有「魔轰神」的怪兽解放召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53208660,'灵摆呼唤',replace(replace('「灵摆呼唤」在1回合只能发动1张，把「魔术师」灵摆怪兽的灵摆效果发动过的回合不能发动。\r\n①：丢弃1张手卡才能发动。把2只卡名不同的「魔术师」灵摆怪兽从卡组加入手卡。这个效果的发动后，直到下次的对方回合结束时自己的灵摆区域的「魔术师」卡不会被效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53239672,'灵魂障壁','只要自己场上存在怪兽，对这张卡的控制者的战斗伤害变为0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53244294,'No.57 奋迅龙 三头龙',replace(replace('4星怪兽×3\r\n这张卡特殊召唤成功时，可以选择对方场上的一只怪兽，这张卡的攻击力上升选择的怪兽的攻击力。对方场上存在的卡比自己多的场合，可以取除1个这张卡的超量素材，选择1个怪兽或魔法·陷阱卡区域发动。只要这张卡在场上表侧表示存在，那个卡片区域就不能使用。','\r',char(13)),'\n',char(10)),'攻击上升','区域封锁','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53251824,'急袭猛禽-驱逐伯劳','①：这张卡召唤·特殊召唤成功的回合的自己主要阶段才能发动1次。从手卡把1只4星以下的「急袭猛禽」怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53257892,'巨型植物',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●把自己的手卡或者墓地存在的1只昆虫族或者植物族怪兽特殊召唤。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53262004,'异色眼风雷龙',replace(replace('「异色眼」怪兽＋灵摆怪兽\r\n「异色眼风雷龙」的①②的效果1回合各能使用1次。\r\n①：这张卡特殊召唤成功时，以对方场上1只表侧攻击表示怪兽为对象才能发动。那只怪兽回到持有者手卡。\r\n②：这张卡以外的怪兽的效果·魔法·陷阱卡发动时才能发动。从自己的额外卡组让1只表侧表示的灵摆怪兽回到卡组，那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53266486,'麻醉弹丸龙',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：场上的这张卡为对象的连接怪兽的效果发动时才能发动。这张卡破坏。那之后，选场上1只表侧表示怪兽。那只怪兽不能攻击，效果无效化。\r\n②：场上的这张卡被战斗·效果破坏送去墓地的回合的结束阶段才能发动。从卡组把「麻醉弹丸龙」以外的1只「弹丸」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53274132,'链·修理工','这张卡战斗破坏怪兽送去墓地时，给与对方基本分300分伤害。1回合1次，可以把自己墓地存在的「链·修理工」以外的1只名字带有「链」的4星以下的怪兽特殊召唤。这个效果发动的回合这张卡不能攻击。','伤害','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53291093,'三角区','把场上存在的1只有A指示物放置的怪兽破坏。并且可以再从自己卡组把1只名字带有「外星」的4星怪兽特殊召唤。这个效果特殊召唤的怪兽在结束阶段时破坏。','是否要特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53293545,'火炎草','在火山附近生存的草。从花内吹出来火焰攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53303460,'魔神仪-蜡烛人偶',replace(replace('这个卡名的①②的效果1回合只能有1次使用其中任意1个。\r\n①：把手卡1张仪式魔法卡给对方观看才能发动。「魔神仪-蜡烛人偶」以外的卡组1只「魔神仪」怪兽和手卡的这张卡特殊召唤。\r\n②：这张卡从卡组的特殊召唤成功的场合才能发动。从卡组把1张仪式魔法卡加入手卡。\r\n③：只要这张卡在怪兽区域存在，自己不能从额外卡组把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53309998,'存档海马',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡在手卡·墓地存在，场上的连接怪兽所连接区有怪兽召唤·特殊召唤的场合才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53315891,'合神龙 蒂迈欧',replace(replace('「传说的骑士 克里底亚」＋「传说的骑士 赫谟」＋「传说的骑士 蒂迈欧」\r\n把自己场上的上记卡送去墓地的场合才能特殊召唤（不需要「融合」）。\r\n①：这张卡不受其他卡的效果影响。\r\n②：这张卡进行战斗的伤害计算时才能发动。这张卡的攻击力·守备力变成和场上的怪兽的最高攻击力相同。\r\n③：这张卡被战斗破坏时才能发动。选自己的手卡·卡组·墓地3只「传说的骑士」怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53334471,'御前试合','①：只要这张卡在魔法与陷阱区域存在，双方场上各自只能有1种类的属性的怪兽表侧表示存在。双方玩家在自身场上的表侧表示怪兽的属性是2种类以上的场合直到变成1种类为止必须送去墓地。','请选择要保留的属性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53334641,'鬼计惰天使',replace(replace('4星怪兽×2\r\n这张卡也能在「鬼计惰天使」以外的自己场上的「鬼计」超量怪兽上面重叠来超量召唤。此外，这张卡持有的超量素材数量变成10时，自己决斗胜利。\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。从卡组把1张「鬼计」魔法·陷阱卡加入手卡。\r\n②：1回合1次，自己主要阶段才能发动。把手卡1张「鬼计」卡在这张卡下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'是否在「鬼计」超量怪兽上面重叠超量召唤？','卡组检索','素材增加','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53341729,'吸入闪光的魔镜','只要这张卡在场上存在，墓地或者场上发动的光属性怪兽的效果无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53347303,'青眼光龙',replace(replace('这张卡不能通常召唤。把自己场上1只「青眼究极龙」解放的场合才能特殊召唤。\r\n①：这张卡的攻击力上升自己墓地的龙族怪兽数量×300。\r\n②：这张卡为对象的魔法·陷阱·怪兽的效果发动时才能发动。那个效果无效。','\r',char(13)),'\n',char(10)),'以这张卡为对象的效果无效化。','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53375573,'深渊的冥王','冥界之王，听说以前有着支配所有黑暗力量的能力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53388413,'总剑司令 加特姆士','只要这张卡在场上表侧表示存在，自己场上表侧表示存在的名字带有「剑士」的怪兽的攻击力上升400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53389254,'强袭黑羽-五月雨之骚速刀鸟',replace(replace('调整＋调整以外的怪兽1只以上\r\n「强袭黑羽-五月雨之骚速刀鸟」的②③的效果1回合各能使用1次。\r\n①：「黑羽」怪兽为素材作同调召唤的这张卡当作调整使用。\r\n②：这张卡同调召唤成功时，以自己墓地1只「强袭黑羽」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n③：这张卡被送去墓地的回合的自己主要阶段把这张卡以外的自己墓地1只「强袭黑羽-五月雨之骚速刀鸟」除外才能发动。这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'墓地怪兽特殊召唤','这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53408006,'宝石商人','自己场上表侧表示存在的地属性的通常怪兽进行战斗的伤害步骤时可以把这张卡从手卡送去墓地，那只怪兽的攻击力·守备力直到这个回合的结束阶段时上升1000。','攻守上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53413628,'码语者',replace(replace('效果怪兽2只\r\n①：这张卡的攻击力上升这张卡所连接区的怪兽数量×500。\r\n②：只要这张卡所连接区有怪兽存在，这张卡不会被战斗以及对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53451824,'幻兽机 协和金翅鸟',replace(replace('调整＋调整以外的怪兽1只以上\r\n只要这张卡在场上表侧表示存在，自己场上的衍生物不会被战斗以及效果破坏。场上的这张卡被对方破坏送去墓地的场合，把自己场上的衍生物全部解放才能发动。从自己墓地选择1只4星以下的名字带有「幻兽机」的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53461122,'地构体','地属性的通常怪兽祭品召唤的场合，这1只怪兽可以作为2只的数量的祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53466826,'英豪机会','选择自己场上1只名字带有「英豪」的怪兽才能发动。这个回合，选择的怪兽攻击力变成2倍，不能向对方玩家直接攻击。「英豪机会」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53485634,'真红眼溯刻龙',replace(replace('①：自己场上的7星以下的「真红眼」怪兽被对方怪兽的攻击或者对方的效果破坏送去自己墓地的场合才能发动。这张卡从手卡守备表示特殊召唤，尽可能把那些破坏的怪兽以和破坏时相同表示形式特殊召唤。\r\n②：把这张卡解放才能发动。这个回合，自己在通常召唤外加上只有1次，自己主要阶段可以把1只「真红眼」怪兽召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53493204,'心眼之女神','这张卡可以代替融合怪兽素材的其中1只来融合。这个时候，其他的融合素材必须是指定的融合素材。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53519297,'黑翼推进','把自己场上表侧表示存在的2只名字带有「黑羽」的调整从游戏中除外发动。从自己卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53527835,'暗黑都市','名字带有「命运英雄」的怪兽攻击时，攻击怪兽的攻击力比攻击对象怪兽的攻击力低的场合，攻击怪兽的攻击力在伤害计算时上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53530069,'微风之精灵','只要这张卡在自己场上表侧攻击表示存在，每次自己的准备阶段回复1000基本分。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53539634,'三眼小巫师','「黑森林的魔女」＋「三眼怪」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53540729,'发条战士','选择自己场上表侧表示存在的1只名字带有「发条」的怪兽才能发动。直到结束阶段时选择的1只怪兽的等级上升1星，攻击力上升600。这个效果只在这张卡在场上表侧表示存在能使用1次。','等级攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53550467,'圣骑士 崔斯坦',replace(replace('「圣骑士 崔斯坦」的②的效果1回合只能使用1次。\r\n①：只要自己场上有这张卡以外的「圣骑士」怪兽存在，对方不能把这张卡以外的自己场上的攻击力未满1800的怪兽作为攻击对象，也不能作为效果的对象。\r\n②：让这张卡把「圣剑」装备魔法卡装备的场合，以场上1张表侧表示的卡为对象发动。那张表侧表示的卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53567095,'神鸟攻击','①：把自己场上1只鸟兽族怪兽解放，以场上2张卡为对象才能发动。那些卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53569894,'光之金字塔','自己场上表侧表示存在的这张卡从场上离开的场合，自己场上存在的「斯芬克斯·安德鲁」和「斯芬克斯·迪蕾雅」破坏并从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53573406,'假面变色龙',replace(replace('这张卡在自己场上没有5星以上的怪兽存在的场合才能召唤。这张卡的效果发动的回合，自己不用从额外卡组的特殊召唤以及这张卡的效果不能特殊召唤。\r\n①：这张卡召唤成功时，以自己墓地1只守备力0的怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53581214,'火焰恶魔','手持炎之箭的死神，被其箭击中的话就会变成火人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53582587,'激流葬','①：怪兽召唤·反转召唤·特殊召唤时才能发动。场上的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(53586134,'水泡发射器','「元素英雄 水泡侠」才能装备。装备怪兽的攻击力上升800。装备怪兽被战斗破坏的场合，这张卡代替破坏，装备怪兽的控制者的战斗伤害为0。','','','','','','','','','','','','','','','','');
