INSERT INTO texts VALUES(53610653,'咒缚魔杖',replace(replace('魔法师族怪兽才能装备。\r\n①：装备怪兽的攻击力上升装备怪兽的等级×100。\r\n②：装备怪兽被对方破坏，这张卡被送去墓地的场合才能发动。那只怪兽从墓地往自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
