INSERT INTO texts VALUES(54629413,'游击风筝',replace(replace('「游击风筝」的效果1回合只能使用1次。\r\n①：这张卡从场上送去墓地的场合发动。给与对方500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54631665,'秘旋谍的秘密胜地',replace(replace('这张卡的控制者在每次自己结束阶段让自己墓地1只怪兽回到卡组。或者不回到卡组让这张卡破坏。\r\n①：只要这张卡在场地区域存在，这张卡以外的自己场上的「秘旋谍」卡不会成为对方的效果的对象。\r\n②：1回合1次，自己主要阶段才能发动。从卡组把1只「秘旋谍」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','自己墓地1只怪兽回到卡组','破坏这张卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54631834,'终端世界','这张卡在主要阶段1才能发动。只要这张卡在场上存在，双方把主要阶段2跳过。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54635100,'叮当铃连接星',replace(replace('怪兽2只\r\n这张卡的连接召唤不在自己的额外卡组的数量比对方多3张以上的场合不能进行。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54635862,'美西螈绅士','对方怪兽的攻击宣言时，这张卡可以从手卡特殊召唤。只要这张卡在场上表侧表示存在，对方不能向其他怪兽攻击。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54652250,'食人虫','反转：选择场上1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54658815,'远程苏生','①：以对方墓地1只怪兽为对象才能发动。那只怪兽在作为自己场上的连接怪兽所连接区的对方的主要怪兽区域特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54702678,'极战机王 战神机人',replace(replace('名字带有「机人」的机械族怪兽×5\r\n这张卡在同1次的战斗阶段中可以作2次攻击。这张卡攻击的对方的效果怪兽的效果在伤害计算后无效化。这张卡战斗破坏对方怪兽的场合，给与对方基本分1000分伤害。这张卡不能向对方玩家直接攻击。','\r',char(13)),'\n',char(10)),'效果无效','1000伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54704216,'拷问车轮',replace(replace('以对方场上1只怪兽为对象才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，作为对象的怪兽不能攻击，也不能作表示形式的变更。那只怪兽从场上离开时这张卡破坏。\r\n②：自己准备阶段发动。给与对方500伤害。这个效果在作为对象的怪兽在怪兽区域存在的场合进行发动和处理。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54706054,'测量器人','这张卡的攻击力上升对方场上的超量怪兽的阶级合计×300的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54719828,'No.16 色之支配者',replace(replace('4星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除，宣言卡的种类（怪兽·魔法·陷阱）才能发动。直到下次的对方回合结束时，宣言的种类的卡双方不能发动。','\r',char(13)),'\n',char(10)),'发动限制','请宣言卡的种类','「No.16 色之支配者」宣言怪兽卡','「No.16 色之支配者」宣言魔法卡','「No.16 色之支配者」宣言陷阱卡','','','','','','','','','','','');
INSERT INTO texts VALUES(54747648,'玄武召唤士','①：这张卡反转的场合，以对方场上1只怪兽为对象发动。那只怪兽破坏。','怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54749427,'命运英雄 防御人','对方回合的准备阶段时这张卡表侧守备表示存在的场合，对方玩家抽1张卡。','对方抽卡','','','','','','','','','','','','','','','');
