INSERT INTO texts VALUES(54757758,'沼地的泥龙王',replace(replace('相同属性而种族不同的怪兽×2\r\n①：只要这张卡在怪兽区域存在，对方不能把这张卡以及持有和这张卡相同属性的场上的怪兽作为效果的对象。\r\n②：1回合1次，宣言1个属性才能发动。这张卡直到回合结束时变成宣言的属性。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54762426,'埋藏金的地图','对应将场上的这张卡送回手卡的效果才能发动。从自己卡组抽2张卡，之后丢弃1张手卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54766667,'圣构体','光属性的通常怪兽祭品召唤的场合，这1只怪兽可以作为2只的数量的祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54773234,'禁忌的圣典','双方怪兽进行战斗的伤害计算时才能发动。直到伤害步骤结束时，这张卡以外的场上的卡的效果无效化，那次战斗的伤害计算用原本的攻击力·守备力进行。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54813225,'野鸭战斗机','把自己场上的衍生物那个等级合计直到3以上解放才能发动。这张卡从手卡或者墓地特殊召唤。「野鸭战斗机」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54844990,'死亡潜行者','行动敏捷、用钳子捕捉对方，带有剧毒刺针的蝎子战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54860010,'异虫王子','这张卡战斗破坏对方怪兽的场合，可以从自己卡组把1只名字带有「异虫」的爬虫族怪兽加入手卡。自己场上没有这张卡以外的名字带有「异虫」的爬虫族怪兽存在的场合，结束阶段时这张卡破坏。','检索','自坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54878498,'凯尔柏克','向这张卡攻击的怪兽回到持有者手卡。伤害计算适用。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54895237,'晴天气 锈红',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡在墓地存在的场合，把自己场上1张表侧表示的永续魔法·永续陷阱卡送去墓地才能发动。这张卡守备表示特殊召唤，从手卡选1张「天气」魔法·陷阱卡在自己的魔法与陷阱区域表侧表示放置。\r\n②：场上的这张卡为让「天气」卡的效果发动而被除外的场合，下个回合的准备阶段才能发动。除外的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'墓地的这张卡特殊召唤','除外的这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54903668,'妖仙兽的秘技','①：自己场上有「妖仙兽」卡存在，自己的怪兽区域没有「妖仙兽」怪兽以外的表侧表示怪兽存在的场合，怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。','发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54912977,'魔法神灯',replace(replace('①：自己主要阶段才能发动。这张卡在场上表侧表示存在的场合，从手卡把1只「灯之魔精」特殊召唤。\r\n②：里侧守备表示的这张卡被对方怪兽攻击的伤害计算前，以攻击怪兽以外的1只对方怪兽为对象才能发动。攻击对象转移为那只对方怪兽进行伤害计算。','\r',char(13)),'\n',char(10)),'攻击对象转移','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54913680,'六武之书','把自己场上表侧表示存在的2只名字带有「六武众」的怪兽解放发动。从自己卡组把1只「大将军 紫炎」在自己场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54936498,'影响龙','1回合1次，选择自己场上表侧表示存在的1只怪兽才能发动。选择的怪兽直到结束阶段时变成龙族。','种族变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54941203,'调律之魔术师',replace(replace('「调律之魔术师」的①的效果1回合只能使用1次。\r\n①：这张卡在手卡·墓地存在，自己的灵摆区域有2张「魔术师」卡存在的场合才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。\r\n②：这张卡召唤·特殊召唤成功的场合发动。对方回复400基本分，那之后自己受到400伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54959865,'新空间侠·天空蜂鸟','对方每有1张手卡，自己回复500基本分。这个效果1回合只能使用1次。','回复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54965929,'魔导兽 美杜莎水母',replace(replace('←4 【灵摆】 4→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：另一边的自己的灵摆区域没有卡存在的场合，以自己墓地1只可以放置魔力指示物的怪兽为对象才能发动。这张卡破坏，那只怪兽特殊召唤，给那只怪兽放置1个魔力指示物。\r\n【怪兽效果】\r\n①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。\r\n②：1回合1次，自己·对方的战斗阶段，把自己场上2个魔力指示物取除，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时变成一半。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54974237,'暗之卡组破坏病毒','①：把自己场上1只攻击力2500以上的暗属性怪兽解放，宣言卡的种类（魔法·陷阱）才能发动。对方场上的魔法·陷阱卡，对方手卡，用对方回合计算的3回合内对方抽到的卡全部确认，那之内的宣言种类的卡全部破坏。','魔法卡','陷阱卡','请宣言卡的种类','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54976796,'等级限制A地区','场上表侧表示存在的3星以下怪兽全部变成攻击表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(54977057,'天空的宝札',replace(replace('这张卡发动的回合，自己不能把怪兽特殊召唤，不能进行战斗阶段。\r\n①：从手卡把1只天使族·光属性怪兽除外，自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55001420,'深绿之魔弓使','若自己场上有植物族怪兽存在，则这张卡不能被攻击。每祭掉自己场上1只植物族怪兽，就能破坏场上1张魔法·陷阱卡。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55008284,'同姓同名同盟','选择自己场上1只表侧表示存在的2星以下的通常怪兽发动。从自己卡组尽可能特殊召唤和选择的卡同名的卡上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55010259,'金色零件',replace(replace('「金色零件」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡召唤·特殊召唤成功时才能发动。从手卡把1只机械族·4星怪兽特殊召唤。\r\n②：这张卡被战斗·效果破坏的场合才能发动。从卡组把「金色零件」以外的1只4星的「零件」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55013285,'军队龙','这张卡被战斗破坏送去墓地的场合，从卡组选1张「军队龙」在自己的场上特殊召唤。之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55014050,'水之少女','用水像冰箭般攻击的美丽姐姐。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55034079,'连接死亡炮塔',replace(replace('①：作为这张卡的发动时的效果处理，给这张卡放置4个指示物。\r\n②：每次自己受到战斗伤害给这张卡放置1个指示物。\r\n③：自己主要阶段2，把这张卡1个指示物取除，以自己墓地1只「弹丸」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，从场上离开的场合除外。这个效果发动的回合，自己不是暗属性连接怪兽不能从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55046718,'大日元件','名字带有「大日」的怪兽才能装备。装备怪兽向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。场上表侧表示存在的这张卡被送去墓地的场合，可以从自己卡组把1张名字带有「大日」的魔法卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55063681,'力量供给者','1回合1次，选择自己场上表侧表示存在的1只怪兽才能发动。只要这张卡在自己场上表侧表示存在，选择的怪兽的攻击力上升400。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55063751,'海龟坏兽 加美西耶勒',replace(replace('①：这张卡可以把对方场上1只怪兽解放，从手卡往对方场上攻击表示特殊召唤。\r\n②：对方场上有「坏兽」怪兽存在的场合，这张卡可以从手卡攻击表示特殊召唤。\r\n③：「坏兽」怪兽在自己场上只能有1只表侧表示存在。\r\n④：对方把「海龟坏兽 加美西耶勒」以外的魔法·陷阱·怪兽的效果发动时，把自己·对方场上2个坏兽指示物取除才能发动。那个发动无效并除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55067058,'No.19 冷冻龙',replace(replace('5星怪兽×2\r\n自己的超量怪兽把超量素材取除来让效果发动的场合，取除的1个超量素材可以从这张卡取除。这个效果1回合只能适用1次。','\r',char(13)),'\n',char(10)),'是否要使用「No.19 冷冻龙」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55099248,'自然草莓','对方对怪兽的召唤·特殊召唤成功时，直到这个回合的结束阶段时这张卡的攻击力上升召唤·特殊召唤的1只怪兽的等级×100的数值。这个效果1回合只能使用1次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55100740,'化合兽 氢素鹰',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●丢弃1张手卡，以自己墓地1只二重怪兽为对象才能发动。那只怪兽守备表示特殊召唤。「化合兽 氢素鹰」的这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55106249,'文具电子人006',replace(replace('←1 【灵摆】 1→\r\n自己不是「文具电子人」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n「文具电子人006」的③的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合，以场上1只怪兽为对象才能发动。那只怪兽的表示形式变更。\r\n②：这张卡的攻击力上升自己的额外卡组的表侧表示的「文具电子人」怪兽数量×500。\r\n③：这张卡在灵摆区域被破坏的场合，以自己墓地1张「文具电子人」卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55117418,'能力吸收核心','自己场上有名字带有「核成」的怪兽表侧表示存在，自己墓地有「核成兽的钢核」存在的场合才能发动。效果怪兽的效果的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55119278,'变形斗士·无线电',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：只要这张卡在场上表侧表示存在，自己场上存在的名字带有「变形斗士」的怪兽的攻击力上升800。\r\n●守备表示：只要这张卡在场上表侧表示存在，自己场上存在的名字带有「变形斗士」的怪兽的守备力上升1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55136228,'剑斗兽的底力','自己场上表侧表示存在的1只名字带有「剑斗兽」的怪兽的攻击力直到结束阶段时上升500。可以让自己墓地存在的2张名字带有「剑斗兽」的卡回到卡组，自己墓地存在的这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55144522,'强欲之壶','从自己的卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55154048,'极星宝 德罗普尼尔','名字带有「极神」或者「极星」的怪兽才能装备。装备怪兽的攻击力上升800。场上表侧表示存在的这张卡被卡的效果破坏的场合，可以从自己卡组把1张名字带有「极星宝」的卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55171412,'元素英雄 水波新宇侠',replace(replace('「元素英雄 新宇侠」＋「新空间侠·水波海豚」\r\n把自己场上存在的上记的卡回到卡组的场合才能从额外卡组特殊召唤（不需要「融合」魔法卡）。丢弃1张手卡，随机选择对方的1张手卡破坏。这个效果1回合只能使用1次。结束阶段时这张卡回到额外卡组。','\r',char(13)),'\n',char(10)),'返回卡组','手牌破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55204071,'机关傀儡-梦魇','这张卡可以把自己场上表侧表示存在的1只超量怪兽解放从手卡特殊召唤。这个方法的「机关傀儡-梦魇」的特殊召唤1回合只能有1次。这个方法特殊召唤成功时，可以从自己的手卡·墓地选1只「机关傀儡-梦魇」特殊召唤。此外，这张卡特殊召唤成功的回合，自己不能把名字带有「机关傀儡」的怪兽以外的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55210709,'青玉眼怪','蓝宝石眼睛的野兽。制造幻影，趁敌人混乱的时候进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55226821,'闪电之剑','战士族才可以装备。装备的怪兽的攻击力上升800。场上的全部水属性怪兽的攻击力下降500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55241609,'魔境之圣像骑士',replace(replace('这个卡名的作为①的方法的特殊召唤1回合只能有1次，作为②的效果1回合只能使用1次。\r\n①：这张卡可以从手卡往作为连接怪兽所连接区的自己场上守备表示特殊召唤。\r\n②：这张卡往连接怪兽所连接区的召唤·特殊召唤成功的场合，以自己场上1张「圣像骑士」卡和对方场上1张卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55256016,'阿努比斯的裁决','丢弃1张手卡。使对方所控制的含有破坏场上魔法·陷阱卡效果的魔法卡的发动与效果无效，并且将其破坏。之后，可以破坏对方场上1只表侧表示的怪兽，并对对方造成与被破坏的怪兽攻击力数值相等的伤害。','是否要选择对方场上1只表侧表示的怪兽破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55271628,'暴君的自暴自弃','把自己场上存在的衍生物以外的2只通常怪兽解放发动。只要这张卡在场上存在，双方不能把效果怪兽召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55277252,'魔轰神兽 野槌','自己的主要阶段时才能发动。从手卡丢弃1只名字带有「魔轰神」的怪兽，这张卡从手卡特殊召唤。这个效果特殊召唤成功时，可以从手卡把1只2星以下的名字带有「魔轰神」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55291359,'女夜魔骑士','咏唱起魔法，给予对方血的祭礼的恶魔魔法战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55312487,'圣像骑士的圣战',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：这张卡也能把自己场上1只「圣像骑士」怪兽或者「星遗物」怪兽解放来发动。那个场合，从自己的卡组·墓地选原本卡名和那只怪兽不同的1只「圣像骑士」怪兽或者「星遗物」怪兽特殊召唤。\r\n②：只要自己场上有「圣像骑士」连接怪兽存在，对方只能选择连接怪兽作为攻击对象。','\r',char(13)),'\n',char(10)),'是否解放怪兽并特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55321970,'突风之扇','风属性的怪兽才能装备。装备的怪兽攻击力上升400，守备力下降200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55326322,'水晶机巧-红晶雀',replace(replace('「水晶机巧-红晶雀」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：以自己场上1张表侧表示的卡为对象才能发动。那张卡破坏，从卡组把1只「水晶机巧」调整特殊召唤。这个效果的发动后，直到回合结束时自己不是机械族同调怪兽不能从额外卡组特殊召唤。\r\n②：把墓地的这张卡除外才能发动。在自己场上把1只「水晶机巧衍生物」（机械族·水·1星·攻/守0）特殊召唤。这衍生物不能解放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55326323,'水晶机巧衍生物','由「水晶机巧-红晶雀」的效果特殊召唤。这衍生物不能解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55337339,'转职的魔镜','恶魔镜。受到攻击也不会破裂、守备损伤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55343236,'罪 真红眼黑龙','这张卡不能通常召唤。从自己卡组把1只「真红眼黑龙」从游戏中除外的场合可以特殊召唤。名字带有「罪」的怪兽在场上只能有1只表侧表示存在。只要这张卡表侧表示存在，自己的其他怪兽不能攻击宣言。没有场地魔法卡表侧表示存在的场合这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55348096,'武器库盗贼','对方从卡组里选择1张装备魔法卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55375684,'召唤云','自己场上没有怪兽存在的场合，可以从自己的手卡或者墓地把1只4星以下的名字带有「云魔物」的怪兽特殊召唤。这个效果1回合只有1次在自己的主要阶段才能使用。从墓地特殊召唤的场合这张卡破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55401221,'电池人-单一型','只要这张卡在自己场上表侧表示存在，对方不能选择自己场上存在的「电池人-单一型」以外的雷族怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55410871,'青眼混沌极龙',replace(replace('「混沌形态」降临。这张卡不用仪式召唤不能特殊召唤。\r\n①：这张卡不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的2倍数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55416843,'蛮族的狂宴LV5','①：从自己的手卡·墓地选最多2只战士族·5星怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，这个回合那些怪兽不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55424270,'魔导兽 刻耳柏洛斯',replace(replace('①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。\r\n②：这张卡的攻击力上升这张卡的魔力指示物数量×500。\r\n③：这张卡进行战斗的战斗阶段结束时这张卡的魔力指示物全部取除。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55428242,'阿特拉之虫惑魔','这张卡不受名字带有「洞」或者「落穴」的通常陷阱卡的效果影响。只要这张卡在场上表侧表示存在，自己可以从手卡把名字带有「洞」或者「落穴」的通常陷阱卡发动。此外，只要这张卡在场上表侧表示存在，自己控制的通常陷阱卡的发动和效果不会被无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55428811,'第五幕之希望','选择自己墓地存在的5张名字带有「元素英雄」的卡，加入卡组洗切。之后，从自己卡组抽2张卡。这张卡的发动时自己场上以及手卡没有其他卡存在的场合抽3张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55444629,'下级龙','不怎么强，连吐息攻击都不会的低级龙。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55461064,'命运英雄 匕首人','从手卡丢弃这张卡。自己场上表侧表示存在的名字带有「命运英雄」的怪兽直到这个回合的结束阶段时攻击力上升800。这个效果只能在对方回合的战斗阶段中使用。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55465441,'平等交换','把自己墓地存在的1只怪兽在对方场上守备表示特殊召唤，自己场上表侧表示存在的1只怪兽的等级直到结束阶段时上升那个等级的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55470553,'No.98 绝望皇 霍普勒斯',replace(replace('4星怪兽×2\r\n「No.98 绝望皇 霍普勒斯」的②的效果1回合只能使用1次。\r\n①：自己或者对方的怪兽的攻击宣言时，把这张卡1个超量素材取除才能发动。那只怪兽变成守备表示。\r\n②：这张卡在墓地存在的场合，以场上1只「希望皇 霍普」怪兽为对象才能发动。这张卡守备表示特殊召唤，把作为对象的怪兽在下面重叠作为超量素材。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55488859,'进化虫·桥蜥','这张卡被战斗破坏送去墓地时，可以选择自己墓地存在的1只名字带有「进化龙」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55501446,'光蛇','这张卡从手卡送去墓地的场合，这张卡可以从墓地特殊召唤。这张卡不能作为同调素材。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55550921,'格斗战士 阿提米特','不使用任何武器，空手战斗的格斗战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55553602,'娱乐伙伴戏剧剧场',replace(replace('①：自己场上的怪兽的攻击力上升自己场上的怪兽的种族种类×200。\r\n②：1回合1次，自己场上的「娱乐伙伴」怪兽的种族是4种类的场合才能发动。从自己的手卡·卡组·墓地选1只「异色眼」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55567161,'戏法壶','根据魔术师的命令行动的使魔。不是很强。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55569674,'女神的圣剑-鹰灵',replace(replace('①：装备怪兽的攻击力上升500。\r\n②：这张卡从场上送去墓地时，以自己场上1只「守护者·艾托斯」为对象才能发动。那只怪兽的攻击力上升除外的怪兽数量×500。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55573346,'误爆','①：以自己场上1张卡为对象才能发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55586621,'命运女郎·达琪','这张卡的攻击力·守备力变成这张卡的等级×400的数值。自己的准备阶段时，这张卡的等级上升1星（等级最多12星）。只要这张卡在自己场上表侧表示存在，自己场上表侧表示存在的名字带有「命运女郎」的怪兽战斗破坏对方怪兽送去墓地时，可以选择自己墓地存在的1只名字带有「命运女郎」的怪兽特殊召唤。','等级上升','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55589254,'冰炎之双龙','这张卡不能通常召唤。把自己墓地2只水属性怪兽和1只炎属性怪兽从游戏中除外才能特殊召唤。可以丢弃1张手卡把场上1只怪兽破坏。这个效果1回合只能使用1次。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55599882,'武神决战','选择场上1只名字带有「武神」的怪兽才能发动。这个回合选择的怪兽战斗破坏持有那只怪兽的原本攻击力以上的攻击力的怪兽的场合，破坏的那只怪兽除外，再把那些同名怪兽从对方的手卡·卡组·额外卡组·墓地全部除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55608151,'狮鹫之翼','①：对方把「鹰身女妖的羽毛扫」发动时才能发动。那个效果无效，对方场上的魔法·陷阱卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55610595,'黑羽-上弦之灭弓鸟',replace(replace('「黑羽-上弦之灭弓鸟」的效果1回合只能使用1次。把这张卡作为同调素材的场合，不是「黑羽」怪兽的同调召唤不能使用。\r\n①：这张卡从场上送去墓地的回合的结束阶段才能发动。从卡组把「黑羽-上弦之灭弓鸟」以外的1只「黑羽」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55615891,'元素英雄 荒野翼侠',replace(replace('「元素英雄 荒野侠」＋「元素英雄 羽翼侠」\r\n这只怪兽不能作融合召唤以外的特殊召唤。可以丢弃1张手卡，把场上的1张魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55623480,'妖精传姬-白雪',replace(replace('①：这张卡召唤·特殊召唤成功的场合，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示。\r\n②：这张卡在墓地存在的场合，从自己的手卡·场上·墓地把这张卡以外的7张卡除外才能发动。这张卡从墓地特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'变成里侧守备表示','这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55624610,'废铁猎人','1回合1次，可以选择这张卡以外的自己场上表侧表示存在的1只名字带有「废铁」的怪兽破坏，从自己卡组把1只调整送去墓地。','破坏并送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55673611,'念力触发器','自己基本分比对方低的场合才能发动。把自己墓地存在的2只念动力族怪兽从游戏中除外，从自己卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55690251,'堕天使 迪赛尔','这张卡不能特殊召唤。这张卡可以把1只天使族怪兽解放作上级召唤。1回合1次，自己的主要阶段时可以把这张卡的攻击力下降1000，对方场上存在的1只怪兽送去墓地。','用1只天使族怪兽解放作上级召唤','对方场上存在的1只怪兽送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55691901,'大嘴','拥有可以吞下一切的大嘴。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55696885,'疫病狼',replace(replace('①：1回合1次，自己主要阶段才能发动。这张卡的攻击力直到回合结束时变成原本攻击力的2倍。\r\n②：这张卡的①的效果发动的场合，结束阶段发动。这张卡破坏。','\r',char(13)),'\n',char(10)),'攻击上升','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55705473,'狒狒面包树',replace(replace('①：这张卡召唤·特殊召唤成功的场合才能发动。自己从卡组抽1张，那之后选1张手卡回到卡组最上面或者最下面。\r\n②：这张卡被战斗·效果破坏的场合才能发动。从卡组把「狒狒面包树」任意数量特殊召唤。','\r',char(13)),'\n',char(10)),'抽卡','回到卡组最上面','回到卡组最下面','特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(55713623,'收缩','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽的原本攻击力直到回合结束时变成一半。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55727845,'No.96 黑雾',replace(replace('2星怪兽×3\r\n这张卡和对方怪兽进行战斗的攻击宣言时1次，把这张卡1个超量素材取除才能发动。那只对方怪兽的攻击力变成一半，这张卡的攻击力上升那个数值。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55735315,'魂食神龙 吸灵龙','这张卡不能通常召唤。自己的龙族超量怪兽的效果才能特殊召唤。这张卡特殊召唤成功时，自己基本分比对方少的场合，这张卡的攻击力上升那个相差数值，这个回合对方玩家受到的全部伤害变成0。此外，这张卡不能向对方玩家直接攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55737443,'守护神 艾克佐德','这张卡不能通常召唤。把自己场上存在的1只名字带有「斯芬克斯」的怪兽作为祭品的场合才能特殊召唤。只要这张卡在场上表侧表示存在，每次地属性怪兽反转召唤成功，给与对方基本分1000分的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55742055,'圆桌的圣骑士',replace(replace('①：自己结束阶段，自己的场上·墓地的「圣骑士」卡种类的以下效果各能发动1次。\r\n●3种类以上：从卡组把1张「圣骑士」卡送去墓地。\r\n●6种类以上：从手卡把1只「圣骑士」怪兽特殊召唤。那之后，可以从手卡把1张「圣剑」装备魔法卡给那只怪兽装备。\r\n●9种类以上：以自己墓地1只「圣骑士」怪兽为对象才能发动。那只怪兽加入手卡。\r\n●12种类：自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'从卡组把1张「圣骑士」卡送去墓地','从手卡把1只「圣骑士」怪兽特殊召唤','选择自己墓地1只「圣骑士」怪兽加入手卡','抽1张卡','是否把装备魔法卡给那只怪兽装备？','','','','','','','','','','','');
INSERT INTO texts VALUES(55758589,'光子翼龙','①：这张卡召唤·反转召唤成功的场合发动。对方场上盖放的卡全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55761792,'混沌的仪式',replace(replace('「混沌战士」的降临必需。\r\n①：从自己的手卡·场上把等级合计直到8以上的怪兽解放，从手卡把「混沌战士」仪式召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55763552,'壶魔人','反转：场上表侧表示的「龙族封印之壶」破坏。破坏时，场上表侧表示存在的龙族怪兽全部攻击表示。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55773067,'打落','对方抽卡阶段时发动。对方的抽卡阶段抽的1张卡丢弃送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55784832,'莫林芬','以长臂和钩爪为特征有着奇妙样貌的恶魔。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55787576,'星遗物-『星盾』',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：这张卡只要在怪兽区域存在，不受从额外卡组特殊召唤的怪兽发动的效果影响。\r\n②：和这张卡相同纵列的自己的「星遗物」卡不会成为对方的效果的对象，不会被对方的效果破坏。\r\n③：这张卡在墓地存在的场合，自己·对方的准备阶段支付1000基本分才能发动。这张卡从墓地特殊召唤。那之后，对方可以从自身的手卡·墓地选1只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','是否从手卡·墓地选1只怪兽特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55794644,'主宰者·许珀里翁','这张卡可以把自己的手卡·场上·墓地存在的1只名字带有「代行者」的怪兽从游戏中除外，从手卡特殊召唤。1回合1次，可以把自己墓地存在的1只天使族·光属性怪兽从游戏中除外，选择场上存在的1张卡破坏。场上有「天空的圣域」表侧表示存在的场合，这个效果1回合可以使用最多2次。','场上存在的1张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55818463,'死王 巫妖大君',replace(replace('这张卡上级召唤的场合，解放的怪兽必须是暗属性怪兽。\r\n①：这张卡被效果解放送去墓地的场合发动。墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55821894,'亚马逊格斗战士','这张卡进行战斗时，这张卡的控制者所受的战斗伤害为零。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55824220,'辉石融合','从手卡·自己场上把融合怪兽卡决定的融合素材怪兽送去墓地，把名字带有「宝石骑士」的那1只融合怪兽当作融合召唤从额外卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55863245,'龙子',replace(replace('调整＋调整以外的怪兽1只以上\r\n同调召唤的这张卡为素材作同调召唤的场合，手卡1只怪兽也能作为同调素材。\r\n①：只要这张卡在怪兽区域存在，这张卡不受其他怪兽的效果影响。','\r',char(13)),'\n',char(10)),'是否使用手卡1只怪兽作为同调素材？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55870497,'燃烧的竹光','①：这张卡已在魔法与陷阱区域存在的状态，自己把「竹光」卡发动的场合才能发动。下次的对方主要阶段1跳过。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55875323,'电气蜥蜴','不死族以外的怪兽攻击这张卡的场合，那只怪兽下一个回合不能攻击宣言。','攻击限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55885348,'星际仙踪-达克大弯刀号',replace(replace('①：这张卡召唤·特殊召唤成功的场合，以场上1只怪兽为对象才能发动。那只怪兽破坏。\r\n②：这张卡不会成为对方的效果的对象。\r\n③：这张卡被战斗·效果破坏送去墓地的场合，把墓地的这张卡除外才能发动。从卡组把1只7星以下的「星际仙踪」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'破坏怪兽','从卡组把「星际仙踪」怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55888045,'混沌No.106 熔岩掌 巨手·红掌',replace(replace('5星怪兽×3\r\n这张卡有名字带有「No.」的怪兽在作为超量素材的场合，得到以下效果。\r\n●1回合1次，魔法·陷阱·效果怪兽的效果在场上发动时发动。把这张卡1个超量素材取除，这张卡以外的场上表侧表示存在的全部卡的效果直到回合结束时无效。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55935416,'No.56 金鼠',replace(replace('1星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除才能发动。从自己卡组抽1张卡，那之后1张手卡回到卡组。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55948544,'法老的审判',replace(replace('支付一半基本分，选择下面1个效果发动：\r\n●自己墓地存在「友情 YU-JYO」，对方回合终了前怪兽不能盖伏·召唤·反转召唤·特殊召唤·发动效果怪兽的效果。\r\n●自己墓地存在「团结 UNITY」，回合终了前对方场上的魔法·陷阱效果无效，并且不能发动·盖伏，魔法·陷阱卡。','\r',char(13)),'\n',char(10)),'对方回合终了前怪兽不能盖伏·召唤·反转召唤·特殊召唤·发动效果怪兽的效果','回合终了前对方场上的魔法·陷阱效果无效，并且不能发动·盖伏，魔法·陷阱卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55969226,'青龙召唤士','①：这张卡从场上送去墓地的场合才能发动。从卡组把1只龙族·战士族·魔法师族的通常怪兽加入手卡。','卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55982698,'正义盟军 破反车','场上表侧表示光属性怪兽存在的场合，这张卡破坏。这张卡向里侧守备表示怪兽攻击的场合，不进行伤害计算以里侧守备表示的状态把那只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55985014,'奇迹小子','直到结束阶段前，对方的1只怪兽的攻击力下降自己墓地存在的「英雄小子」的数量×400的数值。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55991637,'火龙的火炎弹',replace(replace('自己场上有龙族怪兽表侧表示存在的场合，从下面效果选择1个发动：\r\n●给与对方基本分800分伤害。\r\n●选择场上表侧表示存在的1只守备力800以下的怪兽破坏。','\r',char(13)),'\n',char(10)),'给予对方基本分800分伤害','选择场上表侧表示存在的1只守备力800以下的怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55997110,'娱乐伙伴 俊侍狮虎',replace(replace('「娱乐伙伴 俊侍狮虎」的效果1回合只能使用1次。\r\n①：这张卡战斗破坏对方怪兽送去墓地时才能发动。从卡组把1只5星以上的灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(55998462,'金属鱼','金属鱼，用刀一样锋利的尾巴切开对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56043446,'万力魔神 死亡拷问机',replace(replace('①：这张卡的召唤成功的场合，以对方场上1只怪兽为对象发动。发动后，第3次的自己准备阶段把作为对象的怪兽破坏。\r\n②：只要这张卡的①的效果作为对象的怪兽在场上存在，这张卡不会被战斗破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56043447,'万力魔神 死亡拷问机',replace(replace('①：这张卡的召唤成功的场合，以对方场上1只怪兽为对象发动。发动后，第3次的自己准备阶段把作为对象的怪兽破坏。\r\n②：只要这张卡的①的效果作为对象的怪兽在场上存在，这张卡不会被战斗破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56049970,'水晶机巧-绿晶龟',replace(replace('「水晶机巧-绿晶龟」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：以自己场上1张表侧表示的卡为对象才能发动。那张卡破坏，从卡组把1只「水晶机巧」调整特殊召唤。这个效果的发动后，直到回合结束时自己不是机械族同调怪兽不能从额外卡组特殊召唤。\r\n②：把墓地的这张卡除外才能发动。从手卡把1只「水晶机巧」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56051086,'No.43 魂魄傀儡鬼 灵魂傀儡师',replace(replace('暗属性2星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除才能发动。选择自己墓地1只名字带有「No.」的怪兽当作装备卡使用给这张卡装备。只要这张卡有名字带有「No.」的卡装备，这张卡不会被战斗以及卡的效果破坏。此外，1回合1次，自己基本分回复时才能发动。这张卡的攻击力上升那个数值，给与对方基本分那个数值的伤害。','\r',char(13)),'\n',char(10)),'装备','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56051648,'蜘蛛卵','对方宣言直接攻击时，自己墓地昆虫族怪兽有3只以上存在的场合才能发动。把那只怪兽的攻击无效，在自己场上把3只「蜘蛛衍生物」（昆虫族·地·1星·攻/守100）攻击表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56051649,'蜘蛛衍生物','由「蜘蛛卵」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56052205,'贯青蛙','这张卡可以直接攻击对方玩家。这张卡直接攻击成功时，自己场上有「贯青蛙」以外的名字带有「青蛙」的怪兽存在的场合，可以把对方场上1张魔法·陷阱卡破坏。','把对方场上1张魔法·陷阱卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56058888,'王宫的陷落','对方发动永续陷阱卡时才能发动。那张卡的发动和效果无效，并且破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56074358,'变形斗士·场地','每次怪兽的表示形式改变，给这张卡放置1个变形斗士指示物。场上表侧表示存在的名字带有「变形斗士」的怪兽的攻击力上升这张卡放置的变形斗士指示物数量×300的数值。场上存在的这张卡被破坏送去墓地时，可以把自己墓地存在的1只名字带有「变形斗士」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56094445,'古代的机械士兵','①：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56105047,'隆隆隆幽灵','这张卡特殊召唤成功的场合，可以选择自己墓地1只「隆隆隆石人」表侧守备表示特殊召唤。那之后，这张卡变成守备表示。「隆隆隆幽灵」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56111151,'京塔 海滨地区',replace(replace('①：每次场上的卡被送去墓地，每有1张给这张卡放置1个坏兽指示物（最多5个）。\r\n②：1回合1次，这张卡的坏兽指示物是3个以上的场合才能发动。自己从卡组把1只「坏兽」怪兽加入手卡。\r\n③：这张卡被效果破坏的场合，可以作为代替把这张卡1个坏兽指示物取除。','\r',char(13)),'\n',char(10)),'是否取除「京塔 海滨地区」的指示物代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56119752,'礼物卡',replace(replace('「礼物卡」在1回合只能发动1张。\r\n①：对方把手卡全部丢弃。那之后，对方从卡组抽5张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56120475,'炸裂装甲','①：对方怪兽的攻击宣言时，以1只攻击怪兽为对象才能发动。那只攻击怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56132807,'苹果魔术少女',replace(replace('①：1回合1次，这张卡被选择作为攻击对象的场合才能发动。从手卡把1只5星以下的魔法师族怪兽特殊召唤。那之后，攻击对象转移为那只怪兽，攻击怪兽的攻击力变成一半。\r\n②：这张卡被战斗·效果破坏的场合，以这张卡以外的自己墓地最多3只「魔术少女」怪兽为对象才能发动（同名卡最多1张）。那些卡加入手卡。','\r',char(13)),'\n',char(10)),'是否继续选择怪兽加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56166150,'暮光道萨满 露米娜丝',replace(replace('①：1回合1次，从自己的手卡·墓地把1只「光道」怪兽除外，以「暮光道萨满 露米娜丝」以外的除外的1只自己的「光道」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：1回合1次，这张卡以外的自己的「光道」怪兽的效果发动的场合发动。从自己卡组上面把3张卡送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56174248,'魔导鬼士 迪亚勒','这张卡在墓地存在的场合，把自己墓地3张名字带有「魔导书」的魔法卡从游戏中除外才能发动。这张卡从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56209279,'永火死灵师','这张卡召唤成功时，变成守备表示。自己手卡是0张的场合，得到以下效果。1回合1次，可以把自己墓地存在的「永火死灵师」以外的1只名字带有「永火」的怪兽特殊召唤。','变成守备表示','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56223084,'针弹翻车鱼','这张卡被送去墓地时，选择对方场上表侧表示存在的1只怪兽发动。选择的对方怪兽的攻击力下降500。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56240989,'进化虫·奶酪湾蜥','这张卡战斗破坏对方怪兽的战斗阶段结束时，把这张卡解放才能发动。从卡组把2只恐龙族·炎属性·6星以下的同名怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，结束阶段时从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56246017,'恶魔的雄叫','支付500基本分才能发动。从自己的墓地里特殊召唤1只名称中含有「恶魔」字样的怪兽上场。此怪兽不可作为任何情况下的祭品。本回合的结束阶段时，此怪兽被破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56252810,'再爆裂','把自己墓地存在的1张「爆裂模式」从游戏中除外发动。自己场上存在的怪兽全部破坏，自己墓地存在的1只名字带有「/爆裂体」的怪兽无视召唤条件特殊召唤。这个效果特殊召唤的怪兽的效果无效化，也不能解放，从场上离开的场合从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56256517,'预见存折',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：从自己卡组上面把3张卡里侧表示除外。这张卡的发动后第3次的自己准备阶段，这个效果除外的3张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56260110,'雷鸣','对方受到300分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56283725,'蜘蛛男','巨大的蜘蛛被赋予智慧的形态，通过吐丝将敌人的行动封锁。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56286179,'钻头同调士',replace(replace('①：只要这张卡在怪兽区域存在，自己的战士族怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n②：1回合1次，这张卡的①的效果适用给与对方战斗伤害时才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56292140,'No.51 怪腕之必杀摔角手',replace(replace('3星怪兽×3\r\n①：这张卡不会被战斗破坏。\r\n②：这张卡进行战斗的伤害步骤结束时，把这张卡1个超量素材取除才能发动。给这张卡放置1个指示物（最多3个）。\r\n③：这张卡进行战斗的战斗阶段结束时，这张卡有3个指示物放置的场合才能发动。对方场上的卡全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56321639,'魔导书廊 埃图瓦勒','只要这张卡在场上存在，每次自己或者对方把名字带有「魔导书」的魔法卡发动，给这张卡放置1个魔力指示物。自己场上的魔法师族怪兽的攻击力上升这张卡放置的魔力指示物数量×100的数值。此外，有魔力指示物放置的这张卡被破坏送去墓地时，可以把持有这张卡放置的魔力指示物数量以下的等级的1只魔法师族怪兽从卡组加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56339050,'地缚神的咆哮','对方怪兽的攻击宣言时，攻击怪兽的攻击力比自己场上表侧表示存在的名字带有「地缚神」的怪兽的攻击力低的场合，把那1只攻击怪兽破坏，给与对方基本分破坏怪兽的攻击力一半数值的伤害。这个效果1回合只能使用1次。','破坏并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56342351,'磁力战士1号','擅长配合的战士，发动强大的磁力，谁也逃不掉。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56343672,'救援雪貂',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：让场上的这张卡回到持有者卡组才能发动。等级合计直到变成6星为止从卡组选「救援雪貂」以外的怪兽任意数量在作为连接怪兽所连接区的自己场上特殊召唤。这个效果特殊召唤的怪兽的效果无效化，结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56346071,'图腾五星','①：包含这张卡的5只怪兽同时特殊召唤成功的场合发动。对方场上的卡全部破坏，给与对方破坏数量×500伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56350972,'龙姬神 萨菲拉',replace(replace('「祝祷的圣歌」降临。「龙姬神 萨菲拉」的效果1回合只能使用1次。\r\n①：这张卡仪式召唤的回合的结束阶段以及这张卡在怪兽区域存在并从手卡·卡组有光属性怪兽被送去墓地的回合的结束阶段，可以从以下效果选择1个发动。\r\n●自己从卡组抽2张。那之后，丢弃1张手卡。\r\n●对方手卡随机选1张丢弃去墓地。\r\n●选自己墓地1只光属性怪兽加入手卡。','\r',char(13)),'\n',char(10)),'选择效果发动','自己从卡组抽2张。那之后，丢弃1张手卡','对方手卡随机选1张丢弃去墓地','选自己墓地1只光属性怪兽加入手卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(56364287,'电子龙芯',replace(replace('这个卡名的②③的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡的卡名只要在场上·墓地存在当作「电子龙」使用。\r\n②：这张卡特殊召唤成功的场合才能发动。这张卡的等级直到回合结束时变成5星。这个效果的发动后，直到回合结束时自己不是机械族怪兽不能特殊召唤。\r\n③：这张卡被送去墓地的场合才能发动。从自己的卡组·墓地选这张卡以外的1只「电子龙」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56369281,'贝奥武夫狼','一旦开始战斗，在没分出胜负之前就不会停止战斗的狂战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56387350,'吸血鬼婴儿','这张卡战斗破坏怪兽送去墓地的场合，战斗阶段结束时可以把那只怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56399890,'魔轰神兽 凯希','这张卡从手卡丢弃去墓地时，场上表侧表示存在的1张卡破坏。','表侧表示存在的1张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56410040,'废品前锋','自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56413937,'战场的死装束','「音女」＋「斩首的美女」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56421754,'超级运动员 猛力强击手',replace(replace('「超级运动员 猛力强击手」的①的方法的特殊召唤1回合只能有1次。\r\n①：这张卡可以让「超级运动员 猛力强击手」以外的自己场上1只「超级运动员」怪兽回到手卡，从手卡特殊召唤。\r\n②：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱·怪兽的效果不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56427559,'机关傀儡-齿轮变速者','这张卡不能从卡组特殊召唤。1回合1次，选择这张卡以外的自己场上1只名字带有「机关傀儡」的怪兽才能发动。这张卡的等级变成和选择的怪兽的等级相同。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56433456,'天空的圣域','①：只要这张卡在场地区域存在，天使族怪兽的战斗发生的对那控制者的战斗伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56460688,'异次元隔离机','从自己和对方场上各选择1只怪兽，从游戏中除外。这张卡被破坏并送去墓地时，被除外怪兽以相同的表示形式回到原本所在的场上。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56511382,'盟军·次世代火炮兵','把自己场上表侧表示存在的1只名字带有「次世代」的炎属性怪兽送去墓地发动。选择对方场上表侧表示存在的1只怪兽破坏，给与对方基本分破坏的怪兽的等级×400的数值的伤害。这个效果1回合只能使用1次。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56514812,'女忍战士','场上表侧表示存在的这张卡的控制权转移时，控制者把手卡随机丢弃1张。','丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56524813,'逆卷之艾莉娅','可以把自己场上除这张卡以外的1只水属性怪兽做祭品，从手卡特殊召唤1只水属性怪兽。这个效果1回合只能使用1次。这个效果特殊召唤的怪兽，在「逆卷之艾莉娅」从自己场上离开的场合破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56526564,'绝缘的落穴','①：连接怪兽连接召唤成功时才能发动。场上的不在连接状态的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56532353,'真青眼究极龙',replace(replace('「青眼白龙」＋「青眼白龙」＋「青眼白龙」\r\n「真青眼究极龙」的①的效果1回合可以使用最多2次。\r\n①：融合召唤的这张卡攻击的伤害步骤结束时，自己场上的表侧表示的卡只有这张卡的场合，从额外卡组把1只「青眼」融合怪兽送去墓地才能发动。这张卡可以继续攻击。\r\n②：自己场上的「青眼」怪兽为对象的魔法·陷阱·怪兽的效果发动时，把墓地的这张卡除外才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56532632,'威风堂堂','战斗阶段中才能发动。对方发动的效果怪兽的效果无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56535497,'狂怒','1回合1次，选择场上表侧表示存在的2只兽族怪兽才能发动。直到那个回合的结束阶段时，选择的1只怪兽的攻击力变成一半，另1只怪兽的攻击力上升那个数值。这个效果在自己的主要阶段时以及对方的战斗阶段时才能发动。','是否现在使用「狂怒」的效果？','攻击变化','请选择攻击力变成一半的怪兽','请选择攻击力上升的怪兽','','','','','','','','','','','','');
INSERT INTO texts VALUES(56562619,'黑龙忍者',replace(replace('这张卡不用「忍者」怪兽或者「忍法」卡的效果不能特殊召唤。\r\n①：1回合1次，从手卡以及自己场上的表侧表示的卡之中把1只「忍者」怪兽和1张「忍法」卡送去墓地，以场上1只怪兽为对象才能发动。那只怪兽除外。这个效果在对方回合也能发动。\r\n②：表侧表示的这张卡从场上离开的场合发动。这张卡的效果除外的怪兽尽可能在原本持有者的场上特殊召唤。','\r',char(13)),'\n',char(10)),'卡片除外','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56570271,'命运英雄 圆盘人',replace(replace('这个卡名的效果在决斗中只能使用1次，这张卡在送去墓地的那个回合不能从墓地特殊召唤。\r\n①：这张卡从墓地的特殊召唤成功的场合才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56574543,'武神器-都牟刈',replace(replace('「武神器-都牟刈」的效果1回合只能使用1次。\r\n①：自己的兽战士族的「武神」怪兽和对方怪兽进行战斗的从伤害步骤开始时到伤害计算前，把墓地的这张卡除外才能发动。那只进行战斗的自己怪兽的攻击力直到伤害步骤结束时上升进行战斗的对方怪兽的攻击力数值，那次战斗给与对方的战斗伤害变成一半。','\r',char(13)),'\n',char(10)),'攻击力上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56585883,'鹰身竖琴师',replace(replace('「鹰身竖琴师」的②③的效果1回合各能使用1次。\r\n①：这张卡的卡名只要在场上·墓地存在当作「鹰身女郎」使用。\r\n②：这张卡召唤成功时，以这张卡以外的自己场上1只鸟兽族怪兽和对方场上1只表侧表示怪兽为对象才能发动。那些怪兽回到持有者手卡。\r\n③：这张卡被送去墓地的回合的结束阶段才能发动。从卡组把1只攻击力1500以下的鸟兽族·4星怪兽加入手卡。','\r',char(13)),'\n',char(10)),'弹回手卡','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56594520,'大地力量','全部的地属性的怪兽攻击力上升500，守备力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56597272,'云魔物-羊云','这张卡被战斗破坏送去墓地时，在自己场上把2只「云魔物衍生物」（天使族·水·1星·攻/守0）守备表示特殊召唤。这些衍生物不能作为名字带有「云魔物」的卡以外的祭品召唤的祭品。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56597273,'云魔物衍生物','由「云魔物-羊云」的效果特殊召唤。这个衍生物不能作为名字带有「云魔物」的卡以外的祭品召唤的祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56605802,'幻影蟋蟀','反转：场上里侧表示存在的1只怪兽回到持有者卡组最上面。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56606928,'永远的渴水','场上表侧表示存在的鱼族怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56611470,'古遗物的解放','选择自己场上2只名字带有「古遗物」的怪兽才能发动。只用选择的2只怪兽为素材把1只超量怪兽超量召唤。这张卡发动过的回合，名字带有「古遗物」的怪兽以外的自己场上的怪兽不能攻击。此外，这张卡被对方破坏的场合，把手卡1只光属性·5星怪兽给对方观看才能发动。从卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56619314,'DDD 霸龙王 潘德拉刚',replace(replace('①：这张卡在手卡的场合，自己主要阶段从自己的手卡·场上把龙族怪兽和恶魔族怪兽各1只解放才能发动。这张卡从手卡特殊召唤。\r\n②：1回合1次，自己主要阶段丢弃1张手卡才能发动。这张卡的攻击力直到回合结束时上升500。那之后，可以选场上1张魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','攻击上升','是否选场上1张魔法·陷阱卡破坏？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56638325,'星辉士 夏三角',replace(replace('4星怪兽×3\r\n①：只要持有超量素材的这张卡在怪兽区域存在，在自己对怪兽的召唤·特殊召唤成功时，对方不能把魔法·陷阱·怪兽的效果发动。\r\n②：1回合1次，把这张卡1个超量素材取除，以场上1张卡为对象才能发动。那张卡破坏。\r\n③：这张卡从场上送去墓地的场合才能发动。从手卡·卡组把1只「星守」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'卡片破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56641453,'茧状衣','把自己场上表侧表示存在的1只名字带有「茧状体」的怪兽作为祭品才能发动。这个回合效果的对玩家的伤害变成0。之后，作为祭品的名字带有「茧状体」的怪兽记述的1只名字带有「新空间侠」的怪兽从手卡·卡组·墓地特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56647086,'暗黑之侵略者','只要这张卡在自己场上以表侧表示存在，对方不能发动速攻魔法卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56649609,'幻煌龙 螺旋','经历了炽烈战涡的猛龙。其负伤之躯接触古老光芒，最终完成自身的浸涡。不久，巨龙展开自己的双翅，变为称霸天涡的煌者。那位新生的煌，究竟是梦是真还是幻。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56655675,'圣灵兽骑 地火狮',replace(replace('「圣灵兽骑」怪兽＋「灵兽使」怪兽＋「精灵兽」怪兽\r\n把自己场上的上记卡除外的场合才能特殊召唤（不需要「融合」）。\r\n①：上记的方法特殊召唤的这张卡得到以下效果。\r\n●怪兽的效果·魔法·陷阱卡发动时，从手卡把1张「灵兽」卡除外才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56673480,'上千主上的契约',replace(replace('「上千主上的契约」在1回合只能发动1张。\r\n①：作为这张卡的发动时的效果处理，双方玩家失去1000基本分，各自从卡组抽1张。\r\n②：双方玩家把这张卡在魔法与陷阱区域存在期间抽到的卡以及这张卡的①的效果抽到的卡持续公开。\r\n③：这张卡的效果让手卡的魔法卡公开中的玩家不能把怪兽通常召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56675280,'娱乐伙伴 合唱鹦鹉',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，另一边的自己的灵摆区域有「娱乐伙伴 合唱鹦鹉」以外的「娱乐伙伴」卡、「魔术师」卡、「异色眼」卡之内任意种存在的场合才能发动。这张卡的灵摆刻度直到回合结束时变成7。\r\n【怪兽效果】\r\n①：这张卡被战斗破坏时才能发动。从卡组把灵摆怪兽以外的1只「娱乐伙伴」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56681873,'魔装战士 地虎','①：这张卡召唤成功时才能发动。从手卡把1只4星以下的通常怪兽守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56704140,'冰结界的风水师','丢弃1张手卡，宣言1个属性发动。宣言属性的怪兽不能选择这张卡作为攻击对象。这个效果只要这张卡在场上表侧表示存在只能使用1次。','不能成为攻击对象','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56713552,'邪恶老鼠','任何东西都咬的野老鼠，行仪极坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56746202,'废铁奇美拉','这张卡召唤成功时，可以选择自己墓地存在的1只名字带有「废铁」的调整特殊召唤。把这张卡作为同调素材的场合，不是名字带有「废铁」的怪兽的同调召唤不能使用，其他的同调素材怪兽必须全部是名字带有「废铁」的怪兽。','特殊召唤','是否使用「废铁奇美拉」作同调素材？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56747793,'团结之力','①：装备怪兽的攻击力·守备力上升自己场上的表侧表示怪兽数量×800。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56768355,'阿尔法大日',replace(replace('名字带有「大日」的调整＋调整以外的怪兽1只以上\r\n这张卡同调召唤成功时，可以选择自己墓地存在的1张装备魔法卡给这张卡装备。有装备卡装备的这张卡不会被装备卡以外的魔法·陷阱卡的效果破坏。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56769674,'DNA移植手术','这张卡发动时，宣言1种属性。这张卡在场上存在时，场上所有表侧表示的怪兽全部变成被宣言的属性。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56784842,'天使O7','这张卡生祭召唤的时候，可以获得以下的效果。这张卡表侧表示存在场上，效果怪兽的效果不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56789759,'红龙','喷射炎球将四周化为火海的真红之龙。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56804361,'魔装圣龙 以太神兵龙',replace(replace('「魔装圣龙 以太神兵龙」的效果1回合只能使用1次。\r\n①：这张卡灵摆召唤成功时，以场上1张卡为对象才能发动。那张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'回到手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56809158,'红莲之机界骑士',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：相同纵列有卡2张以上存在的场合，这张卡可以在那个纵列的自己场上特殊召唤。\r\n②：从自己墓地把1只「机界骑士」怪兽除外，以和这张卡相同纵列1只表侧表示怪兽为对象才能发动。那只怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56827051,'影灵衣巫女 艾莉娅儿',replace(replace('「影灵衣巫女 艾莉娅儿」的②的效果1回合只能使用1次。\r\n①：1回合1次，把手卡的「影灵衣」卡任意数量给对方观看才能发动。直到回合结束时，这张卡的等级上升或者下降给人观看的卡数量的数值。\r\n②：这张卡被效果解放的场合才能发动。从卡组把仪式怪兽以外的1只「影灵衣」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'等级变更','加入手卡','等级上升','等级下降','','','','','','','','','','','','');
INSERT INTO texts VALUES(56830749,'分担痛苦','自己场上的1只怪兽作祭品。对方选择对方场上1只怪做祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56832966,'闪光No.39 希望皇 霍普·电光皇',replace(replace('光属性5星怪兽×3\r\n这张卡也能在自己场上的4阶的「希望皇 霍普」怪兽上面重叠来超量召唤。这张卡不能作为超量召唤的素材。\r\n①：这张卡进行战斗的场合，对方直到伤害步骤结束时卡的效果不能发动。\r\n②：这张卡有「希望皇 霍普」怪兽在作为超量素材的场合，这张卡和对方怪兽进行战斗的伤害计算时，把这张卡2个超量素材取除才能发动。这张卡的攻击力只在那次伤害计算时变成5000。','\r',char(13)),'\n',char(10)),'是否在4阶的「希望皇 霍普」怪兽上面重叠来超量召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56839613,'电池人-纽扣型','反转：从自己卡组把1只「电池人-纽扣型」以外的4星以下的名字带有「电池人」的怪兽特殊召唤。此外，反转过的这张卡被战斗破坏送去墓地时，从自己卡组抽1张卡。','特殊召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56840427,'混沌No.39 希望皇 霍普雷',replace(replace('光属性4星怪兽×3\r\n这张卡也能在自己场上的「No.39 希望皇霍普」上面重叠来超量召唤。\r\n①：把这张卡1个超量素材取除才能发动。直到回合结束时这张卡的攻击力上升500，选对方场上1只怪兽那个攻击力直到回合结束时下降1000。这个效果在自己基本分是1000以下的场合才能发动和处理。','\r',char(13)),'\n',char(10)),'攻击变化','是否在「No.39 希望皇霍普」上面重叠超量召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56840658,'抽卡青蛙','场上表侧表示的这张卡被送去墓地时，可以从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56856951,'流星日珥','对方基本分比3000高的场合，把2张手卡送去墓地才能发动。给与对方基本分2000分伤害。这张卡在墓地存在的场合，可以作为自己的抽卡阶段时进行通常抽卡的代替，把这张卡加入手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56897896,'一星同调士','把这张卡作为同调素材的场合，其他的同调素材怪兽必须是4星以下的战士族·机械族怪兽，那些等级当作1星使用。','','','','','','','','','','','','','','','','');
