INSERT INTO texts VALUES(64681432,'破坏龙 甘多拉',replace(replace('这张卡不能特殊召唤。\r\n①：把基本分支付一半才能发动。这张卡以外的场上的卡全部破坏并除外。这张卡的攻击力上升这个效果破坏的卡数量×300。\r\n②：这张卡召唤·反转召唤的回合的结束阶段发动。这张卡送去墓地。','\r',char(13)),'\n',char(10)),'破坏','送去墓地','','','','','','','','','','','','','','');
