INSERT INTO texts VALUES(64990807,'冰结界的三方阵','把手卡的名字带有「冰结界」的怪兽3种类给对方观看，选择对方场上存在的1张卡发动。选择的对方的卡破坏，从自己手卡把1只名字带有「冰结界」的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65017789,'雪天气 天蓝',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从卡组选1张「天气」魔法·陷阱卡在自己的魔法与陷阱区域表侧表示放置。\r\n②：场上的这张卡为让「天气」卡的效果发动而被除外的场合，下个回合的准备阶段才能发动。除外的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'从卡组放置「天气」魔法·陷阱卡','除外的这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65025250,'妖仙兽 左镰神柱',replace(replace('←3 【灵摆】 3→\r\n①：自己场上的「妖仙兽」怪兽被战斗·效果破坏的场合，可以作为代替把这张卡破坏。\r\n【怪兽效果】\r\n①：这张卡召唤成功的场合发动。这张卡变成守备表示。\r\n②：只要这张卡在怪兽区域存在，对方不能把这张卡以外的自己场上的「妖仙兽」怪兽作为效果的对象。','\r',char(13)),'\n',char(10)),'变成守备表示','是否使用「妖仙兽 左镰神柱」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65026212,'核成巨龙','这张卡不能通常召唤。从自己手卡把1张「核成兽的钢核」从游戏中除外的场合可以特殊召唤。这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」或1只名字带有「核成」的怪兽送去墓地。或者都不进行让这张卡破坏。1回合1次，自己的主要阶段时可以选择对方场上存在的1张卡破坏。','选择一张「核成兽的钢核」送去墓地','选择一张名字带有「核成」的怪兽送去墓地','破坏「核成巨龙」','破坏','','','','','','','','','','','','');
INSERT INTO texts VALUES(65029288,'娱乐伙伴 异色眼钢爪狼',replace(replace('「异色眼」怪兽＋「娱乐伙伴」怪兽\r\n这张卡不能作为融合素材。\r\n①：「融合」的效果融合召唤的这张卡不受其他卡的效果影响。\r\n②：这张卡的攻击宣言时发动。自己场上的全部怪兽的攻击力直到战斗阶段结束时上升300。','\r',char(13)),'\n',char(10)),'「融合」的效果融合召唤','攻击力上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65056481,'星因士 左旗一',replace(replace('「星因士 左旗一」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合才能发动。给与对方1000伤害。','\r',char(13)),'\n',char(10)),'基本分伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65064143,'对空放花','每祭掉自己场上1只昆虫族怪兽，给与对方基本分800分的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65079854,'憎恶之棘','「黑蔷薇龙」或者植物族怪兽才能装备。装备怪兽的攻击力上升600。装备怪兽攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。装备怪兽向怪兽攻击的场合，伤害计算后攻击对象怪兽的攻击力·守备力下降600。和装备怪兽进行战斗的对方怪兽不会被那次战斗破坏。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65100616,'连接设施飞行员',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：这张卡可以从手卡往作为场上的连接怪兽所连接区的自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65149697,'真次世代破碎机','这张卡召唤成功时，可以从自己卡组把1只4星的名字带有「真次世代」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65150219,'机甲忍法 冻结封锁','自己场上有名字带有「忍者」的怪兽存在的场合，对方怪兽的攻击宣言时才能把盖放的这张卡发动。那次攻击无效，战斗阶段结束。此外，只要自己场上有这张卡和名字带有「忍者」的怪兽存在，对方场上的全部怪兽不能把表示形式变更。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65156847,'新炎狱金翅鸟','自己的结束阶段时，这张卡以外的名字带有「炎狱」的怪兽在自己场上表侧表示存在的场合，选择对方墓地存在的1张卡从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65169794,'黑项链','装备怪兽的攻击力上升500。这张卡从场上送去墓地时，给与对方基本分500分伤害。','给予对方500分伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65170459,'孩星老爷',replace(replace('水属性怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：场上的水属性怪兽的攻击力·守备力上升500，炎属性怪兽的攻击力·守备力下降400。\r\n②：这张卡被战斗·效果破坏的场合，以自己墓地1只水属性怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65172015,'AtoZ-神龙歼灭炮',replace(replace('「ABC-神龙歼灭者」＋「XYZ-神龙炮」\r\n把自己场上的原本卡名是上记的卡除外的场合才能特殊召唤（不需要「融合」）。\r\n①：对方把怪兽的效果·魔法·陷阱卡发动时，丢弃1张手卡才能发动。那个发动无效并破坏。\r\n②：把场上的这张卡除外，以除外的自己的「ABC-神龙歼灭者」「XYZ-神龙炮」各1只为对象才能发动。那些怪兽特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'丢弃1张手卡，发动无效并破坏','把这张卡除外，把除外的怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65192027,'暗黑武装龙',replace(replace('这张卡不能通常召唤。自己墓地的暗属性怪兽是3只的场合才能特殊召唤。\r\n①：把自己墓地1只暗属性怪兽除外，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'不能通常召唤','墓地暗属性三张时才能特殊召唤','破坏场上一张卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65193366,'精灵兽使 薇茵妲',replace(replace('自己对「精灵兽使 薇茵妲」1回合只能有1次特殊召唤。\r\n①：这张卡被对方破坏的场合才能发动。从卡组或者额外卡组把1只「灵兽」怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65195959,'娱乐伙伴 加号龟','①：1回合1次，以场上最多2只表侧表示怪兽为对象才能发动。那些怪兽的等级上升1星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65196094,'多彩释放','让自己场上表侧表示存在的1只同调怪兽回到额外卡组发动。直到等级合计和那只同调怪兽的等级相同，选择自己墓地存在的调整在自己场上特殊召唤。这张卡发动的回合，自己不能同调召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65236257,'创星的因子','①：选这张卡以外的自己场上的「星守」卡数量的场上的魔法·陷阱卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65240384,'巨盾守卫者',replace(replace('①：只以里侧表示的这只怪兽1只为对象的魔法卡发动时发动。这张卡变成表侧守备表示，那个发动无效。\r\n②：这张卡被攻击的场合，伤害步骤结束时变成攻击表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65247798,'妖仙兽 镰壹太刀',replace(replace('①：这张卡召唤成功的场合才能发动。从手卡把「妖仙兽 镰壹太刀」以外的1只「妖仙兽」怪兽召唤。\r\n②：只在这张卡在场上表侧表示存在才有1次，自己场上有这张卡以外的「妖仙兽」怪兽存在的场合以对方场上1张表侧表示的卡为对象才能发动。那张卡回到持有者手卡。\r\n③：这张卡召唤的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'召唤','弹回手卡','返回手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65260293,'元素魔术师',replace(replace('这只怪兽在场上有特定属性的怪兽存在的场合，得到以下的效果。\r\n●水属性：这张卡的控制权不能变更。\r\n●风属性：这张卡战斗破坏对方怪兽的场合，只有1次可以再度攻击。','\r',char(13)),'\n',char(10)),'连续攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65277087,'薰风鹏','这张卡从场上送去墓地时，可以从自己卡组把1只2星以下的名字带有「薰风」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65282484,'暗黑裁决者 伏尔泰尼斯','自己对反击陷阱的发动成功的场合，可以把自己场上存在的1只暗属性怪兽解放，从手卡把这张卡特殊召唤。这个效果特殊召唤成功时，场上存在的1张卡破坏。','特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65287621,'暗黑三角怪鸟龙','这张卡攻击守备表示的怪兽时，攻击力超出部分对对方造成战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65301952,'炼金魔术师',replace(replace('魔法师族4星怪兽×3\r\n这张卡的攻击力上升自己墓地的魔法卡数量×200的数值。此外，自己的结束阶段时1次，把这张卡1个超量素材取除，把1张手卡送去墓地才能发动。从卡组选1张魔法卡，在自己的魔法与陷阱卡区域盖放。','\r',char(13)),'\n',char(10)),'盖放魔法卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65303664,'森之狩人 黄狒狒','自己场上存在的兽族怪兽被战斗破坏并送去墓地时，可以把自己墓地存在的2只兽族怪兽从游戏中除外。这张卡从手卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65305468,'未来No.0 未来皇 霍普',replace(replace('「No.」怪兽以外的相同阶级的超量怪兽×2\r\n这张卡的阶级在规则上当作1阶使用。\r\n①：这张卡不会被战斗破坏，这张卡的战斗发生的双方的战斗伤害变成0。\r\n②：这张卡和对方怪兽进行战斗的伤害步骤结束时才能发动。那只对方怪兽的控制权直到战斗阶段结束时得到。\r\n③：场上的这张卡被效果破坏的场合，可以作为代替把这张卡1个超量素材取除。','\r',char(13)),'\n',char(10)),'获得控制权','是否使用「未来No.0 未来皇 霍普」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65314286,'时械神 然迪恩',replace(replace('这张卡不能从卡组特殊召唤。\r\n①：自己场上没有怪兽存在的场合，这张卡可以不用解放作召唤。\r\n②：这张卡不会被战斗·效果破坏，这张卡的战斗发生的对自己的战斗伤害变成0。\r\n③：这张卡进行战斗的战斗阶段结束时，自己基本分比4000少的场合发动。自己基本分变成4000。\r\n④：自己准备阶段发动。这张卡回到持有者卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65330383,'梦幻崩影·狮鹫',replace(replace('卡名不同的怪兽2只以上\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功的场合，丢弃1张手卡，以自己墓地1张魔法·陷阱卡为对象才能发动。那张卡在自己场上盖放。那张卡在这个回合不能发动。这个效果的发动时这张卡是互相连接状态的场合，再让自己可以从卡组抽1张。\r\n②：只要这张卡在怪兽区域存在，场上的特殊召唤的怪兽若不在连接状态则不能把效果发动。','\r',char(13)),'\n',char(10)),'','是否抽卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65331686,'毛绒动物·猫头鹰',replace(replace('「毛绒动物·猫头鹰」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡从手卡的召唤·特殊召唤成功时才能发动。从卡组把1张「融合」加入手卡。\r\n②：支付500基本分才能发动。从自己的手卡·场上把「魔玩具」融合怪兽决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'卡组检索','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65338781,'熟练的赤魔术士',replace(replace('①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物（最多3个）。\r\n②：把有3个魔力指示物放置的这张卡解放才能发动。从自己的手卡·卡组·墓地选1只「恶魔」怪兽特殊召唤。\r\n③：自己主要阶段把墓地的这张卡除外，以自己场上1张可以放置魔力指示物的卡为对象才能发动。给那张卡放置1个魔力指示物。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65367484,'光子斩击者',replace(replace('这张卡不能通常召唤。自己场上没有怪兽存在的场合可以特殊召唤。\r\n①：自己场上有这张卡以外的怪兽存在的场合，这张卡不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65384019,'炼金周期','直到发动回合的结束阶段时，自己场上表侧表示存在的怪兽全部的原本攻击力变成0。每次被这个效果把原本攻击力变成0的怪兽被战斗破坏送去墓地，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65384188,'实力伯仲','选择自己以及对方场上表侧攻击表示存在的效果怪兽各1只才能发动。选择的2只怪兽的效果无效。那之后，只要选择的2只怪兽在场上表侧攻击表示存在，选择的怪兽不会被战斗破坏，不受这张卡以外的卡的效果影响，也不能作攻击和表示形式的变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65393205,'查克拉','借由「查克拉的复活」降临。必须从场上或者手札，牺牲奉献等级合计为7个以上的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65396880,'大革命','这张卡只有当处于自己的主要阶段，在自己的场上有「流离的饥民」「受弹压的民众」「团结的反抗军」表侧表示存在时才能发动。将对方的手卡全部送去墓地，将场上所有对方控制的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65403020,'终结之阿努比斯','当这张卡在场上以表侧表示存在时，所有以墓地里的卡为对象的，以及所有在墓地发动的魔法·陷阱·怪兽卡的效果无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65422840,'酒吞童子',replace(replace('1回合1次，可以从下面的效果选择1个发动。\r\n●把自己墓地存在的2只不死族怪兽从游戏中除外，从自己卡组抽1张卡。\r\n●选择从游戏中除外的1只自己的不死族怪兽回到卡组最上面。','\r',char(13)),'\n',char(10)),'把自己墓地存在的2只不死族怪兽从游戏中除外，从自己卡组抽1张卡。','选择从游戏中除外的1只自己的不死族怪兽回到卡组最上面。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65430834,'侏罗纪冲击','①：自己场上有恐龙族怪兽2只以上存在，自己基本分比对方少的场合才能发动。场上的怪兽全部破坏，自己受到破坏的怪兽数量×1000伤害。那之后，给与对方为和自己受到的伤害相同数值的伤害。这张卡的发动后，直到下个回合的结束时双方不能把怪兽召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65446452,'水舞台装置',replace(replace('①：自己场上的水属性怪兽的攻击力·守备力上升300。\r\n②：自己场上的「水伶女」怪兽的攻击力·守备力上升300。\r\n③：这张卡从场上送去墓地的场合，以自己墓地1只水族怪兽为对象才能发动。那只怪兽特殊召唤。这个效果的发动后，直到回合结束时自己不是水族怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65450690,'限定解除','支付1000基本分。从手卡特殊召唤1只仪式怪兽。这个效果特殊召唤的仪式怪兽不能攻击，结束阶段时破坏。「限定解除」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65458948,'卡通人鱼',replace(replace('这张卡不能通常召唤。自己场上有「卡通世界」存在的场合可以特殊召唤。\r\n①：这张卡在特殊召唤的回合不能攻击。\r\n②：这张卡若不支付500基本分则不能攻击宣言。\r\n③：对方场上没有卡通怪兽存在的场合，这张卡可以直接攻击。存在的场合，必须把卡通怪兽作为攻击对象。\r\n④：场上的「卡通世界」被破坏时这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65472618,'魔装战士 龙天',replace(replace('←2 【灵摆】 2→\r\n①：自己怪兽和对方的表侧表示怪兽进行战斗的伤害步骤开始时丢弃1张手卡才能发动。那只进行战斗的对方怪兽的攻击力·守备力变成一半。\r\n【怪兽效果】\r\n①：自己的怪兽区域的这张卡被对方怪兽的攻击或者对方的效果破坏的场合才能发动。这个回合的结束阶段，从卡组把「魔装战士 龙天」以外的攻击力2000以下的1只战士族或者魔法师族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65475294,'杀戮同伴的女骑士','每次自己的准备阶段，如果不用这张卡以外的1只怪兽做祭品维持，这张卡破坏。','是否要解放一只怪兽维持「杀戮同伴的女骑士」？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65496056,'科达路斯','可以把自己场上表侧表示存在的「海」送去墓地，选择对方场上存在的最多2张卡送去墓地。','送去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65503206,'废铁士兵','场上表侧守备表示存在的这张卡被选择作为攻击对象的场合，战斗阶段结束时这张卡破坏。这张卡被名字带有「废铁」的卡的效果破坏送去墓地的场合，可以选择「废铁士兵」以外的自己墓地存在的1只名字带有「废铁」的怪兽加入手卡。把这张卡作为同调素材的场合，不是名字带有「废铁」的怪兽的同调召唤不能使用。','这张卡破坏','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65518099,'机壳工具 丑恶',replace(replace('←9 【灵摆】 9→\r\n①：自己不是「机壳」怪兽不能特殊召唤。这个效果不会被无效化。\r\n②：1回合1次，支付800基本分才能发动。从卡组把「机壳工具 丑恶」以外的1张「机壳」卡加入手卡。\r\n【怪兽描述】\r\n正在准备以副本模式启动系统...\r\nC:\sophia\sefiroth.exe 执行中发生错误。\r\n正在试图执行来自未知发布者的以下程序。\r\nC:\tierra\qliphoth.exe 您想允许执行吗? <Y/N>...[Y]\r\n以自律模式启动系统。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65536818,'源龙星-望天吼',replace(replace('调整＋调整以外的怪兽1只以上\r\n自己对「源龙星-望天吼」1回合只能有1次特殊召唤。\r\n①：这张卡特殊召唤成功的场合才能发动。从卡组把1张「龙星」卡加入手卡。\r\n②：1回合1次，从卡组把1只幻龙族怪兽送去墓地才能发动。这张卡的等级变成和送去墓地的怪兽相同。\r\n③：表侧表示的这张卡从场上离开的场合才能发动。从卡组把1只「龙星」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'卡组检索','改变等级','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65549080,'霞雾秃鹰','这张卡可以让自己场上表侧表示存在的1只名字带有「霞之谷」的怪兽回到手卡，从手卡特殊召唤。这个方法特殊召唤的这张卡的攻击力变成1700。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65570596,'拉弓的人鱼','平常躲在贝壳里，对擅自接近的人射箭的美人鱼。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65591858,'先史遗产 飞翼斯芬克斯像','这张卡召唤成功时，可以从自己墓地选择1只名字带有「先史遗产」的5星怪兽特殊召唤。这个效果发动的回合，自己不能把名字带有「先史遗产」的怪兽以外的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65612386,'魔帝 安格玛','①：这张卡上级召唤成功时，把自己墓地1张魔法卡除外才能发动。把1张除外的那张魔法卡的同名卡从卡组加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65612454,'电气立方体','雷族怪兽才能装备。装备怪兽的攻击力上升自己墓地存在的雷族怪兽数量×100的数值。此外，可以把场上表侧表示存在的这张卡送去墓地，自己场上表侧表示存在的1只雷族怪兽的攻击力上升1000。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65622692,'Y-龙头',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只「X-首领加农」为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：装备怪兽的攻击力·守备力上升400。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65623423,'杀人污泥','史莱姆们的老大。尽管样子上没什么区别，但别小看它，否则可是会吃亏的哦。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65646587,'灵摆融合',replace(replace('「灵摆融合」在1回合只能发动1张。\r\n①：融合怪兽卡决定的融合素材怪兽从自己场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。自己的灵摆区域有2张卡存在的场合，自己的灵摆区域存在的融合素材怪兽也能作为融合素材使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65659181,'大日多胞体','选择当作装备卡使用在自己场上存在的名字带有「大日」的怪兽卡任意数量发动。选择的卡表侧守备表示特殊召唤。这个效果特殊召唤的怪兽从场上离开的场合从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65676461,'No.32 海咬龙 鲨龙兽',replace(replace('4星怪兽×3\r\n1回合1次，这张卡的攻击破坏对方怪兽送去墓地时，把这张卡1个超量素材取除才能发动。破坏的那只怪兽在对方场上表侧攻击表示特殊召唤。这个效果特殊召唤的怪兽的攻击力下降1000。并且，这次战斗阶段中，这张卡只有1次可以再攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65685470,'六武众的御灵代','1回合只有1次在自己的主要阶段可以当作装备卡使用给自己场上的名字带有「六武众」的怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。只有因这个效果当作装备卡使用时，装备怪兽的攻击力·守备力上升500。装备怪兽战斗破坏对方怪兽的场合，自己抽1张卡。（1只怪兽可以装备的同盟最多1张。装备怪兽被破坏的场合，作为代替把这张卡破坏。）','变成装备卡','特殊召唤','抽卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65703851,'透破拔','①：手卡·墓地有怪兽的效果发动时才能发动。那个发动无效并除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65737274,'龙龙','这张卡召唤成功时，可以选择自己墓地1只攻击力1000以下的通常怪兽表侧守备表示特殊召唤。1回合1次，把自己场上1只龙族怪兽解放，选择自己场上表侧表示存在的1只怪兽才能发动。直到结束阶段时，选择的怪兽的等级变成8星，攻击力上升800。','特殊召唤','等级攻击变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65743242,'地缚灵的引诱','对方怪兽攻击宣言时发动。攻击怪兽的攻击对象由这张卡的控制者选择。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65749035,'冰结界之龙 天枪龙',replace(replace('调整＋调整以外的水属性怪兽1只以上\r\n把手卡最多2张丢弃去墓地，选择丢弃数量的对方场上存在的卡发动。选择的卡破坏。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65758454,'加把劲枪兵','这张卡召唤成功时，可以从自己的手卡·墓地选1只「加把劲枪兵」表侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65810489,'黄金邪神像','里侧盖伏的这张卡被破坏送去墓地时，自己场上特殊召唤1只「邪神衍生物」（恶魔族·暗·4星·攻/防1000）。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65810490,'邪神衍生物','由「黄金邪神像」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65824822,'暗之取引','对方发动通常魔法时支付1000基本分才能发动。那时对方发动的通常魔法的效果，变成对方随机丢弃1张手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65830223,'卖棺者','每次对方的怪兽卡送去墓地，对方基本分受到300分的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65844845,'甲虫装机 吉咖蝼蛄','可以把自己墓地1只昆虫族怪兽从游戏中除外，墓地的这张卡当作装备卡使用给自己场上1只名字带有「甲虫装机」的怪兽装备。「甲虫装机 吉咖蝼蛄」的这个效果1回合只能使用1次。此外，这张卡当作装备卡使用而装备中的场合，装备怪兽的原本攻击力变成2000，装备怪兽向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','装备','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65848811,'英豪挑战者 强袭之戟兵',replace(replace('①：对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n③：这张卡给与对方战斗伤害时才能发动。从卡组把1张「英豪」卡加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65872270,'点火骑士炸裂',replace(replace('①：1回合1次，自己主要阶段才能把这个效果发动。选这张卡以外的自己场上最多3张「点火骑士」卡破坏。那之后，选破坏数量的对方场上的卡回到持有者手卡。\r\n②：这张卡被送去墓地的场合才能发动。选自己的额外卡组1只表侧表示的「点火骑士」灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'是否使用效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65878864,'食使徒虫','反转：场上2只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65884091,'入魔死神 塔那托斯',replace(replace('暗属性4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。这个回合，这张卡不受这张卡以外的怪兽的效果影响。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'效果免疫','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65957473,'铁钢装甲虫','全身覆盖着厚厚装甲的巨大昆虫型生物。毫不留情的破坏着碍事者。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65959844,'化合电界',replace(replace('①：只要这张卡在场地区域存在，自己在5星以上的二重怪兽召唤的场合需要的解放可以不用。这个效果1回合只能适用1次。\r\n②：只要这张卡在场地区域存在，自己在通常召唤外加上只有1次，自己主要阶段可以把1只二重怪兽召唤。\r\n③：1回合1次，以对方场上1张卡为对象才能发动。自己场上1只再1次召唤状态的二重怪兽直到对方结束阶段除外，作为对象的卡破坏。','\r',char(13)),'\n',char(10)),'不用解放作召唤','多次召唤','破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65961085,'魔导兽士 鲁德','这张卡用魔法师族怪兽的效果特殊召唤成功时，选择从游戏中除外的自己的名字带有「魔导书」的魔法卡任意数量才能发动。选择的卡回到卡组，剩下的名字带有「魔导书」的魔法卡回到墓地。「魔导兽士 鲁德」的效果1回合只能使用1次。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65961683,'朱罗纪伶盗龙',replace(replace('调整＋调整以外的恐龙族怪兽1只以上\r\n这张卡的攻击力·守备力变成作为这张卡的同调素材的怪兽的原本攻击力合计数值。这张卡向里侧守备表示怪兽攻击的场合，可以不进行伤害计算以里侧守备表示的状态把那只怪兽破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65976795,'地中族邪界兽·亚得导水妖',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合，以自己场上的「地中族邪界」怪兽数量的场上盖放的卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65984457,'剑斗兽 轻斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功的场合，这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组并抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(65993085,'过火的埋葬',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：从手卡丢弃1只怪兽，以原本等级比丢弃的怪兽低的自己墓地1只怪兽为对象才能把这张卡发动。那只怪兽特殊召唤，把这张卡装备。\r\n②：装备怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66015185,'双三角龙',replace(replace('衍生物以外的4星以下的龙族怪兽2只\r\n①：这张卡连接召唤成功时，支付500基本分，以自己墓地1只5星以上的怪兽为对象才能发动。那只怪兽在作为这张卡所连接区的自己场上特殊召唤。这个效果特殊召唤的怪兽的效果无效化，这个回合不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66022706,'翠岚之机界骑士',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：相同纵列有卡2张以上存在的场合，这张卡可以在那个纵列的自己场上特殊召唤。\r\n②：这张卡和相同纵列的对方怪兽进行战斗的攻击宣言时，以自己墓地1只「机界骑士」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66066482,'甲虫装机 萤火虫','1回合1次，可以从自己的手卡·墓地把1只名字带有「甲虫装机」的怪兽当作装备卡使用给这张卡装备。这张卡被名字带有「甲虫装機」的卡装备的场合，可以把对方场上盖放的卡全部确认。此外，这张卡当作装备卡使用而装备中的场合，装备怪兽的等级上升1星，攻击力·守备力上升这张卡的各自数值。','装备','确认盖卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66073051,'杰拉的战士','为可以获得大天使力量而寻求圣域所在的战士。为了逃避凶恶魔族的诱惑，而一直孤独的战斗着。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66084673,'空炎星-犀超','自己场上的名字带有「炎星」的怪兽进行战斗的伤害计算时只有1次，把自己场上表侧表示存在的1张名字带有「炎舞」的魔法·陷阱卡和手卡1只名字带有「炎星」的怪兽送去墓地才能发动。进行战斗的自己怪兽的攻击力只在那次伤害计算时上升送去墓地的怪兽的原本攻击力数值。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66094973,'变化气圈鸟','1回合1次，选择对方场上表侧守备表示存在的1只怪兽才能发动。丢弃1张手卡，把选择的对方怪兽当作装备卡使用只有1只给这张卡装备。这张卡的攻击力上升这张卡的效果装备的怪兽的攻击力数值。这张卡攻击的场合，战斗阶段结束时变成守备表示。结束阶段时，这张卡的效果装备的怪兽在对方场上表侧守备表示特殊召唤。','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66100045,'转生断绝','从墓地回到卡组的卡不回到卡组从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66127916,'融合准备','①：把额外卡组1只融合怪兽给对方观看，那只怪兽有卡名记述的1只融合素材怪兽从卡组加入手卡。那之后，可以选自己墓地1张「融合」加入手卡。','是否把自己墓地1张「融合」加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66141736,'红莲升龙',replace(replace('恶魔族调整＋调整以外的怪兽1只以上\r\n①：这张卡同调召唤成功时，以自己墓地1只「共鸣者」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果发动的回合，自己不是龙族·暗属性同调怪兽不能从额外卡组特殊召唤。\r\n②：把墓地的这张卡除外，以自己墓地2只1星「共鸣者」怪兽为对象才能发动。那2只怪兽特殊召唤。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66149377,'魔弹-舞动之针',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：自己场上有「魔弹」怪兽存在的场合，以双方墓地的卡合计最多3张为对象才能发动。那些卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66165755,'盟军·次世代加速人',replace(replace('名字带有「次世代」的调整＋调整以外的怪兽1只以上\r\n1回合1次，可以丢弃1张手卡，选择自己墓地存在的1只4星以下的机械族怪兽特殊召唤。这个效果特殊召唤的怪兽的攻击力直到结束阶段时变成2倍，不能直接攻击对方玩家，自己的结束阶段时从游戏中除外。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66171432,'超来来',replace(replace('「超来来」在1回合只能发动1张。\r\n①：从自己卡组上面把3张卡翻开，那之中的「花札卫」怪兽尽可能无视召唤条件特殊召唤。这个效果特殊召唤的怪兽的等级变成2星，效果无效化。剩下的卡全部里侧表示除外，自己失去除外的卡数量×1000基本分。\r\n②：把墓地的这张卡除外，把自己场上1只怪兽解放才能发动。从手卡把1只「花札卫」怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'卡组特殊召唤','手卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66194206,'光道的裁决',replace(replace('①：这张卡回到卡组最上面。\r\n②：这张卡被「光道」怪兽的效果从卡组送去墓地的场合才能发动。从卡组把1只「裁决之龙」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66200210,'幻兽机 同温层仓鼠','这张卡反转时，把2只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。这张卡的等级上升自己场上的「幻兽机衍生物」的等级的合计数值。只要自己场上有衍生物存在，这张卡不会被战斗以及效果破坏。此外，把1只衍生物解放才能发动。选择自己墓地1只名字带有「幻兽机」的怪兽特殊召唤。「幻兽机 同温层仓鼠」的这个效果1回合只能使用1次。','特殊召唤Token','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66200211,'幻兽机衍生物','由「幻兽机 同温层仓鼠」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66214679,'暗黑黑炎龙','只要这张卡在场上表侧表示存在，对方的主要阶段时有魔法卡发动的场合，可以从自己墓地把1只4星的暗属性怪兽特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66235877,'死亡恶魔龙',replace(replace('「巨龙」＋「下位恶魔」\r\n这只怪兽，必须用上面所写的卡融合召唤。只要这张卡在场上存在，反转效果怪兽的效果无效化。以这张卡为对象的陷阱卡无效并破坏。','\r',char(13)),'\n',char(10)),'效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66262416,'命运英雄 梦乡人',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡在墓地存在，自己的「命运英雄」怪兽进行战斗的伤害计算时才能发动。这张卡从墓地特殊召唤，那只自己怪兽不会被那次战斗破坏，那次战斗发生的对自己的战斗伤害变成0。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66288028,'重型战士','这张卡的战斗发生的对自己的战斗伤害变成0。这张卡1回合只有1次不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66322203,'方程式运动员下压力',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：以自己场上1只「方程式运动员」怪兽为对象才能发动。那只怪兽的等级直到回合结束时上升2星。\r\n②：自己主要阶段把墓地的这张卡除外，以自己场上1只「方程式运动员」怪兽为对象才能发动。那只怪兽的等级直到回合结束时上升2星。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66331855,'变形斗士·打火机',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：可以把自己场上存在的1只怪兽解放给与对方基本分600分伤害。这个效果1回合只能使用1次。\r\n●守备表示：1回合1次，可以给与对方基本分300分伤害。','\r',char(13)),'\n',char(10)),'给予对方600伤害','给予对方300伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66331856,'变形斗士·打火机',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：可以把自己场上存在的1只怪兽解放给与对方基本分600分伤害。这个效果1回合只能使用1次。\r\n●守备表示：1回合1次，可以给与对方基本分300分伤害。','\r',char(13)),'\n',char(10)),'给予对方600伤害','给予对方300伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66337215,'创世之预言者','①：1回合1次，丢弃1张手卡，以自己墓地1只7星以上的怪兽为对象才能发动。那只怪兽加入手卡。','7星以上的怪兽加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66362965,'魔导电子巨人','自己场上的怪兽比对方场上的怪兽少2只以上的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66378485,'新炎狱火源','自己场上有「新炎狱火源」以外的名字带有「炎狱」的怪兽表侧表示存在，对方墓地存在的卡是3张以下的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66393507,'超机怪虫·圣言神经元虫',replace(replace('昆虫族怪兽2只\r\n①：这张卡所连接区的「机怪虫」怪兽不会被战斗破坏，攻击力·守备力上升300，和对方怪兽进行战斗的场合，给与对方的战斗伤害变成2倍。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合或者被战斗破坏的场合，以自己墓地2只「机怪虫」怪兽为对象才能发动（同名卡最多1张）。那些怪兽里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66395299,'二重身回击','因对方场上存在的怪兽的效果受到伤害时，给与对方基本分和受到的伤害相同的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66399653,'同盟格纳库',replace(replace('「同盟格纳库」在1回合只能发动1张。\r\n①：作为这张卡的发动时的效果处理，可以从卡组把1只机械族·光属性的同盟怪兽加入手卡。\r\n②：1回合1次，自己场上有机械族·光属性的同盟怪兽召唤·特殊召唤的场合，以那1只怪兽为对象才能发动。从卡组选可以给那只怪兽装备而卡名不同的1只机械族·光属性的同盟怪兽给那只怪兽装备。这个效果装备的同盟怪兽在这个回合不能特殊召唤。','\r',char(13)),'\n',char(10)),'是否把同盟怪兽加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66399675,'遗式锁链手','这张卡召唤成功时，从自己卡组上面把3张卡确认。确认的卡之中有仪式怪兽或者仪式魔法卡的场合，可以把那1张给对方观看并加入手卡。确认的卡用喜欢的顺序回到卡组上面。','卡组确认','是否要将仪式怪兽或者仪式魔法卡加入手卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66413481,'炎王兽 夜叉','自己场上表侧表示存在的名字带有「炎王」的怪兽被卡的效果破坏的场合，这张卡可以从手卡特殊召唤。此外，这张卡被破坏送去墓地的场合，可以选自己的手卡·场上1张卡破坏。「炎王兽 夜叉」的这个效果1回合只能使用1次。','特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66436257,'火山反击兽','自己受到战斗伤害时，墓地存在的这张卡从游戏中除外。那个时候，自己墓地有「火山反击兽」以外的炎属性怪兽存在的场合，给与对方基本分和自己受到的战斗伤害相同的伤害。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66451379,'灰篮短吻鳄',replace(replace('①：自己的怪兽区域的这张卡被战斗或者魔法卡的效果破坏送去墓地的场合，以对方场上1只表侧表示怪兽为对象才能发动。这张卡当作装备卡使用给那只对方怪兽装备。\r\n②：这张卡的效果让这张卡装备中的场合，得到装备怪兽的控制权。这张卡从场上离开时装备怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66457138,'毛绒动物·狮子','①：这张卡的攻击宣言时发动。这张卡的攻击力直到战斗阶段结束时上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66457407,'复制植物','1回合1次，选择这张卡以外的场上1只植物族怪兽才能发动。这张卡的等级直到结束阶段时变成和选择的怪兽相同等级。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66499018,'焰虎','这张卡在墓地存在，自己场上没有怪兽存在的场合，作为自己的抽卡阶段时进行通常抽卡的代替才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66500065,'地龙星-狴犴',replace(replace('「地龙星-狴犴」的①的效果1回合只能使用1次。\r\n①：自己场上的这张卡被战斗·效果破坏送去墓地时才能发动。从卡组把「地龙星-狴犴」以外的1只「龙星」怪兽守备表示特殊召唤。\r\n②：1回合1次，对方的主要阶段以及战斗阶段才能发动。只用自己场上的「龙星」怪兽为同调素材作同调召唤。\r\n③：这张卡为同调素材的同调怪兽不会被战斗破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','「地龙星-狴犴」效果适用中','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66506689,'铁皮大公',replace(replace('4星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上1只怪兽才能发动。选择的怪兽的表示形式变更。这个时候，反转效果怪兽的效果不发动。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66516792,'邪恶骑士龙','寄宿在邪恶骑士之心的龙的实体化后的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66518841,'自尊的咆哮','战斗伤害计算时，自己怪兽的攻击力比对方怪兽低的场合，支付那个攻击力差的数值的基本分发动。只在伤害计算时，自己怪兽的攻击力上升和对方怪兽的攻击力差的数值＋300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66523544,'超次元机器人 银河破坏王',replace(replace('10星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除才能发动。对方场上的魔法·陷阱卡全部破坏。对方不能对应这个效果的发动把魔法·陷阱卡发动。','\r',char(13)),'\n',char(10)),'魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66526672,'噩梦之迷宫','在每1个回合的结束阶段时，变更该回合行动玩家场上所有以表侧表示存在的怪兽的表示形式。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66540884,'诡计恶魔',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡被效果送去墓地的场合或者被战斗破坏送去墓地的场合才能发动。从卡组把「诡计恶魔」以外的1张「恶魔」卡加入手卡。','\r',char(13)),'\n',char(10)),'检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66547759,'No.23 冥界的灵骑士 兰斯洛特',replace(replace('8星怪兽×2\r\n①：持有超量素材的这张卡可以直接攻击。\r\n②：这张卡给与对方战斗伤害时，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽破坏。\r\n③：1回合1次，这张卡以外的怪兽的效果·魔法·陷阱卡发动时，把这张卡1个超量素材取除发动。那个发动无效。','\r',char(13)),'\n',char(10)),'怪兽破坏','发动无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66574418,'传说的黑石',replace(replace('「传说的黑石」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：把这张卡解放才能发动。从卡组把1只7星以下的「真红眼」怪兽特殊召唤。\r\n②：这张卡在墓地存在的场合，以自己墓地1只7星以下的「真红眼」怪兽为对象才能发动。那只怪兽回到卡组，墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66602787,'暗道化师 扎奇','来去无踪的道化师，以不可思议的动作躲避攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66604523,'超量逆转','选择对方场上1只超量怪兽和自己场上1只超量怪兽才能发动。选择的怪兽的控制权交换。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66607691,'光子发生装置','把自己场上2只「电子龙」作为祭品才能发动。从自己的手卡·卡组·墓地特殊召唤1只「电子镭射龙」。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66625883,'机巧参谋 二四八','这张卡可以攻击的场合必须作出攻击。场上表侧攻击表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变成守备表示。这张卡召唤·特殊召唤成功时，把场上存在的1只怪兽的表示形式变更。','变成守备表示','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66661678,'冰结界的王家骑士','这张卡上级召唤成功时，在对方场上把1只「冰棺衍生物」（水·1星·水族·攻1000/守0）攻击表示特殊召唤。这衍生物不能为上级召唤而解放。','特殊召唤Token','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66661679,'冰棺衍生物','由「冰结界的王家骑士」的效果特殊召唤。这个衍生物不能为上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66668900,'水伶女·孔雀鱼','①：1回合1次，自己主要阶段才能发动。从手卡把1只「水伶女」怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66672569,'龙僵尸','被魔力唤醒的龙，吐出的气息能使物体腐烂。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66690411,'本性暴露','只要这张卡在场上表侧表示存在，对方必须永续公开手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66698383,'灼银之机龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：1回合1次，从自己的手卡·墓地以及自己场上的表侧表示怪兽之中把1只调整除外，以场上1张卡为对象才能发动。那张卡破坏。\r\n②：同调召唤的这张卡被效果破坏送去墓地的场合，以除外的1只自己的调整为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66707058,'哥布林暗杀部队','这张卡可以直接攻击对方玩家。这张卡攻击的场合，战斗阶段结束时变成守备表示。直到下次的自己回合结束时这张卡不能把表示形式改变。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66712593,'元素战士',replace(replace('这只怪兽在场上有特定属性的怪兽存在的场合，得到以下的效果。\r\n●水属性：这张卡的控制权不能变更。\r\n●地属性：这张卡战斗破坏的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66719324,'恩惠之雨','双方的基本分回复1000分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66719533,'非对称玄化',replace(replace('①：1回合1次，自己主要阶段才能发动。从手卡把1张「玄化」卡除外，自己从卡组抽1张。\r\n②：1回合1次，自己的「玄化」卡被除外的场合发动。那个回合的以下效果适用。\r\n●自己回合：「玄化」怪兽以外的场上的全部怪兽的攻击力·守备力下降500。\r\n●对方回合：「玄化」怪兽以外的场上的全部怪兽的表示形式变更。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66727115,'武神逐','把自己场上1只名字带有「武神」的兽战士族怪兽解放才能发动。从自己墓地选择和那只怪兽的卡名不同的1只名字带有「武神」的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66729231,'邪遗式魂魄巨灵','名字带有「遗式」的仪式魔法卡降临。1回合1次，支付1000基本分才能发动。从卡组抽1张卡，给双方确认。确认的卡是名字带有「遗式」的怪兽的场合，场上1张卡回到持有者卡组。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66740005,'空牙团的斗士 布拉沃',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。从手卡把「空牙团的斗士 布拉沃」以外的1只「空牙团」怪兽特殊召唤。\r\n②：这张卡已在怪兽区域存在的状态，自己场上有这张卡以外的「空牙团」怪兽特殊召唤的场合才能发动。场上的全部「空牙团」怪兽的攻击力·守备力直到回合结束时上升500。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66742250,'阿努比斯的诅咒','场上以表侧表示存在的效果怪兽全部变成守备表示。这张卡发动的回合，这些效果怪兽的原始守备力变为零，且不能改变表示形式。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66752837,'灵庙守护者',replace(replace('「灵庙守护者」的②的效果1回合只能使用1次。\r\n①：龙族怪兽上级召唤的场合，这张卡可以作为2只的数量解放。\r\n②：这张卡在手卡·墓地存在，「灵庙守护者」以外的场上的表侧表示的龙族怪兽被效果送去墓地的场合或者被战斗破坏送去墓地的场合才能发动。这张卡特殊召唤。并且送去墓地的怪兽是通常怪兽的场合，可以再选自己墓地1只龙族通常怪兽加入手卡。','\r',char(13)),'\n',char(10)),'是否选墓地1只龙族通常怪兽加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66762372,'孤炎星-鲁猪深','把这张卡作为同调素材的场合，不是炎属性怪兽的同调召唤不能使用。这张卡被战斗破坏送去墓地时，可以从卡组把「孤炎星-鲁猪深」以外的1只名字带有「炎星」的4星怪兽特殊召唤。1回合1次，这张卡在场上存在的场合名字带有「炎星」的怪兽从自己的额外卡组特殊召唤时，可以从卡组选1张名字带有「炎舞」的魔法卡在自己场上盖放。','特殊召唤','盖放','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66768175,'娱乐伙伴 歌谣蔷薇',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，自己的「娱乐伙伴」怪兽和对方的表侧表示怪兽进行战斗的伤害步骤开始时才能发动。那只对方怪兽的攻击力下降600。\r\n【怪兽效果】\r\n①：自己的「娱乐伙伴」怪兽攻击的伤害计算后，以对方场上1只表侧表示怪兽为对象才能发动。那只对方怪兽的攻击力下降那只「娱乐伙伴」怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66788016,'地割','对方场上表侧表示存在的攻击力最低的1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66789970,'圣刻龙-塞特龙','这张卡不能通常召唤。把自己墓地3只龙族的通常怪兽从游戏中除外的场合可以特殊召唤。1回合1次，可以把自己墓地1只龙族怪兽从游戏中除外，选择场上1张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66815913,'灵魂鸟-巫鹤',replace(replace('这张卡不能特殊召唤。\r\n①：1回合1次，这张卡在怪兽区域存在，这张卡以外的灵魂怪兽召唤·特殊召唤成功的场合才能发动。自己从卡组抽1张。\r\n②：这张卡召唤·反转的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66816282,'核成墙人','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只岩石族怪兽给对方观看。或者都不进行让这张卡破坏。对方的魔法卡发动时，可以把这张卡解放让那个发动无效并破坏。','选择一张「核成兽的钢核」送去墓地','选择一只岩石族怪物给对方观看','破坏「核成墙人」','魔法发动无效并破坏','','','','','','','','','','','','');
INSERT INTO texts VALUES(66818682,'月影龙 基利亚',replace(replace('「苏帕伊」＋调整以外的怪兽1只以上\r\n这张卡被选择作为攻击对象时，自己基本分回复攻击怪兽的攻击力一半的数值。场上存在的这张卡被破坏的场合，可以把自己墓地存在的1只「太阳龙 因蒂」特殊召唤。','\r',char(13)),'\n',char(10)),'LP回复','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66835946,'奇迹之金字塔','自己场上表侧表示存在的不死族怪兽的攻击力上升对方场上存在的怪兽数量×200的数值。自己场上表侧表示存在的不死族怪兽1只被破坏的场合，可以作为代替把这张卡送去墓地。','是否要使用「奇迹之金字塔」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66836598,'温蒂妮','在水中摇摇晃晃漂浮的妖精。好像可以召唤水龙。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66853752,'电子鱼人-火箭炮手','「电子鱼人-火箭炮手」以外的自己墓地的怪兽全部是水属性的场合，自己的主要阶段时才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合，从游戏中除外。把这张卡作为同调素材的场合，不是水属性怪兽的同调召唤不能使用。「电子鱼人-火箭炮手」的效果1回合只能发动1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66865880,'棉花糖的眼镜','只要这张卡和「棉花糖」在自己场上表侧表示存在，对方不能选择「棉花糖」以外的怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66889139,'龙骑士 盖亚','「暗黑骑士 盖亚」＋「诅咒之龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66926224,'弱肉一色','这张卡仅当自己场上存在5只2星以下的表侧表示的通常怪兽时才能发动。玩家各自丢弃全部手卡，并破坏场上除2星以下通常怪兽以外的所有卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66927994,'鬼坦克T-34','被移植了鬼的灵魂，对有意识的敌人穷追不舍。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66938505,'水晶机巧-中枢黑晶',replace(replace('「水晶机巧-中枢黑晶」的效果1回合只能使用1次。\r\n①：对方的主要阶段以及战斗阶段，以调整以外的除外的1只自己怪兽为对象才能发动。那只怪兽效果无效特殊召唤，只用那只怪兽和这张卡为素材把1只机械族同调怪兽同调召唤。那个时候的同调素材怪兽不去墓地回到持有者卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66947414,'头目连战',replace(replace('自己没有通常召唤的回合才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，自己不能通常召唤。\r\n②：自己场上的表侧表示的「巨大战舰」怪兽被破坏送去墓地的场合，那个回合的结束阶段才能发动。从卡组把1只「巨大战舰」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66957584,'永火炮',replace(replace('①：1回合1次，自己主要阶段才能发动。从手卡把1只「永火」怪兽送去墓地。\r\n②：把魔法与陷阱区域的表侧表示的这张卡送去墓地，以自己墓地最多2只「永火」怪兽为对象才能发动。那些怪兽特殊召唤。这个效果在自己手卡是0张的场合才能发动和处理。','\r',char(13)),'\n',char(10)),'从手卡把1只名字带有「永火」的怪兽送去墓地','选择自己墓地存在的最多2只名字带有「永火」的怪兽在自己场上特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66970002,'混沌No.39 希望皇 霍普雷V',replace(replace('5星怪兽×3\r\n这张卡被对方破坏时，可以选择自己墓地1只超量怪兽回到额外卡组。此外，这张卡有名字带有「希望皇 霍普」的怪兽在作为超量素材的场合，得到以下效果。\r\n●1回合1次，把这张卡1个超量素材取除，选择对方场上1只怪兽才能发动。选择的怪兽破坏，给与对方基本分破坏的怪兽的攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'返回卡组','破坏并伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66970385,'圣骑士传说的终幕',replace(replace('「圣骑士传说的终幕」在1回合只能发动1张。\r\n①：对方场上有怪兽存在，自己场上没有怪兽存在的场合，以自己墓地1只「圣骑士」怪兽和1张那只怪兽可以装备的「圣剑」装备魔法卡为对象才能发动。那只怪兽特殊召唤，那张装备魔法卡给作为正确对象的那只怪兽装备。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66973070,'死镰杀手','①：把这张卡以外的自己场上1只昆虫族怪兽解放才能发动。这张卡的攻击力直到回合结束时上升500。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66976526,'机巧大将军 无零怒',replace(replace('调整＋调整以外的机械族怪兽1只以上\r\n这张卡同调召唤成功时，可以从自己卡组把1只名字带有「机巧」的怪兽特殊召唤。1回合1次，自己场上表侧表示存在的名字带有「机巧」的怪兽的表示形式变更时，从自己卡组抽1张卡。','\r',char(13)),'\n',char(10)),'特殊召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66989694,'死灵伯爵','魔界的伯爵，外表很绅士，但本性却十分邪恶，不只是人类，连低级的恶魔们都对他感到恐惧。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(66994718,'猛禽突风','①：自己场上有「急袭猛禽」卡存在，魔法·陷阱卡发动时才能发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67030233,'救世魔龙',replace(replace('「救世龙」＋「红莲魔龙」＋调整以外的怪兽1只\r\n这张卡不会被卡的效果破坏。这张卡攻击的场合，伤害计算后把场上守备表示存在的怪兽全部破坏。1回合1次，可以直到结束阶段时把对方1只表侧表示怪兽的效果无效，这张卡的攻击力上升那只怪兽的攻击力数值。结束阶段时这张卡回到额外卡组，把自己墓地存在的1只「红莲魔龙」特殊召唤。','\r',char(13)),'\n',char(10)),'守备怪兽全部破坏','效果无效化','返回额外卡组','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67038874,'废铁读心者','1回合1次，在自己主要阶段2这张卡在自己的墓地存在的场合，可以选择自己场上表侧表示存在的「废铁读心者」以外的1只名字带有「废铁」的怪兽，选择的怪兽破坏，这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡，从场上离开的场合从游戏中除外。这张卡作为同调素材的场合，其他同调素材怪兽全部必须是名字带有「废铁」的怪兽。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67045174,'二重落穴','再度召唤状态的二重怪兽被战斗破坏的场合才能发动。对方场上存在的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67045745,'方程式运动员 音速赛道名手',replace(replace('①：这张卡的攻击力上升这张卡的等级×300，不会被和原本的等级或者阶级比这张卡的等级低的对方怪兽的战斗破坏。\r\n②：「方程式运动员」魔法·陷阱卡的效果发动的场合才能发动（伤害步骤也能发动）。这张卡的等级上升1星。\r\n③：这张卡的等级是7星以上的场合，这张卡在同1次的战斗阶段中最多2次可以向怪兽攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67048711,'7','当自己场上凑齐3张以表侧表示存在的「7」时，自己抽3张卡。之后，所有的「7」被破坏。当这张卡从场上被送去墓地时，自己回复700基本分。','抽卡','回复','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67049542,'暗黑蝙蝠','住在暗世界用精神波动找寻敌人的蝙蝠。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67050396,'星际仙踪-格琳德尔',replace(replace('「星际仙踪-格琳德尔」的①的效果1回合只能使用1次。\r\n①：把场上的这张卡除外才能发动。从手卡把1只5星以上的「星际仙踪」怪兽特殊召唤。这个效果在对方回合也能发动。\r\n②：1回合1次，支付500基本分，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示。','\r',char(13)),'\n',char(10)),'从手卡把「星际仙踪」怪兽特殊召唤','怪兽变成里侧守备表示','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67095270,'次元壁','对方怪兽的攻击宣言时才能发动。这次战斗让自己的基本分受到的战斗伤害，由对方来承受。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67098114,'极神皇 洛基',replace(replace('名字带有「极星灵」的调整＋调整以外的怪兽2只以上\r\n1回合1次，自己的战斗阶段中对方把魔法·陷阱卡发动时，可以把那个发动无效并破坏。场上表侧表示存在的这张卡被对方破坏送去墓地的场合，那个回合的结束阶段时可以把自己墓地存在的1只名字带有「极星灵」的调整从游戏中除外，这张卡从墓地特殊召唤。这个效果特殊召唤成功时，可以选择自己墓地存在的1张陷阱卡加入手卡。','\r',char(13)),'\n',char(10)),'魔法陷阱的发动无效并破坏','特殊召唤','墓地的1张陷阱卡加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67105242,'地缚灵','战死沙场的士兵们的灵魂所聚集而产生的怨灵，踏入这片土地的人将被牵引入地底深处。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67111213,'遗式深渊鲨','这张卡召唤·反转召唤·特殊召唤成功时，可以从自己卡组把「遗式深渊鲨」以外的1只守备力1000以下的名字带有「遗式」的怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67113830,'墓地墓地的怨恨','对方墓地的卡是8张以上的场合才能发动。对方场上的全部怪兽的攻击力变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67120578,'我我我首领',replace(replace('①：只有对方场上才有怪兽存在的场合，这张卡可以不用解放并作为4星怪兽召唤。\r\n②：这张卡召唤成功时，以「我我我首领」以外的自己墓地最多2只「我我我」怪兽为对象才能发动。那些怪兽特殊召唤。这个回合自己不能作除只用「我我我」怪兽为素材的超量召唤以外的特殊召唤。\r\n③：场上的这张卡为素材作超量召唤的怪兽得到以下效果。\r\n●这次超量召唤成功的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67136033,'热血兽王 熊人','这张卡可以不用解放作召唤。这个方法召唤的这张卡的原本攻击力变成1300。此外，1回合1次，自己的主要阶段时才能发动。自己场上的全部兽战士族·4星怪兽的等级直到结束阶段时变成8星。把这张卡作为超量召唤的素材的场合，不是战士族·炎属性怪兽的超量召唤不能使用。','不用解放作召唤','等级变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67159705,'装甲电子翼',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只需以「电子龙」为素材的融合怪兽或者「电子龙」为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：1回合1次，以场上1只表侧表示怪兽为对象才能发动。装备怪兽的攻击力下降1000，作为对象的表侧表示怪兽破坏。','\r',char(13)),'\n',char(10)),'装备','表侧攻击表示特殊召唤','装备怪兽的攻击力下降1000，场上表侧表示存在的1只怪兽破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67169062,'贪欲之壶','①：以自己墓地5只怪兽为对象才能发动。那5只怪兽加入卡组洗切。那之后，自己从卡组抽2张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67173574,'混沌No.102 光堕天使 贵魔',replace(replace('光属性5星怪兽×4\r\n场上的这张卡被破坏的场合，可以作为代替把这张卡2个超量素材取除。这张卡的超量素材全部被取除时，给与对方基本分1500分伤害。此外，这张卡有「No.102 光天使 辉环」在作为超量素材的场合，得到以下效果。\r\n●1回合1次，把这张卡1个超量素材取除才能发动。选择对方场上1只怪兽攻击力变成0，那个效果无效。','\r',char(13)),'\n',char(10)),'是否要使用「混沌No.102 光堕天使 贵魔」的效果代替破坏？','伤害','攻击变成0','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67196946,'星级爆破','支付500的倍数的基本分发动。自己的手卡，场上1只怪兽的等级直到这个回合的结束阶段时，每500基本分下降1星。','请选择要支付的基本分','请选择等级要下降的怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67211766,'真文色龙','只要对方场上有魔法·陷阱卡存在，对方不能选择这张卡作为攻击对象。此外，这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67218327,'SIMM标签貘',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：把手卡的这张卡给对方观看，以自己墓地1只电子界族·4星怪兽为对象才能发动。这张卡从手卡往作为连接状态的自己的连接怪兽的所连接区的自己场上特殊召唤，作为对象的怪兽回到手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67223587,'障碍赛！','自己把名字带有「剑斗兽」的怪兽的特殊召唤成功时才能发动。从自己的手卡·卡组把1只名字带有「剑斗兽」的4星以下的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67227834,'魔术咒文书','「黑魔术师」「黑魔术少女」才能装备。装备怪兽的攻击力上升700。这张卡从场上送去墓地时，自己回复1000基本分。','LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67231737,'连接缓冲人',replace(replace('电子界族怪兽2只\r\n①：1回合1次，这张卡所连接区的自己怪兽向对方的连接怪兽攻击的伤害步骤结束时才能发动。这次战斗阶段中，那只怪兽在通常攻击外加上可以向对方的连接怪兽作出最多有这张卡以外的自己场上的连接怪兽数量的攻击。这个效果发动的回合，那只怪兽以外的自己怪兽不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67232306,'神圣之铠 -反射镜甲-','自己场上表侧表示存在的怪兽被选择作为攻击对象时才能发动。攻击对象怪兽的攻击力变成和攻击怪兽的攻击力相同。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67234805,'能力吸收石','每次怪兽的效果发动，给这张卡放置1个魔石指示物（最多2个）。这张卡有2个魔石指示物放置的场合，场上表侧表示存在的怪兽效果不能发动并无效化。结束阶段时，这张卡放置的魔石指示物全部取除。','去除指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67237709,'星际仙踪-翠玉都',replace(replace('①：1回合1次，以除外的1只自己的「星际仙踪」怪兽为对象才能发动。那只怪兽回到手卡，自己失去那只怪兽的原本等级×100基本分。\r\n②：1回合1次，自己主要阶段才能发动。手卡的「星际仙踪」怪兽任意数量给对方观看，回到卡组洗切。那之后，自己从卡组抽出回到卡组的数量。\r\n③：场地区域的这张卡被效果破坏的场合才能发动。从卡组把1张「星际仙踪」卡加入手卡。','\r',char(13)),'\n',char(10)),'除外的「星际仙踪」怪兽回到手卡','手卡的「星际仙踪」怪兽回到卡组并抽卡','从卡组把「星际仙踪」卡加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67249508,'龙星的凶暴化','①：自己的「龙星」怪兽和对方怪兽进行战斗的伤害计算时才能发动。那只自己怪兽的攻击力·守备力只在那次伤害计算时变成原本数值的2倍，伤害步骤结束时破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67267333,'复仇死者·地狱犬',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡在墓地存在的场合，从手卡丢弃1张「复仇死者」卡才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。\r\n②：使用场上的这张卡仪式召唤的「复仇死者」怪兽得到以下效果。\r\n●1回合1次，以对方场上1张魔法·陷阱卡为对象才能发动。那张卡除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'这张卡特殊召唤','对方魔法·陷阱卡除外（复仇死者·地狱犬）','「复仇死者·地狱犬」效果适用中','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67270095,'涡轮同调士',replace(replace('①：这张卡向攻击表示怪兽攻击宣言时才能发动。攻击对象怪兽变成守备表示。\r\n②：这张卡的攻击让自己受到战斗伤害时才能发动。把受到的战斗伤害数值以下的攻击力的1只怪兽从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'攻击对象变成守备表示','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67273917,'点火骑士·德林加',replace(replace('←2 【灵摆】 2→\r\n①：另一边的自己的灵摆区域有「点火骑士」卡存在的场合才能发动。自己的灵摆区域的卡全部破坏，从自己的卡组·墓地选1只战士族·炎属性怪兽加入手卡。\r\n【怪兽描述】\r\n被同伴们娇纵惯了的点火骑士一点红。她自豪的武器射中同伴比敌人还要多，能提出意见的却连一个也没有。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67284107,'替罪鬼羊','①：这张卡反转的场合才能发动。在自己场上把「黑羊衍生物」（不死族·暗·1星·攻/守0）任意数量特殊召唤。','特殊召唤衍生物','是否继续特殊召唤「黑羊衍生物」？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67284108,'黑羊衍生物','由「替罪鬼羊」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67284908,'迷宫壁','出现结界般的厚壁，制造出没有出口的迷宫。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67287533,'死之信息「A」','这张卡不用「通灵盘」的效果不能在场上出现。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67300516,'真红眼飞龙',replace(replace('这张卡的效果发动的回合，自己不能通常召唤。\r\n①：自己结束阶段把墓地的这张卡除外，以自己墓地1只「真红眼」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67310848,'矮星龙 恒行星龙','①：这张卡召唤的回合的结束阶段才能发动。从卡组把1只光属性或者暗属性的7星怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67316075,'堕天使护士-蕾菲库儿','只要这张卡在场上表侧表示存在，对方基本分回复的效果变成给与对方基本分伤害的效果。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67328336,'机皇城','自己场上表侧表示存在的名字带有「机皇帝」的怪兽不会成为同调怪兽的效果的对象。场上存在的这张卡被破坏送去墓地时，可以从自己卡组把1只名字带有「机皇帝」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67371383,'半鱼兽','在陆地像兽一样，在海里像鱼一样都能快速攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67378104,'呼星天仪台',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：从手卡以及自己场上的表侧表示怪兽之中让1只6星怪兽回到持有者卡组最下面才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67381587,'黑猫瞪眼',replace(replace('①：自己场上有里侧守备表示怪兽2只以上存在的场合，对方战斗阶段才能发动。那次战斗阶段结束。\r\n②：把墓地的这张卡除外，以包含「占术姬」怪兽的场上2只表侧表示怪兽为对象才能发动。那些怪兽变成里侧守备表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67385964,'剑斗兽 罪斗',replace(replace('①：对方怪兽的直接攻击宣言时才能发动。这张卡从手卡特殊召唤，那只对方怪兽的攻击对象转移为这张卡进行伤害计算。这张卡不会被那次战斗破坏。\r\n②：这张卡用「剑斗兽」怪兽的效果特殊召唤成功的场合才能发动。从卡组把1只「剑斗兽」怪兽送去墓地。\r\n③：这张卡进行战斗的战斗阶段结束时让这张卡回到持有者卡组才能发动。从卡组把「剑斗兽 罪斗」以外的1只「剑斗兽」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67441435,'成长的鳞茎',replace(replace('这个卡名的效果在决斗中只能使用1次。\r\n①：这张卡在墓地存在的场合才能发动。自己卡组最上面的卡送去墓地，这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67443336,'裁决的天秤',replace(replace('「裁决的天秤」在1回合只能发动1张。\r\n①：对方场上的卡数量比自己的手卡·场上的卡合计数量多的场合才能发动。自己从卡组抽出那个相差的数量。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67445676,'松果小矮人','这张卡被「小矮人橡子」的效果送去墓地的场合，这张卡可以从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67464807,'命运之宝牌','这张卡只能在对方回合发动。选择对方场上1只以表侧表示存在的怪兽。下一个自己的回合，若自己成功召唤出比被选择的怪兽低1颗星的怪兽，则召唤成功时对对方造成被选择的怪兽等级×500点伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67466547,'空牙团的舵手 赫耳玛',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。从手卡把「空牙团的舵手 赫耳玛」以外的1只「空牙团」怪兽特殊召唤。\r\n②：这张卡已在怪兽区域存在的状态，自己场上有这张卡以外的「空牙团」怪兽特殊召唤的场合，从手卡丢弃1张「空牙团」卡才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67468948,'救济之莱亚德','①：只要这张卡在怪兽区域存在，每次自己或者对方把反击陷阱卡发动，选除外的2只自己的天使族怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67483216,'次世代搜索员','这张卡被战斗破坏送去墓地时，可以从自己卡组把1只攻击力1500以下的名字带有「次世代」的怪兽在自己场上表侧攻击表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67489919,'幻兽机 蓝冲高角羚','把这张卡作为同调素材的场合，不是机械族怪兽的同调召唤不能使用，其他的同调素材怪兽必须是自己的手卡·场上的名字带有「幻兽机」的怪兽。只要自己场上有衍生物存在，这张卡不会被战斗以及效果破坏。此外，对方场上有怪兽存在，自己场上没有怪兽存在的场合，把墓地的这张卡从游戏中除外才能发动。把1只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。','特殊召唤Token','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67489920,'幻兽机衍生物','由「幻兽机 蓝冲高角羚」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67494157,'伏地龙','力量弱小无法飞天的龙，但攻击还是很强的。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67503139,'占术姬 水晶球水精','①：这张卡反转的场合才能发动。从自己的卡组·墓地选1只仪式怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67511500,'龙魔女-龙之守护者-','只要这张卡在场上表侧表示存在，对方不能选择自己场上的龙族怪兽作为攻击对象。场上的这张卡被战斗或者卡的效果破坏的场合，可以作为代替从手卡把1只龙族怪兽送去墓地。','是否要使用「龙魔女-龙之守护者-」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67532912,'科学特殊兵','为对抗未知生物，装备着最新科学武器的士兵。背后的集装箱里藏着各种各样的兵器。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67547370,'睡巨人 咕咚',replace(replace('这张卡不能通常召唤。把有咕咚指示物10个放置的1只自己怪兽解放的场合才能特殊召唤。\r\n①：1回合1次，把手卡的这张卡直到回合结束时给对方观看，以自己场上1只1星通常怪兽为对象才能发动。给那只怪兽放置1个咕咚指示物。\r\n②：这张卡不受其他卡的效果影响。\r\n③：这张卡和怪兽进行战斗的伤害计算时发动。这张卡的攻击力·守备力只在伤害计算时变成那只怪兽的攻击力＋1000的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67556500,'波动龙 声子龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡同调召唤成功时，宣言1～3的等级才能发动。这张卡的等级变成宣言的等级。这个效果发动过的回合，自己不能作以这张卡为同调素材的同调召唤以外的特殊召唤。自己对「波动龙 声子龙」1回合只能有1次特殊召唤。','\r',char(13)),'\n',char(10)),'等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67559101,'先史遗产 泥土人偶·遮光器','只要这张卡在场上表侧表示存在，自己场上的名字带有「先史遗产」的怪兽向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67586735,'刚鬼 双人夹击猪',replace(replace('①：这张卡作为「刚鬼」连接怪兽的连接素材送去墓地的场合才能发动。那只「刚鬼」连接怪兽的攻击力直到回合结束时上升1000。\r\n②：把墓地的这张卡除外，以自己墓地1张「刚鬼」魔法卡为对象才能发动。那张卡回到卡组。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67598234,'电影之骑士 盖亚剑士','怪兽2只以上','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67616300,'试胆竞速',replace(replace('①：只要这张卡在场地区域存在，比对方基本分少的玩家受到的全部伤害变成0。\r\n②：双方玩家1回合1次，自己主要阶段可以支付1000基本分从以下效果选择1个发动。双方不能对应这个效果的发动把魔法·陷阱·怪兽的效果发动。\r\n●从卡组抽1张。\r\n●这张卡破坏。\r\n●对方回复1000基本分。','\r',char(13)),'\n',char(10)),'从卡组抽1张','这张卡破坏','对方回复1000基本分','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67629977,'超级流星','只要这张卡在场上表侧表示存在，全部光属性的怪兽攻击力上升500。暗属性的怪兽攻击力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67630339,'混乱箔片','同1次的战斗阶段中宣言第2次的直接攻击时才能发动。那只对方怪兽和直接攻击过的第1只对方怪兽战斗进行伤害计算。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67646312,'探照灯人','反转：这个回合对方玩家不能在场上把卡进行盖伏操作。','盖卡限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67675300,'雪尘龙','这张卡可以把场上4个冰指示物取除，从手卡特殊召唤。只要这张卡在场上表侧表示存在，这张卡以外的有冰指示物放置的怪兽不能攻击，也不能作表示形式的变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67688478,'双生恶魔','包含有把自己手卡丢弃的效果的卡对方发动时，可以把这张卡从手卡送去墓地使那个发动和效果无效，把那张卡破坏然后从自己卡组抽1张卡。','效果无效并抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67692580,'乌爪鸦','自己场上有暗属性怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法的「乌爪鸦」的特殊召唤1回合只能有1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67696066,'娱乐法师 戏法小丑',replace(replace('「娱乐法师 戏法小丑」的效果1回合只能使用1次。\r\n①：这张卡被送去墓地的场合，以自己墓地1只「娱乐法师」怪兽为对象才能发动。那只怪兽攻击力·守备力变成0特殊召唤。那之后，自己受到1000伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67723438,'紧急瞬间移动','①：从手卡·卡组把1只3星以下的念动力族怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合的结束阶段除外。','「紧急瞬间移动」的效果特殊召唤的怪兽除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67724379,'恶魔龙','凶恶的龙。吐出邪恶的火焰使人变得邪恶。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67744384,'廷达魔三角之使徒',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡反转的场合才能发动。选自己场上最多3只里侧表示怪兽变成表侧守备表示。这个效果变成表侧守备表示的怪兽全部是「廷达魔三角」怪兽的场合，可以把最多有那些怪兽数量的「廷达魔三角」卡从卡组加入手卡。','\r',char(13)),'\n',char(10)),'变成表侧守备表示','是否把卡加入手卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67750322,'骷髅大王','①：对方墓地有魔法·陷阱·怪兽的效果发动时，把这张卡从手卡送去墓地才能发动。那个效果无效。','墓地的卡发动的效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67754901,'异色眼幻象龙',replace(replace('←8 【灵摆】 8→\r\n①：1回合1次，自己场上的表侧表示的「异色眼」灵摆怪兽被战斗·效果破坏的场合才能发动。选自己的灵摆区域1张卡破坏，从自己的额外卡组选「异色眼幻象龙」以外的1只表侧表示的「异色眼」灵摆怪兽在自己的灵摆区域放置。\r\n【怪兽效果】\r\n「异色眼幻象龙」的怪兽效果1回合只能使用1次。\r\n①：自己的灵摆区域有「异色眼」卡存在的场合，以自己场上1只「异色眼」怪兽为对象才能发动。那只怪兽在这个回合只有1次不会被战斗·效果破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67757079,'毛兽之王','可以把1只自己场上表侧表示存在的「毛扎」解放发动。这张卡从手卡或者墓地特殊召唤。「毛兽之王」在场上只能有1只表侧表示存在。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67775894,'神奇魔杖',replace(replace('魔法师族怪兽才能装备。\r\n①：装备怪兽的攻击力上升500。\r\n②：把装备怪兽和这张卡从自己场上送去墓地才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67779172,'黑羽射击','从手卡把1只名字带有「黑羽」的怪兽送去墓地，选择对方场上守备表示存在的1只怪兽发动。选择的怪兽送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67808837,'娱乐伙伴 国王熊精',replace(replace('←7 【灵摆】 7→\r\n①：这张卡发动的回合的结束阶段才能发动。这张卡破坏，从自己的额外卡组的表侧表示的灵摆怪兽以及自己墓地的怪兽之中选1只7星以上的怪兽加入手卡。\r\n【怪兽效果】\r\n①：攻击表示的这张卡不会被魔法·陷阱卡的效果破坏。\r\n②：这张卡的攻击力在自己战斗阶段内上升自己场上的「娱乐伙伴」卡数量×100。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67820935,'肥大化','①：以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力·守备力变成2倍，不能直接攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67829249,'古代的采掘机','自己场上有名字中带有「古代的机械」的怪兽表侧表示存在的场合，丢弃1张手卡才能发动。从卡组选择1张魔法卡，在自己场上盖伏。这个回合这张魔法卡不能使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67831115,'投射于星遗物的暗影',replace(replace('①：场上的「机怪虫」怪兽的攻击力·守备力上升300。\r\n②：1回合1次，自己主要阶段才能发动。从手卡把1只2星以下的昆虫族怪兽表侧守备表示或者里侧守备表示特殊召唤。\r\n③：自己的反转怪兽被和对方怪兽的战斗破坏时才能发动。那只对方怪兽送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67841515,'枪管百合','所谓的铁炮百合。就是射出花粉攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67901914,'魔弹-无尽内啡肽',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己场上1只「魔弹」怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时变成原本数值的2倍。这张卡发动的回合，作为对象的怪兽不能直接攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67904682,'暗黑航空母舰',replace(replace('暗属性调整＋调整以外的机械族怪兽1只以上\r\n1回合1次，选择自己墓地1只名字带有「反应机」的怪兽或者「巨人轰炸机·大空袭式」才能发动。选择的怪兽无视召唤条件特殊召唤。此外，这张卡被破坏送去墓地的场合，可以从手卡把1只5星以下的机械族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67922702,'幻兽机 绳狼',replace(replace('①：这张卡召唤成功的场合发动。在自己场上把1只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。\r\n②：这张卡的等级上升自己场上的「幻兽机衍生物」的等级的合计数值。\r\n③：只要自己场上有衍生物存在，这张卡不会被战斗·效果破坏。\r\n④：这张卡和对方怪兽进行战斗的从伤害步骤开始时到伤害计算前1次，把自己场上1只衍生物解放才能发动。这张卡的攻击力直到回合结束时上升800。','\r',char(13)),'\n',char(10)),'特殊召唤Token','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67922703,'幻兽机衍生物','由「幻兽机 绳狼」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67926903,'混沌超量 冀望皇 异晶人',replace(replace('7星怪兽×3只以上\r\n这张卡也能在卡名包含「混沌No.101」～「混沌No.107」其中任意1种的自己场上的怪兽上面叠放来超量召唤。这张卡的攻击力上升这张卡的超量素材数量×1000的数值。选择自己墓地1只名字带有「No.」的怪兽才能发动。直到下次的对方的结束阶段时，这张卡当作和选择的怪兽同名卡使用，得到相同效果。「混沌超量 冀望皇 异晶人」的这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'是否要在自己场上的怪兽上面叠放来超量召唤？','效果复制','复制效果结束','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67934141,'超热血球儿','场上有这张卡以外的炎属性怪兽存在的场合，每有1张，这张卡的攻击力上升1000。每把这张卡以外的1只炎属性怪兽送去墓地，给与对方基本分500分的伤害。','给与对方1000伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67949763,'罪键之法','选择自己场上1只超量怪兽才能发动。把3只「阴影蜃景衍生物」（恶魔族·暗·1星·攻?/守0）特殊召唤。这衍生物的攻击力变成和选择的怪兽的攻击力相同。这衍生物不能直接攻击，不能为上级召唤以外而解放。选择的怪兽从场上离开时，这个效果特殊召唤的衍生物全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67949764,'阴影蜃景衍生物','由「罪键之法」的效果特殊召唤。这衍生物不能直接攻击，不能为上级召唤以外而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67951831,'英雄之心','选择自己场上1只表侧表示存在的名字带有「元素英雄」的怪兽发动。这个回合选择的怪兽的攻击力变成一半，同1次战斗阶段中可以攻击2次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67957315,'灵魂龙','这张卡在自己的回合战斗的场合，在那个战斗阶段时可以发动效果。手卡每丢弃1只龙族怪兽去墓地，在结束阶段前这张卡的攻击力·守备力上升1000。','攻守上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67959180,'任性的女神','①：1回合1次，自己主要阶段才能发动。进行1次投掷硬币，对里表作猜测。猜中的场合，这张卡的攻击力直到回合结束时变成2倍。猜错的场合，这张卡的攻击力直到回合结束时变成一半。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67964209,'神龙 阿库阿巴扎','把这张卡以外的自己场上表侧表示存在的1只水属性怪兽解放，选择自己墓地存在的1张永续魔法或者场地魔法卡发动。选择的卡从自己墓地回到卡组最上面。这个效果1回合只能使用1次。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67968069,'废品傀儡','选择自己墓地1只名字带有「机关傀儡」的怪兽才能发动。选择的怪兽特殊召唤。「废品傀儡」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67985943,'宝石骑士·橙黄晶',replace(replace('「宝石骑士」怪兽＋炎族怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。\r\n①：这张卡进行战斗的场合，对方直到伤害步骤结束时魔法·陷阱·怪兽的效果不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67987302,'地缚大神官','只要这张卡在场上表侧表示存在，名字带有「地缚神」的怪兽不会被自身的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(67987611,'亚马逊弩弓队','只有在对方进行攻击宣言，且自己场上存在名称中含有「亚马逊」字样的怪兽时这张卡才能发动。将对方场上所有怪兽都变成表向攻击表示（此时反转不发动），攻击力下降500点。对方所有的怪物都必须进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68001309,'地中族的激斗',replace(replace('①：自己场上的「地中族」怪兽的攻击力·守备力上升场上的里侧表示怪兽数量×500。\r\n②：1回合1次，自己的「地中族」怪兽给与对方战斗伤害时，以「地中族的激斗」以外的自己墓地1张「地中族」卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68005187,'灵魂交错',replace(replace('这张卡发动的回合，自己不能进行战斗阶段。\r\n①：以对方场上1只怪兽为对象才能发动。这个回合自己把怪兽解放的场合，必须作为1只自己怪兽的代替而把作为对象的对方怪兽解放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68007326,'守护天使 贞德','这张卡战斗破坏怪兽送去墓地时，自己基本分回复破坏的怪兽的原本攻击力的数值。','LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68018709,'失乐之圣女','只要这张卡在场上表侧表示存在，自己在对方回合可以从手卡把1张速攻魔法卡发动。「失乐之圣女」的效果在决斗中只能使用1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68024506,'魔弹射手 灾星',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，自己·对方回合自己可以把「魔弹」魔法·陷阱卡从手卡发动。\r\n②：和这张卡相同纵列有魔法·陷阱卡发动的场合，以自己墓地1只「魔弹」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'墓地苏生','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68049471,'梦魔的亡灵','进入熟睡者的梦中吸取其生命力的恶魔，被附身的人无法自己醒来。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68054593,'燃烧的斗志','发动后这张卡变成装备卡，给自己场上表侧表示存在的1只怪兽装备。攻击力比原本攻击力高的怪兽在对方场上存在的场合，装备怪兽的攻击力在伤害步骤内变成原本攻击力的2倍。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68057622,'反击机枪拳','若被攻击的守备怪兽的守备力比对方攻击怪兽的攻击力高，则这只攻击怪兽被破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68073522,'魂吸收','将卡从游戏中除外时，每除外1张，这张卡的控制者自己回复500基本分。','恢复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68077936,'融爆',replace(replace('①：自己场上的卡被魔法卡的效果破坏时，以对方场上1张卡为对象才能发动。那张卡破坏。\r\n②：自己场上的卡被魔法卡的效果破坏时，把墓地的这张卡除外，以对方场上1张卡为对象才能发动。那张卡破坏。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68084557,'机械龙 动力工具',replace(replace('调整＋调整以外的怪兽1只以上\r\n自己回合让这张卡把装备魔法卡装备时，可以从卡组抽1张卡。「机械龙 动力工具」的这个效果1回合只能使用1次。此外，1回合1次，可以选择场上表侧表示存在的1张装备卡转移给作为正确对象的这张卡。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'抽卡','装备转移','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68087897,'鳞粉蛾','只要这张卡场上表侧表示存在，双方玩家1回合只能1次把怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68120130,'废品防御者','对方怪兽的直接攻击宣言时，这张卡可以从手卡特殊召唤。此外，1回合1次，可以把这张卡的守备力直到结束阶段时上升300。这个效果在对方回合也能发动。','特殊召唤','守备上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68124775,'东方龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡同调召唤成功时，选择对方场上1只同调怪兽从游戏中除外。','\r',char(13)),'\n',char(10)),'除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68140974,'机皇帝 神智∞','这张卡不能通常召唤。自己场上表侧表示存在的怪兽被效果破坏送去墓地时才能从手卡特殊召唤。1回合1次，可以把对方1只同调怪兽当作装备卡使用给这张卡装备。这张卡的攻击力上升这个效果装备的怪兽的攻击力数值。只要这张卡在场上表侧表示存在，这张卡以外的自己怪兽不能攻击宣言。此外，1回合1次，可以把对方的魔法卡的发动无效并破坏。','特殊召唤','装备同调怪兽','魔法无效并破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68144350,'燃烧拳击手 左右开弓拳手','这张卡召唤成功时，可以选择自己墓地1只名字带有「燃烧拳击手」的怪兽特殊召唤。这个效果发动的回合，自己不能把名字带有「燃烧拳击手」的怪兽以外的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68167124,'针剑士','这张卡给与对方基本分战斗伤害时，对方的魔法与陷阱卡区域表侧表示存在的卡全部回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68170903,'佯动作战','这个回合，双方不能向里侧表示怪兽攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68171737,'岩石龙','完全由岩石组成的龙。岩石的攻击很强。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68182934,'德拉科尼亚的翼龙骑兵',replace(replace('←7 【灵摆】 7→\r\n①：1回合1次，自己的通常怪兽给与对方战斗伤害时，以场上1张卡为对象才能发动。那张卡破坏。\r\n【怪兽描述】\r\n龙人族国家德拉科尼亚帝国所拥有的龙骑士团空兵部队。有传闻说是为了对空中都市国家苏鲁伯这个中立国进行入侵而结成，使得周边国家对此加强警戒。','\r',char(13)),'\n',char(10)),'破坏1张卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68184115,'甲虫装机 豆娘',replace(replace('①：1回合1次，自己主要阶段才能发动。从自己的手卡·墓地选1只「甲虫装机」怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡在自己场上存在，给这张卡装备的卡被送去自己墓地的场合才能发动。从卡组把「甲虫装机 豆娘」以外的1只「甲虫装机」怪兽特殊召唤。\r\n③：把这张卡当作装备卡使用来装备的怪兽的等级上升3星。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68191243,'黑蝎团召集','当自己场上存在表侧表示的「首领 扎鲁格」时这张卡才能发动。可以从手卡里将名称中含有「黑蝎」字样的怪兽全部特殊召唤上场。（同名怪兽只能特殊召唤1只）','是否要继续特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68191756,'星遗物的机忆',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：从手卡·卡组把1只「机界骑士」怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽在结束阶段回到持有者手卡。这张卡的发动后，直到回合结束时自己不是「机界骑士」怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68215963,'宝玉兽 翠玉龟','可以把这个回合中进行过攻击的自己场上存在的1只怪兽变成守备表示。这个效果1回合只能使用1次。这张卡在怪兽卡区域上被破坏的场合，可以不送去墓地当作永续魔法卡使用在自己的魔法与陷阱卡区域表侧表示安置。','破坏时放置魔法陷阱区','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68226653,'炎狱连射龙','这张卡被战斗破坏送去墓地时，给与对方基本分500分伤害。可以把自己墓地存在的这张卡和自己场上表侧表示存在的1只炎属性怪兽从游戏中除外，从自己卡组把1只「炎狱连射龙」加入手卡。','给予对方500伤害','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68246154,'魔弹射手 医生',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，自己·对方回合自己可以把「魔弹」魔法·陷阱卡从手卡发动。\r\n②：和这张卡相同纵列有魔法·陷阱卡发动的场合才能发动。从自己墓地选和那张发动的卡卡名不同的1张「魔弹」卡加入手卡。','\r',char(13)),'\n',char(10)),'墓地回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68280530,'王家骑士','这张卡战斗破坏怪兽送去墓地时，自己基本分回复破坏怪兽的守备力的数值。','LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68299524,'瘟疫龙',replace(replace('①：1回合1次，支付100的倍数的基本分才能发动。这张卡以外的场上的表侧表示怪兽的攻击力下降因为这个效果发动而支付的基本分数值。\r\n②：1回合1次，以持有这张卡的攻击力以下的攻击力的场上1只怪兽为对象才能发动。那只怪兽破坏。\r\n③：这张卡被战斗·效果破坏的场合发动。场上的全部表侧表示怪兽的攻击力下降1000。','\r',char(13)),'\n',char(10)),'攻击力下降','怪兽破坏','全部怪兽攻击力下降1000','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68304813,'冥界的宝札','①：自己作把怪兽2只以上解放的上级召唤成功的场合发动。自己从卡组抽2张。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68319538,'宇宙城 哥尔加',replace(replace('「外星菊石」＋调整以外的名字带有「外星」的怪兽1只以上\r\n1回合1次，可以让场上表侧表示存在的魔法·陷阱卡任意数量回到持有者手卡，把那个数量的A指示物给场上存在的怪兽放置。1回合1次，可以把场上存在的2个A指示物取除，对方场上存在的1张卡破坏。','\r',char(13)),'\n',char(10)),'表侧表示存在的魔法·陷阱卡任意数量回到持有者手牌。','对方场上存在的1张卡破坏。','请选择要放置「A指示物」的怪兽','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68334074,'奇迹之复活','从自己场上除去2个魔力指示物。从自己墓地里特殊召唤1只「黑魔术师」或者「巴斯达·布雷达」上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68339286,'金属守护者','守护魔界宝物的恶魔在黑暗中守备相当强。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68353324,'迅捷河狸','①：这张卡召唤成功时才能发动。从自己的卡组·墓地选1只3星以下的「迅捷」怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68366996,'二重士兵',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡1回合只有1次不会被战斗破坏。这张卡进行战斗的场合，那次伤害计算后可以从自己卡组把「二重士兵」以外的1只4星以下的二重怪兽在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68371799,'恶魔将星',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：自己场上有「恶魔」卡存在的场合，这张卡可以从手卡特殊召唤。这个回合，这张卡不能攻击。\r\n②：这张卡的①的方法特殊召唤成功的场合，以自己场上1张「恶魔」卡为对象发动。那张自己的「恶魔」卡破坏。\r\n③：这张卡上级召唤成功时，以自己墓地1只6星「恶魔」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68392533,'念动增幅装置','念动力族怪兽才能装备。为装备怪兽的效果发动而支付的基本分变成不需要。装备怪兽被破坏让这张卡送去墓地时，可以支付1000基本分让这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68395509,'威风妖怪·乌',replace(replace('←5 【灵摆】 5→\r\n【怪兽效果】\r\n「威风妖怪·乌」的①的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功时才能发动。从卡组把1张「威风妖怪」魔法卡加入手卡。\r\n②：这张卡只要在怪兽区域存在，不会成为对方的效果的对象，不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68396121,'混沌No.107 超银河眼时空龙',replace(replace('9星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除才能发动。这张卡以外的场上表侧表示存在的全部卡的效果直到回合结束时无效，这个回合，对方不能把场上的卡的效果发动。此外，这张卡有「No.107 银河眼时空龙」在作为超量素材的场合，得到以下效果。\r\n●把自己场上2只怪兽解放才能发动。这个回合这张卡在同1次的战斗阶段中最多3次可以向怪兽攻击。','\r',char(13)),'\n',char(10)),'效果无效','多次攻击','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68396778,'给恶魔的贡物','场上存在的1只特殊召唤的怪兽送去墓地，从自己手卡把1只4星以下的通常怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68400115,'皇帝的新装','全部装备卡的效果无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68401546,'妖精的赠礼','据说能使任何人幸福的魔法一边飞一边散发着。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68406755,'三形金字塔的斯芬克斯',replace(replace('这张卡不能通常召唤，用「三形金字塔」卡的效果才能特殊召唤。\r\n①：「三形金字塔的斯芬克斯」以外的自己场上的表侧表示的「三形金字塔」卡被战斗·效果破坏的场合才能发动。这张卡从手卡特殊召唤。\r\n②：自己场上有其他的「三形金字塔」卡存在的场合，这张卡的攻击力·守备力上升自己墓地的场地魔法卡种类×500，对方怪兽只能向「三形金字塔的斯芬克斯」攻击。','\r',char(13)),'\n',char(10)),'这张卡特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68427465,'破邪之大剑-黄昏','从手卡送去墓地1张卡，这张卡才能进行装备。装备这张卡的怪兽攻击力上升500点。被装备这张卡的怪兽战斗破坏的效果怪兽的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68431965,'流星登龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡同调召唤成功的场合才能发动。把持有比场上的这张卡低的等级的1只怪兽从卡组送去墓地，这张卡的等级下降那只怪兽的等级数值。这个回合，自己不能把送去墓地的那只怪兽以及那些同名怪兽的怪兽效果发动。\r\n②：对方主要阶段才能发动。用包含这张卡的自己场上的怪兽为同调素材作同调召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68450517,'盟军·次世代同暗兵','1回合1次，自己的主要阶段时才能发动。选择持有和这张卡相同属性的对方场上表侧攻击表示存在的1只怪兽破坏。这个效果发动的回合这张卡不能攻击。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68456353,'木龙兽之牙','自己场上有名字带有「自然」的怪兽表侧表示存在的场合才能发动。对方发动的魔法·陷阱卡的发动无效并破坏。那之后，把自己1张手卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68462976,'魔法族之里','只有自己场上才有魔法师族怪兽存在的场合，对方不能把魔法卡发动。自己场上没有魔法师族怪兽存在的场合，自己不能把魔法卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68473226,'硬甲龙',replace(replace('①：这张卡可以把手卡1只8星以上的怪兽送去墓地，从手卡特殊召唤。\r\n②：把这张卡解放作召唤的7星以上的怪兽不会被效果破坏。','\r',char(13)),'\n',char(10)),'「硬甲龙」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68477598,'灵摆洞','①：自己或者对方把怪兽灵摆召唤之际才能发动。那次灵摆召唤无效，那些怪兽回到持有者卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68505803,'次世代控制员','与同伴们能心意相通的少数次世代一员。可是能控制各种元素力量的哟。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68507541,'亚马逊宠物虎狮子',replace(replace('「亚马逊宠物虎」＋「亚马逊」怪兽\r\n①：这张卡攻击的伤害计算时才能发动。这张卡的攻击力只在那次伤害计算时上升500。\r\n②：自己的「亚马逊」怪兽向对方怪兽攻击的伤害计算后以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力下降800。\r\n③：只要这张卡在怪兽区域存在，对方不能向其他的「亚马逊」怪兽攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68516705,'人马兽','人与马化身的怪兽，奔跑的速度谁也追不上。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68535320,'火焰手','①：这张卡被对方破坏送去墓地时，以对方场上1只怪兽为对象才能发动。那只对方怪兽破坏。那之后，可以从卡组把1只「寒冰手」特殊召唤。','破坏并特殊召唤','是否要特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68540058,'金属化·魔法反射装甲',replace(replace('①：以场上1只表侧表示怪兽为对象才能把这张卡发动。这张卡当作攻击力·守备力上升300的装备卡使用给那只怪兽装备。\r\n②：用这张卡的效果把这张卡装备的怪兽的攻击力只在向怪兽攻击的伤害计算时上升那只攻击对象怪兽的攻击力一半的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68540059,'金属化·魔法反射装甲',replace(replace('①：以场上1只表侧表示怪兽为对象才能把这张卡发动。这张卡当作攻击力·守备力上升300的装备卡使用给那只怪兽装备。\r\n②：用这张卡的效果把这张卡装备的怪兽的攻击力只在向怪兽攻击的伤害计算时上升那只攻击对象怪兽的攻击力一半的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68543408,'星尘小龙','自己对「星尘龙」的同调召唤成功时，自己墓地存在的这张卡可以在自己场上表侧攻击表示特殊召唤。这张卡1回合只有1次不会被战斗破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68597372,'发条机甲 发条大王',replace(replace('4星怪兽×2\r\n这张卡的攻击力上升这张卡的超量素材数量×300的数值。1回合1次，把这张卡1个超量素材取除，选择自己场上表侧表示存在的1只怪兽才能发动。选择的怪兽变成里侧守备表示。这个回合的结束阶段时，选择的怪兽变成表侧攻击表示。','\r',char(13)),'\n',char(10)),'改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68601507,'武神器-羽羽斩','自己场上的名字带有「武神」的兽战士族怪兽和对方怪兽进行战斗的伤害计算时，把这张卡从手卡送去墓地才能发动。进行战斗的自己怪兽的攻击力只在那次伤害计算时变成原本攻击力的2倍。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68618157,'武神姬-天照',replace(replace('4星怪兽×3\r\n「武神姬-天照」在自己场上只能有1只表侧表示存在。1回合1次，把这张卡1个超量素材取除才能发动。发动的回合的以下效果适用。这个效果在对方回合也能发动。\r\n●自己回合：选择从游戏中除外的1只自己的4星以下的怪兽特殊召唤。\r\n●对方回合：选择从游戏中除外的1只自己的4星以下的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68625727,'占术姬 花瓣精灵','①：这张卡反转的场合才能发动。对方场上的表侧攻击表示怪兽全部变成守备表示。这个效果变成守备表示的怪兽不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68638985,'蛙形史莱姆','青蛙头形的史以恐怖的歌声作为攻击手段。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68658728,'小奇美拉','只要这张卡在场上表侧表示存在，全部炎属性的怪兽攻击力上升500。水属性的怪兽攻击力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68661341,'一点式买法','这张卡以外的手卡有3张以上存在，那之中没有怪兽卡存在的场合，把手卡全部表侧表示从游戏中除外才能发动。从卡组把1只怪兽加入手卡。这个回合，自己不能把加入手卡的同名怪兽以外的怪兽召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68663748,'命运未来','选择从游戏中除外的1只自己的名字带有「命运女郎」的怪兽回到墓地。那之后，从自己卡组抽2张卡。','请选择要返回墓地的卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68670547,'暗龙之黑骑士','1回合1次，可以从对方墓地把1只被战斗破坏的4星以下的不死族怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68688135,'吸血鬼的支配',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：自己场上有「吸血鬼」怪兽存在，怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。那之后，破坏的卡是怪兽卡的场合，自己回复那个原本攻击力数值的基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68722455,'赤鬼','这张卡召唤成功时，可以把自己手卡任意数量送去墓地，那个数量的场上的卡回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68745629,'元素英雄 爆裂火焰侠',replace(replace('「元素英雄 炽热侠」＋「元素英雄 火焰女郎」\r\n这只怪兽不能作融合召唤以外的特殊召唤。和水属性怪兽战斗的场合，伤害步骤内这只怪兽的攻击力上升1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68762510,'幸运之吹笛人',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当通常召唤使用的再度召唤，这张卡当作效果怪兽使用并得到以下效果。\r\n●这张卡战斗破坏对方怪兽送去墓地时，从自己卡组抽1张卡。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68774379,'电子障壁龙','这张卡不能通常召唤。这张卡只能通过「攻击反射装置」的效果特殊召唤。这张卡攻击表示的场合，1回合1次，可以把1只对方怪兽的攻击无效。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68786330,'攻击信息素','自己场上存在的爬虫族怪兽向守备表示怪兽攻击的场合，那只怪兽在伤害步骤结束时变成表侧攻击表示。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68809475,'核成高速鸟','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只机械族怪兽给对方观看。或者都不进行让这张卡破坏。只要这张卡在场上表侧表示存在，自己的抽卡阶段时抽到的卡是「核成兽的钢核」的场合，可以把那张卡给对方观看让自己再抽1张卡。','选择一张「核成兽的钢核」送去墓地','选择一张机械族怪兽给对方观看','破坏「核成高速鸟」','抽卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(68811206,'伟大的战士 泰勒','这张卡不能特殊召唤。当这张卡战斗破坏怪兽并将它送去墓地时，给与对方等于被破坏怪兽的攻击力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68812773,'吉器美',replace(replace('①：这张卡召唤·反转时，以自己墓地1只「轨道7」为对象才能发动。那只怪兽表侧攻击表示或者里侧守备表示特殊召唤。\r\n②：1回合1次，把自己场上的机械族怪兽任意数量解放才能发动。把解放数量的「光子」怪兽或者「银河」怪兽从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68815132,'鹰身舞者','自己的主要阶段时，选择自己场上1只风属性怪兽才能发动。选择的怪兽回到持有者手卡，那之后，可以把1只风属性怪兽召唤。「鹰身舞者」的这个效果1回合只能使用1次。这张卡的卡名只要在场上·墓地存在当作「鹰身女郎」使用。','返回手牌','是否要选择1只风属性怪兽召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68815401,'狂焰','支付500基本分。自己场上表侧表示存在的名字带有「烈焰加农炮」的卡破坏，场上的怪兽全部破坏。之后，在自己场上攻击表示特殊召唤1只「狂焰衍生物」（炎族·炎·3星·攻/守1000）。这个回合自己怪兽不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68815402,'狂焰衍生物','由「狂焰」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68819554,'娱乐法师 伤害杂耍人',replace(replace('「娱乐法师 伤害杂耍人」的③的效果1回合只能使用1次。\r\n①：给与自己伤害的魔法·陷阱·怪兽的效果发动时，把这张卡从手卡丢弃才能发动。那个发动无效并破坏。\r\n②：自己或者对方的战斗阶段把这张卡从手卡丢弃才能发动。这个回合自己受到的战斗伤害只有1次变成0。\r\n③：自己主要阶段把墓地的这张卡除外才能发动。从卡组把「娱乐法师 伤害杂耍人」以外的1只「娱乐法师」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'发动无效并破坏','战斗伤害变成0','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68823957,'地中族刀匠',replace(replace('①：只让自己场上的里侧表示怪兽1只成为对方的效果的对象时或者被选择作为对方怪兽的攻击对象时，把手卡·场上的这张卡送去墓地，以那只里侧表示怪兽以外的自己场上1只怪兽为对象才能发动。那个对象转移为作为正确对象的那只怪兽。\r\n②：只要自己场上有里侧表示怪兽存在，这张卡不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68833958,'一骑加势','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升1500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68836428,'三刃戟海龙神',replace(replace('3星怪兽×3\r\n这张卡战斗破坏的怪兽不去墓地从游戏中除外。此外，1回合1次，把这张卡1个超量素材取除，选择场上表侧表示存在的1只怪兽才能发动。直到回合结束时，选择的怪兽的攻击力下降800，效果无效化。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'除外','效果无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68846917,'岩窟魔人','岩石之躯成就高强的守备力。挥起巨腕时务必警惕。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68860936,'廷达魔三角之天使',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡反转的场合才能发动。从自己的手卡·墓地选「廷达魔三角之天使」以外的1只反转怪兽里侧守备表示特殊召唤。这个效果在对方回合的战斗阶段发动的场合，再把那次战斗阶段结束。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68870276,'绝对鸟','可以将对方吸入镜中世界。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68875140,'哥布林推销员','每次自己场上的怪兽给与对方玩家战斗伤害，对方的魔法与陷阱卡区域存在的1张卡回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(68881649,'凭依装着-艾莉娅',replace(replace('可以把自己场上1只「水灵使 艾莉娅」和1只水属性怪兽送去墓地，从手卡·卡组特殊召唤。这个方法特殊召唤成功的场合，获得下面的效果。\r\n●这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
