INSERT INTO texts VALUES(69170557,'混沌No.40 机关傀儡-魔界弦乐手',replace(replace('9星怪兽×3\r\n这张卡特殊召唤成功时，场上的有线指示物放置的怪兽全部破坏，自己从卡组抽1张卡。那之后，给与对方基本分这个效果破坏送去墓地的怪兽之内原本攻击力最高的怪兽的那个数值的伤害。此外，1回合1次，把这张卡1个超量素材取除才能发动。给对方场上表侧表示存在的全部怪兽放置1个线指示物。','\r',char(13)),'\n',char(10)),'破坏','放置指示物','','','','','','','','','','','','','','');
