INSERT INTO texts VALUES(69207766,'甲虫装机 大黄蜂',replace(replace('①：1回合1次，自己主要阶段才能发动。从自己的手卡·墓地选1只「甲虫装机」怪兽当作装备卡使用给这张卡装备。\r\n②：把这张卡当作装备卡使用来装备的怪兽的等级上升3星，攻击力·守备力上升这张卡的各自数值。\r\n③：把给怪兽装备的这张卡送去墓地，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'装备','场上1张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69211541,'娱乐伙伴 搭档蛇',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，以自己场上1只怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升自己场上的「娱乐伙伴」卡数量×300。\r\n【怪兽效果】\r\n①：这张卡召唤·特殊召唤成功的场合，以自己场上1只怪兽为对象才能发动。那只怪兽的攻击力上升自己场上的「娱乐伙伴」怪兽数量×300。\r\n②：只要这张卡在怪兽区域存在，5星以下的怪兽不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69217334,'世界破坏',replace(replace('①：1回合1次，以自己场上1只仪式怪兽为对象才能发动。手卡1只仪式怪兽直到回合结束时公开。那只公开的仪式怪兽的等级直到回合结束时变成和作为对象的怪兽的等级相同。\r\n②：1回合1次，自己场上有「破灭之女神 露茵」或者「终焉之王 迪米斯」仪式召唤的场合，可以从以下效果选择1个发动。\r\n●自己从卡组抽1张。\r\n●选场上1张卡破坏。','\r',char(13)),'\n',char(10)),'自己从卡组抽1张','选场上1张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69228245,'娱乐伙伴 变形链长颈鹿',replace(replace('←5 【灵摆】 5→\r\n①：自己怪兽1只被战斗破坏时才能发动。这张卡破坏，那只战斗破坏的怪兽攻击表示特殊召唤。这个效果特殊召唤的怪兽在这个回合不会被战斗破坏。\r\n【怪兽效果】\r\n①：这张卡召唤·特殊召唤成功时，以对方场上1只表侧表示怪兽为对象才能发动。这只怪兽表侧表示存在期间，作为对象的表侧表示怪兽不能攻击，效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69230391,'爆炎帝 泰斯塔罗斯',replace(replace('这张卡可以把1只上级召唤的怪兽解放作上级召唤。这张卡上级召唤成功时，把对方手卡确认并丢弃1张。丢弃的卡是怪兽卡的场合，给与对方基本分那只怪兽的等级×200的数值的伤害。这张卡把炎属性怪兽解放作上级召唤成功的场合，那个时候的效果加上以下效果。\r\n●给与对方基本分1000分伤害。','\r',char(13)),'\n',char(10)),'把1只上级召唤的怪兽解放作上级召唤','确认手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69243722,'宝石骑士·翠玉','①：把自己场上1只表侧表示的通常怪兽和这张卡除外，以自己墓地1只「宝石骑士」融合怪兽为对象才能发动。那只怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69243953,'蝶之短剑-回音','装备这张卡的怪兽攻击力上升300点。装备在怪兽身上的这张卡被破坏送去墓地时，这张卡可以弹回持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69247929,'基因狼人','被遗传基因操作所强化的狼人。本来的和善之心已被完全破坏，肉体变成只为战斗而生。那个破坏力无可估量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69257165,'精神污染','从手卡丢弃1只怪兽才能发动。选择持有和那只怪兽相同等级的对方场上1只怪兽直到结束阶段时得到控制权。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69270537,'接触分离','自己场上表侧表示存在的1只名字带有「新宇」的融合怪兽回到额外卡组。并且，回到额外卡组的怪兽记述的1组融合素材怪兽若在自己卡组齐集，可以把这1组在自己场上特殊召唤。','是否要特殊召唤一组融合素材？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69279219,'我身作盾','当对方发动持有「破坏场上怪兽」效果的卡时，支付1500点基本分，使其发动无效并且将其破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69293721,'水精鳞-深渊昆德','这张卡从手卡丢弃去墓地的场合，可以从自己墓地选择「水精鳞-深渊昆德」以外的1只名字带有「水精鳞」的怪兽特殊召唤。「水精鳞-深渊昆德」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69296555,'光之护封阵','宣言1个种族。宣言的种族的全部怪兽召唤·反转召唤·特殊召唤的回合不能进行攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69303178,'光神机-樱火','这张卡可以不用祭品作召唤。这个方法召唤的场合，这张卡在结束阶段时送去墓地。','不用祭品召唤','送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69304426,'古遗物-金刚杵',replace(replace('①：这张卡可以当作魔法卡使用从手卡到魔法与陷阱区域盖放。\r\n②：魔法与陷阱区域盖放的这张卡在对方回合被破坏送去墓地的场合发动。这张卡特殊召唤。\r\n③：对方回合，这张卡特殊召唤成功的场合发动。自己的魔法与陷阱区域的卡全部破坏。\r\n④：对方怪兽的直接攻击宣言时才能发动。这张卡从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69313735,'闪击','祭掉自己场上1只名称中含有「恶魔」字样的怪兽。本回合，自己场上的1只「灭绝国王恶魔」可以对对方进行直接攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69319869,'钝重','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时下降那只怪兽的守备力数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69320362,'传说的发条','自己场上表侧表示存在的名字带有「发条」的怪兽全部变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69327790,'烈风帝 莱扎',replace(replace('这张卡可以把1只上级召唤的怪兽解放作上级召唤。\r\n①：这张卡上级召唤成功的场合，以场上1张卡和自己或者对方的墓地1张卡为对象发动。那些卡用喜欢的顺序回到持有者卡组最上面。这张卡把风属性怪兽解放作上级召唤成功的场合，那个时候的效果加上以下效果。\r\n●可以以场上1张卡为对象回到持有者手卡。','\r',char(13)),'\n',char(10)),'把1只上级召唤的怪兽解放作上级召唤','返回卡组最上面','是否要选择场上1张卡回到手卡？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69351984,'炼装勇士·钨火',replace(replace('←8 【灵摆】 8→\r\n①：1回合1次，以这张卡以外的自己场上1张表侧表示的卡为对象才能发动。那张卡破坏，从卡组选1张「炼装」魔法·陷阱卡在自己场上盖放。\r\n【怪兽描述】\r\n拥有赤热魂钢的上级战士。就在与将世界带向终结的红色真龙这威胁对峙的时候，他得到仿佛彼此呼应而穿越次元出现的光之意志引导，使身披百炼钢甲之术迎来了绽放时刻。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69380702,'小白兔','非常喜欢甜食的甜食派兔子。在找据说是世界第一甜的甘糖胡萝卜，无论今天还是明天都想要啃胡萝卜。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69402394,'暗黑的谋略','双方玩家选择2张手卡丢弃，从卡组抽2张卡。对方可以丢弃1张手卡让这张卡的效果无效。','是否要丢弃1张手卡让「暗黑的谋略」的效果无效？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69408987,'蜘蛛网','怪兽攻击宣言的场合，那只怪兽在伤害步骤结束时变成守备表示，直到从那只怪兽的控制者来看的下次的自己回合的结束阶段时不能把表示形式改变。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69448290,'霞之谷的雷鸟','场上表侧表示存在的这张卡回到手卡时，这张卡在自己场上特殊召唤。这个效果特殊召唤成功的回合，这张卡不能进行攻击。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69452756,'长眠不醒的噩梦',replace(replace('「长眠不醒的噩梦」的效果在同一连锁上只能发动1次。\r\n①：支付1000基本分，以场上1张表侧表示的魔法·陷阱卡为对象才能把这个效果发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69455834,'暗魔界的霸王','使用强大的暗之力量，破坏周围的一切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69456283,'此者',replace(replace('1回合1次，在自己的主要阶段，这张卡可以视为1张装备卡装备在自己的「彼者」上，或者解除装备状态表侧攻击表示特殊召唤。当这张卡以自身效果变成装备卡时具有以下效果\r\n●装备怪兽的攻击力上升3000点。装备怪兽攻击守备表示的怪兽时，攻击力超出部分对对方造成战斗伤害。（1只怪兽至多只能装备1张同盟。当装备了同盟的怪兽被战斗破坏时，同盟会代替怪兽被破坏。）','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69461394,'正义盟军 陆军元帅',replace(replace('调整＋调整以外的怪兽2只以上\r\n这张卡不能作同调召唤以外的特殊召唤。这张卡战斗破坏里侧守备表示怪兽送去墓地时，这张卡的控制者可以从自己卡组抽1张卡。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69488544,'凤凰剑圣 基亚·弗里德',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●对方把魔法卡发动的场合，可以选择自己墓地存在的1只二重怪兽特殊召唤。此外，可以把自己场上表侧表示存在的1张装备卡送去墓地，场上存在的怪兽为对象的魔法·陷阱卡的发动无效并破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69512157,'龙魔王 魔道矢·灵摆',replace(replace('←3 【灵摆】 3→\r\n①：只要这张卡在灵摆区域存在，对方的灵摆区域的卡的效果无效化。\r\n【怪兽描述】\r\n统率着突然出现在这个世上并转眼就彻底蹂躏了世界的大群龙魔族的魔王。据说他是以“龙化秘法”转变了万物成为邪恶的龙形，不过那种力量的真面目还不怎么清楚。强大魔力的源泉甚至有传闻说是不属于这个次元的。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69514125,'神圣骑士 珀耳修斯',replace(replace('调整＋调整以外的光属性怪兽1只以上\r\n①：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。那只对方的表侧表示怪兽的表示形式变更。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69529337,'三形金字塔的舞者',replace(replace('①：1回合1次，以自己墓地1张「三形金字塔」卡为对象才能发动。那张卡回到卡组，自己场上的岩石族怪兽的攻击力·守备力上升500。\r\n②：对方回合1次，以自己场上1张「三形金字塔」场地魔法卡为对象才能发动。那张卡送去墓地，从卡组把和那张卡卡名不同的1张「三形金字塔」场地魔法卡发动。','\r',char(13)),'\n',char(10)),'攻守变化','场地魔法卡发动','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69529567,'装傻负鼠','自己的主要阶段时，持有比这张卡的攻击力高的攻击力的怪兽在对方场上表侧表示存在的场合，可以把场上存在的这张卡破坏。此外，自己的准备阶段时，这张卡的效果破坏的这张卡可以从墓地特殊召唤。','这张卡破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69537999,'烈焰加农炮','可以从手卡把1只攻击力500以下的炎族怪兽送去墓地，对方场上存在的1只怪兽破坏。这个效果使用的回合，自己怪兽不能攻击。','对方场上存在的1只怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69542930,'光与暗之洗礼','①：把自己场上1只「黑魔术师」解放才能发动。从自己的手卡·卡组·墓地选1只「混沌之黑魔术师」特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69572024,'舌鱼','用长舌头抓住其他的鱼，吸取能量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69572169,'元素英雄 电闪侠','这张卡被战斗破坏送去墓地时，可以从自己墓地把这张卡和名字带有「元素英雄」的怪兽卡3种类从游戏中除外，选择自己墓地存在的1张通常魔法卡加入手卡。','魔法回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69579761,'死亡树熊','反转：对方每拥有1张手卡，就受到400点伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69584564,'芳香风暴','把场上表侧表示存在的1只植物族怪兽破坏，从自己卡组抽1张卡。并且，这个效果抽到的卡是植物族怪兽的场合，可以把那张卡给双方确认自己再抽1张卡。','是否要给对方确认抽到的卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69599136,'无底的落穴','①：对方对怪兽的召唤·反转召唤·特殊召唤成功时才能发动。那些怪兽变成里侧守备表示。这个效果变成里侧守备表示的怪兽不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69610326,'霸王眷龙 暗黑亚龙',replace(replace('←5 【灵摆】 5→\r\n①：1回合1次，自己场上没有怪兽存在的场合才能发动。从卡组选1只「霸王门」灵摆怪兽在自己的灵摆区域放置。这个效果的发动后，直到回合结束时自己不是暗属性怪兽不能灵摆召唤。\r\n【怪兽效果】\r\n「霸王眷龙 暗黑亚龙」的①②的怪兽效果1回合各能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1只「霸王门」灵摆怪兽加入手卡。\r\n②：这张卡在墓地存在，自己场上没有怪兽存在的场合才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69610924,'No.17 海恶龙',replace(replace('3星怪兽×2\r\n1回合1次，可以把这张卡1个超量素材取除，这张卡的攻击力上升500。这张卡的超量素材没有的场合，这张卡不能向对方玩家直接攻击。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69632396,'哥布林的敷衍了事','支付500基本分。魔法卡的发动无效，那张卡回到持有者的手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69633792,'进化龙·依潘龙','这张卡用名字带有「进化虫」的怪兽的效果特殊召唤成功时，这张卡的攻击力下降500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69669405,'小鬼','住在黑暗的小鬼。攻击意外的强。要注意它头上的角。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69695704,'血风燕','这张卡的攻击力上升自己场上表侧表示存在的鸟兽族怪兽数量×100的数值。自己场上有鸟兽族怪兽表侧表示3只以上存在的场合，可以把对方场上存在的1张魔法·陷阱卡破坏。这个效果1回合只能使用1次。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69700783,'吸血鬼欲望',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：可以从以下效果选择1个发动。\r\n●以自己场上1只表侧表示怪兽为对象才能发动。把持有和那只怪兽的等级不同等级的1只「吸血鬼」怪兽从卡组送去墓地。作为对象的怪兽的等级直到回合结束时变成和送去墓地的怪兽相同。\r\n●以自己墓地1只「吸血鬼」怪兽为对象才能发动。选自己场上1只怪兽送去墓地，作为对象的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'改变等级','墓地苏生','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69711728,'炼装联合',replace(replace('①：1回合1次，融合怪兽融合召唤的场合，以比那融合怪兽等级低的自己墓地1只「炼装」怪兽为对象才能把这个效果发动。那只怪兽特殊召唤。\r\n②：这张卡从场上送去墓地的场合才能发动。从卡组把1只「炼装」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'发动但不使用效果','发动并使用①效果','加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69723159,'武神器-丛云','自己的主要阶段时，自己场上有名字带有「武神」的兽战士族怪兽存在的场合，把墓地的这张卡从游戏中除外才能发动。选择对方场上表侧表示存在的1张卡破坏。「武神器-丛云」的效果1回合只能使用1次。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69724380,'魔之取引','对方的魔法卡发动时支付1000基本分才能发动。对方随机丢弃1张手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69750536,'鱼战士','拥有手和脚的鱼人兽，用尖锐的牙齿撕咬敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69750546,'火山鹿弹','这张卡被送去墓地时，给与对方基本分500分伤害。这张卡被名字带有「烈焰加农炮」的卡的效果送去墓地的场合，可以从手卡·卡组把2只「火山鹿弹」送去墓地，对方场上怪兽全部破坏。','给予对方500伤害','对方场上怪兽全部破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69757518,'混沌No.5 亡胧龙 混沌嵌合龙',replace(replace('6星怪兽×3只以上\r\n①：这张卡的攻击力上升这张卡的超量素材数量×1000。\r\n②：这张卡进行攻击的伤害步骤结束时，把这张卡1个超量素材取除才能发动。这张卡向对方怪兽可以继续攻击。\r\n③：战斗阶段结束时，把基本分支付一半，以自己·对方的墓地的卡合计2张为对象才能发动。那之内的1张卡在持有者卡组最上面放置，另1张在这张卡下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'再次攻击','请选择要返回卡组的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69764158,'舔舔三舌犬','①：这张卡在墓地存在，战斗或者对方的效果让自己受到伤害的场合，把墓地的这张卡除外，以场上1张卡为对象才能发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69780745,'加尔瓦斯','恶之化身。样子如同长着羽毛的狮子。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69831560,'秘仪之力EX-暗之支配者',replace(replace('这张卡不能通常召唤。把自己场上存在的3只怪兽送去墓地的场合才能特殊召唤。这张卡特殊召唤成功时，进行1次投掷硬币得到以下效果。\r\n●表：这张卡在战斗阶段中可以作2次攻击。进行这个效果适用的第2次战斗的场合，这张卡在战斗阶段结束时变成守备表示。直到下次的自己回合结束时这张卡不能把表示形式改变。\r\n●里：这张卡被破坏的场合，场上的卡全部破坏。','\r',char(13)),'\n',char(10)),'投掷硬币','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69832741,'灵魂消灭',replace(replace('（注：暂时无法正常使用）\r\n\r\n墓地的怪兽从游戏中除外的场合，用自己场上的怪兽从游戏中除外代替。这个效果发动的回合有效。','\r',char(13)),'\n',char(10)),'请选择要代替除外的怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69838592,'妖仙兽 大幽谷响',replace(replace('「妖仙兽 大幽谷响」的①的效果1回合只能使用1次。\r\n①：对方怪兽的直接攻击宣言时从手卡把「妖仙兽 大幽谷响」以外的1只「妖仙兽」怪兽送去墓地才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡和对方怪兽进行战斗的伤害步骤开始时才能发动。这张卡的攻击力·守备力直到回合结束时变成和进行战斗的对方怪兽的原本攻击力相同。\r\n③：这张卡被战斗破坏送去墓地时才能发动。从卡组把1张「妖仙兽」卡加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','攻守变化','从卡组把1张「妖仙兽」卡加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69840739,'古遗物-恒常剑',replace(replace('5星怪兽×2\r\n1回合1次，可以从以下效果选择1个发动。这个效果在对方回合也能发动。\r\n●在场上有怪兽的效果发动时或者在通常魔法·通常陷阱卡的发动时把这张卡1个超量素材取除才能发动。那个效果变成「选对方场上1张魔法·陷阱卡破坏」。\r\n●把这张卡1个超量素材取除才能发动。双方手卡全部回到持有者卡组洗切。那之后，各自抽出回到卡组的数量。','\r',char(13)),'\n',char(10)),'效果变化','手卡扰乱','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69846323,'贯通海鳝','1回合1次，自己的主要阶段时把这张卡以外的自己场上1只鱼族·海龙族·水族怪兽解放才能发动。这张卡的攻击力上升600。此外，这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69865139,'天轮之葬送士','①：这张卡召唤成功时，以自己墓地1只光属性·1星怪兽为对象才能发动。那只光属性怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69868555,'巨神龙的遗迹',replace(replace('①：自己场上有7·8星的龙族怪兽存在，从墓地以外有怪兽特殊召唤的场合发动。那些怪兽的效果直到回合结束时无效化。\r\n②：1回合1次，把这张卡以外的自己场上1张表侧表示的卡送去墓地才能发动。在自己场上把1只「巨龙衍生物」（龙族·光·1星·攻/守0）特殊召唤。\r\n③：这张卡在墓地存在的场合，把自己的手卡·场上1只7·8星的龙族怪兽送去墓地才能发动。这张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69868556,'巨龙衍生物','由「巨神龙的遗迹」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69884162,'元素英雄 次新宇侠',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡只要在怪兽区域存在，卡名当作「元素英雄 新宇侠」使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69890967,'幻魔皇 拉比艾尔',replace(replace('这张卡不能通常召唤。把自己场上3只恶魔族怪兽解放的场合才能特殊召唤。\r\n①：1回合1次，把这张卡以外的自己场上1只怪兽解放才能发动。这张卡的攻击力直到回合结束时上升解放的怪兽的原本攻击力数值。\r\n②：对方对怪兽的召唤成功的场合发动。在自己场上把1只「幻魔衍生物」（恶魔族·暗·1星·攻/守1000）特殊召唤。这衍生物不能攻击宣言。','\r',char(13)),'\n',char(10)),'特殊召唤Token','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69890968,'幻魔衍生物','由「幻魔皇 拉比艾尔」的效果特殊召唤。这个衍生物不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69893315,'骰子犰狳','卷起身子就像骰子一样的犰狳。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69931927,'地缚神 查库·查略亚','名字带有「地缚神」的怪兽在场上只能有1只表侧表示存在。场上没有表侧表示场地魔法卡存在的场合这张卡破坏。对方不能选择这张卡作为攻击对象。这张卡可以直接攻击对方玩家。1回合1次，可以给与对方基本分这张卡的守备力一半数值的伤害。这个效果发动的回合，这张卡不能攻击。此外，这张卡在场上表侧守备表示存在的场合，对方不能进行战斗阶段。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69933858,'传说的战士 吉尔福德','这张卡不能特殊召唤。这张卡召唤成功时，可以把自己墓地存在的装备卡尽可能装备到自己场上的战士族怪兽上。','装备','请选择装备魔法卡','请选择要装备的怪兽','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69937550,'宝玉兽 琥珀猛犸','自己场上表侧表示存在的名字带有「宝玉兽」的怪兽被选择作为攻击对象时，可以把攻击对象改变为这张卡。这张卡在怪兽卡区域上被破坏的场合，可以不送去墓地当作永续魔法卡使用在自己的魔法与陷阱卡区域表侧表示安置。','破坏时放置魔法陷阱区','转移攻击目标','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69946549,'捕食植物 犀角龙',replace(replace('融合怪兽＋暗属性怪兽\r\n①：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。给那只怪兽放置1个捕食指示物。有捕食指示物放置的2星以上的怪兽的等级变成1星。这个效果在对方回合也能发动。\r\n②：只要这张卡在怪兽区域存在，对方发动的有捕食指示物放置的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69954399,'疫病病毒 黑尘','装备这张卡的怪兽不能攻击。装备怪兽的控制者的第2次回合结束时，装备怪兽破坏。这个效果成功的场合，这张卡回到持有者的手卡。','破坏','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69982329,'灵摆转动','①：以自己或者对方的灵摆区域1张卡为对象，宣言1～10的灵摆刻度才能发动。这个回合，那张卡变成宣言的灵摆刻度。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(69992868,'缪斯天使','艺术家的天使。尤其琴技无人能出其右。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70000776,'王家长眠之谷的祭殿','场上有名字带有「守墓」的怪兽以及「王家长眠之谷」存在的场合才能发动。只要这张卡在场上存在，双方不能把名字带有「守墓」的怪兽以外的怪兽特殊召唤。名字带有「守墓」的怪兽以及「王家长眠之谷」不在场上存在的场合，这张卡破坏。','是否要使用「超量超控」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70026064,'武神-蛭子',replace(replace('←3 【灵摆】 3→\r\n①：把自己的灵摆区域的这张卡除外，以自己场上1只「武神」超量怪兽为对象才能发动。和那只自己怪兽卡名不同的1只「武神」超量怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。\r\n【怪兽描述】\r\n遥远的太古过去赌上主神的宝座跟「武神-日孁」战斗，在死斗之后被封印起来的恶神。为使自己的封印解开而操纵了「日孁」，产生出不祥的「天照」给世界带来了黑暗，但他的野心在「倭」等年轻武神的活跃之下最终破灭了。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70043345,'超级运动员受罚',replace(replace('「超级运动员受罚」的①的效果1回合只能使用1次。\r\n①：自己的「超级运动员」怪兽和对方怪兽进行战斗的伤害步骤开始时才能把这个效果发动。那只对方怪兽直到发动后第2次的对方结束阶段除外。\r\n②：把墓地的这张卡除外才能发动。从卡组把1张「超级运动员」魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'除外的怪兽回到场上','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70046172,'突进','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升700。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70050374,'双管手枪龙','这张卡召唤·反转召唤·特殊召唤成功时，选择对方场上存在的1张卡发动。进行2次投掷硬币，2次都是表的场合，选择的卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70054514,'暗海救生圈','场上存在的这张卡被卡的效果破坏送去墓地时，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70074904,'D.D.暗杀者','①：这张卡被和对方怪兽的战斗破坏的伤害计算后发动。那只怪兽和这张卡除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70083592,'缝合苏生',replace(replace('「缝合苏生」在1回合只能发动1张。\r\n①：以自己墓地1只「毛绒动物」怪兽或者「魔玩具」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70083723,'自然蜻蜓','这张卡不会被和攻击力2000以上的怪兽的战斗破坏。这张卡的攻击力上升自己墓地存在的名字带有「自然」的怪兽数量×200的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70084224,'猎头魔人','挥舞着大镰刀的恶魔。从大眼睛中还会发出光柱。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70089580,'极光翼鸟','这张卡被战斗破坏送去墓地时，这张卡可以表侧攻击表示特殊召唤。「极光翼鸟」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70095154,'电子龙','①：只有对方场上才有怪兽存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70095155,'电子龙','①：只有对方场上才有怪兽存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70101178,'豹鲨','对方场上的怪兽是2只以上的场合，这张卡可以不用解放作召唤。此外，自己场上有「鹰鲨」存在的场合，这张卡可以从手卡特殊召唤。「豹鲨」在自己场上只能有1只表侧表示存在。','不解放进行召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70117860,'风魔女-雪铃',replace(replace('①：自己场上有风属性怪兽2只以上存在，没有风属性以外的怪兽存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：用这张卡为同调素材把风属性同调怪兽同调召唤的场合，那只同调怪兽不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','「风魔女-雪铃」效果适用中','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70124586,'拘束足',replace(replace('①：这张卡召唤·反转召唤·特殊召唤成功的场合发动。场上盖放的魔法·陷阱卡全部回到持有者手卡。\r\n②：把墓地的这张卡除外，以对方场上盖放的1张魔法·陷阱卡为对象才能发动。直到下次的对方回合结束时那张卡不能发动。','\r',char(13)),'\n',char(10)),'回到手卡','不能发动','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70138455,'炸弹先生','在自己的准备阶段时发动。表侧表示的这张卡作为祭品，选择2只表侧表示的攻击力1000以下的怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70147689,'古代的机械要塞',replace(replace('①：自己场上的「古代的机械」怪兽在召唤·特殊召唤的回合不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：对方不能对应「古代的机械」卡的效果的发动把魔法·陷阱·怪兽的效果发动。\r\n③：魔法与陷阱区域的这张卡被破坏的场合才能发动。从自己的手卡·墓地选1只「古代的机械」怪兽特殊召唤。这个效果的发动后，直到回合结束时自己不是「古代的机械」怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70156997,'地灵术-「铁」','自己场上存在的1只地属性怪兽做祭品。从自己墓地选择1只祭品怪兽以外的4星以下的地属性怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70180284,'影六武众-道治',replace(replace('①：这张卡已在怪兽区域存在的状态，自己场上有这张卡以外的「六武众」怪兽召唤·特殊召唤时才能发动。从卡组把1张「六武众」卡送去墓地。\r\n②：只让自己场上的「六武众」怪兽1只被效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70187958,'暗黑沙貘','这张卡从游戏中除外时，可以选择自己墓地存在的1只4星以下的兽族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70194827,'利刃保镖','这张卡进行攻击的伤害步骤结束时，对方场上有怪兽存在的场合，丢弃1张手卡才能发动。这次战斗阶段中，这张卡只有1次可以向对方怪兽再攻击。这个效果1回合只能使用1次。','再次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70222318,'森罗的灵峰','自己的主要阶段时把手卡或者自己场上表侧表示存在的1只植物族怪兽送去墓地才能发动。从卡组选1张名字带有「森罗」的卡在卡组最上面放置。「森罗的灵峰」的这个效果1回合只能使用1次。此外，对方的结束阶段时只有1次，可以把自己卡组最上面的卡翻开。翻开的卡是植物族怪兽的场合，那张卡送去墓地。不是的场合，那张卡回到卡组最上面或者最下面。','选择一张名字带有「森罗」的卡','放置卡组最上面','放置卡组最下面','检索','','','','','','','','','','','','');
INSERT INTO texts VALUES(70231910,'黑核','丢弃1张手卡。将场上1张表侧表示的怪兽卡从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70238111,'重编码存活',replace(replace('①：以自己的场上·墓地1只连接3电子界族连接怪兽为对象才能发动。那只怪兽除外，从额外卡组把1只「码语者」怪兽特殊召唤。\r\n②：额外怪兽区域没有自己怪兽存在的场合，把墓地的这张卡除外，以除外的1只自己的「码语者」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70245411,'玩具罐',replace(replace('①：1回合1次，丢弃1张手卡才能发动。自己从卡组抽1张，给双方确认。确认的卡是「毛绒动物」怪兽的场合，可以从手卡把1只怪兽特殊召唤。不是的场合，那张抽到的卡丢弃。\r\n②：这张卡被送去墓地的场合才能发动。从卡组把1只「锋利小鬼·剪刀」或者1只「毛绒动物」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'抽1张卡','卡组1只怪兽加入手卡','是否特殊召唤？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70261145,'自然郁金香','只要这张卡在自己场上表侧表示存在，每次对方把魔法·陷阱卡发动，自己场上表侧表示存在的名字带有「自然」的怪兽的攻击力直到那个回合的结束阶段时上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70271583,'机巧守卫 三一三','这张卡可以攻击的场合必须作出攻击。场上表侧表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变更。这张卡的战斗让自己受到战斗伤害时，自己场上表侧表示存在的名字带有「机巧」的全部怪兽的攻击力·守备力直到结束阶段时上升800。此外，这张卡只要在场上表侧攻击表示存在不会被战斗破坏。','表示形式变更','攻守上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70278545,'谦虚之壶','选择自己2张手卡放回卡组。之后卡组洗切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70284332,'调律师的阴谋','对方场上有同调怪兽特殊召唤时才能发动。得到那1只同调怪兽的控制权。这个效果得到控制权的怪兽破坏的场合从游戏中除外。那只怪兽不在场上存在时，这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70298454,'比翼连鳞',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只表侧表示怪兽为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：装备怪兽的原本攻击力变成1000，同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70307656,'咒怨','这张卡可以直接攻击对方玩家。这张卡给与对方玩家战斗伤害的场合，下次的自己的准备阶段时这张卡的攻击力上升1000。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70329348,'炎舞-「天权」','这张卡在主要阶段1才能发动。这张卡的发动时，选择自己场上1只兽战士族怪兽。只在这张卡发动的主要阶段1内，选择的怪兽的效果无效，不受这张卡以外的卡的效果影响。此外，只要这张卡在场上存在，自己场上的兽战士族怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70342110,'次元幽闭','对方怪兽的攻击宣言时才能发动。那1只攻击怪兽从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70344351,'力场','以场上的1只怪兽为对象的魔法的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70345785,'三头巨龙','用三个头不断地喷火，使周围化为一片火海以攻击敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70355994,'勇炎星-猿胜','1回合1次，这张卡战斗破坏对方怪兽送去墓地时，可以从卡组选1张名字带有「炎舞」的魔法卡在自己场上盖放。此外，1回合1次，把自己场上表侧表示存在的1张名字带有「炎舞」的魔法·陷阱卡送去墓地才能发动。选择场上1张魔法·陷阱卡破坏。','盖放','魔陷破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70368879,'哥布林暴发户','①：自己从卡组抽1张。那之后，对方回复1000基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70391588,'星食-等级攀升-','同调怪兽特殊召唤时，选择那1只怪兽发动。在自己场上把1只「星食衍生物」（魔法师族·暗·1星·攻/守0）特殊召唤。这衍生物的等级变成和选择的怪兽相同的等级，选择的怪兽的等级变成1星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70391589,'星食衍生物','由「星食-等级攀升-」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70406920,'机械王-B.C.3000','这张卡发动后变成怪兽卡（机械族·地·4星·攻/守1000）在自己的怪兽卡区域特殊召唤。只在这个效果特殊召唤的场合，1回合1次，可以把自己场上存在的1只机械族怪兽解放，这张卡的攻击力直到结束阶段时上升解放的怪兽的攻击力数值。这张卡发动的回合，自己不能把怪兽召唤·特殊召唤。（这张卡也当作陷阱卡使用）','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70410002,'累加器人','①：这张卡的攻击力上升场上的连接怪兽的连接标记数量×300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70422863,'星守之结界',replace(replace('①：场上的「星守」超量怪兽的攻击力·守备力上升那超量素材数量×200。\r\n②：自己场上的「星守」超量怪兽被选择作为攻击对象时，把手卡1张「星守」卡送去墓地才能发动。那次攻击无效。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70423794,'变形斗士·电线','名字带有「变形斗士」的怪兽才能装备。每次装备怪兽的表示形式改变，场上存在的1张魔法或者陷阱卡破坏。','1张魔法或者陷阱卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70456282,'强袭黑羽-雾雨之苦无鸟',replace(replace('①：这张卡可以把自己场上1只「黑羽」怪兽解放从手卡特殊召唤。这个方法特殊召唤的这张卡当作调整使用。\r\n②：1回合1次，以自己场上1只同调怪兽为对象，宣言1～8的任意等级才能发动。那只怪兽直到回合结束时变成宣言的等级。','\r',char(13)),'\n',char(10)),'宣言等级','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70458081,'娱乐伙伴 迅雷犀牛',replace(replace('①：只要这张卡在怪兽区域存在，对方不能选择其他的「娱乐伙伴」怪兽作为攻击对象。\r\n②：这张卡在墓地存在，「娱乐伙伴 迅雷犀牛」以外的自己场上的「娱乐伙伴」卡被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'是否使用「娱乐伙伴 迅雷犀牛」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70479321,'娱乐伙伴 打鼓金刚',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，自己怪兽和对方怪兽进行战斗的攻击宣言时，以那1只自己怪兽为对象才能发动。那只怪兽的攻击力直到战斗阶段结束时上升600。\r\n【怪兽效果】\r\n①：双方场上没有怪兽存在的场合，这张卡可以不用解放作召唤。\r\n②：不用解放作召唤的这张卡的等级变成4星。\r\n③：1回合1次，自己怪兽和对方怪兽进行战斗的攻击宣言时，以那1只自己怪兽为对象才能发动。那只怪兽的攻击力直到战斗阶段结束时上升600。','\r',char(13)),'\n',char(10)),'不用解放作召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70493141,'雷仙神',replace(replace('①：这张卡可以支付3000基本分从手卡特殊召唤。\r\n②：这张卡的①的方法特殊召唤的这张卡被对方破坏的场合发动。自己回复5000基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70546737,'阴影鬼火','这张卡召唤·特殊召唤成功时，选择这张卡以外的自己的场上·墓地1只名字带有「阴影」的怪兽才能发动。这张卡的等级变成和选择的怪兽的等级相同。此外，场上表侧攻击表示存在的这张卡被战斗破坏送去墓地时，把让这张卡破坏的怪兽破坏。','等级变化','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70560957,'卡通倒带','①：以自己场上1只卡通怪兽为对象才能发动。这个回合，那只怪兽在同1次的战斗阶段中可以作2次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70564929,'魔界台本「魔界的宴咜女」',replace(replace('这个卡名的①的效果1回合可以使用最多2次。\r\n①：把自己场上1只「魔界剧团」怪兽解放，以自己墓地1张「魔界台本」魔法卡为对象才能发动。那张卡在自己场上盖放。\r\n②：自己的额外卡组有表侧表示的「魔界剧团」灵摆怪兽存在，盖放的这张卡被对方的效果破坏的场合才能发动。从卡组把「魔界剧团」灵摆怪兽任意数量特殊召唤。','\r',char(13)),'\n',char(10)),'墓地魔法卡盖放','卡组怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70583986,'冰结界的虎王 雪虎',replace(replace('调整＋调整以外的水属性怪兽1只以上\r\n1回合1次，可以让自己场上表侧表示存在的卡任意数量回到手卡。这张卡的攻击力直到这个回合的结束阶段时上升这个效果回到手卡的卡数量×500的数值。','\r',char(13)),'\n',char(10)),'返回手牌，攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70595331,'地狱的门番 伊尔·布拉德',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当通常召唤使用的再度召唤，这张卡当作效果怪兽使用并得到以下效果。\r\n●1回合1次，可以把手卡·自己或者对方墓地存在的1只不死族怪兽特殊召唤。这张卡从场上离开时，这个效果特殊召唤的不死族怪兽破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70624184,'星圣·候','把这张卡解放才能发动。从自己的手卡·墓地把「星圣·候」以外的1只名字带有「星圣」的怪兽表侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70628672,'红色机人呼唤',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：自己场上有「机人」融合怪兽存在，怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效。那之后，从发动的那个玩家的卡组·额外卡组把同名卡全部送去墓地。\r\n②：把墓地的这张卡除外，以自己墓地1只「机人」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70630741,'救援战士','这张卡的战斗发生的对自己的战斗伤害变成0。这张卡被战斗破坏的场合，选择对方场上表侧表示存在的1只原本持有者是自己的怪兽得到控制权。','获得控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70645913,'吸血鬼的眷属',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡特殊召唤成功的场合，支付500基本分才能发动。从卡组把1张「吸血鬼」魔法·陷阱卡加入手卡。\r\n②：这张卡在墓地存在的场合，从手卡以及自己场上的表侧表示的卡之中把1张「吸血鬼」卡送去墓地才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70655556,'鱼叉鲨','这张卡召唤成功时，可以让自己场上的全部鱼族·3星怪兽的等级上升1星。这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70668285,'光天使 天剑','1回合1次，把手卡1只名字带有「光天使」的怪兽送去墓地才能发动。这张卡的攻击力直到结束阶段时上升送去墓地的怪兽的原本攻击力数值。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70676581,'暗黑将军 弗里德','这张卡不能特殊召唤。自己场上表侧表示存在的暗属性怪兽为对象的魔法卡的效果无效并破坏。只要这张卡在场上表侧表示存在，可以作为自己的抽卡阶段时进行通常抽卡的代替，从自己卡组把1只4星的暗属性怪兽加入手卡。','魔法无效并破坏','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70681994,'魔装骑士 德拉格尼斯','「铠甲剑尾战士」＋「独眼盾龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70771599,'霸王眷龙 幻透翼',replace(replace('调整＋调整以外的暗属性灵摆怪兽1只以上\r\n①：这张卡同调召唤成功的场合才能发动。对方场上的表侧表示怪兽全部破坏。\r\n②：1回合1次，这张卡和对方怪兽进行战斗的伤害计算前才能发动。那只怪兽破坏，给与对方破坏的怪兽的原本攻击力数值的伤害。\r\n③：这张卡在墓地存在的场合，把自己场上2只「霸王眷龙」怪兽解放才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70780151,'精神界恶魔',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡战斗破坏怪兽送去墓地时，自己基本分回复破坏怪兽的原本攻击力的数值。以1只念动力族怪兽为对象的魔法或者陷阱卡发动时，可以支付1000基本分把那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'基本分回复破坏怪兽的原本攻击力的数值','以1只念动力族怪兽为对象的魔法或者陷阱卡的发动无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70781052,'恶魔召唤','使用黑暗力量，迷惑人心的恶魔。在恶魔族中以相当强大的力量著称。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70781053,'恶魔召唤','使用黑暗力量，迷惑人心的恶魔。在恶魔族中以相当强大的力量著称。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70781054,'恶魔召唤','使用黑暗力量，迷惑人心的恶魔。在恶魔族中以相当强大的力量著称。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70781055,'恶魔召唤','使用黑暗力量，迷惑人心的恶魔。在恶魔族中以相当强大的力量著称。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70791313,'王立魔法图书馆',replace(replace('①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物（最多3个）。\r\n②：把这张卡3个魔力指示物取除才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70797118,'雷电娘娘','自己场上存在光属性以外的表侧表示怪兽的场合，表侧表示的这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70821187,'再生木乃伊','对方控制的卡的效果把这张卡从手卡送去墓地时，这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70828912,'过早的埋葬','支付800分。选择自己的墓地的1张怪兽卡攻击表示特殊召唤到场上，并装备上这张卡。这张卡破坏时，装备的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70832512,'真十字军','5星以上的怪兽特殊召唤成功时，必须把场上表侧表示存在的这张卡解放发动。那些5星以上的怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70861343,'忍法 变化之术','①：把自己场上1只表侧表示的「忍者」怪兽解放才能把这张卡发动。把持有解放的怪兽的等级＋3以下的等级的1只兽族·鸟兽族·昆虫族怪兽从手卡·卡组特殊召唤。这张卡从场上离开时那只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70865988,'全弹发射','这张卡发动后，把手卡全部送去墓地。给与对方基本分送去墓地的卡数量×200数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70875955,'空中补给','1回合1次，可以在自己场上把1只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。这张卡的控制者在每次双方的结束阶段把自己场上1只衍生物或者名字带有「幻兽机」的怪兽解放。或者都不解放让这张卡送去墓地。','特殊召唤Token','是否现在使用「空中补给」的效果？','是否要把场上1只衍生物或者名字带有「幻兽机」的怪兽解放维持「空中补给」？','发动同时使用效果','','','','','','','','','','','','');
INSERT INTO texts VALUES(70875956,'幻兽机衍生物','由「空中补给」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70899775,'极弱音','①：以自己场上1只表侧表示怪兽为对象才能发动。这个回合，那只表侧表示怪兽的原本攻击力变成100，不会被战斗·效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70902743,'红莲魔龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡向对方的守备表示怪兽攻击的伤害计算后发动。对方场上的守备表示怪兽全部破坏。\r\n②：自己结束阶段发动。这张卡在场上表侧表示存在的场合，这张卡以外的这个回合没有攻击宣言的自己场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'守备怪物全部破坏','未攻击的怪物全部破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70903634,'被封印者的右腕','被封印的右腕。假若解开封印，能得无限之力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70908596,'星圣·箕宿三',replace(replace('这张卡不能作为同调素材。\r\n①：可以以场上1只「星圣」怪兽为对象从以下效果选择1个发动。这个效果1回合可以使用最多2次。\r\n●作为对象的怪兽的等级上升1星。\r\n●作为对象的怪兽的等级下降1星。','\r',char(13)),'\n',char(10)),'等级变化','等级上升1星。','等级下降1星。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70913714,'古神 哈斯塔',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡从怪兽区域送去墓地的场合，以对方场上1只表侧表示怪兽为对象才能发动。墓地的这张卡当作装备卡使用给那只怪兽装备。装备怪兽不能攻击，效果无效化。\r\n②：用这张卡的效果给对方怪兽装备的这张卡从场上离开的场合发动。自己得到这张卡装备过的对方怪兽的控制权。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70917315,'无形噬体·色欲',replace(replace('←5 【灵摆】 5→\r\n这张卡的控制者在每次自己准备阶段把自己场上1只怪兽解放。或者不解放让这张卡破坏。\r\n①：只要自己场上有「无形噬体」怪兽存在，双方不能把「无形噬体」卡以外的魔法卡的效果发动。\r\n【怪兽效果】\r\n①：只要灵摆召唤·反转过的这张卡在怪兽区域存在，双方不是「无形噬体」怪兽不能从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'是否解放自己场上1只怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70924884,'锯足锹形虫','大甲虫。除了头上的锯子，手腕也变成了锯子。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70939418,'疾行机人 OMK口香糖',replace(replace('①：自己·对方的战斗阶段自己因战斗·效果受到伤害的场合才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡的效果让这张卡特殊召唤成功的战斗阶段才能发动。只用包含这张卡的自己场上的风属性怪兽为同调素材作同调召唤。\r\n③：这张卡作为同调素材送去墓地的场合才能发动。自己卡组最上面的卡送去墓地，那张卡是「疾行机人」怪兽的场合，这张卡为同调素材的同调怪兽的攻击力上升1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70946699,'炎虎梁山爆','这张卡的发动时，自己回复自己场上的永续魔法·永续陷阱卡数量×500基本分。此外，场上表侧表示存在的这张卡被对方的效果送去墓地的场合，给与对方基本分自己墓地的永续魔法·永续陷阱卡数量×500的数值的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70948327,'纳米破坏者','这张卡攻击3星以下的表侧表示怪兽的场合，不进行伤害计算把那只怪兽直接破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70950698,'引导交错鹿',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：自己场上有电子界族怪兽召唤时才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡给与对方战斗伤害时才能发动。在自己场上把1只「引导鹿衍生物」（电子界族·地·1星·攻/守0）特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70950699,'引导鹿衍生物','由「引导交错鹿」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70969517,'幻兽 巨岩蜥','把名字带有「幻兽」的怪兽作为祭品的场合，这张卡可以用1只祭品作召唤。这张卡每战斗破坏1只怪兽，给与对方基本分500分伤害。对方控制的卡效果把这张卡破坏送去墓地时，给与对方基本分2000分伤害。','用1只怪兽解放上级召唤','给予对方500伤害','给予对方2000伤害','','','','','','','','','','','','','');
INSERT INTO texts VALUES(70975131,'番猫-戴表看门猫',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己场上没有怪兽存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡特殊召唤成功的自己回合的结束阶段把场上的这张卡除外才能发动。从卡组选1张永续魔法卡在自己的魔法与陷阱区域盖放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71007216,'风魔女-玻璃铃',replace(replace('「风魔女-玻璃铃」的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把「风魔女-玻璃铃」以外的1只「风魔女」怪兽加入手卡。这个效果的发动后，直到回合结束时自己不是风属性怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71015787,'寂静须鲨','自己的主要阶段时才能发动。这张卡从手卡往对方场上特殊召唤。这个效果特殊召唤成功时，这张卡的控制者从卡组抽1张卡，从这张卡的控制者来看的对方玩家回复2000基本分。「寂静须鲨」的这个效果1回合只能使用1次。此外，只要这张卡在场上表侧表示存在，这张卡的控制者的手卡限制数量变成3张。','特殊召唤','抽卡回复','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71039903,'太古的白石',replace(replace('「太古的白石」的②的效果1回合只能使用1次。\r\n①：这张卡被送去墓地的回合的结束阶段才能发动。从卡组把1只「青眼」怪兽特殊召唤。\r\n②：把墓地的这张卡除外，以自己墓地1只「青眼」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71044499,'抹杀之使徒','破坏1只里侧表示的怪兽，并从游戏中除外。破坏的怪兽是反转效果的场合，双方互相确认对方卡组，和破坏的怪兽同名的卡全部从游戏中除外。之后洗切卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71060915,'羽翼之风','自己场上有「元素英雄 羽翼侠」表侧表示存在的场合才能发动。魔法·陷阱卡的发动无效，并把那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71068247,'图腾鸟',replace(replace('风属性3星怪兽×2\r\n把这张卡2个超量素材取除才能发动。魔法·陷阱卡的发动无效并破坏。这张卡没有超量素材的场合，这张卡的攻击力下降300。','\r',char(13)),'\n',char(10)),'效果无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71068263,'食人玩偶','看起来是个可爱的布娃娃，用拉链的嘴巴蒙上敌人并加以攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71069715,'DD重组',replace(replace('①：只要这张卡在魔法与陷阱区域存在，自己场上的「契约书」卡不会被对方的效果破坏。\r\n②：把墓地的这张卡除外，以这张卡以外的除外的最多3张自己的「DD」卡为对象才能发动。那些卡回到卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71071546,'轨道 7',replace(replace('①：这张卡反转的场合发动。给这张卡放置1个遵命指示物。\r\n②：自己主要阶段，把这张卡的遵命指示物全部取除才能发动。这张卡的攻击力变成2000，这个回合不能直接攻击，结束阶段送去墓地。\r\n③：把这张卡解放，以自己墓地1只「光子」怪兽或者「银河」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'放置指示物','这张卡的攻击力变成2000','自己墓地「光子」或者「银河」怪兽加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71098407,'健忘','对方场上表侧攻击表示存在的怪兽的效果发动时才能发动。那个发动的效果无效，那只怪兽变成表侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71106375,'朱罗纪禽龙','这张卡战斗破坏对方怪兽的场合，可以让对方场上盖放的1张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71107816,'恶魔烹调师','这张卡给与对方战斗伤害时，对方在卡组最上面抽2张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71133680,'水精鳞-深渊海仙女',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：把这张卡从手卡丢弃，以自己场上1只水属性怪兽为对象才能发动。选那只怪兽以外的自己的手卡·场上1只水属性怪兽破坏，作为对象的怪兽的攻击力·守备力直到回合结束时上升这个效果破坏的怪兽的原本数值。这个效果在对方回合也能发动。\r\n②：这张卡从场上送去墓地的场合才能发动。自己从卡组抽1张，那之后选1张手卡丢弃。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71172240,'反向连接兽',replace(replace('①：额外怪兽区域只有对方怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：把这张卡解放才能发动。额外怪兽区域的怪兽全部回到持有者卡组。这个效果的发动后，直到回合结束时自己不能从额外卡组把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71175527,'薰风的神裔 皮莉佳','这张卡召唤·特殊召唤成功时，可以从自己墓地选择1只风属性的调整表侧守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。这个效果发动过的回合，自己不能把风属性以外的怪兽特殊召唤。「薰风的神裔 皮莉佳」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71181155,'幻兽 十字翼鸟','只要这张卡在墓地存在，场上的名字带有「幻兽」的怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71200730,'出于黑暗的绝望','当这张卡被对方的效果从手卡或卡组送去墓地时，这张卡特殊召唤上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71203602,'邪遗式双魂海龙','名字带有「遗式」的仪式魔法卡降临。这张卡的攻击宣言时，从自己卡组抽1张卡，给双方确认。确认的卡是名字带有「遗式」的怪兽的场合，把对方手卡随机1张确认。','抽卡确认','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71207871,'影六武众-风魔',replace(replace('①：这张卡被战斗·效果破坏的场合才能发动。从卡组把「影六武众-风魔」以外的1只「六武众」怪兽特殊召唤。\r\n②：只让自己场上的「六武众」怪兽1只被效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71209500,'亚马逊童军','把这张卡解放发动。自己场上表侧表示存在的名字带有「亚马逊」的怪兽在这个回合不会成为效果怪兽的效果的对象，不会被魔法·陷阱·效果怪兽的效果破坏。这个效果在对方回合也能发动。','耐性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71218746,'钻头机人','这张卡攻击守备表示怪兽的场合，不进行伤害计算，被攻击怪兽直接破坏。','破坏守备怪物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71233859,'光子推进','选择衍生物以外的场上表侧表示存在的1只4星以下的光属性怪兽发动。选择的怪兽以及场上表侧表示存在的同名的怪兽的攻击力直到结束阶段时变成2000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71249758,'D2护盾','选择自己场上表侧守备表示存在的1只怪兽发动。选择的怪兽的守备力变成原本守备力2倍的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71272951,'性能过剩','场上表侧表示存在的怪兽的攻击力比原本攻击力高的场合，那些怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71279983,'超级运动员 无畏扣篮手',replace(replace('「超级运动员 无畏扣篮手」的①的方法的特殊召唤1回合只能有1次。\r\n①：这张卡可以让「超级运动员 无畏扣篮手」以外的自己场上1只「超级运动员」怪兽回到手卡，从手卡特殊召唤。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n③：这张卡给与对方战斗伤害时，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71280811,'宝箱鬼','突袭企图打开宝箱的盗贼。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71283180,'龙卷鸟','反转：场上的魔法·陷阱卡2张回主人的手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71315423,'米利迪斯异虫','反转：这张卡可以当作装备卡使用给1只对方怪兽装备。每次准备阶段，给与装备怪兽的控制者400分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71340250,'搅拌机人',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：把自己场上1只机械族怪兽解放才能发动。从卡组把1只风属性以外的「机人」怪兽特殊召唤。\r\n②：把基本分支付一半，从自己墓地把包含这张卡的机械族怪兽任意数量除外才能发动。和除外的怪兽数量相同等级的1只「机人」融合怪兽从额外卡组无视召唤条件特殊召唤。这个效果特殊召唤的怪兽在结束阶段破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','融合怪兽特殊召唤','请选择要特殊召唤的融合怪兽的等级','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71341529,'永火骑士','场上存在的这张卡被破坏送去墓地时，可以丢弃2张手卡把这张卡从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71345905,'降阶魔法-源数之落',replace(replace('选择自己场上1只名字带有「希望皇 霍普」的怪兽才能发动。比选择的怪兽阶级低的1只名字带有「希望皇 霍普」的怪兽在选择的自己怪兽上面重叠当作超量召唤从额外卡组特殊召唤。这个效果超量召唤的怪兽得到以下效果。\r\n●这张卡和对方怪兽进行战斗的场合，只在战斗阶段内那只对方怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'「降阶魔法-源数之落」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71353388,'领头蝴蝶','对方场上有同调怪兽表侧表示存在，自己场上没有同调怪兽表侧表示存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71384012,'见习魔娘',replace(replace('暗属性怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：场上的暗属性怪兽的攻击力·守备力上升500，光属性怪兽的攻击力·守备力下降400。\r\n②：这张卡被战斗·效果破坏的场合，以自己墓地1只暗属性怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71386411,'超重武者 拳-C',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己的「超重武者」怪兽战斗破坏对方怪兽的自己战斗阶段才能发动。用包含这张卡的自己场上的怪兽为同调素材作同调召唤。\r\n②：自己墓地没有魔法·陷阱卡存在的场合，以自己场上1只「超重武者」同调怪兽为对象才能发动。那只怪兽的等级下降1星，这张卡从墓地特殊召唤。这个效果的发动后，直到回合结束时自己不是「超重武者」怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'同调召唤','特殊召唤','请选择要下降等级的怪兽','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71395725,'兽人 阿瑞斯','只要这张卡在场上表侧表示存在，每次有连锁发生这张卡的攻击力上升500。','','','','','','','','','','','','','','','','');
