INSERT INTO texts VALUES(71650854,'半魔导带域',replace(replace('自己的主要阶段1·主要阶段2的开始时才能把这张卡发动。\r\n①：双方的主要阶段1内，场上的怪兽不会成为各自的对方的效果的对象，不会被各自的对方的效果破坏。\r\n②：只要这张卡在场地区域存在，自己不能把场地魔法卡发动·盖放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','本数据库来自23333服，如果您获得的版本未标明出处','则那个版本是可耻的盗版');
INSERT INTO texts VALUES(71652522,'战斗瞬间移动','自己场上表侧表示存在的念动力族怪兽只有1只的场合，选择那1只怪兽发动。这个回合选择怪兽可以直接攻击对方玩家。这个回合的战斗阶段结束时，选择怪兽的控制权转移给对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71692913,'相克之魔术师',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，以自己场上1只超量怪兽为对象才能发动。这个回合那只超量怪兽可以作为和那个阶级相同数值的等级的怪兽来成为超量召唤的素材。\r\n【怪兽效果】\r\n①：1回合1次，以场上1只光属性怪兽为对象才能发动。那只怪兽的效果直到回合结束时无效。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71696014,'魔术师之袍',replace(replace('「魔术师之袍」的①②的效果1回合各能使用1次。\r\n①：对方回合从手卡丢弃1张魔法·陷阱卡才能发动。从卡组把1只「黑魔术师」特殊召唤。\r\n②：这张卡在墓地存在的状态，对方回合自己把魔法·陷阱卡的效果发动的场合才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'特殊召唤','这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71703785,'守护神官 马哈德',replace(replace('①：把这张卡抽到时，把这张卡给对方观看才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡和暗属性怪兽进行战斗的伤害步骤内，这张卡的攻击力变成2倍。\r\n③：这张卡被战斗·效果破坏的场合才能发动。从自己的手卡·卡组·墓地选1只「黑魔术师」特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71705144,'娱乐伙伴阵容更换',replace(replace('「娱乐伙伴阵容更换」在1回合只能发动1张。\r\n①：把手卡的「娱乐伙伴」怪兽任意数量给对方观看，回到卡组洗切。那之后，自己从卡组抽出回到卡组的数量＋1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71717923,'摩耳摩','把自己场上表侧表示存在的1只地属性怪兽解放发动。场上表侧表示存在持有解放怪兽的攻击力以下的守备力的怪兽全部破坏。','怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71746462,'深海切割手','以锐利的钩爪撕裂对方的残忍怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71759912,'海獭树熊','这张卡以外的兽族怪兽在自己场上表侧表示存在的场合，可以把自己场上表侧表示存在的1只怪兽的攻击力直到结束阶段时上升1000。这个效果1回合只能使用1次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71782404,'真红眼烧灭',replace(replace('「真红眼烧灭」在1回合只能发动1张。\r\n①：自己场上的表侧表示的「真红眼」怪兽被战斗·效果破坏的场合，以破坏的那1只怪兽为对象才能发动。双方玩家受到那只怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71786742,'齿轮齿轮人','把这张卡解放，选择自己墓地存在的1只4星的机械族怪兽发动。选择的怪兽从墓地特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71797713,'元素灵剑士·金光',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：从手卡把2只其他怪兽送去墓地才能发动。这张卡从手卡特殊召唤。\r\n②：只要这张卡在怪兽区域存在，自己场上的「元素灵剑士」怪兽以及「灵神」怪兽受为这张卡的①的效果发动而送去墓地的「元素灵剑士」怪兽的原本属性对应的以下所适用。\r\n●地或者风：不会被战斗破坏。\r\n●水或者炎：不会被效果破坏。\r\n●光或者暗：不会成为对方的效果的对象。','\r',char(13)),'\n',char(10)),'从手卡特殊召唤','使用地·风特殊召唤','使用水·炎特殊召唤','使用光·暗特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(71799173,'秩序守护者','自己场上有光属性怪兽表侧表示2只以上存在的场合，这张卡可以从手卡特殊召唤。「秩序守护者」在自己场上只能有1只表侧表示存在。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71829750,'纳迦','表侧表示的这张卡从场上回到卡组的场合，从卡组选1张3星以下的怪兽特殊召唤到场上。之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71863024,'娱乐伙伴 萝卜马',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，以对方场上1只表侧表示怪兽和自己场上1只「娱乐伙伴」怪兽为对象才能发动。那只对方怪兽的攻击力下降那只自己的「娱乐伙伴」怪兽的攻击力数值。\r\n【怪兽效果】\r\n①：特殊召唤的怪兽在对方场上存在，对方场上的怪兽数量是自己场上的怪兽数量以上的场合，这张卡可以从手卡特殊召唤。\r\n②：1回合1次，以对方场上1只表侧表示怪兽和自己场上1只表侧表示怪兽为对象才能发动。直到回合结束时，那只对方怪兽的攻击力下降这张卡的攻击力数值，那只自己怪兽的攻击力上升这张卡的攻击力数值。','\r',char(13)),'\n',char(10)),'这张卡特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71867500,'龙复活狂想曲','自己场上有魔法师族怪兽存在的场合：可以选择包含通常怪兽的自己墓地最多2只龙族怪兽；那些怪兽特殊召唤。这个回合对方受到的全部伤害变成0。「龙复活狂想曲」在1回合只能发动1张。','是否继续选择？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71870152,'命运女郎·菲莉','这张卡的攻击力·守备力变成这张卡的等级×200的数值。自己的准备阶段时，这张卡的等级上升1星（等级最多12星）。这张卡用名字带有「命运女郎」的卡的效果表侧攻击表示特殊召唤成功时，把对方场上表侧表示存在的1只怪兽破坏，给与对方基本分破坏怪兽的攻击力数值的伤害。','等级上升','破坏并伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71880877,'超级防卫机器人·猴斗士','这张卡召唤成功时，可以从手卡把1只名字带有「超级防卫机器人」的怪兽或者「轨道 7」特殊召唤。此外，1回合1次，把自己墓地1只机械族怪兽从游戏中除外才能发动。从自己墓地选择1只名字带有「超级防卫机器人」的怪兽或者「轨道 7」加入手卡。','特殊召唤','加入收卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71921856,'No.79 燃烧拳击手 新星之皇帝拳士',replace(replace('4星怪兽×2\r\n1回合1次，自己的主要阶段时才能发动。从自己的手卡·墓地选1只名字带有「燃烧拳击手」的怪兽在这张卡下面重叠作为超量素材。这张卡的攻击力上升这张卡的超量素材数量×100的数值。此外，这张卡被对方破坏送去墓地时，可以从自己墓地选择最多有那个时候这张卡持有的超量素材数量的4星以下的名字带有「燃烧拳击手」的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'补充素材','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71923655,'触腕鲨','这张卡可以把自己场上1只水属性怪兽解放，从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71930383,'警车机人','确认对方场上的1张盖伏的卡，确认后按原本的形式放回。这个效果1回合1次，只能在自己的主要阶段发动。','确认盖卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71934924,'疾风！凶杀阵','自己场上名字带有「六武众」的怪兽进行过战斗的场合，直到这个回合的结束阶段时自己场上名字带有「六武众」的怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71950093,'铁铲粉碎机','善于破坏一切。要注意其双手的大铲。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71971554,'王道同调士',replace(replace('①：把这张卡作为「王道战士」以外的同调怪兽的素材的场合，当作这张卡的等级下降2星的等级使用。\r\n②：这张卡攻击的场合，那次伤害步骤结束时发动。这张卡的等级直到回合结束时上升1星。','\r',char(13)),'\n',char(10)),'等级上升1','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71983925,'魔法封印咒符','这张卡仅当自己场上存在「封印师 明晴」时才能发动。这张卡在场上存在时，魔法卡不能发动，场上所有魔法卡的效果无效化。当「封印师 明晴」离场时，这张卡被破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(71985676,'娱乐伙伴 障碍气球貘',replace(replace('「娱乐伙伴 障碍气球貘」的②的效果1回合只能使用1次。\r\n①：自己怪兽和对方怪兽进行战斗的伤害计算时，把这张卡从手卡丢弃才能发动。那次战斗发生的双方的战斗伤害变成0。\r\n②：这张卡在墓地存在的场合，对方怪兽的直接攻击宣言时从手卡丢弃1只「娱乐伙伴」怪兽才能发动。这张卡从墓地守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'战斗伤害变成0','这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72006609,'明星之机械骑士',replace(replace('包含「机界骑士」怪兽的怪兽2只\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功的场合，从手卡丢弃1只「机界骑士」怪兽或者1张「星遗物」卡才能发动。从卡组把1张「星遗物」卡加入手卡。\r\n②：只要这张卡在怪兽区域存在，自己的「机界骑士」怪兽不会被和与那自身纵列不同纵列的怪兽的战斗破坏，那次战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72022087,'自由解放','自己场上存在的怪兽被战斗破坏送去墓地时才能发动。选择场上表侧表示存在的2只怪兽回到持有者卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72029628,'废石融合','①：从自己墓地把「宝石骑士」融合怪兽卡决定的融合素材怪兽除外，把那1只融合怪兽从额外卡组融合召唤。这个效果特殊召唤的怪兽在结束阶段破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72044448,'破灭之光子流','自己场上有名字带有「银河眼」的怪兽存在的场合才能发动。选择场上1张卡从游戏中除外。自己场上没有「银河眼光子龙」存在的场合，这张卡不在自己回合不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72053645,'气象报告员','反转：对方场上表侧表示存在的「光之护封剑」全部破坏。破坏成功的场合，下次的自己的战斗阶段可以进行2次。','破坏','是否要再进行一次战斗阶段？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72056560,'宝石骑士·琥珀',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●1回合1次，可以从手卡把1张名字带有「宝石骑士」的卡送去墓地，选择从游戏中除外的1只自己怪兽回到手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72076281,'机械蝙蝠','投下左右翅膀里搭载的爆弹的机械蝙蝠。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72129804,'捕食生成',replace(replace('①：把手卡的「捕食」卡任意数量给对方观看，以给人观看的数量的对方场上的表侧表示怪兽为对象才能发动。给那些怪兽各放置1个捕食指示物。有捕食指示物放置的2星以上的怪兽的等级变成1星。\r\n②：自己的「捕食植物」怪兽被战斗破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'是否使用「捕食生成」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72142276,'炎热传导场','从自己卡组把2只名字带有「熔岩」的怪兽送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72144675,'对流气圈鸟','这张卡上级召唤的场合，解放的怪兽必须是鸟兽族怪兽。这张卡只要在场上表侧表示存在不受陷阱卡的效果影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72150572,'融合体驱除装置','场上表侧表示存在的1只融合怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72167543,'扫兴书呆魔术师',replace(replace('魔法师族4星怪兽×2\r\n这张卡也能在自己主要阶段2在自己场上的3阶以下的超量怪兽上面重叠来超量召唤。\r\n①：这张卡的攻击力上升这张卡的超量素材数量×200。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n③：这张卡进行战斗的伤害计算后发动。这张卡1个超量素材取除。','\r',char(13)),'\n',char(10)),'是否在3阶以下的超量怪兽上面重叠超量召唤？','移除素材','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72181263,'DD 俄耳托斯',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，以场上1张魔法·陷阱卡和这张卡以外的自己场上1张「DD」卡或者「契约书」卡为对象才能发动。那些卡破坏。\r\n【怪兽效果】\r\n①：自己因战斗·效果受到伤害时才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡特殊召唤成功的回合，自己不是恶魔族怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72192100,'死亡城堡恶魔','这张卡的控制者在自己的每1个准备阶段支付500基本分。当这张卡成为对方所控制的卡的效果对象时，在效果处理时掷1次骰子，若掷出3，则使此效果无效并将其破坏。当自己场上的「灭绝国王恶魔」被破坏送去墓地时，可以从手卡将这张卡送去墓地，将此「灭绝国王恶魔」特殊召唤上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72204747,'超越命运','①：以自己墓地1只「命运英雄」怪兽为对象才能发动。把持有那只怪兽的等级一半以下的等级的1只「命运英雄」怪兽从卡组特殊召唤。这个效果特殊召唤的怪兽在这个回合的结束阶段破坏。','请选择1只名字带有「命运英雄」的怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72228247,'魔导之圣像骑士',replace(replace('「魔导之圣像骑士」以外的「圣像骑士」怪兽1只\r\n这个卡名的③的效果1回合只能使用1次。\r\n①：这张卡的攻击力上升这张卡所连接区的怪兽的原本攻击力数值。\r\n②：这张卡所连接区的怪兽不能攻击。\r\n③：这张卡所连接区有效果怪兽特殊召唤的场合才能发动。从卡组把1只「圣像骑士」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72237166,'帝王的牲祭','这张卡为祭品的祭品召唤成功时，这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72258771,'核成魔神 华拉法','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地。或者不送去墓地让这张卡破坏。这张卡可以把1只名字带有「核成」的怪兽解放作上级召唤。这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。这张卡不会被陷阱卡的效果破坏。','选择一张「核成兽的钢核」送去墓地','破坏「核成魔神 华拉法」','把1只名字带有「核成」的怪兽解放上级召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72269672,'岩石幽灵','激怒它的话，头上的火山可是会爆发的哦。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72278479,'黑翼返回','名字带有「黑羽」的怪兽1只特殊召唤成功时，选择对方场上表侧表示存在的1只怪兽发动。自己基本分回复选择的对方怪兽的攻击力的数值，那只怪兽回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72287557,'地狱聚合体','对方的融合怪兽融合召唤时发动。把自己场上1只怪兽作为祭品，得到那1只融合怪兽的控制权。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72291078,'幻兽机 猎户座飞狮','自己的主要阶段时把墓地的这张卡从游戏中除外才能发动。从手卡把1只名字带有「幻兽机」的怪兽召唤。只要自己场上有衍生物存在，这张卡不会被战斗以及效果破坏。此外，这张卡被送去墓地的场合，可以把1只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。「幻兽机 猎户座飞狮」的这个效果1回合只能使用1次。','特殊召唤','特殊召唤衍生物','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72291079,'幻兽机衍生物','由「幻兽机 猎户座飞狮」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72291412,'DD 死灵史莱姆',replace(replace('「DD 死灵史莱姆」的效果1回合只能使用1次。\r\n①：这张卡在墓地存在的场合才能发动。「DDD」融合怪兽卡决定的包含这张卡的融合素材怪兽从自己墓地除外，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72299832,'机械巨兵','巨斧的一击可以割开大地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72302403,'光之护封剑',replace(replace('这张卡发动后继续留在场上，用对方回合计算的3回合后的对方结束阶段破坏。\r\n①：作为这张卡的发动时的效果处理，对方场上有里侧表示怪兽存在的场合，那些怪兽全部变成表侧表示。\r\n②：只要这张卡在魔法与陷阱区域存在，对方怪兽不能攻击宣言。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72318602,'红莲守卫者','①：自己场上有「红莲魔」怪兽存在，对方的魔法·陷阱·怪兽的效果发动时，把这张卡从手卡送去墓地才能发动。这个回合，自己场上的怪兽不会被对方的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72328962,'魔轰神 索尔基乌斯','从手卡把「魔轰神 索尔基乌斯」以外的2张卡送去墓地发动。这张卡从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72332074,'超级量子必杀 阿尔方球',replace(replace('①：自己场上有「超级量子战士」怪兽3种类以上存在的场合才能发动。对方场上的卡全部回到持有者卡组。那之后，对方从额外卡组把1只怪兽无视召唤条件特殊召唤。\r\n②：从自己墓地把这张卡和1只「超级量子妖精 阿尔方」除外才能发动。从卡组把1张「超级量子机舰 炎磁大母舰」发动。','\r',char(13)),'\n',char(10)),'回到卡组','发动场地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72336818,'渗透测试锹甲',replace(replace('效果怪兽2只\r\n①：只要这张卡在怪兽区域存在，连接状态的自己怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72345736,'六武众的团结',replace(replace('①：每次「六武众」怪兽召唤·特殊召唤给这张卡放置1个武士道指示物（最多2个）。\r\n②：把有武士道指示物放置的这张卡送去墓地才能发动。自己从卡组抽出这张卡放置的武士道指示物的数量。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72355272,'玄化凤凰神',replace(replace('①：这张卡用「玄化」怪兽的效果特殊召唤成功的场合才能发动。场上盖放的魔法·陷阱卡全部除外。\r\n②：这张卡被除外的场合，下个回合的准备阶段让除外的这张卡回到卡组才能发动。从卡组把「玄化凤凰神」以外的1张「玄化」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72355441,'超量礼物','自己场上有超量怪兽表侧表示2只以上存在的场合才能发动。把自己场上存在的2个超量素材取除，从自己卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72370114,'齿轮齿轮攻击人','这张卡1回合只有1次可以变成里侧守备表示。这张卡反转时，可以选最多有这张卡以外的自己场上的名字带有「齿轮齿轮」的怪兽数量的场上的魔法·陷阱卡破坏。','变成里侧守备','魔陷破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72378329,'野兽眼灵摆龙',replace(replace('龙族·暗属性怪兽＋兽族怪兽\r\n这张卡用融合召唤以及以下方法才能特殊召唤。\r\n●把自己场上的上记卡解放的场合可以从额外卡组特殊召唤（不需要「融合」）。\r\n①：这张卡战斗破坏怪兽的场合发动。给与对方作为这张卡的融合素材的1只兽族怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72402069,'DDD 超死伟王 白地狱终末神',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，对方怪兽的攻击宣言时以自己场上1只「DDD」同调怪兽为对象才能发动。持有那只怪兽的攻击力以下的守备力的对方场上的怪兽全部破坏，给与对方破坏的怪兽数量×1000伤害。\r\n【怪兽效果】\r\n「DD」调整＋调整以外的「DDD」怪兽1只以上\r\n①：只要这张卡在怪兽区域存在，对方不能把自己场上的怪兽作为效果的对象。\r\n②：这张卡已在怪兽区域存在的状态，这张卡以外的怪兽召唤·特殊召唤的场合才能发动。对方选自身场上1只灵摆怪兽。那只怪兽以外的对方场上的怪兽的效果无效化。\r\n③：怪兽区域的这张卡被破坏的场合才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'破坏并伤害','效果无效','这张卡在自己的灵摆区域放置','请选择场上1只灵摆怪兽','','','','','','','','','','','','');
INSERT INTO texts VALUES(72403299,'遗式占卜师','1回合1次，宣言1个卡名才能发动。把自己卡组最上面的卡翻开，那是宣言的卡的场合加入手卡。不是的场合，回到自己卡组最上面。','宣言卡名','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72405967,'王家的牲祭','当自己场上有「王家长眠之谷」存在时才能发动。所有玩家将手卡中的怪兽卡全部丢进墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72413000,'毛绒动物之翼',replace(replace('「毛绒动物之翼」的效果1回合只能使用1次。\r\n①：自己场上有「玩具罐」存在的场合，把墓地的这张卡除外，以自己墓地1只「毛绒动物」怪兽为对象才能发动。那只怪兽除外，自己从卡组抽1张。那之后，以下效果可以适用。\r\n●选自己场上1张「玩具罐」送去墓地，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'是否将「玩具罐」送入墓地，抽1张牌？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72426662,'终焉之王 迪米斯',replace(replace('「世界末日」降临。\r\n①：支付2000基本分才能发动。场上的其他卡全部破坏。','\r',char(13)),'\n',char(10)),'此卡以外全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72429240,'入魔鬼灯人','把这张卡解放发动。选择对方场上表侧表示存在的1只怪兽破坏。','怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72439556,'薰风的希望 神威','反转：从自己卡组把1只名字带有「薰风」的调整特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72443568,'沉默魔术师 LV8',replace(replace('这张卡不能通常召唤。「沉默魔术师 LV4」的效果才能特殊召唤。\r\n①：这张卡不受对方的魔法卡的效果影响。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72446038,'合成魔术','「合成狼人」的降临需要。必须从场上或手卡把合计6星以上的卡作为祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72491806,'超级运动员 九号半球员',replace(replace('「超级运动员 九号半球员」的在①的方法的特殊召唤1回合只能有1次，在②的效果1回合只能使用1次。\r\n①：这张卡可以让「超级运动员 九号半球员」以外的自己场上1只「超级运动员」怪兽回到手卡，从手卡特殊召唤。\r\n②：以这张卡以外的自己场上1只「超级运动员」怪兽为对象才能发动。那只表侧表示怪兽回到手卡，那之后把和那只怪兽卡名不同的1只「超级运动员」怪兽从手卡特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72497366,'恶魔的凭代',replace(replace('①：只要这张卡在魔法与陷阱区域存在，自己在5星以上的恶魔族怪兽召唤的场合需要的解放可以不用。这个效果1回合只能适用1次。\r\n②：只让通常召唤的5星以上的恶魔族怪兽1只被破坏的场合，可以作为代替把这张卡送去墓地。','\r',char(13)),'\n',char(10)),'使用「恶魔的凭代」的效果召唤','是否要使用「恶魔的凭代」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72502414,'超量谍报士','这张卡在墓地存在的场合，自己的主要阶段时选择自己场上1只名字带有「希望皇 霍普」的超量怪兽才能发动。把墓地的这张卡在选择的怪兽下面重叠作为超量素材。「超量谍报士」的效果在决斗中只能使用1次。','素材补充','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72520073,'暗之艺术家','受到了光属性怪兽的攻击时，这张卡的守备力成为一半。','守备变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72529749,'拓扑三叶双头蛇',replace(replace('效果怪兽2只以上\r\n①：这张卡所连接区有怪兽特殊召唤的场合发动。那些怪兽以及场上的魔法·陷阱卡全部除外，给与对方这个效果除外的对方的卡数量×500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72537897,'魔兽的怀柔','①：自己场上没有怪兽存在的场合才能发动。把3只卡名不同的2星以下的兽族的效果怪兽从卡组特殊召唤。这个效果特殊召唤的怪兽的效果无效化，结束阶段破坏。这张卡的发动后，直到回合结束时自己不能把兽族以外的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72549351,'龙棋战术','把自己场上存在的2只龙族怪兽解放发动。从自己卡组把1只8星的龙族怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72563071,'念力震撼波','对方把陷阱卡发动时，从手卡丢弃1张魔法·陷阱卡才能发动。从自己卡组把1只机械族·暗属性·6星的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72575145,'降格处分','装备怪兽的等级下降2星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72580321,'积木巨人','①：自己墓地的怪兽只有地属性的场合，把这张卡解放，以「积木巨人」以外的自己墓地2只4星以下的岩石族怪兽为对象才能发动。那些岩石族怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不能把场上发动的效果发动。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72589042,'复仇之女神','自己场上没有卡存在并且手卡只有这1张卡的场合，对方怪兽的攻击宣言时：可以把这张卡从手卡丢弃；对方场上的卡全部破坏，之后可以从卡组把1只怪兽特殊召唤。','','是否特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72621670,'二重魔术臂束缚','①：把自己场上2只怪兽解放，以对方场上2只表侧表示怪兽为对象才能发动。那2只表侧表示怪兽的控制权直到自己结束阶段得到。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72630549,'混沌魔术师','仅以这张卡1张卡为对象的效果怪兽的效果无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72631243,'能量勇士','只要这张卡在自己场上表侧表示存在，自己场上存在的再度召唤状态的二重怪兽不会被卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72634965,'虚无之统括者','这张卡不能特殊召唤。只要这张卡在场上表侧表示存在，对方不能特殊召唤怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72648577,'DDD的人事权','①：从自己的手卡·场上·墓地的「DD」怪兽以及自己的灵摆区域的「DD」卡之中选合计3张回到持有者卡组。那之后，可以从卡组把2只「DD」怪兽加入手卡。','卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72648810,'不可动摇的牵绊','①：灵摆怪兽的效果或者已在灵摆区域存在的卡的效果由对方发动时才能发动。那个发动无效并除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72657739,'怨念之杀人人偶','这张卡因永续魔法的效果从场上送去墓地的场合，自己的回合的准备阶段时从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72664875,'暗黑方界邪神 深红之挪婆·三神一体',replace(replace('「暗黑方界神 深红之挪婆」×3\r\n这张卡用以上记的卡为融合素材的融合召唤才能特殊召唤。\r\n①：这张卡不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：这张卡的攻击宣言时发动。对方基本分变成一半。\r\n③：这张卡战斗破坏怪兽时才能发动。那次战斗阶段中，这张卡只再1次可以攻击。\r\n④：自己受到效果伤害的场合发动。给与对方为受到的伤害数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72677437,'毒蛇王 维诺米隆','这张卡以外的效果怪兽的效果不能把这张卡特殊召唤。这张卡不受「蛇毒沼泽」的效果影响。这张卡的攻击力自己墓地每有1只爬虫族怪兽上升500。这张卡被战斗破坏送去墓地时，可以把自己墓地这张卡以外的1只爬虫族怪兽从游戏中除外把这张卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72708264,'娱乐伙伴 机械眼蜥蜴','①：这张卡召唤·特殊召唤成功的回合的自己主要阶段只有1次，从卡组把1只「异色眼」怪兽送去墓地才能发动。直到结束阶段，这张卡当作和送去墓地的怪兽同名卡使用。','变成同名卡','变成同名卡的效果结束','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72709014,'王女的试炼','「白魔导士 绒儿」「黑魔导师 库兰」才能装备。装备怪兽攻击力上升800。装备怪兽因战斗破坏5星以上的怪兽的回合，可以把装备怪兽和这张卡作为祭品，「白魔导士 绒儿」对应「魔法之国的王女-绒儿」，「黑魔导师 库兰」对应「魔法之国的王女-库兰」从手卡·卡组特殊召唤1只。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72710085,'星邪的神食','只让自己墓地的怪兽1只被从游戏中除外的场合，可以把和除外的那只怪兽不同属性的1只怪兽从卡组送去墓地。「星邪的神食」的效果1回合只能使用1次。','送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72714226,'突击枪犬','①：这张卡被战斗破坏送去墓地时才能发动。从卡组把「突击枪犬」任意数量特殊召唤。','特殊召唤','是否继续特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72714392,'黑羽-大旆之伐由',replace(replace('①：这张卡只要在怪兽区域存在，不能作为同调素材。\r\n②：这张卡在墓地存在的场合，以调整以外的自己墓地1只「黑羽」怪兽为对象才能发动。那只怪兽和这张卡从墓地除外，把持有和那2只的等级合计相同等级的1只「黑羽」同调怪兽从额外卡组特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72714461,'慧眼之魔术师',replace(replace('←5 【灵摆】 5→\r\n①：另一边的自己的灵摆区域有「魔术师」卡或者「娱乐伙伴」卡存在的场合才能发动。这张卡破坏，从卡组选「慧眼之魔术师」以外的1只「魔术师」灵摆怪兽在自己的灵摆区域放置。\r\n【怪兽效果】\r\n①：把这张卡从手卡丢弃，以自己的灵摆区域1张灵摆刻度和原本数值不同的卡为对象才能发动。那张卡的灵摆刻度直到回合结束时变成原本数值。','\r',char(13)),'\n',char(10)),'在灵摆区发动','改变灵摆刻度','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72767833,'卡片反转者','把1张手卡送去墓地发动。把对方场上存在的全部怪兽的表示形式改变。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72772445,'三形金字塔·巨人王',replace(replace('「三形金字塔·巨人王」的③的效果1回合只能使用1次。\r\n①：场上的岩石族怪兽的攻击力上升500。\r\n②：自己的「三形金字塔」怪兽进行战斗的场合，直到伤害步骤结束时对方不能把魔法·陷阱·怪兽的效果发动。\r\n③：场地区域的表侧表示的这张卡被送去墓地的场合才能发动。从手卡把1只「三形金字塔」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','本数据库来自23333服，转载请注明出处');
INSERT INTO texts VALUES(72819261,'元素灵剑士·日炙',replace(replace('①：1回合1次，从手卡把1只「元素灵剑士」怪兽送去墓地才能发动。从卡组把「元素灵剑士·日炙」以外的1只「元素灵剑士」怪兽或者「灵神」怪兽送去墓地。\r\n②：这张卡在墓地存在的场合，1回合1次，宣言1个属性才能发动。墓地的这张卡直到回合结束时变成宣言的属性。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72837335,'先史遗产 戈耳狄俄斯结合体','这张卡召唤成功时，可以从手卡把1只名字带有「先史遗产」的怪兽特殊召唤。这张卡的等级变成和这个效果特殊召唤的怪兽的等级相同。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72842870,'大炮鸟','从口中发射远程炮弹，在深山里炮击威力强劲。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72845813,'正义盟军 D.D.阻隔虫','只要这张卡在场上表侧表示存在，双方不能把光属性怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72855441,'青色眼睛的护人',replace(replace('「青色眼睛的护人」的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从手卡把1只光属性·1星调整特殊召唤。\r\n②：以自己场上1只效果怪兽为对象才能发动。那只怪兽送去墓地，从手卡把1只「青眼」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72869010,'狩魂魔人','「神灯魔人」＋「来自异次元的侵略者」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72881007,'宝玉的泛滥','把自己的魔法与陷阱卡区域存在的4张名字带有「宝玉兽」的卡送去墓地才能发动。场上的卡全部送去墓地。再把最多有被那个效果送去墓地的对方场上的卡数量的自己墓地存在的名字带有「宝玉兽」的怪兽尽可能特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72885174,'命运旅行','对方怪兽的攻击宣言时才能发动。把1只对方怪兽的攻击无效，1只攻击对象怪兽直到下次的准备阶段时从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72892473,'手札抹杀','①：有手卡的玩家把那些手卡全部丢弃。那之后，各自从卡组抽出自身丢弃的数量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72896720,'永火死亡龙',replace(replace('暗属性调整＋调整以外的怪兽1只以上\r\n1回合1次，自己手卡是0张的场合选择对方场上存在的1只怪兽才能发动。选择的对方怪兽破坏，给与对方基本分破坏的怪兽的攻击力一半数值的伤害。这个效果发动的回合这张卡不能攻击。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72903645,'巨刃斩龙','把自己场上存在的1张表侧表示的永续魔法卡送去墓地。对方场上的1张魔法或者陷阱卡破坏。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72913666,'鬼计狼人','自己场上有名字带有「鬼计」的怪兽存在的场合才能让这张卡表侧表示召唤。这张卡1回合只有1次可以变成里侧守备表示。此外，这张卡反转时，给与对方基本分场上盖放的卡数量×100的数值的伤害。「鬼计狼人」的这个效果1回合只能使用1次。','变成里侧表示','LP伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72926163,'元素英雄 新宇骑士',replace(replace('「元素英雄 新宇侠」＋战士族怪兽\r\n这张卡不用融合召唤不能特殊召唤。这张卡的攻击力上升作为这张卡的融合素材的「元素英雄 新宇侠」以外的怪兽的攻击力一半的数值。这张卡在同1次的战斗阶段中可以作2次攻击。这张卡进行战斗的场合，对方玩家受到的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72929454,'龟鸟','主要栖息在水中，也能在空中飞翔的珍奇乌龟。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72930878,'黑翼音速',replace(replace('自己场上的怪兽只有「黑羽」怪兽3只的场合，这张卡的发动从手卡也能用。\r\n①：对方怪兽向自己场上的「黑羽」怪兽攻击宣言时才能发动。对方场上的表侧攻击表示怪兽全部除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72932673,'深渊鳞甲-蛟龙','名字带有「水精鳞」的怪兽才能装备。装备怪兽的攻击力上升800。只要这张卡在场上存在，对方场上发动的魔法卡的效果无效。那之后，这张卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72959823,'重装机甲 装甲车龙',replace(replace('机械族怪兽＋龙族怪兽\r\n①：这张卡被破坏送去墓地的场合，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72989439,'混沌战士 -开辟的使者-',replace(replace('这张卡不能通常召唤。从自己墓地把光属性和暗属性的怪兽各1只除外的场合可以特殊召唤。这张卡的①②的效果1回合只能有1次使用其中任意1个。\r\n①：以场上1只怪兽为对象才能发动。那只怪兽除外。这个效果发动的回合，这张卡不能攻击。\r\n②：这张卡的攻击破坏对方怪兽时才能发动。这张卡只再1次可以继续攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','除外','连续攻击','','','','','','','','','','','','','');
INSERT INTO texts VALUES(72992744,'小丑王','场上没有这张卡以外的怪兽存在的场合，双方的魔法与陷阱卡区域存在的卡每有1张，这张卡的攻击力上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73001017,'希尔菲德','这张卡不能进行通常召唤。从自己墓地里除外1只风属性怪兽进行特殊召唤。这张卡被战斗破坏送去墓地时，对方随机丢弃1张手卡。','手牌丢弃','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73018302,'黑羽狩猎者','对方场上有表侧表示怪兽2只以上存在，那些怪兽的种族全部相同的场合，可以把1张手卡送去墓地选择对方场上表侧表示存在的1只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73026394,'融合防御','给与伤害的效果发动时才能发动。那个发动和效果无效，从自己融合卡组随机把1只融合怪兽送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73040500,'魔轰神 乌尔史特斯','自己手卡是2张以下的场合，自己场上表侧表示存在的名字带有「魔轰神」的怪兽的攻击力上升400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73048641,'半封','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽直到回合结束时攻击力变成一半，不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73051941,'沙石怪','从地下突然出现在眼前，用触手做攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73055622,'灵魂的降神',replace(replace('「灵魂鸟神-姬孔雀」「灵魂鸟神-彦孔雀」的降临必需。\r\n①：等级合计直到变成仪式召唤的怪兽的等级以上为止，把自己的手卡·场上的怪兽解放或者作为解放的代替而把自己墓地的灵魂怪兽除外，从手卡把「灵魂鸟神-姬孔雀」或者「灵魂鸟神-彦孔雀」仪式召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73061465,'冰结界的封魔团','从手卡把1只名字带有「冰结界」的怪兽送去墓地发动。只要这张卡在场上表侧表示存在，直到下次的自己回合的结束阶段时双方不能把魔法卡发动。这个效果1回合只能使用1次。','不能发动魔法卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73079365,'突风','自己的魔法卡被对方控制的卡的效果破坏，从场地送去墓地时才能发动。场上的1张魔法·陷阱卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73081602,'女王鸟','用巨大的嘴啄击敌人作为攻击手段。守备力很高。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73125233,'风帝 莱扎','①：这张卡上级召唤成功的场合，以场上1张卡为对象发动。那张卡回到持有者卡组最上面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73130445,'娱乐伙伴 抽卡蜥蜴',replace(replace('←6【灵摆】 6→\r\n「娱乐伙伴 抽卡蜥蜴」的灵摆效果1回合只能使用1次。\r\n①：自己主要阶段在另一边的自己的灵摆区域有「娱乐伙伴 抽卡蜥蜴」以外的「娱乐伙伴」卡存在的场合才能发动。这张卡破坏，自己从卡组抽1张。\r\n【怪兽效果】\r\n「娱乐伙伴 抽卡蜥蜴」的怪兽效果1回合只能使用1次。\r\n①：这张卡以外的自己场上的表侧表示怪兽被对方怪兽的攻击或者对方的效果破坏的场合才能发动。自己从卡组抽出自己场上的「娱乐伙伴」怪兽的数量。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
