INSERT INTO texts VALUES(73136204,'森罗的渡守 莲花','1回合1次，可以把对方场上的卡数量的卡从自己卡组上面翻开。翻开的卡之中有植物族怪兽的场合，那些怪兽全部送去墓地。剩下的卡用喜欢的顺序回到卡组最下面。此外，卡组的这张卡被卡的效果翻开送去墓地的场合，可以选择「森罗的渡守 莲花」以外的自己墓地最多5张名字带有「森罗」的卡用喜欢的顺序回到卡组下面。','翻开卡组','返回卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73146473,'交叉搬运员','可以把自己场上1只怪兽送去墓地，从手卡把1只名字带有「新空间侠」的怪兽特殊召唤。这张卡被送去墓地时，可以从自己卡组把1只字带有「新空间侠」的怪兽加入手卡。','特殊召唤','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73148972,'无情的抹杀','选择自己场上存在的1只怪兽发动。选择的自己怪兽送去墓地，对方手卡随机1张送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73176465,'光道弓手 费莉丝',replace(replace('这张卡不能通常召唤，用卡的效果才能特殊召唤。\r\n①：这张卡被怪兽的效果从卡组送去墓地的场合发动。这张卡从墓地特殊召唤。\r\n②：把这张卡解放，以对方场上1只怪兽为对象才能发动。那只对方怪兽破坏。那之后，从自己卡组上面把3张卡送去墓地。','\r',char(13)),'\n',char(10)),'特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73178098,'虚荣巨影','①：怪兽的攻击宣言时，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到那次战斗阶段结束时上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73199638,'海皇的咆哮','选择自己墓地3只3星以下的海龙族怪兽才能发动。选择的3只怪兽从墓地特殊召唤。这张卡发动的回合，自己不能把怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73206827,'光之结界',replace(replace('自己的准备阶段时进行1次投掷硬币。里出现的场合，这张卡的以下效果直到下次的自己的准备阶段时无效。\r\n●名字带有「秘仪之力」的怪兽的召唤·反转召唤·特殊召唤时发动的效果不进行投掷硬币选择其中1个适用。名字带有「秘仪之力」的怪兽战斗破坏对方怪兽的场合，自己回复破坏怪兽的原本攻击力数值的基本分。','\r',char(13)),'\n',char(10)),'投掷硬币','回复','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73213494,'彼岸的恶鬼 卡尔卡布里纳',replace(replace('「彼岸的恶鬼 卡尔卡布里纳」的①③的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上没有魔法·陷阱卡存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：自己场上有「彼岸」怪兽以外的怪兽存在的场合这张卡破坏。\r\n③：这张卡被送去墓地的场合，以场上盖放的1张魔法·陷阱卡为对象才能发动。盖放的那张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','回到手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73216412,'鳞虫','被那修长的身体卷住后插翅难逃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73219648,'地狱兵卒恶魔','这张卡的控制者在自己的每1个准备阶段支付500基本分。当这张卡成为对方所控制的卡的效果对象时，在效果处理时掷1次骰子，若掷出3，则使此效果无效并将其破坏。只要这张卡在场上存在，除这张卡的同名卡以外，对方不能攻击自己场上名称中含有「恶魔」字样的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73240432,'锋利小鬼·棉花吞噬者',replace(replace('←1 【灵摆】 1→\r\n①：自己场上的融合怪兽的攻击力上升300。\r\n②：1回合1次，自己场上有「魔玩具」融合怪兽融合召唤的场合才能发动。自己从卡组抽1张。\r\n【怪兽效果】\r\n「锋利小鬼·棉花吞噬者」的怪兽效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。给与对方为自己墓地的「魔玩具」怪兽数量×200伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73262676,'「A」细胞散布爆弹','选择自己场上表侧表示存在的1只名字带有「外星」的怪兽发动。选择的怪兽破坏，那只怪兽等级的数量的A指示物在对方场上表侧表示存在的怪兽放置。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73271204,'札再生',replace(replace('①：以自己墓地1只「花札卫」怪兽为对象才能发动。那只怪兽加入手卡。那之后，可以从手卡把1只「花札卫」怪兽无视召唤条件特殊召唤。\r\n②：这张卡被「花札卫」怪兽的效果送去墓地的场合才能发动。从自己卡组上面把5张卡翻开。可以从那之中选1张魔法·陷阱卡加入手卡。剩下的卡用喜欢的顺序回到卡组上面。','\r',char(13)),'\n',char(10)),'特殊召唤','翻开卡组','是否把「花札卫」怪兽特殊召唤？','是否把魔法·陷阱卡加入手卡？','','','','','','','','','','','','');
INSERT INTO texts VALUES(73275815,'连接洞',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：自己场上有连接4以上的连接怪兽存在的场合才能发动。选最多有自己场上的连接3以上的连接怪兽数量的对方场上的怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73285669,'剑斗兽 车斗',replace(replace('名字带有「剑斗兽」的怪兽×2\r\n让自己场上的上记的卡回到卡组的场合才能从额外卡组特殊召唤（不需要「融合」魔法卡）。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73289035,'武神帝-月读',replace(replace('光属性4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。手卡全部送去墓地，从卡组抽2张卡。此外，这张卡因对方的卡的效果从场上离开时，可以从自己墓地选择最多有那个时候这张卡持有的超量素材数量的4星的名字带有「武神」的兽战士族怪兽特殊召唤。「武神帝-月读」在自己场上只能有1只表侧表示存在。','\r',char(13)),'\n',char(10)),'抽卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73318863,'光灵使 莱娜','①：这张卡反转的场合，以对方场上1只光属性怪兽为对象发动。这只怪兽表侧表示存在期间，得到那只怪兽的控制权。','获得对方场上1只光属性怪兽的控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73333463,'战甲机人 铠电合','①：把「机人」怪兽解放让这张卡上级召唤成功的场合发动。场上的魔法·陷阱卡全部除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73341839,'短枪管龙',replace(replace('「弹丸」怪兽2只\r\n这个卡名的效果1回合只能使用1次。\r\n①：这张卡在墓地存在，自己场上有「枪管」连接怪兽存在的场合，把自己场上1只连接3以下的怪兽解放才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡不能作为连接标记数量和解放的怪兽相同的怪兽的连接素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73347079,'急袭猛禽-武力林鸮',replace(replace('4星怪兽×2\r\n①：这张卡的攻击力·守备力上升这张卡以外的自己场上的鸟兽族怪兽数量×500。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。从卡组把1只鸟兽族·暗属性·4星怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73356503,'烈风之结界像','只要这张卡在场上表侧表示存在，不能作风属性怪兽以外的特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73359475,'圣骑士 珀西瓦尔',replace(replace('①：只要这张卡有「圣剑」装备魔法卡装备，这张卡等级上升1星并变成暗属性。\r\n②：有「圣剑」装备魔法卡装备的这张卡被送去墓地的场合，以自己墓地1张「圣剑」卡为对象发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73360025,'魔神王的契约书',replace(replace('「魔神王的契约书」的①的效果1回合只能使用1次。\r\n①：自己主要阶段才能发动。从自己的手卡·场上把恶魔族的融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。「DD」融合怪兽融合召唤的场合，也能把自己墓地的怪兽除外作为融合素材。\r\n②：自己准备阶段发动。自己受到1000伤害。','\r',char(13)),'\n',char(10)),'融合召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73398797,'白龙之圣骑士','用「白龙降临」特殊召唤。特殊召唤时，必须以场上或手卡中合计4星以上的怪兽作为祭品。这张卡攻击里侧守备表示的怪兽的场合，不经过伤害计算直接将其在里侧守备表示状态下破坏。可以将这张卡祭掉，从自己的手卡或卡组中特殊召唤1只「青眼白龙」。（这个回合「青眼白龙」不能攻击。）','破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73414375,'次元壶','反转：双方均可以从对方墓地中选至多3张卡从游戏中除外。','除外','是否要选择卡从游戏中除外？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73417207,'极星灵 黑精灵','把这张卡作为同调素材的场合，其他的同调素材怪兽必须是手卡2只名字带有「极星」的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73428497,'齿轮齿轮武库人','这张卡的攻击力上升自己场上的名字带有「齿轮齿轮」的怪兽数量×200的数值。此外，可以把这张卡解放，从卡组把「齿轮齿轮武库人」以外的1只名字带有「齿轮齿轮」的怪兽表侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73431236,'名匠 虎铁','①：这张卡反转的场合发动。从卡组把1张装备魔法卡加入手卡。','检索装备魔法','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73445448,'No.22 不乱健',replace(replace('暗属性8星怪兽×2\r\n这张卡不用超量召唤不能特殊召唤。1回合1次，把这张卡1个超量素材取除，把1张手卡送去墓地，选择对方场上表侧表示存在的1张卡才能发动。这张卡变成守备表示，选择的卡的效果直到结束阶段时无效。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73468603,'舞台旋转','①：从自己卡组选2张卡名不同的场地魔法卡，那之内的1张在自己场上盖放，另1张在对方场上盖放。只要这个效果盖放的卡的其中任意张在场地区域盖放中，双方不能把其他的场地魔法卡发动·盖放。','请选择要盖放到自己场上的卡','请选择要盖放到对方场上的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73481154,'破坏的石人','巨大右臂为特征的石人，用右臂进行压迫攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73483491,'真次世代风筝',replace(replace('名字带有「次世代」的调整＋调整以外的风属性怪兽1只以上\r\n对方不能选择这张卡作为攻击对象。这张卡战斗破坏对方怪兽的场合，可以从自己卡组把1只名字带有「次世代」的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73507661,'妖精之风','场上表侧表示存在的魔法·陷阱卡全部破坏，双方受到破坏的卡数量×300的数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73511233,'娱乐伙伴 U合石人',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，自己场上有怪兽融合召唤的场合才能发动。从自己墓地的灵摆怪兽以及自己的额外卡组的表侧表示的灵摆怪兽之中选「娱乐伙伴」怪兽、「异色眼」怪兽、「魔术师」怪兽之内任意1只加入手卡。\r\n【怪兽效果】\r\n①：这张卡灵摆召唤成功的回合的自己主要阶段才能发动1次。融合怪兽卡决定的包含这张卡的融合素材怪兽从自己场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。那个时候，其他的融合素材怪兽必须全部是龙族怪兽。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73534250,'黑羽-逆卷之托纳多','①：特殊召唤的怪兽在对方场上存在，这张卡召唤成功时，以自己墓地1只「黑羽」调整为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。这个效果的发动后，直到回合结束时自己不是「黑羽」怪兽不能从额外卡组特殊召唤。','特殊召唤','','','','','','','','','','','','','','本数据库来自23333服，如果您获得的版本未标明出处','则那个版本是可耻的盗版');
INSERT INTO texts VALUES(73544866,'守护者·巴奥','当自己场上存在「破邪之大剑-黄昏」时才能召唤·反转召唤·特殊召唤。这张卡每战斗破坏对方1只怪兽并将其送去墓地，攻击力上升1000点。被这张卡战斗破坏的效果怪兽的效果无效化。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73551138,'遗式术师·艾米莉娅','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。这张卡召唤·反转时，自己场上有这张卡以外的名字带有「遗式」的怪兽存在的场合，直到结束阶段时场上的陷阱卡的效果无效。','返回手牌','陷阱的效果无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73558460,'电脑网恢复',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：自己场上的连接怪兽被战斗或者对方的效果破坏的场合，以连接怪兽以外的自己墓地1只怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73567374,'力量解放','这张卡的发动时自己场上表侧表示存在的全部二重怪兽变成再度召唤的状态。这个效果适用的怪兽在结束阶段时变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73574678,'亚马逊吹箭兵','在自己的每1个准备阶段选择对方场上1只怪兽。被选择的怪兽攻击力下降500直到回合结束。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73578229,'杆位','场上表侧表示存在的攻击力最高的怪兽不受魔法的效果影响。场上不存在「杆位」时，场上表侧表示存在的攻击力最高的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73580471,'黑蔷薇龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡同调召唤成功时才能发动。场上的卡全部破坏。\r\n②：1回合1次，从自己墓地把1只植物族怪兽除外，以对方场上1只守备表示怪兽为对象才能发动。那只对方的守备表示怪兽变成表侧攻击表示，那个攻击力直到回合结束时变成0。','\r',char(13)),'\n',char(10)),'场上卡全部破坏','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73594093,'炼装融合',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：从自己的手卡·场上把「炼装」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：这张卡在墓地存在的场合才能发动。墓地的这张卡加入卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'融合召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73599290,'灵魂抽取','支付1000基本分才能发动。只要这张卡在场上存在，从游戏中除外的怪兽的效果以及墓地存在的怪兽的效果不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73616671,'幻像魔法',replace(replace('「幻像魔法」在1回合只能发动1张。\r\n①：把自己场上1只魔法师族怪兽解放才能发动。从自己的卡组·墓地选最多2只「黑魔术师」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73625877,'时间逃脱者','把这张卡从手卡丢弃，选择自己场上表侧表示存在的1只念动力族怪兽发动。选择的怪兽直到下次的自己的准备阶段时从游戏中除外。这个效果在对方回合也能发动。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73628505,'星球改造','①：从卡组把1张场地魔法卡加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73632127,'次元崩落','自己场上有怪兽特殊召唤时才能发动。选择对方场上表侧表示存在的1只怪兽从游戏中除外。那次特殊召唤是超量召唤的场合，这张卡可以在盖放的回合发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73642296,'屋敷童',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：包含以下其中任意种效果的魔法·陷阱·怪兽的效果发动时，把这张卡从手卡丢弃才能发动。那个发动无效。\r\n●从墓地把卡加入手卡·卡组·额外卡组的效果\r\n●从墓地把怪兽特殊召唤的效果\r\n●从墓地把卡除外的效果','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73648243,'沙沼怪','里侧守备表示的这张卡被战斗以外的方式破坏送去墓地时，原本的攻击力·守备力交换并在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73652465,'黑羽-突风之颪',replace(replace('「黑羽-突风之颪」的①的方法的特殊召唤1回合只能有1次。\r\n①：自己场上有「黑羽-突风之颪」以外的「黑羽」怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡作为同调素材送去墓地的场合，以场上1只怪兽为对象才能发动。那只怪兽的表示形式变更。','\r',char(13)),'\n',char(10)),'改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73659078,'雪尘巨人',replace(replace('水属性4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。手卡的水属性怪兽任意数量给对方观看，给人观看的数量的冰指示物给场上表侧表示存在的怪兽放置。只要这张卡在场上表侧表示存在，水属性以外的场上的怪兽的攻击力下降场上的冰指示物数量×200的数值。','\r',char(13)),'\n',char(10)),'放置指示物','请选择要放置冰指示物的怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73665146,'沉默魔术师 LV4',replace(replace('①：只要这张卡在怪兽区域存在，每次对方抽卡，给这张卡放置1个魔力指示物（最多5个）。\r\n②：这张卡的攻击力上升这张卡的魔力指示物数量×500。\r\n③：这张卡有第5个魔力指示物被放置的下次的自己回合的准备阶段，把有5个魔力指示物放置的这张卡送去墓地才能发动。从手卡·卡组把1只「沉默魔术师 LV8」特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73680966,'终结之始','自己墓地暗属性怪兽有7只以上存在的场合才能发动。把自己墓地存在的5只暗属性怪兽从游戏中除外，从自己卡组抽3张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73694478,'转生的超战士',replace(replace('「转生的超战士」在1回合只能发动1张。\r\n①：以自己场上1只「混沌战士」怪兽为对象才能发动。那只怪兽送去墓地，和那只怪兽卡名不同的1只「混沌战士」怪兽无视召唤条件从手卡特殊召唤。\r\n②：自己主要阶段把墓地的这张卡除外，以自己墓地1只「混沌战士」怪兽为对象才能发动。那张卡加入手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73698349,'巨大兽人','这张卡攻击的场合，在战斗阶段终了时变成守备表示。到下一个自己的回合结束时为止，这张卡的表示形式不能变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73702909,'小骑兵','这张卡被战斗破坏的场合，可以从卡组把1只2星以下的战士族怪兽里侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73729209,'技能继承','选择自己场上表侧表示存在的1只怪兽发动。直到这个回合的结束阶段时，选择怪兽的攻击力上升400。此外，可以把墓地存在的这张卡从游戏中除外，自己场上表侧表示存在的1只怪兽的攻击力直到这个回合的结束阶段时上升800。这个效果在这张卡送去墓地的回合不能发动，自己回合才能发动。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73734821,'娱乐伙伴 天空魔术家',replace(replace('「娱乐伙伴 天空魔术家」的②的效果1回合只能使用1次。\r\n①：1回合1次，自己把魔法卡发动的场合发动。这张卡的攻击力上升300。\r\n②：以自己场上1张永续魔法卡为对象才能发动。那张卡回到持有者手卡。那之后，可以从手卡把1张「魔术师」永续魔法卡发动。这个效果在对方回合也能发动。\r\n③：表侧表示的这张卡从场上离开的场合，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'这张卡攻击力上升','自己永续魔法卡回到手卡','是否把「魔术师」永续魔法卡发动？','场上卡破坏','','','','','','','','','','','','');
INSERT INTO texts VALUES(73752131,'熟练的黑魔术师','自己与对方每发动1次魔法，就在这张卡上放置1个魔力指示物（最多只能放置3个）。当这张卡处于放置了3个魔力指示物的状态时，可以将这张卡祭掉，从自己的手卡、卡组或墓地中特殊召唤1只「黑魔术师」上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73776643,'亲子玉米','场地魔法卡表侧表示存在的场合，这张卡的攻击力上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73778008,'深海之怒','这张卡的攻击力·守备力上升自己墓地存在的鱼族·海龙族·水族怪兽数量×500的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73779005,'德拉科尼亚的兽龙骑兵',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，自己的通常怪兽战斗破坏对方怪兽的伤害计算后才能发动。从卡组把1只4星以上的通常怪兽加入手卡。\r\n【怪兽描述】\r\n龙人族国家德拉科尼亚帝国所拥有的龙骑士团陆兵部队。使用鸟铳与铁枪组合攻击而无懈可击，令雷普提尔皇国等周边国家有所畏惧。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73783043,'真次世代加速器','名字带有「次世代」的怪兽从自己卡组加入手卡时，把那1只怪兽给对方观看才能发动。那只怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73787254,'剑窖','场上表侧表示存在的名字带有「X-剑士」的怪兽的攻击力上升自身等级×100，守备力下降自身等级×100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73820802,'鬼签','①：自己准备阶段把这个效果发动。对方宣言卡的种类（怪兽·魔法·陷阱）。自己卡组最上面的卡翻开，翻开的卡是宣言的种类的卡的场合，对方从卡组抽1张。不是的场合，对方手卡随机选1张丢弃。翻开的卡回到卡组最下面。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73828446,'秘旋谍装备-特级臂甲',replace(replace('「秘旋谍-花公子」才能装备。\r\n①：装备怪兽的攻击力上升1000。\r\n②：装备怪兽战斗破坏对方怪兽的场合才能发动。选对方场上1张卡，那张卡和破坏的怪兽除外。\r\n③：场上的表侧表示的这张卡被破坏送去墓地时，以自己墓地1只「秘旋谍-花公子」为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73837870,'阳光妖精','这张卡被光属性的同调怪兽的同调召唤使用送去墓地的场合，自己回复1000基本分。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73853830,'恩底弥翁的十字军',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●1回合1次，可以给场上表侧表示存在的1张可以放置魔力指示物的卡放置1个魔力指示物。直到这个效果把魔力指示物放置的回合的结束阶段时，这张卡的攻击力上升600。','\r',char(13)),'\n',char(10)),'放置魔力指示物','请选择要放置「魔力指示物」的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73860462,'奇妙超量','①：用自己场上的怪兽为素材把1只超量怪兽超量召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73866096,'怒怒怒抽卡','把手卡或者自己场上表侧表示存在的1只名字带有「怒怒怒」的怪兽送去墓地才能发动。从卡组抽2张卡。「怒怒怒抽卡」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73872164,'家破人亡','①：从手卡丢弃1张魔法卡，以对方场上1只表侧表示怪兽为对象才能发动。那只对方的表侧表示怪兽破坏。那之后，有对方手卡的场合，把对方手卡确认，破坏的卡的同名卡全部送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73879377,'武装龙 LV7',replace(replace('这张卡不能通常召唤。「武装龙 LV5」的效果才能特殊召唤。\r\n①：从手卡把1只怪兽送去墓地才能发动。持有送去墓地的那只怪兽的攻击力以下的攻击力的对方场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73881652,'十二兽的方合',replace(replace('①：以自己场上1只「十二兽」超量怪兽为对象才能发动。从卡组选1只「十二兽」怪兽在那只超量怪兽下面重叠作为超量素材。\r\n②：把墓地的这张卡除外，以自己墓地5张「十二兽」卡为对象才能发动（同名卡最多1张）。那5张卡加入卡组洗切。那之后，自己从卡组抽1张。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73887236,'急袭猛禽-起翼猎鹰',replace(replace('鸟兽族4星怪兽×3\r\n①：这张卡可以向特殊召唤的对方怪兽全部各作1次攻击。\r\n②：1回合1次，把这张卡1个超量素材取除，以对方场上1只特殊召唤的怪兽为对象才能发动。这张卡的攻击力上升作为对象的怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73891874,'白角龙','①：这张卡召唤·特殊召唤成功的场合，以对方墓地最多5张魔法卡为对象发动。那些卡除外，这张卡的攻击力上升那些除外的卡数量×300。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73899015,'蛇毒响尾蛇','1回合只有1次，可以给对方场上1只怪兽放置1个毒指示物。这个效果使用的回合这只怪兽不能攻击宣言。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73906480,'武神降临','对方场上有怪兽存在，自己场上没有怪兽存在的场合，选择自己墓地1只名字带有「武神」的怪兽和从游戏中除外的1只自己的名字带有「武神」的怪兽才能发动。选择的2只怪兽特殊召唤。把这个效果特殊召唤的怪兽作为超量召唤的素材的场合，不是兽族·兽战士族·鸟兽族怪兽的超量召唤不能使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73911410,'杀人机器','挥舞着大弯刀的狂暴杀人机器。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73915051,'替罪羊',replace(replace('这张卡发动的回合，自己不能用这张卡的效果以外把怪兽召唤·反转召唤·特殊召唤。\r\n①：在自己场上把4只「羊衍生物」（兽族·地·1星·攻/守0）守备表示特殊召唤。这衍生物不能为上级召唤而解放。','\r',char(13)),'\n',char(10)),'特殊召唤衍生物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73915052,'羊衍生物','由「替罪羊」的效果特殊召唤。这衍生物不能为上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73915053,'羊衍生物','由「替罪羊」的效果特殊召唤。这衍生物不能为上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73915054,'羊衍生物','由「替罪羊」的效果特殊召唤。这衍生物不能为上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73915055,'羊衍生物','由「替罪羊」的效果特殊召唤。这衍生物不能为上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73941492,'调弦之魔术师',replace(replace('←8 【灵摆】 8→\r\n①：只要这张卡在灵摆区域存在，自己场上的怪兽的攻击力·守备力上升自己的额外卡组的表侧表示的「魔术师」灵摆怪兽种类×100。\r\n【怪兽效果】\r\n这张卡不能作从额外卡组的特殊召唤，把这张卡作为融合·同调·超量召唤的素材的场合，其他素材必须全部是「魔术师」灵摆怪兽。这个卡名的怪兽效果1回合只能使用1次。\r\n①：这张卡从手卡的灵摆召唤成功时才能发动。从卡组把「调弦之魔术师」以外的1只「魔术师」灵摆怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化，从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73964868,'星圣·昴星团',replace(replace('光属性5星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以场上1张卡为对象才能发动。那张卡回到持有者手卡。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73977033,'急袭猛禽-推进林鸮','①：自己的「急袭猛禽」怪兽被选择作为对方怪兽的攻击对象时，把这张卡从手卡除外才能发动。那只攻击怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(73988674,'三位一择','宣言额外卡组的怪兽卡的种类（融合·同调·超量）才能发动。双方的额外卡组全部确认，宣言的种类的卡数量多的玩家回复3000基本分。','融合卡','同调卡','超量卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74003290,'迷途风',replace(replace('①：以特殊召唤的1只表侧表示怪兽为对象才能发动。那只怪兽的效果无效化，原本攻击力变成一半。\r\n②：这张卡在墓地存在，从对方的额外卡组有怪兽特殊召唤的场合才能发动。这张卡在自己场上盖放。这个效果盖放的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74009824,'神影依·文迪戈',replace(replace('「影依」怪兽＋风属性怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。这个卡名的①的效果1回合只能使用1次。\r\n①：以自己场上1只怪兽为对象才能发动。这个回合，那只怪兽不会被和特殊召唤的对方怪兽的战斗破坏。这个效果在对方回合也能发动。\r\n②：这张卡被送去墓地的场合，以自己墓地1张「影依」魔法·陷阱卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74010769,'阳炎兽 狮鹫','对方场上有怪兽存在，自己的场上·墓地没有炎属性以外的怪兽存在的场合，这张卡可以从手卡特殊召唤。只要这张卡在场上表侧表示存在，对方不能把这张卡作为卡的效果的对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74029853,'黄金色的竹光','自己场上名字带有「竹光」的装备魔法卡表侧表示存在的场合才能发动。从自己卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74063034,'召唤魔术',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：融合怪兽卡决定的融合素材怪兽从手卡送去墓地，把那1只融合怪兽从额外卡组融合召唤。「召唤兽」融合怪兽融合召唤的场合，也能把自己场上以及自己·对方的墓地的怪兽除外作为融合素材。\r\n②：这张卡在墓地存在的场合，以除外的1只自己的「召唤师 阿莱斯特」为对象才能发动。墓地的这张卡回到卡组，作为对象的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'融合召唤','回收','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74064212,'大日棱柱体','这张卡从怪兽卡区域上送去墓地的场合，可以支付500基本分，把这张卡当作装备卡使用给自己场上表侧表示存在的1只怪兽装备。这张卡的装备怪兽进行战斗的场合，装备怪兽的攻击力在伤害步骤内上升1000。','当成装备卡装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74069667,'DDD 坏薙王 深渊末日神',replace(replace('←5 【灵摆】 5→\r\n「DDD 坏薙王 深渊末日神」的灵摆效果1回合只能使用1次。\r\n①：自己把「DD」怪兽特殊召唤的场合，以自己墓地1只「DD」怪兽为对象才能发动。那只怪兽特殊召唤，自己受到1000伤害。这个回合，对方受到的战斗伤害变成一半。\r\n【怪兽效果】\r\n「DDD 坏薙王 深渊末日神」的①的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合，以自己墓地1只「DDD」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：1回合1次，把这张卡以外的自己场上1只「DD」怪兽解放，以对方场上1只怪兽为对象才能发动。那只怪兽除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74093656,'调整战士','对任何东西进行调节的电波系战士。虽然总是伸出天线，但接收却不那么好。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74094021,'真六武众-瑞穂','自己场上有「真六武众-竹刀」表侧表示存在的场合，这张卡可以从手卡特殊召唤。此外，1回合1次，可以把这张卡以外的自己场上存在的1只名字带有「六武众」的怪兽解放，选择场上存在的1张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74095602,'英雄变化-反光体','自己场上存在的名字带有「元素英雄」的融合怪兽被战斗破坏送去墓地时才能发动。给与对方基本分破坏的融合怪兽等级×300的数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74100225,'进化的特异点','从自己墓地选择1只名字带有「进化虫」的怪兽和1只名字带有「进化龙」的怪兽才能发动。从额外卡组把1只名字带有「进化帝」的怪兽特殊召唤，把选择的怪兽在那下面重叠作为超量素材。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74115234,'八尺勾玉','灵魂怪兽才能装备。装备怪兽战斗破坏对方怪兽送去墓地时，自己基本分回复破坏怪兽的原本攻击力的数值。装备怪兽从自己场上回到手卡让这张卡被送去墓地时，这张卡回到手卡。','LP回复','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74117290,'暗黑界的取引','①：双方玩家从卡组抽1张。那之后，双方玩家选1张手卡丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74122412,'天枪龙之影灵衣',replace(replace('「影灵衣」仪式魔法卡降临。不作只拿7星以外的怪兽来使用的仪式召唤则不能特殊召唤。「天枪龙之影灵衣」的①②的效果1回合各能使用1次，对方回合也能发动。\r\n①：把这张卡从手卡丢弃，以自己场上1只「影灵衣」怪兽为对象才能发动。这个回合那只怪兽不会被战斗·效果破坏。\r\n②：把手卡1张「影灵衣」卡丢弃，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74130411,'生化猎鹰','这张卡在场上表侧表示存在的场合自己场上存在的机械族怪兽被战斗破坏送去墓地时，可以从自己卡组把1只攻击力1000以下的机械族怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74131780,'流氓佣兵部队','这张卡做祭品。破坏场上的1只怪兽。','破坏场上的1只怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74137509,'天使的骰子','①：掷1次骰子。自己场上的怪兽的攻击力·守备力直到回合结束时上升出现的数目×100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74153887,'黑蝎-荆棘的美奈',replace(replace('这张卡对对方造成战斗伤害时，可以从下列效果中选择1项发动：\r\n●从自己的卡组中选择1张名称中含有「黑蝎」字样的卡加入手卡。\r\n●从自己的墓地中选择1张名称中含有「黑蝎」字样的卡加入手卡。','\r',char(13)),'\n',char(10)),'选择一个效果发动','从自己的卡组中选择1张名称中含有「黑蝎」字样的卡加入手牌。','从自己的墓地中选择1张名称中含有「黑蝎」字样的卡加入手牌。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74157028,'电子双生龙',replace(replace('「电子龙」＋「电子龙」\r\n这只怪兽融合召唤只能用上记的卡进行。这张卡在同1次的战斗阶段中可以进行2次攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74163487,'还原点控球后卫',replace(replace('电子界族怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：1回合1次，这张卡以外的连接怪兽连接召唤成功的场合发动。这个回合，这张卡不会被战斗·效果破坏。\r\n②：这张卡在墓地存在，这张卡为素材作连接召唤的连接怪兽被对方的效果破坏的场合才能发动。这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74168099,'炎星侯-马信',replace(replace('炎属性调整＋调整以外的怪兽1只以上\r\n这张卡同调召唤成功时，可以从卡组把1只炎属性·3星怪兽特殊召唤。此外，这张卡同调召唤成功的回合，自己不能把5星以上的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74191942,'苦涩的选择','从卡组选择5张卡给对方看。对方从这些卡中选择1张。对方选择的卡加入自己手卡，其他的卡丢弃去墓地。','请选择５张卡','请选择要加入对方手牌的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74203495,'PSY骨架装备·δ',replace(replace('这张卡不能通常召唤，用卡的效果才能特殊召唤。\r\n①：自己场上没有怪兽存在，对方的魔法卡发动时才能发动。选手卡的这张卡和自己的手卡·卡组·墓地1只「PSY骨架驱动者」特殊召唤，那个发动无效并破坏。这个效果特殊召唤的怪兽全部在结束阶段除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74210057,'纹理转换蛙','①：1回合1次，自己怪兽被选择作为攻击对象时才能发动。那次攻击无效。那之后，从自己的手卡·卡组·墓地选1只电子界族通常怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74270067,'绒儿的魔法阵','直到这个回合的结束阶段前，这张卡的控制者受到的卡的效果的伤害为0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74277583,'剪刀勇士','熟练地利用几个夹子切向对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74294676,'进化帝·半鸟龙',replace(replace('恐龙族4星怪兽×2\r\n①：可以把这张卡2个超量素材取除，以下效果发动。\r\n●魔法·陷阱卡发动时才能发动。那个发动无效并破坏。\r\n●自己或者对方把怪兽召唤·特殊召唤之际才能发动。那个无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'魔法·陷阱卡的发动无效并破坏','召唤无效并破坏','特殊召唤无效并破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74298287,'水精鳞-深渊蒂妮','自己场上有名字带有「水精鳞」的怪兽存在的场合，这张卡用卡的效果从卡组或者墓地加入手卡时，这张卡可以从手卡特殊召唤。此外，这张卡用名字带有「水精鳞」的怪兽的效果特殊召唤成功时，可以从自己墓地选择1只3星以下的名字带有「水精鳞」的怪兽特殊召唤。「水精鳞-深渊蒂妮」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74311226,'海皇的龙骑队',replace(replace('①：只要这张卡在怪兽区域存在，自己的3星以下的海龙族怪兽可以直接攻击。\r\n②：这张卡为让水属性怪兽的效果发动而被送去墓地的场合发动。从卡组把「海皇的龙骑队」以外的1只海龙族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74329404,'D-构成','每次自己场上表侧表示存在的名字带有「命运英雄」的怪兽被破坏，每有1只给这张卡放置1个D指示物。自己的主要阶段时有怪兽的召唤·特殊召唤成功时，可以把D指示物有2个以上放置的这张卡送去墓地，和召唤·特殊召唤的怪兽同名卡最多2张从自己的卡组·墓地加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74335036,'置换融合',replace(replace('这张卡的卡名在规则上当作「融合」使用。\r\n①：从自己场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：把墓地的这张卡除外，以自己墓地1只融合怪兽为对象才能发动。那只怪兽回到额外卡组。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'融合召唤','回到额外卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74364659,'水灵使 艾莉娅','反转：只要这张卡在场上表侧表示存在，获得对方场上1只水属性怪兽的控制权。','获得对方场上1只水属性怪兽的控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74367458,'守护者·艾尔玛','当自己场上存在「蝶之短剑-回音」时才能召唤·反转召唤·特殊召唤。这张卡召唤·特殊召唤成功时，可以从自己的墓地里选择1张装备魔法卡装备在这张卡身上。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74371660,'水精鳞-撼地深渊王',replace(replace('水属性7星怪兽×2\r\n只要持有超量素材的这张卡在场上表侧表示存在，5星以上的怪兽不能攻击。此外，1回合1次，把这张卡1个超量素材取除才能发动。持有比这张卡的攻击力低的攻击力的对方场上的怪兽的效果直到回合结束时无效。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74388798,'沉默剑士 LV5',replace(replace('①：这张卡不受对方的魔法卡的效果影响。\r\n②：这张卡直接攻击给与对方战斗伤害的场合，下次的自己回合的准备阶段把场上的这张卡送去墓地才能发动。从手卡·卡组把1只「沉默剑士 LV7」特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74393852,'十二兽 猪弓',replace(replace('4星怪兽×5\r\n「十二兽 猪弓」1回合1次也能在同名卡以外的自己场上的「十二兽」怪兽上面重叠来超量召唤。\r\n①：这张卡的攻击力·守备力上升这张卡作为超量素材中的「十二兽」怪兽的各自数值。\r\n②：这张卡可以向对方直接攻击。\r\n③：持有的超量素材数量是12以上的这张卡给与对方战斗伤害时才能发动。对方的手卡·场上的卡全部送去墓地，那之后，这张卡变成守备表示。','\r',char(13)),'\n',char(10)),'是否在「十二兽」怪兽上面重叠来超量召唤？','对方卡全部送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74402414,'魔法年代记','把手卡全部送去墓地发动。从卡组选择5张魔法·陷阱卡从游戏中除外。每次对方把魔法卡发动，给这张卡放置1个年代记指示物。可以把这张卡2个年代记指示物取除，从被这张卡的效果从游戏中除外的卡之中让对方选择1张，加入自己手卡。这张卡从场上离开时，这张卡的效果从游戏中除外中的卡每有1张，自己的基本分受到500分伤害。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74416026,'魔玩具行进',replace(replace('①：自己场上的「魔玩具」怪兽为对象的怪兽的效果·魔法·陷阱卡由对方发动时才能发动。那个发动无效并破坏。那之后，以下效果可以适用。\r\n●成为对象的1只「魔玩具」怪兽送去墓地，把1只8星以上的「魔玩具」融合怪兽当作融合召唤从额外卡组特殊召唤。这个效果特殊召唤的怪兽在下次的自己结束阶段除外。','\r',char(13)),'\n',char(10)),'是否特殊召喚？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74416224,'黑鳍条枪兵',replace(replace('水属性3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择场上表侧表示存在的1只怪兽才能发动。选择的怪兽的效果直到结束阶段时无效。','\r',char(13)),'\n',char(10)),'效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74439492,'废铁抛光','选择自己场上表侧表示存在的1只名字带有「废铁」的怪兽破坏。自己场上表侧表示存在的全部名字带有「废铁」的怪兽的攻击力直到这个回合的结束阶段时上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74440055,'小仙人掌斗士','这张卡战斗破坏对方怪兽的场合，在对方场上把1只「针衍生物」（植物族·地·1星·攻/守500）守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74440056,'针衍生物','由「小仙人掌斗士」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74458486,'援护射击','对方怪兽攻击自己场上怪兽的场合，伤害步骤时发动。被攻击的自己怪兽的攻击力上升自己场上表侧表示存在的另外1只怪兽的攻击力的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74506079,'异虫·零',replace(replace('名字带有「异虫」的爬虫族怪兽×2只以上\r\n这张卡的攻击力变成作为融合素材的怪兽种类×500的数值。这张卡得到作为融合素材的怪兽种类的以下效果。\r\n●2种类以上：1回合1次，可以把自己墓地1只爬虫族怪兽里侧守备表示特殊召唤。\r\n●4种类以上：可以把自己墓地1只爬虫族怪兽从游戏中除外，场上1只怪兽送去墓地。\r\n●6种类以上：1回合1次，从自己卡组抽1张卡。','\r',char(13)),'\n',char(10)),'把自己墓地1只爬虫类族怪兽里侧守备表示特殊召唤。','把自己墓地1只爬虫类族怪兽从游戏中除外，场上1只怪兽送去墓地。','从自己卡组抽1张卡。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74509280,'罪 平行齿轮','把这张卡作为同调素材的场合，其他的同调素材怪兽必须是手卡1只名字带有「罪」的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74519184,'手札断杀','①：双方玩家把2张手卡送去墓地。那之后，各自从卡组抽2张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74530899,'时械神 梅塔伊恩',replace(replace('这张卡不能从卡组特殊召唤。\r\n①：自己场上没有怪兽存在的场合，这张卡可以不用解放作召唤。\r\n②：这张卡不会被战斗·效果破坏，攻击表示的这张卡的战斗发生的对自己的战斗伤害变成0。\r\n③：这张卡进行战斗的战斗阶段结束时发动。这张卡以外的场上的怪兽全部回到持有者手卡，给与对方回去数量×300伤害。\r\n④：自己准备阶段发动。这张卡回到持有者卡组。','\r',char(13)),'\n',char(10)),'不用解放召唤','这张卡以外的怪兽返回手牌','这张卡回到卡组','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74576482,'核成海豹','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只水属性怪兽给对方观看。或者都不进行让这张卡破坏。1回合1次，可以从手卡把1张「核成兽的钢核」送去墓地，选择自己墓地存在的1张魔法卡回到卡组最上面。','选择一张「核成兽的钢核」送去墓地','选择一张水属性怪兽给对方观看','破坏「核成海豹」','返回卡组','','','','','','','','','','','','');
INSERT INTO texts VALUES(74580251,'神数的神意',replace(replace('「神数的神意」在1回合只能发动1张。\r\n①：从卡组把「神数的神意」以外的1张「神数」卡加入手卡。\r\n②：自己场上的「神数」卡被破坏的场合，可以作为代替把墓地的这张卡除外。这个效果在这张卡送去墓地的回合不能使用。','\r',char(13)),'\n',char(10)),'是否使用「神数的神意」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74582050,'雾动机龙喷发','①：自己场上的「雾动机龙」怪兽被战斗·效果破坏的场合，以对方场上1张卡为对象才能发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74583607,'DDD 烈火王 铁木真',replace(replace('「DD」怪兽×2\r\n「DDD 烈火王 铁木真」的①的效果1回合只能使用1次。\r\n①：这张卡在怪兽区域存在，自己场上有这张卡以外的「DD」怪兽特殊召唤的场合，以自己墓地1只「DD」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：这张卡被战斗或者对方的效果破坏的场合，以自己墓地1张「契约书」卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','卡片回收','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74586817,'PSY骨架王·Ω',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：1回合1次，自己·对方的主要阶段才能发动。对方手卡随机选1张，那张卡和表侧表示的这张卡直到下次的自己准备阶段表侧表示除外。\r\n②：对方准备阶段，以除外的1张自己或者对方的卡为对象才能发动。那张卡回到墓地。\r\n③：这张卡在墓地存在的场合，以这张卡以外的自己或者对方的墓地1张卡为对象才能发动。那张卡和墓地的这张卡回到卡组。','\r',char(13)),'\n',char(10)),'对方手卡和这张卡除外','除外的卡回到墓地','请选择要回到墓地的卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74591968,'谜之剑士 LV4','当这张卡在通常召唤的场合时，必须里侧守备表示出场。攻击里侧守备表示怪兽的场合，不进行战斗伤害，那只怪兽以里侧守备表示的状态直接破坏。这张卡战斗破坏怪兽的回合的结束阶段，可以把这张卡送去墓地，从手卡·卡组特殊召唤1只「谜之剑士 LV6」','破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74593218,'英豪冠军 草薙剑王',replace(replace('战士族4星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除才能发动。陷阱卡的发动无效并破坏。那之后，这张卡的攻击力上升500。','\r',char(13)),'\n',char(10)),'陷阱无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74605254,'DD 魔导贤者 伽利莱',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「DD」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n②：自己准备阶段发动。这张卡的灵摆刻度上升2（最多到10）。那之后，持有这张卡的灵摆刻度以下的等级的除「DD」怪兽以外的自己场上的怪兽全部破坏。\r\n【怪兽效果】\r\n「DD 魔导贤者 伽利莱」的怪兽效果1回合只能使用1次。\r\n①：把这张卡从手卡丢弃，以自己场上1张「DD」卡或者「契约书」卡为对象才能发动。那张卡回到持有者手卡。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'自己场上1张「DD」卡或者「契约书」卡回到手卡','当作魔法卡发动','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74611888,'翻脸不认人','和原本等级不同等级的怪兽在场上表侧表示存在的场合才能发动。场上的怪兽全部变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74637266,'章鱼怪','鱼的头，章鱼的脚，非常不可思议的生物，用枪发动攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74640994,'地中族的决战',replace(replace('①：可以从以下效果选择1个发动。发动后这张卡不送去墓地，直接盖放。\r\n●选自己场上1只里侧表示的「地中族」怪兽变成表侧攻击表示或者表侧守备表示。\r\n●选场上1只表侧表示的「地中族」怪兽变成里侧守备表示。\r\n●选场上1只表侧表示的「地中族」怪兽，那只怪兽的攻击力·守备力直到回合结束时变成那个原本的攻击力和守备力合计数值。\r\n●这个回合，「地中族」卡的所发动的效果不会被无效化。','\r',char(13)),'\n',char(10)),'选自己场上1只里侧表示的「地中族」怪兽变成表侧攻击表示或者表侧守备表示。','选场上1只表侧表示的「地中族」怪兽变成里侧守备表示。','选场上1只表侧表示的「地中族」怪兽，那只怪兽的攻击力·守备力直到回合结束时变成那个原本的攻击力和守备力合计数值。','这个回合，「地中族」卡的所发动的效果不会被无效化。','','','','','','','','','','','','');
INSERT INTO texts VALUES(74641045,'魔偶甜点·布丁公主',replace(replace('①：自己墓地没有怪兽存在的场合，这张卡的攻击力·守备力上升800。\r\n②：这张卡和对方怪兽进行战斗的伤害计算后，以对方场上1张卡为对象才能发动。那张对方的卡破坏。\r\n③：这张卡被对方破坏送去墓地的场合发动。这张卡回到卡组。','\r',char(13)),'\n',char(10)),'返回卡组','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74657662,'大日素材','名字带有「大日」的怪兽才能装备。装备怪兽的攻击力上升600。场上表侧表示存在的这张卡被送去墓地的场合，可以从卡组把1张名字带有「大日」的魔法卡加入手卡。','检索','','','','','','','','','','','','','','','');
