INSERT INTO texts VALUES(74711057,'元素英雄 地球侠',replace(replace('「元素英雄 海洋侠」＋「元素英雄 森林侠」\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：把这张卡以外的自己场上1只表侧表示的「元素英雄」怪兽解放才能发动。这张卡的攻击力直到回合结束时上升解放的怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74713516,'暗黑之宝箱怪 LV1','反转：从卡组抽1张卡。此外，自己的准备阶段时，可以把表侧表示的这张卡送去墓地，从手卡·卡组特殊召唤1只「暗黑之宝箱怪 LV3」','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74715061,'条带化伙伴犬',replace(replace('这张卡不能通常召唤，用这张卡的①的效果才能特殊召唤。这个卡名的②的效果1回合只能使用1次。\r\n①：自己场上发动的怪兽的效果的发动无效的场合才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡特殊召唤成功时，以自己墓地1只4星以下的电子界族怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74717840,'地龟的光藓','自己场上有名字带有「自然」的怪兽表侧表示存在的场合才能发动。这个回合，对方的效果怪兽不能把效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74728028,'超量应接','选择自己场上表侧表示存在的1只怪兽才能发动。和选择的怪兽相同等级的1只怪兽从手卡特殊召唤。这个效果特殊召唤的怪兽的效果无效化，攻击力·守备力变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74730899,'燃料电池人','自己场上有名字带有「电池人」的怪兽表侧表示2只以上存在的场合，这张卡可以从手卡特殊召唤。1回合1次，可以把这张卡以外的自己场上存在的1只名字带有「电池人」的怪兽解放，选择对方场上存在的1张卡回到持有者手卡。','返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74741494,'能力调整','自己场上表侧表示存在的全部怪兽的等级直到结束阶段时下降1星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74762582,'地中族妖魔',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：对方把魔法·陷阱·怪兽的效果发动时，把手卡·场上的这张卡送去墓地，以自己场上1只「地中族」怪兽为对象才能发动。那个发动无效。那之后，作为对象的怪兽变成里侧守备表示。\r\n②：以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示，选自己的手卡·墓地1只「地中族」怪兽表侧守备表示或者里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'发动无效','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74822425,'神影依·舍金纳迦',replace(replace('「影依」怪兽＋地属性怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。这个卡名的①的效果1回合只能使用1次。\r\n①：特殊召唤的怪兽把效果发动时才能发动。那个发动无效并破坏。那之后，自己把手卡1张「影依」卡送去墓地。\r\n②：这张卡被送去墓地的场合，以自己墓地1张「影依」魔法·陷阱卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'无效并破坏','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74823665,'因菲尔诺','这张卡不能进行通常召唤。从自己墓地里除外1只炎属性怪兽进行特殊召唤。这张卡战斗破坏对方怪兽并将其送去墓地时，给与对方基本分1500分伤害。','1500伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74825788,'H-火热之心','①：以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升500，这个回合那只怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74839123,'魔玩具圣域',replace(replace('丢弃1张手卡，从自己的额外卡组把2只「魔玩具」怪兽送去墓地才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，自己场上的全部融合怪兽也当作「魔玩具」怪兽使用。\r\n②：这张卡被送去墓地的场合，以自己墓地1只「魔玩具」融合怪兽为对象才能发动。那只怪兽回到额外卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74841885,'天魔神 因维希尔',replace(replace('这张卡不能特殊召唤。得到祭品召唤时的祭品的以下效果。\r\n●光属性·天使族：只要这张卡在场上表侧表示存在，魔法卡的效果无效。\r\n●暗属性·恶魔族：只要这张卡在场上表侧表示存在，陷阱卡的效果无效。','\r',char(13)),'\n',char(10)),'魔法卡的效果无效','陷阱卡的效果无效','魔法和陷阱卡的效果无效','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74845897,'真炎的爆发','自己墓地存在的守备力200的炎属性怪兽尽可能特殊召唤。这个效果特殊召唤的怪兽在这个回合的结束阶段时从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74848038,'死者转生','①：丢弃1张手卡，以自己墓地1只怪兽为对象才能发动。那只怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74850403,'星霜之灵摆读阵',replace(replace('「星霜之灵摆读阵」的②的效果1回合只能使用1次。\r\n①：只要这张卡在魔法与陷阱区域存在，对方不能把自己场上的魔法师族怪兽作为魔法卡的效果的对象。\r\n②：表侧表示的「魔术师」灵摆怪兽卡从自己的怪兽区域·灵摆区域离开的场合发动。从卡组把1只「魔术师」灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74852097,'幻之狮鹫','隐居于山岳的传说中怪兽。据说它的双翼振翅一下就会掀起风暴。好像跟鹰身女妖的关系很差，围绕着狩猎场不断发生争纷。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74852810,'叠光捕获','选择对方场上1只持有超量素材的超量怪兽和自己场上1只超量怪兽才能发动。把选择的对方怪兽的超量素材全部取除，把这张卡在选择的自己怪兽下面重叠作为超量素材。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74854609,'虫装变换','自己场上的名字带有「甲虫装机」的怪兽被选择作为攻击对象时才能发动。攻击对象怪兽当作装备卡使用给1只攻击怪兽装备。只要因这个效果被名字带有「甲虫装机」的怪兽装备中，自己得到那只装备怪兽的控制权。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74860293,'废品破坏王',replace(replace('「废品同调士」＋调整以外的怪兽1只以上\r\n这张卡同调召唤成功时，可以选择最多有作为这张卡的同调素材的调整以外的怪兽数量的场上存在的卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74875003,'太阳神的使徒',replace(replace('①：这张卡召唤·反转召唤·特殊召唤成功时才能发动。从手卡·卡组把最多2只「太阳神的使徒」特殊召唤。\r\n②：只要这张卡在怪兽区域存在，自己不能用「太阳神的使徒」的效果以外把怪兽特殊召唤，这张卡不能为「奥西里斯之天空龙」「欧贝利斯克之巨神兵」「太阳神之翼神龙」的上级召唤以外而解放。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74892653,'星尘战士',replace(replace('同调怪兽调整＋调整以外的同调怪兽1只以上\r\n①：对方把怪兽特殊召唤之际，把这张卡解放才能发动。那次特殊召唤无效，那些怪兽破坏。\r\n②：这张卡的①的效果适用的回合的结束阶段才能发动。为那个效果发动而解放的这张卡从墓地特殊召唤。\r\n③：战斗或者对方的效果让表侧表示的这张卡从场上离开的场合才能发动。从额外卡组把1只8星以下的「战士」同调怪兽当作同调召唤作特殊召唤。','\r',char(13)),'\n',char(10)),'无效特殊召唤','苏生','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74923978,'强制接收','自己丢弃手卡时才能发动。那之后每次自己的手卡丢弃，对方选择相同数量的手卡丢弃。','手牌丢弃','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74926274,'灵摆显现',replace(replace('「灵摆显现」在1回合只能发动1张。\r\n①：把自己场上1只怪兽送去墓地才能发动。和那只怪兽的原本等级相同等级的1只灵摆怪兽从卡组特殊召唤。这个效果特殊召唤的怪兽在结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74937659,'地中族邪界妖魔',replace(replace('反转怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡的攻击力上升作为这张卡的连接素材的「地中族」怪兽的原本等级合计×100。\r\n②：自己主要阶段才能发动。从卡组把1只反转怪兽送去墓地，从手卡把1只怪兽在这张卡所连接区里侧守备表示特殊召唤。\r\n③：1回合1次，这张卡所连接区的怪兽反转的场合发动。从自己的卡组·墓地选1只反转怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74952447,'曾被称为神的龟','只要这张卡在场上表侧表示存在，双方不能把攻击力1800以上的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74968065,'赫卡忒利斯','①：自己主要阶段把这张卡从手卡丢弃去墓地才能发动。从卡组把1张「神之居城-瓦尔哈拉」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74976215,'十字剑猎手','自己场上有这张卡以外的昆虫族怪兽存在的场合，自己场上存在的昆虫族怪兽攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74983881,'小玉米','1回合1次，自己的主要阶段2支付500基本分才能发动。在自己场上把1只「玉米粒衍生物」（植物族·地·1星·攻/守0）特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74983882,'玉米粒衍生物','由「小玉米」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(74997493,'锁龙蛇-骷髅四面鬼',replace(replace('卡名不同的怪兽2只以上\r\n①：这张卡得到作为这张卡的连接素材的怪兽数量的以下效果。\r\n●2只以上：这张卡所连接区有怪兽召唤·特殊召唤的场合发动。那些怪兽的攻击力·守备力上升300。\r\n●3只以上：1回合1次，自己主要阶段才能发动。从手卡把1只怪兽特殊召唤。\r\n●4只：这张卡连接召唤成功时才能发动。自己从卡组抽4张，那之后选3张手卡用喜欢的顺序回到卡组下面。','\r',char(13)),'\n',char(10)),'攻击力·守备力上升','从手卡把1只怪兽特殊召唤','抽滤','2只以上怪兽为素材','3只以上怪兽为素材','','','','','','','','','','','');
INSERT INTO texts VALUES(75014062,'魔力掌握',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以场上1张可以放置魔力指示物的卡为对象才能发动。给那张卡放置1个魔力指示物。那之后，可以从卡组把1张「魔力掌握」加入手卡。','\r',char(13)),'\n',char(10)),'是否要把1张「魔力掌握」加入手牌？','请选择要放置「魔力指示物」的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75041269,'幽狱之时计塔','对方回合的准备阶段时，这张卡放置1个时计指示物。时计指示物合计有4个以上的场合，这张卡的控制者不会受到战斗伤害。放置有4个以上时计指示物的这张卡被破坏送去墓地时，从手卡·卡组特殊召唤1只「命运英雄 恐惧人」。','放置指示物','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75043725,'冥界的使者','当这张卡从场上被送去墓地时，各自从自己的卡组中选择1张3星以下的通常怪兽，相互确认之后分别加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75059201,'方程式运动员 涡压越野车手','这张卡的攻击力上升这张卡的等级×300。原本的等级或者阶级比这张卡的等级低的对方怪兽不能选择这张卡以外的怪兽作为攻击对象。对方不能把这张卡以外的怪兽作为原本的等级或者阶级比这张卡的等级低的怪兽的效果的对象。每次「方程式运动员」魔法/陷阱卡的效果或者卡的发动：可以把这张卡的等级上升1星。只要自己场上有7星以上的这张卡存在，自己的「方程式运动员」怪兽进行战斗的场合，对方直到伤害步骤结束时怪兽的效果不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75064463,'鹰身女王','把这张卡从手卡丢弃去墓地。从卡组把1张「鹰身女妖的狩猎场」加入手卡。这张卡的卡名，只要在场上或者墓地存在当作「鹰身女郎」使用。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75078585,'截敌蛋','①：自己场上的怪兽被战斗·效果破坏送去墓地的场合才能发动。从自己的手卡·卡组·墓地选1只「小走鹃」特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75081613,'巨大钻土虫','这张卡不能通常召唤。把自己墓地存在的1只昆虫族怪兽从游戏中除外的场合才能特殊召唤。这张卡给与对方基本分战斗伤害时，从对方卡组上面把1张卡送去墓地。','卡组送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75105429,'同调驱逐','选择对方场上表侧表示存在的1只同调怪兽从游戏中除外，对方从卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75109441,'半蛇人 萨库兹','这张卡1个回合可以有1次变回里侧守备表示。这张卡反转时，对方场上的盖伏的全部魔法·陷阱卡翻开，确认后变回原来的盖伏形式。','变成里侧守备','确认盖卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75116619,'真六武众-缘','自己场上有「真六武众-缘」以外的名字带有「六武众」的怪兽表侧表示存在的场合，1回合1次，可以把自己墓地存在的2只名字带有「六武众」的怪兽从游戏中除外，选择场上表侧表示存在的1只怪兽回到手卡。这个效果在对方回合也能发动。此外，自己场上有「真六武众-缘」以外的名字带有「六武众」的怪兽表侧表示2只以上存在的场合，这张卡的攻击力·守备力上升500。','返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75119040,'廷达魔三角之锐角地狱犬',replace(replace('「廷达魔三角」怪兽3只\r\n①：自己墓地有包含「廷达魔三角之底边守卫者」的「廷达魔三角」怪兽3种类以上存在的场合，这张卡的攻击力上升3000。\r\n②：这张卡的攻击力上升这张卡所连接区的「廷达魔三角」怪兽数量×500。\r\n③：这张卡攻击宣言的战斗阶段结束时才能发动。在自己场上把1只「廷达魔三角衍生物」（恶魔族·暗·1星·攻/守0）特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75119041,'廷达魔三角衍生物','由「廷达魔三角之锐角地狱犬」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75132317,'雄炎星-蛇林','1回合1次，名字带有「炎舞」的魔法·陷阱卡被送去自己墓地的场合，可以从卡组选1张名字带有「炎舞」的陷阱卡在自己场上盖放。此外，自己场上没有这张卡以外的怪兽存在的场合，1回合1次，把自己场上表侧表示存在的2张名字带有「炎舞」的魔法·陷阱卡送去墓地才能发动。从卡组抽1张卡。','盖放','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75141056,'英雄假面','①：以自己场上1只表侧表示怪兽为对象才能发动。从卡组把1只「元素英雄」怪兽送去墓地，作为对象的自己的表侧表示怪兽直到结束阶段当作和这个效果送去墓地的怪兽同名卡使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75162696,'维多利亚','1回合1次，可以把对方墓地存在的1只龙族怪兽在自己场上特殊召唤。只要这张卡在场上表侧表示存在，对方不能选择表侧表示存在的其他的天使族怪兽作为攻击对象。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75180828,'水精鳞-大蓝深渊鲸','自己的主要阶段时，从手卡把4只名字带有「水精鳞」的怪兽丢弃去墓地才能发动。这张卡从手卡特殊召唤。这个效果特殊召唤时，这张卡的攻击力上升500，选择最多有自己墓地的名字带有「水精鳞」的怪兽数量的对方场上的卡破坏。此外，可以通过把这张卡以外的表侧攻击表示存在的1只名字带有「水精鳞」的怪兽解放，这个回合这张卡向守备表示怪兽攻击的场合，不进行伤害计算把那只怪兽破坏。','特殊召唤','破坏','效果附加','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75190122,'黑·爆·裂·破·魔·导','①：原本卡名是「黑魔术师」和「黑魔术少女」的怪兽各在自己场上存在的场合才能发动。对方场上的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75195825,'龙剑士 卓辉星·灵摆',replace(replace('←3 【灵摆】 3→\r\n①：只在这张卡在灵摆区域存在才有1次，以自己或者对方的灵摆区域1张卡为对象才能发动。那张卡破坏。\r\n【怪兽描述】\r\n得到志同道合之士们的力量后「龙剑士 光辉星·灵摆」成长的模样。他是被施过不明神秘诅咒后显现出类似龙魔族的龙之力，但在此之前的记忆全部丢失了，真相因此不明。他相信“龙化秘法”就是解开那诅咒和记忆的关键，今天仍旧继续着前去讨伐邪恶魔王的旅程。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75198893,'扫描机器人','选择从游戏中除外的1只对方怪兽发动。这张卡直到结束阶段时当作和选择怪兽同名卡使用，变成和选择怪兽相同属性·等级·攻击力·守备力。这个效果1回合只能使用1次。这个效果适用的这张卡从场上离开的场合，从游戏中除外。','卡名变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75209824,'守护者雕像','这张卡1个回合1次可以变成里侧守备表示。这张卡反转召唤成功时，对方场上1只怪兽回到持有者的手卡。','变成里侧守备','返回手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75214390,'叠光推进者','自己场上有攻击力2000以上的怪兽存在的场合，这张卡可以从手卡表侧守备表示特殊召唤。这个方法的「叠光推进者」的特殊召唤1回合只能有1次。此外，把墓地的这张卡从游戏中除外，选择自己场上1只持有超量素材的超量怪兽才能发动。选择的怪兽的攻击力上升那只怪兽的超量素材数量×500的数值。这个效果在这张卡送去墓地的回合不能发动。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75249652,'业炎防护罩 -火焰之力-','①：对方怪兽的攻击宣言时才能发动。对方场上的攻击表示怪兽全部破坏，自己受到这个效果破坏的怪兽的原本攻击力合计数值一半的伤害。那之后，给与对方为和自己受到的伤害相同数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75252099,'蔷薇鸟','场上表侧攻击表示存在的这张卡被对方怪兽的攻击破坏送去墓地时，可以从自己卡组把2只植物族调整在自己场上表侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75253697,'No.72 行列怪兽 战车之飞车',replace(replace('6星怪兽×2\r\n1回合1次，把这张卡2个超量素材取除，选择对方场上表侧表示存在的1只怪兽和对方场上盖放的1张魔法·陷阱卡才能发动。选择的卡破坏。这个效果发动过的回合，对方受到的战斗伤害变成一半。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75285069,'莫伊斯彻星人','用3只怪兽做这张卡的召唤祭品召唤的场合，对方场上的全部魔法·陷阱破坏。','解放3只怪兽召唤','魔陷破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75286621,'召唤兽 梅尔卡巴',replace(replace('「召唤师 阿莱斯特」＋光属性怪兽\r\n①：1回合1次，怪兽的效果·魔法·陷阱卡发动时，把和那张卡相同种类（怪兽·魔法·陷阱）的1张手卡送去墓地才能发动。那个发动无效并除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75292259,'海神独角仙','这张卡向对方场上表侧攻击表示存在的怪兽攻击，那只怪兽没被战斗破坏的场合，可以向同只怪兽继续攻击。这个效果1回合可以使用最多2次。','是否要继续攻击？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75304793,'音响放大器',replace(replace('①：只要这张卡在场地区域存在，每次「音响战士」卡的效果发动给这张卡放置1个音响指示物。\r\n②：场上的「音响战士」怪兽的攻击力上升这张卡的音响指示物数量×100。\r\n③：1回合1次，可以把自己场上5个或者7个音响指示物取除把以下效果发动。\r\n●5个：给与对方为场上的「音响战士」卡数量×300伤害。\r\n●7个：选最多有场上的「音响战士」卡数量的对方的场上·墓地的卡除外。','\r',char(13)),'\n',char(10)),'5个：给与对方为场上的「音响战士」卡数量×300伤害','7个：选最多有场上的「音响战士」卡数量的对方的场上·墓地的卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75326861,'天刑王 黑天',replace(replace('恶魔族调整＋调整以外的恶魔族怪兽1只以上\r\n只要这张卡在场上表侧表示存在，双方不能同调召唤。1回合1次，选择有装备卡装备的1只对方怪兽才能发动。选择的怪兽装备的装备卡全部破坏，给与对方基本分破坏数量×400的数值的伤害。','\r',char(13)),'\n',char(10)),'装备卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75347539,'磁石战士 电磁武神',replace(replace('这张卡不能通常召唤。从自己的手卡·场上把「磁石战士α」「磁石战士β」「磁石战士γ」各1只解放的场合可以特殊召唤。\r\n①：把这张卡解放，以自己墓地的「磁石战士α」「磁石战士β」「磁石战士γ」各1只为对象才能发动。那些怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75356564,'小龙','非常小的龙，充分用尽那弱小的身体进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75361204,'灰篮分裂',replace(replace('「灰篮分裂」的②的效果1回合只能使用1次。\r\n①：以自己场上1只怪兽为对象才能把这张卡发动。这张卡当作攻击力上升500的装备卡使用给那只怪兽装备。\r\n②：自己主要阶段，把这张卡的效果装备的这张卡送去墓地才能发动。这张卡装备过的怪兽破坏，从卡组把2只「灰篮」怪兽特殊召唤（同名卡最多1张）。这个效果特殊召唤的怪兽在结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75363626,'魔偶甜点·泡芙骑士','这张卡被对方破坏送去墓地时，这张卡回到卡组。此外，只要这张卡在场上表侧表示存在，对方不能选择「魔偶甜点·泡芙骑士」以外的名字带有「魔偶甜点」的怪兽作为攻击对象。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75367227,'鬼计妖魔·阿鲁卡德',replace(replace('3星怪兽×2\r\n对方不能选择这张卡以外的名字带有「鬼计」的怪兽以及里侧守备表示怪兽作为攻击对象。把这张卡1个超量素材取除才能发动。选择对方场上盖放的1张卡破坏。「鬼计妖魔·阿鲁卡德」的这个效果1回合只能使用1次。此外，这张卡被送去墓地的场合，可以从自己墓地选择这张卡以外的1张名字带有「鬼计」的卡加入手卡。','\r',char(13)),'\n',char(10)),'盖卡破坏','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75372290,'绝对防御将军','这张卡召唤·反转召唤成功的场合守备表示。这张卡可以在守备表示时攻击。守备表示攻击时用攻击力的数值计算伤害。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75375465,'地狱的番熊','只要这张卡在自己场上表侧表示存在，自己场上的「万魔殿-恶魔的巢窟-」不会被对方所控制的卡的效果所破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75376965,'恍惚的人鱼','诱惑在海上航行的人们并将他们淹死的美人鱼。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75380687,'咒符龙',replace(replace('「黑魔术师」＋龙族怪兽\r\n这张卡用以上记卡为融合素材的融合召唤或者用「蒂迈欧之眼」的效果才能特殊召唤。\r\n①：这张卡特殊召唤成功的场合，以自己·对方的墓地的魔法卡任意数量为对象发动。那些卡除外，这张卡的攻击力上升那些除外的卡数量×100。\r\n②：这张卡被破坏的场合，以自己墓地1只魔法师族怪兽为对象才能发动。那只魔法师族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'除外','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75390004,'角龙','全身长满角的恐龙。突击攻击很强烈！','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75392615,'心灵透视','支付500基本分。对方的手卡和对方场上盖伏的卡全部确认。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75417459,'拘束解除','可以把自己场上的1只「铁骑士 基亚·弗里德」做祭品，从自己手卡·卡组特殊召唤1只「剑圣-赤膊的基亚·弗里德」上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75421661,'怒怒怒斗笠剑士','只要反转召唤的这张卡在场上表侧表示存在，这张卡的攻击力上升3500。此外，这张卡反转时，选择场上表侧表示存在的最多2只怪兽才能发动。选择的怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75425043,'PSY骨架装备·α',replace(replace('这张卡不能通常召唤，用卡的效果才能特殊召唤。\r\n①：自己场上没有怪兽存在，对方把怪兽召唤·特殊召唤时才能发动。选手卡的这张卡和自己的手卡·卡组·墓地1只「PSY骨架驱动者」特殊召唤，从卡组把「PSY骨架装备·α」以外的1张「PSY骨架」卡加入手卡。这个效果特殊召唤的怪兽全部在结束阶段除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75425320,'真龙凰的使徒',replace(replace('「真龙凰的使徒」的①②③的效果1回合各能使用1次。\r\n①：以自己墓地3张「真龙」卡为对象才能发动。那些卡加入卡组洗切。那之后，自己从卡组抽1张。\r\n②：自己主要阶段才能发动。把1只「真龙」怪兽表侧表示上级召唤。\r\n③：这张卡从魔法与陷阱区域送去墓地的场合，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'回收并抽卡','上级召唤','魔法·陷阱卡破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75433814,'No.40 机关傀儡-天堂弦乐手',replace(replace('8星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。给这张卡以外的场上表侧表示存在的全部怪兽放置1个线指示物。下次的对方的结束阶段时，有线指示物放置的怪兽全部破坏，给与对方基本分破坏的怪兽数量×500的数值的伤害。','\r',char(13)),'\n',char(10)),'放置指示物','破坏并伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75434695,'元素英雄 森林侠','①：1回合1次，自己准备阶段才能发动。选自己的卡组·墓地1张「融合」加入手卡。','把1张「融合」魔法卡加入手牌。','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75452921,'梦幻崩影·地狱犬',replace(replace('卡名不同的怪兽2只\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功的场合，丢弃1张手卡，以对方的主要怪兽区域1只特殊召唤的怪兽为对象才能发动。那只怪兽破坏。这个效果的发动时这张卡是互相连接状态的场合，再让自己可以从卡组抽1张。\r\n②：只要这张卡在怪兽区域存在，自己场上的互相连接状态的怪兽不会被效果破坏。','\r',char(13)),'\n',char(10)),'','是否抽卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75457624,'灵兽的相绊','①：把自己场上2只表侧表示的「灵兽」怪兽除外才能发动。从额外卡组把1只「灵兽」怪兽无视召唤条件特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75487237,'中型盾守卫者','这张卡1个回合1次可以变成里侧守备表示。以里侧表示的这只怪兽为对象的魔法卡的发动无效。那个时候，这张卡变成表侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75498415,'黑羽-晓之希洛克',replace(replace('①：对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以不用解放作通常召唤。\r\n②：1回合1次，自己主要阶段1以自己场上1只「黑羽」怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升那只怪兽以外的场上的「黑羽」怪兽的攻击力的合计数值。这个效果发动的回合，不用作为对象的怪兽不能攻击。','\r',char(13)),'\n',char(10)),'不用祭品召唤','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75499502,'主人与能手','怪兽使高手、忠心于主人的怪兽。组合起来相当完美。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75500286,'封印之黄金柜','①：从卡组选1张卡除外。这张卡的发动后第2次的自己准备阶段，这个效果除外的卡加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75524092,'堕恶之爪','装备怪兽的攻击力上升300。装备怪兽被战斗破坏的场合，作为代替让这张卡回到手卡。再把进行战斗的对方怪兽以外的1只怪兽破坏，给与对方基本分600分伤害。那之后，在对方场上把1只「邪心衍生物」（恶魔族·暗·7星·攻/守2500）特殊召唤。这张卡回到手卡的回合「堕恶之爪」不能从手卡使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75524093,'邪心衍生物','由「堕恶之爪」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75525309,'六武派二刀流','自己场上存在的怪兽只有表侧攻击表示存在的名字带有「六武众」的怪兽1只的场合才能发动。选择对方场上存在的2张卡回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75539614,'念力宝剑',replace(replace('「念力宝剑」在1回合只能发动1张。支付100的倍数的基本分才能把这张卡发动（最多2000）。\r\n①：装备怪兽的攻击力·守备力上升因为这张卡发动而支付的基本分数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75559356,'魔界的机械兵','暗之力制造的机器士兵，疯狂的将敌人破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75560629,'打火石','这张卡装备的怪兽，不能改变表示形式和攻击宣言，攻击力下降300。装备怪兽破坏的场合，选择场上1只怪兽，把这张卡装备在那只怪兽上。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75574498,'珂珑公主',replace(replace('4星怪兽×2\r\n这张卡超量召唤成功时，可以选择自己墓地1只「玩具盒」特殊召唤。自己场上有这张卡以外的怪兽存在的场合，对方不能把这张卡作为攻击对象，也不能作为卡的效果的对象。此外，自己场上的通常怪兽被战斗或者卡的效果破坏送去墓地的场合，把这张卡1个超量素材取除才能发动。从自己的卡组·墓地选1只通常怪兽表侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75582395,'圣鸟','尾巴非常长的鸟，全身散发出神圣的光芒。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75620895,'独眼学艺者',replace(replace('4星怪兽×3\r\n把这张卡1个超量素材取除，选择对方场上1只超量怪兽才能发动。这张卡当作和选择的怪兽同名卡使用，得到相同效果。这个效果只在这张卡在场上表侧表示存在能使用1次。','\r',char(13)),'\n',char(10)),'效果复制','复制效果结束','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75622824,'诡术师的魔术4','①：把自己场上1只表侧表示的「诡术师」送去墓地才能发动。对方场上的怪兽数量的「诡术师衍生物」（魔法师族·风·5星·攻2000/守1200）在自己场上守备表示特殊召唤。这衍生物不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75622825,'诡术师衍生物','由「诡术师的魔术4」的效果特殊召唤。这衍生物不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75646173,'法兰克斯','上下都有头的令人作呕的怪兽。从口中吐出激光。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75646520,'金属探知器','永续陷阱卡发动时才能发动。直到回合结束时场上的全部永续陷阱卡的效果无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75652080,'二重旋风','①：以自己场上1张魔法·陷阱卡和对方场上1张魔法·陷阱卡为对象才能发动。那些卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75672051,'黑牙之魔术师',replace(replace('←8 【灵摆】 8→\r\n①：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时变成一半。那之后，这张卡破坏。\r\n【怪兽效果】\r\n这张卡在规则上也当作「超量龙」卡使用。\r\n①：这张卡被战斗·效果破坏的场合，以自己墓地1只魔法师族·暗属性怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75673220,'龙口花','这张卡被送去墓地时，对方手卡随机选择1张，直到结束阶段时表侧表示从游戏中除外。「龙口花」的效果1回合只能使用1次。','手牌除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75675029,'记忆破坏王','这张卡直接攻击给与对方基本分战斗伤害时，对方墓地存在的同调怪兽全部从游戏中除外，给与对方基本分那个数量×1000的数值的伤害。','除外并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75676192,'方程式运动员 车间运输员',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡的攻击力上升这张卡的等级×300。每次「方程式运动员」魔法/陷阱卡的效果或者卡的发动：可以把这张卡的等级上升1星。这张卡得到这张卡等级的以下效果。\r\n●11星以上：不会被战斗或者卡的效果破坏。\r\n●13星以上：1回合1次：可以选择自己墓地1只「方程式运动员」怪兽；那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75702749,'幻煌龙的螺旋绞',replace(replace('通常怪兽才能装备。「幻煌龙的螺旋绞」的②的效果1回合只能使用1次。\r\n①：装备怪兽的攻击力上升500。\r\n②：装备怪兽战斗破坏对方怪兽时才能发动。从自己的手卡·卡组·墓地选1只「幻煌龙 螺旋」特殊召唤，这张卡给那只怪兽装备。那之后，给与对方1000伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75719089,'光荣的圣骑士团',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己场上1只「圣骑士」怪兽为对象才能发动。从卡组选1张那只自己怪兽可以装备的装备魔法卡给那只怪兽装备。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75732622,'拷问巨人','这张卡不能通常召唤。通过在自己场上把2只「拷问衍生物」（恶魔族·暗·1星·攻/守0）攻击表示特殊召唤可以在对方场上特殊召唤。把这张卡特殊召唤的回合，自己不能通常召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75732623,'拷问衍生物','由「拷问巨人」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75733063,'机皇兵 神空一型','这张卡的攻击力上升这张卡以外的场上表侧表示存在的名字带有「机皇」的怪兽数量×200的数值。这张卡被战斗破坏送去墓地时，可以从自己卡组把1只名字带有「机皇兵」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75745607,'火之迦具土','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到持有者的手卡。这张卡给与对方玩家战斗伤害的场合，下个回合的抽卡阶段时，对方抽卡前把手卡全部丢弃。','返回手牌','手牌丢弃','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75775867,'变形斗士·弹弓',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：1回合1次，可以把「变形斗士·弹弓」以外的1只名字带有「变形斗士」的怪兽解放让场上1张卡破坏。\r\n●守备表示：这张卡被破坏的场合，可以作为代替把这张卡以外的1只名字带有「变形斗士」的怪兽破坏。','\r',char(13)),'\n',char(10)),'场上1张卡破坏','是否用1只名字带有「变形斗士」的怪兽代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75779210,'艾普西龙大日',replace(replace('光属性调整＋调整以外的怪兽1只以上\r\n这张卡装备的装备卡不能成为魔法·陷阱·效果怪兽的效果的对象。1回合1次，可以把这张卡装备的1张装备卡送去墓地，选择对方场上存在的1只怪兽破坏。','\r',char(13)),'\n',char(10)),'对方场上的1只怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75782277,'鹰身女妖的狩猎场','「鹰身女郎」或者「鹰身女郎三姐妹」在场上召唤·特殊召唤时，场上存在的1张魔法·陷阱卡破坏。场上表侧表示存在的鸟兽族怪兽攻击力·守备力上升200。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75797046,'光子亚历山大蝶后',replace(replace('名字带有「幻蝶刺客」的4星怪兽×2\r\n把这张卡1个超量素材取除才能发动。场上的怪兽全部回到持有者手卡。那之后，这个效果让卡加入手卡的玩家受到那个数量×300的数值的伤害。','\r',char(13)),'\n',char(10)),'返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75830094,'荷鲁斯之黑炎龙 LV4','只要这张卡在自己场上表侧表示存在，控制权不会被变更。这张卡战斗破坏怪兽的回合的结束阶段时，可以把这张卡送去墓地，从手卡·卡组特殊召唤1只「荷鲁斯之黑炎龙 LV6」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75833426,'魔偶甜点茶休','自己墓地没有怪兽存在的场合才能发动。魔法·陷阱卡的发动无效，那张卡回到持有者手卡。自己场上有「魔偶甜点·布丁公主」存在的场合，可以再选对方场上1张卡破坏。','是否要选择对方场上一张卡破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75840616,'武神帝-须佐之男',replace(replace('名字带有「武神」的4星怪兽×2\r\n这张卡可以向对方场上的全部怪兽各作1次攻击。1回合1次，把这张卡1个超量素材取除才能发动。从卡组选1只名字带有「武神」的怪兽加入手卡或送去墓地。「武神帝-须佐之男」在自己场上只能有1只表侧表示存在。','\r',char(13)),'\n',char(10)),'检索','是否要将怪兽加入手卡？','怪兽送去墓地','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75850803,'月之魔法师','住在月亮上的魔法师。用月之魔力迷惑对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75878039,'星因士 天津四',replace(replace('「星因士 天津四」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合才能发动。从卡组把「星因士 天津四」以外的1只「星守」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75884822,'扰乱睡衣派对',replace(replace('这个卡名的①③的效果1回合各能使用1次。\r\n①：自己·对方的主要阶段才能发动。从卡组把1张「扰乱」卡加入手卡，那之后选1张手卡丢弃。\r\n②：自己场上的「武装龙」怪兽或者机械族·光属性的融合怪兽被战斗·效果破坏的场合，可以作为代替把自己的手卡·场上·墓地1张「扰乱」卡除外。\r\n③：这张卡被送去墓地的场合才能发动。除外的自己的「扰乱」怪兽尽可能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75886890,'大日球体','这张卡从怪兽卡区域上送去墓地的场合，可以支付500基本分，把这张卡当作装备卡使用给自己场上表侧表示存在的1只怪兽装备。此外，可以把当作装备卡使用而装备中的这张卡送去墓地，从自己墓地选择1张装备魔法卡给这张卡的装备怪兽装备。','当成装备卡装备','选择一张装备魔法卡装备','请选择一张装备魔法卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75889523,'恶魔海狸','拥有恶魔角与翅膀的海狸。投掷橡实果进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75902998,'穿刺的落穴','①：这个回合召唤·特殊召唤的对方怪兽的攻击宣言时才能发动。那只攻击怪兽破坏，给与对方那只怪兽的原本攻击力一半数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75906310,'武装龙强击炮',replace(replace('「VWXYZ-神龙强击炮」＋「武装龙 LV7」\r\n自己对上记的卡全部的特殊召唤成功的决斗中，把自己的场上·墓地的上记的卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。\r\n①：只要这张卡在怪兽区域存在，对方不能把和除外的自己·对方的卡同名卡的效果发动。\r\n②：对方回合1次，从卡组·额外卡组把1张卡除外才能发动。对方的场上·墓地的卡全部除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75917088,'魔法之国的王女-绒儿','这张卡不能通常召唤。这张卡只能通过「王女的试炼」的效果才能特殊召唤。自己准备阶段时，回复自己场上存在的怪兽数量×800的数值的基本分。','LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75923050,'稀有金属女武神',replace(replace('「稀有金属女郎」＋「稀有金属战士」\r\n这张卡在给对方直接攻击的伤害计算时攻击力上升1000，上场上的这张卡可以和融合区的「稀有金属骑士」互换（这张卡特殊召唤的回合不可以互换。）','\r',char(13)),'\n',char(10)),'攻击上升','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75937826,'巨大战舰 巨核Mk-2','这张卡特殊召唤成功时，给这张卡放置3个指示物。这张卡不会被战斗破坏。这张卡进行战斗的场合，伤害步骤结束时把这张卡放置的1个指示物取除。这张卡没有指示物的状态进行战斗的场合，伤害步骤结束时这张卡破坏。自己场上没有怪兽存在的场合，这张卡可以不用解放作召唤。','放置指示物','取除指示物','破坏','不解放作召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(75944053,'文具电子人003',replace(replace('①：这张卡召唤成功时才能发动。从卡组把「文具电子人003」以外的1只「文具电子人」怪兽特殊召唤。\r\n②：1回合1次，以自己场上1只「文具电子人」怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时上升自己场上的「文具电子人」卡数量×500。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75946257,'混沌咒术师','反转：从自己或对方的墓地里选择1张怪兽卡，将被选择的卡从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75953262,'战士 戴·格雷法','隐藏着操纵龙族才能的战士。其过去被迷所包围着。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75967082,'漏电','自己场上名字带有「电池人」的怪兽有3只以上表侧表示存在的场合才能发动。对方场上存在的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75987257,'隶属鳞粉','对方怪兽的攻击宣言时才能发动。攻击怪兽的表示形式变成守备表示，那1只怪兽把这张卡装备。此外，1回合1次，主要阶段以及战斗阶段时才能发动。装备怪兽的表示形式变更。','变更表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75988594,'超重剑圣 武藏-C',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡在规则上也当作「超重武者」卡使用。\r\n①：这张卡同调召唤成功时，以自己墓地1只机械族怪兽为对象才能发动。那只怪兽加入手卡。自己墓地有魔法·陷阱卡存在的场合，这个回合自己不能把那只怪兽以及那些同名怪兽召唤·特殊召唤。\r\n②：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(75991898,'熔岩谷龙',replace(replace('调整＋调整以外的炎属性怪兽1只以上\r\n让自己墓地存在的2只名字带有「熔岩」的怪兽回到卡组，选择对方场上存在的1张卡发动。选择的卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76004142,'不运的爆弹',replace(replace('「不运的爆弹」在1回合只能发动1张。\r\n①：以对方场上1只表侧表示怪兽为对象才能发动。自己受到那只表侧表示怪兽的攻击力一半数值的伤害。那之后，给与对方为和自己受到的伤害相同数值的伤害。\r\n②：场上的这张卡被对方破坏送去墓地的场合发动。给与对方1000伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76029419,'DDD 超视王 零·麦克斯韦',replace(replace('←4 【灵摆】 4→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽的守备力直到回合结束时变成0。\r\n【怪兽效果】\r\n①：这张卡向对方的守备表示怪兽攻击的伤害计算前才能发动。那只对方怪兽的守备力直到伤害步骤结束时变成0。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n③：这张卡的战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76039636,'恶魔铲土虫','这张卡不能通常召唤。把自己墓地2只昆虫族怪兽从游戏中除外的场合才能特殊召唤。这张卡给与对方基本分战斗伤害时，从对方卡组最上面把1张卡送去墓地。','对方卡组最上面把1张卡送去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76052811,'地狱诗人',replace(replace('这张卡不能作从墓地的特殊召唤。\r\n①：被战斗破坏的这张卡在墓地存在的场合，对方战斗阶段结束时发动。这张卡在墓地存在的场合，对方手卡随机选1张丢弃。','\r',char(13)),'\n',char(10)),'丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76066541,'小龙探物摆',replace(replace('「小龙探物摆」的效果1回合只能使用1次。\r\n①：场上的这张卡被战斗或者对方的效果破坏送去墓地时才能发动。从卡组把1只地属性灵摆怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽在结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76067258,'No.66 霸键甲虫',replace(replace('暗属性4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择这张卡以外的自己场上1张卡才能发动。只要这张卡在场上表侧表示存在，选择的卡不会被卡的效果破坏。场上的这张卡被破坏的场合，可以作为代替把选择的1张自己的卡送去墓地。','\r',char(13)),'\n',char(10)),'破坏耐性','是否要使用「No.66 霸键甲虫」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76075810,'投石部队','自己的场上的1只战士族怪兽做祭品。破坏1只表侧表示的守备力比这张卡的攻击力低的怪兽。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76080032,'异热同心武器-一角兽皇枪','这张卡可以从手卡当作装备卡使用给自己场上的「混沌No.39 希望皇 霍普雷」装备。用这个效果把这张卡装备的怪兽的攻击力上升1900。装备怪兽和对方怪兽进行战斗的场合，只在战斗阶段内那只对方怪兽的效果无效化。「异热同心武器-一角兽皇枪」在自己场上只能有1张表侧表示存在。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76103404,'电子化小天使',replace(replace('「电子化小天使」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合才能发动。从卡组把1只「电子化天使」怪兽或者1张「机械天使的仪式」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76103675,'火星','给与对方基本分200分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76136345,'回转调车',replace(replace('「回转调车」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上有机械族·地属性·10星怪兽召唤·特殊召唤的场合才能发动。从卡组把1只攻击力1800以上的机械族·地属性·4星怪兽特殊召唤。这个效果特殊召唤的怪兽的等级变成10星。这个效果的发动后，直到回合结束时对方受到的战斗伤害变成0。\r\n②：把自己1张手卡送去墓地才能发动。从卡组把1只机械族·地属性·10星怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76137276,'魔法捕获','自己把魔法卡发动时，丢弃1张手卡连锁发动。连锁发动的魔法卡被送去墓地时，那张卡回到手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76137614,'神圣解咒师','1回合1次，可以把场上存在的1个魔力指示物取除，场上表侧表示存在的1张魔法卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76184692,'独眼巨人','只长有一只眼睛的巨人。利用巨腕殴打敌人，值得小心。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76202610,'暗黑怪','这张卡召唤成功时才能发动。自己场上表侧表示存在的怪兽直到结束阶段时等级上升1星并变成暗属性。','等级属性变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76203291,'光鳞飞鱼','把自己场上存在的这张卡以外的1只鱼族怪兽解放发动。场上1张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76211194,'暗黑拿破仑','恶人创造的眼珠恶魔。用黑暗炸弹进行爆破攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76214441,'生命协调体','对方把持有「给与基本分伤害的效果」的卡发动时，可以从手卡把这张卡丢弃让那个发动无效并破坏。','效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76218313,'破坏剑-龙破坏之剑',replace(replace('「破坏剑-龙破坏之剑」的③的效果1回合只能使用1次。\r\n①：自己主要阶段以自己场上1只「巴斯达·布雷达」为对象才能发动。从自己的手卡·场上把这只怪兽当作装备卡使用给那只自己怪兽装备。\r\n②：这张卡装备中的场合，对方不能从额外卡组把怪兽特殊召唤。\r\n③：这张卡装备中的场合才能发动。装备的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76218643,'黑面波波','自己场上有调整表侧表示存在的场合，这张卡不会被战斗破坏。自己场上表侧表示存在的调整从场上离开时，自己回复800基本分。','LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76224717,'上级进化区',replace(replace('1回合1次，自己对怪兽的上级召唤成功的回合的结束阶段时才能发动。这个回合自己为上级召唤而解放的怪兽数量的以下效果适用。\r\n●1只以上：选对方场上盖放的1张卡破坏。\r\n●2只以上：从卡组抽1张卡。\r\n●3只以上：选自己墓地1只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'破坏对方场上1张盖卡','抽1张卡','把1只自己墓地的怪兽加入自己手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76232340,'千年原人','任何时候都以力量坚持到底，拥有千年宝物的原始人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76232522,'恢复巫师',replace(replace('电子界族怪兽2只\r\n这个卡名的效果1回合只能使用1次。\r\n①：以这个回合被破坏的自己墓地1只电子界族连接怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，结束阶段破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76263644,'龙骑士 D-终',replace(replace('「命运英雄 血魔-D」＋「命运英雄 教义人」\r\n这张卡的融合召唤不用上记的卡不能进行。\r\n①：1回合1次，以对方场上1只怪兽为对象才能发动。那只对方怪兽破坏，把表侧表示怪兽破坏的场合，给与对方那个攻击力数值的伤害。这个效果发动的回合，自己不能进行战斗阶段。\r\n②：这张卡在墓地存在的场合，自己准备阶段把自己墓地1张「命运英雄」卡除外才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76297408,'魂粉碎','支付500分，互相选择对方墓地的1张怪兽卡。选择的卡从游戏中除外。这个效果在自己的场上有恶魔族存在的场合才能发动。','除外','是否现在使用「魂粉碎」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76305638,'岩之精灵 泰坦','这张卡不能通常召唤。自己墓地1只地属性怪兽从游戏中除外特殊召唤上场。这只怪兽在对方的战斗阶段攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76321376,'地雷石人','这张卡被战斗破坏送去墓地时，给与对方基本分500分伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76348260,'极星天 密弥尔','自己场上有名字带有「极星」的怪兽表侧表示存在的场合，自己的准备阶段开始时只有1次，从手卡把1张魔法卡送去墓地才能发动。墓地存在的这张卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76359406,'水晶机巧-矩阵紫黄晶',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡同调召唤成功的场合才能发动。对方场上的特殊召唤的表侧表示怪兽全部变成守备表示。\r\n②：同调召唤的这张卡被战斗·效果破坏的场合，以同调怪兽以外的自己墓地1只「水晶机巧」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76372778,'邪遗式人鱼风灵',replace(replace('4星怪兽×2\r\n这张卡战斗破坏对方怪兽的场合，那次伤害计算后可以把这张卡1个超量素材取除，破坏的那只怪兽不送去墓地回到持有者卡组。','\r',char(13)),'\n',char(10)),'战斗破坏的怪兽返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76384284,'特洛伊剑斗兽','从自己手卡把1只名字带有「剑斗兽」的怪兽在对方场上特殊召唤。那之后，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76403456,'力量之壁','①：对方怪兽的攻击要让自己受到战斗伤害的伤害计算时才能发动。直到那次战斗发生的对自己的战斗伤害变成0为止把每500伤害1张卡从自己卡组上面送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76407432,'爆裂反击','自己场上有名字带有「/爆裂体」的怪兽表侧表示存在的场合才能发动。魔法·陷阱·效果怪兽的效果的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76419637,'混沌超量 激烈华战舰 手弱女',replace(replace('4星怪兽×4\r\n对方的结束阶段时只有1次在对方手卡比自己手卡多的场合发动。对方选1张手卡丢弃。此外，这张卡有「烈华炮舰 抚子」在作为超量素材的场合，得到以下效果。\r\n●把这张卡1个超量素材取除才能发动。给与对方基本分场上的卡数量×300的数值的伤害。「混沌超量 激烈华战舰 手弱女」的这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'丢弃手牌','伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76436988,'闪闪枪兵','对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。只要这张卡在场上表侧表示存在，自己在每次结束阶段受到500分伤害。','500伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76442347,'子型企鹅','反转过的这张卡被送去墓地时，从自己墓地选择「子型企鹅」以外的1只名字带有「企鹅」的怪兽表侧攻击表示或者里侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76442616,'英雄联合','场上有名字带有「英雄」的怪兽存在时才能发动。从手卡特殊召唤2只4星以下的名字带有「元素英雄」的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76446915,'圆盘魔术师','将自己封印在铁盘里，攻击时才露出本体。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76459806,'火山火箭弹','当这张卡在召唤、反转召唤、特殊召唤时，可以将1张名字含有「烈焰加农炮」的卡从卡组或者墓地加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76471944,'超重忍者 猿飞-B',replace(replace('机械族调整＋调整以外的「超重武者」怪兽1只以上\r\n这张卡在规则上也当作「超重武者」卡使用。\r\n①：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。\r\n②：1回合1次，自己墓地没有魔法·陷阱卡存在的场合，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏，给与对方500伤害。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76473843,'威风阵·飞马',replace(replace('「威风阵·飞马」的②的效果1回合只能使用1次。\r\n①：场上的「威风妖怪」怪兽的攻击力·守备力上升300。\r\n②：把自己场上1只魔法师族·风属性怪兽解放才能发动。从卡组把1只4星以下的「威风妖怪」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76512652,'鳄鱼人','有智慧而且狂暴化的鳄鱼。以坚硬的鳞片反弹对方的攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76515293,'心镇壶','选择场上盖伏的2张魔法·陷阱卡才能发动。只要这张卡在场上存在，选择的魔法·陷阱卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76520646,'无限暗魔',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡的攻击宣言时，可以把对方场上存在的1只表侧表示怪兽的表示形式改变。','\r',char(13)),'\n',char(10)),'改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76532077,'无底的流沙','对方的回合结束时，全场的最高攻击力的1只表侧表示的怪兽破坏。自己的准备阶段时，自己的手卡4张以下的场合，这张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76539047,'猛毒之牙','兽族怪兽每次给与对方战斗伤害时，给与对方基本分500分的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76543119,'机关傀儡-死之木马','自己的主要阶段时，选择场上1只名字带有「机关傀儡」的怪兽才能发动。选择的怪兽破坏。这个效果只在这张卡在场上表侧表示存在能使用1次。此外，这张卡从场上送去墓地时，可以从手卡把最多2只名字带有「机关傀儡」的怪兽特殊召唤。','破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76547525,'红莲翼龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：只在同调召唤的这张卡在场上表侧表示存在才有1次，比这张卡攻击力高的怪兽在场上存在的场合才能发动。场上1只攻击力最高的怪兽破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76573247,'外星人侵略者','这张卡1回合只有1次，可以移动到没有使用的相邻的怪兽卡区域。这张卡的正对面没有对方的怪兽·魔法·陷阱卡存在的场合，这张卡可以直接攻击对方玩家。','移动位置','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76589815,'燃烧拳击手 犯规拳击委员',replace(replace('3星怪兽×2\r\n只要这张卡在场上表侧表示存在，可以攻击的对方怪兽必须作出攻击。此外，自己场上有这张卡以外的名字带有「燃烧拳击手」的怪兽存在的场合，对方不能把这张卡作为攻击对象。这张卡以外的自己的名字带有「燃烧拳击手」的怪兽进行战斗的攻击宣言时，把这张卡2个超量素材取除才能发动。把对方手卡确认，从那之中选1张魔法卡在自己场上盖放。','\r',char(13)),'\n',char(10)),'确认手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76614003,'暗黑修理师','这张卡从自己场上送去墓地时，把自己卡组最上面的卡确认再回到卡组最上面或者最下面。','确认卡组','回到卡组最上面','回到卡组最下面','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76614340,'宝石骑士·红玉',replace(replace('「宝石骑士·红榴」＋「宝石骑士」怪兽\r\n这张卡用以上记卡为融合素材的融合召唤才能从额外卡组特殊召唤。\r\n①：1回合1次，把这张卡以外的自己场上1只表侧表示的「宝石」怪兽解放才能发动。这张卡的攻击力直到回合结束时上升解放的怪兽的攻击力数值。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76634149,'海龙神','被称为海洋之主的海龙。掀起海啸吞噬一切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76641981,'TG1－EM1','选择对方场上存在的1只怪兽和自己场上表侧表示存在的1只名字带有「科技属」的怪兽发动。选择的怪兽的控制权交换。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76647978,'超越融合',replace(replace('不能对应这张卡的发动让卡的效果发动。\r\n①：支付2000基本分才能发动。融合怪兽卡决定的2只融合素材怪兽从自己场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：把墓地的这张卡除外，以这张卡的效果融合召唤的1只怪兽为对象才能发动。那只怪兽的融合召唤使用过的一组融合素材怪兽从自己墓地特殊召唤。这个效果特殊召唤的怪兽的攻击力·守备力变成0，效果无效化。','\r',char(13)),'\n',char(10)),'融合召唤','融合素材特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76650663,'正义盟军 遮光吸收兵','和这张卡进行战斗的光属性怪兽的效果无效化。','效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76660409,'灵摆回归','①：自己的灵摆区域有2张卡存在的场合，以持有用那些灵摆刻度可以灵摆召唤的等级的自己墓地2只怪兽为对象才能发动。那些怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76683171,'阿格里异虫','把这张卡解放对名字带有「异虫」的爬虫族怪兽的上级召唤成功时，自己墓地存在的这张卡可以在对方场上表侧攻击表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76685519,'幻变骚灵·击键录杜尔迦',replace(replace('「幻变骚灵」怪兽2只\r\n①：这张卡以外的自己的「幻变骚灵」怪兽给与对方战斗伤害时，以对方墓地1只怪兽为对象才能发动。那只怪兽在作为这张卡所连接区的自己场上特殊召唤。这个效果特殊召唤的怪兽不在这张卡攻击宣言过的回合不能攻击。\r\n②：这张卡被战斗破坏的场合，以自己墓地1张「幻变骚灵」卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76704943,'八俣龙绘卷','画卷龙实体他的产物。守备力相当低。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76714458,'痛魂之咒术','①：给与自己伤害的效果由对方发动时才能发动。那个效果发生的对自己的效果伤害由对方代受。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76721030,'暴君的暴言','把自己场上存在的2只怪兽解放发动。只要这张卡在场上存在，双方不能把手卡以及场上发动的效果怪兽的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76728962,'亡命左轮手枪龙',replace(replace('①：自己场上的机械族·暗属性怪兽被战斗·效果破坏的场合才能发动。这张卡从手卡特殊召唤。\r\n②：1回合1次，自己·对方的战斗阶段才能发动。进行3次投掷硬币。选最多有表出现数量的场上的表侧表示怪兽破坏。3次都是表的场合，再让自己从卡组抽1张。这个效果发动的回合，这张卡不能攻击。\r\n③：这张卡被送去墓地的场合才能发动。把持有进行投掷硬币效果的1只7星以下的怪兽从卡组加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76751255,'点火骑士上膛',replace(replace('「点火骑士上膛」在1回合只能发动1张。\r\n①：把手卡的灵摆怪兽任意数量给对方观看，回到卡组洗切。那之后，自己从卡组抽出回到卡组的数量＋1张。这张卡的发动后，直到回合结束时自己不能用卡的效果抽卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76754619,'金字塔能量',replace(replace('选择下面的1个效果发动。\r\n●自己的场上全部表侧表示存在的怪兽直到回合结束前攻击力上升200。\r\n●自己的场上全部表侧表示存在的怪兽直到回合结束前守备力上升500。','\r',char(13)),'\n',char(10)),'自己的场上全部表侧表示存在的怪兽直到回合结束前攻击力上升200','自己的场上全部表侧表示存在的怪兽直到回合结束前守备力上升500','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76763417,'电子化体操员','①：1回合1次，丢弃1张手卡，以对方场上1只表侧攻击表示怪兽为对象才能发动。那只对方的表侧攻击表示怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76766706,'混沌之种','自己场上有光属性以及暗属性怪兽存在的场合，选择除外的1只自己的光属性或者暗属性的战士族怪兽才能发动。选择的怪兽加入手卡。「混沌之种」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76774528,'废铁龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n1回合1次，选择自己以及对方场上存在的卡各1张才能发动。选择的卡破坏。这张卡被对方破坏送去墓地时，选择同调怪兽以外的自己墓地存在的1只名字带有「废铁」的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76775123,'巡逻机器人','只要这张卡在场上表侧表示存在，自己的准备阶段可以把对方场上盖放的1张卡确认。','确认盖卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76792184,'混沌-黑魔术的仪式','召唤「黑混沌之魔术师」必须。场上和手卡加起来总共8颗星以上的怪兽作祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76794549,'宙读之魔术士',replace(replace('←1 【灵摆】 1→\r\n「宙读之魔术士」的灵摆效果1回合只能使用1次。\r\n①：自己主要阶段才能发动。这张卡破坏，从手卡·卡组选1只「星读之魔术师」在自己的灵摆区域放置或特殊召唤。\r\n【怪兽效果】\r\n①：自己场上的卡被战斗·效果破坏的场合才能发动。这张卡从手卡特殊召唤。那之后，可以选这个回合被破坏的1只怪兽，那1只同名怪兽从卡组加入手卡。\r\n②：把场上的这张卡除外，从自己的手卡·场上·墓地把「灵摆龙」「超量龙」「同调龙」「融合龙」怪兽各1只除外才能发动。把1只「霸王龙 扎克」当作融合召唤从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'这张卡破坏','灵摆区域放置','特殊召唤','这张卡从手卡特殊召唤','是否把破坏怪兽的同名怪兽加入手卡？','融合召唤「霸王龙 扎克」','请选择1只「星读之魔术师」','请选择这个回合被破坏的1只怪兽','','','','','','','','');
INSERT INTO texts VALUES(76798740,'复仇死者突击','从手卡以及自己场上的表侧表示怪兽之中把1只不死族怪兽送去墓地；从卡组把1只「复仇死者」怪兽特殊召唤。「复仇死者突击」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76806714,'龟的誓言','「蟹龟」的降临必需。若不从场地和手卡把等级直到8以上的卡作为祭品，则「蟹龟」不能降临。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76812113,'鹰身女郎','人长出羽毛的魔兽，表演华丽的舞蹈进行快攻。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76815942,'抒情歌鸲-独立夜莺',replace(replace('「抒情歌鸲-聚集夜莺」＋「抒情歌鸲」怪兽\r\n①：原本卡名包含「抒情歌鸲」的超量怪兽作为素材让这张卡融合召唤成功的场合才能发动。这张卡的等级上升那些怪兽持有的超量素材数量的数值。\r\n②：这张卡的攻击力上升这张卡的等级×500，这张卡不受其他卡的效果影响。\r\n③：1回合1次，自己主要阶段才能发动。给与对方这张卡的等级×500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76848240,'绝对不可侵领域','在自己的准备阶段才能发动。丢弃1张手卡。在下一个对方的回合中，对方不能进行通常召唤和特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76862289,'八俣大蛇','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到主人的手卡。这张卡给与对方玩家战斗伤害时，从卡组抽卡直到手卡5张为止。','返回手牌','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76865611,'变形斗士·照明灯',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：只要这张卡在场上表侧表示存在，对方的卡的效果发生的对自己的效果伤害由对方代受。\r\n●守备表示：这张卡被战斗破坏送去墓地时，这个回合战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'战斗伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76871889,'复仇死者之夜','可以丢弃1张手卡；从卡组把1只「复仇死者」怪兽加入手卡。「复仇死者之夜」的这个效果1回合只能使用1次。自己的「复仇死者」怪兽战斗破坏对方怪兽时：可以从自己墓地把1只「复仇死者」怪兽除外；那只怪兽向对方怪兽可以继续攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76891401,'神海龙 基希尔诺顿',replace(replace('调整＋调整以外的3星怪兽1只\r\n场上表侧表示存在的3星以下的怪兽被送去墓地时，这张卡的攻击力直到这个回合的结束阶段时变成3000。','\r',char(13)),'\n',char(10)),'攻击力变成3000','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76895648,'危险机器 6型',replace(replace('每次自己的准备阶段丢1个骰子，根据丢出的数目决定效果。\r\n●投掷1的场合：自己丢弃1张手卡。\r\n●投掷2的场合：对方丢弃1张手卡。\r\n●投掷3的场合：自己抽1张卡。\r\n●投掷4的场合：对方抽1张卡。\r\n●投掷5的场合：对方的场上1只怪兽破坏。\r\n●投掷6的场合：这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76902476,'幻兽机 追踪海龟','这张卡的等级上升自己场上的「幻兽机衍生物」的等级的合计数值。只要自己场上有衍生物存在，这张卡不会被战斗以及效果破坏。此外，只要这张卡在场上表侧表示存在，1回合1次，自己场上的「幻兽机衍生物」不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76908448,'宝石骑士·白晶','通过优化水晶的力量，使之变为战斗力进行战斗的上级宝石骑士。利用高攻击力压倒性的打击敌人。但由于时常感到自己的优化存在瓶颈，因此非常珍惜与同伴们团结的友谊之情。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76909279,'激昂的牛头人','①：只要这张卡在怪兽区域存在，自己的兽族·兽战士族·鸟兽族怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76913983,'黑羽-兵翼鸦',replace(replace('「黑羽」调整＋调整以外的怪兽1只以上\r\n①：这张卡向守备表示怪兽攻击的伤害步骤内，这张卡的攻击力上升500。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76922029,'首领 扎鲁格',replace(replace('这张卡给与对方玩家战斗伤害时，可以选择下面1个效果发动：\r\n●对方随机丢弃1张手卡。\r\n●对方的卡组最上面的2张卡送去墓地。','\r',char(13)),'\n',char(10)),'选择一个效果发动','对方随机丢弃1张手牌。','对方的卡组最上面的2张卡送去墓地。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76925842,'暗黑骑士 珀耳修斯','这张卡战斗破坏对方怪兽的场合，可以把自己墓地存在的1只暗属性怪兽从游戏中除外，从自己卡组抽1张卡。这张卡的攻击力上升自己墓地存在的暗属性怪兽数量×100的数值。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76930964,'黄昏之忍者将军-月牙',replace(replace('这张卡可以把1只「忍者」怪兽解放作上级召唤。「黄昏之忍者将军-月牙」的效果1回合只能使用1次。\r\n①：这张卡在场上攻击表示存在的场合，以「黄昏之忍者将军-月牙」以外的自己墓地2只「忍者」怪兽为对象才能发动。这张卡变成守备表示，作为对象的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'把1只「忍者」怪兽解放作上级召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76972801,'我我我防御','自己场上有名字带有「我我我」的怪兽2只以上存在的场合才能发动。这个回合，自己场上的怪兽不会被战斗以及卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76986005,'电子麒麟','把这张卡做祭品。直到这个回合的结束阶段，这张卡的控制者受到的因效果造成的伤害变成0。','效果伤害免疫','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(76990617,'幻奏的音女 索娜塔',replace(replace('①：自己场上有「幻奏」怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：只要特殊召唤的这张卡在怪兽区域存在，自己场上的天使族怪兽的攻击力·守备力上升500。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77007920,'镭射炮机甲铠','只有昆虫族怪兽可以装备。装备怪兽的攻击力·守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77013169,'超重武者 刀狩-999',replace(replace('①：这张卡召唤·特殊召唤成功时才能发动。这张卡的表示形式变更。\r\n②：自己场上的「超重武者」怪兽和对方怪兽进行战斗的伤害计算后发动。那只对方怪兽的攻击力·守备力变成0。','\r',char(13)),'\n',char(10)),'改变表示形式','攻守变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77027445,'波塞冬之力','水族才能装备。1只装备怪兽的攻击力和守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77036039,'爆裂佣兵','可以让自己的手卡或者墓地存在的1张「爆裂模式」回到卡组，对方场上存在的1张魔法·陷阱卡破坏。这个效果1回合只能使用1次。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77044671,'金字塔龟','这张卡被战斗破坏送去墓地时，可以从自己卡组把1只守备力2000以下的不死族怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77058170,'降频承办员',replace(replace('效果怪兽2只\r\n①：1回合1次，以这张卡所连接区1只表侧表示怪兽和对方场上1只表侧表示怪兽为对象才能发动。那只对方怪兽的攻击力直到回合结束时下降作为对象的所连接区的怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77060848,'极星灵 暗精灵','这张卡召唤成功时，可以选择自己墓地存在的1只名字带有「极星」的怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77084837,'因幡之白兔','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到主人的手卡。对方场上存在怪兽也只能直接攻击对方玩家。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77087109,'时钟共鸣者','只要这张卡在场上表侧守备表示存在，这张卡1回合只有1次不会被战斗或者卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77098449,'银河波动','每次自己超量召唤成功，给与对方基本分500分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77116346,'雾动机龙充能',replace(replace('「雾动机龙充能」在1回合只能发动1张。\r\n①：作为这张卡的发动时的效果处理，从卡组把1只「雾动机龙」怪兽加入手卡。\r\n②：1回合1次，「雾动机龙」卡从场上表侧表示加入自己的额外卡组的场合发动。那1张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77121851,'暗黑之蝎尾狮','在这张卡被送去墓地回合的结束阶段，这张卡的效果才能发动。从自己的手卡或场上将1张兽族·兽战士族·鸟兽族的怪兽卡送去墓地，将墓地里存在的这张卡特殊召唤上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77133792,'磁力变换',replace(replace('①：以自己墓地最多3只4星以下的「磁石战士」怪兽为对象才能发动。那些怪兽加入手卡。\r\n②：把墓地的这张卡除外，以除外的1只自己的4星以下的「磁石战士」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77135531,'龙之尖兵','可以从手卡把1只龙族怪兽送去墓地，这张卡的攻击力上升300。自己场上存在的这张卡被对方的卡的效果送去墓地时，可以把自己或者对方的墓地存在的1只龙族的通常怪兽特殊召唤。','攻击上升','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77150143,'十二兽 马剑',replace(replace('①：这张卡召唤·特殊召唤成功的场合才能发动。从手卡丢弃1张「十二兽」卡，自己从卡组抽1张。\r\n②：持有这张卡作为素材中的原本种族是兽战士族的超量怪兽得到以下效果。\r\n●这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'从手卡丢弃1张「十二兽」卡，自己从卡组抽1张','穿刺伤害（十二兽 马剑）','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77152542,'黑羽-沙尘之哈麦丹',replace(replace('「黑羽-沙尘之哈麦丹」的①的方法的特殊召唤1回合只能有1次。\r\n①：自己场上有「黑羽-沙尘之哈麦丹」以外的「黑羽」怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡召唤·特殊召唤成功时，以这张卡以外的自己场上1只「黑羽」怪兽为对象才能发动。这张卡的等级上升那只怪兽的等级数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77153811,'仪式魔人 诅咒魔法师','仪式怪兽的仪式召唤进行的场合，可以作为那次仪式召唤需要的等级数值的1只怪兽，把墓地存在的这张卡从游戏中除外。把这张卡在仪式召唤使用的仪式怪兽只要在场上表侧表示存在，同调怪兽的效果无效化。','「仪式魔人 诅咒魔法师」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77205367,'混沌No.96 黑飙',replace(replace('暗属性3星怪兽×4\r\n这张卡不会被战斗破坏，这张卡的战斗发生的对自己的战斗伤害让对方也承受。此外，这张卡有「No.96 黑雾」在作为超量素材的场合，得到以下效果。\r\n●这张卡和对方怪兽进行战斗的攻击宣言时1次，把这张卡1个超量素材取除才能发动。那只对方怪兽的攻击力变成0，这张卡的攻击力上升那只对方怪兽的原本攻击力数值。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77207191,'巴风特','①：这张卡召唤·反转召唤成功时才能发动。从卡组把1只「幻兽王 加泽尔」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77229910,'变形斗士·防护罩','把要让自己场上表侧表示存在的名字带有「变形斗士」的怪兽破坏的魔法·陷阱卡的发动无效并破坏。再从自己卡组把1张名字带有「变形斗士」的卡加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77235086,'电子化天使-弁天-',replace(replace('「机械天使的仪式」降临。\r\n①：这张卡战斗破坏怪兽送去墓地的场合发动。给与对方那只怪兽的原本守备力数值的伤害。\r\n②：这张卡被解放的场合才能发动。从卡组把1只天使族·光属性怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77252217,'电动刃虫','这张卡进行战斗的场合，伤害步骤结束时对方玩家抽1张卡。','对方抽1张卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77307161,'淘气仙星·布露姆',replace(replace('2星以下的「淘气仙星」怪兽1只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功的场合才能发动。对方从卡组抽1张。\r\n②：这张卡所连接区的「淘气仙星」怪兽被战斗·效果破坏的场合才能发动。给与对方为对方手卡数量×200伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77330185,'XX-剑士 加尔塞姆','场上存在的这张卡被卡的效果破坏送去墓地时，从自己卡组把1只名字带有「X-剑士」的怪兽加入手卡。这张卡的攻击力上升自己场上表侧表示存在的名字带有「X-剑士」的怪兽数量×200的数值。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77334267,'发条装攻 发条王',replace(replace('5星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以场上盖放的2张卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77336644,'红莲魔龙/爆裂体','这张卡不能通常召唤。「爆裂模式」的效果才能特殊召唤。这张卡攻击的场合，伤害计算后把这张卡以外的怪兽全部破坏。此外，场上存在的这张卡被破坏时，可以把自己墓地存在的1只「红莲魔龙」特殊召唤。','破坏','特殊召唤','特殊召唤「红莲魔龙」','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77360173,'同调克隆共鸣者',replace(replace('「同调克隆共鸣者」的①的方法的特殊召唤1回合只能有1次。\r\n①：场上有同调怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡从场上送去墓地的场合，以「同调克隆共鸣者」以外的自己墓地1只「共鸣者」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','加入手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77363314,'水晶龙','①：1回合1次，这张卡进行过战斗的自己回合的战斗步骤才能发动。从卡组把1只龙族·8星怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77372241,'炎狱龙人','这张卡战斗破坏怪兽送去墓地时，给与对方基本分破坏怪兽的等级×200的数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77379481,'大盘振舞侍','这张卡给与对方战斗伤害时，对方玩家将手卡抽到7张为止。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77387463,'方界帝 神火之德拉耆尼',replace(replace('这张卡不能通常召唤。把自己场上2只「方界」怪兽送去墓地的场合可以特殊召唤。\r\n①：这个方法特殊召唤的这张卡的攻击力上升1600。\r\n②：这张卡从手卡的特殊召唤成功的场合发动。给与对方800伤害。\r\n③：这张卡战斗的伤害步骤结束时，以自己墓地最多3只「方界胤 毗贾姆」为对象才能发动。这张卡送去墓地，作为对象的怪兽特殊召唤。那之后，可以从卡组把1只「方界超帝 死雷之印陀罗」加入手卡。','\r',char(13)),'\n',char(10)),'是否把1只「方界超帝 死雷之印陀罗」加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77411244,'B-破坏龙兽',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只机械族·光属性怪兽为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：装备怪兽不受其他的对方的魔法卡的效果影响。\r\n③：这张卡从场上送去墓地的场合才能发动。从卡组把1只同盟怪兽加入手卡。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77414722,'魔法干扰阵','①：魔法卡发动时，丢弃1张手卡才能发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77428945,'土地锯','这张卡特殊召唤成功时，这张卡以外的场上的特殊召唤的怪兽全部变成里侧守备表示。「土地鋸」的效果1回合只能使用1次。','变成里侧守备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77454922,'要塞鲸的誓言','「要塞鲸」的降临必要。必须从场上或者手札，牺牲奉献等级合计为7个以上的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77456781,'恶魔乌贼','潜在海里的巨大章鱼。从海中突然出现并攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77462146,'幻影骑士团 影佑',replace(replace('①：以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力·守备力上升300。\r\n②：这张卡在墓地存在的场合，对方的直接攻击宣言时才能发动。这张卡变成通常怪兽（战士族·暗·4星·攻0/守300）在怪兽区域守备表示特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'当作通常怪兽特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77491079,'爆风蜥蜴','反转：选择对方场上1只怪兽弹回其持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77498348,'混沌战士 -宵暗的使者-',replace(replace('这张卡不能通常召唤。自己墓地的光属性和暗属性的怪兽数量相同的场合，把那之内的其中1个属性全部除外的场合才能特殊召唤。这张卡的属性也当作「光」使用。这张卡特殊召唤成功时，可以把为特殊召唤而除外的怪兽属性的以下效果发动。这个效果发动的回合，自己不能进行战斗阶段。\r\n●光：选择场上1只怪兽除外。\r\n●暗：对方手卡随机选1张直到对方的结束阶段时里侧表示除外。','\r',char(13)),'\n',char(10)),'除外光属性','除外暗属性','除外','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77505534,'堕影的蠢动','①：从卡组把1张「影依」卡送去墓地。那之后，可以选自己场上的里侧守备表示的「影依」怪兽任意数量变成表侧守备表示。','是否要选择里侧守备表示的「影依」怪兽变成表侧守备表示？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77506119,'雷电独角兽',replace(replace('兽族调整＋调整以外的怪兽1只以上\r\n1回合1次，自己的主要阶段时选择对方场上表侧表示存在的1只怪兽才能发动。选择的怪兽的攻击力直到结束阶段时下降自己场上存在的怪兽数量×500的数值。这个效果发动的回合，这张卡以外的怪兽不能攻击。','\r',char(13)),'\n',char(10)),'攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77511331,'空手魂 黑带',replace(replace('←9 【灵摆】 9→\r\n场上有怪兽灵摆召唤的场合：灵摆区域的这张卡回到持有者手卡。\r\n【怪兽效果】\r\n这张卡召唤成功时：可以把和自己的灵摆区域的卡相同纵列的对方场上的魔法/陷阱卡全部送去墓地。1回合1次，这张卡召唤·反转的回合的结束阶段时：这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77527210,'神圣之魂','这张卡不能通常召唤。自己墓地2只光属性怪兽从游戏中除外特殊召唤上场。只要这张卡在场上存在，对方的战斗阶段中对方的全部怪兽攻击力下降300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77538567,'魔宫的贿赂','①：对方把魔法·陷阱卡发动时才能发动。那个发动无效并破坏。对方从卡组抽1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77542832,'入魔鬼·血石','求所无别　结终　灭破　坏破　念邪的高崇　鬼魔入侵　魂之辈我　恶邪为又　粹纯为既','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77558536,'光道暗杀者 莱登',replace(replace('「光道暗杀者 莱登」的①的效果1回合只能使用1次。\r\n①：自己主要阶段才能发动。从自己卡组上面把2张卡送去墓地。这个效果送去墓地的卡之中有「光道」怪兽的场合，这张卡的攻击力直到对方回合结束时上升200。\r\n②：自己结束阶段发动。从自己卡组上面把2张卡送去墓地。','\r',char(13)),'\n',char(10)),'送墓','送墓','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77561728,'搅乱作战','对方将手卡加入卡组，再从卡组抽出与原本手卡数一样数目的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77565204,'未来融合',replace(replace('①：这张卡的发动后第1次的自己准备阶段发动。自己的额外卡组1只融合怪兽给双方确认，那只怪兽决定的融合素材怪兽从自己卡组送去墓地。\r\n②：这张卡的发动后第2次的自己准备阶段发动。把1只和这张卡的①的效果确认的怪兽同名的融合怪兽从额外卡组融合召唤。这张卡从场上离开时那只怪兽破坏。那只怪兽破坏时这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77568553,'酸液爬虫','巨大的青虫。吐出浓烈的酸液、可以溶解任何东西。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77581312,'假面道化','一边跳着死亡的舞蹈、一边用手上的镰刀切碎敌人的战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77584012,'拟似空间','①：1回合1次，从自己墓地把1张场地魔法卡除外才能发动。直到结束阶段，这张卡当作和除外的卡同名卡使用，得到相同效果。','获得场地魔法卡的效果','结束复制效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77585513,'人造人-念力震慑者','①：只要这张卡在怪兽区域存在，双方不能把场上的陷阱卡的效果发动，场上的陷阱卡的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77585514,'人造人-念力震慑者','①：只要这张卡在怪兽区域存在，双方不能把场上的陷阱卡的效果发动，场上的陷阱卡的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77600660,'念力帝皇','这张卡召唤·特殊召唤成功时，自己墓地存在的念动力族怪兽每有1只，自己回复500基本分。','LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77603950,'萨塔那','诅咒敌人。可以使人动弹不得的魔法师。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77608643,'命运英雄 顽强人','这张卡在自己场上表侧表示存在时，自己场上除这张卡以外的名字带有「命运英雄」的怪兽被战斗破坏送去墓地的场合，可以把那1只怪兽在下次自己的准备阶段时在自己场上特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77610772,'星杯神乐 夏娃',replace(replace('种族和属性不同的怪兽2只\r\n①：连接状态的这张卡不会被战斗·效果破坏，不会成为对方的效果的对象。\r\n②：这张卡所连接区的怪兽被效果破坏的场合，可以作为代替把这张卡送去墓地。\r\n③：这张卡从场上送去墓地的场合才能发动。从手卡把1只「星杯」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'是否使用「星杯神乐 夏娃」的效果代替破坏？','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77622396,'天邪鬼的诅咒','直到发动回合的结束阶段时，攻击力·守备力的上升·下降的效果变成相反。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77625948,'电子暗黑刃翼',replace(replace('①：这张卡召唤成功的场合，以自己墓地1只3星以下的龙族怪兽为对象发动。那只龙族怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡的攻击力上升这张卡的效果装备的怪兽的原本攻击力数值。\r\n③：这张卡可以直接攻击。那个场合，这张卡的攻击力只在伤害计算时变成一半。\r\n④：这张卡被战斗破坏的场合，作为代替把这张卡的效果装备的怪兽破坏。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77631175,'漫画英雄 亚瑟王',replace(replace('战士族4星怪兽×2\r\n这张卡被战斗破坏的场合，可以作为代替把这张卡1个超量素材取除。这个效果让超量素材被取除时，这张卡的攻击力上升500，给与对方基本分500分伤害。','\r',char(13)),'\n',char(10)),'是否使用「漫画英雄 亚瑟王」的效果代替破坏？','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77637979,'LAN喙嘴龙','怪兽2只','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77642288,'剑斗兽 追斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功的场合，这张卡进行战斗的战斗阶段结束时，从卡组把「剑斗兽 追斗」以外的2只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77679716,'超重武者装留 破坏铠',replace(replace('①：自己主要阶段以自己场上1只「超重武者」怪兽为对象才能发动。自己的手卡·场上的这只怪兽当作守备力下降1000的装备卡使用给那只自己怪兽装备。装备怪兽不会被战斗破坏。\r\n②：自己主要阶段，从自己墓地把包含这张卡的「超重武者装留 破坏铠」全部除外，以守备力比原本守备力低的1只「超重武者」怪兽为对象才能发动。给与对方那只怪兽的守备力和那个原本守备力的相差数值的伤害。','\r',char(13)),'\n',char(10)),'装备','效果伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77693536,'重炼装勇士·万溶炼金师',replace(replace('「炼装」怪兽＋通常怪兽\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：对方回合1次，以场上1只效果怪兽为对象才能发动。那只效果怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡的守备力上升这张卡的效果装备的怪兽的原本攻击力数值。\r\n③：这张卡有由「炼装」融合怪兽卡决定的融合素材怪兽装备的场合，可以把那装备卡作为那只融合怪兽的融合素材使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77700347,'死灵防御者','自己的主要阶段时，把墓地存在的这张卡从游戏中除外，选择自己场上存在的1只怪兽才能发动。直到下次的对方的结束阶段时，选择的怪兽不会被战斗破坏，选择的怪兽的战斗发生的对自己的战斗伤害变成0。','战斗耐性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77723643,'影依龙',replace(replace('「影依龙」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡反转的场合，以对方场上1张卡为对象才能发动。那张卡回到持有者手卡。\r\n②：这张卡被效果送去墓地的场合，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'回到手牌','魔陷破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77754944,'万能地雷 阔剑式','①：对方怪兽的攻击宣言时才能发动。对方场上的表侧攻击表示怪兽之内攻击力最高的1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77754945,'万能地雷 阔剑式','①：对方怪兽的攻击宣言时才能发动。对方场上的表侧攻击表示怪兽之内攻击力最高的1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77778835,'歇斯底里的舞会','①：丢弃1张手卡才能把这张卡发动。从自己墓地把「鹰身女郎」尽可能特殊召唤。这张卡从场上离开时这个效果特殊召唤的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77783947,'龙星的极致',replace(replace('①：只要这张卡在魔法与陷阱区域存在，可以攻击的对方怪兽必须作出攻击。\r\n②：自己或者对方的主要阶段以及战斗阶段把魔法与陷阱区域表侧表示存在的这张卡送去墓地才能把这个效果发动。用包含「龙星」怪兽1只以上的怪兽为素材把1只同调怪兽同调召唤。','\r',char(13)),'\n',char(10)),'同调召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77797992,'森之圣兽 羊驼驯鹿',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：自己场上表侧攻击表示存在的鸟兽族·昆虫族·植物族怪兽不会被战斗破坏。\r\n●守备表示：自己场上表侧守备表示存在的鸟兽族·昆虫族·植物族怪兽不会成为卡的效果的对象，不会被卡的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77799846,'废品眼太鼓龙',replace(replace('机械族8星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。这张卡的攻击力直到下次的对方的结束阶段时上升1000。持有超量素材的这张卡被破坏的场合，可以通过把自己墓地1只名字带有「超级防卫机器人」的怪兽从游戏中除外，这张卡从墓地特殊召唤。那之后，可以选自己墓地1只名字带有「超级防卫机器人」的怪兽在这张卡下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'攻击上升','特殊召唤','是否要选择墓地1只名字带有「超级防卫机器人」的怪兽作为超量素材？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77826734,'灵摆苏生','①：选1只自己的额外卡组的表侧表示的灵摆怪兽或者自己墓地的灵摆怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77827521,'地狱的裁判','将敌人封入棺材里。然后由地狱使者下达判决。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77840540,'超进化之茧',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：有装备卡装备的自己·对方场上1只昆虫族怪兽解放，从卡组把1只昆虫族怪兽无视召唤条件特殊召唤。\r\n②：自己主要阶段把墓地的这张卡除外，以自己墓地1只昆虫族怪兽为对象才能发动。那只怪兽加入卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77841719,'入魔人偶 葛佩利亚','这张卡不能特殊召唤。这张卡因对方从场上离开时，选择对方场上表侧表示存在的1只怪兽直到下次的自己的结束阶段时得到控制权。','获得控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77847678,'紫炎的计略','自己场上存在的名字带有「六武众」的怪兽被战斗破坏的场合才能发动。从手卡把名字带有「六武众」的怪兽最多2只特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77859858,'步向破灭的速攻抽卡','双方玩家在抽卡阶段开始时手卡是0张的场合，通常抽卡外可以再抽1张。这张卡的控制者在每次自己回合的结束阶段支付700基本分。这个时候基本分700未满的场合，基本分变成0。自己场上表侧表示存在的这张卡从场上离开时，自己受到3000分伤害。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77864539,'机皇厂','场上表侧表示存在的名字带有「机皇」的怪兽被选择作为攻击对象时才能发动。选择自己墓地存在的1只名字带有「机皇兵」的怪兽加入手卡。那之后，成为攻击对象的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77876207,'通向财宝的隐藏通路','选择自己场上的1只表侧表示存在的攻击力1000以下的怪兽。这个回合，选择的怪兽可以直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77901552,'圣刻龙-泰芙龙',replace(replace('①：只有对方场上才有怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤的回合，这张卡不能攻击。\r\n②：这张卡被解放的场合发动。从自己的手卡·卡组·墓地选1只龙族通常怪兽，攻击力·守备力变成0特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77910045,'死之演算盘','每次怪兽从场上送去墓地，每1张卡使那张卡的主人受到500分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77936940,'杰尔尼亚','①：场上的表侧表示的这张卡被对方的效果破坏送去墓地的场合，下次的自己准备阶段发动。这张卡从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77972406,'反击的毒牙','自己场上表侧表示存在的名字带有「蛇毒」的怪兽受到攻击宣言时才能发动。把1只对方怪兽的攻击无效，战斗阶段结束。之后，给攻击怪兽放置1个毒指示物。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77994337,'哈喽哈喽',replace(replace('←2 【灵摆】 2→\r\n1回合1次：可以选择场上1只表侧表示怪兽；掷1次骰子，那只怪兽的等级直到回合结束时变成和出现的数目相同。\r\n【怪兽描述】\r\n哈喽哈喽，蜡烛脑子，\r\n头顶中空，没有肠子。\r\n寻找甜食，棉花糖子，\r\n小心小心，糖果袋子。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(77998771,'乌鸦天狗','知道各种事的天狗。据说能使用神通之力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78004197,'暗黑界的武神 高尔德','这张卡因为其他卡的效果从手卡丢弃去墓地的场合，这张卡特殊召唤到自己场上。这张卡因为对方的卡的效果丢弃的场合，可以再选择对方场上最多2张卡破坏。','特殊召唤','是否要破坏对方场上的卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78009994,'龙神守卫','每次怪兽通常召唤，给这张卡放置1个龙神指示物。此外，可以把场上表侧表示存在的这张卡送去墓地，把持有这张卡放置的龙神指示物数量以下的等级的1只龙族怪兽从自己卡组特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78010363,'黑森林的魔女',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡从场上送去墓地的场合发动。从卡组把1只守备力1500以下的怪兽加入手卡。这个回合，自己不能作这个效果加入手卡的卡以及那些同名卡的发动。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78033100,'圣刻龙-半龙盖布','这张卡战斗破坏对方怪兽送去墓地时，从自己的手卡·卡组·墓地选1只龙族的通常怪兽，攻击力·守备力变成0特殊召唤。此外，这张卡被解放时，从自己的手卡·卡组·墓地把1只名字带有「圣刻」的通常怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78053598,'暗之指名者','宣言1种怪兽卡的名字。当这种怪兽卡在对方的卡组中存在时，那种卡其中1张加入对方手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78060096,'灭绝国王鲑','暗黑之海的主人，可怕的巨型大马哈鱼。其卵可是暗黑界第一的美食。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78063197,'纳祭融合',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：「眼纳祭神」融合怪兽卡的融合素材怪兽从自己的手卡·场上·墓地除外，把那1只融合怪兽从额外卡组融合召唤。\r\n②：自己主要阶段把墓地的这张卡除外，以对方场上1只效果怪兽为对象才能发动。选自己场上1只「眼纳祭神」融合怪兽或者「纳祭之魔」把作为对象的对方的效果怪兽当作那个效果的装备卡使用来装备。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78080961,'秘旋谍-天才',replace(replace('①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1张「秘旋谍装备」卡加入手卡。\r\n②：这张卡在墓地存在，自己场上有「秘旋谍-花公子」存在的场合丢弃1张手卡才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78082039,'封闭的森林','自己墓地存在的怪兽每有1只，自己场上表侧表示存在的兽族怪兽的攻击力上升100。只要这张卡在场上存在，不能把场地魔法卡发动。此外，这张卡被破坏的回合，不能把场地魔法卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78121572,'黑魔力精制者','1回合1次，可以把自己场上表侧攻击表示存在的这张卡变更为表侧守备表示，并给自己场上表侧表示存在的1张可以放置魔力指示物的卡放置1个魔力指示物。','改变表示形式','请选择要放置魔力指示物的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78156759,'发条机雷 发条雷',replace(replace('3星怪兽×2\r\n场上的这张卡被破坏的场合，可以作为代替把这张卡1个超量素材取除。这个效果适用的回合的结束阶段时1次，选择场上1张卡破坏。','\r',char(13)),'\n',char(10)),'是否要使用「发条机雷 发条雷」的效果代替破坏？','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78161960,'抗拒苏生','①：对方怪兽的直接攻击宣言时才能发动。战斗阶段结束。那之后，可以从自己墓地选调整和同调怪兽各1只特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78184733,'娱乐一闪','①：自己场上有「娱乐伙伴」怪兽存在的场合才能发动。对方场上的表侧攻击表示怪兽全部变成守备表示，那些怪兽直到下个回合的结束时不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78193831,'巴斯达·布雷达','①：这张卡的攻击力上升对方的场上·墓地的龙族怪兽数量×500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78193832,'巴斯达·布雷达','①：这张卡的攻击力上升对方的场上·墓地的龙族怪兽数量×500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78202553,'地中族邪界兽·直布油岩兽',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合才能发动。从手卡丢弃1只「地中族」怪兽，自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78211862,'能量提升','①：丢弃1张手卡，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升1500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78229193,'白斗气海豚',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时变成原本攻击力的一半。\r\n②：这张卡被对方破坏送去墓地的场合，把这张卡以外的自己墓地1只水属性怪兽除外才能发动。这张卡当作调整使用特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78243409,'火山口的潜伏者','当这张卡从场上被破坏送去墓地时，可以从手卡特殊召唤1只炎族怪兽上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78266168,'巨大木乃伊','这张卡1个回合可以有1次变回里侧守备表示。里侧守备表示的这张卡受到攻击，攻击的怪兽的攻击力比这张卡的守备力低的场合，攻击的怪兽破坏。','变成里侧守备','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78274190,'超重辉将 珊瑚-5',replace(replace('←8 【灵摆】 8→\r\n①：自己墓地有魔法·陷阱卡存在的场合，这张卡的灵摆刻度变成4。\r\n②：1回合1次，自己的「超重武者」怪兽战斗破坏对方怪兽时才能发动。那只怪兽只再1次可以继续攻击。\r\n【怪兽效果】\r\n这张卡在规则上也当作「超重武者」卡使用。「超重辉将 珊瑚-5」的怪兽效果1回合只能使用1次。\r\n①：自己墓地没有魔法·陷阱卡存在的场合，把自己场上最多2只「超重武者」怪兽解放才能发动。自己从卡组抽出解放的数量。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78275321,'赤蚁','这张卡被战斗破坏送去墓地时，可以选择自己墓地存在的1只5星的怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，那个回合的结束阶段时送去墓地。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78316184,'电子化天使-美朱濡-',replace(replace('「机械天使的仪式」降临。\r\n①：这张卡仪式召唤成功的场合才能发动。从额外卡组特殊召唤的对方场上的怪兽全部破坏，给与对方破坏的怪兽数量×1000伤害。这个回合，这张卡在同1次的战斗阶段中可以作2次攻击。\r\n②：1回合1次，要让场上的卡破坏的魔法·陷阱·怪兽的效果发动时，让自己墓地1只仪式怪兽回到卡组才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78348934,'破坏剑士的宿命',replace(replace('「破坏剑士的宿命」的①②的效果1回合各能使用1次。\r\n①：以对方墓地最多3只相同种族的怪兽为对象才能发动。那些怪兽除外，选自己场上1只「巴斯达·布雷达」怪兽或者「破坏剑」怪兽，直到回合结束时那个攻击力·守备力上升除外的怪兽数量×500。\r\n②：这张卡在墓地存在的场合，从手卡丢弃1张「破坏剑」卡才能发动。墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'继续除外怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78349103,'机甲和平卫士','场上存在的这张卡被破坏送去墓地时，可以从自己卡组把1只同盟怪兽加入手卡。1回合1次，自己的主要阶段时可以当作装备卡使用给自己场上的机械族怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。（1只怪兽可以装备的同盟最多1张。装备怪兽被破坏的场合，作为代替把这张卡破坏。）','变成装备卡','特殊召唤','检索','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78355370,'苏生栗子球',replace(replace('①：自己·对方的战斗阶段结束时把这张卡从手卡丢弃，以这个回合被战斗破坏送去自己墓地的1只怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：对方怪兽的攻击宣言时，把墓地的这张卡除外，以自己墓地的「栗子球」怪兽任意数量为对象才能发动。那些怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78358521,'星圣·草帽星系',replace(replace('「星圣·草帽星系」的①②③的效果1回合各能使用1次。\r\n①：把自己墓地1只「星圣」怪兽除外，以自己墓地1只「星圣」怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：这张卡的①的效果适用的回合的主要阶段才能发动。把1只「星圣」怪兽召唤。\r\n③：这张卡被送去墓地的回合，「星圣」怪兽召唤的场合需要的解放可以减少1只。','\r',char(13)),'\n',char(10)),'选择自己墓地1只名字带有「星圣」的怪兽加入手卡','把1只名字带有「星圣」的怪兽召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78364470,'星圣·北河三','①：这张卡召唤成功的回合，自己在通常召唤外加上只有1次，自己主要阶段可以把1只「星圣」怪兽召唤。','额外的通常召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78371393,'于贝尔','这张卡不会被战斗破坏。表侧攻击表示存在的这张卡被对方怪兽攻击的场合，伤害计算前给与对方基本分攻击怪兽的攻击力数值的伤害。这张卡进行战斗所受到的对控制者的战斗伤害变成0。这张卡在自己的结束阶段时若不把自己场上1只怪兽作为祭品则被破坏。被这张卡的效果以外的方法破坏时，可以从自己的手卡·卡组·墓地把1只「于贝尔-被憎恶的骑士」特殊召唤。','伤害','破坏','是否要选择1只怪兽作为祭品？','特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(78387742,'冒牌英雄','从自己手卡特殊召唤1只名字带有「元素英雄」的怪兽。那只怪兽不能攻击，这个回合的结束阶段时回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78391364,'超重武者 天秤B-N',replace(replace('「超重武者 天秤B-N」的②的效果1回合只能使用1次。\r\n①：对方场上有怪兽2只以上存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡召唤·特殊召唤成功的场合，以「超重武者 天秤B-N」以外的自己墓地1只4星以下的「超重武者」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','墓地怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78402798,'蜥蜴骑士','守护着大会、被绿色的鳞片包住身体的蜥蜴战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78422252,'XX-剑士 富勒姆奈特',replace(replace('①：只在这张卡在场上表侧表示存在才有1次，对方怪兽的攻击宣言时以那1只怪兽为对象才能发动。那次攻击无效。\r\n②：这张卡战斗破坏对方的守备表示怪兽的场合，以自己墓地1只4星以下的「X-剑士」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'攻击无效','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78423643,'三头基多','有三个头的怪兽，性格非常凶暴。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78437364,'刚鬼 巨人食人魔',replace(replace('「刚鬼」怪兽2只以上\r\n①：只要这张卡在怪兽区域存在，场上的怪兽的攻击力下降那怪兽的原本守备力数值。\r\n②：这张卡被战斗·效果破坏的场合，可以作为代替把这张卡所连接区1只自己怪兽破坏。','\r',char(13)),'\n',char(10)),'是否使用「刚鬼 巨人食人魔」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78437365,'刚鬼 巨人食人魔',replace(replace('「刚鬼」怪兽2只以上\r\n①：只要这张卡在怪兽区域存在，场上的怪兽的攻击力下降那怪兽的原本守备力数值。\r\n②：这张卡被战斗·效果破坏的场合，可以作为代替把这张卡所连接区1只自己怪兽破坏。','\r',char(13)),'\n',char(10)),'是否使用「刚鬼 巨人食人魔」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78474168,'突破技能',replace(replace('①：以对方场上1只效果怪兽为对象才能发动。那只对方怪兽的效果直到回合结束时无效。\r\n②：自己回合把墓地的这张卡除外，以对方场上1只效果怪兽为对象才能发动。那只对方的效果怪兽的效果直到回合结束时无效。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78486968,'星圣·娄宿一','①：这张卡召唤成功时才能发动。从卡组把1只「星圣」怪兽加入手卡。','卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78509901,'方界兽 利刃之迦楼迪亚',replace(replace('这张卡不能通常召唤。把自己场上2只「方界」怪兽送去墓地的场合可以特殊召唤。\r\n①：这个方法特殊召唤的这张卡的攻击力上升2000。\r\n②：这张卡在同1次的战斗阶段中可以作2次攻击。\r\n③：这张卡战斗破坏怪兽时，以自己墓地最多3只「方界胤 毗贾姆」为对象才能发动。这张卡送去墓地，作为对象的怪兽特殊召唤。那之后，可以从卡组把1只「方界超兽 破坏之乾闼尔」加入手卡。','\r',char(13)),'\n',char(10)),'是否把1只「方界超兽 破坏之乾闼尔」加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78512663,'元素英雄 熔岩新宇侠',replace(replace('「元素英雄 新宇侠」＋「新空间侠·火焰甲虫」＋「新空间侠·大地鼹鼠」\r\n把自己场上存在的上记的卡回到卡组的场合才能从额外卡组特殊召唤（不需要「融合」魔法卡）。这张卡的攻击力上升场上的卡的数量×400的数值。结束阶段时这张卡回到额外卡组。这个效果回到额外卡组时，场上存在的全部卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'返回卡组','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78540593,'姬葵 玛丽娜','只要这张卡在场上表侧表示存在，这张卡以外的自己场上的植物族怪兽1只被战斗或者卡的效果破坏送去墓地的场合，可以选择对方场上1张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78552773,'苏帕伊','场上存在的这张卡被卡的效果送去墓地时，可以从自己卡组把1只「太阳之神官」特殊召唤。这个效果特殊召唤的怪兽的攻击力变成2倍，这个回合的结束阶段时回到持有者手卡。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78556320,'女夜魔战士','侍奉黑暗的女战士。将对方进行血祭是她生存的意义。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78564023,'黑羽-二太刀之厄特西安','自己场上存在的名字带有「黑羽」的怪兽进行和对方怪兽的战斗的伤害步骤结束时那只对方怪兽在场上存在的场合，把这张卡从手卡送去墓地才能发动。给与对方基本分1000分伤害。','给与对方1000伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78574395,'神奇气球',replace(replace('①：1回合1次，把手卡任意数量送去墓地才能发动。送去墓地的那个数量的气球指示物给这张卡放置。\r\n②：只要这张卡在魔法与陷阱区域存在，对方场上的怪兽的攻击力下降这张卡的气球指示物数量×300。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78577570,'伽玛剑的誓言',replace(replace('「伽玛剑」的降临必需。\r\n①：从自己的手卡·场上把等级合计直到7以上的怪兽解放，从手卡把1只「伽玛剑」仪式召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78586116,'加电插头','发动后这张卡变成装备卡，给自己场上表侧表示存在的1只名字带有「变形斗士」的怪兽装备。装备怪兽不能攻击。被这个效果装备时，选择装备怪兽以外的场上表侧表示存在的1只怪兽。选择的怪兽的攻击力上升装备怪兽的攻击力数值。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78610936,'超量叠光延迟',replace(replace('不能对应这张卡的发动让魔法·陷阱·怪兽的效果发动。\r\n①：以对方场上1只持有超量素材的超量怪兽为对象才能发动。作为对象的怪兽的超量素材全部取除，作为对象的怪兽回到持有者的额外卡组。那之后，取除的超量素材之中有怪兽卡的场合，那些怪兽从墓地尽可能往对方场上守备表示特殊召唤。这个效果特殊召唤的怪兽的等级下降1星。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78613627,'死亡袋鼠','守备表示的这张卡受到攻击时，若攻击怪兽的攻击力比这张卡的守备力低的场合，那只攻击怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78621186,'野蛮人之吼','①：自己场上的战士族怪兽被选择作为攻击对象时或者成为对方怪兽的效果的对象时，以对方场上1只表侧表示怪兽为对象才能发动。给与对方那只怪兽的原本攻击力数值的伤害，那只怪兽回到手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78625592,'光之护封灵剑',replace(replace('①：对方怪兽的攻击宣言时1次，支付1000基本分才能把这个效果发动。那次攻击无效。\r\n②：对方回合把墓地的这张卡除外才能发动。这个回合，对方怪兽不能直接攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78636495,'纽特',replace(replace('①：这张卡反转的场合发动。这张卡的攻击力·守备力上升500。\r\n②：这张卡被和怪兽的战斗破坏的场合发动。那只怪兽的攻击力·守备力下降500。','\r',char(13)),'\n',char(10)),'攻守上升500','攻守下降500','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78637313,'来自黑暗的呼声','用「死者苏生」苏生的怪兽全部送到墓地。只要场上有这张卡「死者苏生」就不能使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78651105,'神兽王 巴巴罗斯',replace(replace('①：这张卡可以不用解放作通常召唤。\r\n②：这张卡的①的方法通常召唤的这张卡的原本攻击力变成1900。\r\n③：这张卡也能把3只怪兽解放作召唤。\r\n④：这张卡用这张卡的③的方法召唤成功的场合发动。对方场上的卡全部破坏。','\r',char(13)),'\n',char(10)),'不使用祭品通常召唤','使用三个祭品通常召唤','对方场上的卡全部破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78658564,'哥布林突击部队','这张卡攻击的场合，战斗阶段结束时改为守备表示。直到下次自己的回合结束这张卡都不能改变表示形式。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78663366,'入魔雷神鸟','魔法·陷阱·效果怪兽的效果发动时，可以把自己场上的这张卡从游戏中除外。这个效果在对方回合也能发动。这个效果除外的这张卡在下次的准备阶段时回到场上，攻击力上升300。「入魔雷神鸟」的效果1回合只能发动1次。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78697395,'第三之棺','这张卡只能通过「第一之棺」的效果上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78700060,'死灵骑士',replace(replace('这张卡不能特殊召唤。\r\n①：怪兽的效果发动时，把这张卡解放发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'不能特殊召唤','怪物效果发动无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78706415,'纤维壶','反转：双方把自己场上的卡，手卡，墓地的卡和卡组合在一起洗切。之后各自从卡组抽5张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78734254,'新空间侠·海洋海豚','这张卡名在规则上也当作「新空间侠·水波海豚」使用。这张卡用「新空间侠界限」的效果才能特殊召唤。丢弃1张手卡。确认对方手卡并选择1张怪兽卡。选择怪兽的攻击力以上的怪兽在自己场上存在的场合，选择的怪兽卡破坏并给与对方基本分500分伤害。这个效果1回合只能使用1次。','确认手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78748366,'炼狱的消华',replace(replace('「炼狱的消华」的①的效果1回合只能使用1次。\r\n①：丢弃1张手卡才能把这个效果发动。从卡组把「炼狱的消华」以外的1张「炼狱」魔法·陷阱卡加入手卡。这个回合，自己不能把「狱火机」怪兽以外的怪兽召唤·特殊召唤。\r\n②：自己的「狱火机」怪兽和对方怪兽进行战斗的伤害计算后，把魔法与陷阱区域的表侧表示的这张卡送去墓地才能发动。那些进行战斗的双方怪兽除外。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78765160,'不知火流 轮回之阵',replace(replace('①：这张卡只要在魔法与陷阱区域存在，卡名当作「不知火流 转生之阵」使用。\r\n②：1回合1次，可以从以下效果选择1个发动。\r\n●把自己场上1只表侧表示的不死族怪兽除外才能发动。这个回合，自己受到的全部伤害变成0。\r\n●以除外的2只自己的守备力0的不死族怪兽为对象才能发动。那2只怪兽回到卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'除外并使伤害变成0','回收并抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78780140,'特伦特','还在逐渐成长的大树，是森林的守护神。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78783370,'地狱的冷枪','给与战斗伤害以外的伤害的效果发动时才能发动这张卡。将自己所受的伤害转给对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78785392,'花合',replace(replace('「花合」在1回合只能发动1张，这张卡发动的回合，自己不是「花札卫」怪兽不能召唤·特殊召唤。\r\n①：从卡组把4只攻击力100的「花札卫」怪兽攻击表示特殊召唤（同名卡最多1张）。这个效果特殊召唤的怪兽的效果无效化，不能为上级召唤而解放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78792195,'六武众的先导','自己场上有这张卡以外的名字带有「六武众」的怪兽表侧表示存在的场合才能发动。可以把自己场上存在的1只名字带有「六武众」的怪兽解放，场上存在的1只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78794994,'战线复活的代偿','把自己场上1只通常怪兽送去墓地。选择墓地存在的1只怪兽在自己场上特殊召唤，把这张卡装备。这张卡不在场上存在时，装备怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78811937,'偷偷靠近的黑暗','把自己墓地2只暗属性怪兽从游戏中除外发动。从卡组把1只暗属性·4星怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78835747,'娱乐伙伴 万花筒蝎',replace(replace('←4 【灵摆】 4→\r\n①：自己场上的光属性怪兽的攻击力上升300。\r\n【怪兽效果】\r\n①：1回合1次，以自己场上1只表侧表示怪兽为对象才能发动。这个回合，那只怪兽可以向对方场上的特殊召唤的怪兽全部各作1次攻击。','\r',char(13)),'\n',char(10)),'多次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78845026,'电光调整','选择自己场上表侧表示存在的1只4星的光属性怪兽发动。选择怪兽只要在场上表侧表示存在当作调整使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78859567,'分组连接',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：自己·对方的主要阶段2才能发动。选自己的手卡·卡组·墓地的2星以下的怪兽任意数量在作为场上的连接怪兽所连接区的自己场上特殊召唤（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'是否继续选择？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78861134,'炎之剑豪','传说不幸的剑豪跌入了火山，从此便拥有了散发出火焰的能力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78864369,'还魂','将自己墓地里的1张反转效果怪兽卡弹回卡组最上面。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78868119,'深海歌后','①：这张卡召唤成功时才能发动。从卡组把1只3星以下的海龙族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78868776,'剑斗兽 绳斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功的场合，这张卡的原本攻击力变成2100。这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组，从卡组把「剑斗兽 绳斗」以外的1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78872731,'十二兽 鼠骑',replace(replace('①：这张卡召唤成功的场合才能发动。从卡组把1张「十二兽」卡送去墓地。\r\n②：持有这张卡作为素材中的原本种族是兽战士族的超量怪兽得到以下效果。\r\n●1回合1次，把这张卡1个超量素材取除才能发动。从手卡·卡组把1只「十二兽 鼠骑」特殊召唤。','\r',char(13)),'\n',char(10)),'从卡组把1张「十二兽」卡送去墓地','从手卡·卡组把1只「十二兽 鼠骑」特殊召唤（十二兽 鼠骑）','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78910579,'仪水镜的返魂术','选择自己场上1只水属性怪兽回到卡组，选择自己墓地存在的2只水属性怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78922939,'废品从者','自己场上有名字带有「废品」的怪兽表侧表示存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78933589,'原初之液','1回合1次，自己的主要阶段时才能发动。让手卡最多2只名字带有「进化龙」的怪兽回到卡组洗切，并从卡组抽出回去数量的卡。「原初之液」在自己场上只能有1张表侧表示存在。','手牌交换','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78942513,'通向魂源的影劫回归','①：以自己场上1只「影依」怪兽为对象才能发动。从手卡把1张「影依」卡送去墓地。作为对象的怪兽攻击力·守备力上升1000，结束阶段变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78949372,'威风妖怪超级单体',replace(replace('①：自己的灵摆区域有「威风妖怪」卡存在的场合，这张卡以外的「威风妖怪」卡的自己场上发动的效果的发动和效果不会被无效化。\r\n②：1回合1次，以自己墓地5张「威风妖怪」卡为对象才能发动。那5张卡加入卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'墓地回收','发动同时使用效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78984772,'爆炎龙','宇宙形成时所诞生的龙，那股冲击力使双胞胎龙合体。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(78986941,'突击指令','①：以衍生物以外的自己场上1只通常怪兽为对象才能发动。那只通常怪兽解放，选对方场上1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79016563,'二进制女巫',replace(replace('衍生物以外的怪兽2只\r\n①：得到和这张卡互相连接的怪兽数量的以下效果。\r\n●1只以上：和这张卡互相连接的怪兽用和对方怪兽的战斗给与对方战斗伤害时才能发动。自己基本分回复那个数值。\r\n●2只：1回合1次，以自己场上2只表侧表示怪兽为对象才能发动。直到回合结束时，那2只怪兽之内1只的攻击力变成一半，另1只的攻击力上升那个数值。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'基本分回复','攻击力上升','请选择要让攻击力变成一半的怪兽','请选择要让攻击力上升的怪兽','','','','','','','','','','','','');
INSERT INTO texts VALUES(79068663,'废品坠击','装备怪兽战斗破坏怪兽送去墓地时，给与对方基本分破坏怪兽的攻击力一半数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79080761,'巨石终极龙','这张卡不能通常召唤。把自己墓地存在的2只地属性怪兽从游戏中除外的场合可以特殊召唤。自己场上表侧表示存在的这张卡被卡的效果送去墓地时，对方场上存在的魔法·陷阱卡全部破坏。','对方场上所有魔法·陷阱卡全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79094383,'光波异邦臣',replace(replace('「光波异邦臣」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡在手卡·墓地存在的场合，以自己场上1只「光波」超量怪兽为对象才能发动。把这张卡在那只怪兽下面重叠作为超量素材。\r\n②：这张卡被送去墓地的场合才能发动。从卡组把1张「光波」魔法·陷阱卡加入手卡。','\r',char(13)),'\n',char(10)),'这张卡作为超量素材','「光波」魔法·陷阱卡加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79106360,'混沌壶','反转：双方场上的怪兽卡加入持有者的卡组中洗切。那之后直到把和加入卡组数量相同的怪兽卡翻出为止，从双方卡组的最上方翻卡，等级4以下的怪兽以里侧守备表示在场上特殊召唤。那些以外的卡全部丢弃去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79109599,'沼地的魔神王',replace(replace('①：这张卡可以作为融合怪兽卡有卡名记述的1只融合素材怪兽的代替。那个时候，其他的融合素材怪兽必须是正规品。\r\n②：自己主要阶段把这张卡从手卡丢弃去墓地才能发动。从卡组把1张「融合」加入手卡。','\r',char(13)),'\n',char(10)),'检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79126789,'暗黑界的狩人 布劳','这张卡因为其他卡的效果从手卡丢弃去墓地的场合，从卡组抽1张卡。这张卡因为对方的卡的效果丢弃的场合，再抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79155167,'虫甲爆破','选择自己场上1张名字带有「甲虫装机」的卡和对方场上表侧表示存在的2张卡发动。选择的卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79161790,'反转世界','场上表侧表示存在的全部效果怪兽的攻击力·守备力交换。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79176962,'旋风机 风神电子人',replace(replace('这张卡表侧表示上级召唤的场合解放的怪兽不送去墓地回到持有者手卡。\r\n①：1回合1次，丢弃1张手卡才能发动。和这张卡相同纵列的对方场上的卡全部回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79178930,'时限机巧爆弹','表侧守备表示存在的名字带有「机巧」的怪兽被选择作为攻击对象时才能发动。对方场上表侧表示存在的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79182538,'暗黑之狂犬','原本曾经是在公园玩耍的普通狗类，但是由于黑暗的力量而凶暴化了。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79185500,'铁巨人 大铁锤','自己场上有「异次元超能人·星斗罗宾」「野兽战士 豹人」「凤王兽 铠楼罗」存在的场合，这张卡可以从手卡特殊召唤。这张卡只要在场上表侧表示存在，不能把表示形式变更。此外，1回合1次，选择自己场上1只怪兽才能发动。这个回合，选择的怪兽可以直接攻击对方玩家。','直接攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79205581,'强制终了','可以把自己场上存在的这张卡以外的1张卡送去墓地，这个回合的战斗阶段结束。这个效果在战斗阶段时才能发动。','战斗阶段结束','是否现在使用「強制終了」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79206750,'深渊蔑噬','选择自己场上1只名字带有「水精鳞」的怪兽才能发动。选择的怪兽的攻击力直到结束阶段时上升1000。此外，盖放的这张卡被送去墓地时，选择对方场上1只怪兽送去墓地。','怪兽送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79229522,'嵌合要塞龙',replace(replace('「电子龙」＋机械族怪兽1只以上\r\n把自己·对方场上的上记卡送去墓地的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作为融合素材。\r\n①：这张卡的原本攻击力变成作为这张卡的融合素材的怪兽数量×1000。','\r',char(13)),'\n',char(10)),'请选择一只「电子龙」','请选择1只以上的机械族怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79234734,'超战士之魂',replace(replace('「超战士之魂」的①②的效果1回合各能使用1次。\r\n①：把手卡1只「混沌战士」怪兽送去墓地才能发动。这张卡直到下次的对方结束阶段攻击力变成3000，卡名当作「混沌战士」使用。\r\n②：把墓地的这张卡除外才能发动。从卡组把1只「开辟之骑士」或者「宵暗之骑士」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79279397,'D少年组','反转：可以从卡组把「D少年组」任意数量表侧攻击表示特殊召唤。那之后，自己受到这个效果特殊召唤的怪兽数量×1000的数值的伤害。','特殊召唤','是否要进行特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79306385,'宣告者的神托',replace(replace('「崇光之宣告者」的降临必需。对方不能对应这张卡的发动把魔法·陷阱·怪兽的效果发动。\r\n①：从自己的手卡·场上把等级合计直到12以上的怪兽解放，从手卡把「崇光之宣告者」仪式召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79323590,'魔力之枷','双方每次把手卡召唤·特殊召唤·盖伏·发动时，每1张必须支付基本分500分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79324191,'隐密忍法帖','①：1回合1次，从手卡把1只「忍者」怪兽送去墓地才能发动。从卡组选「隐密忍法帖」以外的1张「忍法」魔法·陷阱卡在自己场上盖放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79333300,'风灵术-「雅」','自己场上存在的1只风属性怪兽做祭品。选择对方场上存在的1张卡，回到持有者的卡组的最下面。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79335209,'扰乱·黑','使用一切手段捣乱的扰乱三人组中的一员。据说集齐了三人就会发生什么。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79337169,'手甲战士','把这张卡解放发动。自己场上表侧表示存在的全部战士族怪兽的攻击力·守备力上升500，直到那些怪兽进行战斗的伤害步骤结束时适用。这个效果在对方回合也能发动。','攻守上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79402185,'化学结合-D2O',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：把自己的手卡·场上2只「氘素龙」和1只「氧素龙」解放才能发动。从自己的手卡·卡组·墓地选1只「水龙」或者「水龙-团簇」当作「化学结合-H2O」的效果特殊召唤。\r\n②：这张卡在墓地存在，「水龙」或者「水龙-团簇」从场上送去自己墓地的场合发动。墓地的这张卡回到手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79407975,'究极宝玉神 虹暗龙','这张卡不能通常召唤。把自己墓地存在的暗属性怪兽7种类从游戏中除外的场合才能特殊召唤。可以把除这张卡外的自己场上和自己墓地存在的暗属性怪兽全部从游戏中除外，除外的卡每有1张，这张卡的攻击力上升500。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79409334,'暗黑剑龙','这张卡被选择作为对方怪兽的攻击对象时，这张卡变成守备表示。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79418153,'枪兵恶魔','对方场上守备表示存在的怪兽为攻击对象的自己怪兽的攻击宣言时才能发动。那只怪兽向守备表示怪兽攻击的场合，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。这个效果1回合只能使用1次。','穿刺伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79418928,'电磁石战士β',replace(replace('「电磁石战士β」的①的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把「电磁石战士β」以外的1只4星以下的「磁石战士」怪兽加入手卡。\r\n②：对方回合把这张卡解放才能发动。从卡组把1只4星的「磁石战士」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'加入手卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79441381,'黄昏之忍者-上弦',replace(replace('←1 【灵摆】 1→\r\n①：自己的「忍者」怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n【怪兽效果】\r\n①：把手卡1张「忍法」卡给对方观看才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79444933,'炎之魔精 伊格尼斯','把自己场上表侧表示存在的1只炎属性怪兽解放发动。给与对方基本分自己墓地存在的炎属性怪兽数量×100的数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79447365,'卡通面具','①：自己场上有「卡通世界」存在的场合，以对方场上1只表侧表示怪兽为对象才能发动。把持有那只怪兽的等级或者阶级的数值以下的等级的1只卡通怪兽无视召唤条件从手卡·卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79473793,'青冰白夜龙',replace(replace('①：这张卡为对象的魔法·陷阱卡发动时发动。那个发动无效并破坏。\r\n②：这张卡以外的自己的表侧表示怪兽被选择作为攻击对象时，把自己场上1张魔法·陷阱卡送去墓地才能发动。攻击对象转移为这张卡。','\r',char(13)),'\n',char(10)),'无效并破坏','改变攻击对象','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79491903,'爬虫妖眼镜蛇','这张卡不会被战斗破坏。和这张卡进行战斗的怪兽在战斗阶段结束时攻击力变成0。自己的结束阶段时，场上表侧守备表示存在的这张卡变成表侧攻击表示。','攻击变成0','变成表侧攻击表示','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79514956,'幻奏的音女 埃勒姬',replace(replace('①：只要这张卡在怪兽区域存在，自己场上的特殊召唤的「幻奏」怪兽不会被效果破坏。\r\n②：只要特殊召唤的这张卡在怪兽区域存在，自己场上的天使族怪兽的攻击力上升300。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79523365,'吸血鬼·红灾星',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合，支付1000基本分，以「吸血鬼·红灾星」以外的自己墓地1只「吸血鬼」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不能攻击。\r\n②：这张卡战斗破坏怪兽的战斗阶段结束时才能发动。那些怪兽从墓地尽可能往自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79531196,'水晶蔷薇',replace(replace('「水晶蔷薇」的②的效果1回合只能使用1次。\r\n①：1回合1次，自己主要阶段才能发动。从手卡·卡组把1只「宝石骑士」怪兽或者「幻奏」怪兽送去墓地。直到结束阶段，这张卡当作和送去墓地的怪兽同名卡使用。\r\n②：这张卡在墓地存在的场合，从自己墓地把1只融合怪兽除外才能发动。这张卡守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79538761,'扭力调整齿轮',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只表侧表示怪兽为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：装备怪兽当作调整使用，攻击力·守备力上升500。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79544790,'悔恨苏生','自己场上存在的怪兽被战斗破坏送去墓地时才能发动。那1只怪兽在自己场上表侧守备表示特殊召唤。这个效果特殊召唤的怪兽在自己的结束阶段时破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79555535,'点火阵·凤凰',replace(replace('①：场上的「点火骑士」怪兽的攻击力·守备力上升300。\r\n②：1回合1次，以自己场上1张「点火骑士」卡为对象才能发动。那张卡破坏，从卡组把1张「点火骑士」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79559912,'DDD 怒涛大王 决策凯撒',replace(replace('恶魔族6星怪兽×2\r\n①：包含把怪兽特殊召唤效果的怪兽的效果·魔法·陷阱卡发动时，把这张卡1个超量素材取除才能发动。那个发动无效并破坏。那之后，可以选这张卡以外的自己场上1只「DD」怪兽，那只怪兽和这张卡的攻击力直到回合结束时上升1800。\r\n②：这张卡从场上送去墓地的场合才能发动。从卡组把1张「契约书」卡加入手卡。','\r',char(13)),'\n',char(10)),'发动无效并破坏','是否选怪兽上升攻击力？','卡组「契约书」卡加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79569173,'大地震','自己场上存在的恐龙族怪兽被破坏送去墓地时才能发动。从对方场上的魔法与陷阱卡区域指定3处。指定的魔法·陷阱卡区域不能使用。这个时候有卡存在的地方不能指定。这张卡发动后第3次的自己准备阶段时破坏。因这个效果破坏的场合，可以使自己墓地的1只恐龙族怪兽回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79571449,'天使的施舍','从卡组抽3张卡，之后从手卡选2张丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79575620,'注射天使 莉莉','①：这张卡进行战斗的那次伤害计算时1次，支付2000基本分才能发动。这张卡的攻击力只在那次伤害计算时上升3000。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79580323,'剑斗兽 斯巴达克斯','「剑斗兽 重斗」以外的效果不能把这张卡特殊召唤。这张卡特殊召唤成功时，从卡组把1张名字带有「斗器」的装备魔法卡加入手卡。这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组，从卡组把「剑斗兽 斯巴达克斯」以外的1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','检索卡组','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79606837,'虹光之宣告者',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：只要这张卡在怪兽区域存在，从双方的手卡·卡组送去墓地的怪兽不去墓地除外。\r\n②：怪兽的效果·魔法·陷阱卡发动时，把这张卡解放才能发动。那个发动无效并破坏。\r\n③：这张卡被送去墓地的场合才能发动。从卡组把1只仪式怪兽或者1张仪式魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'无效并破坏','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79629370,'月光少女','月亮加护的魔导士。神秘的魔法能使人看到幻觉。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79636594,'积木蜘蛛',replace(replace('「积木蜘蛛」的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，对方不能把其他的昆虫族怪兽作为攻击对象。\r\n②：这张卡特殊召唤成功的场合才能发动。从卡组把1只「积木蜘蛛」特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79649195,'铠甲破坏','使装备魔法卡的发动无效，并且将其破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79703905,'云魔物-高层云','这张卡不会被战斗破坏。这张卡表侧守备表示在场上存在的场合，这张卡破坏。这张卡的召唤成功时，给这张卡放置场上存在的名字带有「云魔物」的怪兽数量的雾指示物。可以把场上存在的雾指示物取除3个，对方手卡随机丢弃1张。','放置指示物','丢弃手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79707116,'麻痹链','每次卡的效果从对方卡组把卡送去墓地，给与对方基本分300伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79718768,'急转直下','对方把以墓地存在的卡为对象的魔法·陷阱·效果怪兽的效果发动的场合这张卡破坏。这张卡的效果让这张卡被破坏送去墓地时，对方把墓地存在的卡全部加入卡组洗切。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79757784,'幻奏的音女 唐唐',replace(replace('①：自己场上有「幻奏」怪兽存在，这张卡特殊召唤成功的场合才能发动。从自己的卡组·墓地选1张「融合」加入手卡。\r\n②：这张卡成为融合召唤的素材送去墓地的场合，以自己场上1只「幻奏」怪兽为对象才能发动。那只怪兽的攻击力下降500，给与对方500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79759367,'魔导人偶之夜','自己墓地没有怪兽存在的场合才能发动。效果怪兽的效果的发动无效。自己场上有「魔偶甜点·布丁公主」存在的场合，再让对方手卡随机1张回到卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79759861,'对死者的供奉','①：丢弃1张手卡，以场上1只怪兽为对象才能发动。那只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79766336,'黑暗中的陷阱','①：自己基本分是3000以下时，支付1000基本分，以「黑暗中的陷阱」以外的自己墓地1张通常陷阱卡为对象才能发动。这张卡的效果变成和那张墓地的通常陷阱卡发动时的效果相同。那之后，那张墓地的通常陷阱卡除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79777187,'护罩泡泡',replace(replace('①：只要这张卡在魔法与陷阱区域存在，自己场上的全部「娱乐伙伴」怪兽以及「娱乐法师」怪兽1回合各有1次不会被战斗·效果破坏。\r\n②：只要这张卡在魔法与陷阱区域存在，自己场上的「娱乐伙伴」怪兽以及「娱乐法师」怪兽的战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'「护罩泡泡」的效果已经适用一次','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79785958,'入魔人·北河二','这张卡召唤成功的回合，自己在通常召唤外加上只有1次可以把1只名字带有「入魔」的怪兽召唤。','「入魔人·北河二」的效果召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79794767,'无形噬体·愤怒',replace(replace('←3 【灵摆】 3→\r\n这张卡的控制者在每次自己准备阶段把自己场上1只怪兽解放。或者不解放让这张卡破坏。\r\n①：只要自己场上有「无形噬体」怪兽存在，双方不能把「无形噬体」怪兽以外的怪兽解放。\r\n【怪兽效果】\r\n①：只要灵摆召唤·反转过的这张卡在怪兽区域存在，双方不是「无形噬体」怪兽不能从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'是否解放自己场上1只怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79796561,'同调炮手','1回合1次，自己的主要阶段1才能发动。把自己场上表侧表示存在的1只同调怪兽从游戏中除外并给与对方基本分600分伤害。这个效果除外的怪兽在下次的自己的准备阶段时回到场上。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79798060,'地缚神 卡拉伊瓦','名字带有「地缚神」的怪兽在场上只能有1只表侧表示存在。场上没有表侧表示场地魔法卡存在的场合这张卡破坏。对方不能选择这张卡作为攻击对象。这张卡可以直接攻击对方玩家。这张卡的效果以外的效果让这张卡破坏时，场上存在的卡全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79814787,'传说的白石','这张卡被送去墓地时，从自己卡组把1只「青眼白龙」加入手卡。','加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79816536,'召唤师的技艺','①：从卡组把1只5星以上的通常怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79844764,'帝王的烈旋',replace(replace('这个卡名的卡在1回合只能发动1张，这张卡发动的回合，自己不能从额外卡组把怪兽特殊召唤。\r\n①：这个回合，自己把怪兽上级召唤的场合只有1次，可以作为自己场上1只怪兽的代替而把对方场上1只怪兽解放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79850798,'爆走特急 火箭飞矢','这张卡不能通常召唤。自己场上没有卡存在的场合才能特殊召唤。把这张卡特殊召唤的回合，自己不能进行战斗阶段。只要这张卡在场上表侧表示存在，自己不能把魔法·陷阱·效果怪兽的效果发动，也不能把卡盖放。这张卡的控制者在每次自己的准备阶段把手卡全部送去墓地。或者让这张卡破坏。','效果选择','是否要把手卡全部送去墓地？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79852326,'死灵佐玛','这张卡发动后当怪兽卡（不死族·暗·4星·攻1800/防500），在自己的怪兽区域守备表示特殊召唤。这张卡被对方战斗破坏时，给与对方破坏这张卡的怪兽的攻击力数值的伤害。这张卡也当作陷阱卡使用。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79853073,'密码侦察者','这张卡和战士族怪兽进行战斗的伤害计算时发动。这张卡的攻击力·守备力只在那次伤害计算时上升2000。','攻守上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79856792,'究极宝玉神 虹龙',replace(replace('这张卡不能通常召唤。自己的场上·墓地有「宝玉兽」卡7种类存在的场合才能特殊召唤。这张卡特殊召唤的回合，这张卡的①②的效果不能发动。\r\n①：把自己场上的表侧表示的「宝玉兽」怪兽全部送去墓地才能发动。这张卡的攻击力上升送去墓地的怪兽数量×1000。这个效果在对方回合也能发动。\r\n②：从自己墓地把「宝玉兽」怪兽全部除外才能发动。场上的卡全部回到持有者卡组。','\r',char(13)),'\n',char(10)),'攻击力上升','场上的卡全部回到持有者卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79861914,'妖仙大旋风',replace(replace('支付800基本分才能把这张卡发动。\r\n①：1回合1次，自己场上的表侧表示的「妖仙兽」怪兽回到手卡的场合，以对方场上1张卡为对象才能把这个效果发动。那张卡回到持有者手卡。\r\n②：自己回合这张卡的①的效果没有适用的场合，自己结束阶段这张卡破坏。','\r',char(13)),'\n',char(10)),'回到手卡','这张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79867938,'燃烧拳击手 护头拳手','这张卡召唤成功时，可以从卡组把1只名字带有「燃烧拳击手」的怪兽送去墓地。场上表侧攻击表示存在的这张卡1回合只有1次不会被战斗破坏。','送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79870141,'猛进的剑角兽','攻击守备表示的怪兽时，这张卡的攻击力超过守备表示怪兽的守备力，对方受到这个超过的数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79875176,'卡通加农炮兵','这张卡召唤·反转召唤·特殊召唤的回合不能攻击。场上的「卡通世界」被破坏时这张卡也破坏。自己场上有「卡通世界」且对方不控制卡通的场合，这张卡可以直接攻击对方玩家。每祭品1只自己场上存在的怪兽，对方受到500分的基本分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79965360,'亚马逊的秘宝','名字带有「亚马逊」的怪兽才能装备。装备怪兽1回合只有1次不会被战斗破坏。装备怪兽攻击的场合，受到那次攻击的怪兽在伤害计算后破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79967395,'娱乐伙伴 鞭子蛇','①：1回合1次，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时交换。这个效果在双方的主要阶段才能发动。','攻守互换','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79968632,'六武众的影忍术',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：把自己场上1只怪兽送去墓地，以除外的1只自己的「六武众」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79972330,'幻蝶刺客 凤蝶','这张卡以外的自己场上的怪兽给与对方基本分战斗伤害时，选择对方场上表侧表示存在的1只怪兽发动。选择的怪兽的攻击力下降那个时候给与的伤害的数值。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79979666,'元素英雄 水泡侠',replace(replace('①：手卡只有这1张卡的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡召唤·反转召唤·特殊召唤成功时才能发动。自己从卡组抽2张。这个效果在自己的手卡·场上没有其他卡的场合才能发动和处理。','\r',char(13)),'\n',char(10)),'特殊召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79985120,'急袭猛禽-革命猎鹰-大空袭',replace(replace('鸟兽族6星怪兽×3\r\n这张卡也能把手卡1张「升阶魔法」魔法卡丢弃，在自己场上的5阶以下的「急袭猛禽」超量怪兽上面重叠来超量召唤。\r\n①：这张卡超量召唤成功的场合，以对方场上1只怪兽为对象才能发动。那只怪兽破坏，给与对方那个攻击力数值的伤害。\r\n②：这张卡被对方破坏送去墓地的场合才能发动。从额外卡组把1只「急袭猛禽-革命猎鹰」特殊召唤，把这张卡在下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'是否在5阶以下的「急袭猛禽」超量怪兽上面重叠来超量召唤？','对方怪兽破坏','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(79997591,'二重巴塞舞姿','①：对方怪兽向自己场上的表侧攻击表示怪兽攻击宣言时才能发动。给与对方为攻击对象怪兽的攻击力数值的伤害，那只对方怪兽的攻击变成向自己的直接攻击。那只自己怪兽在下次的自己回合可以直接攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80019195,'传说的骑士 蒂迈欧',replace(replace('这张卡不能通常召唤，「传说之心」的效果才能特殊召唤。\r\n①：这张卡特殊召唤成功时，以场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张表侧表示的魔法·陷阱卡除外。\r\n②：这张卡被选择作为攻击对象时，以自己墓地1张魔法卡为对象才能发动。那张卡在自己场上盖放。','\r',char(13)),'\n',char(10)),'除外','盖放','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80032567,'朱罗纪巨兽龙',replace(replace('调整＋调整以外的恐龙族怪兽1只以上\r\n只要这张卡在场上表侧表示存在，自己场上表侧表示存在的名字带有「朱罗纪」的怪兽的攻击力上升自己墓地存在的名字带有「朱罗纪」的怪兽数量×200的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80033124,'电子暗黑冲击！','①：从自己的手卡·场上·墓地让「电子暗黑魔角」「电子暗黑翼刃」「电子暗黑龙骨」各1张回到持有者卡组，把1只「铠黑龙-电子暗黑龙」从额外卡组融合召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80036543,'对活路的希望','①：自己基本分比对方少1000以上的场合，支付1000基本分才能发动。双方基本分差每有2000，自己从卡组抽1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80045583,'古代的机械炮台','把这张卡作祭品。给与对方基本分500分的伤害，这个回合的战斗阶段中双方不能发动陷阱卡。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80071763,'魔人 暗黑护持师',replace(replace('「凭依的血魂」＋「边境的大贤者」\r\n融合召唤这只怪兽，必须用上面所写的卡融合召唤。通常魔法发动时支付1000分，那个通常魔法的效果无效化。这张卡战斗破坏的效果怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80075749,'水泡幻像','「元素英雄 水泡侠」在自己场上表侧表示存在时才能发动。这个回合，自己可以从手卡发动1张陷阱卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80102359,'强化人类 赛克','把自己墓地存在的1只念动力族怪兽从游戏中除外发动。这张卡的攻击力上升500。这个效果1回合可以使用最多2次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80108118,'X-剑士 乌尔贝鲁姆',replace(replace('调整＋调整以外的怪兽1只以上\r\n对方手卡有4张以上的场合，这张卡给与对方基本分战斗伤害时，对方手卡随机1张回到卡组最上面。','\r',char(13)),'\n',char(10)),'返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80117527,'No.11 巨眼',replace(replace('7星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上1只怪兽才能发动。得到选择的怪兽的控制权。这个效果发动的回合，这张卡不能攻击。','\r',char(13)),'\n',char(10)),'得到控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80141480,'猎手蜘蛛','布置蜘蛛网狩猎，落入网中的东西将被吃掉。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80143954,'幻变骚灵伪装',replace(replace('①：以自己场上1只「幻变骚灵」怪兽为对象才能把这张卡发动。这张卡当作装备卡使用给那只怪兽装备。装备怪兽不会被作为对方怪兽的攻击对象。（自己场上只有被这个效果适用的怪兽存在的状态中对方的攻击变成对自己的直接攻击。）\r\n②：装备怪兽为对象发动的对方怪兽的效果无效化。\r\n③：自己场上的「幻变骚灵」卡被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'是否使用「幻变骚灵伪装」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80159717,'龙骑兵团骑士-三叉龙骑士',replace(replace('龙族调整＋调整以外的鸟兽族怪兽1只以上\r\n把自己场上存在的最多3张卡送去墓地发动。把对方的额外卡组确认，选择和为这个效果发动而送去墓地的数量相同数量的卡送去墓地。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'确认额外卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80161395,'神秘的中华锅','祭掉自己场上1只怪兽。选择被祭掉的怪兽攻击力或守备力的其中1项数值，回复与此数值等同的基本分。','请选择要回复的数值','怪兽的攻击力','怪兽的守备力','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80163754,'爆裂龙息','①：把自己场上1只龙族怪兽解放才能发动。持有解放的怪兽的攻击力以下的守备力的场上的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80168720,'暗之拜访','丢弃2张手卡。选择表侧表示的1只怪兽，变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80186010,'嗜杀恐龙','把这张卡从手卡丢弃去墓地。从卡组把1张「侏罗纪世界」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80190753,'樱姬 塔利亚','这张卡的攻击力上升自己场上的植物族怪兽数量×100的数值。只要这张卡在场上表侧表示存在，这张卡以外的双方场上的植物族怪兽不会被卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80193355,'救出剧','以场上存在的名称中含有「亚马逊」字样的怪兽为对象的卡发动时这张卡才能发动。将成为对象的怪兽卡弹回持有者手卡，从自己手卡中特殊召唤另1只怪兽上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80193356,'救出剧','以场上存在的名称中含有「亚马逊」字样的怪兽为对象的卡发动时这张卡才能发动。将成为对象的怪兽卡弹回持有者手卡，从自己手卡中特殊召唤另1只怪兽上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80204957,'借机巧藏','选择自己场上表侧表示存在的1只名字带有「机巧」的怪兽发动。从自己卡组把1只4星以下的名字带有「机巧」的怪兽加入手卡，选择的怪兽的表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80208158,'异次元超能人·星斗罗宾','只要这张卡在场上表侧表示存在，对方不能把其他的自己怪兽作为卡的效果的对象，也不能作为攻击对象。此外，这张卡在墓地存在的场合，对方怪兽的直接攻击宣言时才能发动。这张卡从墓地表侧守备表示特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合从游戏中除外。「异次元超能人·星斗罗宾」在场上只能有1只表侧表示存在。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80208323,'异界的棘紫兽','这张卡在墓地存在，自己场上的怪兽被战斗破坏送去墓地时，这张卡可以从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合从游戏中除外。「异界的棘紫兽」的效果1回合只能发动1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80230510,'被埋葬的牲祭','①：这个回合，自己作需要怪兽2只解放的上级召唤的场合只有1次，可以不把怪兽2只解放而从自己·对方的墓地把怪兽各1只除外来上级召唤。这个效果的发动后，直到回合结束时自己不能把怪兽特殊召唤。','把墓地怪兽除外上级召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80233946,'筮龟','只要这张卡在场上表侧表示存在，攻击力1900以上的怪兽不能进行攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80234301,'棱镜人','晶莹剔透的水晶凝固体。收集光线而射出激光。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80237445,'古遗物-雷神锤',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：这张卡可以当作魔法卡使用从手卡到魔法与陷阱区域盖放。\r\n②：魔法与陷阱区域盖放的这张卡在对方回合被破坏送去墓地的场合发动。这张卡特殊召唤。\r\n③：对方回合，这张卡特殊召唤成功的场合，以自己墓地1只「古遗物」怪兽为对象发动。那只怪兽守备表示特殊召唤。这个效果的发动后，直到下个回合的结束时自己不是「古遗物」怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80244114,'星尘幻影','自己场上存在的这张卡被对方破坏送去墓地时，可以选择自己墓地存在的1只「星尘龙」表侧守备表示特殊召唤。此外，把墓地存在的这张卡从游戏中除外，选择自己场上表侧表示存在的1只龙族的同调怪兽才能发动。选择的怪兽1回合只有1次不会被战斗破坏，这个效果适用的伤害步骤结束时攻击力·守备力下降800。','特殊召唤','战斗破坏耐性','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80250185,'自动手枪弹丸龙',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：场上的这张卡为对象的连接怪兽的效果发动时才能发动。这张卡破坏。那之后，选场上1张魔法·陷阱卡送去墓地。\r\n②：场上的这张卡被战斗·效果破坏送去墓地的回合的结束阶段才能发动。从卡组把「自动手枪弹丸龙」以外的1只「弹丸」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80250319,'小灰篮史莱姆',replace(replace('①：这张卡召唤成功时，以自己墓地1只「灰篮」怪兽为对象才能发动。那只怪兽特殊召唤。那之后，可以把和这个效果特殊召唤的怪兽相同等级的1只水族怪兽从手卡特殊召唤。这个效果的发动后，直到回合结束时自己不是水属性怪兽不能特殊召唤。\r\n②：这张卡被战斗破坏送去墓地时才能发动。从卡组把1只「灰篮」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'从墓地特殊召唤','是否从手卡把水族怪兽特殊召唤？','从卡组特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80280737,'爆裂模式','把自己场上存在的1只同调怪兽解放发动。包含有解放的同调怪兽卡名的1只名字带有「/爆裂体」的怪兽从自己卡组攻击表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80280944,'巨王棘龙',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡不能直接攻击。\r\n②：这张卡被除外的场合才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡的攻击力上升除外的自己的恐龙族怪兽数量×200。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80304126,'魔术女武神','①：只要这张卡在怪兽区域存在，对方不能把其他的魔法师族怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80316585,'鹰身女郎 电子紧身装束','这张卡的卡名在规则上当作「鹰身女郎」使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80321197,'深红剑士',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡战斗破坏对方怪兽送去墓地的场合发动。下次的对方回合，对方不能把5星以上的怪兽召唤·特殊召唤。','\r',char(13)),'\n',char(10)),'召唤限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80335817,'刻剑之魔术师',replace(replace('←2 【灵摆】 2→\r\n①：只要这张卡在灵摆区域存在，1回合1次，自己场上的灵摆怪兽不会被对方的效果破坏。\r\n【怪兽效果】\r\n①：只让手卡的这张卡灵摆召唤成功时才能发动。这张卡的攻击力变成原本攻击力的2倍。\r\n②：1回合1次，以场上1只怪兽为对象才能发动。那只怪兽和场上的这张卡直到下次的自己准备阶段除外。','\r',char(13)),'\n',char(10)),'请选择要回到场上的卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80344569,'新空间侠·大地鼹鼠','①：这张卡和对方怪兽进行战斗的伤害步骤开始时才能发动。那只对方怪兽和这张卡回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80352158,'磁力再生','①：从自己墓地的怪兽以及除外的自己怪兽之中以1只机械族或者岩石族的不能通常召唤的怪兽为对象才能发动。那只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80367387,'核成双刀手','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只战士族怪兽给对方观看。或者都不进行让这张卡破坏。这张卡战斗破坏对方怪兽的场合，只有1次可以继续攻击。','选择一张「核成兽的钢核」送去墓地','选择一只战士族怪物给对方观看','破坏「核成双刀手」','继续攻击','','','','','','','','','','','','');
INSERT INTO texts VALUES(80368942,'茧状体派对','自己墓地存在的名字带有「新空间侠」的怪兽每有1种类，把1只名字带有「茧状体」的怪兽从自己卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80402389,'大树海','场上表侧表示存在的昆虫族怪兽被破坏送去墓地时，那些怪兽的控制者可以把1只和破坏怪兽相同等级的昆虫族怪兽从卡组加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80441106,'凯尔多','这张卡被战斗破坏送去墓地时，从对方墓地里选择2张卡回到对方卡组。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80457744,'推进战士','自己场上有调整表侧表示存在的场合，这张卡可以从手卡表侧守备表示特殊召唤。只要这张卡在场上表侧表示存在，自己场上表侧表示存在的战士族怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80476891,'进化合兽 水蛇龙',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡以外的二重怪兽召唤成功时才能发动。那只怪兽的攻击力·守备力上升500。\r\n●自己场上的二重怪兽被效果破坏的场合，可以作为代替把自己场上1张卡破坏。','\r',char(13)),'\n',char(10)),'攻击力·守备力上升','是否作为代替把自己场上1张卡破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80485722,'吸血鬼杀手','这张卡和暗属性怪兽进行战斗的场合，不进行伤害计算把那只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80495985,'光子剑齿虎','这张卡召唤·反转召唤成功时，可以从卡组把1只「光子剑齿虎」加入手卡。自己场上没有这张卡以外的「光子剑齿虎」存在的场合，这张卡的攻击力下降800。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80513550,'悲剧结局女王龙','这张卡不能通常召唤。自己场上有永续魔法卡3张以上表侧表示存在的场合可以特殊召唤。这张卡的攻击给与对方基本分战斗伤害时，对方选择1张手卡送去墓地，自己从卡组抽1张卡。此外，这张卡从场上送去墓地的场合，自己的准备阶段时，可以把自己场上表侧表示存在的1张永续魔法卡送去墓地，这张卡从墓地特殊召唤。','抽卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80516007,'珍鱼','「融合体」＋「恍惚的人鱼」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80532587,'旧神 努茨',replace(replace('同调怪兽＋超量怪兽\r\n把自己场上的上记卡送去墓地的场合才能特殊召唤（不需要「融合」）。自己对「旧神 努茨」1回合只能有1次特殊召唤。\r\n①：1回合1次，自己主要阶段才能发动。从手卡把1只4星怪兽特殊召唤。\r\n②：这张卡被送去墓地的场合，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80538728,'发条骑士','自己场上表侧表示存在的名字带有「发条」的怪兽被选择作为攻击对象时，可以把那只怪兽的攻击无效。这个效果只在这张卡在场上表侧表示存在能使用1次。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80549379,'龙骑兵团-标枪龙','这张卡在怪兽卡区域上被破坏的场合，可以不送去墓地当作装备魔法卡使用给自己场上表侧表示存在的1只名字带有「龙骑兵团」的鸟兽族怪兽装备。','是否要使用「龙骑兵团-标枪龙」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80551130,'奇迹之光临','①：以除外的1只自己的天使族怪兽为对象才能把这张卡发动。那只怪兽特殊召唤。这张卡从场上离开时那只怪兽破坏。那只怪兽破坏时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80555062,'武神器-道返','①：只要这张卡在怪兽区域守备表示存在，这张卡以外的自己场上的「武神」怪兽不会被效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80555116,'自然守护者','对方对怪兽的召唤成功时，这张卡的攻击力直到结束阶段时上升300。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80559548,'疾行机人 变形回力镖',replace(replace('①：这张卡在召唤的回合不能攻击。\r\n②：1回合1次，以场上1只表侧表示怪兽为对象才能发动。这张卡变成守备表示，作为对象的怪兽的攻击力直到回合结束时下降800。','\r',char(13)),'\n',char(10)),'守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80560728,'次元海市蜃楼',replace(replace('以对方场上1只表侧攻击表示怪兽为对象才能把这张卡发动。\r\n①：作为对象的怪兽的攻击没让攻击对象怪兽被破坏的伤害步骤结束时，把自己墓地1只怪兽除外才能把这个效果发动。作为对象的怪兽变成再1次可以攻击，必须继续攻击。\r\n②：作为对象的怪兽从场上离开的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80566312,'祝祷的圣歌',replace(replace('「龙姬神 萨菲拉」的降临必需。\r\n①：从自己的手卡·场上把等级合计直到6以上的怪兽解放，从手卡把「龙姬神 萨菲拉」仪式召唤。\r\n②：自己场上的仪式怪兽被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'是否要使用「祝祷的圣歌」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80577258,'雪之天气模样',replace(replace('①：「雪之天气模样」在自己场上只能有1张表侧表示存在。\r\n②：和这张卡相同纵列的自己的主要怪兽区域以及那些两邻的自己的主要怪兽区域存在的「天气」效果怪兽得到以下效果。\r\n●把这张卡除外才能发动。从卡组把1张「天气」卡加入手卡。这个效果的发动后，直到回合结束时自己不能用抽卡以外的方法从卡组把卡加入手卡。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'从卡组把1张「天气」卡加入手卡（雪之天气模样）','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80584548,'弩弓部队','①：把自己场上1只怪兽解放，以对方场上1张卡为对象才能发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80600490,'暗影战士','用实体和影子袭击对方。疏忽的话就会受到夹击的哦。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80604091,'血之代偿','可以支付500基本分，把1只怪兽通常召唤。这个效果在自己回合的主要阶段及对方回合的战斗阶段才能发动。','通常召唤','是否使用效果？','表侧表示召唤','里侧表示放置','','','','','','','','','','','','');
INSERT INTO texts VALUES(80604092,'血之代偿','可以支付500基本分，把1只怪兽通常召唤。这个效果在自己回合的主要阶段及对方回合的战斗阶段才能发动。','通常召唤','是否使用效果？','表侧表示召唤','里侧表示放置','','','','','','','','','','','','');
INSERT INTO texts VALUES(80630522,'花札卫-桐-',replace(replace('①：自己场上有11星以下的「花札卫」怪兽存在的场合才能发动。这张卡从手卡特殊召唤。这个效果的发动后，直到回合结束时自己不是「花札卫」怪兽不能召唤·特殊召唤。\r\n②：这张卡被选择作为攻击对象时才能发动。那次攻击无效，战斗阶段结束。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'特殊召唤','攻击无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80637190,'间谍蜘蛛','这张卡战斗破坏对方场上守备表示存在的怪兽的场合，可以选择自己墓地存在的1只4星以下的昆虫族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80651316,'进化龙·角鼻龙','这张卡用名字带有「进化虫」的怪兽的效果特殊召唤成功时，这张卡的攻击力上升200。那之后，这张卡战斗破坏对方怪兽的场合，可以从自己卡组把1只名字带有「进化虫」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80666118,'红莲魔龙·右红痕',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡的卡名只要在场上·墓地存在当作「红莲魔龙」使用。\r\n②：1回合1次，自己主要阶段才能发动。这张卡以外的持有这张卡的攻击力以下的攻击力的特殊召唤的效果怪兽全部破坏。那之后，给与对方这个效果破坏的怪兽数量×500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80678380,'蛇神的敕命','把手卡1张名字带有「蛇毒」的怪兽卡给对方观看发动。对方的魔法卡的发动和效果无效，并把那个破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80696379,'异色眼陨火龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n「异色眼陨火龙」的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功时，以自己的灵摆区域1张卡为对象才能发动。那张卡特殊召唤。这个回合，这张卡不能攻击。\r\n②：只要这张卡在怪兽区域存在，对方在战斗阶段中不能把怪兽的效果发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80701178,'魔神仪-曼德拉护符草',replace(replace('这个卡名的①②的效果1回合只能有1次使用其中任意1个。\r\n①：把手卡1只仪式怪兽给对方观看才能发动。「魔神仪-曼德拉护符草」以外的卡组1只「魔神仪」怪兽和手卡的这张卡特殊召唤。\r\n②：这张卡从卡组的特殊召唤成功的场合才能发动。从卡组把1只仪式怪兽加入手卡。\r\n③：只要这张卡在怪兽区域存在，自己不能从额外卡组把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80723580,'大落穴','同时有2只以上的怪兽特殊召唤成功时才能发动。场上存在的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80727036,'黑色食人鲨','「深海切割手」＋「杀人污泥」＋「海原的女战士」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80727721,'朱罗纪异特龙','把这张卡解放，选择自己墓地存在的「朱罗纪异特龙」以外的1只4星以下的名字带有「朱罗纪」的怪兽发动。选择的怪兽从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80741828,'见习魔女','只要这张卡在场上表侧表示存在，全部暗属性的怪兽攻击力上升500。光属性的怪兽攻击力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80744121,'命运英雄 破灭人','这张卡被战斗破坏送去墓地的场合，下次自己回合的准备阶段时，自己墓地存在的「命运英雄 破灭人」以外的1只名字带有「命运英雄」的怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80764541,'No.44 白天马',replace(replace('4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上表侧表示存在的1只怪兽才能发动。对方可以通过支付1000基本分让这张卡的效果无效。没支付的场合，选择的怪兽破坏。','\r',char(13)),'\n',char(10)),'破坏','是否要支付1000LP无效「No.44 白天马」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80769747,'链·锁缚蛇','自己的主要阶段时可以当作装备卡使用给对方场上存在的1只表侧表示怪兽装备。这张卡装备的怪兽的攻击力·守备力下降800。装备怪兽被战斗破坏送去墓地时，从装备怪兽的控制者的卡组上面把和那只怪兽的等级相同数量的卡送去墓地。','装备','卡组送墓','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80770678,'竖琴精灵','在天界演奏竖琴的精灵，拥有无穷魅力的音色使周围听众的心变得平静。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80773359,'强袭黑羽-神立之鬼丸刀鸟',replace(replace('调整＋调整以外的怪兽1只以上\r\n「强袭黑羽-神立之鬼丸刀鸟」的③的效果在决斗中只能使用1次。\r\n①：「黑羽」怪兽为素材作同调召唤的这张卡当作调整使用。\r\n②：这张卡不会被效果破坏。\r\n③：以自己墓地1只「黑羽」怪兽为对象才能发动。这张卡的等级变成和那只怪兽相同。\r\n④：只用同调怪兽为素材作同调召唤的这张卡攻击的场合，伤害步骤内这张卡的攻击力上升3000。','\r',char(13)),'\n',char(10)),'等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80794697,'帧缓存火牛',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：表侧表示的这张卡从场上离开的场合才能发动。从手卡丢弃1只电子界族怪兽，自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80796456,'No.70 大罪蛛',replace(replace('4星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以对方场上1只怪兽为对象才能发动。那只怪兽直到下次的对方准备阶段除外。\r\n②：这张卡攻击的伤害步骤结束时才能发动。这张卡的攻击力上升300，阶级上升3阶。','\r',char(13)),'\n',char(10)),'对方怪兽除外','这张卡攻击力上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80802524,'鬼计心碎','①：自己场上的「鬼计」怪兽1只被战斗或者对方的效果破坏送去自己墓地时，以和破坏的那只怪兽卡名不同的自己墓地2只「鬼计」怪兽为对象才能发动。那些怪兽里侧守备表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80811661,'汉堡的食谱','「饥饿的汉堡」的降临必需。若不从场地和手卡把等级直到6以上的卡作为祭品，则「饥饿的汉堡」不能降临。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80813021,'犀牛','虽然守备不像外表般高强。但是角的攻击力还是很强。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80825553,'云魔物-小烟球','小小的云魔物的孩子云。非常讨厌一个人孤零零的感觉，喜欢和伙伴们乘着微风晃动着到处散步。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80831721,'贤者之石-萨巴希尔',replace(replace('①：自己墓地有「羽翼栗子球」怪兽存在的场合，把基本分支付一半才能发动。从卡组把1张「融合」魔法卡加入手卡。\r\n②：这张卡在墓地存在的场合，把自己墓地3张「贤者之石-萨巴希尔」除外，以场上1只怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升场上的攻击力最高的怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'加入手卡','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80863132,'无效','抽卡的效果发动时发动。那张卡的效果所抽的卡双方确认，并全部丢弃去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80885284,'鬼计僵尸','自己场上有名字带有「鬼计」的怪兽存在的场合才能让这张卡表侧表示召唤。这张卡1回合只有1次可以变成里侧守备表示。此外，这张卡反转时，可以把持有自己场上的名字带有「鬼计」的怪兽数量以下的等级的1只名字带有「鬼计」的怪兽从卡组加入手卡。「鬼计僵尸」的这个效果1回合只能使用1次。','变成里侧守备','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80885324,'突击搜查官','这张卡向里侧守备表示怪兽攻击的场合，可以不进行伤害计算以里侧守备表示的状态把那只怪兽破坏。这个效果发动的场合，这张卡在战斗阶段结束时变成守备表示，直到下次的自己回合的结束阶段时不能把表示形式改变。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80887714,'银河风暴','选择场上存在的1只没有超量素材的超量怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80887952,'双子太阳 赫利俄斯','这张卡可以用自己场上的1只「原始太阳 赫利俄斯」作为祭品特殊召唤。这张卡的攻击力·守备力为从游戏中除外的怪兽卡数量×200的数值。这张卡被战斗破坏送去墓地的场合，结束阶段时攻击力·守备力上升300并特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80889750,'魔玩具·军刀剑齿虎',replace(replace('「魔玩具」融合怪兽＋「毛绒动物」怪兽或者「锋利小鬼」怪兽1只以上\r\n①：这张卡融合召唤成功时，以自己墓地1只「魔玩具」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：只要这张卡在怪兽区域存在，自己场上的「魔玩具」怪兽的攻击力上升400。\r\n③：怪兽3只以上为素材作融合召唤的这张卡不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'不会被战斗·效果破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80896940,'涅槃之超魔导剑士',replace(replace('←8 【灵摆】 8→\r\n①：自己的灵摆怪兽攻击的场合，那只怪兽不会被那次战斗破坏，那次战斗发生的对自己的战斗伤害变成0。\r\n②：自己的灵摆怪兽攻击的伤害步骤结束时发动。对方场上的全部怪兽的攻击力直到回合结束时下降攻击的那只怪兽的攻击力数值。\r\n【怪兽效果】\r\n调整＋调整以外的同调怪兽1只以上\r\n这张卡同调召唤的场合，可以用自己场上1只灵摆召唤的灵摆怪兽当作调整使用。\r\n①：这张卡用灵摆召唤的灵摆怪兽为调整作同调召唤成功的场合，以自己墓地1张卡为对象才能发动。那张卡加入手卡。\r\n②：这张卡战斗破坏对方怪兽时才能发动。对方基本分变成一半。\r\n③：怪兽区域的这张卡被战斗·效果破坏的场合才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'同调召唤','使用灵摆召唤的灵摆怪兽当作调整同调召唤','攻击力下降','加入手卡','基本分变成一半','这张卡在灵摆区域放置','','','','','','','','','','');
INSERT INTO texts VALUES(80908502,'元素英雄 黄金队长','把这张卡从手卡丢弃去墓地。从卡组把1张「摩天楼」加入手卡。场上没有「摩天楼」存在的场合，场上的这张卡破坏。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80921533,'死皇帝之陵墓',replace(replace('①：双方玩家在自己主要阶段可以从以下效果选择1个发动。\r\n●支付1000基本分才能发动。不用解放来进行需要1只解放的手卡1只怪兽的通常召唤。\r\n●支付2000基本分才能发动。不用解放来进行需要2只解放的手卡1只怪兽的通常召唤。','\r',char(13)),'\n',char(10)),'通常召唤','表侧表示召唤','里侧表示放置','请选择要支付的基本分','','','','','','','','','','','','');
INSERT INTO texts VALUES(80925836,'核成恶魔','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只恶魔族怪兽给对方观看。或者都不进行让这张卡破坏。只要这张卡在自己场上表侧表示存在，主要阶段时发动的光属性以及暗属性的效果怪兽的效果无效化。','选择一张「核成兽的钢核」送去墓地','选择一只恶魔族怪物给对方观看','破坏「核成恶魔」','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80955168,'王墓的陷阱','对方墓地存在的不死族怪兽在自己场上特殊召唤时才能发动。场上2张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80959027,'魔导兽 巴西利斯克冠蜥',replace(replace('←4 【灵摆】 4→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：另一边的自己的灵摆区域没有卡存在的场合才能发动。这张卡破坏，从自己的额外卡组让「魔导兽 巴西利斯克冠蜥」以外的1只表侧表示的魔法师族灵摆怪兽回到卡组。那之后，自己从卡组抽1张。\r\n【怪兽效果】\r\n这个卡名的②的怪兽效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。\r\n②：把自己场上3个魔力指示物取除才能发动。从自己的灵摆区域的卡以及自己的额外卡组的表侧表示的灵摆怪兽之中选1张「魔导兽」卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80978111,'飞翔的G','对方对怪兽的召唤·特殊召唤成功时，这张卡可以从手卡往对方场上表侧守备表示特殊召唤。只要这张卡在场上表侧表示存在，这张卡的控制者不能超量召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(80987696,'时间机械','①：自己或者对方的怪兽1只被战斗破坏送去墓地时才能发动。在那只怪兽被破坏时的控制者场上以相同表示形式把那只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81000306,'浑身一击','选择自己场上表侧表示存在的1只怪兽发动。这个回合，选择的怪兽不会被战斗破坏，那次攻击发生的对双方的战斗伤害变成0。此外，这个回合，选择的怪兽向对方怪兽攻击的场合，伤害计算后那只对方怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81003500,'元素英雄 死灵萨满',replace(replace('「元素英雄 荒野侠」＋「元素英雄 死灵暗侠」\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：这张卡特殊召唤成功的场合，以对方场上1只怪兽为对象发动。那只对方怪兽破坏。那之后，从对方墓地选1只怪兽在对方场上特殊召唤。','\r',char(13)),'\n',char(10)),'破坏并特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81020140,'火山转轮','这张卡被战斗破坏送去墓地时，可以让卡组的1只名字带有「火山」的怪兽在卡组最上面安置。','检索','请选择要在卡组最上方放置的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81020646,'炼狱龙 食人魔龙鬼',replace(replace('暗属性调整＋调整以外的怪兽1只以上\r\n自己手卡是0张的场合，1回合1次，对方把魔法·陷阱卡发动时才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81028112,'电气念力斗士',replace(replace('「电气念力斗士」的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功时，以对方墓地1张卡为对象才能发动。那张卡回到对方卡组最上面或者最下面。','\r',char(13)),'\n',char(10)),'墓地干扰','卡组最上面','卡组最下面','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81035362,'魔犀族战士',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，「魔犀族战士」以外的自己场上的恶魔族怪兽不会被战斗·效果破坏。\r\n②：这张卡被送去墓地的场合才能发动。从卡组把「魔犀族战士」以外的1只恶魔族怪兽送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81055000,'娱乐伙伴 返回狸',replace(replace('←3 【灵摆】 3→\r\n「娱乐伙伴 返回狸」的灵摆效果1回合只能使用1次。\r\n①：以自己场上1张「娱乐伙伴」卡为对象才能发动。那张卡回到持有者手卡。这个回合，自己不能作这个效果回到手卡的卡以及那些同名卡的发动。\r\n【怪兽效果】\r\n①：这张卡被战斗破坏时，以场上1张卡为对象才能发动。那张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81057455,'坏兽捕获大作战',replace(replace('「坏兽捕获大作战」的②的效果1回合只能使用1次。\r\n①：1回合1次，以场上1只「坏兽」怪兽为对象才能发动。那只怪兽变成里侧守备表示。那之后，给这张卡放置1个坏兽指示物（最多3个）。\r\n②：这张卡被对方的效果破坏送去墓地的场合才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81057959,'龙人','挥动用龙牙打造的剑、拥有龙之力量的战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81059524,'死亡魔导龙',replace(replace('①：这张卡战斗破坏对方怪兽送去墓地的场合发动。给与对方500伤害。\r\n②：只要这张卡在怪兽区域存在，每次自己或者对方把通常·速攻魔法卡发动，直到回合结束时这张卡的攻击力上升各200。','\r',char(13)),'\n',char(10)),'LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81066751,'神罚','①：场上有「天空的圣域」存在，怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81105204,'黑羽-残夜之波刃剑鸟',replace(replace('「黑羽-残夜之波刃剑鸟」的①的方法的特殊召唤1回合只能有1次。\r\n①：自己场上有「黑羽-残夜之波刃剑鸟」以外的「黑羽」怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡1回合只有1次不会被魔法·陷阱卡的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81109178,'爆竹鬼',replace(replace('「爆竹鬼」的①的效果1回合只能使用1次。\r\n①：把这张卡从手卡丢弃才能发动。给与对方1000伤害，下次的自己抽卡阶段跳过。这个效果在对方回合也能发动。\r\n②：只要这张卡在怪兽区域存在，每次对方受到效果伤害给这张卡放置1个指示物。\r\n③：自己·对方的结束阶段发动。这张卡的指示物全部取除，给与对方那个数量×300伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81122844,'发条空母 发条巨舰',replace(replace('3星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。从手卡·卡组把1只「发条」怪兽特殊召唤。\r\n②：场上的表侧表示的「发条」怪兽被战斗以外破坏送去自己墓地时，把这张卡1个超量素材取除，以那1只「发条」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','返回手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81128478,'蛛穴丝网','把自己墓地存在的1只昆虫族怪兽从游戏中除外，选择对方场上存在的1只怪兽发动。选择的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81146288,'大凛魔天使 蔷薇仙','这张卡不能通常召唤。从自己的手卡和墓地把7星以上的植物族怪兽各1只从游戏中除外的场合可以特殊召唤。1回合1次，自己的主要阶段时才能发动。这张卡以外的场上表侧表示存在的卡的效果直到结束阶段时无效。','效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81167171,'英雄精神','自己场上的名字带有「元素英雄」的怪兽被战斗破坏的场合，那个回合的战斗阶段才能发动。1只对方怪兽造成的战斗伤害为0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81171949,'头奖壶7','这张卡回到卡组洗切。此外，这张卡被对方的卡的效果送去墓地时，这张卡从游戏中除外。这个效果从游戏中除外的自己的「头奖壶7」3张齐集时，自己决斗胜利。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81172176,'恶魔喜剧演员','①：进行1次投掷硬币，对硬币的里表作猜测。猜中的场合，对方墓地的卡全部除外。猜错的场合，对方墓地的卡数量的卡从自己卡组上面送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81172177,'恶魔喜剧演员','①：进行1次投掷硬币，对硬币的里表作猜测。猜中的场合，对方墓地的卡全部除外。猜错的场合，对方墓地的卡数量的卡从自己卡组上面送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81179446,'青虫','吐丝攻击。不知道以后会长成什么样子的虫。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81191584,'噩梦再临','①：以自己墓地2只守备力0的暗属性怪兽为对象才能发动。那些暗属性怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81197327,'元素英雄 蒸汽医治者',replace(replace('「元素英雄 爆热女郎」＋「元素英雄 水泡侠」\r\n这张卡只能融合召唤不能特殊召唤。这张卡战斗破坏怪兽送去墓地时，自己基本分回复破坏怪兽的攻击力的数值。','\r',char(13)),'\n',char(10)),'LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81210420,'魔术礼帽','①：对方战斗阶段才能发动。从卡组选2张魔法·陷阱卡，那2张卡当作通常怪兽卡（攻/守0）使用，和自己的主要怪兽区域1只怪兽混合洗切并里侧守备表示盖放。这个效果从卡组特殊召唤的卡不在战斗阶段内不能存在，战斗阶段结束时破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81218874,'侵略的波纹','支付500基本分，选择自己墓地存在的1只4星以下的名字带有「侵入魔鬼」的怪兽发动。选择的怪兽从墓地特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81231742,'破邪之魔法壁','①：自己场上的怪兽在自己回合内攻击力上升300，对方回合内守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81254059,'异虫女王','这张卡可以把1只名字带有「异虫」的爬虫族怪兽解放表侧攻击表示上级召唤。1回合1次，可以把自己场上存在的1只名字带有「异虫」的爬虫族怪兽解放，解放怪兽的等级以下的1只名字带有「异虫」的爬虫族怪兽从自己卡组特殊召唤。','用1只怪兽解放上级召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81269231,'古代的机械合成龙',replace(replace('①：和把「古代的机械」怪兽解放作上级召唤的这张卡的战斗没让对方怪兽被破坏的伤害步骤结束时才能发动。那只对方怪兽除外。\r\n②：把「零件」怪兽解放作上级召唤的这张卡可以向对方怪兽全部各作1次攻击。\r\n③：自己的「古代的机械」怪兽攻击的场合，对方直到伤害步骤结束时怪兽的效果·魔法·陷阱卡不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81275020,'疾行机人 贝陀螺集合体',replace(replace('「疾行机人 贝陀螺集合体」的②的效果1回合只能使用1次。\r\n①：自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡召唤·特殊召唤成功时才能发动。从卡组把「疾行机人 贝陀螺集合体」以外的1只「疾行机人」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81275309,'冰结界的虎将 莱蓬','只要这张卡在场上表侧表示存在，场上发动的对方怪兽的效果处理时对方可以丢弃1张手卡。没丢弃的场合，那只效果怪兽的效果无效化。','是否要丢弃一张手牌？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81278754,'里青蛙','这张卡1回合只有1次可以变成里侧守备表示。这张卡反转时，可以让最多有自己场上表侧表示存在的名字带有「青蛙」的怪兽数量的对方场上存在的怪兽回到持有者手卡。','变成里侧守备','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81306186,'世界再生',replace(replace('这个卡名的①②的效果1回合只能有1次使用其中任意1个。\r\n①：作为这张卡的发动时的效果处理，从卡组把1只仪式怪兽除外。\r\n②：可以把这张卡送去墓地，从以下效果选择1个发动。\r\n●直到变成仪式召唤的怪兽的等级以上为止，把自己的手卡·场上1只怪兽解放或者作为解放的代替而让自己墓地1只仪式怪兽回到卡组，把这张卡的①的效果除外的怪兽仪式召唤。\r\n●这张卡的效果除外的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'除外的怪兽仪式召唤','除外的怪兽加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81306586,'梦魇企鹅','这张卡反转时，对方场上1张卡回持有者手卡。只要这张卡在场上表侧表示存在，自己场上的水属性怪兽攻击力上升200。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81325903,'亚马逊诅咒师','到回合终了时为止，自己场上1只表侧表示的名称中含有「亚马逊」字样的怪兽与对方场上1只表侧表示的怪兽的原始攻击力相互交换。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81330115,'No.30 破灭之酸液石人',replace(replace('3星怪兽×2\r\n自己的准备阶段时，把这张卡1个超量素材取除或自己受到2000分伤害。这张卡的超量素材没有的场合，这张卡不能攻击。只要这张卡在场上存在，自己不能把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'去除素材','是否要去除一个超量素材？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81332143,'友情 YU-JYO','①：向对方玩家提出握手。对方答应握手的场合，双方基本分变成双方基本分合计数值的一半。自己手卡有「团结 UNITY」的场合，可以把那张卡给对方观看。那个场合，双方握手，这张卡的效果适用。','同意握手','拒绝握手','是否要把「团结 UNITY」给对方观看？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81336148,'剑圣','自己场上存在的战士族怪兽的攻击没让对方怪兽破坏的伤害步骤结束时，这张卡可以从手卡特殊召唤。此外，这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81354330,'朱雀召唤士','①：这张卡被对方怪兽的攻击破坏送去墓地时才能发动。从卡组把1只攻击力1500以下的战士族怪兽攻击表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81380218,'圣域歌声','全部守备表示的怪兽守备力上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81383947,'白魔导士 绒儿','在自己的准备阶段时，回复数值与自己场上存在的怪兽数量×400点等同的基本分。','回复LP','','','','','','','','','','','','','','','');
