INSERT INTO texts VALUES(81386177,'神鱼','在水中幽雅游动的鱼神，若它发怒会很危险。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(81426505,'六武式三段冲',replace(replace('自己场上有名字带有「六武众」的怪兽表侧表示3只以上存在的场合，可以从以下效果选择1个发动。\r\n●对方场上表侧表示存在的怪兽全部破坏。\r\n●对方场上表侧表示存在的魔法·陷阱卡全部破坏。\r\n●对方场上盖放的魔法·陷阱卡全部破坏。','\r',char(13)),'\n',char(10)),'对方表侧表示怪兽全部破坏','对方表侧表示魔法·陷阱全部破坏','对方盖放的魔法·陷阱全部破坏','选择一个效果发动','','','','','','','','','','','','');
