INSERT INTO texts VALUES(81788994,'影牢之咒缚',replace(replace('①：只要这张卡在场地区域存在，每次「影依」怪兽被效果送去墓地，每有1只给这张卡放置1个魔石指示物。\r\n②：对方回合中，对方场上的怪兽的攻击力下降这张卡的魔石指示物数量×100。\r\n③：只要这张卡在场地区域存在，每次自己把「影依」融合怪兽融合召唤有1次，可以把这张卡3个魔石指示物取除，把对方场上1只表侧表示怪兽作为融合素材。','\r',char(13)),'\n',char(10)),'是否要把对方场上1只表侧表示怪兽作为融合素材？','','','','','','','','','','','','','','','');
