INSERT INTO texts VALUES(81866673,'命运英雄 冲刺人','把自己场上的1只怪兽作为祭品，直到这个回合的结束阶段时这张卡的攻击力上升1000。这个效果1回合只能使用1次。这张卡攻击的场合，战斗阶段结束时变成守备表示。这张卡在墓地存在的场合，只有1次可以把抽卡阶段时抽到的怪兽卡给双方确认并特殊召唤。','攻击上升','特殊召唤','','','','','','','','','','','','','','');
