INSERT INTO texts VALUES(83991690,'天猪福','自己的主要阶段时，把墓地存在的这张卡从游戏中除外，选择自己场上表侧表示存在的1只4星以下的天使族·光属性怪兽才能发动。选择的怪兽只要在场上表侧表示存在当作调整使用。','当成调整使用','','','','','','','','','','','','','','','');
