INSERT INTO texts VALUES(84136000,'复活的墓穴','自己场上存在的怪兽被战斗破坏送去墓地时发动。自己和对方选择各自的墓地1只怪兽，守备表示在场上特殊召唤。这个效果特殊召唤的怪兽只要在场上表侧表示存在，表示形式不能改变。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84171830,'真帝王领域',replace(replace('①：自己的额外卡组没有卡存在，只有自己场上才有上级召唤的怪兽存在的场合，对方不能从额外卡组把怪兽特殊召唤。\r\n②：自己的上级召唤的怪兽的攻击力只在向对方怪兽攻击的伤害计算时上升800。\r\n③：1回合1次，自己主要阶段才能把这个效果发动。选自己手卡1只攻击力2800/守备力1000的怪兽，那只怪兽的等级直到回合结束时下降2星。','\r',char(13)),'\n',char(10)),'等级下降','选择要把等级下降的怪兽','','','','','','','','','','','','','','');
