INSERT INTO texts VALUES(84224627,'猫鲨',replace(replace('2星怪兽×2\r\n①：这张卡持有水属性怪兽作为超量素材的场合，这张卡不会被战斗破坏。\r\n②：1回合1次，把这张卡1个超量素材取除，以自己场上1只4阶以下的超量怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时变成原本数值的2倍。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84243274,'VWXYZ-神龙强击炮',replace(replace('「VW-强击虎」＋「XYZ-神龙炮」\r\n把自己场上的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。\r\n①：1回合1次，以对方场上1张卡为对象才能发动。那张对方的卡除外。\r\n②：这张卡向对方怪兽攻击宣言时，以那1只攻击对象怪兽为对象才能发动。那只攻击对象怪兽的表示形式变更。这个时候，反转怪兽的效果不发动。','\r',char(13)),'\n',char(10)),'除外','改变表示形式','','','','','','','','','','','','','','');
