INSERT INTO texts VALUES(84341431,'恶魔巨神',replace(replace('①：怪兽区域的这张卡被效果破坏的场合，可以作为代替而支付500基本分。这个效果只在这张卡在场上表侧表示存在能使用1次。\r\n②：这张卡被效果破坏送去墓地的场合才能发动。从手卡把1只「恶魔」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'是否要使用「恶魔巨人」的效果代替破坏？','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84361420,'金刃锤击','把自己场上存在的1只「元素英雄 金刃侠」作为祭品。对方场上存在的1只怪兽破坏，给与对方基本分那只怪兽的原本攻击力数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84385264,'合成狼人','用「合成魔术」降临。这张卡给与对方基本分战斗伤害时，给与对方基本分自己墓地存在的通常怪兽数量×200的数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84388461,'剑圣之影灵衣-神数剑士',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「影灵衣」怪兽以及「神数」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n「剑圣之影灵衣-神数剑士」的怪兽效果1回合只能使用1次。\r\n①：把自己的手卡·场上的这张卡解放才能发动。等级合计直到变成和仪式召唤的怪兽相同为止，把自己的手卡·场上的怪兽解放，从手卡把1只「影灵衣」仪式怪兽仪式召唤。','\r',char(13)),'\n',char(10)),'放置灵摆刻度','仪式召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84389640,'穷鼠的进击','自己场上存在的3星以下的通常怪兽进行战斗的场合，那个伤害步骤时支付100的倍数的基本分才能发动。直到这个回合的结束阶段时，进行战斗的1只对方怪兽的攻击力下降支付的数值。','攻击下降','请选择要支付的基本分','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84397023,'等级变换实验室','选择自己手卡1张怪兽卡给对方看，投掷1次骰子。出现的数目是1的场合，选择的怪兽送去墓地。出现的数目是2至6的场合，这个回合的结束阶段前，这只怪兽的等级变为投掷的数目。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84401683,'蛇头守护者',replace(replace('岩石族3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上表侧表示存在的1只怪兽才能发动。直到回合结束时，选择的怪兽的攻击力变成0，那个效果无效。这个效果在对方回合也能发动。此外，1回合1次，选择场上1只攻击力是0的怪兽才能发动。选择的怪兽破坏。','\r',char(13)),'\n',char(10)),'攻击变成0','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84417082,'No.91 雷闪龙',replace(replace('4星怪兽×3\r\n1回合1次，可以从以下效果选择1个发动。\r\n●通过把这张卡3个超量素材取除，这张卡以外的场上表侧表示存在的怪兽全部破坏。\r\n●通过把这张卡5个超量素材取除，对方场上的卡全部破坏。','\r',char(13)),'\n',char(10)),'这张卡以外的场上表侧表示存在的怪兽全部破坏','对方场上的卡全部破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84428023,'电气存储枪','3星以下的雷族怪兽才能装备。装备怪兽的攻击力上升800，效果无效化。装备怪兽给与对方基本分战斗伤害时，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84430950,'重装武者-弁庆','每次的战斗阶段中，这张卡在通常攻击之上追加、这张卡装备的装备卡数量的攻击次数。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84433295,'五阵魔术师',replace(replace('魔法师族怪兽×5\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：魔法师族怪兽5种类作为素材让这张卡融合召唤成功的场合才能发动。对方场上的卡全部破坏。\r\n②：这张卡只要在怪兽区域存在，不能解放，不能作为融合素材，不会被效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84442536,'灰篮战斗',replace(replace('①：只以自己场上的「灰篮」怪兽1只为对象的怪兽的效果·魔法·陷阱卡发动时，可以从以下效果选择1个发动。\r\n●那个效果变成「作为对象的1只怪兽破坏」。\r\n●那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'那个效果变成「作为对象的1只怪兽破坏」','那个发动无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84451804,'死亡青蛙','这张卡祭品召唤成功时，可以从手卡·卡组特殊召唤最多和自己墓地「恶魂邪苦止」同样数量的「死亡青蛙」上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84453939,'念力王牌机师','①：怪兽区域的这张卡被解放的场合，以场上1张卡为对象才能发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84472026,'鬼计雪人',replace(replace('自己场上有「鬼计」怪兽存在的场合才能让这张卡表侧表示召唤。\r\n①：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n②：这张卡反转时，以场上1只「鬼计」怪兽为对象才能发动。那只怪兽在这个回合不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'变成里侧守备','破坏耐性','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84478195,'深渊之结界像','只要这张卡在场上表侧表示存在，不能作暗属性怪兽以外的特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84488827,'侵入魔鬼·巨角',replace(replace('把2只名字带有「侵入魔鬼」的怪兽解放对这张卡的上级召唤成功时，从以下效果选择1个发动。\r\n●这张卡以外的场上存在的怪兽全部破坏。\r\n●场上存在的魔法·陷阱卡全部破坏。','\r',char(13)),'\n',char(10)),'选择1个效果发动','这张卡以外的场上存在的怪兽全部破坏。','场上存在的魔法·陷阱卡全部破坏。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84491298,'细胞爆破病毒','放置有A指示物的对方怪兽的攻击宣言时才能发动。对方场上存在的攻击表示怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84530620,'雷电爸爸','1回合1次，自己的主要阶段时才能发动。从手卡把「雷电爸爸」以外的1只雷族·光属性·4星的怪兽召唤。','召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84536654,'形态变化','①：以自己场上1只「英雄」融合怪兽为对象才能发动。那只怪兽回到额外卡组，和那只怪兽的原本等级相同等级而卡名不同的1只「假面英雄」怪兽当作「假面变化」的特殊召唤从额外卡组特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84539520,'亚马逊王女',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡的卡名只要在场上·墓地存在当作「亚马逊女王」使用。\r\n②：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1张「亚马逊」魔法·陷阱卡加入手卡。\r\n③：这张卡的攻击宣言时把这张卡以外的自己的手卡·场上1张卡送去墓地才能发动。从卡组把「亚马逊王女」以外的1只「亚马逊」怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84550200,'声波干扰虫','反转：到下一个结束阶段终了时为止，对方不能发动魔法卡。','发动限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84565800,'传说的骑士 赫谟',replace(replace('这张卡不能通常召唤。「传说之心」的效果才能特殊召唤。\r\n①：这张卡特殊召唤成功时，以场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张表侧表示的魔法·陷阱卡除外。\r\n②：1回合1次，这张卡被选择作为攻击对象时，以自己墓地1只效果怪兽为对象才能发动。这张卡直到下次的自己回合的结束阶段当作和那只墓地的怪兽同名卡使用，得到相同效果。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84569017,'旗鱼战士','这张卡的攻击力上升从游戏中除外的自己的鱼族·海龙族·水族怪兽数量×100的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84569886,'DDD 超死伟王 紫地狱终末神',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，自己的「DDD」融合怪兽和对方怪兽进行战斗的伤害计算前才能发动。那只对方怪兽的攻击力直到回合结束时下降1000。\r\n【怪兽效果】\r\n「DDD」怪兽×2\r\n这个卡名的①的怪兽效果1回合只能使用1次。\r\n①：以对方场上1只攻击表示怪兽为对象才能发动。那只怪兽破坏，给与对方破坏的怪兽的原本攻击力一半数值的伤害。\r\n②：对方怪兽进行战斗的伤害计算前才能发动。那只对方怪兽的攻击力直到伤害步骤结束时变成原本数值。\r\n③：怪兽区域的这张卡被破坏的场合才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84592800,'变形斗士·摄像机',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：这张卡的攻击力上升这张卡装备的装备卡数量×800的数值。\r\n●守备表示：这张卡的守备力上升这张卡装备的装备卡数量×800的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84613836,'神速之具足','自己的抽卡阶段时抽到的卡是名字带有「六武众」的怪兽卡的场合，可以把那张卡给对方观看并在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84620194,'断头台锹甲','栖息在精灵之森的甲虫，虽不过人的大拇指一般大，但却有着连钢铁都能咬断的下颚。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84636823,'魔法抵消器','只要这张卡在场上表侧表示存在，魔法卡不能发动，场上所有魔法卡的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84640866,'电网的落穴','①：对方从卡组·墓地把怪兽特殊召唤时才能发动。那些怪兽里侧表示除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84650463,'冰沙怪','这张卡召唤成功时，可以从卡组把1只「冰沙怪」送去墓地。此外，自己墓地的「冰沙怪」以及从游戏中除外的自己的「冰沙怪」的合计是3只的场合，把墓地的这张卡从游戏中除外才能发动。选择自己墓地1只海龙族·5星以上的怪兽特殊召唤。「冰沙怪」的这个效果1回合只能使用1次。','送墓','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84653834,'超能力増幅器','这张卡的发动时，自己场上表侧表示存在的全部念动力族怪兽的攻击力上升从游戏中除外的自己的念动力族怪兽数量×300的数值。受到这个效果影响的怪兽在结束阶段时从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84673417,'暮光道将军 简',replace(replace('①：1回合1次，从自己的手卡·墓地把1只「光道」怪兽除外，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时下降除外的怪兽的等级×300。\r\n②：1回合1次，这张卡以外的自己的「光道」怪兽的效果发动的场合发动。从自己卡组上面把2张卡送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84677654,'大金星！？','宣言从1到12的任意等级才能发动。双方玩家各自进行1次投掷硬币，都是表出现的场合，自己场上表侧表示存在的全部怪兽的等级变成宣言的等级。都是里出现的场合，自己失去宣言的等级数值×500基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84686841,'烟之王','潜伏在烟雾中的恶魔，四周被浓烟包围，毫无踪迹可循。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84687358,'反射镜力龙',replace(replace('这张卡在用「克里底亚之牙」的效果把自己的手卡·场上的「神圣防护罩 -反射镜力-」送去墓地的场合才能特殊召唤。\r\n①：自己场上的怪兽被选择作为攻击对象时或者成为对方的效果的对象时才能发动。对方场上的卡全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84696266,'音速鸭','可以以音速步行的鸭子。由于不能适应其惊人的速度而经常失控。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84740193,'破坏炮','这张卡只能装备在攻击力1000以下的怪兽上。伤害计算时，对方怪兽攻击表示则看攻击力，守备表示则看守备力，其数值在2500以上时，装备这张卡的怪兽攻击力上升2500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84747429,'空邪鲸','1回合1次，可以从手卡把1只鱼族·海龙族·水族怪兽从游戏中除外，选择对方场上表侧表示存在的1张卡破坏。那之后，这张卡直到下次的自己的准备阶段时从游戏中除外。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84749824,'神之警告',replace(replace('①：可以支付2000基本分把以下效果发动。\r\n●包含把怪兽特殊召唤效果的怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。\r\n●自己或者对方把怪兽召唤·反转召唤·特殊召唤之际才能发动。那个无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84754430,'机界骑士 亚伯拉姆',replace(replace('守护星之光明的勇者　为灭除幻界于世之暗　对天选之人托付己身之力。\r\n星之宝杯继承的意志成为崭新钥匙　化成斩绝黑暗之剑。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84764038,'彼岸的恶鬼 斯卡尔米利奥内',replace(replace('这个卡名的①③的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上没有魔法·陷阱卡存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：自己场上有「彼岸」怪兽以外的怪兽存在的场合这张卡破坏。\r\n③：这张卡被送去墓地的回合的结束阶段才能发动。从卡组把「彼岸的恶鬼 斯卡尔米利奥内」以外的1只恶魔族·暗属性·3星怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84766279,'大薰风翔鹏',replace(replace('调整＋调整以外的名字带有「薰风」的怪兽1只以上\r\n1回合1次，可以让自己墓地存在的2只名字带有「薰风」的怪兽回到卡组，选择对方场上表侧表示存在的1只怪兽破坏。','\r',char(13)),'\n',char(10)),'对方场上表侧表示存在的1只怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84769941,'对坏兽用决战兵器 超级机械多哥兰',replace(replace('这张卡不能通常召唤。对方场上有「坏兽」怪兽存在的场合可以特殊召唤。\r\n①：「坏兽」怪兽在自己场上只能有1只表侧表示存在。\r\n②：1回合1次，把自己·对方场上2个坏兽指示物取除才能发动。从自己的手卡·墓地选1只「坏兽」怪兽当作装备卡使用给这张卡装备。\r\n③：这张卡的攻击力上升这张卡的效果装备的「坏兽」怪兽的原本攻击力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84794011,'孤寂','下半身是鹿，持有吸收灵魂的大镰刀的兽战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84808313,'大进化药',replace(replace('把自己场上1只恐龙族怪兽解放才能把这张卡发动。这张卡发动后继续留在场上，用对方回合计算的第3回合的对方结束阶段破坏。\r\n①：只要这张卡在魔法与陷阱区域存在，自己在5星以上的恐龙族怪兽召唤的场合需要的解放可以不用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84812868,'月光白兔',replace(replace('①：这张卡召唤成功时，以「月光白兔」以外的自己墓地1只「月光」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。\r\n②：1回合1次，以最多有这张卡以外的自己场上的「月光」卡数量的对方场上的魔法·陷阱卡为对象才能发动。那些卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84814897,'骑龙',replace(replace('1回合1次，在自己的主要阶段，这张卡可以视为1张装备卡装备在自己的「暗魔界的战士 暗黑之剑」上，或者解除装备状态表侧攻击表示特殊召唤。当这张卡以自身效果变成装备卡时具有以下效果：\r\n●装备怪兽的攻击力·守备力上升900点。可以把装备状态的这张卡作为祭品，装备怪兽在这个回合可以直接攻击对方玩家。（1只怪兽至多只能装备1张同盟。当装备了同盟的怪兽被战斗破坏时，同盟会代替怪兽被破坏。）','\r',char(13)),'\n',char(10)),'装备','解除装备状态表侧攻击表示特殊召唤','装备怪兽在这个回合可以直接攻击','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84816244,'软件狗橡子',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。在自己场上把1只「软件狗衍生物」（电子界族·暗·1星·攻/守0）特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84816245,'软件狗衍生物','由「软件狗橡子」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84824601,'植物少女','这张卡从场上送去墓地时，可以从自己卡组把1只守备力1000以下的植物族怪兽加入手卡。','卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84834865,'蜻蜓','这张卡被战斗破坏送去墓地时，可以从卡组选择1只攻击力1500以下的风属性怪兽表侧攻击表示特殊召唤到自己场上。之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84845628,'狱落鸟',replace(replace('①：这张卡的攻击力·守备力上升自己墓地的调整数量×100。\r\n②：1回合1次，把手卡1只调整送去墓地，以对方场上1只怪兽为对象才能发动。那只怪兽的控制权直到结束阶段得到。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84847656,'念动攻击者','念动力族怪兽被破坏的场合，可以支付500基本分作为代替把这张卡破坏。','是否要使用「念动攻击者」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84869738,'霸王的逆鳞',replace(replace('①：自己场上有「霸王龙 扎克」存在的场合才能发动。「霸王龙 扎克」以外的自己场上的怪兽全部破坏，从自己的手卡·卡组·额外卡组·墓地把最多4只卡名不同的「霸王眷龙」怪兽无视召唤条件特殊召唤。\r\n②：把墓地的这张卡除外，以自己场上1只「霸王眷龙」超量怪兽为对象才能发动。从自己墓地的怪兽以及自己的额外卡组的表侧表示的灵摆怪兽之中选2只「霸王眷龙」怪兽在作为对象的怪兽下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'是否继续特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84877802,'最强之盾',replace(replace('战士族怪兽才能装备。\r\n①：装备怪兽的表示形式的以下效果适用。\r\n●攻击表示：装备怪兽的攻击力上升那个原本守备力数值。\r\n●守备表示：装备怪兽的守备力上升那个原本攻击力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84899094,'星杯的守护龙',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：自己场上的连接状态的怪兽为对象的魔法·陷阱·怪兽的效果发动时，把手卡·场上的这张卡送去墓地才能发动。那个发动无效并破坏。\r\n②：把墓地的这张卡除外，以自己墓地1只通常怪兽为对象才能发动。那只怪兽在作为连接怪兽所连接区的自己场上守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84905691,'自然黄蜂针','对方对怪兽的特殊召唤成功时，把这张卡以外的自己场上表侧表示存在的1只名字带有「自然」的怪兽解放才能发动。那些怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84914462,'巨斧龙人','①：这张卡攻击的场合，伤害步骤结束时变成守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84916669,'树精','森之精灵。借助草木之力封住对方的行动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84926738,'雷仙人','反转：基本分回复3000。这张卡从场上送去墓地的时候，基本分失去5000分。','LP回复','LP失去','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84932271,'贾特里克普斯异虫','反转：这个回合这张卡被破坏送去墓地时，这张卡在自己场上守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84962466,'兽毛竖起','①：1回合1次，把自己场上1只表侧表示的兽族·兽战士族怪兽除外，以自己场上1只兽族·兽战士族怪兽为对象才能发动。那只自己的兽族·兽战士族怪兽的攻击力上升因为这个效果发动而除外的怪兽的原本攻击力数值。','是否现在使用「兽毛竖起」的效果？','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84968490,'针虫的巢窟','①：从自己卡组上面把5张卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84970821,'暗黑之咒缚','①：只要这张卡在魔法与陷阱区域存在，每次自己或者对方把魔法卡发动给与那个发动的玩家1000伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84976088,'后盾龙',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡在墓地存在，自己场上没有卡存在的场合才能发动。墓地的这张卡和手卡1只4星以下的龙族怪兽守备表示特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84988419,'幻奏的华歌圣 花之蒂娃',replace(replace('「幻奏的音姬」怪兽＋「幻奏」怪兽\r\n①：这张卡不会被战斗·效果破坏，这张卡的战斗发生的对自己的战斗伤害变成0。\r\n②：这张卡和特殊召唤的怪兽进行战斗的伤害计算后才能发动。给与对方那只对方怪兽和这张卡的原本攻击力差的数值的伤害，那只对方怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(84990171,'豌豆战士','使用剑和大豆攻击的植物战士，意外的强悍。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85004150,'电子光虫-总线独角仙',replace(replace('昆虫族·光属性7星怪兽×2只以上\r\n这张卡也能从自己场上的5·6阶的昆虫族超量怪兽把2个超量素材取除，在那只超量怪兽上面重叠来超量召唤。\r\n①：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。对方场上的守备力最高的怪兽破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'是否在在超量怪兽上面重叠来超量召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85008676,'刚鬼 锁臂章鱼',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：对方怪兽攻击的场合，那次伤害计算时把这张卡从手卡丢弃才能发动。那次战斗发生的对自己的战斗伤害变成一半。\r\n②：给与自己伤害的效果由对方发动时，把这张卡从手卡丢弃才能发动（伤害步骤也能发动）。那个效果让自己受到的伤害变成一半。\r\n③：这张卡从场上送去墓地的场合才能发动。从卡组把「刚鬼 锁臂章鱼」以外的1张「刚鬼」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85028288,'朱罗纪巨神兽','这张卡不能特殊召唤。只要这张卡在场上表侧表示存在，不能成为陷阱·效果怪兽的效果的对象。1回合1次，可以把自己墓地存在的1只攻击力1700以下的名字带有「朱罗纪」的怪兽从游戏中除外，这张卡的攻击力直到结束阶段时上升1000。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85034450,'疾行机人 弹珠机卡丁车','①：1回合1次，从手卡丢弃1只机械族怪兽，以场上1只怪兽为对象才能发动。那只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85060248,'精神防护者','这张卡的控制者在每次自己的准备阶段支付500基本分。这个时候不支付500基本分的场合这张卡破坏。只要这张卡在场上表侧表示存在，念动力族怪兽以外的攻击力2000以下的怪兽不能攻击宣言。','是否要支付500LP维持「精神防护者」？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85066822,'水龙',replace(replace('这张卡不能通常召唤。「化学结合-H2O」的效果才能特殊召唤。\r\n①：只要这张卡在怪兽区域存在，场上的炎属性怪兽以及炎族怪兽的攻击力变成0。\r\n②：这张卡被破坏送去墓地时，以自己墓地2只「氢素龙」和1只「氧素龙」为对象才能发动。那些怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85080048,'雷之裁决','①：自己场上有雷族怪兽召唤·反转召唤·特殊召唤时，以对方场上1张卡为对象才能发动。那张对方的卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85080444,'古遗物-神盾','这张卡可以当作魔法卡使用从手卡到魔法与陷阱卡区域盖放。魔法与陷阱卡区域盖放的这张卡在对方回合被破坏送去墓地时，这张卡特殊召唤。对方回合中这张卡特殊召唤成功的场合，直到回合结束时，自己场上的名字带有「古遗物」的怪兽不会成为对方的卡的效果的对象，不会被对方的卡的效果破坏。','特殊召唤','效果耐性','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85087012,'卡片炮击士',replace(replace('①：1回合1次，从自己卡组上面把最多3张卡送去墓地才能发动。这张卡的攻击力直到回合结束时上升因为这个效果发动而送去墓地的卡数量×500。\r\n②：自己场上的这张卡被破坏送去墓地的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'攻击上升','抽卡','请选择要送去墓地的卡的数量','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85101097,'布丁队',replace(replace('这张卡不能作为融合·同调·超量召唤的素材。\r\n①：只要这张卡在怪兽区域存在，这张卡不能解放。\r\n②：自己结束阶段以自己的灵摆区域1张卡为对象才能发动。那张卡破坏，这张卡的控制权移给对方。\r\n③：自己准备阶段发动。自己受到300伤害。','\r',char(13)),'\n',char(10)),'控制权转移','效果伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85101228,'变形斗士·电网','只要自己场上有名字带有「变形斗士」的怪兽表侧表示存在，对方场上存在的4星以上的怪兽不能攻击宣言，也不能把表示形式改变。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85103922,'古遗物-巨怒剑','这张卡可以当作魔法卡使用从手卡到魔法与陷阱卡区域盖放。魔法与陷阱卡区域盖放的这张卡在对方回合被破坏送去墓地时，这张卡特殊召唤。对方回合中这张卡特殊召唤成功的场合，可以选对方场上表侧表示存在的1张卡破坏。','特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85115440,'十二兽 牛犄',replace(replace('4星怪兽×2\r\n「十二兽 牛犄」1回合1次也能在同名卡以外的自己场上的「十二兽」怪兽上面重叠来超量召唤。\r\n①：这张卡的攻击力·守备力上升这张卡作为超量素材中的「十二兽」怪兽的各自数值。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。从卡组把1只可以通常召唤的兽战士族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'是否在「十二兽」怪兽上面重叠来超量召唤？','兽战士族怪兽加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85121942,'混沌No.105 燃烧拳击手 彗星之指套拳王',replace(replace('5星怪兽×4\r\n这张卡战斗破坏怪兽送去墓地时，给与对方基本分破坏的怪兽的原本攻击力一半数值的伤害。此外，这张卡有「No.105 燃烧拳击手 流星之指套拳士」在作为超量素材的场合，得到以下效果。\r\n●1回合1次，把这张卡1个超量素材取除，选择对方场上1只怪兽才能发动。选择的怪兽破坏，给与对方基本分破坏的怪兽的攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'伤害','破坏并伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85138716,'救援兔',replace(replace('「救援兔」的效果1回合只能使用1次。这张卡不能从卡组特殊召唤。\r\n①：把场上的这张卡除外才能发动。从卡组把2只4星以下的同名通常怪兽特殊召唤。这个效果特殊召唤的怪兽在结束阶段破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85166216,'恩惠之像','当这张卡因对方控制的卡的效果从手卡被送去墓地时，自己回复2000基本分。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85215458,'黑羽-月影之卡鲁特','①：自己的「黑羽」怪兽进行战斗的从伤害步骤开始时到伤害计算前，把这张卡从手卡送去墓地才能发动。那只怪兽的攻击力直到回合结束时上升1400。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85216896,'神数炼机圣 梅塔特隆',replace(replace('从额外卡组特殊召唤的怪兽2只以上\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡所连接区的从额外卡组特殊召唤的怪兽被战斗或者对方的效果破坏的场合才能发动。从自己墓地的怪兽以及自己的额外卡组的表侧表示的灵摆怪兽之中选1只怪兽加入手卡。\r\n②：以这张卡以外的自己以及对方场上的从额外卡组特殊召唤的怪兽各1只为对象才能发动。那2只怪兽直到结束阶段除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85239662,'神秘之妖精 艾尔菲莉娅','1回合1次，把手卡1只风属性怪兽给对方观看才能发动。直到下次的对方的结束阶段时，双方玩家不能用持有给人观看的怪兽的等级以外的等级的怪兽为素材作超量召唤。','召唤限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85252081,'超级量子机兽 脉冲虎鲸',replace(replace('3星怪兽×2\r\n①：没有超量素材的这张卡不能攻击。\r\n②：1回合1次，把这张卡1个超量素材取除，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。这张卡有「超级量子战士 蓝光层」在作为超量素材的场合，这个效果在对方回合也能发动。\r\n③：1回合1次，自己主要阶段才能发动。选自己的手卡·场上1只「超级量子战士」怪兽在这张卡下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'魔陷破坏','增加素材','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85255550,'异国的剑士','受到「异国的剑士」攻击的怪兽，5回合后被破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85289965,'刺刀枪管龙',replace(replace('效果怪兽3只以上\r\n①：这张卡不会被战斗破坏。\r\n②：1回合1次，以1只攻击表示怪兽为对象才能发动。那只怪兽变成守备表示。这个回合，这张卡在同1次的战斗阶段中可以作2次攻击。对方不能对应这个效果的发动把效果发动。这个效果在对方回合也能发动。\r\n③：1回合1次，这张卡向表侧表示怪兽攻击宣言时才能发动。直到回合结束时，这张卡的攻击力上升那只怪兽的攻击力一半数值，那只怪兽的攻击力变成一半。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85306040,'哥布林精英部队','①：这张卡攻击的场合，战斗阶段结束时变成守备表示，直到下次的自己回合的结束时不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85309439,'北风与太阳','交情深厚的北风和太阳，能发动飓风与热光线的攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85310252,'怒怒怒车夫',replace(replace('这张卡被名字带有「怒怒怒」的怪兽的效果特殊召唤的回合，可以选择自己场上1只名字带有「怒怒怒」的怪兽，从以下效果选择1个发动。这个效果1回合可以使用最多2次。\r\n●选择的怪兽的等级上升1星。\r\n●选择的怪兽的等级下降1星。','\r',char(13)),'\n',char(10)),'等级变化','等级上升1星','等级下降1星','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85313220,'漆黑之魔王 LV4','这张卡战斗破坏的对方怪兽的效果无效化。这张卡战斗破坏怪兽的下次的自己回合的准备阶段时，可以把这张卡送去墓地从手卡·卡组特殊召唤1只「漆黑之魔王 LV6」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85326399,'尖刺海龙','用身体的刺攻击敌人，并能放出电流。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85346853,'光子龙之圣骑士','「光子龙降临」降临。把这张卡解放才能发动。从手卡·卡组把1只「银河眼光子龙」特殊召唤。此外，这张卡战斗破坏对方怪兽送去墓地时，从卡组抽1张卡。','特殊召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85352446,'统制训练','选择对方场上表侧表示存在的1只5星以下的怪兽发动。场上表侧表示存在的持有那以外的等级的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85359414,'冰岩魔兽',replace(replace('1回合1次，在自己的主要阶段，这张卡可以视为1张装备卡装备在自己的「灼岩魔兽」上，或者解除装备状态表侧攻击表示特殊召唤。当这张卡以自身效果变成装备卡时具有以下效果：\r\n●每当装备怪兽对对方造成战斗伤害时，破坏场上1张里侧表示的魔法或者陷阱卡。（1只怪兽至多只能装备1张同盟。当装备了同盟的怪兽被战斗破坏时，同盟会代替怪兽被破坏。）','\r',char(13)),'\n',char(10)),'装备','特殊召唤','破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85374678,'超级量子战士 绿光层',replace(replace('「超级量子战士 绿光层」的①②的效果1回合各能使用1次。\r\n①：这张卡召唤·特殊召唤成功时才能发动。从手卡把1只「超级量子」怪兽特殊召唤。\r\n②：这张卡被送去墓地的场合，把手卡1张「超级量子」卡丢弃才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'特殊召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85399281,'智天使 哈维斯特','①：这张卡被战斗破坏送去墓地时，以自己墓地1张反击陷阱卡为对象才能发动。那张卡加入手卡。','加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85431040,'邪恶之棘','把这张卡解放发动。给与对方基本分300分伤害，可以从自己卡组把最多2只「邪恶之棘」表侧攻击表示特殊召唤。这个效果特殊召唤的「邪恶之棘」效果不能发动。','伤害','是否要特殊召唤「邪恶之棘」？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85446833,'零零封锁','只要这张卡在场上存在，对方不能选择表侧攻击表示存在的攻击力0的怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85448931,'守卫海洋的战士','彻底的攻击玷污大海的家伙、人鱼战士。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85463083,'鬼计食尸鬼',replace(replace('自己场上有「鬼计」怪兽存在的场合才能让这张卡表侧表示召唤。\r\n①：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n②：自己主要阶段1有1次，以自己场上1只「鬼计」怪兽为对象才能发动。那只怪兽的攻击力直到下次的对方回合的结束时变成场上的「鬼计」怪兽的原本攻击力合计数值，这个回合作为对象的怪兽以外的怪兽不能攻击。','\r',char(13)),'\n',char(10)),'变成里侧守备','攻击变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85475641,'永火复仇者','这张卡在墓地存在，自己手卡是0张的场合，「永火复仇者」以外的自己场上存在的怪兽被和对方怪兽的战斗破坏送去墓地时，这张卡可以从墓地特殊召唤。这个效果特殊召唤的这张卡的等级变成和对方怪兽破坏的自己怪兽相同等级。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85489096,'武器召唤师','①：这张卡反转的场合发动。从卡组把1张「守护者」卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85505315,'侵入魔鬼·螳镰','把名字带有「侵入魔鬼」的怪兽解放对这张卡的上级召唤成功时，可以支付1000基本分，选择自己墓地存在的1只名字带有「侵入魔鬼」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85507811,'元素英雄 光辉新宇侠',replace(replace('「元素英雄 新宇侠」＋「新空间侠·光辉青苔」\r\n把自己场上存在的上记的卡回到卡组的场合才能从额外卡组特殊召唤（不需要「融合」魔法卡）。结束阶段时这张卡回到额外卡组。把对方场上表侧表示存在的1张卡破坏，这张卡得到那张卡的种类的以下效果：这个效果1回合只有1次在自己的主要阶段一才能使用。\r\n●怪兽卡-这个回合，这张卡不能进行战斗。\r\n●魔法卡-这张卡可以直接攻击对方玩家。\r\n●陷阱卡-这张卡变成守备表示。','\r',char(13)),'\n',char(10)),'返回卡组','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85519211,'岩盘爆破','自己场上表侧表示存在的「地雷石人」每有1只，给与对方基本分1000分的伤害。之后自己场上表侧表示存在的「地雷石人」全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85520851,'超传导恐兽','①：1回合1次，把自己场上1只怪兽解放才能发动。给与对方1000伤害。这个效果发动的回合，这张卡不能攻击宣言。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85528209,'超重魔兽 九尾-B',replace(replace('调整＋调整以外的「超重武者」怪兽1只以上\r\n这张卡在规则上也当作「超重武者」卡使用。\r\n①：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。\r\n②：自己墓地没有魔法·陷阱卡存在的场合，这张卡的守备力上升对方场上的特殊召唤的怪兽数量×900。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85541675,'机巧解体新书','每次名字带有「机巧」的怪兽的表示形式变更，给这张卡放置1个机巧指示物（最多2个）。此外，可以把场上存在的这张卡送去墓地，从自己卡组抽出这张卡放置的机巧指示物数量的卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85545073,'魔玩具·剪刀熊',replace(replace('「锋利小鬼·剪刀」＋「毛绒动物·熊」\r\n①：这张卡战斗破坏对方怪兽送去墓地时才能发动。那只怪兽当作攻击力上升1000的装备卡使用给这张卡装备。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85551711,'虚空之黑魔导师',replace(replace('魔法师族7星怪兽×2\r\n①：只要持有超量素材的这张卡在怪兽区域存在，自己在对方回合可以把速攻魔法卡以及陷阱卡从手卡发动。那个发动之际把这张卡1个超量素材取除。\r\n②：超量召唤的这张卡被对方的效果送去墓地的场合或者被战斗破坏送去墓地的场合才能发动。从手卡·卡组把1只魔法师族·暗属性怪兽特殊召唤。那之后，可以选场上1张卡破坏。','\r',char(13)),'\n',char(10)),'是否选场上1张卡破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85555787,'恶之卡组破坏病毒','①：把自己场上1只攻击力3000以下的暗属性怪兽解放才能发动。那只怪兽的攻击力每有500，对方从自身的手卡·卡组选1张卡破坏。为这张卡发动而把攻击力2000以上的怪兽解放的场合，用对方回合计算的3回合内对方抽到的卡全部确认，那之内的怪兽全部破坏。此外，这张卡的效果破坏送去墓地的卡在破坏的回合不能把效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85562745,'噩梦之拷问室','每次对对方造成战斗伤害以外的伤害时，这张卡对对方造成300点伤害。「噩梦之拷问室」的效果不适用于这张卡效果。','给对方300伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85602018,'遗言状','只要该回合有怪兽从自己场上送入自己墓地就可以从卡组特殊召唤攻击力1500以下的1只怪兽到场上。','是否要使用「遗言状」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85605684,'狂暴死龙','这张卡只能以「与恶魔的暗中交易」的效果进行特殊召唤。这张卡可以对对方场上所有怪兽各进行1次攻击。在自己每回合的结束阶段时，这张卡的攻击力下降500点。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85638822,'刚鬼死斗',replace(replace('①：作为这张卡的发动时的效果处理，给这张卡放置3个指示物。\r\n②：自己的「刚鬼」怪兽战斗破坏对方怪兽的场合发动。这张卡1个指示物取除。\r\n③：这张卡的效果给这张卡放置的指示物全部被取除的战斗阶段结束时才能由自己把这个效果发动。从手卡·卡组把「刚鬼」怪兽尽可能特殊召唤（同名卡最多1张）。那之后，给这张卡放置3个指示物。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85639257,'水魔道士','能够操纵水的魔法师，能做出水壁压迫敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85646474,'守墓的使徒','这张卡被对方怪兽的攻击破坏送去墓地时，可以从卡组把「守墓的使徒」以外的1只名字带有「守墓」的怪兽里侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85651167,'真红眼铁骑士-基亚·弗里德',replace(replace('①：1回合1次，这张卡有装备卡被装备的场合才能发动。那些装备卡破坏。那之后，可以选对方场上1张魔法·陷阱卡破坏。\r\n②：1回合1次，把这张卡装备的自己场上1张装备卡送去墓地，以自己墓地1只7星以下的「真红眼」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'装备卡破坏','特殊召唤','是否选对方场上1张魔法·陷阱卡破坏？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85668449,'脑开发研究所','只要这张卡在场上存在，可以在通常召唤外加上只有1次把1只念动力族怪兽召唤。这个方法对念动力族怪兽的召唤成功时，给这张卡放置1个念力指示物。此外，为自己场上存在的念动力族怪兽把效果发动而支付基本分的场合，可以作为代替给这张卡放置1个念力指示物。这张卡从场上离开时，这张卡的控制者受到这张卡放置的念力指示物数量×1000的数值的伤害。','给「脑开发研究所」放置1个「念力指示物」','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85679527,'宇宙智者',replace(replace('这张卡不能通常召唤。从手卡以及自己场上的表侧表示怪兽之中把效果怪兽以外的1只怪兽送去墓地的场合可以特殊召唤。这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡的攻击力上升因为这张卡特殊召唤而送去墓地的怪兽的等级×200。\r\n②：把自己场上1只效果怪兽解放才能发动。从手卡·卡组把1只通常怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85682655,'发条杂耍人','这张卡和对方怪兽进行战斗的场合，可以把那只对方怪兽在伤害计算后破坏。这个效果只在这张卡在场上表侧表示存在能使用1次。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85684223,'驱使梦魇的死灵',replace(replace('「削魂的死灵」＋「梦魇马」\r\n这张卡不会被战斗破坏。这张卡成为魔法·陷阱·效果怪兽效果的对象时，这张卡破坏。这张卡在对方场上存在怪兽的状态下也能对对方进行直接攻击。这张卡对对方进行直接攻击成功时，对方随机丢弃1张手卡。','\r',char(13)),'\n',char(10)),'丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85704698,'再生骰子',replace(replace('①：以自己墓地1只「疾行机人」调整为对象才能发动。那只怪兽效果无效特殊召唤，掷1次骰子。那只特殊召唤的怪兽直到回合结束时变成和出现的数目相同等级。\r\n②：把墓地的这张卡除外才能发动。用自己场上的包含「疾行机人」调整的怪兽为素材，把1只风属性同调怪兽同调召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85705804,'鸭人','尾巴很长的鸟，从空中发起攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85709845,'有勇无谋','自己场上守备表示存在的怪兽成为攻击对象时才能发动。那只守备表示怪兽的表示形式变更为表侧攻击表示。并且，那次战斗破坏攻击怪兽送去墓地时，再给与对方基本分那只怪兽的原本攻击力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85718645,'暗帝 迪尔格','这张卡召唤·特殊召唤成功时，可以把对方墓地存在的最多2张卡从游戏中除外。并且，再把除外数量的卡从对方卡组上面送去墓地。这张卡在召唤·特殊召唤的回合不能攻击。','把对方墓地存在的最多2张卡从游戏中除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85742772,'超重力网','场上的存在的全部4星以上的怪兽不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85754829,'格鲁斯异虫','每次场上里侧守备表示存在的怪兽反转，给这张卡放置1个虫指示物。这张卡放置的虫指示物每有1个，这张卡的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85763457,'响彻于星遗物的残叫',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：自己场上有互相连接状态的怪兽存在，对方把怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85771019,'堕天使 阿斯蒙蒂斯',replace(replace('这张卡不能作从卡组·墓地的特殊召唤。\r\n①：1回合1次，自己主要阶段才能发动。从卡组把1只天使族怪兽送去墓地。\r\n②：自己场上的这张卡被破坏送去墓地的场合发动。在自己场上把1只「阿斯蒙衍生物」（天使族·暗·5星·攻1800/守1300）和1只「蒂斯衍生物」（天使族·暗·3星·攻/守1200）特殊召唤。「阿斯蒙衍生物」不会被效果破坏。「蒂斯衍生物」不会被战斗破坏。','\r',char(13)),'\n',char(10)),'把1只天使族怪兽送去墓地','特殊召唤衍生物','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85771020,'阿斯蒙衍生物','由「堕天使 阿斯蒙蒂斯」的效果特殊召唤。「阿斯蒙衍生物」不会被效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85771021,'蒂斯衍生物','由「堕天使 阿斯蒙蒂斯」的效果特殊召唤。「蒂斯衍生物」不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85775486,'再机动','从自己手卡让1只名字带有「机皇」的怪兽回到卡组发动。选择自己墓地存在的1张名字带有「机皇」的卡加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85800949,'传说的骑士 克里底亚',replace(replace('这张卡不能通常召唤。「传说之心」的效果才能特殊召唤。\r\n①：这张卡特殊召唤成功时，以场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张表侧表示的魔法·陷阱卡除外。\r\n②：这张卡被选择作为攻击对象时，以自己墓地1张陷阱卡为对象才能发动。那张卡在自己场上盖放。这个效果盖放的卡在盖放的回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85802526,'人鱼公主','只要这张卡在自己场上表侧表示的存在，每次自己的准备阶段回复800基本分。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85821180,'调整欣快跑','①：从手卡丢弃1只怪兽才能发动。和那只怪兽相同种族·属性而等级高1星的1只调整从卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85827713,'鬼计之夜','只要场上有名字带有「鬼计」的怪兽存在，对方场上的怪兽不能反转召唤。此外，这张卡被对方破坏送去墓地时发动。这个回合对方不能攻击宣言。','不能攻击宣言','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85839825,'超量能量','把自己场上存在的1个超量素材取除发动。选择对方场上表侧表示存在的1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85852291,'万宝槌','①：自己手卡任意数量回到卡组洗切。那之后，自己抽出回到卡组的数量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85854214,'英雄规则2','墓地的卡为对象的效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85876417,'正义盟军 千械鬼','这张卡可以向对方场上表侧表示存在的光属性怪兽各作1次攻击。和光属性以外的怪兽进行战斗的场合，那次伤害计算前这张卡破坏。','这张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85893201,'连锁诱爆','这张卡在场上存在，对方场上的怪兽被对方的卡的效果破坏的场合，可以选择场上1张卡破坏。这个效果1回合只能使用1次。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85908279,'召唤兽 科库托斯',replace(replace('「召唤师 阿莱斯特」＋水属性怪兽\r\n①：这张卡不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：这张卡可以用表侧守备表示的状态作出攻击。那个场合，攻击力数值适用进行伤害计算。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85909450,'鹰身女妖的宠物幻龙',replace(replace('风属性4星怪兽×3\r\n这张卡的效果若这张卡的超量素材没有则不适用。这张卡可以直接攻击对方玩家。只要这张卡在场上表侧表示存在，对方不能把名字带有「鹰身」的怪兽作为攻击对象，也不能作为卡的效果的对象。每次自己的结束阶段把这张卡1个超量素材取除。','\r',char(13)),'\n',char(10)),'取除素材','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85936485,'团结的反抗军','立誓要为了对抗强大力量而集结的人们。革命之日将近。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85967160,'芳香法师-香蜂草',replace(replace('①：只要自己基本分比对方多并有这张卡在怪兽区域存在，自己的植物族怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n②：1回合1次，自己基本分回复的场合发动。这张卡的攻击力·守备力直到对方回合的结束时上升1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(85991529,'星际仙踪-达克星',replace(replace('这张卡不能通常召唤。把等级合计直到10以上的手卡的「星际仙踪」怪兽除外的场合才能特殊召唤。\r\n①：这张卡不会成为对方的效果的对象。\r\n②：魔法卡发动时，从自己墓地把1只「星际仙踪」怪兽除外才能发动。那个发动无效并破坏。\r\n③：这张卡被战斗·效果破坏送去墓地的场合，把墓地的这张卡除外才能发动。从卡组把1只9星以下的「星际仙踪」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'发动无效并破坏','从卡组把「星际仙踪」怪兽加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86013171,'星际仙踪-小胆',replace(replace('「星际仙踪-小胆」的①的效果1回合只能使用1次。\r\n①：把场上的这张卡除外才能发动。从手卡把1只3星以上的「星际仙踪」怪兽特殊召唤。这个效果在对方回合也能发动。\r\n②：1回合1次，支付500基本分，以除外的3只自己的「星际仙踪」怪兽为对象才能发动。那些怪兽回到墓地，给与对方500伤害。','\r',char(13)),'\n',char(10)),'从手卡把「星际仙踪」怪兽特殊召唤','除外的「星际仙踪」怪兽回到墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86016245,'弱者的意志','自己手卡是0张的场合，自己场上存在的2星以下的怪兽战斗破坏对方怪兽送去墓地时，从自己卡组抽2张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86039057,'帮一把骑士','这张卡在墓地存在，自己手卡是0张的场合，对方怪兽的攻击宣言时才能发动。这张卡从墓地特殊召唤，战斗阶段结束。「帮一把骑士」的效果在决斗中只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86049351,'最后反击','自己场上的名字带有「燃烧拳击手」的怪兽和对方怪兽进行战斗的攻击宣言时才能发动。那次攻击无效，那只自己怪兽送去墓地。自己场上1只名字带有「燃烧拳击手」的怪兽的攻击力上升那只对方怪兽的原本攻击力数值，和那只对方怪兽进行伤害计算。那之后，自己受到这张卡的效果上升的攻击力数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86062400,'超量复仇者',replace(replace('这张卡不受超量怪兽的效果影响。此外，这张卡被和超量怪兽的战斗破坏送去墓地时，把让这张卡破坏的超量怪兽的阶级的以下效果发动。\r\n●3阶以下：对方选额外卡组1张卡送去墓地。\r\n●4阶：自己选对方的额外卡组1张卡送去墓地。\r\n●5阶以上：对方选那只超量怪兽的阶级数量的额外卡组的卡送去墓地。','\r',char(13)),'\n',char(10)),'额外送墓','','','','','','','','','','','','','','','');
