INSERT INTO texts VALUES(86321248,'古代的机械合成兽',replace(replace('①：得到为这张卡的召唤而解放的怪兽的以下效果。\r\n●绿色零件：这张卡的攻击力上升300。\r\n●红色零件：这张卡直接攻击给与对方战斗伤害的场合发动。给与对方500伤害。\r\n●黄色零件：这张卡战斗破坏对方怪兽的场合发动。给与对方700伤害。','\r',char(13)),'\n',char(10)),'给予对方500伤害','给予对方700伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86325573,'燃烧拳击手 大绷带拳手','这张卡1回合只有1次不会被战斗破坏。此外，1回合1次，选择自己墓地1只名字带有「燃烧拳击手」的怪兽或者除外的1只自己的名字带有「燃烧拳击手」的怪兽才能发动。自己场上的全部名字带有「燃烧拳击手」的怪兽变成和选择的怪兽相同等级。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86325596,'邪剑男爵','操纵着充满怨念之剑的贵族，对追逐猎物的信念十分的执着。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86327225,'天界王 志那都','用「奇迹之方舟」特殊召唤。特殊召唤时，必须以场上或手卡中合计8颗星以上的怪兽作为祭品。这张卡战斗破坏对方守备表示的怪兽并将其送去墓地时，对方受到数值与被破坏怪兽的原始攻击力相同的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86346643,'虹光新宇侠',replace(replace('「元素英雄 新宇侠」＋名字带有「究极宝玉神」的1只怪兽\r\n这只怪兽的融合召唤只能用上记的卡进行，不能作融合召唤以外的特殊召唤。1回合只有1次可以从以下效果把1个发动。\r\n●可以把自己场上1只怪兽送去墓地，对方场上怪兽全部回到卡组。\r\n●可以把自己场上1张魔法·陷阱卡送去墓地，对方场上的魔法·陷阱卡全部回到卡组。\r\n●可以把自己卡组最上面1张卡送去墓地，对方墓地的卡全部回到卡组。','\r',char(13)),'\n',char(10)),'选择效果发动','把自己场上1只怪兽送去墓地，对方场上怪兽全部回到卡组','把自己场上1张魔法·陷阱卡送去墓地，对方场上的魔法·陷阱卡全部回到卡组','把自己卡组最上面1张卡送去墓地，对方墓地的卡全部回到卡组','','','','','','','','','','','','');
INSERT INTO texts VALUES(86361354,'同族感电病毒','1回合1次，把手卡1只怪兽从游戏中除外才能发动。和为这个效果发动而从游戏中除外的怪兽相同种族的场上表侧表示存在的怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86377375,'恶王 阿里曼',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：把这张卡从手卡丢弃才能发动。从卡组把1张「黯黑世界-暗影敌托邦-」加入手卡。\r\n②：把自己场上1只暗属性怪兽解放才能发动。自己从卡组抽1张。为这个效果发动而把这张卡以外的暗属性怪兽解放的场合，可以作为抽卡的代替而从卡组把1只守备力2000以上的暗属性怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','是否作为抽卡的代替从卡组检索？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86396750,'精灵兽 火狮',replace(replace('自己对「精灵兽 火狮」1回合只能有1次特殊召唤。\r\n①：1回合1次，把自己墓地1张「灵兽」卡除外才能发动。这个回合中，以下效果适用。这个效果在对方回合也能发动。\r\n●自己场上的「灵兽」怪兽的攻击力·守备力上升500。','\r',char(13)),'\n',char(10)),'攻守上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86401517,'岚龙之圣骑士',replace(replace('「电脑网仪式」降临。这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡向怪兽攻击的伤害步骤开始时才能发动。那只怪兽回到持有者手卡。\r\n②：把这张卡解放才能发动。从手卡·卡组把1只5星以上的电子界族怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不能把效果发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86421986,'悟道的老树','运用所有知识、能够守备各种攻击的老树。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86442081,'寂静深渊','这张卡被战斗破坏送去墓地时，场上表侧表示存在的水属性怪兽以外的怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86445415,'红色零件','①：这张卡召唤·特殊召唤成功时才能发动。从卡组把1只「黄色零件」加入手卡。','选1只「黄色零件」加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86466163,'星因士 五车二',replace(replace('「星因士 五车二」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合才能发动。这个回合自己在用怪兽3只以上为素材的超量召唤的场合，可以把自己场上的4星以下的「星守」怪兽当作5星的素材使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86474024,'共同战线','自己场上有相同等级的怪兽表侧表示2只以上存在的场合才能发动。陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86489182,'始祖神鸟 斯摩夫','这张卡在手卡的场合，当作通常怪兽使用。只要这张卡在场上表侧表示存在，风属性怪兽的祭品召唤需要的祭品变少1只。只用风属性怪兽作为祭品对这张卡的祭品召唤成功时，对方场上最多2张卡回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86498013,'异次元的驯兽师','被吸入异次元的可怜小矮人。但是现在为了新的目标而每天都在努力着。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86516889,'鬼计惊魂','选择自己场上里侧守备表示存在的怪兽任意数量才能发动。选择的怪兽变成表侧守备表示，选最多有那之中的名字带有「鬼计」的怪兽数量的对方场上表侧表示存在的怪兽变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86532744,'闪光No.39 希望皇 霍普一',replace(replace('光属性4星怪兽×3\r\n这张卡也能在自己场上的「No.39 希望皇 霍普」上面把这张卡重叠来超量召唤。自己基本分比对方少3000以上的场合，把这张卡3个超量素材取除，把基本分支付到变成10才能发动。对方场上的特殊召唤的怪兽全部破坏并从游戏中除外。那之后，给与对方基本分这个效果除外的怪兽数量×300的数值的伤害。','\r',char(13)),'\n',char(10)),'破坏并除外','是否在「No.39 希望皇 霍普」上面重叠超量召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86541496,'左腕的代偿',replace(replace('这张卡发动的回合，自己不能把魔法·陷阱卡盖放。\r\n①：这张卡以外的自己手卡是2张以上的场合，把那些手卡全部除外才能发动。从卡组把1张魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86555018,'环状列石术','自己场上的名字带有「先史遗产」的怪兽被战斗或者卡的效果破坏送去墓地时才能发动。从卡组把1只4星以下的名字带有「先史遗产」的怪兽表侧守备表示特殊召唤。这个效果特殊召唤的怪兽不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86559484,'超量透视细剑龙',replace(replace('「超量透视细剑龙」的①②的效果1回合各能使用1次。\r\n①：自己主要阶段把自己墓地的龙族怪兽和幻龙族怪兽各1只除外才能发动。从手卡·卡组把1只「超量透视细剑龙」特殊召唤。\r\n②：这张卡从场上送去墓地的场合才能发动。给与对方500伤害。','\r',char(13)),'\n',char(10)),'特殊召唤','给予伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86569121,'梅尔基多四面兽','一边变换4张假面，一边发起4种攻击的怪物。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86578200,'魔界大道具「逃命马车」',replace(replace('①：只要这张卡在魔法与陷阱区域存在，自己的「魔界剧团」怪兽在1回合各有1次不会被战斗破坏。\r\n②：1回合1次，以自己场上1只「魔界剧团」怪兽为对象才能发动。直到对方回合结束时，对方不能把那只怪兽作为效果的对象。\r\n③：自己的额外卡组有表侧表示的「魔界剧团」灵摆怪兽存在，盖放的这张卡被对方的效果破坏的场合才能发动。对方场上的卡全部回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86585274,'魔导法士 朱诺',replace(replace('①：把手卡3张「魔导书」魔法卡给对方观看才能发动。这张卡从手卡特殊召唤。\r\n②：1回合1次，从自己的手卡·墓地把1张「魔导书」魔法卡除外，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86607583,'限制代码',replace(replace('这个卡名的卡在决斗中只能发动1张。\r\n①：自己墓地有电子界族连接怪兽存在的场合才能把这张卡发动。那些怪兽数量的指示物给这张卡放置，从额外卡组把1只「码语者」怪兽特殊召唤，把这张卡当作装备卡使用给那只怪兽装备。这张卡从场上离开时那只怪兽破坏。\r\n②：自己结束阶段发动。这张卡1个指示物取除。不能取除的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86643777,'光虫基盘',replace(replace('「光虫基盘」的②的效果1回合只能使用1次。\r\n①：自己场上的昆虫族怪兽的攻击力·守备力上升300。\r\n②：以自己场上1只昆虫族超量怪兽为对象才能发动。把手卡1只昆虫族怪兽在那只怪兽下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86652646,'骨犬 小栗子','1000年前与主人不幸走散的迷犬。至今仍在等待着主人能够回来。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86676862,'邪心英雄 恶刃魔王',replace(replace('「邪心英雄 恶刃魔」＋6星以上的恶魔族怪兽\r\n这只怪兽不能作「暗黑融合」的融合召唤以外的特殊召唤。只要这张卡在场上表侧表示存在，对方回合的战斗阶段中把对方场上存在的怪兽全部变成表侧攻击表示，对方玩家必须用全部怪兽对这张卡作出攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86686671,'电子修理工厂',replace(replace('自己墓地有「电子龙」存在的场合，可以从以下效果选择1个发动。自己墓地有「电子龙」3只以上存在的场合，可以选择两方。「电子修理工厂」在1回合只能发动1张。\r\n●从卡组把1只机械族·光属性怪兽加入手卡。\r\n●选择自己墓地1只机械族·光属性怪兽回到卡组。','\r',char(13)),'\n',char(10)),'从卡组把1只机械族·光属性怪兽加入手卡','选择自己墓地1只机械族·光属性怪兽回到卡组','选择两个效果发动','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86690572,'炎涡的胎动','从手卡把1只名字带有「熔岩」的怪兽送去墓地发动。陷阱卡的发动无效并破坏。此外，这张卡在墓地存在的场合，可以把自己墓地存在的2只炎属性怪兽从游戏中除外，这张卡加入手卡。','这张卡加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86742443,'王宫的重税','对方回合的准备阶段时才能发动。随机选择对方的1张手卡并确认。选择的卡或者同名卡直到下次自己的结束阶段结束时没有召唤或者发动的场合，给与对方基本分1000分伤害。有召唤或者发动的场合，自己受到1000分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86750474,'淘气仙星·福克希维琪',replace(replace('天使族怪兽2只以上\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。给与对方为对方场上的卡数量×200伤害。\r\n②：连接召唤的这张卡被战斗·效果破坏的场合才能发动。从额外卡组把1只连接2以下的「淘气仙星」怪兽特殊召唤。那之后，给与对方为对方场上的卡数量×200伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86767655,'审判女士',replace(replace('「审判女士」的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，对方的卡的效果发动的场合，那次处理进行时进行2次投掷硬币，2次都是表的场合，那个效果无效。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86778566,'攻击无敌化',replace(replace('战斗阶段时才能从以下效果选择1个发动。\r\n●选择场上1只怪兽才能发动。选择的怪兽在这次战斗阶段中不会被战斗以及卡的效果破坏。\r\n●这次战斗阶段中，对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'选择场上1只怪兽，选择的怪兽在这次战斗阶段中不会被战斗以及卡的效果破坏','这次战斗阶段中，对自己的战斗伤害变成0','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86780027,'一族的团结','①：自己墓地的全部怪兽的原本种族相同的场合，自己场上的那个种族的怪兽的攻击力上升800。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86801871,'蛇壶','反转：在自己场上特殊召唤1个「毒蛇衍生物」（爬虫·地·3星·攻/守1200）。「毒蛇衍生物」被战斗破坏的场合，给与对方基本分500分的伤害。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86801872,'毒蛇衍生物','由「蛇壶」的效果特殊召唤。「毒蛇衍生物」被战斗破坏的场合，给予对方基本分500分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86804246,'超级漏洞人','这张卡不能通常召唤。把自己墓地存在的「漏洞人X」「漏洞人Y」「漏洞人Z」从游戏中除外的场合可以表侧守备表示特殊召唤。只要这张卡在场上表侧表示存在，场上表侧攻击表示存在的全部怪兽的攻击力·守备力交换。「超级漏洞人」在场上只能有1只表侧表示存在。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86805855,'暗魔界的龙骑士 暗黑之剑',replace(replace('「暗魔界的战士 暗黑之剑」＋「漆黑的斗龙」\r\n每次这张卡给与对方战斗伤害，可以选择对方的墓地的最多3张怪兽卡，从游戏中除外。','\r',char(13)),'\n',char(10)),'除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86821010,'武力解除','自己场上有「动力工具龙」表侧表示存在的场合才能发动。选择自己的场上·墓地存在的最多3张装备卡回到卡组，给与对方基本分回去数量×500的数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86825114,'淘气仙星·菲沃斯',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡作为「淘气仙星」连接怪兽的连接素材送去墓地的场合才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86825483,'神星领域','只要这张卡在场上存在，光属性怪兽的效果的发动不会被无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86827882,'极星宝 梅金吉奥德','选择自己场上表侧表示存在的1只名字带有「极神」或「极星」的怪兽发动。直到结束阶段时为止，那只怪兽的攻击力·守备力变成原本攻击力·守备力的两倍。这个回合被选择的怪兽不能直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86840720,'魔轰神 迪夫','名字带有「魔轰神」的怪兽从手卡送去自己墓地时，把自己场上存在的这张卡解放，选择那1只怪兽才能发动。选择怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86848580,'零鸟兽 水风女精',replace(replace('鸟兽族4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。对方场上表侧表示存在的全部卡的效果无效，这张卡的攻击力上升这张卡以外的场上表侧表示存在的卡数量×300的数值。这张卡的效果直到下次的自己的准备阶段时适用。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86868952,'武神器-八握','自己的主要阶段1，把这张卡从手卡送去墓地，选择自己场上1只名字带有「武神」的怪兽才能发动。这个回合，选择的怪兽在同1次的战斗阶段中可以作2次攻击。这个效果发动的回合，选择的怪兽以外的怪兽不能攻击。','2次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86871614,'克隆复制','①：对方对怪兽的召唤·反转召唤成功时，以那1只怪兽为对象才能发动。把持有那只表侧表示怪兽的原本的种族·属性·等级·攻击力·守备力的1只「克隆衍生物」在自己场上特殊召唤。那只表侧表示怪兽被破坏送去墓地时这衍生物破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86871615,'克隆衍生物','由「克隆复制」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86885905,'幻变骚灵·模拟精灵',replace(replace('①：这张卡发动后变成效果怪兽（魔法师族·光·4星·攻1400/守1800）在怪兽区域特殊召唤。这张卡也当作陷阱卡使用。\r\n②：只要这张卡的效果特殊召唤的这张卡在怪兽区域存在，这张卡以外的自己场上的「幻变骚灵」陷阱卡不会成为效果的对象，不会被效果破坏。','\r',char(13)),'\n',char(10)),replace(replace('\r\n','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86889202,'番犬-戴表看门狗',replace(replace('「番犬-戴表看门狗」的效果1回合只能使用1次，这个效果发动的回合，自己不能把怪兽特殊召唤。\r\n①：这张卡召唤成功的回合的主要阶段2把手卡1张魔法卡送去墓地才能发动。从卡组选1张永续魔法卡在自己的魔法与陷阱区域盖放。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86915847,'甲蜂','1回合1次，选择对方场上表侧表示存在的1只怪兽才能发动。选择的对方怪兽的攻击力直到结束阶段时变成一半数值。','攻防减半','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86937530,'妖精传姬-辉夜',replace(replace('①：这张卡召唤成功时才能发动。从卡组把1只攻击力1850的魔法师族怪兽加入手卡。\r\n②：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。对方可以把1张那只怪兽的同名卡从自身的卡组·额外卡组送去墓地让这个效果无效。没送去墓地的场合，这张卡和作为对象的怪兽回到持有者手卡。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'卡组检索','回到手卡','是否把同名卡送去墓地让这个效果无效？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86938484,'影依·拿非利',replace(replace('反转怪兽2只\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。从自己的手卡·场上把「影依」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：这张卡在墓地存在的场合才能发动。从手卡以及自己场上的表侧表示的卡之中选1张「影依」卡送去墓地，这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86943389,'高速疾行机人 快刀乱破智游',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡和特殊召唤的怪兽进行战斗的伤害步骤开始时才能发动。这张卡的攻击力直到那次伤害步骤结束时变成2倍。\r\n②：同调召唤的这张卡被送去墓地的回合的结束阶段，以「高速疾行机人 快刀乱破智游」以外的自己墓地1只「疾行机人」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86952477,'光辉骑士','只要这张卡在场上表侧守备表示存在，这张卡的等级变成4星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86988864,'三峰驼','当自己场上存在3只表侧表示的「三峰驼」时，祭掉其中2只，就可以抽3张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(86997073,'机动要塞 极强音','1回合1次，可以从自己手卡把1只名字带有「机皇兵」的怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87008374,'异热同心武器-不死鸟弩弓','这张卡可以从手卡当作装备卡使用给自己场上的「混沌No.39 希望皇 霍普雷」装备。用这个效果把这张卡装备的怪兽的攻击力上升1100。此外，装备怪兽战斗破坏对方怪兽时，给与对方基本分1000分伤害。「异热同心武器-不死鸟弩弓」在自己场上只能有1张表侧表示存在。','装备','伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87010442,'遗产猎人','当这张卡战斗破坏里侧守备表示的怪兽并将其送去墓地时，对方随机将1张手卡弹回其卡组。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87025064,'银龙的轰咆',replace(replace('「银龙的轰咆」在1回合只能发动1张。\r\n①：以自己墓地1只龙族的通常怪兽为对象才能发动。那只龙族的通常怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87043568,'强袭之魂','发动后这张卡变成装备卡，给自己场上存在的1只怪兽装备。装备怪兽攻击的场合，那次伤害步骤时可以从手卡把1只攻击力1000以下的怪兽送去墓地，装备怪兽的攻击力直到结束阶段时上升送去墓地的怪兽的攻击力数值。这个效果1回合只能使用1次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87046457,'超越死线','只要这张卡在场上存在，从墓地在自己场上特殊召唤的植物族怪兽的攻击力上升1000。这张卡在发动后第2次的自己的结束阶段时破坏。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87047074,'甲虫装机的魔弓 虫装弓箭','名字带有「甲虫装机」的怪兽才能装备。装备怪兽的攻击力上升500。对方不能对应装备怪兽的效果的发动把魔法·陷阱·效果怪兽的效果发动。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87047161,'人鱼鲨','这张卡召唤成功时，可以从卡组把1只3～5星的鱼族怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87102774,'黄金瓢虫','自己的准备阶段时，可以把手卡的这张卡给对方玩家观看，自己回复500基本分。这个效果使用的场合，直到结束阶段时把手卡的这张卡公开。这个效果1回合只能使用1次。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87106146,'伤害之门','自己受到战斗伤害时才能发动。把持有那个时候受到的伤害数值以下的攻击力的1只怪兽从自己墓地往场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87112784,'堕天使的追放',replace(replace('「堕天使的追放」在1回合只能发动1张。\r\n①：从卡组把「堕天使的追放」以外的1张「堕天使」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87116928,'嵌合巨舰龙',replace(replace('「电子龙」怪兽＋额外怪兽区域的怪兽1只以上\r\n把自己·对方场上的上记卡送去墓地的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作为融合素材。\r\n①：这张卡的原本攻击力变成作为这张卡的融合素材的怪兽数量×1200。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87118301,'灵兽使 蕾拉',replace(replace('自己对「灵兽使 蕾拉」1回合只能有1次特殊召唤。\r\n①：这张卡召唤成功的场合，以自己墓地1只「灵兽」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87148330,'光神 忒堤斯','①：自己抽卡时，那卡是天使族怪兽的场合，把那张卡给对方观看才能发动。这张卡在场上表侧表示存在的场合，自己从卡组抽1张。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87151205,'电气尾龙','总有电气缠绕的空中浮游之龙。从古代就存在，其生态还有很多的谜，但由于古之规则而禁止捕获。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87170768,'接触的G',replace(replace('（注：暂时无法正常使用）\r\n\r\n①：对方对怪兽的召唤·特殊召唤成功时才能发动。这张卡从手卡往对方场上守备表示特殊召唤。\r\n②：只要这张卡在怪兽区域存在，这张卡的控制者进行融合·同调·超量·连接召唤的场合，不是以这张卡为素材的融合·同调·超量·连接召唤不能进行。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87182127,'古代的机械魔神',replace(replace('「古代的机械」怪兽×2\r\n「古代的机械魔神」的②的效果1回合只能使用1次。\r\n①：这张卡不受其他卡的效果影响。\r\n②：自己主要阶段才能发动。给与对方1000伤害。\r\n③：这张卡被战斗破坏送去墓地的场合才能发动。从卡组把1只「古代的机械」怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87210505,'骑士的称号','①：把自己场上1只表侧表示的「黑魔术师」解放才能发动。从自己的手卡·卡组·墓地选1只「黑魔术骑士」特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87246309,'毛绒动物·章鱼',replace(replace('「毛绒动物·章鱼」的①②的效果1回合各能使用1次。\r\n①：这张卡召唤·特殊召唤成功时，以自己墓地1只「毛绒动物」怪兽或者「锋利小鬼」怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：这张卡成为「魔玩具」融合怪兽的融合召唤的素材送去墓地的场合，以除外的最多2只自己怪兽为对象才能发动。那些怪兽回到墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87255382,'纹章兽 双头蛇','自己的主要阶段时，从手卡把这张卡以外的1只名字带有「纹章兽」的怪兽丢弃才能发动。这张卡从手卡特殊召唤。此外，1回合1次，从手卡丢弃1只名字带有「纹章兽」的怪兽才能发动。这张卡的攻击力直到结束阶段时上升800。','特殊召唤','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87257460,'魅惑的女王 LV3','1回合只有1次在自己的主要阶段时可以选择对方场上的1只3星以下的怪兽，当作装备卡使用装备在这张卡上（这个效果可以装备的装备卡最多1张）。这张卡被战斗破坏的场合，装备的怪兽代替破坏。自己回合的准备阶段时，可以把因这个效果装备有装备卡的这张卡送去墓地，从手卡·卡组特殊召唤1只「魅惑的女王 LV5」。','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87259077,'闪电战士',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡战斗破坏对方怪兽送去墓地时，给与对方基本分对方手卡数量×300的数值的伤害。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87259933,'宝玉的集结',replace(replace('这张卡的①②的效果在同一连锁上不能发动。\r\n①：1回合1次，自己场上的表侧表示的「宝玉兽」怪兽被战斗·效果破坏的场合才能把这个效果发动。从卡组把1只「宝玉兽」怪兽特殊召唤。\r\n②：把魔法与陷阱区域的表侧表示的这张卡送去墓地，以自己场上1张「宝玉兽」卡和场上1张卡为对象才能把这个效果发动。作为对象的卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'发动但不使用效果','发动并使用②效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87288189,'怨邪帝 盖乌斯',replace(replace('这张卡可以把1只上级召唤的怪兽解放作上级召唤。\r\n①：这张卡上级召唤成功的场合，以场上1张卡为对象发动。那张卡除外，给与对方1000伤害。除外的卡是暗属性怪兽卡的场合，从那控制者的手卡·卡组·额外卡组·墓地把同名卡全部除外。这张卡把暗属性怪兽解放作上级召唤成功的场合，那个时候的效果加上以下效果。\r\n●这个效果的对象可以变成2张。','\r',char(13)),'\n',char(10)),'把1只上级召唤的怪兽解放作上级召唤','卡片除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87292536,'XX-剑士 雷吉格拉','①：这张卡召唤·特殊召唤成功时，以自己墓地1只「X-剑士」怪兽为对象才能发动。那只怪兽加入手卡。','墓地回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87294988,'红姬 知流姬','只要这张卡在场上表侧表示存在，对方不能选择其他的植物族怪兽作为攻击对象。此外，这张卡被对方送去墓地的场合，可以从卡组把「红姬 知流姬」以外的1只植物族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87303357,'光辉深渊','光与暗之力兼备的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87313164,'大吃鱼惊！','让从游戏中除外的1只自己的鱼族·海龙族·水族怪兽回到卡组发动。效果怪兽的效果的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87319876,'信赖守护者','把这张卡作为同调素材的场合，不是7星以上的同调怪兽的同调召唤不能使用。这张卡为同调素材的同调怪兽1回合只有1次不会被战斗破坏。这个效果适用的伤害步骤结束时，那只同调怪兽的攻击力·守备力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87322377,'TM-1火箭炮蜘蛛','开着火箭炮乱放一通，爆杀对手的机械蜘蛛。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87322378,'TM-1火箭炮蜘蛛','开着火箭炮乱放一通，爆杀对手的机械蜘蛛。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87340664,'原子萤火虫','战斗破坏以表侧表示存在于场上的这张卡的玩家受到1000点伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87347365,'再生石人',replace(replace('这张卡从卡组送去墓地时，从以下效果选择1个发动。「再生石人」的效果1回合只能使用1次。\r\n●这张卡从墓地特殊召唤。\r\n●这张卡从墓地加入手卡。','\r',char(13)),'\n',char(10)),'选择效果发动','从墓地把这张卡特殊召唤','从墓地把这张卡加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87350908,'假面斩杀者','这张卡战斗破坏对方怪兽的场合，给与对方基本分2000分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87390067,'黑羽-苍天之捷特','战斗伤害计算时，把这张卡从手卡送去墓地发动。自己场上存在的名字带有「黑羽」的怪兽不会被那次战斗破坏。','不被战斗破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87390798,'魔界台本「幻想魔法」',replace(replace('①：以自己场上1只「魔界剧团」怪兽为对象才能发动。这个回合，没被和那只怪兽的战斗破坏的怪兽在伤害步骤结束时回到持有者手卡。\r\n②：自己的额外卡组有表侧表示的「魔界剧团」灵摆怪兽存在，盖放的这张卡被对方的效果破坏的场合，以对方场上1张卡为对象才能发动。那张卡回到持有者卡组最上面。','\r',char(13)),'\n',char(10)),'「魔界台本「幻想魔法」」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87430304,'先史遗产 摩艾石像','自己场上有名字带有「先史遗产」的怪兽存在的场合，这张卡可以从手卡表侧守备表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87430998,'森','全部昆虫族·兽族·植物族·兽战士族的怪兽攻击力·守备力上升200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87460579,'花札卫-五光-',replace(replace('调整＋调整以外的怪兽4只\r\n①：1回合1次，对方把魔法·陷阱卡发动时才能发动。那个发动无效并破坏。\r\n②：自己的「花札卫」怪兽和对方怪兽进行战斗的场合，只在战斗阶段内那只对方怪兽的效果无效化。\r\n③：同调召唤的这张卡被战斗破坏的场合或者因对方的效果从场上离开的场合才能发动。从额外卡组把「花札卫-五光-」以外的1只「花札卫」同调怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87473172,'火炎鸟','只要这张卡在场上表侧表示存在，每次自己场上的鸟兽族怪兽被破坏，这张卡攻击力上升500。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87475570,'宝玉的先导者',replace(replace('←5 【灵摆】 5→\r\n①：只要这张卡在灵摆区域存在，自己场上的「究极宝玉神」怪兽以及「宝玉兽」卡不会成为对方的效果的对象。\r\n【怪兽效果】\r\n①：把这张卡解放才能发动。从卡组把「究极宝玉神」怪兽、「宝玉兽」怪兽、「宝玉」魔法·陷阱卡之内任意1张加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87483942,'素银忍者','这张卡反转时，可以从自己的手卡·墓地把「素银忍者」以外的名字带有「忍者」的怪兽任意数量里侧守备表示特殊召唤。「素银忍者」在场上只能有1只表侧表示存在。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87511987,'尖钉头','地狱魔术师制造的机器士兵，不断地用双手的铁球对敌人和友军发动无差别攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87514539,'傀儡虫','把这张卡从手卡丢弃去墓地发动。直到这个回合的结束阶段时，得到对方场上表侧表示存在的1只恶魔族或者不死族怪兽的控制权。','获得控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87523462,'有翼贤者 法尔科斯','被这张卡战斗破坏并将送去墓地的对方表侧攻击表示的怪兽，可以把其放回对方卡组最上面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87526784,'漏洞人X','这张卡召唤成功时，自己场上有「漏洞人Y」表侧表示存在的场合，可以从自己卡组把1只「漏洞人Z」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87535691,'超重武者 鼓-3',replace(replace('「超重武者 鼓-3」的效果1回合只能使用1次。\r\n①：场上的这张卡被破坏送去墓地的场合，以「超重武者 鼓-3」以外的自己墓地1只「超重武者」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87557188,'严格的老魔术师','①：这张卡反转的场合发动。场上盖放的卡全部确认。','确认盖卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87564352,'暗黑之龙王','生活在黑暗深处的龙，由于长期出活在暗处。视力不太好。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87564935,'金属化寄生生物-日之衣','1回合1次，自己的主要阶段时可以当作装备卡使用给自己场上的怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。只在这个效果当作装备卡使用的场合，装备怪兽不会成为对方的效果怪兽的效果的对象，不会被对方的效果怪兽的效果破坏。（1只怪兽可以装备的同盟最多1张。装备怪兽被战斗破坏的场合，作为代替把这张卡破坏。）','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87567063,'造反剧',replace(replace('「造反剧」在1回合只能发动1张。\r\n①：自己·对方的战斗阶段以对方场上1只怪兽为对象才能发动。那只怪兽的控制权直到战斗阶段结束时得到。这个回合，作为对象的怪兽以外的自己怪兽不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87588741,'机壳存取 残酷',replace(replace('←9 【灵摆】 9→\r\n①：自己不是「机壳」怪兽不能特殊召唤。这个效果不会被无效化。\r\n②：对方场上的怪兽的攻击力下降300。\r\n【怪兽效果】\r\n①：这张卡可以不用解放作召唤。\r\n②：特殊召唤或者不用解放作召唤的这张卡的等级变成4星，原本攻击力变成1800。\r\n③：通常召唤的这张卡不受原本的等级或者阶级比这张卡的等级低的怪兽发动的效果影响。\r\n④：把「机壳」怪兽解放对这张卡的上级召唤成功时才能发动。对方墓地的怪兽数量比自己墓地的怪兽多的场合，自己回复那个相差×300基本分，给与对方那个数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87602890,'轰雷帝 扎博尔格',replace(replace('这张卡可以把1只上级召唤的怪兽解放作上级召唤。\r\n①：这张卡上级召唤成功的场合，以场上1只怪兽为对象发动。那只怪兽破坏。破坏的怪兽是光属性的场合，双方各自从自己的额外卡组选那个原本的等级或者阶级数量的卡送去墓地。这张卡把光属性怪兽解放作上级召唤成功的场合，那个时候的效果加上以下效果。\r\n●送去墓地的对方的卡由自己来选。','\r',char(13)),'\n',char(10)),'把1只上级召唤的怪兽解放作上级召唤','怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87608852,'魔导召唤士 坦佩尔','自己把名字带有「魔导书」的魔法卡发动的自己回合的主要阶段时，把这张卡解放才能发动。从卡组把1只光属性或者暗属性的魔法师族·5星以上的怪兽特殊召唤。这个效果发动的回合，自己不能把其他的5星以上的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87609391,'猛禽究极硬头锤',replace(replace('「急袭猛禽」怪兽才能装备。\r\n①：装备怪兽的攻击力上升1000。\r\n②：装备怪兽被选择作为比装备怪兽攻击力高的怪兽的攻击对象时才能发动。从卡组把1张「升阶魔法」魔法卡加入手卡，那次战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87614611,'红莲魔龙之壶','自己场上有「红莲魔龙」表侧表示存在的场合才能发动。从自己卡组抽2张卡。这张卡发动的场合，直到下次的对方回合结束时自己不能把怪兽召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87621407,'魔装机关车 迪科伊奇','反转：抽1张卡。若自己场上存在表侧表示的「魔货物车辆 博科伊奇」的场合，再抽「魔货物车辆 博科伊奇」数量的卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87622767,'最终念力食人魔','这张卡战斗破坏对方怪兽的场合，可以支付800基本分选择自己墓地存在的1只念动力族怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87624166,'古之森','这张卡的发动时，场上有守备表示怪兽存在的场合，那些怪兽全部变成表侧攻击表示。这个时候，反转效果怪兽的效果不发动。此外，怪兽进行攻击的场合，进行攻击的怪兽在战斗阶段结束时破坏。','攻击怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87639778,'鹰身女妖的羽毛吹雪',replace(replace('自己场上有「鹰身」怪兽存在的场合，这张卡的发动从手卡也能用。\r\n①：自己场上有鸟兽族·风属性怪兽存在的场合才能发动。直到回合结束时，对方发动的怪兽的效果无效化。\r\n②：魔法与陷阱区域的这张卡被对方的效果破坏的场合才能发动。从自己的卡组·墓地选1张「鹰身女妖的羽毛扫」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87649699,'自然豚草','对方在抽卡阶段以外抽卡时，可以把自己场上表侧表示存在的这张卡送去墓地，从自己卡组抽2张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87685879,'树熊海獭','这张卡以外的兽族怪兽在自己场上表侧表示存在的场合，可以把对方场上表侧表示存在的1只怪兽的攻击力直到结束阶段时变成0。这个效果1回合只能使用1次。','攻击力变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87751584,'机炮龙','「左轮手枪龙」＋「回膛手枪龙」。投掷3个硬币。其中出现的与表侧硬币的数量同等数量的场上怪兽被破坏。这个效果1个回合1次，在自己的主要阶段使用。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87756343,'飞蛾幼虫','这张卡不能通常召唤。需要装备了「进化之茧」2回合（数自己的回合数）的「幼虫宝宝」做祭品特殊召唤上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87765315,'真龙的觉醒','①：「龙魔王」怪兽以及除灵摆怪兽以外的「龙剑士」怪兽在场上存在的场合才能发动。双方场上的卡全部回到持有者卡组。那之后，可以从卡组把1只「龙剑士」怪兽或者「龙魔王」怪兽无视召唤条件特殊召唤。','是否要把怪兽特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87769556,'魔术师的右手','①：1回合1次，自己场上有魔法师族怪兽存在的场合，对方发动的魔法卡的效果无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87772572,'量子猫','①：宣言种族和属性各1个才能发动。这张卡发动后变成持有宣言的种族·属性的通常怪兽（4星·攻0/守2200）在怪兽区域特殊召唤。这张卡也当作陷阱卡使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87774234,'棉花球','这张卡被魔法·陷阱·效果怪兽的效果从自己卡组加入手卡的场合，这张卡可以在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87796900,'守城的翼龙','守护山寨的龙，从空中急速下降攻击敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87798440,'铠甲破坏士','1回合只有1次在自己的主要阶段可以当作装备卡使用给自己场上的战士族怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。这个效果当作装备卡使用的场合才能，装备怪兽给与对方基本分战斗伤害时，把场上1张卡破坏。（1只怪兽可以装备的同盟最多1张。装备怪兽被战斗破坏的场合，作为代替把这张卡破坏。）','装备','特殊召唤','破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87819421,'假面充能','①：以自己墓地1只「英雄」怪兽和1张「变化」速攻魔法卡为对象才能发动。那些卡加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87835759,'创世之龙骑士',replace(replace('「创世之龙骑士」的③的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，这张卡的等级在对方回合内上升4星。\r\n②：这张卡战斗破坏对方怪兽送去墓地时才能发动。从卡组把1只7·8星的龙族怪兽送去墓地。\r\n③：把1张手卡送去墓地，以自己墓地1只7·8星的龙族怪兽为对象才能发动。这张卡送去墓地，作为对象的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87836938,'大日前锋','这张卡被卡的效果破坏送去墓地时，可以从自己卡组抽出这张卡装备的装备卡数量的卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87880531,'扩散的波动','支付1000基本分。选择自己场上1只7星以上的魔法师族怪兽。本回合中，只有所选择的这只怪兽可以进行攻击，可以攻击对方场上所有怪兽各1次。被此攻击破坏的效果怪兽的效果不发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87890143,'世代之力','自己场上有超量怪兽存在的场合才能发动。从卡组把1张名字带有「超量」的卡加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87902575,'未来视界','自己或者对方对怪兽的召唤成功时，那1只怪兽从游戏中除外。从召唤怪兽的控制者来看的下次的自己的准备阶段时，这张卡的效果除外的怪兽表侧攻击表示回到场上。','除外','返回场上','请选择要回到场上的卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87910978,'洗脑','①：支付800基本分，以对方场上1只可以通常召唤的表侧表示怪兽为对象才能发动。那只表侧表示怪兽的控制权直到结束阶段得到。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87911394,'混沌No.39 希望皇 霍普雷·胜光',replace(replace('5星怪兽×3\r\n这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。此外，这张卡有名字带有「希望皇 霍普」的怪兽在作为超量素材的场合，得到以下效果。\r\n●这张卡向对方的表侧表示怪兽攻击宣言时，把这张卡1个超量素材取除才能发动。直到回合结束时，那只对方怪兽的效果无效化，这张卡的攻击力上升那只对方怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87917187,'暗黑之召唤神',replace(replace('「暗黑之召唤神」的①的效果1回合只能使用1次。\r\n①：把这张卡解放才能发动。「神炎皇 乌利亚」「降雷皇 哈蒙」「幻魔皇 拉比艾尔」的其中1只从手卡·卡组无视召唤条件特殊召唤。这个回合，自己怪兽不能攻击。\r\n②：把墓地的这张卡除外才能发动。从卡组把「神炎皇 乌利亚」「降雷皇 哈蒙」「幻魔皇 拉比艾尔」的其中1只加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87973893,'甲虫装机的魔斧 虫装战斧','名字带有「甲虫装机」的怪兽才能装备。装备怪兽的攻击力上升1000。装备怪兽的攻击宣言时，对方不能把魔法·陷阱卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87978805,'寄生体 壁虱','这张卡的攻击力·守备力是场上的衍生物的数量×500的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87979586,'曼陀罗天使号手','如天使般的美丽之花。不断产生雾来迷惑侵入者，不允许进入到圣兽们居住的森林最深处。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87990236,'魅惑的堕天使',replace(replace('「魅惑的堕天使」在1回合只能发动1张。\r\n①：从手卡以及自己场上的表侧表示怪兽之中把1只「堕天使」怪兽送去墓地才能发动。选对方场上1只表侧表示怪兽直到结束阶段得到控制权。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(87997872,'斯芬克斯·安德鲁吉尼斯','这张卡不能通常召唤。自己场上的「斯芬克斯·安德鲁」和「斯芬克斯·迪蕾雅」同时被破坏时，支付500基本分才能从手卡或者卡组特殊召唤。这张卡特殊召唤成功时，可以支付500基本分，直到结束阶段结束时这张卡的攻击力上升3000。','特殊召唤','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88032368,'二次推进士','把这张卡解放，选择自己场上表侧攻击表示存在的1只怪兽发动。选择的怪兽的攻击力直到结束阶段时上升1500。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88032456,'复制猫',replace(replace('「复制猫」在1回合只能发动1张。\r\n①：自己场上有「卡通世界」以及卡通怪兽存在的场合，以对方墓地1张卡为对象才能发动。那张卡是怪兽的场合，那只怪兽在自己场上特殊召唤。那张卡是魔法·陷阱卡的场合，那张卡在自己场上盖放。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88033975,'幻层守护者 阿玛迪斯',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡进行战斗的场合，对方直到伤害步骤结束时魔法·陷阱·效果怪兽的效果不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88069166,'奥丁之眼','1回合1次，双方的准备阶段时选择自己场上表侧表示存在的1只名字带有「极神」的怪兽才能发动。选择的怪兽的效果直到结束阶段时无效，把对方手卡以及对方场上盖放的卡全部确认。对方不能对应这个效果的发动把魔法·陷阱·效果怪兽的效果发动。','确认手卡和盖卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88069597,'捕食花盆',replace(replace('这张卡的控制者在每次自己准备阶段支付800基本分。或者不支付基本分让这张卡破坏。\r\n①：1回合1次，自己主要阶段才能发动。从自己的手卡·墓地选1只4星以下的「捕食植物」怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'「捕食植物」怪兽特殊召唤','是否支付800基本分？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88071625,'暴君海王星','这张卡不能特殊召唤。这张卡可以把1只怪兽解放作上级召唤。这张卡的攻击力·守备力上升上级召唤时解放的怪兽的原本的攻击力·守备力各自合计数值。这张卡上级召唤成功时，选择墓地存在的1只解放的效果怪兽，当作和那只怪兽同名卡使用，得到相同效果。','把1只怪兽解放召唤','效果复制','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88086137,'直通断线','①：和这张卡相同纵列有怪兽的效果·魔法·陷阱卡发动时才能把盖放的这张卡发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88089103,'四次元之墓','选择自己墓地存在的2只名字含有「LV」的怪兽，加入到自己卡组并且洗切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88095331,'进化虫·乃者史蛇','场上的这张卡被解放的场合，可以从卡组把1只名字带有「进化龙」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88120966,'No.15 机关傀儡-巨人杀手',replace(replace('8星怪兽×2\r\n自己的主要阶段1把这张卡1个超量素材取除，选择对方场上1只特殊召唤的怪兽才能发动。选择的怪兽破坏。破坏的怪兽是超量怪兽的场合，再给与对方基本分那只怪兽的原本攻击力数值的伤害。这个效果1回合可以使用最多2次。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88123329,'精灵兽 心企太',replace(replace('自己对「精灵兽 心企太」1回合只能有1次特殊召唤。\r\n①：1回合1次，自己主要阶段才能发动。从额外卡组把1只「灵兽」怪兽除外，和那只怪兽相同种族的1只「灵兽」怪兽从卡组送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88132637,'双头地狱犬','自己的场上有这张卡以外的其他恶魔族怪兽表侧表示存在的场合，这张卡战斗破坏的反转效果怪兽的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88177324,'No.107 银河眼时空龙',replace(replace('8星怪兽×2\r\n自己的战斗阶段开始时1次，把这张卡1个超量素材取除才能发动。这张卡以外的场上表侧表示存在的全部怪兽的效果无效化，那个攻击力·守备力变成原本数值。这个效果适用的回合的战斗阶段中每次对方的卡的效果发动，这张卡的攻击力直到战斗阶段结束时上升1000，这个回合，这张卡在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88190453,'链击者','这张卡可以在通常的攻击之外再增加和自己墓地存在的「链击者」相同的数目，在同1次的战斗阶段中进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88190790,'强袭气铠','自己的怪兽卡区域只有1只战士族怪兽存在的场合，才能给那只怪兽装备。装备怪兽的攻击力上升300。可以把装备的这张卡送去墓地，这个回合装备怪兽在同1次的战斗阶段中可以作2次攻击。','多次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88197162,'魂之转身',replace(replace('「魂之转身」在1回合只能发动1张，这张卡发动的回合，自己不能把怪兽特殊召唤。\r\n①：自己场上没有特殊召唤的怪兽存在的场合，把自己场上1只通常召唤的表侧表示的4星怪兽解放才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88204302,'疾行再生',replace(replace('①：以自己墓地1只「疾行机人」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：把墓地的这张卡除外，以自己墓地1只「疾行机人」怪兽为对象才能发动。那只怪兽加入手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88205593,'电气眼镜王蛇','这张卡可以直接攻击对方玩家。这张卡直接攻击给与对方基本分战斗伤害时，从自己卡组把1只名字带有「电气」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88234365,'堕天使 特斯卡特利波卡',replace(replace('自己对「堕天使 特斯卡特利波卡」1回合只能有1次特殊召唤，那个作为②的效果1回合只能使用1次。\r\n①：自己场上的「堕天使」怪兽被战斗·效果破坏的场合，可以作为代替把手卡的这张卡丢弃。\r\n②：支付1000基本分，以自己墓地1张「堕天使」魔法·陷阱卡为对象才能发动。那张魔法·陷阱卡的效果适用。那之后，墓地的那张卡回到卡组。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'是否使用「堕天使 特斯卡特利波卡」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88236094,'阿斯旺的亡灵','这张卡对对方造成战斗伤害时，可以将自己墓地里的1张陷阱卡弹回卡组最上面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88240808,'灵灭术师 海空','每次这张卡战斗造成对方基本分伤害，可以选对方墓地2只怪兽除外。只要这张卡在场上存在，对方不能把墓地的卡从游戏中除外。','选对方墓地2只怪兽除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88240999,'决战兵器之影灵衣',replace(replace('「影灵衣」仪式魔法卡降临。不是只使用除10星以外的怪兽来仪式召唤不能特殊召唤。「决战兵器之影灵衣」的①②的效果1回合各能使用1次。\r\n①：把这张卡从手卡丢弃，以自己场上1只「影灵衣」怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时上升1000。这个效果在对方回合也能发动。\r\n②：以对方场上盖放的1张卡为对象才能发动。那张卡破坏并除外。','\r',char(13)),'\n',char(10)),'攻守上升','破坏并除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88241506,'青色眼睛的少女',replace(replace('「青色眼睛的少女」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：场上的表侧表示的这张卡成为效果的对象时才能发动。从自己的手卡·卡组·墓地选1只「青眼白龙」特殊召唤。\r\n②：这张卡被选择作为攻击对象时才能发动。那次攻击无效，这张卡的表示形式变更。那之后，可以从自己的手卡·卡组·墓地选1只「青眼白龙」特殊召唤。','\r',char(13)),'\n',char(10)),'攻击无效','特殊召唤','是否要选择「青眼白龙」特殊召唤？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88264978,'真红眼暗钢龙',replace(replace('①：这张卡可以把自己场上1只表侧表示的龙族怪兽除外，从手卡特殊召唤。\r\n②：1回合1次，自己主要阶段才能发动。从自己的手卡·墓地选「真红眼暗钢龙」以外的1只龙族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88279736,'哥布林拦路贼','自己场上的怪兽每次造成对方玩家的战斗伤害时，对方随机丢弃1张手卡。','对方随机丢弃1张手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88283496,'极星兽 加尔姆','这张卡和4星以下的怪兽进行战斗的伤害计算后，可以让那只怪兽回到手卡。','返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88289295,'同调控制','这张卡在自己场上以及自己墓地没有同调怪兽存在的场合才能发动。支付1000基本分，选择对方场上表侧表示存在的1只同调怪兽发动。直到自己的结束阶段时，得到选择的怪兽的控制权。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88301393,'狱火机·亚得米勒',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把2只「狱火机」怪兽除外的场合才能从手卡·墓地特殊召唤。\r\n①：这张卡战斗破坏对方怪兽送去墓地时才能发动。这张卡只再1次可以继续攻击。\r\n②：1回合1次，把自己场上1只怪兽解放，以对方墓地1张卡为对象才能发动。那张卡除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'连续攻击','对方墓地的卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88301833,'升华之魂','仪式怪兽仪式召唤成功时，可以选择那次仪式召唤解放的自己墓地存在的1只怪兽加入手卡。这个效果1回合只能使用1次。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88305705,'勇猛眼灵摆龙',replace(replace('「灵摆龙」怪兽＋战士族怪兽\r\n①：这张卡融合召唤成功时才能发动。对方场上的全部怪兽的攻击力变成0。这个回合，这张卡以外的自己怪兽不能攻击。\r\n②：只要这张卡在怪兽区域存在，攻击力0的怪兽发动的效果无效化。\r\n③：这张卡的攻击没让对方怪兽被破坏的伤害步骤结束时才能发动。那只对方怪兽除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88305978,'黑羽-阳炎之卡姆','对方的战斗阶段时，自己场上没有怪兽存在的场合，把墓地存在的这张卡从游戏中除外才能发动。选择自己墓地存在的1只同调怪兽特殊召唤。这个效果特殊召唤的怪兽在战斗阶段结束时从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88307361,'超古深海王 空棘鱼',replace(replace('①：1回合1次，丢弃1张手卡才能发动。从卡组把4星以下的鱼族怪兽尽可能特殊召唤。这个效果特殊召唤的怪兽不能攻击宣言，效果无效化。\r\n②：场上的这张卡为对象的魔法·陷阱·怪兽的效果发动时，把这张卡以外的自己场上1只鱼族怪兽解放才能发动。那个效果无效并破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','效果无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88316955,'机怪虫·树突棘虫',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡反转的场合，以场上1只怪兽为对象才能发动。那只怪兽破坏。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合才能发动。「机怪虫·树突棘虫」以外的2只「机怪虫」怪兽从卡组里侧守备表示特殊召唤（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'怪兽破坏','2只怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88341502,'黑翼箭矢','选择自己场上表侧表示存在的1只怪兽发动。直到结束阶段时，那只怪兽的攻击力下降500，攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。选择怪兽战斗破坏怪兽送去墓地时，给与对方基本分破坏怪兽的原本守备力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88358139,'娱乐伙伴 帮助公主','①：自己对「娱乐伙伴 帮助公主」以外的「娱乐伙伴」怪兽的召唤·特殊召唤成功时才能发动。这张卡从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88361177,'龙骑兵团-窄带军事官','这张卡有名字带有「龙骑兵团」的龙族怪兽装备的场合，这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88369727,'愚蠢的转生','选择对方墓地存在的1张卡发动。选择的卡回到卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88392300,'电送拟人 电气念动人','①：把手卡·场上的这张卡送去墓地，以对方场上1只怪兽为对象才能发动。那只对方怪兽的位置向其他的对方的主要怪兽区域移动。这个效果在对方回合也能发动。','请选择要移动位置的怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88406570,'刚鬼 毁灭食人魔',replace(replace('「刚鬼」怪兽2只以上\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡所连接区的怪兽在1回合各有1次不会被战斗破坏。\r\n②：自己主要阶段才能发动。对方从自身墓地选最多2只怪兽特殊召唤。那之后，自己从自己墓地选最多有特殊召唤的怪兽数量的连接怪兽以外的「刚鬼」怪兽在这张卡所连接区特殊召唤。这个效果的发动后，直到回合结束时自己不是「刚鬼」怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88409165,'深渊战士','1回合1次，从手卡把1只水属性怪兽丢弃去墓地，选择自己或者对方的墓地1只怪兽才能发动。选择的怪兽回到持有者卡组最上面或者最下面。','返回卡组','回到卡组最上面','回到卡组最下面','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88412339,'魔界剧团-临时演员',replace(replace('←3 【灵摆】 3→\r\n「魔界剧团-临时演员」的灵摆效果1回合只能使用1次。\r\n①：对方场上有怪兽存在的场合才能发动。灵摆区域的这张卡特殊召唤。\r\n【怪兽效果】\r\n「魔界剧团-临时演员」的怪兽效果1回合只能使用1次。\r\n①：把这张卡解放才能发动。从卡组选1只「魔界剧团」灵摆怪兽在自己的灵摆区域放置。这个效果的发动后，直到回合结束时自己不是「魔界剧团」怪兽不能特殊召唤，不能把「魔界剧团-临时演员」的灵摆效果发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88435542,'火眼','被火炎包围着的眼球。挥动翅膀就能引起火炎之风。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88438982,'迪米克雷斯异虫','反转：只要这张卡在场上表侧表示存在，这张卡的攻击力·守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88472456,'暗黑英雄 尸魔侠','这张卡不可以直接攻击玩家。这张卡每次战斗破坏1只怪兽，这张卡的攻击力下降200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88482761,'掷骰战斗',replace(replace('①：对方怪兽的攻击宣言时以自己墓地1只「疾行机人」怪兽为对象才能发动。那只怪兽和手卡1只「疾行机人」调整除外，把持有和那2只的原本等级合计相同等级的1只同调怪兽从额外卡组特殊召唤。\r\n②：对方战斗步骤把墓地的这张卡除外，以自己以及对方场上的表侧攻击表示的同调怪兽各1只为对象才能发动。那只对方的表侧攻击表示怪兽向那只自己怪兽作出攻击，进行伤害计算。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88494120,'不屈的斗志','①：自己场上的表侧表示怪兽只有1只的场合，以那1只怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升对方场上的攻击力最低的怪兽的攻击力数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88494899,'冰结界的交灵师','对方场上存在的卡比自己场上存在的卡的数量多4张以上的场合，这张卡可以从手卡特殊召唤。只要这张卡在场上表侧表示存在，对方玩家1回合魔法·陷阱卡只能发动1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88513608,'舍身的宝札','自己场上表侧攻击表示存在的怪兽2只以上的攻击力合计比对方场上表侧表示存在的攻击力最低的怪兽低的场合，从自己卡组抽2张卡。这张卡发动的回合，自己不能把怪兽召唤·反转召唤·特殊召唤，也不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88552992,'先史遗产 黄金航天飞机','1回合1次，自己的主要阶段时才能发动。自己场上的全部名字带有「先史遗产」的怪兽的等级上升1星。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88559132,'炮塔战士','这张卡可以把自己场上存在的1只战士族怪兽解放，从手卡特殊召唤。这个方法特殊召唤的这张卡的攻击力上升解放怪兽的原本攻击力的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88581108,'真龙皇 法·王·兽',replace(replace('9星怪兽×2只以上\r\n①：1回合1次，把这张卡1个超量素材取除，宣言1个属性才能发动。直到回合结束时，场上的表侧表示怪兽变成宣言的属性，宣言的属性的对方怪兽不能把效果发动，也不能攻击。这个效果在对方回合也能发动。\r\n②：只要这张卡在怪兽区域存在，自己手卡的「真龙」怪兽的效果破坏的怪兽从对方场上也能选。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88610708,'盾牌猛击',replace(replace('通常召唤的怪兽才能装备。\r\n①：装备怪兽的攻击力上升1000。\r\n②：装备怪兽的战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88616795,'律法之魔导书',replace(replace('可以选择场上1只魔法师族怪兽，从以下效果选择1个发动。\r\n●这个回合，选择的怪兽不受这张卡以外的魔法卡的效果影响。\r\n●这个回合，选择的怪兽不受陷阱卡的效果影响。','\r',char(13)),'\n',char(10)),'选择的怪兽不受这张卡以外的魔法卡的效果影响','选择的怪兽不受陷阱卡的效果影响','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88619463,'黑魔法神官',replace(replace('这张卡不能通常召唤。把自己场上2只6星以上的魔法师族怪兽解放的场合才能特殊召唤。\r\n①：陷阱卡发动时才能发动。这张卡在场上表侧表示存在的场合，那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'陷阱发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88643173,'梦魇蝎','造出恶梦，并在呻吟的时候用四条有毒的尾巴刺向敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88643579,'暗黑终结龙',replace(replace('调整＋调整以外的暗属性怪兽1只以上\r\n1回合1次，可以把这张卡的攻击力·守备力下降500，对方场上存在的1只怪兽送去墓地。','\r',char(13)),'\n',char(10)),'对方场上存在的1只怪兽送去墓地。','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88650530,'阿波克立普斯异虫','反转：场上的1张魔法或者陷阱卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88671720,'黑色炸弹球','这张卡召唤成功时，可以把自己墓地存在的1只机械族·暗属性的4星怪兽表侧守备表示特殊召唤。这个效果特殊召唤的效果怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88686573,'迅捷鮟鱇','这张卡从手卡·卡组送去墓地的场合，可以从卡组把「迅捷鮟鱇」以外的最多2只3星以下的名字带有「迅捷」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88696724,'融合咒印生物-地',replace(replace('①：这张卡可以作为融合怪兽卡有卡名记述的1只融合素材怪兽的代替。那个时候，其他的融合素材怪兽必须是正规品。\r\n②：把地属性融合怪兽卡决定的一组融合素材怪兽（这张卡作为那之内的1只）从自己场上解放才能发动。把以那些解放的怪兽为融合素材的1只地属性融合怪兽从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88722973,'升龙剑士 威风星·圣骑',replace(replace('4星的灵摆怪兽×2\r\n①：这张卡超量召唤成功时才能发动。这个回合的结束阶段，从卡组把1只灵摆怪兽加入手卡。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。从自己的额外卡组把1只表侧表示的「龙剑士」灵摆怪兽特殊召唤。这个效果特殊召唤的怪兽不能作为超量召唤的素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88724332,'夜龙巫妖',replace(replace('①：只要这张卡在怪兽区域存在，幻龙族以外的从卡组·额外卡组特殊召唤的攻击表示怪兽变成守备表示。\r\n②：只要这张卡在怪兽区域存在，幻龙族以外的从卡组·额外卡组特殊召唤的怪兽的守备力下降那个原本守备力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88728507,'吸血鬼巫师',replace(replace('①：这张卡被对方送去墓地的场合才能发动。从卡组把1只暗属性「吸血鬼」怪兽或者1张「吸血鬼」魔法·陷阱卡加入手卡。\r\n②：把墓地的这张卡除外才能发动。这个回合只有1次，自己在5星以上的暗属性「吸血鬼」怪兽召唤的场合需要的解放可以不用。','\r',char(13)),'\n',char(10)),'检索','减少解放数量','不解放进行召唤（吸血鬼巫师）','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88733579,'穿孔虫','这张卡给与对方战斗伤害的时候，从自己的卡组取出1张「寄生虫 帕拉赛德」，之后在卡组洗切后放在卡组最上面。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88753985,'狐火','表侧表示存在的这张卡被战斗破坏的回合的结束阶段时，这张卡从墓地特殊召唤到自己场上。这张卡不能用作祭品召唤的祭品。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88754763,'混沌超量 热血指导神 究极教练',replace(replace('9星怪兽×4\r\n场上的这张卡不会成为卡的效果的对象。此外，这张卡有超量怪兽在作为超量素材的场合，得到以下效果。\r\n●1回合1次，把这张卡1个超量素材取除才能发动。从卡组抽1张卡，给双方确认。确认的卡是怪兽的场合，再给与对方基本分800分伤害。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88757791,'法眼之魔术师',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，把手卡1只灵摆怪兽给对方观看，以自己的灵摆区域1张「魔术师」卡为对象才能发动。那张「魔术师」卡的灵摆刻度直到回合结束时变成和给人观看的灵摆怪兽的灵摆刻度相同。\r\n【怪兽效果】\r\n①：只要这个回合灵摆召唤的这张卡在怪兽区域存在，自己场上的「魔术师」灵摆怪兽不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88760522,'多样进化','卡组把1只名字带有「进化虫」或者「进化龙」的怪兽加入手卡。「多样进化」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88789641,'神圣生命防护罩','丢弃1张手卡。这张卡发动的回合，对方造成的伤害全部为0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88819587,'宝贝龙','不可轻视的幼龙，说不定隐藏着什么力量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88820235,'元素英雄 闪光凤凰人',replace(replace('「元素英雄 凤凰人」＋「元素英雄 电光侠」\r\n这只怪兽不能作融合召唤以外的特殊召唤。自己墓地每有1张名字带有「元素英雄」的卡，这张卡的攻击力上升300。这张卡不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88845345,'飞象','这张卡被战斗破坏送去墓地时，可以选择从游戏中除外的1只自己的3星以下的兽族·兽战士族·鸟兽族怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88851326,'炽热的决斗者们',replace(replace('①：自己怪兽的攻击宣言时，以对方场上1张魔法·陷阱卡为对象才能发动。那次攻击无效，作为对象的卡破坏。\r\n②：只要这张卡在魔法与陷阱区域存在，双方1回合只能有1张魔法·陷阱卡从手卡盖放，从额外卡组特殊召唤的怪兽在那个回合不能攻击。\r\n③：自己抽卡阶段的抽卡前才能发动。作为这个回合进行通常抽卡的代替，选自己墓地1只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88901771,'熟练的青魔道士',replace(replace('①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物（最多3个）。\r\n②：把有3个魔力指示物放置的这张卡解放才能发动。从自己的手卡·卡组·墓地选1只「暗黑骑士 盖亚」怪兽特殊召唤。\r\n③：自己主要阶段把墓地的这张卡除外，以自己场上1张可以放置魔力指示物的卡为对象才能发动。给那张卡放置1个魔力指示物。','\r',char(13)),'\n',char(10)),'放置1个魔力指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88928798,'炸弹防御','持有「把自己场上存在的1只怪兽破坏的效果」的卡发动时才能发动。那个发动无效并破坏。并且再给与对方基本分500分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88935103,'贵龙之魔术师',replace(replace('←5 【灵摆】 5→\r\n①：另一边的自己的灵摆区域没有「魔术师」卡存在的场合这张卡破坏。\r\n【怪兽效果】\r\n把这张卡作为同调素材的场合，不是龙族怪兽的同调召唤不能使用，其他的同调素材有使用除「异色眼」怪兽以外的怪兽的场合，这张卡回到持有者卡组最下面。\r\n①：这张卡在手卡·墓地存在的场合，以自己场上1只7星以上的「异色眼」怪兽为对象才能发动。那只怪兽的等级下降3星，这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'在灵摆区发动','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88940154,'武神器-蜂','自己的主要阶段时，自己场上有名字带有「武神」的兽战士族怪兽存在的场合，把墓地的这张卡从游戏中除外才能发动。选择对方场上1张魔法·陷阱卡破坏。「武神器-蜂」的效果1回合只能使用1次。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88942504,'管魔人 迷迷旋律',replace(replace('3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择自己场上1只名字带有「魔人」的超量怪兽才能发动。这个回合，选择的怪兽在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'多次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88958576,'熔岩焚烧者','自己墓地有名字带有「熔岩」的怪兽3种类以上存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88975532,'漆黑的战士 狼人','①：只要这张卡在怪兽区域存在，对方在战斗阶段不能把陷阱卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88979991,'杀人蜂','巨大的黄蜂，攻击出乎意料的强，若被群体攻击则非常危险。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(88989706,'大神官 迪·扎德',replace(replace('根据战斗破坏怪兽的次数得到下面的效果：\r\n●1次：只要这张卡在场上表侧表示存在，以这张卡为对象的魔法·陷阱卡的发动和效果无效并破坏。\r\n●2次：可以把这张卡做祭品，从自己的手卡·卡组选1只「不死王 巫妖」特殊召唤到场上。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89015998,'异次元的探求者',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽直到下个回合的结束阶段除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89040386,'黑羽-反闪','自己墓地有名字带有「黑羽」的怪兽5只以上存在的场合，对方怪兽的直接攻击宣言时才能发动。对方场上存在的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89041555,'牲祭怀抱的爆弹','①：上级召唤的对方怪兽的攻击宣言时才能发动。对方场上的表侧攻击表示怪兽全部破坏，给与对方1000伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89055154,'力天使 女武神',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：自己把怪兽的效果·魔法·陷阱卡的发动无效的场合发动。从卡组把1只天使族·光属性怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89086566,'机雷化','自己场上表侧表示存在的「栗子球」以及「栗子球衍生物」全部破坏。那之后，选最多有和破坏数量相同数量的对方场上的卡破坏。','是否要破坏对方场上的卡？','','','','','','','','','','','','','','','');
