INSERT INTO texts VALUES(89091772,'超重武者 磁石-Q',replace(replace('①：这张卡召唤成功时才能发动。从手卡把1只4星以下的「超重武者」怪兽特殊召唤。那之后，这张卡变成守备表示。\r\n②：只要这张卡在怪兽区域存在，对方不能向其他怪兽攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89111398,'沙尘之恶灵','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到主人的手卡。这张卡召唤·反转时，场上的这张卡以外的全部表侧表示的怪兽破坏。','回到手牌','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89112729,'机械恐龙','「炸弹先生」＋「双头恐龙王」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89113320,'娱乐伙伴 鱼饵大头龟',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，自己主要阶段才能发动。手卡1只「娱乐伙伴」怪兽或者「异色眼」怪兽给对方观看。这个回合，那只怪兽以及自己手卡的同名怪兽的等级下降1星。\r\n【怪兽效果】\r\n①：这张卡被战斗破坏时才能发动。把让这张卡破坏的怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89127526,'护罩共鸣者','把这张卡从手卡送去墓地，选择自己场上表侧表示存在的1只调整发动。选择的怪兽在这个回合不会被战斗破坏，选择的怪兽的战斗发生的对自己的战斗伤害变成0。这个效果在对方回合也能发动。','战斗破坏耐性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89181134,'捕食植物 茅膏菜伞蜥',replace(replace('「捕食植物 茅膏菜伞蜥」的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，自己要作为融合素材的有捕食指示物放置的怪兽的属性当作暗属性使用。\r\n②：自己主要阶段才能发动。暗属性融合怪兽卡决定的包含场上的这张卡的融合素材怪兽从自己的手卡·场上以及对方场上的有捕食指示物放置的怪兽之中选出送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89181369,'星屑的辉煌','选择自己墓地存在的1只龙族的同调怪兽发动。直到变成和那只怪兽的等级相同等级为止，把选择的怪兽以外的自己墓地存在的怪兽从游戏中除外，选择的怪兽从墓地特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89185742,'风征龙-霆龙','把1只龙族或者风属性的怪兽和这张卡从手卡丢弃才能发动。从卡组把1只「岚征龙-飙龙」特殊召唤。这个效果特殊召唤的怪兽在这个回合不能攻击。「风征龙-霆龙」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89189982,'玄化武装龙','崇高的存在犹如虚幻。那龙伫立于巅峰之上。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89194033,'圣兽 塞勒凯特',replace(replace('①：自己场上没有「王家的神殿」存在的场合这张卡破坏。\r\n②：只要这张卡在怪兽区域存在，这张卡战斗破坏的怪兽除外。\r\n③：这张卡战斗破坏怪兽的场合发动。这张卡的攻击力上升500。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89194103,'魔轰神兽 件',replace(replace('名字带有「魔轰神」的调整＋调整以外的怪兽1只以上\r\n这张卡得到自己手卡的数量的以下效果。\r\n●0张：这张卡不会被战斗以及卡的效果破坏。\r\n●3张以上：结束阶段时这张卡破坏。','\r',char(13)),'\n',char(10)),'这张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89208725,'虚拟世界','①：从卡组选1张场地魔法卡加入手卡或在自己场上发动。','请选择1张场地魔法卡','是否加入手卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89211486,'次世代博士','把自己场上表侧表示存在的1只「次世代控制员」解放发动。场上1张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89222931,'机械王-原型','场上存在的这张卡以外的机械族怪兽每有1只，这张卡的攻击力·守备力上升100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89235196,'大一战士','自己的主要阶段时，把这张卡以外的手卡1只1星怪兽送去墓地才能发动。这张卡从手卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89238128,'电子界加速人',replace(replace('衍生物以外的怪兽2只以上\r\n这个卡名的效果1回合只能使用1次。\r\n①：自己·对方的战斗阶段可以以这张卡所连接区1只电子界族怪兽为对象从以下效果选择1个发动。这个效果发动的回合，这张卡不能攻击。\r\n●那只怪兽的攻击力直到回合结束时上升2000。\r\n●这个回合，那只怪兽在同1次的战斗阶段中最多2次可以向怪兽攻击。','\r',char(13)),'\n',char(10)),'那只怪兽的攻击力直到回合结束时上升2000。','这个回合，那只怪兽在同1次的战斗阶段中最多2次可以向怪兽攻击。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89252153,'元素英雄 死灵暗侠','①：只在这张卡在墓地存在才有1次，自己可以把1只5星以上的「元素英雄」怪兽不用解放作召唤。','「元素英雄 死灵暗侠」的效果召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89252157,'蔷薇恋人',replace(replace('「蔷薇恋人」的效果1回合只能使用1次。\r\n①：把墓地的这张卡除外才能发动。从手卡把1只植物族怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不受对方的陷阱卡的效果影响。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89258225,'哈·迪斯的使魔','这张卡做祭品。选择场上表侧表示存在的1只恶魔族怪兽。只要那只怪兽表侧表示在场上存在，攻击力·守备力上升700。','攻守上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89258906,'黑羽-微风之布利兹','这张卡被魔法·陷阱·效果怪兽的效果从自己卡组加入手卡的场合，这张卡可以在自己场上特殊召唤。把这张卡作为同调素材的场合，不是名字带有「黑羽」的怪兽的同调召唤不能使用。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89272878,'冥界的番人','守护着冥界入口的战士，对侵略者豪不留情的斩杀。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89310929,'霞之谷的雷神鬼',replace(replace('调整＋调整以外的名字带有「霞之谷」的怪兽1只以上\r\n1回合1次，可以让自己场上存在的1张卡回到手卡，这张卡的攻击力直到结束阶段时上升500。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89312388,'元素英雄 棱镜侠','①：1回合1次，把额外卡组1只融合怪兽给对方观看，把那只怪兽有卡名记述的1只融合素材怪兽从卡组送去墓地才能发动。直到结束阶段，这张卡当作和为这个效果发动而送去墓地的怪兽同名卡使用。','变成同名卡','变成同名卡的效果结束','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89320376,'星遗物的傀儡',replace(replace('这个卡名的①②的效果1回合只能有1次使用其中任意1个。\r\n①：以自己场上1只里侧表示怪兽为对象才能发动。那只怪兽变成表侧攻击表示或者表侧守备表示。\r\n②：让自己墓地1只「机怪虫」怪兽回到卡组，以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示。','\r',char(13)),'\n',char(10)),'变成攻击表示','变成守备表示','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89326990,'疾行机人 弹子小子','①：这张卡召唤成功时，以自己或者对方的墓地1只调整为对象才能发动。那只怪兽在自己场上特殊召唤，只用那只怪兽和这张卡为素材把1只风属性的同调怪兽同调召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89333528,'次世代地矿人','这张卡被破坏的场合，可以作为代替把这张卡以外的1只名字带有「次世代控制员」的怪兽破坏。','是否要使用「次世代地矿人」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89355716,'晴之天气模样',replace(replace('①：「晴之天气模样」在自己场上只能有1张表侧表示存在。\r\n②：和这张卡相同纵列的自己的主要怪兽区域以及那些两邻的自己的主要怪兽区域存在的「天气」效果怪兽得到以下效果。\r\n●把这张卡除外，以自己场上1只怪兽为对象才能发动。那只自己怪兽解放，从自己的手卡·墓地选和那只怪兽卡名不同的1只「天气」怪兽特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'「天气」怪兽特殊召唤（晴之天气模样）','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89362180,'天生的白骨恐龙',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡的种族·属性变成恐龙族·地属性。\r\n●这张卡战斗破坏对方怪兽送去墓地时才能发动。那只怪兽在自己场上守备表示特殊召唤。这个效果特殊召唤的怪兽的种族变成不死族。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89386122,'正义盟军 辉剑鸟','霞之谷栖息的怪兽辉剑鸟为主题开发出的对外敌用战斗兵器。以从高空而来的奇袭玩弄敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89397517,'传说之心',replace(replace('「传说之心」在1回合只能发动1张。\r\n①：支付2000基本分，把自己场上1只战士族怪兽解放才能发动。自己的手卡·墓地的「传说之龙」魔法卡最多3种类除外，从自己的手卡·卡组·墓地选除外的种类数量的「传说的骑士」怪兽特殊召唤（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'是否继续除外？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89399912,'岚征龙-飙龙','从自己的手卡·墓地把这张卡以外的龙族或者风属性的怪兽合计2只除外才能发动。这张卡从手卡·墓地特殊召唤。特殊召唤的这张卡在对方的结束阶段时回到持有者手卡。此外，可以通过把这张卡和1只风属性怪兽从手卡丢弃去墓地，从卡组把1只龙族怪兽加入手卡。这张卡被除外的场合，可以从卡组把1只龙族·风属性怪兽加入手卡。「岚征龙-飙龙」的效果1回合只能使用1次。','从手卡特殊召唤','返回手牌','从卡组把1只龙族怪兽加入手卡','加入手卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(89405199,'欲望的幻象','因卡的效果进行抽卡的玩家，在那个回合的结束阶段终了时受到因卡的效果抽的卡的数量×500的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89423971,'狱火机·阿斯塔罗特',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把1只「狱火机」怪兽除外的场合才能从手卡特殊召唤。\r\n①：1回合1次，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。这个效果发动的回合，这张卡不能攻击。\r\n②：对方回合1次，把自己场上1只怪兽解放，以对方墓地1张卡为对象才能发动。那张卡除外。','\r',char(13)),'\n',char(10)),'魔陷破环','对方墓地的卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89448140,'魔术师的守护',replace(replace('①：只要自己场上有魔法师族怪兽存在，自己受到的全部伤害变成一半。\r\n②：这张卡从场上送去墓地的场合，以自己墓地1只魔法师族怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89462956,'庄家选择','①：双方玩家把卡组洗切，从卡组抽1张。那之后，双方玩家选1张手卡丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89463537,'尤尼科之影灵衣',replace(replace('「影灵衣」仪式魔法卡降临。这张卡不用仪式召唤不能特殊召唤。「尤尼科之影灵衣」的①的效果1回合只能使用1次。\r\n①：把这张卡从手卡丢弃，以「尤尼科之影灵衣」以外的自己墓地1张「影灵衣」卡为对象才能发动。那张卡加入手卡。\r\n②：只要这张卡在怪兽区域存在，从额外卡组特殊召唤的场上的表侧表示怪兽的效果无效。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89474727,'真闪珖龙 星尘·录',replace(replace('同调怪兽调整＋调整以外的同调怪兽1只以上\r\n这张卡不用同调召唤不能特殊召唤。\r\n①：1回合1次，把自己墓地1只同调怪兽除外才能发动。这张卡直到回合结束时不受其他卡的效果影响。这个效果在对方回合也能发动。\r\n②：这张卡被对方破坏的场合，以除外的1只自己的龙族同调怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'是否发动「真闪珖龙 星尘·录」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89493368,'召唤反应机·大式','只要这张卡在自己场上存在，对方场上有怪兽召唤、反转召唤、特殊召唤时，给与对方基本分800分伤害。这个效果1回合只能使用1次。这个效果使用的回合的战斗阶段时，可以把1只对方怪兽的攻击无效。可以把自己场上表侧表示存在的这张卡和「陷阱反应机·空式」「魔法反应机·袭式」各1只送去墓地，从自己的手卡·卡组·墓地把1只「巨人轰炸机·大空袭式」特殊召唤。','伤害','攻击无效','特殊召唤','','','','','','','','','','','','','');
