INSERT INTO texts VALUES(89563150,'白银防护罩 -银色之力-','对方把给与伤害的陷阱卡发动时才能发动。那个发动无效，那张卡和对方场上表侧表示存在的魔法·陷阱卡全部破坏。','','','','','','','','','','','','','','','','');
