INSERT INTO texts VALUES(89571015,'风暴密码人',replace(replace('①：这张卡不能直接攻击，不能向额外怪兽区域的怪兽攻击。\r\n②：这张卡不受额外怪兽区域的怪兽发动的效果影响，不会被和额外怪兽区域的怪兽的战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
