INSERT INTO texts VALUES(89642993,'No.63 饭勺战士',replace(replace('1星怪兽×2\r\n可以把这张卡1个超量素材取除，从以下效果选择1个发动。「No.63 饭勺战士」的效果1回合只能发动1次。\r\n●下次的对方的准备阶段开始时，双方玩家从卡组抽1张卡。\r\n●双方玩家回复1000基本分。','\r',char(13)),'\n',char(10)),'选择效果发动','双方玩家从卡组抽卡','双方玩家回复1000基本分','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89662736,'武神器-八咫','自己场上的名字带有「武神」的兽战士族怪兽被选择作为对方怪兽的攻击对象时，把这张卡从手卡送去墓地才能发动。攻击怪兽的攻击无效，给与对方基本分那只怪兽的攻击力一半数值的伤害。「武神器-八咫」的效果1回合只能使用1次。','攻击无效','','','','','','','','','','','','','','','');
