INSERT INTO texts VALUES(89718302,'暴走牛鬼',replace(replace('猜硬币正反。\r\n●猜中的场合：给与对方基本分1000分的伤害。\r\n●猜错的场合：自己基本分受到1000分的伤害。这个效果1回合只能1次，在自己回合的主要阶段使用。','\r',char(13)),'\n',char(10)),'猜硬币','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(89719143,'决战融合','①：自己场上的融合怪兽和对方场上的融合怪兽进行战斗的战斗步骤，以那2只融合怪兽为对象才能发动。那次攻击无效，双方玩家受到那2只融合怪兽的攻击力合计数值的伤害。','','','','','','','','','','','','','','','','');
