INSERT INTO texts VALUES(90011152,'扰乱之乡','1回合1次，可以从手卡把1张名字带有「扰乱」的卡送去墓地，自己墓地存在的1只名字带有「扰乱」的怪兽特殊召唤。只要自己场上有名字带有「扰乱」的怪兽表侧表示存在，场上表侧表示存在的全部怪兽的原本的攻击力·守备力交换。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90019393,'宝石骑士·紫翠','①：把这张卡解放才能发动。从卡组把1只「宝石骑士」通常怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90020065,'定时炸弹','反转：自己的准备阶段把这张卡作为祭品。全部自己的怪兽破坏，给与对方那个总攻击力一半数值的伤害。','反转效果','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90036274,'幻透翼疾速龙',replace(replace('←4 【灵摆】 4→\r\n「幻透翼疾速龙」的灵摆效果1回合只能使用1次。\r\n①：把等级合计直到7的自己场上的表侧表示的1只「疾行机人」调整和1只调整以外的怪兽送去墓地才能发动。灵摆区域的这张卡特殊召唤。\r\n【怪兽效果】\r\n调整＋调整以外的风属性怪兽1只以上\r\n「幻透翼疾速龙」的①的怪兽效果1回合只能使用1次。\r\n①：以从额外卡组特殊召唤的对方场上1只表侧表示怪兽为对象才能发动。直到回合结束时，那只怪兽的攻击力变成0，效果无效化。这个效果在对方回合也能发动。\r\n②：怪兽区域的这张卡被战斗·效果破坏的场合才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90075978,'W星云陨石','场上里侧表示存在的怪兽全部变成表侧守备表示。这个回合的结束阶段时自己场上表侧表示存在的爬虫族·光属性怪兽全部变成里侧守备表示，自己从卡组抽出那个数量的卡。那之后，可以从自己卡组把1只7星以上的爬虫族·光属性怪兽特殊召唤。','是否要特殊召唤7星以上的爬虫类族·光属性怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90098780,'键魔人 挠挠哼唱',replace(replace('3星怪兽×2\r\n①：这张卡特殊召唤成功时，以自己墓地1只「魔人」超量怪兽为对象才能发动。那只怪兽特殊召唤。那之后，可以把最多2张手卡在那只怪兽下面重叠作为超量素材。\r\n②：1回合1次，把这张卡1个超量素材取除，以自己场上1只「魔人」超量怪兽为对象才能发动。这个回合，那只怪兽可以向对方直接攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','直接攻击','是否要选择手卡在怪兽下面叠放？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90122655,'天魔大帝','①：只要这张卡在怪兽区域存在，对方不能对应通常召唤的怪兽的效果的发动把魔法·陷阱·怪兽的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90126061,'No.5 亡胧龙 死亡嵌合龙',replace(replace('5星怪兽×2只以上\r\n①：这张卡的攻击力上升这张卡的超量素材数量×1000。\r\n②：持有超量素材的这张卡可以向对方怪兽全部各作1次攻击。\r\n③：这张卡进行战斗的战斗阶段结束时可以从以下效果选择1个发动。\r\n●以自己墓地1只怪兽为对象才能发动。那只怪兽在这张卡下面重叠作为超量素材。\r\n●以对方墓地1张卡为对象才能发动。那张卡在对方卡组最上面放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90135989,'云魔物的阵雨','每次自己的准备阶段时给场上表侧表示存在的全部怪兽放置1个雾指示物。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90140980,'扰乱王',replace(replace('「扰乱·绿」＋「扰乱·黄」＋「扰乱·黑」\r\n只要这张卡在场上表侧表示存在，选择对方最多3个怪兽区域不能使用。','\r',char(13)),'\n',char(10)),'是否还要选择一个区域？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90147755,'炎之女暗杀者','反转：自己的卡组最上面的3张卡在游戏中除外。对方受到800分的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90156158,'星圣的超新生','选择自己墓地2只名字带有「星圣」的怪兽加入手卡。这张卡发动的回合，自己不能进行战斗阶段。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90162951,'No.35 极饿捕鸟蛛',replace(replace('10星怪兽×2\r\n①：只要这张卡在怪兽区域存在，自己场上的怪兽的攻击力·守备力上升自己和对方的基本分差的数值。\r\n②：只要持有超量素材的这张卡在怪兽区域存在，每次对方把怪兽特殊召唤给与对方600伤害。\r\n③：1回合1次，把这张卡1个超量素材取除才能发动。持有这张卡的攻击力以下的攻击力的对方场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'破坏怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90200789,'忍法 超变化之术','选择自己场上1只名字带有「忍者」的怪兽和对方场上表侧表示存在的1只怪兽发动。选择的怪兽送去墓地，把1只那个等级合计以下的龙族·恐龙族·海龙族怪兽从自己卡组往自己场上特殊召唤。这张卡从场上离开时，那只怪兽从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90219263,'万华镜-华丽的分身-','①：场上有「鹰身女郎」存在的场合才能发动。从手卡·卡组把1只「鹰身女郎」或者「鹰身女郎三姐妹」特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90238142,'鹰身通灵师','从手卡丢弃1张名字带有「鹰身」的卡才能发动。从卡组把「鹰身通灵师」以外的1只名字带有「鹰身」的怪兽表侧守备表示特殊召唤。「鹰身通灵师」的这个效果1回合只能使用1次。此外，自己场上有龙族怪兽存在的场合，这张卡的等级变成7星。这张卡的卡名只要在场上·墓地存在当作「鹰身女郎」使用。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90239723,'变形斗士·修复装置','从手卡把1只名字带有「变形斗士」的怪兽送去墓地，选择自己墓地存在的1只名字带有「变形斗士」的怪兽发动。选择的怪兽特殊召唤，把这张卡装备。装备怪兽不能把表示形式改变。这张卡不在场上存在时，装备怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90243945,'白骨公主',replace(replace('①：这张卡的卡名只要在墓地存在当作「白骨」使用。\r\n②：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1只「白骨王子」送去墓地。\r\n③：把自己的手卡·场上的这张卡送去墓地才能发动。场上的全部怪兽的攻击力·守备力直到回合结束时下降那等级或者阶级×300。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90246973,'动力镐','1回合1次，可以选择持有装备怪兽的等级以下的等级的对方墓地存在的1只怪兽从游戏中除外，直到结束阶段时装备怪兽的攻击力上升500。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90263923,'闪光吸收','选择对方场上表侧表示存在的1只光属性怪兽发动。自己场上表侧攻击表示存在的全部怪兽的攻击力直到结束阶段时上升选择的怪兽的攻击力数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90303176,'寂静鮟鱇','自己场上有水属性怪兽存在的场合，这张卡可以从手卡特殊召唤。这个回合自己不能从手卡把怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90307777,'影灵衣术士 施里特',replace(replace('「影灵衣术士 施里特」的②的效果1回合只能使用1次。\r\n①：「影灵衣」仪式怪兽1只仪式召唤的场合，可以用这1张卡作为仪式召唤需要的等级数值的解放使用。\r\n②：这张卡被效果解放的场合才能发动。从卡组把1只战士族「影灵衣」仪式怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90311614,'冰结界的水影','自己场上表侧表示存在的怪兽只有2星以下的怪兽的场合，这张卡可以直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90330453,'魔女狩猎','场上表侧表示的魔法师族的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90330454,'魔女狩猎','场上表侧表示的魔法师族的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90337190,'鱼雷鱼','当场上存在「海」时，这张卡不受魔法效果的影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90357090,'银牙狼','银光闪闪的狼。外表虽然美，性格却凶暴。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90361010,'超重武者装留 岩融',replace(replace('①：自己主要阶段以自己场上1只「超重武者」怪兽为对象才能发动。从自己的手卡·场上把这只怪兽当作装备卡使用给那只自己怪兽装备。\r\n②：用这张卡的效果把这张卡装备的怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n③：这张卡从场上送去墓地的场合才能发动。从卡组把「超重武者装留 岩融」以外的1只「超重武者」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'给「超重武者」怪兽装备','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90365482,'河伯',replace(replace('这张卡不能特殊召唤。\r\n①：这张卡召唤·反转的场合，以场上1只表侧表示怪兽为对象才能发动。那只表侧表示怪兽变成当作灵魂怪兽使用，结束阶段回到持有者手卡。\r\n②：这张卡召唤·反转的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90374791,'武装转换','从自己手卡把1张装备魔法卡送去墓地发动。装备怪兽战斗破坏怪兽的场合，装备卡的控制者可以从自己墓地选择装备怪兽的攻击力以下的1只怪兽加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90397998,'六武众-火门','只要自己场上有「六武众-火门」以外的名字带有「六武众」的怪兽存在，1回合只有1次可以把表侧表示存在的1张魔法或者陷阱卡破坏。使用这个效果的回合这只怪兽不能攻击宣言。这张卡被破坏的场合，可以用这张卡以外的名字带有「六武众」的怪兽代替破坏。','表侧的1张魔法·陷阱卡破坏','是否要使用「六武众-火门」的代替破坏效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90407382,'蹴杀者','这张卡特殊召唤成功时，可以将1张存在于自己墓地里的装备魔法卡装备在这张卡身上。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90411554,'岩征龙-锈龙','从自己的手卡·墓地把这张卡以外的龙族或者地属性的怪兽合计2只除外才能发动。这张卡从手卡·墓地特殊召唤。特殊召唤的这张卡在对方的结束阶段时回到持有者手卡。此外，可以通过把这张卡和1只地属性怪兽从手卡丢弃去墓地，选择自己墓地1只怪兽特殊召唤。这张卡被除外的场合，可以从卡组把1只龙族·地属性怪兽加入手卡。「岩征龙-锈龙」的效果1回合只能使用1次。','从手卡特殊召唤','返回手牌','选择自己墓地1只怪兽特殊召唤','加入手卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(90432163,'幻影骑士团 沾尘袍',replace(replace('「幻影骑士团 沾尘袍」的①②的效果1回合各能使用1次。\r\n①：这张卡在场上攻击表示存在的场合，以场上1只暗属性怪兽为对象才能发动。这张卡变成守备表示，作为对象的怪兽的攻击力·守备力直到对方回合结束时上升800。\r\n②：把墓地的这张卡除外才能发动。从卡组把「幻影骑士团 沾尘袍」以外的1张「幻影骑士团」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90434657,'王家长眠之谷的王墓','场上有名字带有「守墓」的怪兽以及「王家长眠之谷」存在的场合才能发动。效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。「王家长眠之谷的王墓」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90434926,'漆黑之帐','自己的抽卡阶段抽到的卡是暗属性怪兽的场合，可以把那张卡给对方观看，那张卡送去墓地。那之后，可以从自己卡组抽1张卡。','再次抽卡','是否要抽卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90440725,'电子黑影守卫者','对方的主要阶段时才能发动。这张卡发动后变成怪兽卡（机械族·地·4星·攻/守?）在自己的怪兽卡区域特殊召唤。这张卡为攻击对象的对方怪兽的攻击宣言时，这张卡的攻击力·守备力变成和对方攻击怪兽相同数值。这张卡在对方的结束阶段时在魔法与陷阱卡区域盖放。这张卡也当作陷阱卡使用。','魔陷区盖放','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90452877,'星际仙踪-能术',replace(replace('「星际仙踪-能术」在1回合只能发动1张。\r\n①：以自己场上1只「星际仙踪」怪兽为对象才能发动。那只怪兽破坏，选对方的场上·墓地1张卡除外。','\r',char(13)),'\n',char(10)),'除外场上1张卡','除外墓地1张卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90464188,'黑曜岩龙','只要这张卡在场上表侧表示存在，自己场上表侧表示存在的1只暗属性怪兽为对象的魔法·陷阱卡的效果无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90470931,'鬼神的连击','选择自己场上表侧表示存在的1只超量怪兽，把那些超量素材全部取除发动。这个回合，选择的怪兽在同1次的战斗阶段中可以作2次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90500169,'等级下降！？','选择场上表侧表示存在的1只持有「LV」的怪兽发动。选择的卡回到原本的持有者卡组，从持有者墓地把1只比那张卡的「LV」低的同名怪兽无视召唤条件在持有者场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90502999,'地盘沉下',replace(replace('指定没有使用的主要怪兽区域2处才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，指定的区域不能使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90508760,'X-剑士 艾尔伯恩','①：这张卡直接攻击给与对方战斗伤害的场合发动。对方手卡随机选1张丢弃。','丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90512490,'巨蝇',replace(replace('风属性怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：场上的风属性怪兽的攻击力·守备力上升500，地属性怪兽的攻击力·守备力下降400。\r\n②：这张卡被战斗·效果破坏的场合，以自己墓地1只风属性怪兽为对象才能发动。那只怪兽加入手卡。\r\n','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90519313,'检疫','①：自己·对方的结束阶段以对方场上盖放的1张魔法·陷阱卡为对象才能把这个效果发动。把那张卡确认，回到原状。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90555947,'自然地星狮',replace(replace('地属性同调怪兽×2\r\n①：只以场上的卡1张为对象的魔法·陷阱·怪兽的效果发动时，把1张手卡送去墓地才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90557975,'上升气流','把场上存在的雾指示物全部取除发动。给与对方基本分取除的雾指示物数量×300的数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90576781,'爬虫妖毒','自己场上有名字带有「爬虫妖」的怪兽表侧表示存在的场合才能发动。对方场上守备表示存在的1只怪兽变更为表侧攻击表示，那只怪兽的攻击力变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90579153,'命运英雄 敌托邦人',replace(replace('「命运英雄」怪兽×2\r\n「命运英雄 敌托邦人」的①②的效果1回合各能使用1次。\r\n①：这张卡特殊召唤成功的场合，以自己墓地1只4星以下的「命运英雄」怪兽为对象才能发动。给与对方那只怪兽的攻击力数值的伤害。\r\n②：这张卡的攻击力和原本攻击力不同的场合，以场上1张卡为对象才能发动。那张卡破坏，这张卡的攻击力变成原本数值。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'给与对方伤害','场上1张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90582719,'剑斗兽 独眼斗','以高攻击力追逼敌人的独眼战熊。恐怖的速度与重量引以为傲的得意拳受了后不倒者是不存在的。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90590303,'No.41 泥睡魔兽 睡梦貘',replace(replace('4星怪兽×2\r\n这张卡的控制者在每次自己准备阶段把这张卡1个超量素材取除。不能取除的场合，这张卡破坏。\r\n①：攻击表示的这张卡不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：只要这张卡在怪兽区域守备表示存在，场上的表侧表示怪兽变成守备表示，场上的守备表示怪兽发动的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90592429,'大日细丝','名字带有「大日」的怪兽才能装备。装备怪兽攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。场上表侧表示存在的这张卡被送去墓地的场合，可以从自己卡组把1张名字带有「大日」的魔法卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90616316,'魔轰神兽 珀伽拉索斯','这张卡从手卡丢弃去墓地时，这张卡可以在自己场上盖放。这张卡反转时，可以把手卡1只名字带有「魔轰神」的怪兽给人观看，从自己卡组把1只名字带有「魔轰神」的怪兽送去墓地。','在自己场上盖放','送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90640901,'埋伏破坏者','这张卡只能向里侧守备表示怪兽攻击。这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。这张卡向里侧守备表示怪兽攻击的场合，可以不进行伤害计算以里侧守备表示的状态把那只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90642597,'未来侍',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●可以把自己墓地存在的1只怪兽从游戏中除外，场上表侧表示存在的1只怪兽破坏。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90654356,'暗黑翼龙','这张卡被战斗破坏以外的方法从场上送去墓地时，这张卡回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90660762,'流星黑龙','「真红眼黑龙」＋「流星之龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90669991,'菠萝爆弹','自己召唤怪兽成功时发动。若对方场上的怪兽数比自己控制的怪兽数多，则破坏对方场上的怪兽直到与自己的怪兽数量相同为止。破坏哪些怪兽由对方选择。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90673288,'闪刀姬-雫空',replace(replace('水属性以外的「闪刀姬」怪兽1只\r\n自己对「闪刀姬-雫空」1回合只能有1次特殊召唤。\r\n①：对方场上的怪兽的攻击力·守备力下降自己墓地的魔法卡数量×100。\r\n②：这张卡特殊召唤的回合的结束阶段才能发动。从卡组把同名卡不在自己墓地存在的1张「闪刀」魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90673413,'我我我复仇','选择自己墓地1只名字带有「我我我」的怪兽才能发动。选择的怪兽特殊召唤，把这张卡装备。这张卡从场上离开时，装备怪兽破坏。此外，装备怪兽变成超量素材让这张卡被送去墓地时，自己场上的全部超量怪兽的攻击力上升300。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90726340,'龙魔人 龙骑士女王',replace(replace('4星怪兽×2\r\n只要这张卡在场上表侧表示存在，「龙魔人 龙骑士女王」以外的自己的龙族怪兽不会被战斗破坏。此外，1回合1次，可以把这张卡1个超量素材取除，选择自己墓地1只5星以上的龙族怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，这个回合那只怪兽不能攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90727556,'侵入魔鬼的斥侯','自己场上没有魔法·陷阱卡存在的场合，自己的主要阶段1的开始时才能发动。墓地存在的这张卡在自己场上特殊召唤。这个效果发动的回合，自己不能把怪兽特殊召唤。这张卡不能为名字带有「侵入魔鬼」的怪兽的上级召唤以外而解放，也不能作为同调素材。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90740329,'挑衅','对方回合的主要阶段一，选择自己场上的1只怪兽才能发动。只要选择怪兽在场上存在，对方这个回合攻击的场合，必须以选择的怪兽为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90740330,'挑衅','对方回合的主要阶段一，选择自己场上的1只怪兽才能发动。只要选择怪兽在场上存在，对方这个回合攻击的场合，必须以选择的怪兽为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90743290,'魔导老士 艾尔米特','只要这张卡在场上表侧表示存在，每次名字带有「魔导书」的魔法卡发动，这张卡的等级上升2星，攻击力上升300。','等级攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90764875,'蛇头食尸鬼','自己场上有「蛇头食尸鬼」存在的场合，支付300基本分才能发动。这张卡从手卡特殊召唤。「蛇头食尸鬼」的效果1回合可以使用最多2次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90790253,'小翼守卫','这张卡在自己的结束阶段时可以变更表示形式1次。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90809975,'饼蛙',replace(replace('水族2星怪兽×2\r\n①：自己·对方的准备阶段把这张卡1个超量素材取除才能发动。从卡组把1只「青蛙」怪兽特殊召唤。\r\n②：1回合1次，对方把怪兽的效果·魔法·陷阱卡发动时，把自己的手卡·场上1只水族怪兽送去墓地才能发动。那个发动无效并破坏。那之后，可以把破坏的卡在自己场上盖放。\r\n③：这张卡被送去墓地的场合，以自己墓地1只水属性怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','无效并破坏','加入手卡','是否把破坏的卡在自己场上盖放？','是否把破坏的卡在自己场上盖放？','','','','','','','','','','','');
INSERT INTO texts VALUES(90810762,'逆卷的炎之精灵','这张卡可以直接攻击对方玩家。每次直接攻击成功这张卡的攻击力上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90812044,'连锁召唤','①：自己场上有超量怪兽2只以上存在的场合，以那之内的1只阶级最低的超量怪兽为对象才能发动。比那只超量怪兽阶级低的1只超量怪兽从额外卡组特殊召唤。这个效果特殊召唤的怪兽不能直接攻击，结束阶段回到额外卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90844184,'伽玛剑','「伽玛剑的誓言」降临。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90846359,'群雄割据','①：只要这张卡在魔法与陷阱区域存在，双方场上各自只能有1种类的种族的怪兽表侧表示存在。双方玩家在自身场上的表侧表示怪兽的种族是2种类以上的场合直到变成1种类为止必须送去墓地。','请选择要保留的种族','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90873992,'战士抹杀','场上表侧表示存在的战士族怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90876561,'卫兵骑士','精通一切剑术的战士。正义感非常强烈，为守护弱者而战。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90884403,'究极幻神 奥特美特尔·比希巴尔金',replace(replace('规则上，这张卡的等级当作12星使用。这张卡不能同调召唤，把自己场上的8星以上而相同等级的调整和调整以外的怪兽各1只送去墓地的场合才能特殊召唤。\r\n①：这张卡不会被效果破坏，攻击力上升场上的怪兽数量×1000。\r\n②：1回合1次，自己·对方的主要阶段才能发动。在双方场上把相同数量的「邪眼神衍生物」（恶魔族·暗·1星·攻/守0）尽可能守备表示特殊召唤。这个回合这张卡不能攻击。','\r',char(13)),'\n',char(10)),'特殊召唤衍生物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90884404,'邪眼神衍生物','由「究极幻神 奥特美特尔·比希巴尔金」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90885155,'机壳壳层 拒绝',replace(replace('←9 【灵摆】 9→\r\n①：自己不是「机壳」怪兽不能特殊召唤。这个效果不会被无效化。\r\n②：对方场上的怪兽的攻击力下降300。\r\n【怪兽效果】\r\n①：这张卡可以不用解放作召唤。\r\n②：特殊召唤或者不用解放作召唤的这张卡的等级变成4星，原本攻击力变成1800。\r\n③：通常召唤的这张卡不受原本的等级或者阶级比这张卡的等级低的怪兽发动的效果影响。\r\n④：把「机壳」怪兽解放表侧表示上级召唤成功的场合，这张卡在同1次的战斗阶段中可以作2次攻击，向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'不用解放作召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90887783,'龙之交感','把手卡1只龙族怪兽给对方观看，和给人观看的怪兽相同等级的1只龙族怪兽从卡组加入手卡。那之后，给人观看的怪兽回到卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90908427,'铁腕巨人','用钢铁做成的机器人偶，拥有恐怖的怪力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90925163,'舞蹈妖精','只要这张卡在自己场上表侧守备表示存在，每次自己的准备阶段回复1000基本分。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90928333,'暗之量产工厂','①：以自己墓地2只通常怪兽为对象才能发动。那些怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90934570,'侵略的侵食崩坏','选择自己场上表侧表示存在的1只名字带有「入魔」的怪兽从游戏中除外，选择对方场上2张卡回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90951921,'先史遗产技术','选择自己墓地1只名字带有「先史遗产」的怪兽才能发动。选择的怪兽从游戏中除外。那之后，从自己卡组上面把2张卡确认，从那之中选1张加入手卡，剩下的卡送去墓地。「先史遗产技术」在1回合只能发动1张，这张卡发动的回合自己不能把名字带有「先史遗产」的怪兽以外的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90953320,'科技属 超图书馆员',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡在场上存在，自己或者对方对这张卡以外的同调怪兽的同调召唤成功的场合发动。这张卡在场上表侧表示存在的场合，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90957527,'剑斗兽 乔治',replace(replace('「剑斗兽 斯巴达克斯」＋名字带有「剑斗兽」的怪兽\r\n把自己场上存在的上记的卡回到卡组的场合才能从融合卡组特殊召唤（不需要「融合」魔法卡）。这张卡战斗破坏怪兽送去墓地时，给与对方基本分破坏怪兽的守备力数值的伤害。这张卡进行战斗的战斗阶段结束时可以让这张卡回到融合卡组，从卡组把「剑斗兽 斯巴达克斯」以外的2只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'伤害','特殊召唤','请选择一只「剑斗兽 斯巴达克斯」','请选择一只名字带有「剑斗兽」的怪兽','','','','','','','','','','','','');
INSERT INTO texts VALUES(90960358,'卡通黑魔术少女',replace(replace('这张卡不能通常召唤。自己场上有「卡通世界」存在，把自己场上1只怪兽解放的场合可以特殊召唤。\r\n①：这张卡的攻击力上升双方墓地的「黑魔术师」「黑混沌之魔术师」数量×300。\r\n②：对方场上没有卡通怪兽存在的场合，这张卡可以直接攻击。存在的场合，必须把卡通怪兽作为攻击对象。\r\n③：场上的「卡通世界」被破坏时这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90963488,'睡眠之子','孩子被梦魔所操控永远沉睡着不再醒来。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(90980792,'暗黑胶体人','这张卡召唤·反转召唤·特殊召唤成功时，选择场上1只表侧表示怪兽。只要那只怪兽在场上表侧表示存在，攻击力下降800。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91020571,'地征龙-迹龙','把1只龙族或者地属性的怪兽和这张卡从手卡丢弃才能发动。从卡组把1只「岩征龙-锈龙」特殊召唤。这个效果特殊召唤的怪兽在这个回合不能攻击。「地征龙-迹龙」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91034681,'魔玩具·冒失鬼',replace(replace('「锋利小鬼」怪兽＋「毛绒动物」怪兽\r\n「魔玩具·冒失鬼」的②的效果1回合只能使用1次。\r\n①：这张卡战斗破坏对方怪兽时才能发动。给与对方1000伤害。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合或者被战斗破坏的场合才能发动。给与对方为自己墓地的「魔玩具」怪兽数量×500伤害。','\r',char(13)),'\n',char(10)),'给与对方1000伤害','给与对方「魔玩具」怪兽数量×500伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91070115,'传心外星人','可以把对方怪兽放置的A指示物取除1个，场上1张魔法或者陷阱卡破坏。这个效果1回合只能使用1次。放置有A指示物的怪兽和名字带有「外星」的怪兽战斗的场合，每有1个A指示物攻击力·守备力下降300。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91078716,'花粉症','把自己场上存在的1只植物族怪兽解放发动。魔法·陷阱卡的发动、怪兽的召唤·特殊召唤的其中1个无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91107093,'电磁盾','自己场上表侧守备表示存在的3星以下的雷族怪兽不会被战斗破坏。自己场上有怪兽表侧攻击表示存在的场合，这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91110378,'光天使 天座',replace(replace('把这张卡作为超量召唤的素材的场合，不是以怪兽3只以上为素材的超量召唤不能使用。\r\n①：自己对「光天使」怪兽的召唤·特殊召唤成功的场合才能发动。这张卡从手卡特殊召唤，自己从卡组抽1张。那张抽到的卡是「光天使」怪兽的场合，可以把那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','是否要把抽到的怪兽特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91123920,'力之代行者 玛尔斯','这张卡不受魔法效果的影响。当自己场上存在「天空的圣域」，且自己的基本分数值超过对方时，这张卡的攻击力、守备力上升与基本分超出部分等同的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91133740,'雪人食人怪','这张卡反转时，场上表侧表示存在的1只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91148083,'诸神的黄昏','选择自己场上表侧表示存在的1只名字带有「极神」的怪兽发动。选择的怪兽的控制权转移给对方。下次的对方的结束阶段时，选择的怪兽破坏，对方场上存在的卡全部从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91152256,'精灵剑士','学会了剑术的精灵。以快速的攻击作弄敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91152257,'精灵剑士','学会了剑术的精灵。以快速的攻击作弄敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91152258,'精灵剑士','学会了剑术的精灵。以快速的攻击作弄敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91182675,'魔导兽 胡狼',replace(replace('←4 【灵摆】 4→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：另一边的自己的灵摆区域没有卡存在的场合，以自己场上1张可以放置魔力指示物的卡为对象才能发动。这张卡破坏，给那张卡放置1个魔力指示物。\r\n【怪兽效果】\r\n①：只要这张卡在怪兽区域存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。\r\n②：把自己场上3个魔力指示物取除，把这张卡解放才能发动。从卡组把「魔导兽 胡狼」以外的1只「魔导兽」效果怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','请选择要放置魔力指示物的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91188343,'神秘之代行者 厄斯','这张卡召唤成功时，可以从自己卡组把「神秘之代行者 厄斯」以外的1只名字带有「代行者」的怪兽加入手卡。场上有「天空的圣域」表侧表示存在的场合，可以作为代替把1只「主宰者·许珀里翁」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91231901,'「A」细胞重组装置',replace(replace('①：以场上1只表侧表示怪兽为对象才能发动。从卡组把1只「外星」怪兽送去墓地，送去墓地的怪兽的等级数量的A指示物给作为对象的怪兽放置。\r\n②：自己主要阶段把墓地的这张卡除外才能发动。从卡组把1只「外星」怪兽加入手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91250514,'绕舌怪','上级召唤的这张卡从场上送去墓地时，从自己卡组抽2张卡。这个效果发动的场合，这张卡从游戏中除外。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91258852,'秘旋谍-高雅女士',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：1回合1次，自己主要阶段才能发动。从卡组把1张「秘旋谍任务」卡加入手卡。\r\n②：这张卡从场上送去墓地的场合才能发动。从卡组把「秘旋谍-高雅女士」以外的1只「秘旋谍」怪兽和1张「秘旋谍的秘密胜地」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91269402,'电子界信标',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：战斗或者对方的效果让自己受到伤害的回合才能发动。从卡组把1只4星以下的电子界族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91279700,'入魔龙祖 俄菲翁',replace(replace('4星「入魔」怪兽×2\r\n①：只要持有超量素材的这张卡在怪兽区域存在，双方不能把5星以上的怪兽特殊召唤。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。从卡组把1张「侵略的」魔法·陷阱卡加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91299846,'雷天气 姜黄',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：把自己场上1张表侧表示的永续魔法·永续陷阱卡送去墓地才能发动。从卡组选1张「天气」魔法·陷阱卡在自己的魔法与陷阱区域表侧表示放置。\r\n②：场上的这张卡为让「天气」卡的效果发动而被除外的场合，下个回合的准备阶段才能发动。除外的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'从卡组放置「天气」魔法·陷阱卡','除外的这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91345518,'裁决之代行者 萨图恩','当自己的基本分数值超过对方时，将自己场上存在的这张卡祭掉发动效果。对对方造成等同于基本分数值超出部分的伤害。此效果仅当自己场上存在「天空的圣域」时才适用。此效果使用的场合，这个回合不能再进入战斗阶段。','给与对方基本分差值的伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91349449,'光芒使者 巫师','这张卡不能通常召唤。从游戏中除外的自己的光属性怪兽是3只以上的场合才能特殊召唤。1回合1次，可以选择从游戏中除外的1只自己的光属性怪兽回到卡组，选择场上表侧表示存在的1只怪兽从游戏中除外。这个效果发动的回合，这张卡不能攻击。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91350799,'魔偶甜点·热香饼猫头鹰','这张卡被对方破坏送去墓地时，这张卡回到卡组。此外，自己的主要阶段时，选择自己墓地1只怪兽才能发动。选择的怪兽从游戏中除外，从卡组把「魔偶甜点·热香饼猫头鹰」以外的1只名字带有「魔偶甜点」的怪兽特殊召唤。「魔偶甜点·热香饼猫头鹰」的这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91351370,'黑旋风','①：自己场上有「黑羽」怪兽召唤时才能把这个效果发动。把持有比那只怪兽低的攻击力的1只「黑羽」怪兽从卡组加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91405870,'烈风之空牙团',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己墓地1只「空牙团」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91420254,'妖仙兽 右镰神柱',replace(replace('←5 【灵摆】 5→\r\n①：1回合1次，另一边的自己的灵摆区域有「妖仙兽」卡存在的场合才能发动。这张卡的灵摆刻度直到回合结束时变成11。这个效果的发动后，直到回合结束时自己不是「妖仙兽」怪兽不能特殊召唤。\r\n【怪兽效果】\r\n①：这张卡召唤成功的场合发动。这张卡变成守备表示。\r\n②：只要这张卡在怪兽区域存在，对方不能把其他的「妖仙兽」怪兽作为攻击对象。','\r',char(13)),'\n',char(10)),'刻度变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91422370,'螺旋式发条','把自己场上1只攻击力1500以上的名字带有「发条」的怪兽解放发动。从手卡把1只名字带有「发条」的怪兽特殊召唤。那之后，可以把持有和这个效果特殊召唤的怪兽相同攻击力的1只名字带有「发条」的怪兽从卡组特殊召唤。','是否要从卡组特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91438994,'盟军·次世代狱焰兵','每次自己场上存在的怪兽送去墓地，给这张卡放置1个次世代指示物。此外，每次对方墓地存在的卡从游戏中除外，给这张卡放置2个次世代指示物。这张卡的攻击力上升场上存在的次世代指示物数量×100的数值。这张卡被战斗破坏送去墓地时，给与对方基本分这张卡放置的次世代指示物数量×300的数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91449144,'薰风的静寂 卡姆','1回合1次，可以让自己墓地存在的2只名字带有「薰风」的怪兽回到卡组，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91449532,'娱乐伙伴 合掩河马',replace(replace('①：这张卡召唤成功时才能发动。从手卡把1只「娱乐伙伴」怪兽特殊召唤。这个效果特殊召唤的怪兽的效果直到回合结束时无效化。\r\n②：1回合1次，自己主要阶段才能发动。自己场上的怪兽全部变成守备表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91468551,'太阳的祭坛','只要这张卡在场上存在，自己场上表侧表示存在的从墓地特殊召唤的怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91482773,'关口龙',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：对方场上有连接怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：1回合1次，自己主要阶段才能发动。从手卡把1只4星以下的龙族·暗属性怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91499077,'我我我侍',replace(replace('4星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以自己场上1只「我我我」怪兽为对象才能发动。这个回合，那只怪兽在同1次的战斗阶段中可以作2次攻击。\r\n②：这张卡以外的自己场上的怪兽被选择作为攻击对象时才能发动。这张卡变成表侧守备表示，攻击对象转移为这张卡进行伤害计算。','\r',char(13)),'\n',char(10)),'2次攻击','对象变更','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91501248,'禁忌之壶',replace(replace('「禁忌之壶」的效果1回合只能使用1次。\r\n①：这张卡反转的场合，可以从以下效果选择1个发动。\r\n●自己从卡组抽2张。\r\n●场上的魔法·陷阱卡全部回到持有者手卡。\r\n●对方场上的怪兽全部破坏。\r\n●把对方手卡确认，从那之中选1张卡回到持有者卡组。','\r',char(13)),'\n',char(10)),'自己从卡组抽2张','场上的魔法·陷阱卡全部回到持有者手卡','对方场上的怪兽全部破坏','把对方手卡确认，从那之中选1张卡回到持有者卡组','','','','','','','','','','','','');
INSERT INTO texts VALUES(91505214,'淘气仙星·那耳姬丝',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：对方受到效果伤害的场合才能发动。这张卡从手卡特殊召唤。\r\n②：只要这张卡在怪兽区域存在，每次对方把手卡·墓地的怪兽的效果发动，给与对方200伤害。','\r',char(13)),'\n',char(10)),'这张卡从手卡特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91512835,'昆虫女王',replace(replace('①：这张卡的攻击力上升场上的昆虫族怪兽数量×200。\r\n②：这张卡的攻击宣言之际，自己必须把自己场上1只其他怪兽解放。\r\n③：这张卡战斗破坏对方怪兽的回合的结束阶段发动。在自己场上把1只「昆虫怪兽衍生物」（昆虫族·地·1星·攻/守100）攻击表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91512836,'昆虫怪兽衍生物','由「昆虫女王」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91554542,'新炎狱剑士','对方墓地存在的卡是4张以下的场合，这张卡的攻击力上升600。对方墓地存在的卡是8张以上的场合，这张卡的攻击力下降300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91559748,'棘之妖精','对方不破坏这张卡，就不能对昆虫族怪兽进行攻击。与这张卡进行战斗的怪兽，在伤害步骤终了时变成守备表示。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91580102,'爬虫妖怒','装备怪兽变成爬虫族，攻击力上升800。此外，这张卡被破坏送去墓地时，选择对方场上表侧表示存在的1只怪兽。那只怪兽的攻击力下降800。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91584698,'娱乐伙伴 扑克魔女',replace(replace('←4 【灵摆】 4→\r\n①：1回合1次，自己主要阶段才能发动。从自己场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n【怪兽效果】\r\n①：把这张卡解放才能发动。从自己的卡组·墓地选1张「融合」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91588074,'创星神 提耶拉',replace(replace('这张卡不能通常召唤。让这张卡以外的自己的手卡·场上的卡10种类回到持有者的卡组·额外卡组的场合才能特殊召唤。\r\n①：这张卡的特殊召唤不会被无效化。\r\n②：这张卡特殊召唤成功的场合发动。这张卡以外的双方的手卡·场上·墓地的卡以及额外卡组的表侧表示的灵摆怪兽全部回到持有者卡组。不能对应这个效果的发动让魔法·陷阱·怪兽的效果发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91595718,'秘术之书','魔法师族才能装备。1只装备怪兽的攻击力和守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91596726,'暗黑十字军','可以从手卡把1只暗属性怪兽送去墓地，这张卡的攻击力上升400。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91597389,'骑乘！','选择自己墓地存在的1只同盟怪兽，给自己场上表侧表示存在的可以装备的怪兽装备。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91607976,'变形斗士·电子钟',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：这张卡放置的变形斗士指示物每有1个，这张卡的攻击力上升500。\r\n●守备表示：1回合1次，可以给这张卡放置1个变形斗士指示物。可以把这张卡解放，给与对方基本分这张卡放置的变形斗士指示物数量×1000的数值的伤害。','\r',char(13)),'\n',char(10)),'放置1个变形斗士指示物','对方基本分这张卡放置的变形斗士指示物数量×1000的数值的伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91623717,'连锁爆击','给与对方基本分这张卡的发动时积累的连锁数量×400分的伤害。同1组连锁上有复数次同名卡的效果发动的场合，这张卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91646304,'神树之圣像骑士',replace(replace('这个卡名的作为①的方法的特殊召唤1回合只能有1次，作为②的效果1回合只能使用1次。\r\n①：这张卡可以从手卡往作为连接怪兽所连接区的自己场上守备表示特殊召唤。\r\n②：自己场上的「圣像骑士」怪兽被战斗·效果破坏的场合，可以作为代替把场上·墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91650245,'森罗的恩惠','选1张手卡回到持有者卡组最上面或者最下面，从自己的手卡·墓地选1只名字带有「森罗」的怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不受这张卡以外的卡的效果影响，结束阶段时回到持有者卡组最上面或者最下面。','返回卡组最上面','返回卡组最下面','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91662792,'薰风鹰','这张卡被战斗破坏送去墓地时，可以从自己卡组把1只调整以外的4星以下的名字带有「薰风」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91663373,'电子超能人','只要这张卡在自己场上表侧攻击表示存在，可以确认对方抽到的卡。','确认抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91665064,'安全屏蔽','①：以场上1只电子界族怪兽为对象才能发动。这个回合，那只怪兽不会被战斗破坏，双方受到的全部战斗伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91677585,'毅飞冲天挑战','①：自己战斗阶段，以这个回合进行过攻击的1只超量怪兽为对象才能发动。这次战斗阶段中那只怪兽只再1次可以攻击。这个效果让那只怪兽攻击的场合，直到伤害步骤结束时对方不能把魔法·陷阱·怪兽的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91691605,'命运英雄 钻头人',replace(replace('「命运英雄 钻头人」的①的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。把持有这张卡的攻击力以下的攻击力的1只「命运英雄」怪兽从手卡特殊召唤。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91697229,'极星邪狼 芬里尔',replace(replace('这张卡不能通常召唤。自己主要阶段2，场上有「极神」怪兽存在的场合可以从手卡往对方场上守备表示特殊召唤。\r\n①：场上没有「极神」怪兽存在的场合这张卡破坏。\r\n②：自己战斗阶段开始时发动。自己场上的守备表示怪兽全部变成表侧攻击表示。\r\n③：这张卡的战斗发生的战斗伤害由双方玩家承受。','\r',char(13)),'\n',char(10)),'特殊召唤','表示变更','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91711547,'炎狱恶魔','这张卡给与对方基本分战斗伤害时，给与对方基本分自己墓地存在的炎族怪兽数量×200的数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91712985,'时械神 卡米恩',replace(replace('这张卡不能从卡组特殊召唤。\r\n①：自己场上没有怪兽存在的场合，这张卡可以不用解放作召唤。\r\n②：这张卡不会被战斗·效果破坏，这张卡的战斗发生的对自己的战斗伤害变成0。\r\n③：这张卡进行战斗的战斗阶段结束时发动。选对方场上1张卡回到持有者卡组，给与对方500伤害。不能对应这个效果的发动让卡的效果发动。\r\n④：自己准备阶段发动。这张卡回到持有者卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91718579,'隆隆隆左手与右手',replace(replace('①：只要这张卡和这张卡以外的「隆隆隆」怪兽在怪兽区域存在，对方不能选择「隆隆隆」怪兽作为攻击对象，也不能作为效果的对象。\r\n②：只用包含这张卡的「隆隆隆」怪兽为素材的超量怪兽得到以下效果。\r\n●这次超量召唤成功时，以对方场上1只表侧攻击表示怪兽为对象才能发动。那只怪兽变成守备表示并把守备力变成0。','\r',char(13)),'\n',char(10)),'对方场上1只怪兽变成守备表示并把守备力变成0。','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91731841,'宝石骑士·红榴','拥有石榴石力量的宝石骑士之战士。火焰铁拳会粉碎所有敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91754175,'人犬兽','①：1回合1次，这张卡和对方怪兽进行战斗的伤害计算时才能发动。从手卡·卡组把1只兽族·兽战士族·鸟兽族怪兽送去墓地，这张卡的攻击力直到战斗阶段结束时上升送去墓地的那只怪兽的等级×100。','是否发动「人犬兽」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91781589,'霸者的独揽','在对方的准备阶段发动。对方本回合不能进行战斗阶段。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91782219,'蟹龟','「龟的誓言」降临。必须从场地和手卡把等级直到8以上的卡作为祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91798373,'二重蝎','这张卡召唤·特殊召唤成功时，可以从手卡把1只4星以下的二重怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91812341,'特莱恩之虫惑魔',replace(replace('①：这张卡召唤成功时才能发动。从卡组把1张「洞」通常陷阱卡或者「落穴」通常陷阱卡加入手卡。\r\n②：这张卡特殊召唤成功的场合，以对方场上1张魔法·陷阱卡为对象发动。那张对方的卡破坏。\r\n③：这张卡只要在怪兽区域存在，不受「洞」通常陷阱卡以及「落穴」通常陷阱卡的效果影响。','\r',char(13)),'\n',char(10)),'卡组检索','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91819979,'魔法爆破','给与对方基本分自己场上表侧表示存在的魔法师族怪兽数量×200的数值的伤害。这张卡在墓地存在的场合，可以作为自己的抽卡阶段时进行通常抽卡的代替，把这张卡加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91822647,'薰风的旋风','自己场上没有怪兽存在的场合才能发动。选择自己墓地存在的2只名字带有「薰风」的怪兽回到卡组。那之后，从自己卡组把1只守备力1000以下的名字带有「薰风」的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91831066,'水照明',replace(replace('①：「水照明」在自己场上只能有1张表侧表示存在。\r\n②：自己的「水伶女」怪兽和对方怪兽进行战斗的伤害计算时发动。那只自己怪兽的攻击力·守备力只在伤害计算时变成2倍。\r\n③：这张卡从场上送去墓地的场合，以自己墓地1只水族怪兽为对象才能发动。那只怪兽特殊召唤。这个效果的发动后，直到回合结束时自己不是水族怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91842653,'卡通恶魔','这张卡不能通常召唤。自己场上有「卡通世界」存在的场合才能特殊召唤（5星以上需要解放）。这张卡在特殊召唤的回合不能攻击。这张卡若不支付500基本分则不能攻击宣言。对方场上不存在卡通怪兽的场合，这张卡可以直接攻击对方玩家。存在的场合，必须选择卡通怪兽作为攻击对象。场上的「卡通世界」被破坏时，这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91862578,'激昂的姆卡姆卡','自己每有1张手卡，这张卡攻击力·守备力各上升400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91869203,'亚马逊射手','①：把自己场上2只怪兽解放才能发动。给与对方1200伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91895091,'银岭巨神',replace(replace('地属性3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方的魔法与陷阱卡区域盖放的1张卡才能发动。选择的卡只要这张卡在场上表侧表示存在不能发动。此外，持有超量素材的这张卡战斗破坏对方怪兽的场合，可以选择自己墓地1只地属性怪兽表侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'发动限制','特殊召唤','请选择魔法与陷阱卡区域盖放的1张卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91903221,'进化虫·埃尔金螈','场上的这张卡被解放送去墓地时，从卡组抽1张卡。那之后，可以让手卡1只恐龙族怪兽回到卡组，从卡组把1只名字带有「进化虫」的怪兽加入手卡。','抽卡','是否要把1只名字带有「进化虫」的怪兽加入手卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91907707,'机壳档案 色欲',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「机壳」怪兽不能特殊召唤。这个效果不会被无效化。\r\n②：自己场上的「机壳」怪兽的攻击力上升300。\r\n【怪兽效果】\r\n①：这张卡可以不用解放作召唤。\r\n②：特殊召唤或者不用解放作召唤的这张卡的等级变成4星，原本攻击力变成1800。\r\n③：通常召唤的这张卡不受原本的等级或者阶级比这张卡的等级低的怪兽发动的效果影响。\r\n④：这张卡被解放的场合，以场上1只怪兽为对象才能发动。那只怪兽回到持有者手卡。','\r',char(13)),'\n',char(10)),'不用解放作召唤','回到手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91932350,'鹰身女郎1','这张卡的卡名当作「鹰身女郎」使用。只要这张卡在场上存在，场上的风属性怪兽攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91939608,'神圣人偶','操纵神圣力量的人偶。在黑暗之中攻击力很强。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91949988,'迅雷之骑士 盖亚龙骑士',replace(replace('7星怪兽×2\r\n这张卡也能在自己场上的5·6阶的超量怪兽上面把这张卡重叠来超量召唤。这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','\r',char(13)),'\n',char(10)),'是否在5·6阶的超量怪兽上面重叠超量召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91985515,'钢核初期化','把自己场上存在的1只名字带有「核成」的怪兽解放发动。从自己的卡组·墓地把1张「核成兽的钢核」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91989718,'攻击反射装置','把自己场上1只「电子龙」作为祭品才能发动。从自己的手卡·卡组特殊召唤1只「电子障壁龙」。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91996584,'鞭尾石像怪','使用鞭子一般长的尾巴，从空中发动攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91998119,'XYZ-神龙炮',replace(replace('「X-首领加农」＋「Y-龙头」＋「Z-金属履带」\r\n把自己场上的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作从墓地的特殊召唤。\r\n①：丢弃1张手卡，以对方场上1张卡为对象才能发动。那张对方的卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91998120,'XYZ-神龙炮',replace(replace('「X-首领加农」＋「Y-龙头」＋「Z-金属履带」\r\n把自己场上的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作从墓地的特殊召唤。\r\n①：丢弃1张手卡，以对方场上1张卡为对象才能发动。那张对方的卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(91998121,'XYZ-神龙炮',replace(replace('「X-首领加农」＋「Y-龙头」＋「Z-金属履带」\r\n把自己场上的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作从墓地的特殊召唤。\r\n①：丢弃1张手卡，以对方场上1张卡为对象才能发动。那张对方的卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92001300,'古代的机械城',replace(replace('①：场上的「古代的机械」怪兽的攻击力上升300。\r\n②：只要这张卡在魔法与陷阱区域存在，每次怪兽通常召唤，给这张卡放置1个指示物。\r\n③：自己把「古代的机械」怪兽表侧表示上级召唤的场合，可以把有需要的解放数量以上的指示物放置的这张卡作为代替而解放来上级召唤。','\r',char(13)),'\n',char(10)),'解放「古代的机械城」进行通常召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92035412,'大日元素','自己场上表侧表示存在的名字带有「大日」的装备卡被破坏时，可以从自己卡组把最多和破坏数量相同数量的名字带有「大日」的调整在自己场上特殊召唤。把这个效果特殊召唤的怪兽作为同调素材的场合，不是名字带有「大日」的怪兽的同调召唤不能使用。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92039899,'恶魔骑兵','①：场上的这张卡被效果破坏送去墓地的场合，以「恶魔骑兵」以外的自己墓地1只「恶魔」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不能攻击。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92053608,'水伶女·龙鱼','①：1回合1次，自己主要阶段才能发动。从卡组把1只「水伶女」怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92065772,'住在冰结界的魔醉虫','这张卡召唤成功时，指定没有使用的怪兽卡区域1处发动。指定的怪兽卡区只要这张卡在场上表侧表示存在变成不能使用。','怪兽区1处不能使用','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92080692,'看破的极意','①：和对方墓地的卡同名的怪兽的效果·魔法·陷阱卡由对方发动时才能发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92084010,'髭须鮟鱇','作为水属性怪兽祭品召唤的场合，这只怪兽1只可以作为2只份的祭品使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92092092,'调星之直线竞速士',replace(replace('①：这张卡发动后变成效果怪兽（机械族·调整·炎·1星·攻0/守1800）在怪兽区域特殊召唤。这张卡也当作陷阱卡使用。\r\n②：只要这张卡的效果特殊召唤的这张卡在怪兽区域存在，这张卡以外的自己场上的调整不会被战斗以及对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92099232,'变形人姐妹','这张卡发动后变成怪兽卡（恶魔族·调整·地·2星·攻/守0）在怪兽卡区域特殊召唤。这张卡也当作陷阱卡使用。「变形人姐妹」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92125819,'圣骑士 阿托利斯',replace(replace('属于圣骑士团的聪明青年骑士。仿佛就像受到了引导，闯入了森林之中的他，终于抵达命运的所在。随后，青年迈出了那一大步。\r\n——这是一切的开端，一段伟大的史诗。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92142169,'大吃虫','对方场上有超量怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤成功时，选择对方场上1只怪兽发动。选择的怪兽的攻击力下降300。','攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92170894,'娱乐伙伴 电话长角牛',replace(replace('「娱乐伙伴 电话长角牛」的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。从卡组把灵摆怪兽以外的1只「娱乐伙伴」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92200612,'灵魂鸟-传鸠',replace(replace('这张卡不能特殊召唤。\r\n①：1回合1次，以这张卡以外的场上1只灵魂怪兽为对象才能发动。那只怪兽回到持有者手卡。\r\n②：这张卡召唤·反转的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92204263,'绀碧之机界骑士',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：相同纵列有卡2张以上存在的场合，这张卡可以在那个纵列的自己场上特殊召唤。\r\n②：1回合1次，以自己场上1只「机界骑士」怪兽为对象才能发动。那只自己怪兽的位置向其他的自己的主要怪兽区域移动。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'移动位置','请选择要移动位置的「机界骑士」怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92219931,'二者一两损','双方互相把自己卡组最上面的1张卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92223641,'天空之泉','光属性怪兽被战斗破坏送去自己墓地时，可以把那1只怪兽从游戏中除外，自己基本分回复那只怪兽的攻击力的数值。','回复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92246806,'妖仙兽 镰贰太刀',replace(replace('①：这张卡召唤成功的场合才能发动。从手卡把「妖仙兽 镰贰太刀」以外的1只「妖仙兽」怪兽召唤。\r\n②：这张卡可以直接攻击。那次战斗给与对方的战斗伤害变成一半。\r\n③：这张卡召唤的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'召唤','返回手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92266279,'湿润之风',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：支付1000基本分才能发动。从卡组把1只「芳香」怪兽加入手卡。\r\n②：自己基本分比对方少的场合才能发动。自己回复500基本分。','\r',char(13)),'\n',char(10)),'是否使用效果？','加入手卡','LP回复','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92300891,'机巧桶 真九六','这张卡可以攻击的场合必须作出攻击。场上表侧攻击表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变成守备表示。这张卡1回合只有1次不会被战斗破坏。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92327802,'缺陷编译器',replace(replace('①：1回合1次，对方的效果让自己受到伤害的场合，作为代替给这张卡放置1个缺陷指示物（最多1个）。\r\n②：1回合1次，把这张卡1个缺陷指示物取除，以自己场上1只电子界族怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升800。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92346415,'念力剑','念动力族怪兽才能装备。自己基本分比对方低的场合，装备怪兽的攻击力上升那个数值（最多2000）。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92353449,'真红眼看破',replace(replace('「真红眼看破」在1回合只能发动1张。\r\n①：从手卡·卡组把1只「真红眼」怪兽送去墓地才能发动。从卡组把「真红眼看破」以外的1张「真红眼」魔法·陷阱卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92361635,'原子废铁龙',replace(replace('名字带有「废铁」的调整＋调整以外的怪兽2只以上\r\n1回合1次，选择自己场上存在的1张卡和对方墓地存在的最多3张卡才能发动。选择的自己的卡破坏，选择的对方的卡回到卡组。这张卡被对方破坏送去墓地时，选择同调怪兽以外的自己墓地存在的1只名字带有「废铁」的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'破坏和返回卡组','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92365601,'升阶魔法-限制型异晶人的魔力','选择自己场上1只4阶的超量怪兽才能发动。比选择的怪兽阶级高1阶的1只名字带有「混沌No.」的怪兽在选择的自己怪兽上面重叠当作超量召唤从额外卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92373006,'奴隶虎',replace(replace('①：自己场上有「剑斗兽」怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：把这张卡解放，以自己场上1只「剑斗兽」怪兽为对象才能发动。那只自己的「剑斗兽」怪兽回到持有者卡组，从卡组把1只「剑斗兽」怪兽特殊召唤。这个效果特殊召唤的怪兽变成当作用「剑斗兽」怪兽的效果特殊召唤使用。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92377303,'黑衣大贤者','这张卡不能通常召唤。猜中自己的「时间魔术师」的掷硬币效果时，可以把自己场上存在的1只「黑魔术师」解放从手卡或者卡组中特殊召唤。这个方法特殊召唤成功时，从自己卡组把1张魔法卡加入手卡。','特殊召唤','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92391084,'威尔米','相当凶暴的兔子。以锐利的钩爪血祭对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92394653,'灵魂的引诱','场上的灵魂怪兽每次回到自己的手卡，选择对方控制的场上的1张怪兽卡回到手卡。每次的自己的准备阶段支付500分维持，不支付维持这张卡破坏。','返回手牌','是否要支付500LP维持「灵魂的引诱」？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92408984,'龙之宝珠','丢弃1张手卡。以1只表侧表示的龙族怪兽作对象的陷阱卡的效果无效并破坏。','效果无效并破坏','是否现在使用「龙之宝珠」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92409659,'白海豚','头上有角的白色海豚。引起大浪攻击对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92411493,'雨天气 湛蓝',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。从手卡选1张「天气」魔法·陷阱卡在自己的魔法与陷阱区域表侧表示放置。\r\n②：场上的这张卡为让「天气」卡的效果发动而被除外的场合，下个回合的准备阶段才能发动。除外的这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'从手卡放置「天气」魔法·陷阱卡','除外的这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92418590,'机关傀儡-死灵人偶','这张卡在墓地存在的场合，把这张卡以外的自己墓地1只名字带有「机关傀儡」的怪兽从游戏中除外才能发动。这张卡从墓地特殊召唤。「机关傀儡-死灵人偶」的效果1回合只能使用1次。此外，把这张卡作为超量召唤的素材的场合，不是名字带有「机关傀儡」的怪兽的超量召唤不能使用。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92421852,'稀有金属女郎','全身覆盖着金属装甲的女战士。与「稀有金属战士」融合将提升力量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92435533,'时械神 拉结恩',replace(replace('这张卡不能从卡组特殊召唤。\r\n①：自己场上没有怪兽存在的场合，这张卡可以不用解放作召唤。\r\n②：这张卡不会被战斗·效果破坏，这张卡的战斗发生的对自己的战斗伤害变成0。\r\n③：这张卡进行战斗的战斗阶段结束时发动。对方墓地的卡全部回到卡组。\r\n④：1回合1次，对方抽卡的场合发动。给与对方1000伤害。\r\n⑤：自己准备阶段发动。这张卡回到持有者卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92450185,'限速解除','①：这张卡被送去墓地的场合发动。从自己的手卡·卡组·墓地选1只「高速战士」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92481084,'心眼之祭殿','①：只要这张卡在场地区域存在，自己或者对方受到战斗伤害的场合，那个数值变成1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92510265,'圣战士 混沌战士',replace(replace('「圣战士 混沌战士」的①的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功时，以除外的1只自己的光属性或者暗属性的怪兽和对方场上1张卡为对象才能发动。那张自己的卡回到墓地，那张对方的卡除外。\r\n②：这张卡战斗破坏对方怪兽时，以自己墓地1只7星以下的战士族怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'除外','加入手卡','请选择要回到墓地的卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92512625,'神之忠告',replace(replace('①：自己的魔法与陷阱区域盖放的卡只有这张卡的场合，支付3000基本分才能发动。\r\n●怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。\r\n●自己或者对方把怪兽召唤·反转召唤·特殊召唤之际才能发动。那个无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92518817,'盛燃之希塔','可以把自己场上除这张卡以外的1只炎属性怪兽做祭品，从手卡特殊召唤1只炎属性怪兽。这个效果1回合只能使用1次。这个效果特殊召唤的怪兽，在「盛燃之希塔」从自己场上离开的场合破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92534075,'魔弹-恶魔交易',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：只要这张卡在魔法与陷阱区域存在，自己场上的「魔弹」怪兽不会被效果破坏。\r\n②：这张卡被对方的效果送去墓地的场合才能发动。从自己的卡组·墓地选「魔弹-恶魔交易」以外的1张「魔弹」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92536468,'DDD 反骨王 列奥尼达',replace(replace('←3 【灵摆】 3→\r\n①：自己受到效果伤害时才能把这个效果发动。这张卡破坏，并且这个回合，给与基本分伤害的效果再变成让基本分回复的效果。\r\n【怪兽效果】\r\n①：自己受到效果伤害时才能发动。这张卡从手卡特殊召唤，自己基本分回复受到的伤害的数值。\r\n②：只要这张卡在怪兽区域存在，自己受到的效果伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92572371,'杀炎星-牛逵','这张卡在墓地存在的场合，把手卡或者自己场上表侧表示存在的名字带有「炎星」或者「炎舞」的卡合计2张送去墓地才能发动。这张卡从墓地特殊召唤。「杀炎星-牛逵」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92595545,'等离子球','这张卡可以直接攻击对方玩家。这张卡直接攻击给与对方基本分战斗伤害时，这张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92595643,'罚则金','自己把手卡2张丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92609670,'英豪挑战者 伏击兵','自己的准备阶段时，把场上的这张卡解放才能发动。可以从自己的手卡·墓地选「英豪挑战者 伏击兵」以外的最多2只名字带有「英豪挑战者」的怪兽特殊召唤。「英豪挑战者 伏击兵」的这个效果1回合只能使用1次。这个效果特殊召唤成功时，可以通过把墓地的这张卡从游戏中除外，自己场上的全部名字带有「英豪挑战者」的怪兽的等级变成1星。','特殊召唤','等级变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92661479,'光子飞奔保镖',replace(replace('6星怪兽×2\r\n对方场上效果怪兽的效果发动时，把这张卡1个超量素材取除才能发动。那个效果无效，给与对方基本分1000分伤害。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92667214,'杀人小丑僵尸','使用黑暗力量重生的小丑，利用摇晃的舞蹈将对方引向死亡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92676637,'调和支援士',replace(replace('①：把场上的这张卡作为同调素材的场合，这张卡可以当作2星怪兽使用。\r\n②：这张卡作为同调素材送去墓地的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92693205,'齿轮齿轮抛锚人',replace(replace('①：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n②：这张卡反转的场合才能发动。选最多有这张卡以外的自己场上的「齿轮齿轮」怪兽数量的场上的怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92719314,'暗黑创世神','这张卡不能通常召唤。自己墓地暗属性怪兽有5只以上存在，自己场上没有怪兽存在的场合可以特殊召唤。可以把自己墓地1只暗属性怪兽从游戏中除外，自己墓地1只暗属性怪兽特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92720564,'变形斗士·收录机',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：这张卡在同1次的战斗阶段中可以作2次攻击。\r\n●守备表示：自己场上表侧表示存在的名字带有「变形斗士」的怪兽被选择作为攻击对象时，可以把那次战斗无效。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'战斗无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92723496,'龙宫的双使者',replace(replace('「龙宫的双使者」的效果1回合只能使用1次。\r\n①：把手卡1只怪兽丢弃才能发动。从卡组把1只4星以下的幻龙族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92729410,'子狸 嘭嘭','这张卡召唤成功时，可以从卡组把「子狸 嘭嘭」以外的1只兽族·2星怪兽里侧守备表示特殊召唤。这个效果发动的回合，自己不能把兽族以外的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92731455,'磁力战士2号','擅长配合的战士，表面涂有电磁的铠甲非常坚固。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92736188,'伟大的神灵','这张卡1回合1次可以变成里侧守备表示。这张卡反转召唤成功时，可以使场上表侧表示存在的1只地属性怪兽的原本攻击力和原本守备力在结束阶段前交换。','变成里侧守备','攻守交换','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92746535,'龙剑士 光辉星·灵摆',replace(replace('←5 【灵摆】 5→\r\n①：1回合1次，另一边的自己的灵摆区域有卡存在的场合才能发动。那张卡破坏，把1张那张卡的同名卡从卡组加入手卡。\r\n【怪兽效果】\r\n不能用这张卡为素材把「龙剑士」怪兽以外的融合·同调·超量怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92755808,'元素恐龙',replace(replace('这只怪兽在场上有特定属性的怪兽存在的场合，得到以下的效果。\r\n●炎属性：这张卡攻击力上升500。\r\n●地属性：这张卡战斗破坏的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92767273,'娱乐伙伴 凶猛蔷薇',replace(replace('←5 【灵摆】 5→\r\n①：1回合1次，自己的「娱乐伙伴」怪兽和对方怪兽进行战斗的伤害计算前才能发动。那只对方怪兽的攻击力下降和那个原本攻击力的相差数值。\r\n【怪兽效果】\r\n「娱乐伙伴 凶猛蔷薇」的怪兽效果1回合只能使用1次。\r\n①：以持有和原本攻击力不同攻击力的1只「娱乐伙伴」怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升和那个原本攻击力的相差数值。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92773018,'电子隐秘技术','对方怪兽的攻击宣言时，可以把自己场上表侧表示存在的1只「电子龙」以及「电子龙」作为融合素材的融合怪兽送去墓地，对方1只攻击怪兽破坏。','攻击怪兽破坏','是否现在使用「电子隐秘技术」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92781606,'超机怪虫·方舟感质虫',replace(replace('「机怪虫」怪兽2只\r\n①：自己场上的「机怪虫」怪兽数量对应的以下适用。\r\n●2只以上：自己场上的怪兽的攻击力·守备力上升300。\r\n●4只以上：对方在战斗阶段中不能把效果发动。\r\n●6只以上：自己怪兽可以直接攻击。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合或者被战斗破坏的场合，以自己墓地2只「机怪虫」怪兽为对象才能发动（同名卡最多1张）。那些怪兽里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92784374,'遗式术师·艾莉娅儿','反转：可以从卡组把1只名字带有「遗式」的怪兽加入手卡。','加入手卡','是否要把1只名字带有「遗式」的怪兽加入手卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92807548,'堕天使 乌科巴克',replace(replace('「堕天使 乌科巴克」的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1张「堕天使」卡送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92821268,'机关傀儡-夜小丑','自己场上的名字带有「机关傀儡」的怪兽被战斗破坏送去自己墓地时，把那怪兽从游戏中除外才能发动。这张卡从手卡特殊召唤。「机关傀儡-夜小丑」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92826944,'马头鬼','①：自己主要阶段把墓地的这张卡除外，以自己墓地1只不死族怪兽为对象才能发动。那只不死族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92841002,'幻水龙','自己场上有地属性怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法的「幻水龙」的特殊召唤1回合只能有1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92854392,'拦路的强敌','对方进行攻击宣言时这张卡才能发动。选择自己场上1张表侧表示的怪兽。这张卡发动回合，对方只能以所选择的这只怪兽为攻击对象，且必须用所有表侧攻击表示的怪兽攻击所选择的这只怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92868896,'龙骑兵团-元老院',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：从手卡丢弃1张「龙骑兵团」卡才能发动。从卡组选1只「龙骑兵团」调整当作装备卡使用给这张卡装备。这个效果的发动后，直到回合结束时自己不是龙族怪兽不能从额外卡组特殊召唤。\r\n②：自己场上的「龙骑兵团」卡被战斗·效果破坏的场合，可以作为代替把给这张卡装备的1张「龙骑兵团」卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92870717,'魔装战士 龙夜',replace(replace('←7 【灵摆】 7→\r\n①：对方怪兽的攻击宣言时才能发动。这张卡破坏，那次战斗阶段结束。\r\n【怪兽效果】\r\n①：1回合1次，丢弃1张手卡，以自己墓地的攻击力2000以下的1只战士族或者魔法师族怪兽为对象才能发动。那只怪兽里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92887027,'流浪佣兵部队','这张卡战斗破坏对方怪兽送去墓地时，把这张卡解放才能发动。选择对方场上里侧守备表示存在的1只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92890308,'变形','自己场上表侧表示存在的名字带有「变形斗士」的怪兽被选择作为攻击对象时才能发动。把1只攻击怪兽的攻击无效，被选择作为攻击对象的1只名字带有「变形斗士」的怪兽的表示形式改变。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92901944,'废铁搜索鸟','这张卡在墓地存在，自己场上存在的「废铁搜索鸟」以外的名字带有「废铁」的怪兽被破坏送去墓地时，这张卡可以从墓地特殊召唤。这张卡特殊召唤成功时，名字带有「废铁」的怪兽以外的自己场上表侧表示存在的怪兽全部破坏。','特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92918648,'魔导法皇 海隆',replace(replace('魔法师族7星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。选最多有自己墓地的名字带有「魔导书」的魔法卡数量的对方场上的魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92924317,'苏生之魂','①：以自己墓地1只通常怪兽为对象才能把这张卡发动。那只怪兽守备表示特殊召唤。这张卡从场上离开时那只怪兽破坏。那只怪兽破坏时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92933195,'霞之谷的风使','双方手卡有5张以上的场合才能发动。双方玩家直到手卡变成4张把手卡送去墓地。这个效果1回合只能使用1次。','手牌调整','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92944626,'邪炎之翼','原形是红黑色燃烧着的翅膀。从全身喷出火焰进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92958307,'娱乐伙伴表演尾声','①：以最多有自己场上的表侧表示的魔法卡数量的对方场上的表侧表示怪兽为对象才能发动。那些怪兽变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(92970404,'地中族邪界兽·巴利灰谷主',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合才能发动。这张卡以外的场上的表侧表示怪兽全部变成里侧守备表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93013676,'摩诃毗卢','①：这张卡的攻击力上升这张卡装备的装备卡数量×500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93016201,'王宫的弹压','支付800基本分。怪兽的特殊召唤或含有怪兽特殊召唤的效果无效，那张卡破坏。这个效果对方玩家也能使用。','无效特殊召唤','是否现在使用「王宫的弹压」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93020401,'灯影之机界骑士',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：相同纵列有卡2张以上存在的场合，这张卡可以在那个纵列的自己场上特殊召唤。\r\n②：和这张卡相同纵列的对方的卡从场上离开的场合或者被战斗破坏的场合才能发动。从手卡把1只「机界骑士」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93023479,'守墓的召唤师','这张卡从自己场上送去自己墓地时，从自己的卡组把1只守备力1500以下的名字带有「守墓」的怪兽加入手卡。','卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93085839,'圣骑士 爱克托·德·马利斯',replace(replace('「圣骑士 爱克托·德·马利斯」的①的效果1回合只能使用1次。\r\n①：把自己墓地2只「圣骑士」怪兽除外才能发动。这张卡从手卡·墓地特殊召唤。\r\n②：这张卡为素材的「圣骑士」怪兽的同调召唤·超量召唤不会被无效化，在那次特殊召唤成功时对方不能把魔法·陷阱·怪兽的效果发动。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93087299,'炎之护封剑','自己场上没有怪兽存在的场合，对方场上的怪兽不能攻击宣言。自己场上有怪兽存在的场合或者对方手卡是5张以上的场合，这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93104632,'动态密码',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：在自己场上把1只「安全令牌衍生物」（电子界族·光·4星·攻/守2000）守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93104633,'安全令牌衍生物','由「动态密码」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93107608,'共鸣虫','这张卡被战斗破坏送去墓地时，可以从卡组选择1只攻击力1500以下的昆虫族怪兽特殊召唤到自己场上。之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93108297,'液体兽','粘粘糊糊的融化，能在水里自由移动的液态生命体。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93108433,'怪兽回收','选自己场上的1只主人是自己的怪兽，和自己的手卡合在一起和卡组洗切后，从卡组抽自己的原来的手卡的数量的卡。（若不是自己的卡或者没手卡的场合，这张卡不能发动）','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93108839,'No.58 炎压鬼',replace(replace('4星怪兽×2\r\n1回合1次，自己的主要阶段时，这张卡可以当作装备卡使用给自己场上的超量怪兽装备，或者把装备解除以表侧攻击表示特殊召唤。这张卡的装备怪兽可以直接攻击对方玩家。此外，装备怪兽给与对方基本分战斗伤害时，丢弃1张手卡才能发动。给与对方基本分500分伤害。','\r',char(13)),'\n',char(10)),'装备','LP伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93124273,'雷普提尔的武者骑兵',replace(replace('←3 【灵摆】 3→\r\n【怪兽效果】\r\n①：这张卡向灵摆怪兽以外的对方的表侧表示怪兽攻击的伤害步骤开始时才能发动。那只怪兽破坏。','\r',char(13)),'\n',char(10)),'怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93130021,'胜利蛇XX03',replace(replace('这张卡战斗破坏对方怪兽时，从下面效果选择1个发动。\r\n●这张卡的攻击力上升400。\r\n●把场上表侧表示存在的1张魔法或者陷阱卡破坏。\r\n●在自己场上把1只一直和这张卡相同种族·属性·等级·攻击力·守备力的「子机衍生物」特殊召唤。','\r',char(13)),'\n',char(10)),'选择一个效果发动','这张卡攻击力上升400。','把场上表侧表示存在的1张魔法·陷阱卡破坏。','在自己场上把1只一直和这张卡相同种族·属性·等级·攻击力·守备力的「子机衍生物」特殊召唤。','','','','','','','','','','','','');
INSERT INTO texts VALUES(93130022,'子机衍生物','由「胜利蛇XX03」特殊召唤。这个衍生物的种族·属性·等级·攻击力·守备力始终和特殊召唤的卡相同。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93149655,'异色眼幻影龙',replace(replace('←4 【灵摆】 4→\r\n①：1回合1次，另一边的自己的灵摆区域有「异色眼」卡存在的场合，自己的表侧表示怪兽和对方怪兽进行战斗的攻击宣言时才能发动。那只自己怪兽的攻击力直到战斗阶段结束时上升1200。\r\n【怪兽效果】\r\n「异色眼幻影龙」的怪兽效果1回合只能使用1次。\r\n①：灵摆召唤的这张卡的攻击给与对方战斗伤害时才能发动。给与对方为自己的灵摆区域的「异色眼」卡数量×1200伤害。','\r',char(13)),'\n',char(10)),'攻击力上升','效果伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93151201,'雷电金刚','这张卡给与对方基本分战斗伤害时，把自己场上表侧表示存在的光属性怪兽数量的卡从对方卡组上面送去墓地。','卡组送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93157004,'欧米伽大日',replace(replace('调整2只＋调整以外的名字带有「大日」的怪兽1只\r\n这张卡同调召唤成功时，场上表侧表示存在的通常召唤的怪兽全部破坏。1回合1次，可以选择自己墓地存在的1只名字带有「大日」的怪兽当作装备卡使用给这张卡装备。效果怪兽的效果发动时，可以把这张卡装备的1张装备卡送去墓地让那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'通常召唤的怪兽全部破坏','装备','效果怪兽的效果发动无效并破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93187568,'魔法攻击士',replace(replace('①：这张卡可以把自己墓地1张魔法卡除外，从手卡特殊召唤。\r\n②：这张卡可以直接攻击。\r\n③：这张卡的战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93211810,'超音速波','自己回合选择场上1只名字带有「幻兽机」的怪兽才能发动。选择的怪兽在这个回合，攻击力变成原本攻击力的2倍，不受这张卡以外的魔法·陷阱卡的效果影响，向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。那之后，这个回合的结束阶段时自己场上的机械族怪兽全部破坏。这张卡发动的回合，选择的怪兽以外的怪兽不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93211836,'次世代军队','把名字带有「次世代」的怪兽解放对这张卡的上级召唤成功时，可以把对方场上存在的1张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93217231,'毒蛇供物','自己场上表侧表示存在的1只爬虫族怪兽破坏，对方场上存在的2张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93220472,'巨龙','这张卡在自己场上至少有1只其他的怪兽存在不能召唤。自己场上没有这张卡以外的龙族怪兽存在时，这张卡不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93221206,'古代精灵','活了数千年的妖精，能够操纵精灵发动攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93224848,'幻魔的殉教者','自己的手卡存在2张以上，自己场上有「神炎皇 乌利亚」或者「降雷皇 哈蒙」表侧表示存在时才能发动。把手卡全部送去墓地。在自己场上将3只「幻魔的殉教者衍生物」（恶魔族·暗·1星·攻/守0）攻击表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93224849,'幻魔的殉教者衍生物','由「幻魔的殉教者」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93236220,'围绕着星遗物的战斗','①：把自己场上1只表侧表示怪兽直到结束阶段除外，以对方场上1只表侧表示怪兽为对象才能发动。那只对方怪兽的攻击力·守备力下降因为这张卡发动而除外的怪兽的各自原本数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93238626,'上千主上的玉座','对方的结束阶段时，自己回复那个回合受到战斗伤害的次数×500基本分。此外，自己场上的名字带有「混沌No.」以外的「No.」的怪兽成为攻击对象时，可以通过把这张卡送去墓地让那次攻击无效。那之后，把持有和那只自己怪兽相同「No.」数字的名字带有「混沌No.」的怪兽在那只怪兽上面重叠当作超量召唤从额外卡组特殊召唤。「上千主上的玉座」在自己场上只能有1张表侧表示存在。','回复','攻击无效特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93260132,'魔法效果之矢','①：对方场上的表侧表示的魔法卡全部破坏，给与对方破坏数量×500伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93294869,'伤炎星-狼武松','这张卡反转的场合，可以从卡组选1张名字带有「炎舞」的陷阱卡在自己场上盖放。用反转召唤反转的场合，可以再从卡组选1张名字带有「炎舞」的魔法卡在自己场上盖放。','盖放','是否要选择名字带有「炎舞」的魔法卡在自己场上盖放？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93298460,'魔装战士 风龙',replace(replace('①：这张卡可以直接攻击。\r\n②：这张卡从场上送去墓地的场合，以自己墓地1只龙族·战士族·魔法师族的通常怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡片回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93302695,'星际仙踪-达克·萝丝',replace(replace('「星际仙踪-达克·萝丝」的①的效果1回合只能使用1次。\r\n①：把场上的这张卡除外才能发动。从手卡把1只5星以上的「星际仙踪」怪兽特殊召唤。这个效果在对方回合也能发动。\r\n②：1回合1次，支付1000基本分才能发动。这个回合，这张卡不会被战斗·效果破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'从手卡把「星际仙踪」怪兽特殊召唤','这张卡不会被战斗·效果破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93332803,'怒炎坏兽 多哥兰',replace(replace('①：这张卡可以把对方场上1只怪兽解放，从手卡往对方场上攻击表示特殊召唤。\r\n②：对方场上有「坏兽」怪兽存在的场合，这张卡可以从手卡攻击表示特殊召唤。\r\n③：「坏兽」怪兽在自己场上只能有1只表侧表示存在。\r\n④：1回合1次，把自己·对方场上3个坏兽指示物取除才能发动。对方场上的怪兽全部破坏。这个效果发动的回合，这张卡不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93343894,'水之魔导师','用水裹住对方并包围着施以攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93346024,'洞穴的潜龙','潜伏在洞窟的巨龙。平时很温顺、一旦生气就很恐怖。传说其在守着巨大的财富。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93353691,'不退的荒武者',replace(replace('战士族调整＋调整以外的战士族怪兽1只以上\r\n从持有比这张卡的攻击力高的攻击力的怪兽受到攻击的场合，这张卡不会被那次战斗破坏，进行战斗的对方怪兽在伤害计算后破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93356623,'魔弹-交叉统治者',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：自己场上有「魔弹」怪兽存在的场合，以场上1只表侧表示怪兽为对象才能发动。直到回合结束时，那只怪兽的攻击力·守备力变成0，效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93368494,'魔妖仙兽 大刃祸是',replace(replace('←7 【灵摆】 7→\r\n①：自己场上的「妖仙兽」怪兽的攻击宣言时才能发动。那只攻击怪兽的攻击力直到战斗阶段结束时上升300。\r\n【怪兽效果】\r\n这张卡不用灵摆召唤不能特殊召唤。\r\n①：这张卡的灵摆召唤不会被无效化。\r\n②：这张卡召唤·特殊召唤成功的场合，以场上最多2张卡为对象才能发动。那些卡回到持有者手卡。\r\n③：这张卡特殊召唤的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'攻击上升','弹回手卡','返回手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93369354,'电子鱼人-枪手','自己场上有3星以下的水属性怪兽表侧表示存在的场合，丢弃1张手卡才能发动。墓地存在的这张卡在自己场上特殊召唤。把这张卡作为同调素材的场合，其他的同调素材怪兽必须全部是水属性怪兽。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93377803,'孤毒之剑',replace(replace('自己场上的怪兽才能装备。\r\n①：「孤毒之剑」在自己场上只能有1张表侧表示存在。\r\n②：装备怪兽的原本的攻击力·守备力只在和对方怪兽进行战斗的伤害计算时变成2倍。\r\n③：自己场上有装备怪兽以外的怪兽存在的场合这张卡送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93379652,'宝石骑士·光棱晶',replace(replace('「宝石骑士」怪兽＋雷族怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。\r\n①：1回合1次，从手卡把1张「宝石骑士」卡送去墓地，以场上1张表侧表示的卡为对象才能发动。那张表侧表示的卡破坏。','\r',char(13)),'\n',char(10)),'表侧表示的1张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93382620,'命之纲','当自己的怪兽因战斗送去墓地时，将手卡全部丢弃发动这张卡，那只怪兽的攻击力上升800，并且特殊召唤上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93396832,'角笛粉碎','要让怪兽的召唤·特殊召唤无效的怪兽的效果·陷阱卡发动时才能发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93431518,'连接暗黑界的结界通路','这张卡发动的场合，自己不能在发动回合内召唤·反转召唤·特殊召唤。从自己墓地把名字中带有「暗黑界」的1只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93431862,'命运英雄 扣杀人','可以从手卡把1张名字带有「命运英雄」的卡送去墓地，给与对方基本分500分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93445074,'灰篮眼镜蛇',replace(replace('①：自己的怪兽区域的这张卡被战斗或者陷阱卡的效果破坏送去墓地的场合，以对方场上1只表侧表示怪兽为对象才能发动。这张卡当作装备卡使用给那只对方怪兽装备。\r\n②：这张卡的效果让这张卡装备中的场合，得到装备怪兽的控制权。这张卡从场上离开时装备怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93449450,'方程式运动员 声速侧挂骑手',replace(replace('①：这张卡的攻击力上升这张卡的等级×300，不受原本的等级或者阶级比这张卡的等级低的对方怪兽发动的效果影响。\r\n②：「方程式运动员」魔法·陷阱卡的效果发动的场合才能发动（伤害步骤也能发动）。这张卡的等级上升1星。\r\n③：这张卡的等级是7星以上的场合，被送去对方墓地的卡不去墓地而除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93451636,'发条蜜蜂','这张卡被战斗破坏送去墓地时，可以从卡组把1只4星以下的名字带有「发条」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93469007,'爆裂爆发','把自己场上存在的1只名字带有「/爆裂体」的怪兽解放发动。双方受到那只怪兽的等级×200的数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93483212,'极神圣帝 奥丁',replace(replace('名字带有「极星天」的调整＋调整以外的怪兽2只以上\r\n1回合1次，自己的主要阶段时才能发动。这张卡直到结束阶段时不受魔法·陷阱卡的效果影响。此外，场上表侧表示存在的这张卡被对方破坏送去墓地的场合，那个回合的结束阶段时可以把自己墓地存在的1只名字带有「极星天」的调整从游戏中除外，这张卡从墓地特殊召唤。这个效果特殊召唤成功时，可以从自己卡组抽1张卡。','\r',char(13)),'\n',char(10)),'不受魔法·陷阱卡的影响','特殊召唤','抽卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93503294,'幻变骚灵·隐私王班西',replace(replace('「幻变骚灵」怪兽2只以上\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：自己·对方的主要阶段把这张卡以外的自己场上1只「幻变骚灵」怪兽解放才能发动。从卡组把1只「幻变骚灵」怪兽在作为这张卡所连接区的自己场上特殊召唤。\r\n②：这张卡从场上送去墓地的场合，以自己墓地1张「幻变骚灵」卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93504463,'通向进化的吊桥','自己场上存在的怪兽被选择作为攻击对象时才能发动。选择自己墓地存在的1只名字带有「进化虫」的怪兽特殊召唤，把攻击对象转换为那只怪兽进行伤害计算。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93506862,'遗式虚法师','自己的主要阶段时，把这张卡从手卡丢弃才能发动。这个回合，对方不能对应名字带有「遗式」的仪式魔法卡的发动把魔法·陷阱·效果怪兽的效果发动，名字带有「遗式」的仪式怪兽仪式召唤成功时，对方不能把魔法·陷阱·效果怪兽的效果发动。','发动限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93542102,'变形斗士·手机',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：掷1次骰子。从自己卡组上面把出现的数目的卡翻开。那之中有4星以下的名字带有「变形斗士」的怪兽存在的场合，把1只无视召唤条件特殊召唤。那以外的卡回到卡组洗切。这个效果1回合只能使用1次。\r\n●守备表示：掷1次骰子。从自己卡组上面把出现的数目的卡确认再回到原状。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'特殊召唤','确认卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93543806,'先史遗产 太阳独石碑','1回合1次，选择自己场上1只名字带有「先史遗产」的怪兽才能发动。选择的怪兽的等级变成6星。这个效果发动的回合，自己不能把名字带有「先史遗产」的怪兽以外的怪兽特殊召唤。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93553943,'食人花','吃人的人面花，用有毒的触手进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93554166,'暗黑界之雷','选择场上里侧表示存在的1张卡破坏。之后，自己选择1张手卡丢弃去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93568288,'No.80 狂装霸王 狂想战曲王',replace(replace('4星怪兽×2\r\n自己的主要阶段时，自己场上的这只怪兽可以当作攻击力上升1200的装备卡使用给自己场上的超量怪兽装备。此外，把这张卡1个超量素材取除才能发动。选择对方墓地1张卡从游戏中除外。「No.80 狂装霸王 狂想战曲王」的这个效果1回合可以使用最多2次。','\r',char(13)),'\n',char(10)),'当作攻击力上升1200的装备卡使用给自己场上的超量怪兽装备','选择对方墓地1张卡从游戏中除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93581434,'刚鬼 地狱教练',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：自己场上没有怪兽存在的场合，把手卡的这张卡给对方观看，以自己墓地1只「刚鬼」连接怪兽为对象才能发动。这张卡从手卡特殊召唤。那之后，作为对象的怪兽特殊召唤。这个效果特殊召唤的连接怪兽攻击力下降500。\r\n②：只要自己场上有「刚鬼」连接怪兽存在，这张卡不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93599951,'沉默的邪恶灵','对方的战斗阶段才可以发动。1只攻击怪兽的攻击无效化，改为对方的其他的表侧表示的1只怪兽攻击。（对象是守备表示的场合变攻击表示）','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93600443,'假面变化二型',replace(replace('「假面变化二型」在1回合只能发动1张。\r\n①：丢弃1张手卡，以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽送去墓地，比那只怪兽等级高的1只相同属性的「假面英雄」怪兽当作「假面变化」的特殊召唤从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93655221,'屏蔽盾处理器','①：要让场上的怪兽破坏的魔法·陷阱·怪兽的效果发动时，以自己以及对方场上的连接怪兽各1只为对象才能把这张卡发动。作为对象的对方怪兽的效果无效，把这张卡当作装备卡使用给作为对象的自己怪兽装备。装备怪兽不会被效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93657021,'命运英雄 暮托邦人',replace(replace('「命运英雄」融合怪兽＋「命运英雄」怪兽\r\n①：这张卡融合召唤成功的场合才能发动。从自己的手卡·场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：1回合1次，以场上1只怪兽为对象才能发动。这个回合，那只怪兽不会被战斗·效果破坏，那只怪兽的战斗发生的双方的战斗伤害变成0。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93662626,'点火骑士·乌兹',replace(replace('←7 【灵摆】 7→\r\n①：另一边的自己的灵摆区域有「点火骑士」卡存在的场合才能发动。自己的灵摆区域的卡全部破坏，从自己的卡组·墓地选1只战士族·炎属性怪兽加入手卡。\r\n【怪兽描述】\r\n「德林加」的监督官兼亲卫队队长。总是被鲁莽行事的她牵着鼻子走，经常跟唯一的知己「点五零」吐露苦水。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93665266,'水晶机巧-量子白晶',replace(replace('「水晶机巧-量子白晶」的效果1回合只能使用1次。\r\n①：对方的主要阶段以及战斗阶段才能发动。从手卡把1只调整以外的怪兽效果无效特殊召唤，只用那只怪兽和这张卡为素材把1只机械族同调怪兽同调召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93671934,'士气高扬','每当1张装备魔法卡装备在怪兽身上时，这张装备魔法的控制者回复1000基本分。每当1张装备魔法卡离场时，这张装备魔法的控制者受到1000点伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93709215,'暗冥破坏神','这张卡不能特殊召唤。这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。这张卡在同1次的战斗阶段中可以作2次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93713837,'No.24 龙血鬼 德拉古勒斯',replace(replace('6星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以从额外卡组特殊召唤的1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示。这个效果在对方回合也能发动。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合才能发动。这张卡里侧守备表示特殊召唤。\r\n③：这张卡反转的场合发动。选场上1张卡送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93715853,'魔神 大神机','①：这张卡给与对方战斗伤害时才能发动。从自己的手卡·墓地选1只通常怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93717133,'银河眼光子龙',replace(replace('①：这张卡可以把自己场上2只攻击力2000以上的怪兽解放从手卡特殊召唤。\r\n②：这张卡和对方怪兽进行战斗的战斗步骤，以那1只对方怪兽为对象才能发动。那只对方怪兽和场上的这张卡除外。这个效果除外的怪兽在战斗阶段结束时回到场上，这个效果把超量怪兽除外的场合，这张卡的攻击力上升把那只超量怪兽除外时的超量素材数量×500。','\r',char(13)),'\n',char(10)),'对方怪兽和这张卡从游戏中除外','出场方式为特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93724592,'机巧忍者 三三九','这张卡可以攻击的场合必须作出攻击。场上表侧攻击表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变成守备表示。这张卡反转时，选择场上表侧表示存在的1只怪兽送去墓地。此外，这张卡反转的回合，这张卡可以直接攻击对方玩家。','变成守备表示','送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93730230,'先史遗产 水晶外星人',replace(replace('3星怪兽×2\r\n1回合1次，这张卡被选择作为攻击对象时，把这张卡1个超量素材取除才能发动。这个回合，这张卡不会被战斗以及卡的效果破坏，这张卡的战斗发生的对自己的战斗伤害由对方代受。','\r',char(13)),'\n',char(10)),'伤害转移','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93730409,'真空鼬鼠','反转：这个回合对方玩家不能发动魔法·陷阱卡。','发动限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93738004,'空牙团的孤高 莎吉塔',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。给与对方为「空牙团的孤高 莎吉塔」以外的自己场上的「空牙团」怪兽种类×500伤害。\r\n②：只要这张卡在怪兽区域存在，对方不能把这张卡以外的自己场上的「空牙团」怪兽作为效果的对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93747864,'沙漠之光','自己场上的所有怪兽全部变成表侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93749093,'马卡龙棉花糖',replace(replace('「马卡龙棉花糖」的效果1回合只能使用1次。\r\n①：这张卡被战斗·效果破坏的场合才能发动。从自己的手卡·卡组·墓地选这张卡以外的最多2只「马卡龙棉花糖」特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93751476,'猛炎星-鹿明','这张卡被卡的效果破坏送去墓地的回合的结束阶段时，可以从卡组把「猛炎星-鹿明」以外的1只名字带有「炎星」的4星怪兽特殊召唤。这张卡被名字带有「炎星」的同调怪兽的同调召唤使用送去墓地的场合，可以从卡组选1张名字带有「炎舞」的魔法卡在自己场上盖放。','特殊召唤','盖放','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93775296,'反转重用','①：以自己墓地最多2只反转怪兽为对象才能发动。那些怪兽表侧守备表示或者里侧守备表示在对方场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93788854,'彷徨的亡者','因不能成佛，而只能漫无目的徘徊的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93816465,'零点守卫士','把这张卡解放发动。这个回合自己怪兽不会被战斗破坏，和对方怪兽的战斗发生的对自己的战斗伤害变成0。这个效果在对方回合也能发动。','战斗伤害免疫','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93830681,'杀人皱鳃鲨','自己场上表侧表示存在的鱼族·海龙族·水族怪兽被选择作为攻击对象时，把墓地存在的这张卡从游戏中除外发动。把1只攻击怪兽的攻击无效，那个攻击力直到下次的自己的结束阶段时下降500。「杀人皱鳃鲨」的效果1回合只能使用1次。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93850652,'空牙团的剑士 比特',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。从手卡把「空牙团的剑士 比特」以外的1只「空牙团」怪兽特殊召唤。\r\n②：这张卡已在怪兽区域存在的状态，自己场上有这张卡以外的「空牙团」怪兽特殊召唤的场合才能发动。从卡组把「空牙团的剑士 比特」以外的1只「空牙团」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93882364,'次世代工作员','把这张卡解放发动。从手卡把1只名字带有「次世代」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93889755,'杀人小丑','这张卡的表示形式从守备表示变成攻击表示时，对方场上的1只怪兽回到持有者手卡。','对方1只怪兽回到手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93892436,'娱乐伙伴 秋千眼镜蛇',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，自己怪兽给与对方战斗伤害时才能发动。对方卡组最上面的卡送去墓地。\r\n【怪兽效果】\r\n①：这张卡可以直接攻击。\r\n②：这张卡攻击的场合，战斗阶段结束时变成守备表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93895605,'仇恨炸弹','①：自己的恶魔族怪兽被选择作为攻击对象时，以1只攻击怪兽和那1只攻击对象怪兽为对象才能发动。那只攻击怪兽和那只攻击对象怪兽2只破坏，给与对方破坏的攻击怪兽的原本攻击力数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93900406,'蘑菇怪人','每次的自己准备阶段受到300分的伤害。可以在自己的结束阶段支付500分，把这张卡的控制权转移给对方。','伤害','控制权转移','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93912845,'复活礼物','选择自己墓地存在的1只调整特殊召唤。这个效果特殊召唤的怪兽的效果无效化。在对方场上把2只「礼物魔衍生物」（恶魔族·暗·3星·攻/守1500）特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93912846,'礼物魔衍生物','由「苏生的礼物」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93920745,'企鹅士兵','反转：可以选择最多2张怪兽卡回到持有者手卡。','返回手牌','是否要返回怪物到手卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93927067,'血腥魔兽人帝王',replace(replace('①：自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡战斗破坏对方怪兽的场合发动。这张卡的攻击力上升500。\r\n③：这张卡被战斗破坏的场合发动。让把这张卡破坏的怪兽的攻击力下降500。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93946239,'无之炼狱','自己手卡是3张以上的场合才能发动。从自己卡组抽1张卡，这个回合的结束阶段时自己手卡全部丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93966624,'魔法进击者','①：这张卡在手卡存在的场合，以自己以及对方场上的表侧表示的魔法卡各1张为对象才能发动。那些卡除外，这张卡从手卡特殊召唤。','卡片除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93969023,'黑钢龙',replace(replace('①：自己主要阶段以自己场上1只「真红眼」怪兽为对象才能发动。从自己的手卡·场上把这只怪兽当作攻击力上升600的装备卡使用给那只自己怪兽装备。\r\n②：这张卡从场上送去墓地的场合才能发动。从卡组把1张「真红眼」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(93983867,'戏法箱','①：自己场上的「娱乐法师」怪兽被战斗·效果破坏送去墓地的场合，以对方场上1只怪兽为对象才能发动。自己直到结束阶段得到那只怪兽的控制权。那之后，选自己墓地1只「娱乐法师」怪兽在对方场上特殊召唤。这个回合的结束阶段，这个效果特殊召唤的怪兽的控制权回归原本持有者。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94004268,'亚马逊剑士','①：这张卡的战斗发生的对自己的战斗伤害由对方代受。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94022093,'大肚海蛇','样子很怪的海蛇。特征是大口和大牙。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94042337,'暴雨怪','突然降下倾盆大雨的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94068856,'时间推移','自己场上表侧表示存在的1只名字带有「命运女郎」的怪兽的等级直到结束阶段时上升3星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94073244,'空牙团的击手 砰帕',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。从手卡把「空牙团的击手 砰帕」以外的1只「空牙团」怪兽特殊召唤。\r\n②：这张卡已在怪兽区域存在的状态，自己场上有这张卡以外的「空牙团」怪兽特殊召唤的场合，以场上1张表侧表示的卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94079037,'采矿鼹鼠','这张卡1回合只有1次不会被战斗破坏。这张卡作为兽族怪兽的同调召唤的素材送去墓地的场合，从自己卡组抽1张卡。这张卡因对方的卡的效果从场上离开的场合，从游戏中除外。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94081496,'黑亮的G','对方场上有1只同调怪兽特殊召唤时，把自己墓地存在的这张卡从游戏中除外发动。那1只同调怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94092230,'侵入魔鬼·威角','这张卡不能特殊召唤。这张卡通常召唤的场合，必须把自己场上存在的3只名字带有「侵入魔鬼」的怪兽解放作召唤。可以把基本分支付一半，这张卡以外的场上存在的卡全部破坏。这个效果1回合只能使用1次。','把3只怪兽解放召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94096018,'秘女郎-雾美人',replace(replace('①：这张卡召唤·特殊召唤成功的场合，宣言卡的种类（怪兽·魔法·陷阱）才能发动。对方卡组最上面的卡给双方确认，宣言的种类的卡的场合，自己从卡组抽1张。\r\n②：1回合1次，以自己场上1只「秘旋谍-花公子」和对方场上1只怪兽为对象才能发动。那2只怪兽回到持有者手卡。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94096616,'武神器-蛇','自己的主要阶段1，把这张卡从手卡送去墓地，选择自己场上1只名字带有「武神」的怪兽才能发动。这个回合，选择的怪兽可以直接攻击对方玩家。','直接攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94119480,'终焉守护者 阿德琉斯',replace(replace('5星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上表侧表示存在的1张卡才能发动。选择的卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94119974,'双头恐龙王','恐龙族中的强力怪兽，两只头同时攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94142993,'廷达魔三角之侵入者',replace(replace('这个卡名的①②③的效果1回合各能使用1次。\r\n①：这张卡反转的场合才能发动。从卡组把1张「廷达魔三角」卡加入手卡。\r\n②：这张卡召唤成功时才能发动。从卡组把1张「廷达魔三角」卡送去墓地。\r\n③：这张卡在墓地存在，自己场上有怪兽里侧守备表示特殊召唤的场合发动。这张卡从墓地里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94145021,'小丑与锁鸟','①：对方在抽卡阶段以外从卡组把卡加入手卡的场合，把这张卡从手卡送去墓地才能发动。这个回合，双方不能从卡组把卡加入手卡。这个效果在对方回合也能发动。','检索限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94145683,'燕巢','①：把自己场上1只表侧表示的鸟兽族怪兽解放才能发动。和解放的怪兽相同等级的1只鸟兽族怪兽从卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94156050,'颠倒眼镜','场上表侧表示存在的全部怪兽的攻击力直到结束阶段时变成一半。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94160895,'真龙凰 玛丽亚姆内',replace(replace('「真龙凰 玛丽亚姆内」的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。从这张卡以外的手卡以及自己场上的表侧表示怪兽之中把包含风属性怪兽的2只怪兽破坏，这张卡从手卡特殊召唤，把2只风属性怪兽破坏的场合，可以从对方卡组上面把4张卡除外。\r\n②：这张卡被效果破坏的场合才能发动。从卡组把1只风属性以外的幻龙族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','加入手卡','是否把对方卡组的卡除外？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94163677,'无限的手札','只要这张卡在场上表侧表示存在，双方没有手卡数量限制。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94192409,'强制脱出装置','①：以场上1只怪兽为对象才能发动。那只怪兽回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94203886,'我我我小子','自己场上有「我我我小子」以外的名字带有「我我我」的怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤成功时，可以选择自己场上1只名字带有「我我我」的怪兽，这张卡的等级变成和选择的怪兽相同等级。这个效果发动的回合，自己不能进行战斗阶段。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94212438,'通灵盘',replace(replace('这张卡和「死之信息」卡4种类在自己场上齐集时，自己决斗胜利。\r\n①：对方结束阶段把这个效果发动。从手卡·卡组让1张「死之信息」卡以「E」「A」「T」「H」的顺序在自己的魔法与陷阱区域出现。\r\n②：自己场上的「通灵盘」或者「死之信息」卡从场上离开时自己场上的「通灵盘」以及「死之信息」卡全部送去墓地。','\r',char(13)),'\n',char(10)),'让「死之信息」卡出现','请选择要出现在魔法与陷阱卡区域的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94215860,'夜叉','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。这张卡召唤·反转时，可以选择对方场上存在的1张魔法·陷阱卡回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94220427,'升阶魔法-紧急型混沌之力','选择自己场上1只5阶以上的超量怪兽才能发动。比选择的怪兽阶级高1阶的1只名字带有「混沌No.」或者「混沌超量」的怪兽在选择的自己怪兽上面重叠当作超量召唤从额外卡组特殊召唤。此外，这张卡在墓地存在，自己场上有5阶以上的超量怪兽特殊召唤时，墓地的这张卡可以加入手卡。「升阶魔法-紧急型混沌之力」的这个效果在决斗中只能使用1次。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94230224,'针球','反转：可以支付2000分，对方受到1000分的伤害。','反转','是否要使用「针球」的效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94243005,'混沌空间','每次怪兽从游戏中除外，每有1只给这张卡放置1个混沌指示物。1回合1次，可以把自己场上的混沌指示物4个以上取除，选择持有和取除的数量相同等级的从游戏中除外的1只怪兽在自己场上特殊召唤。场上的这张卡被对方的效果送去墓地时，可以把持有这张卡放置的混沌指示物数量以下的等级的1只光属性或者暗属性的怪兽从卡组加入手卡。','特殊召唤','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94253609,'元素吸收器','把手卡1张怪兽卡从游戏中除外。持有和这个效果除外的怪兽卡相同的属性的对方怪兽，只要这张卡在场上存在不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94256039,'通天塔','只要这张卡在场上存在，每次自己或者对方把魔法卡发动，给这张卡放置1个魔力指示物。这张卡放置第4个魔力指示物时这张卡破坏，那个时候把魔法卡发动的玩家受到3000分伤害。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94283662,'传送恶魔',replace(replace('①：1回合1次，自己主要阶段才能发动。从手卡丢弃1只恶魔族怪兽，这张卡的攻击力直到回合结束时上升500。\r\n②：自己场上的这张卡被破坏送去墓地时，以除外的1只自己的暗属性怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'攻击上升','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94303232,'阻碍星','自己场上表侧攻击表示存在的怪兽的表示形式改变为表侧守备表示的回合，那只怪兽才能装备。选择对方场上存在的1只怪兽。那只怪兽不能作攻击和表示形式的改变。装备怪兽被破坏让这张卡送去墓地时，选择的对方怪兽从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94331452,'音响战士 架子鼓','宣言1个属性，选择场上表侧表示存在的1只名字带有「音响战士」的怪兽发动。选择的怪兽变成宣言的属性。这个效果1回合只能使用1次。此外，宣言1个属性，可以把自己墓地存在的这张卡从游戏中除外，自己场上表侧表示存在的1只名字带有「音响战士」的怪兽变成宣言的属性。','属性变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94344242,'电子光虫-焊料织网蛛',replace(replace('把这张卡作为超量召唤的素材的场合，不是昆虫族怪兽的超量召唤不能使用。\r\n①：1回合1次，以自己场上1只表侧攻击表示怪兽为对象才能发动。那只怪兽变成守备表示，从手卡把1只昆虫族·3星怪兽守备表示特殊召唤。\r\n②：场上的这张卡为素材作超量召唤的怪兽得到以下效果。\r\n●这次超量召唤成功的场合发动。对方场上的全部表侧表示怪兽守备力变成0，变成守备表示。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94365540,'廷达魔三角之底边守卫者',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：自己场上有里侧守备表示怪兽存在的场合，这张卡可以从手卡守备表示特殊召唤。\r\n②：这张卡已在怪兽区域存在的状态，对方的连接怪兽的所连接区有怪兽召唤·特殊召唤的场合，把这张卡解放才能发动。从手卡·卡组把1只「廷达魔三角」怪兽表侧攻击表示或者里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94374859,'僵尸推销员','每次自己场上的怪兽给与对方玩家战斗伤害，选择对方墓地存在的1张卡回到对方卡组最下面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94377247,'假面魔兽的仪式',replace(replace('「假面魔兽 鬼面修道士」的降临必需。\r\n①：从自己的手卡·场上把等级合计直到8以上的怪兽解放，从手卡把「假面魔兽 鬼面修道士」仪式召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94380860,'No.103 神葬零娘 暮零',replace(replace('4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上表侧攻击表示存在的1只持有和原本攻击力不同攻击力的怪兽才能发动。选择的怪兽破坏，从卡组抽1张卡。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'破坏抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94381039,'光道天使 基路伯','这张卡用名字带有「光道」的怪兽为祭品作祭品召唤成功时，可以从卡组上面把4张卡送去墓地让对方场上最多2张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94388754,'花札卫-萩间猪-',replace(replace('这张卡不能通常召唤。把「花札卫-萩间猪-」以外的自己场上1只「花札卫」怪兽解放的场合可以特殊召唤。\r\n①：这张卡特殊召唤成功的场合发动。自己从卡组抽1张，给双方确认。那是「花札卫」怪兽的场合，可以选对方场上1只怪兽破坏。不是的场合，那张卡送去墓地。','\r',char(13)),'\n',char(10)),'抽卡','是否选对方场上1只怪兽破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94412545,'机械变色龙','变换身体颜色可以隐藏在任何地方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94415058,'星读之魔术师',replace(replace('←1 【灵摆】 1→\r\n①：自己的灵摆怪兽进行战斗的场合，对方直到伤害步骤结束时魔法卡不能发动。\r\n②：另一边的自己的灵摆区域没有「魔术师」卡或者「异色眼」卡存在的场合，这张卡的灵摆刻度变成4。\r\n【怪兽效果】\r\n①：1回合1次，只让自己场上的灵摆怪兽1只因对方的效果回到自己手卡时才能发动。那1只同名怪兽从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94418111,'魔弹射手 狂野',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，自己·对方回合自己可以把「魔弹」魔法·陷阱卡从手卡发动。\r\n②：和这张卡相同纵列有魔法·陷阱卡发动的场合，以自己墓地3张「魔弹」卡为对象才能发动。那3张卡加入卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94425169,'元素之泉','场上的怪兽回到持有者手卡的场合，每1次自己回复500基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94432298,'壳醒的炼狱',replace(replace('①：每次自己准备阶段才能把这个效果发动。从卡组把最多2只「狱火机」怪兽送去墓地。\r\n②：自己场上有「狱火机」怪兽以外的怪兽存在的场合这张卡送去墓地。','\r',char(13)),'\n',char(10)),'是否现在使用这张卡的效果？','发动同时使用效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94454495,'星际仙踪-魔鞋骑手',replace(replace('①：这张卡召唤·特殊召唤成功的场合，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。\r\n②：这张卡被战斗·效果破坏送去墓地的场合，把墓地的这张卡除外才能发动。从卡组把1只4星以下的「星际仙踪」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'魔法·陷阱卡破坏','从卡组把「星际仙踪」怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94463200,'血之刻印','选择自己场上1只名称中含有「恶魔」字样的怪兽发动。所选择的怪兽在准备阶段支付基本分时，对方也要支付相同的基本分。当这张卡离场时，所选择的怪兽被破坏；当所选择的怪兽离场时，这张卡被破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94484482,'滑流召唤','对方对怪兽的召唤·反转召唤·特殊召唤成功时才能发动。从手卡把1只4星以下的怪兽表侧守备表示特殊召唤。这个效果特殊召唤的怪兽在结束阶段时回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94515289,'冰结之菲茨杰拉德',replace(replace('暗属性调整＋调整以外的兽族怪兽1只\r\n这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。这张卡被战斗破坏送去墓地时，自己场上没有怪兽存在的场合，可以丢弃1张手卡把这张卡从墓地表侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94535485,'携手魔人',replace(replace('①：只要这张卡在怪兽区域存在，对方不能选择其他怪兽作为攻击对象。\r\n②：这张卡的守备力上升这张卡以外的自己场上的表侧守备表示怪兽的原本守备力的合计数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94538053,'最高战士','这张卡向对方怪兽攻击的场合，伤害步骤内攻击力上升400。这张卡战斗破坏对方怪兽的场合，直到下次的自己的准备阶段时这张卡的等级变成2星，原本的攻击力·守备力成为一半数值。','等级攻守变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94561645,'反击之门','①：对方怪兽的直接攻击宣言时才能发动。那次攻击无效，自己从卡组抽1张。那张抽到的卡是怪兽的场合，可以把那只怪兽表侧攻击表示通常召唤。','是否表侧攻击表示通常召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94566432,'帝王龙','「守城的翼龙」＋「妖精龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94568601,'暴君龙','对方的场上有怪兽存在的场合，战斗阶段中有1次可以再次攻击的机会。以这张卡为对象的陷阱卡的效果无效并破坏。其他的卡的效果使这张卡从墓地特殊召唤的场合，必须把自己场上的1只龙族怪兽做祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94573223,'甲虫装机 吉咖螳螂',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：以自己场上1只「甲虫装机」怪兽为对象才能发动。这张卡从手卡当作装备卡使用给那只自己的「甲虫装机」怪兽装备。\r\n②：把这张卡当作装备卡使用来装备的怪兽的原本攻击力变成2400。\r\n③：给怪兽装备的这张卡被送去墓地的场合，以「甲虫装机 吉咖螳螂」以外的自己墓地1只「甲虫装机」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94585852,'万魔殿-恶魔的巢窟-','名称中含有「恶魔」字样的怪兽在准备阶段可以不必支付基本分。当名称中含有「恶魔」字样的怪兽由于战斗以外的方式被破坏送去墓地时，可以从卡组中选择1张等级比被破坏的怪兽等级低，且名称中含有「恶魔」字样的怪兽卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94599451,'魔导研究所',replace(replace('①：每次自己场上的表侧表示的「魔导兽」灵摆怪兽卡被战斗·效果破坏给这张卡放置2个魔力指示物。\r\n②：1回合1次，把自己场上的魔力指示物任意数量取除才能发动。从卡组的怪兽以及自己的额外卡组的表侧表示的灵摆怪兽之中选持有和取除数量相同等级的1只可以放置魔力指示物的怪兽加入手卡。\r\n③：场上的这张卡被效果破坏的场合，可以作为代替把这张卡1个魔力指示物取除。','\r',char(13)),'\n',char(10)),'','请选择要取除的指示物的数量','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94622638,'盟军·次世代集电人','这张卡以外的自己场上表侧表示存在的和这张卡相同属性的怪兽的攻击力上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94626050,'鱼雨','场上表侧表示存在的鱼族·海龙族·水族怪兽从游戏中除外时才能发动。从自己手卡把1只3星以下的鱼族·海龙族·水族怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94626871,'淘气仙星·斯威特戴薇儿',replace(replace('「淘气仙星」怪兽2只\r\n①：只要这张卡在怪兽区域存在，每次这张卡所连接区的怪兽被战斗·效果破坏送去墓地，给与对方200伤害。\r\n②：每次「淘气仙星」怪兽的效果让对方受到伤害发动。对方场上的全部表侧表示怪兽的攻击力直到回合结束时下降这张卡所连接区的怪兽数量×200。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94634433,'紧急同调','这张卡在战斗阶段中才能发动。把1只同调怪兽同调召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94656263,'影蜥蜴','这张卡不能通常召唤。自己对4星怪兽的召唤成功时，这张卡可以从手卡特殊召唤。这张卡不能作为同调素材。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94662235,'命运弯曲','自己场上表侧表示存在的怪兽只有名字带有「命运女郎」的怪兽的场合才能发动。魔法·陷阱卡的发动，怪兽的召唤的其中1个无效，那张卡从游戏中除外。这个回合的结束阶段时，这个效果从游戏中除外的卡回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94664694,'狂暴恶魔',replace(replace('①：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n②：攻击表示的这张卡被选择作为攻击对象的场合发动。这张卡变成守备表示。','\r',char(13)),'\n',char(10)),'变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94666032,'归魂复仇死者的诞生',replace(replace('「复仇死者」仪式怪兽的降临必需。\r\n①：等级合计直到变成仪式召唤的怪兽的等级以上为止，把自己的手卡·场上的怪兽解放或者作为解放的代替而把自己墓地的不死族怪兽除外，从自己的手卡·墓地把1只「复仇死者」仪式怪兽仪式召唤。\r\n②：自己场上的「归魂复仇死者·屠魔侠」被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94667532,'机械犬 小栗子','场上的这张卡被战斗破坏送去墓地时，给与双方基本分1000分的伤害。场上的这张卡因为战斗以外的方式的破坏送去墓地时，给与对方基本分1000分的伤害。','双方伤害','对方伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94675535,'拉瓦斯','行动速度飞快的鸟怪，用细长的手腕将敌人绞杀。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94681654,'羽风攻击','让自己场上表侧表示存在的1只名字带有「黑羽」的怪兽回到卡组发动。从自己卡组把1只名字带有「黑羽」的怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94689206,'积木龙',replace(replace('这张卡不能通常召唤。从自己的手卡·墓地把3只地属性怪兽除外的场合才能从手卡·墓地特殊召唤。「积木龙」的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，自己场上的岩石族怪兽不会被战斗以外破坏。\r\n②：这张卡从场上送去墓地的场合才能发动。等级合计直到变成8星为止，从卡组选最多3只岩石族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94689635,'紫光之宣告者','①：对方把陷阱卡发动时，从手卡把这张卡和1只天使族怪兽送去墓地才能发动。那个发动无效并破坏。','对方的陷阱卡的发动无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94693857,'文具电子人001',replace(replace('①：这张卡的攻击力·守备力上升自己场上的机械族怪兽数量×500。\r\n②：这张卡在墓地存在，场上有机械族怪兽2只以上同时特殊召唤的场合才能发动。这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94703021,'集群拥塞器',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：自己场上没有连接怪兽存在，这张卡召唤·特殊召唤成功的场合才能发动。在自己场上把1只「拥塞器衍生物」（电子界族·暗·1星·攻/守0）特殊召唤。\r\n②：自己的连接怪兽被攻击的战斗步骤1次，把那只连接怪兽和墓地的这张卡除外才能发动。把最多有对方场上的连接怪兽数量的「拥塞器衍生物」在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'是否继续特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94703022,'拥塞器衍生物','由「集群拥塞器」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94716515,'尖刺神的杀虫剂','场上表侧表示存在的昆虫族怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94739788,'洗脑解除','只要这张卡在场上存在，自己与对方场上存在的所有怪兽的控制权全部回到原来的持有者手里。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94766498,'先史遗产 阿兹特克面具石人','自己回合自己是有把名字带有「先史遗产」的魔法卡发动的场合，这张卡可以从手卡特殊召唤。「先史遗产 阿兹特克面具石人」在自己场上只能有1只表侧表示存在。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94770493,'翻倍机会','怪兽的攻击无效时，选择那1只怪兽发动。这次战斗阶段中，选择的怪兽只有1次可以再攻击。那个场合，选择的怪兽在伤害步骤内攻击力变成2倍。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94772232,'死之信息「T」','这张卡不用「通灵盘」的效果不能在场上出现。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94773007,'地雷蜘蛛','这张卡攻击宣言时，猜硬币的正反。猜中的话就继续攻击。猜不中的话自己的基本分减半再攻击。','投掷硬币','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94784213,'威风妖怪·狐',replace(replace('←2 【灵摆】 2→\r\n【怪兽效果】\r\n「威风妖怪·狐」的①的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功时才能发动。从卡组把1张「威风妖怪」陷阱卡加入手卡。\r\n②：这张卡只要在怪兽区域存在，不会成为对方的效果的对象，不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94793422,'心眼之矛','当装备这张卡的怪兽对对方造成战斗伤害时，战斗伤害的数值变为1000点。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94801854,'不知火的隐者',replace(replace('「不知火的隐者」的①②的效果1回合各能使用1次。\r\n①：把自己场上1只不死族怪兽解放才能发动。从卡组把1只守备力0的不死族调整特殊召唤。\r\n②：这张卡被除外的场合，以「不知火的隐者」以外的除外的1只自己的「不知火」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果的发动时场上有「不知火流 转生之阵」存在的场合，这个效果的对象可以变成2只。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94804055,'火焰壁','对方直接攻击宣言时，可以把自己墓地存在的1只炎族怪兽从游戏中除外，使那只怪兽的攻击无效。每次自己的准备阶段支付500基本分。若没有支付，这张卡破坏。','攻击无效','是否现在使用「火焰壁」的效果？','是否要支付500LP维持「火焰壁」？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94807487,'希望剑一斩',replace(replace('「希望皇 霍普」怪兽才能装备。\r\n①：装备怪兽不会被效果破坏。\r\n②：只要这张卡在魔法与陷阱区域存在，每次怪兽的攻击无效，给这张卡放置1个希望剑指示物。装备怪兽的攻击力上升这张卡的希望剑指示物数量×500。\r\n③：自己场上的装备怪兽把超量素材取除来让效果发动的场合，这张卡可以当作取除的超量素材中的1个使用。','\r',char(13)),'\n',char(10)),'放置1个希望剑指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94820406,'暗黑融合','从手卡或者场上把融合怪兽卡决定的怪兽送去墓地，把1只恶魔族的融合怪兽从融合卡组特殊召唤。这个效果特殊召唤的怪兽在这个回合不会成为对方的魔法·陷阱·效果怪兽的效果的对象。（这个特殊召唤当作融合召唤使用）','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94845226,'魔轰神兽 鲁比拉达','自己场上表侧表示存在的这张卡被选择作为攻击对象时，可以从手卡丢弃1只名字带有「魔轰神」的怪兽让那次攻击无效。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94853057,'闪光之追放者','只要这张卡在场上表侧表示存在，被送去墓地的卡不去墓地从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94861297,'检问','①：对方怪兽的攻击宣言时才能发动。对方手卡全部确认，那之中有怪兽卡的场合，那次攻击无效。那之后，自己从对方手卡选1只怪兽丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94878265,'毛扎','这张卡被战斗破坏并送去墓地时，可以把自己墓地存在的1只4星的兽族怪兽加入手卡。','返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94886282,'光之援军','①：从自己卡组上面把3张卡送去墓地才能发动。从卡组把1只4星以下的「光道」怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94905343,'牛头人马兽','「牛头人」＋「人马兽」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94919024,'月光红狐',replace(replace('①：这张卡被效果送去墓地的场合，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时变成0。\r\n②：自己场上的「月光」怪兽为对象的魔法·陷阱·怪兽的效果发动时，把墓地的这张卡除外才能发动。那个发动无效，双方玩家回复1000基本分。','\r',char(13)),'\n',char(10)),'特殊召唤的怪兽全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94933468,'枉然-背叛的嘲笑','对方场上的超量怪兽攻击宣言时，选择那1只怪兽才能发动。选择的怪兽不能攻击，效果无效化。只要选择的怪兽在场上存在，每次对方的结束阶段从对方卡组上面把3张卡送去墓地。选择的怪兽从场上离开时，这张卡破坏。','卡组破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94937430,'临时收入',replace(replace('①：只要这张卡在魔法与陷阱区域存在，每次自己的额外卡组有卡加入，给这张卡放置1个魔力指示物（最多3个）。\r\n②：把有3个魔力指示物放置的这张卡送去墓地才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94940436,'磁力召唤圆 LV2','从手卡特殊召唤1只2星以下的机械族怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94944637,'魔族召唤师',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当通常召唤使用的再度召唤，这张卡当作效果怪兽使用并得到以下效果。\r\n●把手卡或者墓地存在的1只恶魔族怪兽特殊召唤。这个效果1回合只能使用1次。这张卡从场上离开时，这个效果特殊召唤的恶魔族怪兽破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94950218,'埋伏破坏','名字带有「希望皇 霍普」的怪兽才能装备。装备怪兽攻击宣言时，选择对方场上1张魔法·陷阱卡破坏。对方不能对应这个效果的发动把魔法·陷阱卡发动。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94973028,'幻兽机 倾转翼马','这张卡特殊召唤成功时，这张卡以外的名字带有「幻兽机」的怪兽在自己场上存在的场合，把2只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。这张卡的等级上升自己场上的「幻兽机衍生物」的等级的合计数值。只要自己场上有衍生物存在，这张卡不会被战斗以及效果破坏。此外，1回合1次，把2只衍生物解放才能发动。选择对方场上1张卡破坏并从游戏中除外。','特殊召唤Token','破坏并除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94973029,'幻兽机衍生物','由「幻兽机 倾转翼马」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94977269,'神影依·米德拉什',replace(replace('「影依」怪兽＋暗属性怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。\r\n①：这张卡不会被对方的效果破坏。\r\n②：只要这张卡在怪兽区域存在，那个期间双方1回合只能有1次把怪兽特殊召唤。\r\n③：这张卡被送去墓地的场合，以自己墓地1张「影依」魔法·陷阱卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94982447,'真龙骑将 得律阿斯3世',replace(replace('这张卡表侧表示上级召唤的场合，可以作为怪兽的代替而把自己场上的永续魔法·永续陷阱卡解放。\r\n①：上级召唤的表侧表示的这张卡从场上离开的场合才能发动。从卡组把1只「真龙」怪兽守备表示特殊召唤。\r\n②：只要这张卡在怪兽区域存在，这张卡以外的场上的「真龙」怪兽不会成为对方的效果的对象，不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'解放魔法·陷阱卡上级召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(94997874,'圣占术姬 塔罗光巫女',replace(replace('「圣占术的仪式」降临。「圣占术姬 塔罗光巫女」的①②的效果1回合只能有1次使用其中任意1个，对方回合也能发动。\r\n①：以场上1只里侧表示怪兽为对象才能发动。那只怪兽变成表侧攻击表示。\r\n②：以场上1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示。\r\n③：自己结束阶段才能发动。选自己的手卡·墓地1只反转怪兽里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'变成表侧攻击表示','变成里侧守备表示','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95004025,'真龙导士 威风凛·少女',replace(replace('这张卡表侧表示上级召唤的场合，可以作为怪兽的代替而把自己场上的永续魔法·永续陷阱卡解放。\r\n①：1回合1次，上级召唤的这张卡存在，对方把魔法·陷阱·怪兽的效果发动时才能发动。从卡组把1只「真龙」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'解放魔法·陷阱卡上级召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95026693,'火焰之魂','对方玩家抽1张卡。从自己卡组选择1只炎族怪兽从游戏中除外。给与对方基本分除外怪兽的攻击力一半数值的伤害。这张卡发动的场合，这个回合自己不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95027497,'忍者头领 半藏','这张卡召唤成功时，可以从卡组把1张名字带有「忍法」的卡加入手卡。此外，这张卡反转召唤·特殊召唤成功时，可以从卡组把「忍者头领 半藏」以外的1只名字带有「忍者」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95040215,'黑羽-星影之苦剑鸟',replace(replace('调整＋调整以外的怪兽1只以上\r\n「黑羽-星影之苦剑鸟」的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合发动。给与对方800伤害。那之后，选对方1只表侧表示怪兽，那个攻击力·守备力下降800。\r\n②：只要这张卡在怪兽区域存在，自己在通常召唤外加上只有1次，自己主要阶段可以把1只「黑羽」怪兽召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95051344,'成佛','有装备卡装备的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95081386,'三振障壁',replace(replace('①：对方场上的卡只有3张的场合，可以从以下效果选择1个发动。\r\n●这个回合，自己怪兽不会被战斗破坏。\r\n●这个回合，自己受到的战斗伤害变成0。\r\n●这个回合，每次自己怪兽给与对方战斗伤害，自己基本分回复那个数值。','\r',char(13)),'\n',char(10)),'这个回合，自己怪兽不会被战斗破坏','这个回合，自己受到的战斗伤害变成0','这个回合，每次自己怪兽给与对方战斗伤害，自己基本分回复那个数值','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95083785,'过早的归还','①：把1张手卡除外，以除外的1只自己怪兽为对象才能发动。那只怪兽里侧守备表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95084054,'电气大炮','场上有4星以下的雷族怪兽召唤·特殊召唤时，给与对方基本分600分伤害。这个效果1回合只能使用1次。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95090813,'核成战甲兵','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只战士族怪兽给对方观看。或者都不进行让这张卡破坏。1回合1次，可以选择自己墓地存在的1只等级3以下的战士族怪兽装备在这张卡上。这张卡的攻击力上升由这个效果装备的怪兽卡攻击力总和的一半。若这张卡被战斗破坏，作为代替把全部由这个效果装备的怪兽卡破坏。','选择一张「核成兽的钢核」送去墓地','选择一张战士族怪兽给对方观看','破坏「核成战甲兵」','装备','','','','','','','','','','','','');
INSERT INTO texts VALUES(95096437,'义贼的极意书','选择自己场上表侧表示存在的1只通常怪兽发动。这个回合选择怪兽给与对方基本分战斗伤害时，对方随机丢弃2张手卡。','丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95113856,'幻子力空母 进取神船号',replace(replace('9星怪兽×2\r\n1回合1次，可以把这张卡1个超量素材取除，从以下效果选择1个发动。\r\n●选对方场上1张卡从游戏中除外。\r\n●对方手卡随机选1张从游戏中除外。\r\n●选对方墓地1张卡从游戏中除外。\r\n●对方卡组最上面的卡从游戏中除外。','\r',char(13)),'\n',char(10)),'选择效果发动','选对方场上1张卡从游戏中除外','对方手卡随机选1张从游戏中除外','选对方墓地1张卡从游戏中除外','对方卡组最上面的卡从游戏中除外','','','','','','','','','','','');
INSERT INTO texts VALUES(95132338,'水之合唱','①：场上有同名怪兽存在的场上的怪兽的攻击力·守备力上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95144193,'锹甲独角仙','「锹甲阿尔法」＋「大力独角仙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95166228,'贵甲螳螂',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当通常召唤使用的再度召唤，这张卡当作效果怪兽使用并得到以下效果。\r\n●这张卡可以向对方场上的全部怪兽各作1次攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95169481,'恐牙狼 钻石恐狼',replace(replace('4星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以自己场上1只兽族·兽战士族·鸟兽族怪兽和场上1张卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95174353,'阿米巴','场上表侧表示存在的这张卡的控制权转移给对方时，对方受到2000分的伤害。这个效果只在这张卡在场上表侧表示存在能使用1次。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95178994,'巨型病毒','这张卡被战斗破坏送去墓地时，给与对方基本分500分伤害。可以再从卡组把「巨型病毒」表侧攻击表示特殊召唤上场。之后卡组洗切。','伤害','是否要特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95194279,'次元歪曲','当自己的墓地里没有卡存在的场合这张卡才能发动。选择自己1只被除外的怪兽特殊召唤到自己场上。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95204084,'核成风神','从手卡让1张「核成兽的钢核」回到卡组最上面发动。对方场上存在的特殊召唤的怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95214051,'翡翠虫笛','对方从卡组中选择1张昆虫族怪兽卡，那张卡放在卡组最上面。','请选择要放置到卡组最上方的卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95218695,'地中族邪界兽·阿尔褐帘骸',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合才能发动。这个回合，自己场上的「地中族」卡不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95220856,'怨灵的湿地带','全场怪兽召唤·反转召唤·特殊召唤的那个回合不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95231062,'失落之蓝破坏者','场上有这张卡以外的鱼族·海龙族·水族怪兽存在的场合把这张卡解放才能发动。选择场上1张魔法·陷阱卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95239444,'森罗的镇神 山精',replace(replace('7星怪兽×2\r\n1回合1次，把自己的手卡·场上1只植物族怪兽送去墓地才能发动。把那个等级数量的卡从卡组上面确认，用喜欢的顺序回到卡组上面。此外，1回合1次，把这张卡1个超量素材取除才能发动。从自己卡组上面把最多3张卡翻开。那之中有植物族怪兽的场合，那些怪兽全部送去墓地，选最多有那个数量的这张卡以外的场上的卡回到手卡。剩下的卡用喜欢的顺序回到卡组下面。','\r',char(13)),'\n',char(10)),'确认卡组','翻开卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95254840,'临时取消','①：对方怪兽的攻击宣言时才能发动。自己场上的怪兽全部变成守备表示。这张卡的发动后，直到回合结束时被战斗·效果破坏的自己场上的表侧表示的「娱乐伙伴」怪兽不去墓地回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95265975,'贪食的食尸鬼','无论怎么吃也不会饱的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95281259,'战士生还','①：以自己墓地1只战士族怪兽为对象才能发动。那只战士族怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95286165,'融合解除','①：以场上1只融合怪兽为对象才能发动。那只融合怪兽回到持有者的额外卡组。那之后，若回到额外卡组的那只怪兽的融合召唤使用过的一组融合素材怪兽在自己墓地齐集，可以把那一组在自己场上特殊召唤。','是否要特殊召唤融合素材？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95288024,'天空龙','有着四枚羽翼的鸟的外形的龙，用如刀刃一般的羽毛进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95291684,'异次元的一角战士','这张卡不能通常召唤，把这张卡特殊召唤的回合自己不能通常召唤。这张卡在对方场上有怪兽存在，自己场上有调整表侧表示存在的场合可以特殊召唤。这个方法特殊召唤成功时，可以选择从游戏中除外的1只调整以外的3星以下的自己怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95308449,'终焉的倒计时','支付2000基本分。这张卡发动的20回合后，自己获得本次决斗的胜利。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95326659,'宝玉的引导','自己的魔法与陷阱卡区域名字带有「宝玉兽」的卡有2张以上存在的场合，从卡组特殊召唤1只名字带有「宝玉兽」的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95352218,'天下统一','双方的准备阶段时，回合玩家从自己场上的怪兽的等级之内选1个，持有那个等级以外的等级的自己场上表侧表示存在的怪兽全部送去墓地。','送去墓地','请选择要保留的等级对应的怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95360850,'护盾战士','战斗伤害计算时，把自己墓地存在的这张卡从游戏中除外才能发动。自己场上存在的怪兽不会被那次战斗破坏。','不被战斗破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95362816,'元素英雄 火焰女郎','自己回合的结束阶段时，给与对方基本分自己场上表侧表示存在的名字带有「元素英雄」的怪兽数量×200的数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95376428,'召唤限制-额外之网',replace(replace('①：自己或者对方从额外卡组把怪兽特殊召唤的场合把这个效果发动。从把那些怪兽特殊召唤的玩家来看的对方可以让以下效果适用。\r\n●从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','是否抽1张卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95395761,'甲虫装机 蚂蚁','1回合1次，可以从自己的手卡·墓地把1只名字带有「甲虫装机」的怪兽当作装备卡使用给这张卡装备。这张卡当作装备卡使用而装备中的场合，装备怪兽的等级上升3星，攻击力·守备力上升这张卡的各自数值。装备怪兽被破坏的场合，可以作为代替把这张卡破坏。','装备','是否要用「甲虫装机 蚂蚁」代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95401059,'绊影依-神数原核',replace(replace('←7 【灵摆】 7→\r\n①：自己不是「影依」怪兽以及「神数」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n「绊影依-神数原核」的怪兽效果1回合只能使用1次。\r\n①：这张卡灵摆召唤成功的场合或者这张卡被送去墓地的场合，以「绊影依-神数原核」以外的自己的灵摆区域1张「神数」卡为对象才能发动。那张卡特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95403418,'星尘妖','这张卡不能通常召唤。把自己场上表侧表示存在的名字带有「尘妖」的怪兽任意数量送去墓地的场合才能特殊召唤。这张卡的攻击力·守备力变成送去墓地的那些怪兽数量×1000的数值。只要这张卡在场上表侧表示存在，对方不能把场上盖放的魔法·陷阱卡发动，对方不能把怪兽反转召唤·特殊召唤。自己场上的怪兽数量比对方场上的怪兽数量多的场合，这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95442074,'No.31 亚伯恶魔',replace(replace('1星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。对方场上的怪兽全部变成表侧攻击表示，并在这个回合可以攻击的场合必须向这张卡作出攻击。这个效果在对方回合也能发动。此外，只要自己场上有「No.13 该隐恶魔」存在，得到以下效果。\r\n●持有超量素材的这张卡不会被战斗以及效果破坏。\r\n●向这张卡的攻击发生的对自己的战斗伤害由对方代受。','\r',char(13)),'\n',char(10)),'变成表侧攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95443805,'霞之谷的祈祷师','可以让自己场上的这张卡以外的1只怪兽回到手卡，这张卡的攻击力直到结束阶段时上升500。这个效果1回合只能使用1次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95448692,'伤害瘦身','这个回合自己受到的全部伤害变成一半。此外，可以把墓地存在的这张卡从游戏中除外，那个回合自己受到的效果伤害变成一半。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95451366,'魔力枯竭','将自己与对方场上存在的所有魔力指示物全部除去。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95453143,'百眼龙',replace(replace('暗属性调整＋调整以外的恶魔族怪兽1只以上\r\n1回合1次，把自己墓地存在的1只6星以下的暗属性的效果怪兽从游戏中除外才能发动。这张卡直到这个回合的结束阶段时当作和为这个效果发动而从游戏中除外的效果怪兽同名卡使用，得到相同效果。此外，这张卡被破坏送去墓地时，从自己卡组把1只名字带有「地缚神」的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'获得怪物效果','从自己卡组把1只名字带有「地缚神」的怪兽加入手卡','复制效果结束','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95457011,'天帝从骑 爱迪娅',replace(replace('「天帝从骑 爱迪娅」的①②的效果1回合各能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把「天帝从骑 爱迪娅」以外的1只攻击力800/守备力1000的怪兽守备表示特殊召唤。这个回合，自己不能从额外卡组把怪兽特殊召唤。\r\n②：这张卡被送去墓地的场合，以除外的1张自己的「帝王」魔法·陷阱卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95466842,'水精鳞-深渊肺鱼兵','只要这张卡在场上表侧表示存在，对方不能选择其他的水属性怪兽作为攻击对象。此外，只要这张卡在场上表侧表示存在，自己场上的水属性怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95472621,'大火葬','当对方发动以墓地里的怪兽卡为对象的卡时这张卡才能发动。将双方墓地里的怪兽卡全部除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95474755,'No.89 电脑兽 系统破坏神',replace(replace('7星怪兽×2\r\n这个卡名的③的效果1回合只能使用1次。\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。把对方的额外卡组确认，选那之内的1张里侧表示除外。\r\n②：这张卡战斗破坏怪兽的战斗阶段结束时，以对方墓地1张卡为对象才能发动。那张卡里侧表示除外。\r\n③：对方的卡被里侧表示除外的场合才能发动。把里侧表示除外中的对方的卡数量的卡从对方卡组上面里侧表示除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95486586,'元素英雄 地核侠',replace(replace('「元素英雄」怪兽×3\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：1回合1次，这张卡成为攻击对象时才能发动。这张卡的攻击力直到那次伤害步骤结束时变成2倍。\r\n②：这张卡进行战斗的战斗阶段结束时，以场上1只怪兽为对象才能发动。那只怪兽破坏。\r\n③：这张卡被战斗·效果破坏时，以自己墓地1只8星以下的「元素英雄」融合怪兽为对象才能发动。那只怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95492061,'万手神','①：这张卡召唤·反转召唤成功时才能发动。从卡组把1只仪式怪兽或者1张仪式魔法卡加入手卡。','把1只仪式怪兽或者1张仪式魔法卡加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95500396,'超重武者装留 仲裁',replace(replace('「超重武者装留 仲裁」的③的效果1回合只能使用1次。\r\n①：自己主要阶段以自己场上1只「超重武者」怪兽为对象才能发动。从自己的手卡·场上把这只怪兽当作装备卡使用给那只自己怪兽装备。\r\n②：这张卡的效果让这张卡装备中的场合，对方不能向装备怪兽以外的自己怪兽攻击。\r\n③：把用这张卡的效果把这张卡装备的自己怪兽解放才能发动。从卡组把1只「超重武者」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95503687,'光道召唤师 露米娜丝',replace(replace('①：1回合1次，丢弃1张手卡，以自己墓地1只4星以下的「光道」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：自己结束阶段发动。从自己卡组上面把3张卡送去墓地。','\r',char(13)),'\n',char(10)),'特殊召唤','从卡组送3张卡去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95507060,'狂植物的泛滥','自己场上表侧表示存在的植物族怪兽的攻击力直到这个回合的结束阶段时上升自己墓地存在的植物族怪兽数量×300的数值。这个回合的结束阶段时，自己场上表侧表示存在的植物族怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95511642,'领取星杯的巫女',replace(replace('向星神献上镇魂祈祷的巫女。\r\n手持之杖是世代继承的祭器，将力量变为结界从机界骑士支配中守护着森之民。虽然她的身上隐藏有足以驯服森之守护龙的神通力，但平日所见的她却有着珍爱哥哥及童年玩伴的亲切少女一面。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95515060,'静寂之杖-波纹','装备这张卡的怪兽守备力上升500分。以装备怪兽为对象的这张卡以外的魔法卡的效果无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95519486,'六武众-斩次','只要自己场上有「六武众-斩次」以外的名字带有「六武众」的怪兽存在，这张卡进行攻击的怪兽在伤害步骤结束时破坏。这张卡被破坏的场合，可以用这张卡以外的名字带有「六武众」的怪兽代替破坏。','攻击过的怪兽破坏','是否要使用「六武众-斩次」的代替破坏效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95526884,'超念力枪手',replace(replace('调整＋调整以外的念动力族怪兽1只以上\r\n这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。此外，这张卡向守备表示怪兽攻击的伤害步骤结束时，若攻击力超过那个守备力，自己基本分回复那个数值。','\r',char(13)),'\n',char(10)),'回复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95561280,'猛毒之风','只要这张卡在场上存在，双方不能把风属性怪兽特殊召唤。此外，场上表侧表示存在的全部风属性怪兽的攻击力下降500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95600067,'宝玉兽 黄玉虎','这张卡攻击对方怪兽的场合，伤害步骤内攻击力上升400。这张卡在怪兽卡区域上被破坏的场合，可以不送去墓地当作永续魔法卡使用在自己的魔法与陷阱卡区域表侧表示安置。','破坏时放置魔法陷阱区','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95612049,'世界逆转',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：等级合计直到变成仪式召唤的怪兽的等级以上为止，把手卡的仪式怪兽解放，从手卡·卡组把「破灭之女神 露茵」或者「终焉之王 迪米斯」仪式召唤。','\r',char(13)),'\n',char(10)),'自己从卡组抽1张','选场上1张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95614612,'炮弹枪贝','当场上存在「海」时，这张卡不受魔法效果的影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95621257,'炎狱魔术师','只要自己场上有名字带有「正义盟军」的怪兽存在，这张卡的攻击力上升400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95637655,'后援战士','这张卡不能通常召唤。自己场上存在的怪兽只有守备表示怪兽2只的场合可以特殊召唤。把这张卡特殊召唤的回合，自己不能同调召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95638658,'流星之弓-烨焰','装备这张卡的怪兽攻击力下降1000点。装备这张卡的怪兽可以对对方进行直接攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95642274,'谦虚的番兵','自己手卡公开，从那之中选1张卡回到卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95658967,'祝福的教会-仪式教堂',replace(replace('「祝福的教会-仪式教堂」的①②的效果1回合各能使用1次。\r\n①：从自己手卡丢弃1张魔法卡才能把这个效果发动。从卡组把1只光属性的仪式怪兽或者1张仪式魔法卡加入手卡。\r\n②：让自己墓地的魔法卡任意数量回到卡组，以持有和回到卡组的数量相同等级的自己墓地1只天使族·光属性怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'加入手卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95676943,'灭绝的定数','①：自己·对方的主要阶段支付2000基本分才能发动。这张卡的发动后第3次的战斗阶段结束时，双方玩家必须把各自场上的卡全部送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95697223,'龙之斗志','①：以这个回合特殊召唤的自己场上1只龙族怪兽为对象才能发动。这个回合，那只怪兽在同1次的战斗阶段中在通常攻击外加上可以作出最多有对方场上的这个回合特殊召唤的怪兽数量的攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95701283,'光神机-轰龙','这张卡可以用1只祭品作召唤。这个方法召唤的场合，这张卡在结束阶段时送去墓地。此外，这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','用1只祭品作召唤','送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95714077,'发条制造业','名字带有「发条」的怪兽的效果发动的场合，可以从自己卡组把1只4星以下的名字带有「发条」的怪兽加入手卡。这个效果1回合只能使用1次。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95727991,'弹射龟','1回合1次，自己场上存在的1只怪兽做祭品。对方受到这只怪兽攻击力一半的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95735217,'古代的机械巨人-究极重击',replace(replace('这张卡不能特殊召唤。\r\n①：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n②：这张卡的攻击破坏怪兽时，从手卡丢弃1只机械族怪兽才能发动。这张卡可以继续攻击。这个效果1回合可以使用最多2次。\r\n③：场上的这张卡被战斗·效果破坏的场合才能发动。从卡组把1张「融合」加入手卡，从自己墓地选这张卡以外的1只「古代的机械」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95744531,'格里格尔','场上表侧表示存在的这张卡的控制权转移给对方时，自己回复3000基本分。这个效果只在这张卡在场上表侧表示存在能使用1次。','LP回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95750695,'超二重召唤',replace(replace('二重怪兽才能装备。\r\n①：装备怪兽当作再1次召唤的状态使用。\r\n②：表侧表示的这张卡从场上送去墓地的场合，以自己墓地1只通常怪兽为对象发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95772051,'魔圣骑士 兰斯洛特','把自己场上表侧表示存在的1只光属性的通常怪兽送去墓地才能发动。这张卡从手卡或者墓地特殊召唤。此外，把自己场上1只名字带有「圣骑士」的怪兽解放才能发动。从卡组把1张名字带有「圣剑」的卡加入手卡。「魔圣骑士 兰斯洛特」的这个效果1回合只能使用1次。此外，「魔圣骑士 兰斯洛特」在自己场上只能有1只表侧表示存在。','特殊召唤','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95784434,'蜕皮逃生','爬虫族怪兽才能装备。装备怪兽1回合只有1次不会被战斗破坏。这个效果适用的伤害步骤结束时，装备怪兽的攻击力上升300。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95788410,'兔龙','雪原上栖息的龙类突然变异种。巨大的耳朵可分辨距离数千米远的声音，结合惊人跳跃力让盯上的猎物无处可逃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95789089,'袋鼠冠军','与这张卡进行战斗的怪兽，战斗伤害计算后变为守备表示。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95816395,'土偶',replace(replace('「土偶」的效果1回合只能使用1次。\r\n①：这张卡被对方的效果送去墓地的回合的结束阶段才能发动。不在自己的场上·墓地存在的等级的1只怪兽从卡组加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95825679,'恶魔降临',replace(replace('「与奈落的契约」降临。\r\n①：这张卡只要在怪兽区域存在，卡名当作「恶魔召唤」使用。\r\n②：场上的这张卡不会被和仪式怪兽以外的怪兽的战斗破坏，不会被仪式怪兽以外的怪兽的效果破坏。\r\n③：仪式召唤的这张卡被对方送去墓地的场合才能发动。从自己的手卡·卡组·墓地选1只「恶魔召唤」特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95833645,'岩浆龙',replace(replace('「岩浆龙」的效果1回合只能使用1次，这个效果发动的回合，自己不是幻龙族怪兽不能特殊召唤。\r\n①：这张卡特殊召唤成功时，以「岩浆龙」以外的自己墓地1只幻龙族怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95841282,'猫耳族','对方回合时，与这张卡战斗的怪兽的原始攻击力在伤害步骤时变成200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95888876,'地狱帝王龙',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当通常召唤使用的再度召唤，这张卡当作效果怪兽使用并得到以下效果。\r\n●这张卡在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95905259,'预言僧 长莲','1回合1次，宣言1个卡的种类（魔法·陷阱），选择对方的魔法与陷阱卡区域盖放的1张卡才能发动。把选择的卡确认，宣言的种类的卡的场合，这个回合选择的卡不能发动。','确认盖卡','请选择一张要确认的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95920682,'英豪礼物','对方基本分是2000以下的场合才能发动。把对方基本分变成8000并从自己卡组抽2张卡。「英豪礼物」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95923441,'三形金字塔的猎人',replace(replace('①：场地魔法卡表侧表示存在的场合，自己在通常召唤外加上只有1次，自己主要阶段可以把1只岩石族怪兽召唤。\r\n②：对方回合1次，以自己场上1张「三形金字塔」场地魔法卡为对象才能发动。那张卡送去墓地，从卡组把和那张卡卡名不同的1张「三形金字塔」场地魔法卡发动。','\r',char(13)),'\n',char(10)),'场地魔法卡发动','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95929069,'寒冰手','①：这张卡被对方破坏送去墓地时，以对方场上1张魔法·陷阱卡为对象才能发动。那张对方的魔法·陷阱卡破坏。那之后，可以从卡组把1只「火焰手」特殊召唤。','破坏并特殊召唤','是否要特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95943058,'邪心英雄 地狱连魔','自己回合的主要阶段一可以把这张卡从游戏中除外，自己场上表侧表示存在的1只恶魔族怪兽在同1次的战斗阶段中可以作2次攻击。这个效果使用的场合，这张卡在第2次的自己的准备阶段时表侧攻击表示在自己场上特殊召唤。','两次攻击','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95952802,'花狼','「银牙狼」＋「魔界之棘」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95956346,'光辉天使','这张卡被战斗破坏送去墓地时，可以从卡组选择1只攻击力1500以下的光属性怪兽表侧攻击表示特殊召唤到自己场上。之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95992081,'虚空海龙 利维艾尔',replace(replace('3星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以除外的1只自己或者对方的4星以下的怪兽为对象才能发动。那只怪兽在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(95993388,'地帝家臣 兰罗布',replace(replace('「地帝家臣 兰罗布」的①②的效果1回合各能使用1次。\r\n①：以对方场上1只表侧表示怪兽为对象才能发动。这张卡从手卡特殊召唤，作为对象的怪兽变成里侧守备表示。这个回合，自己不能从额外卡组把怪兽特殊召唤。\r\n②：这张卡为上级召唤而被解放的场合，以「地帝家臣 兰罗布」以外的自己墓地1只攻击力800/守备力1000的怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96005454,'猎龙','以尖锐獠牙杀死猎物的龙。虽然能用敏锐快捷的动作陆续发出攻击，但本身很缺乏守备能力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96008713,'魔术臂盾','①：对方场上有表侧表示怪兽2只以上存在，自己场上有怪兽存在的场合，对方怪兽的攻击宣言时以攻击怪兽以外的对方场上1只表侧表示怪兽为对象才能发动。那只表侧表示怪兽的控制权直到战斗阶段结束时得到，攻击对象转移为那只怪兽进行伤害计算。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96012004,'幸运机会！','进行1次投掷硬币的效果怪兽的效果发动时对硬币的里表作猜测。猜中的场合，从自己卡组抽1张卡。','猜硬币','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96015976,'再转','①：1回合1次，以场上1只持有和原本等级不同等级的怪兽为对象才能把这个效果发动。掷1次骰子。作为对象的怪兽的等级变成和出现的数目相同。','等级变化','是否使用效果？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96029574,'黑兽龙',replace(replace('调整＋调整以外的二重怪兽1只以上\r\n1回合1次，可以从手卡把1只二重怪兽送去墓地，选择对方场上存在的1张魔法·陷阱卡破坏。这张卡被破坏送去墓地时，可以选择自己墓地存在的1只二重怪兽特殊召唤。这个效果特殊召唤的二重怪兽变成再度召唤的状态。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96051150,'阳炎兽 蝎狮','只要这张卡在场上表侧表示存在，对方不能把这张卡作为卡的效果的对象。此外，1回合1次，把手卡或者自己场上表侧表示存在的1只炎属性怪兽送去墓地才能发动。这个回合，这张卡不会被卡的效果破坏。这个效果在对方回合也能发动。','破坏耐性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96055137,'狱火机·贝尔芬格',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把2只「狱火机」怪兽除外的场合才能从手卡·墓地特殊召唤。\r\n①：这张卡的攻击宣言时才能发动。对方从额外卡组选1只怪兽除外。\r\n②：1回合1次，把自己场上1只怪兽解放，以对方墓地1张卡为对象才能发动。那张卡除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'除外对方额外卡组的怪兽','除外对方墓地的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96073342,'神数的星战',replace(replace('「神数的星战」在1回合只能发动1张。自己的灵摆区域有2张「神数」卡存在的场合，这张卡的发动从手卡也能用。\r\n①：以自己场上1张「神数」卡和对方场上1张卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96099959,'X-剑士 帕洛姆罗','自己场上存在的名字带有「剑士」的怪兽被战斗破坏送去墓地时，可以支付500基本分，这张卡从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96100333,'古代遗迹的觉醒',replace(replace('①：1回合1次，可以从自己墓地把岩石族怪兽或者场地魔法卡合计2张除外，从以下效果选择1个发动。\r\n●以这张卡以外的场上1张表侧表示的卡为对象才能发动。那张卡破坏。\r\n●以自己墓地1只岩石族怪兽为对象才能发动。那只怪兽守备表示特殊召唤。\r\n●以自己墓地最多3张场地魔法卡为对象才能发动。那些卡加入卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'选择1个效果发动','以这张卡以外的场上1张表侧表示的卡为对象才能发动。那张卡破坏。','以自己墓地1只岩石族怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','以自己墓地最多3张场地魔法卡为对象才能发动。那些卡加入卡组洗切。那之后，自己从卡组抽1张。','','','','','','','','','','','','');
INSERT INTO texts VALUES(96142517,'升阶魔法-千死蛮巧',replace(replace('「升阶魔法-千死蛮巧」在1回合只能发动1张，这张卡发动的回合，自己不能用这张卡的效果以外把怪兽特殊召唤。\r\n①：以自己以及对方的墓地的相同阶级的超量怪兽各1只以上为对象才能发动。比那些怪兽阶级高1阶的1只「混沌No.」怪兽或者「混沌超量」怪兽从额外卡组特殊召唤，把作为对象的怪兽在下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'特殊召唤','是否还要选择更多对象？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96146814,'AD变更者','把墓地存在的这张卡从游戏中除外，选择场上存在的1只怪兽发动。选择的怪兽的表示形式变更。','变更表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96148285,'诱发召唤','对方场上有怪兽特殊召唤时才能发动。双方可以从手卡把1只4星以下的怪兽在场上特殊召唤。','是否要从手卡特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96150936,'魔偶甜点新人·水果挞修女',replace(replace('「魔偶甜点」怪兽2只\r\n①：只要这张卡所连接区有「魔偶甜点」怪兽存在，自己场上的「魔偶甜点」魔法·陷阱卡不会成为效果的对象，不会被效果破坏。\r\n②：场上的这张卡被战斗·效果破坏的场合，可以作为代替让自己墓地1只「魔偶甜点」怪兽回到卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96157835,'急袭猛禽-武库猎鹰',replace(replace('7星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。从卡组把1只鸟兽族·4星怪兽特殊召唤。\r\n②：持有「急袭猛禽」怪兽作为超量素材的这张卡在同1次的战斗阶段中可以作出最多有那个数量的攻击。\r\n③：持有「急袭猛禽」怪兽作为超量素材的这张卡被送去墓地的场合才能发动。从额外卡组把「急袭猛禽-武库猎鹰」以外的1只「急袭猛禽」超量怪兽特殊召唤，把墓地的这张卡在那只超量怪兽下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96163807,'冥界骑士 崔斯坦',replace(replace('①：这张卡召唤成功时，以自己墓地1只守备力0的不死族怪兽为对象才能发动。那张卡加入手卡。\r\n②：自己场上有这张卡以外的不死族怪兽存在的场合，这张卡的攻击力上升300。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96182448,'氮素同调士','这张卡被名字带有「氮素」的同调怪兽的同调召唤使用送去墓地的场合，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96216229,'剑斗兽的战车','①：自己场上有「剑斗兽」怪兽存在，怪兽的效果发动时才能发动。那个发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96218085,'中央突破','选择自己场上表侧表示存在的1只名字带有「六武众」的怪兽发动。这个回合的战斗阶段中，选择怪兽战斗破坏对方怪兽的场合，自己场上存在的「大将军 紫炎」或者名字带有「六武众」的怪兽可以直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96223501,'龙星因士-神数右枢',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「星守」怪兽以及「神数」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n「龙星因士-神数右枢」的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·灵摆召唤成功的场合，以这张卡以外的自己的怪兽区域·灵摆区域1张「星守」卡或者「神数」卡和对方场上1张表侧表示的卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96227613,'霸王门 零',replace(replace('←0 【灵摆】 0→\r\n①：自己场上有「霸王龙 扎克」存在的场合，自己受到的全部伤害变成0。\r\n②：1回合1次，另一边的自己的灵摆区域有「霸王门 无限」存在的场合才能发动。自己的灵摆区域2张卡破坏，从卡组把1张「融合」魔法卡加入手卡。\r\n【怪兽效果】\r\n①：1回合1次，以这张卡以外的自己场上1张表侧表示的卡为对象才能发动。那张卡和这张卡破坏，把1只龙族融合怪兽或者龙族同调怪兽从额外卡组特殊召唤。这个效果特殊召唤的怪兽的攻击力·守备力变成0，效果无效化，不能作为同调·超量召唤的素材。\r\n②：怪兽区域的这张卡被战斗·效果破坏的场合才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96235275,'光道圣骑士 简',replace(replace('①：这张卡向对方怪兽攻击的伤害步骤内，这张卡的攻击力上升300。\r\n②：自己结束阶段发动。从自己卡组上面把2张卡送去墓地。','\r',char(13)),'\n',char(10)),'从卡组送2张卡去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96287685,'瓶龟','只要这张卡在场上表侧表示存在，每次「强欲之瓶」发动从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96300057,'W-弹射飞翼',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只「V-喷气虎」为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：装备怪兽的攻击力·守备力上升400。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96316857,'再生','在自己准备阶段支付300基本分，就可从存在于自己墓地的卡里选择1张怪兽卡以外的卡放回卡组最下方。','返回卡组','','','','','','','','','','','','','','','');
